INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(6, '16', 'Первые АВА-тренинги в Иркутске', 'В январе 2018 года мы встречали Дарью Романову, поведенческого аналитика ABA-центра Мозаика, которая провела с нами 4 очень насыщенных дня. С каждым ребёнком было проведено занятие в соответствии с наиболее актуальными для него целями. ', 'Наше знакомство с АВА-центром \"Мозаика\" началось с череды интенсивных курсов для наших детей в Новосибирске. А в июле 2017 года был организован первый приезд АВА-инструктора Марии Сусловой в Иркутск. Приезд был посвящен работе с детьми и практическому тренингу родителей и начинающих АВА-специалистов.<br>\r\nВ январе 2018 года мы встречали Дарью Романову, поведенческого аналитика ABA-центра Мозаика, которая провела с нами 4 очень насыщенных дня. С каждым ребёнком было проведено занятие в соответствии с наиболее актуальными для него целями.<br> \r\nОсновной задачей для каждого ребенка было развитие «своего уровня» речевых навыков.  Кто-то под руководством опытного специалиста научился делать запрос с помощью карточек PECS -  альтернативной системы коммуникации,  а кто-то тренировался в вокальных запросах.  Кто-то пытался произносить первые звуки, кто-то соединять  слоги в эхо и произносить первые связные слова, а кто-то отвечать на вопросы. Работали и над не менее важным навыком понимания речи, используя новые идеи и подходы. Не обошли стороной и развитие игровых навыков у детей, а также совместное внимание. А процесс обучения навыку самообслуживания -  чистке  зубов, оказался не только познавательным, но и очень веселым. <br> По итогам занятий каждая семья получила рекомендации и много интересных идей.  На консультациях также присутствовали специалисты, которые занимаются с детьми. Занятия промчались незаметно,  насыщенно и очень интересно. ', '/images/training/d1.jpg', '2018-03-30', '/images/training/d1.jpg;/images/training/d2.jpg;/images/training/d3.jpg;/images/training/d4.jpg;/images/training/d5.jpg;/images/training/d6.jpg;/images/training/d7.jpg', '', '', '', '', 0, '', '0'),
(12, '2', 'Первый интеграционный спектакль для детей с РАС', '7 апреля 2018г впервые в Иркутске состоялся интеграционный спектакль для  детей с РАС в театре кукол \"Аистенок\", приуроченный к созданию организации АНО \"АПЦ РАСсвет\"', '<p> 7 апреля 2018г впервые в Иркутске состоялся интеграционный спектакль для  детей с РАС в театре кукол \"Аистенок\", приуроченный к созданию организации АНО \"АПЦ РАСсвет\".</p>\r\n<p> Уникальность мероприятия не только в том, что нам удалось организовать детям с РАС поход в театр, но и в том, что мы смогли создать условия для их взаимодействия с обычными ребятишками. На спектакль \"О рыбаке и рыбке\" были приглашены семьи, воспитывающие детей с аутизмом, а также их друзья со своими детьми. Все заранее были предупреждены об особенностях деток, вместе с билетами раздавались брошюры с информацией о данном расстройстве. С целью уменьшения сенсорной перегрузки количество мест было ограничено, а также была возможность выходить в коридор во время представления. </p>\r\n<p> Первый адаптированный спектакль прошел успешно! Юные зрители остались довольны! </p>\r\n<p> Выражаем огромную благодарность администрации театра за предоставленную возможность!</p>\r\n', '/images/dosug/theatre/9.JPG', '2018-04-07', '/images/dosug/theatre/1.jpg; /images/dosug/theatre/2.jpg; /images/dosug/theatre/3.JPG; /images/dosug/theatre/4.JPG; /images/dosug/theatre/5.JPG; /images/dosug/theatre/6.JPG; /images/dosug/theatre/7.JPG; /images/dosug/theatre/8.JPG;  /images/dosug/theatre/9.JPG; /images/dosug/theatre/10.JPG; /images/dosug/theatre/11.JPG; /images/dosug/theatre/12.JPG; /images/dosug/theatre/13.jpg; /images/dosug/theatre/14.jpg; /images/dosug/theatre/15.jpg; /images/dosug/theatre/16.jpg', '', '', '', '', 0, '', '0'),
(13, '22', 'Благотворительная лотерея от котокафе \"Время котов\"', 'Выражаем огромную благодарность котокафе <a href=\"https://www.facebook.com/vremya.kotov/posts/1946907992047486\" target=\"_blank\">«ВРЕМЯ КОТОВ»</a>   за проведение благотворительной лотереи, приуроченной к дню информирования об аутизме. ', '<p> Выражаем огромную благодарность котокафе <a class=\"a-blue\" href=\"https://www.facebook.com/vremya.kotov/posts/1946907992047486\" target=\"_blank\">«ВРЕМЯ КОТОВ»</a> за проведение благотворительной лотереи, приуроченной к дню информирования об аутизме. </p>\r\n<p>8 апреля состоялся розыгрыш. Победителю достался этот замечательный кот по имени Скиннер.</p>\r\n<p> Все средства, вырученные от проведения лотереи, пойдут на приобретение учебных и игровых материалов для занятий с детьми с аутизмом. Спасибо всем, кто принял участие в лотерее!</p>\r\n', '/images/news/3.jpg', '2018-04-08', '', '', '', 'Новости', '/news.php', 0, '', '0'),
(14, '5', '\"Почему пазл?\"', 'Апрель – месяц распространения информации об аутизме. Пазлы считаются общепринятым международным символом аутизма. Однако мало кто знает о происхождении этого символа. Читайте об этом в нашей статье. ', '<p>Апрель – месяц распространения информации об аутизме. Не секрет, что в  г.Иркутске до сих пор складывается сложная ситуация по отношению к проблеме аутизма, родители объединяются и пытаются внести свой вклад в преодоление всевозможных преград, но пока для нашего города вопрос о соответствующей помощи людям с РАС  остается открытым. В поддержку Всемирного дня распространения информации о проблеме аутизма мы проводим эстафету «СОБЕРИ ПАЗЛ» в соцсетях, подведение итогов которой запланировано на 23 апреля 2018г.  \r\n</p>\r\n<p> Почему именно пазл? Пазлы считаются общепринятым международным символом аутизма. Однако мало кто знает о происхождении этого символа. 23 января 1962 года в Лондоне было основано Национальное общество аутизма Великобритании. Это была первая в мире организация родителей детей с аутизмом, которые начали защищать их интересы. \r\n</p>\r\n<p> В те времена в Великобритании «аутизм» еще не был общепринятым термином среди специалистов. Аутичным детям ставились диагнозы «психоз» или «детская шизофрения», а население в целом вообще ничего про аутизм не слышало. Многие специалисты в то время ставили под сомнение необходимость отдельной организации, которая будет заниматься только аутизмом. В то время существовало Общество для детей с психическими нарушениями, и многие говорили, что его достаточно для представления интересов всех детей с нарушениями развития, и нет смысла создавать «еще одну детскую благотворительную организацию». В то время еще были довольно популярны психоаналитические теории о том, что аутизм развивается из-за «отстраненных, холодных родителей», а также бытовало представление о «критических периодах» развития, согласно которому, после определенного возраста состояние ребенка изменить уже невозможно и единственный вариант – поместить ребенка в интернат до конца жизни. Диагноз «аутизм» было крайне сложно получить, а услуги по специальному образованию предоставлялись только тем детям, которые были признаны «обучаемыми» комиссией специалистов, остальных детей направляли в учреждения, где практически не было педагогов.\r\n</p>\r\n<p> Организация быстро развивалась, формировалась сеть специалистов, появлялись публикации в СМИ, были открыты первые начальные школы для детей с аутизмом. Тогда же, 14 февраля 1963г.,  Джеральдом Гассоном был разработан логотип организации – кусочек пазла, внутри которого был плачущий ребенок.  Поскольку это была первая организация по вопросам аутизма в мире, со временем другие организации начали использовать свои варианты пазлов и символ постепенно стал международным.\r\n</p>\r\n<p> Тогда символ обозначал следующее: дети с аутизмом страдают от расстройства, которое ставит в тупик, оно изолирует их от человеческих отношений и не дает им «найти свое место» в общей картине. Плачущий ребенок должен был напоминать, что аутичные люди действительно страдают от своих трудностей. Однако сейчас организация, положившая начало этому символу, больше его не использует – в 2002 году логотип был изменен на другой. Это было связано с тем, что организация больше не занималась только детьми, и все больший акцент делался на поддержку взрослых с аутизмом, кроме того, было решено подчеркивать предоставление поддержки, а не таинственность аутизма. Однако символ пазла продолжает активно использоваться во всем мире.\r\n</p>\r\n<p> Источник: Фонд «Выход». Аутизм в России\r\n</p>\r\n', '/images/news/puzzle/1.jpg', '2018-04-10', '', '', '', '', '', 0, '', '0'),
(19, '22', 'КОТОФОТОСЕССИЯ', 'Наша организация получила необычное приглашение от друзей – котокафе «Время котов». На настоящую КОТОФОТОСЕССИЮ!!!! Нас встретили приветливые хозяева кафе и их пушистые питомцы! ', '<p>Наша организация получила необычное приглашение от друзей – котокафе «Время котов». Это удивительное по своей миссии и мурчавому настроению место!</p> \r\n<p>Мы уже знакомы с хозяйкой кафе - Валентиной и ее питомцами - кошками. Каждый пушистый обитатель студии имеет свою историю жизни, порой не очень веселую. Но сейчас у этих милых животных есть постоянный дом, заботливый уход и много-много внимания и ласки от всех посетителей антикафе. Наши дети тоже не стали исключением - они полюбили приходить в это уютное место, играть с кошками, знакомиться с историей каждого питомца, наблюдать за их жизнью в студии.</p> \r\n<p>Но в этот раз мы пришли не просто в гости. Нас пригласили на настоящую КОТОФОТОСЕССИЮ!!!! И дети, и взрослые были очень рады снова окунуться в уютную атмосферу кафе: играть с кошками, пить ароматный чай с вкусняшками и, конечно,  фотографироваться в непринужденной обстановке! </p>\r\n<p> Огромное спасибо организаторам за наше сегодняшнее приключение и непередаваемые эмоции! Благодарим за отзывчивость, понимание и  тепло ваших сердец! Это был самый необычный, нестандартный и очень комфортный день из жизни семей с детьми с аутизмом. Мы с удовольствием продолжаем общение со студией \"Время котов\" и желаем нашим пушистым друзьям и их заботливым владельцам удачи и хорошего настроения!</p>\r\n<p>P.S. А вот и наши фото! Наслаждаемся результатом и благодарим нашего фотографа Марину Корч за чудесные фото!</p>', '/images/dosug/kats/1/20.jpg', '2018-04-22', '/images/dosug/kats/1/1.jpg; /images/dosug/kats/1/2.jpg; /images/dosug/kats/1/3.jpg; /images/dosug/kats/1/4.jpg; /images/dosug/kats/1/5.jpg; /images/dosug/kats/1/6.jpg; /images/dosug/kats/1/7.jpg; /images/dosug/kats/1/9.jpg; /images/dosug/kats/1/11.jpg; /images/dosug/kats/1/13.jpg; /images/dosug/kats/1/14.jpg; /images/dosug/kats/1/15.jpg; /images/dosug/kats/1/17.jpg; /images/dosug/kats/1/19.jpg', '', '', '', '', 0, '', '0'),
(25, '3', 'Семинар по основам АВА в Иркутске', '5 мая в Иркутске состоялся семинар по основам Прикладного Анализа Поведения (АВА), который провела поведенческий аналитик  АВА-центра \"Мозаика\" (г.Новосибирск) Дарья Романова.  ', '<p>5 мая 2018 года впервые в Иркутске состоялся семинар по основам Прикладного Анализа Поведения (АВА) на тему «Формирование учебного поведения», который провела поведенческий аналитик  АВА-центра \"Мозаика\" (г. Новосибирск) Дарья Романова.  Организаторы мероприятия  АНО \"АПЦ РАСсвет\" и АВА-центр \"Мозаика\" благодарят руководство библиотеки им. Молчанова-Сибирского за неоценимую помощь в проведении семинара. </p>\r\n<p> На мероприятии присутствовали как родители детей с РАС, так и специалисты, работающие с детьми с особенностями развития. Семинар был интересен не только людям незнакомым с прикладным анализом поведения, но и тем, кто уже не новичок в АВА.  Материал семинара был настолько увлекательным, что все участники внимательно следили за процессом. Были разобраны важные вопросы: как установить сотрудничество с ребёнком и плавно  перейти к обучению, как правильно провести коррекцию ошибки и многое другое.  Самое интересное происходило на практической части семинара. Специалистам и родителям удалось применить полученные знания в процессе тренировки друг с другом, а также поработать с ребёнком с диагнозом аутизм. </p> \r\n<p> Кроме того, наше мероприятие посетила почетная гостья  -  член союза писателей России Мария Яковенко (Артемьева), которая подарила нашей организации АНО \"АПЦ РАСсвет\" и АВА-центру \"Мозаика\" свои замечательные книги </p>\r\n <p> Семинар закончился, а наша работа с детьми под руководством Дарьи в самом разгаре. Следите за нашими новостями! </p>\r\n', '/images/seminar/20.jpg', '2018-05-06', '/images/seminar/01.jpg; /images/seminar/02.jpg; /images/seminar/03.jpg; /images/seminar/04.jpg; /images/seminar/05.jpg; /images/seminar/06.jpg; /images/seminar/07.jpg; /images/seminar/08.jpg;  /images/seminar/09.jpg; /images/seminar/10.jpg; /images/seminar/11.jpg; /images/seminar/12.jpg; /images/seminar/13.jpg; /images/seminar/14.gif; /images/seminar/15.jpg; /images/seminar/16.jpg; /images/seminar/17.jpg; /images/seminar/18.jpg; /images/seminar/19.jpg; /images/seminar/20.jpg; /images/seminar/21.jpg; /images/seminar/22.jpg; /images/seminar/25.jpg', '', '', '', '', 0, '', '0'),
(27, '2', '\"Подари звезду\"', 'Наши дети - с аутизмом. Мы стараемся, чтобы их жизнь была насыщена теми же событиями, что и жизнь других людей. Подготовку к празднованию Дня Победы мы организовали с особым подходом.', '', '/images/projects/stars/1.png', '2018-05-11', '', '', '/stars.php', '', '', 1, '', '0'),
(28, '16', 'Третий интенсивный тренинг в Иркутске', 'Две недели с нашими детьми велась интенсивная работа совместно с поведенческим аналитиком АВА-центра «Мозаика» Дарьей Романовой. Программы детей были обновлены, а для родителей и специалистов проведены тренинги.', '<p>Две недели с нашими детьми велась интенсивная работа совместно с поведенческим аналитиком АВА-центра «Мозаика» Дарьей Романовой. Программы детей были обновлены в соответствии с актуальными целями, а для родителей проведены тренинги. В ходе очных консультаций были охвачены самые разнообразные программы с учётом индивидуальных особенностей каждого ребенка. </p>\r\n<p>Игорь учился навыкам самообслуживания: различать правый и левый сандалии, находить пятку на носках и правильно надевать на ногу, открывать самые разнообразные зубные пасты и тщательней чистить зубы, убирать за собой в туалете, а также расширять свой пищевой рацион. </p>\r\n<p>Один из наших Владиков обучался артикуляционной имитации, а его родители получили консультацию по туалетному тренингу. </p>\r\n<p>Другой Влад совершенствовался в учебном поведении, учился адекватной реакции на отказы, а также продолжил работу над развитием своих речевых навыков: ведение диалога, описание картинки и других. </p>\r\n<p>Саша практиковался в понимании речи, поработали и с учебным поведением. Результат не заставил себя ждать! Саша занимался 2,5 часа без какого-либо нежелательного поведения! </p>\r\n<p>Работа с Тасей была направлена на развитие совместного внимания, понимание двухкомпонентных инструкций и коррекцию поведения. Кроме того, она училась  помогать маме по дому: вытирать пыль, поливать цветы. </p>\r\n<p>С Тимофеем была проведена большая коррекционная работа над развитием навыков слушателя, кроме того, он перешел на второй этап работы по совместному вниманию. А родители получили рекомендации по развитию самостоятельных вокальных запросов. </p>\r\n<p>С Никой разбирали поведенческие моменты, учились саморегуляции и играли в Slapjack. </p>\r\n<p>Ваня отрабатывал цепочку действий в навыках самообслуживания - мыться в душе, надевать штаны и футболку лицевой стороной.  Тренировался в поведении слушателя при помощи музыкальных инструментов и учился раскрашивать, соотнося цвета по образцу. </p>\r\n<p>А Сава, наш самый маленький ученик, учился спокойно сидеть за столом и выполнять инструкции на визуальное восприятие, моторную имитацию и имитацию с предметами. </p>\r\n<p>Все консультации прошли очень плодотворно, родители получили подробные письменные рекомендации от Дарьи! Как всегда, время пролетело незаметно! </p>\r\n', '/images/training/may/10.jpg', '2018-05-13', '/images/training/may/1.jpg; /images/training/may/2.jpg; /images/training/may/3.jpg; /images/training/may/4.jpg; \r\n/images/training/may/5.jpg;  /images/training/may/6.jpg; /images/training/may/7.jpg; /images/training/may/8.jpg;  /images/training/may/9.jpg; /images/training/may/10.jpg; /images/training/may/11.jpeg; /images/training/may/12.jpg; /images/training/may/13.jpg; /images/training/may/14.jpg; /images/training/may/16.jpg; /images/training/may/17.jpg; /images/training/may/18.jpg; /images/training/may/19.jpg; /images/training/may/20.jpg; /images/training/may/21.jpg', '', '/training/may.php', '', '', 0, '', '0'),
(33, '12', 'Тренировка у президента', 'Мы запускаем новый  проект под общим названием «Движение», в который будут входить все мероприятия спортивно-оздоровительного характера, направленные на развитие двигательной активности детей с РАС, 						выявление их интересов в этой сфере и развитие социальных навыков.', '', '/images/AFK/rollers/13.jpg', '2018-05-25', '', '', '/phys/rollers.php', '', '', 1, '', '0'),
(34, '12', 'Футбол для детей с РАС', 'В воскресенье, 27 мая,  мы запустили пилотный проект «Футбол для детей с РАС». Этот проект уникален не только тем, что мы стараемся найти подход к каждому ребенку с аутизмом, но и тем, что он даёт возможность отцам проявить себя и принять участие в развитии и воспитании своих детей', '', '/images/AFK/football/0.jpg', '2018-05-28', '', '', '/phys/football.php', '', '', 1, '', '0'),
(36, '3', 'Лекция для специалистов и родителей', '2 июня 2018 года председатель АНО \"АПЦ Рассвет\" Екатерина Родыгина и детский психиатр Андрей Рупашев провели лекцию об особенностях адаптации, реабилитации и инклюзивного обучения детей с РАС.', '2 июня 2018 года председатель АНО \"АПЦ Рассвет\" Екатерина Родыгина и детский психиатр Андрей Рупашев провели лекцию об особенностях адаптации, реабилитации и инклюзивного обучения детей с расстройствами аутистического спектра на курсах повышения квалификации для родителей детей с ОВЗ и профильных специалистов. Курсы проходят на базе Института дополнительного образования ИГУ и посвящены обучению современным психолого-педагогическим методам работы с детьми с ОВЗ в условиях лекотеки. Выражаем огромную благодарность врачу-психиатру Рупашеву А.В., который согласился принять участие в лекции и рассказать о клинических проявлениях расстройств аутистического спектра.', '/images/seminar/02.06.18/2.jpg', '2018-06-05', '/images/seminar/02.06.18/1.jpg; /images/seminar/02.06.18/2.jpg; /images/seminar/02.06.18/3.jpg;  /images/seminar/02.06.18/4.jpg; /images/seminar/02.06.18/5.jpg', '', '', '', '', 0, '', '0'),
(40, '16', 'Стажировка специалистов', 'С 25 июня по 6 июля в АВА-центре “Мозаика” г.Новосибирск состоялась двухнедельная стажировка начинающих специалистов (АВА-инструкторов), посвященная практическому применению методов прикладного анализа поведения для работы с детьми с РАС. От нашей организации в стажировке приняли участие 5 человек.', '<p>С 25 июня по 6 июля в АВА-центре “Мозаика” г.Новосибирск состоялась двухнедельная стажировка начинающих специалистов (АВА-инструкторов), посвященная практическому применению методов прикладного анализа поведения для работы с детьми с РАС. От нашей организации в стажировке приняли участие 5 человек: 4 учредителя и наш специалист АВА-центра, уже имеющие теоретическую подготовку в области АВА.</p>\r\n<p>Стажировка прошла очень динамично, насыщенно и интересно! Занятия шли полный рабочий день, основное время было посвящено практической работе с детьми. Нам удалось применить на практике множество программ, поработать над формированием речевых, социальных и игровых навыков, над формированием совместного внимания и учебного поведения, поучаствовать в применении поведенческих протоколов и многого другого!</p>\r\n<p>Кроме того, эти две недели были насыщены интересными знакомствами, а также незабываемым и очень продуктивным общением со специалистами “Мозаики” -  инструкторами и поведенческими аналитиками. Время пролетело незаметно!\r\nМы выражаем искреннюю благодарность Евгении Чульжановой, директору АВА-центра “Мозаика” за организацию стажировки, которая дала нам возможность применить свои теоретические знания на практике, а также за постоянное и плодотворное сотрудничество с нашей организацией!</p>', '/images/seminar/07.18/31.jpg', '2018-07-10', '/images/seminar/07.18/1.jpg;/images/seminar/07.18/2.jpg;/images/seminar/07.18/3.jpg;/images/seminar/07.18/4.jpg;/images/seminar/07.18/5.jpg;/images/seminar/07.18/6.jpg;/images/seminar/07.18/7.jpg;/images/seminar/07.18/8.jpg;/images/seminar/07.18/9.jpg;/images/seminar/07.18/10.jpg;/images/seminar/07.18/11.jpg;/images/seminar/07.18/12.jpg;/images/seminar/07.18/13.jpg;/images/seminar/07.18/14.jpg;/images/seminar/07.18/15.jpg;/images/seminar/07.18/16.jpg;/images/seminar/07.18/17.jpg;/images/seminar/07.18/18.jpg;/images/seminar/07.18/19.jpg;/images/seminar/07.18/20.jpg;/images/seminar/07.18/21.jpg;/images/seminar/07.18/22.jpg;/images/seminar/07.18/23.jpg;/images/seminar/07.18/24.jpg;/images/seminar/07.18/25.jpg;/images/seminar/07.18/26.jpg;/images/seminar/07.18/27.jpg;/images/seminar/07.18/28.jpg;/images/seminar/07.18/31.jpg;/images/seminar/07.18/30.jpg', '', '', '', '', 0, '', '0'),
(43, '16', 'Тренинг \"Совместное внимание\"', 'Учредители и специалисты \"Рассвета\" приняли участие в тренинге \"Совместное внимание\". Тренинг был проведен поведенческим аналитиком Дарьей Романовой в рамках стажировки специалистов в АВА-центре «Мозаика» (г.Новосибирск).', 'Дети, у которых диагностирован аутизм, имеют дефицит социальных навыков, а если говорить конкретнее, то им не хватает разделенного (совместного) внимания. Так называется общее внимание двух людей к одному и тому же объекту (Wikipedia) или синхронизация внимания между двумя или более людьми (Per Holth, 2006).<br>\r\nСовместное внимание — это критически важный навык в развитии ребенка. Исследования связывают его с последующим развитием навыков, включая способность к символическому мышлению, речи и общим социально-познавательным процессам. Некоторые дети c РАС учатся совместному вниманию естественным образом, однако иногда необходимы специальные процедуры, чтобы сделать взгляд в глаза и социальное внимание поощрением для ребенка.<br>\r\nВ июле 2018 года, во время стажировки специалистов в АВА-центре «Мозаика» г.Новосибирск, поведенческим аналитиком центра Дарьей Романовой был проведен тренинг, посвященный развитию совместного внимания у детей с аутизмом. А помогал ей в этом наш Владик. Во время тренинга нам удалось поработать с Владом над четырьмя основными этапами совместного внимания. Пользу этого важного навыка уже оценили и взрослые и дети!\r\n<section class=\"video-detail\">\r\n				\r\n<iframe src=\"https://rutube.ru/play/embed/14354947e30de7a888635f134e923a71\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen class=\"social-video\"></iframe>\r\n         </section>', '/images/training/2.jpg', '2018-07-31', '', '', '/training/attention.php', '', '', 0, '', '0'),
(46, '2', 'Проект \"АэроПлан\"', 'Лето - время путешествий. Казалось бы, что тут сложного? Собрал чемоданы,  садишься на самолёт и улетаешь в рассвет... \r\nНо для детей с РАС это целое испытание. ', '', '/images/projects/airplane/36.jpg', '2018-08-04', '', '', '/projects/airplane.php', '', '', 1, '', '0'),
(63, '3', 'Цикл семинаров для родителей детей с РАС и специалистов', 'В сентябре 2018 года мы запустили цикл семинаров для специалистов и родителей детей с РАС, в рамках которого продолжаем работу по повышению информированности родителей и специалистов о методах Прикладного анализа поведения!', '<p>В сентябре 2018 года мы запустили цикл семинаров для специалистов и родителей детей с РАС, в рамках которого продолжаем работу по повышению информированности родителей и специалистов о методах Прикладного анализа поведения!</p>\r\n<p>На первой встрече мы сделали обзор стратегий коррекции нежелательного поведения , познакомили участников с одним из основных методов обучения новым навыкам, а наш специалист Шуткина П.С. рассказала об опыте зарубежных специалистов в применении данных методов. </p>\r\n<p>Второй семинар \"Обзор литературы и ответы на вопросы\", состоявшийся 27 октября 2018 года, был посвящен тематике современного, научного книгоиздательства в сфере аутизма. Нашей целью было познакомить родителей и специалистов с новейшими технологиями взаимодействия и работы с детьми с РАС, отраженными в книгах, показать целевой группе, что самообразование и информированность зачастую - одни из самых эффективных путей помощи ребенку с аутизмом в максимально функциональном развитии.</p>\r\n<p>Спасибо всем, кто причастен к проведению и организации семинаров! А также сотрудникам библиотеки им. Молчанова-Сибирского! И отдельное спасибо всем слушателям!</p>\r\n<p>Без сбора и анализа данных невозможно разработать дальнейшую стратегию реализации любого проекта. Мы провели количественную и качественную оценку нашей работы в проекте «Семинары» и получили следующие результаты. Согласно гистограмме «Количество слушателей на первом ежемесячном семинаре» количественный показатель составил 50% допустимого количества слушателей на семинаре, что указывает на необходимость в увеличении качества информированности населения г. Иркутска о проводимом мероприятии.</p>\r\n<p>Проведенное до и после семинара анкетирование указывает, что качественный показатель правильных ответов изменился практически по всем заданным вопросам более, чем на 30%. Количество правильных ответов после семинара составило более 80%, что говорит о том, что материал усвоен.</p>\r\n<p>Однако у слушателей еще осталась путаница в понимании некоторых вопросов, где показатель правильных ответов составил чуть больше 60%, что указывает о необходимости более тщательного разбора данных вопросов в рамках семинара по информированию специалистов и родителей детей с РАС об эффективных и научно обоснованных стратегиях помощи данной категории людей.</p>', '/images/seminar/29.09.18/16.jpg', '2018-10-28', '/images/seminar/29.09.18/1.jpg; /images/seminar/29.09.18/2.jpg; /images/seminar/29.09.18/3.jpg', '', '', '', '', 0, '', '0'),
(66, '16', 'Конференция «Аутизм. Выбор маршрута»', 'Наши учредители Елена Михеева и Елена Шапилова приняли участие в III Международной научно-практической конференция «Аутизм. Выбор маршрута», которая прошла с 8 по 10 октября 2018 года в «Сколково».', '<p>Наши учредители Елена Михеева и Елена Шапилова приняли участие в III Международной научно-практической конференция «Аутизм. Выбор маршрута», которая прошла с 8 по 10 октября 2018 года в Московской школе управления «Сколково». Мы благодарим Ассоциацию \"АУТИЗМ-РЕГИОНЫ\" и фонд \"Выход\" за эту возможность!</p> <p>Цель конференции - создание площадки для разговора о самых важных проблемах и задачах в сфере помощи людям с аутизмом в России. Ученые, практики, руководители вузов, родители детей с аутизмом, представители исполнительной власти, журналисты, деятели культуры и соратниками по сектору НКО получили возможность встретиться и пообщаться друг с другом.</p>\r\n<p>Очень важно, что в этом году в числе соорганизаторов выступила Ассоциация «Аутизм – Регионы», тем самым пополнив организационный комитет представителями родительской общественности. Ведь кому еще, как ни родителям выступать локомотивами изменений способствующих решению проблем, с которыми сталкиваются люди с аутизмом в России. </p>\r\n<p>Первый день конференции был ориентирован на представителей органов исполнительной власти, были представлены пленарные доклады, описывающие общие направления работы над созданием комплексной системы помощи детям и взрослым с расстройствами аутистического спектра (РАС) в России.</p>\r\n<p>Второй и третий дни конференции содержали лекции, семинары, панельные дискуссии и мастер-классы ведущих мировых и российских специалистов в сфере помощи детям и взрослым с РАС.</p>\r\n<p>Наши учредители отметили высокий уровень организации конференции. Им удалось лично познакомиться и пообщаться с ведущими специалистами в области помощи людям с РАС и привезти в Иркутск массу новых впечатлений, информации, пищи для размышлений и идей для дальнейшей работы! </p>', '/images/seminar/SKOLKOVO/1.jpg', '2018-10-12', '/images/seminar/SKOLKOVO/1.jpg; /images/seminar/SKOLKOVO/2.jpg; /images/seminar/SKOLKOVO/3.jpg;  /images/seminar/SKOLKOVO/4.jpg; /images/seminar/SKOLKOVO/5.jpg; /images/seminar/SKOLKOVO/6.jpg; /images/seminar/SKOLKOVO/7.jpg; /images/seminar/SKOLKOVO/8.jpg; /images/seminar/SKOLKOVO/9.jpg; /images/seminar/SKOLKOVO/10.jpg; /images/seminar/SKOLKOVO/11.jpg;  /images/seminar/SKOLKOVO/12.jpg; /images/seminar/SKOLKOVO/13.jpg; /images/seminar/SKOLKOVO/14.jpg; /images/seminar/SKOLKOVO/15.jpg; /images/seminar/SKOLKOVO/16.jpg;  /images/seminar/SKOLKOVO/17.jpg; /images/seminar/SKOLKOVO/18.jpg; /images/seminar/SKOLKOVO/19.jpg; /images/seminar/SKOLKOVO/20.jpg;\r\n/images/seminar/SKOLKOVO/21.jpg; /images/seminar/SKOLKOVO/22.jpg; /images/seminar/SKOLKOVO/23.jpg;  /images/seminar/SKOLKOVO/24.jpg; /images/seminar/SKOLKOVO/25.jpg; /images/seminar/SKOLKOVO/26.JPG; /images/seminar/SKOLKOVO/27.jpg; /images/seminar/SKOLKOVO/28.jpg; /images/seminar/SKOLKOVO/29.jpg; /images/seminar/SKOLKOVO/30.jpg; /images/seminar/SKOLKOVO/31.JPG;  /images/seminar/SKOLKOVO/32.jpg; /images/seminar/SKOLKOVO/33.jpg; /images/seminar/SKOLKOVO/34.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', '0'),
(70, '4', 'Ваня - обучение счету', 'Ване тяжело воспринимать речь на слух. Но, благодаря своей сильной стороне - визуальному восприятию, Ваня успешно осваивает новые навыки.', 'С начала занятий АВА-терапией Ваня научился очень многому: он стал смотреть в глаза, заниматься за столом, сформировалось учебное поведение. У Вани появилась имитация действий с предметами и без, но это были только первые шаги.<br>\r\nВане тяжело воспринимать речь на слух. Но его сильной стороной является навык визуального восприятия. Благодаря визуальным подсказкам Ваня осваивает новые стимулы, а также научился сортировать предметы по цветам, формам и  категориям. <br>\r\nТакже Ваня освоил альтернативную систему коммуникации с помощью карточек PECS, развиваются и вокальные запросы. <br>\r\nОпираясь на сильную сторону Вани - визуальное восприятие, мы начали обучать его счету. Показываем на видео, каких успехов нам удалось добиться.\r\n<br>\r\n<section class=\"video-detail\">\r\n								<iframe src=\"https://rutube.ru/play/embed/3161c544bc3a4786682bb338397e7aeb/\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen class=\"social-video\"></iframe>\r\n			</section>', '/images/ind/vanya/1.JPG', '2019-02-01', '', '', '', '', '', 0, '', '0'),
(76, '16', 'Встреча победителей Фонда Президентских Грантов', 'Сегодня председатель АНО «АПЦ РАСсвет» выступила с докладом на неформальной встрече победителей Фонда Президентских грантов Иркутской области, которую организовал Благотворительный фонд «Оберег» совместно с Правительством Иркутской области и Общественной палатой Иркутской области.', '<p>Сегодня председатель АНО «АПЦ РАСсвет» выступила с докладом на неформальной встрече победителей Фонда Президентских грантов Иркутской области, которую организовал Благотворительный фонд «Оберег» совместно с Правительством Иркутской области и Общественной палатой Иркутской области.</p>\r\n<p>Екатерина Родыгина кратко рассказала об организации и о проекте - победителе конкурса.</p>\r\n<p>Специальные гости встречи: первый заместитель министра социальной политики, опеки и попечительства Иркутской области Макаров А. С., министр по молодежной политике Иркутской области Попов А. К., глава общественной палаты Шпрах В. В., представитель Министерства образования Иркутской области, заместитель министра спорта Иркутской области Богатырев П. А., начальник управления Губернатора Иркутской области и Правительства Иркутской области по связям с общественностью и национальным отношениям Куриленкова О. В., средства массовой информации.</p>\r\n<p>Ещё раз поздравляем всех победителей! Успехов в работе и достижения поставленных целей!</p>', '/images/news/FPG/9.jpg', '2018-11-13', '/images/news/FPG/1.jpg; /images/news/FPG/2.jpg; /images/news/FPG/3.jpg;  /images/news/FPG/4.jpg; /images/news/FPG/5.jpg; /images/news/FPG/6.jpg;\r\n/images/news/FPG/7.jpg; /images/news/FPG/8.jpg; /images/news/FPG/9.jpg;  /images/news/FPG/10.jpg; /images/news/FPG/11.jpg; /images/news/FPG/12.jpg;\r\n/images/news/FPG/13.jpg; /images/news/FPG/14.jpg; /images/news/FPG/15.jpg;  /images/news/FPG/16.jpg; /images/news/FPG/17.jpg; /images/news/FPG/18.jpg; /images/news/FPG/19.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', '18'),
(81, '2', 'Проект \"Расширяя границы\"', 'В сентябре 2018 года стартовал наш новый проект \"РАСширяя границы\"! Проект направлен на постепенную адаптацию детей с аутизмом к посещению общественных мест, а также на формирование и развитие у них социальных навыков.', 'В сентябре 2018 года стартовал наш новый проект \"РАСширяя границы\"! Проект направлен на постепенную адаптацию детей с аутизмом к посещению общественных мест, а также на формирование и развитие у них социальных навыков посредством организации культурных, спортивных и развлекательных мероприятий, в том числе инклюзивных.<br>\r\nНе секрет, что проблему социализации детей с аутизмом можно решить только с помощью общества, а точнее общественных мест и людей, живущих или работающих в этих местах.<br>\r\nОсенью нам открыл свои двери «Иркутский зоосад». Детей ждала встреча с дикими и домашними животными. Ребята отлично провели время на свежем отдыхе, кормили животных, качались на качелях и просто веселились.<br>\r\nВыражаем благодарность директору и сотрудникам Иркутского зоосада за доставленную радость от общения с прекрасными животными!<br>\r\nВ ноябре мы брали высоту в прямом смысле этого слова, посетив скалодром! Ребята быстро освоились, подключились к разминке с тренером и каждый \"покорял\" свою вершину! Спасибо огромное тренерам Веретениной Екатерине и Башкирцевой Ольге за это замечательное приключение!!!<br>\r\nВ декабре специально для детей и родителей \"Рассвета\" и их друзей прошла экскурсия в Иркутском областном историко-мемориальном музее декабристов -  Доме и усадьбе Волконских. Нам показали как жила семья декабриста, как работали и отдыхали в первой половине 19 века. Экскурсоводом была самая настоящая барышня, что помогало погружаться в атмосферу прошлых лет, с балами и кренолинами. Для детей провели игры, подобные тем, в которые играли два века назад.  Оказалось это достаточно азартно и весело. Огромное спасибо сотрудникам музея за приглашение, за интересный рассказ и экскурсию, за внимательное отношение к нашим семьям!\r\n<br>\r\nТакже в декабре в рамках проекта \"Расширяя границы\" состоялось адаптированное для детей с аутизмом представление \"Домок-Теремок\" в кукольном театре \"Аистёнок\". Благодарим сотрудников театра за благоприятную атмосферу и сказочное настроение.', '/images/dosug/2018/9.jpg', '2018-12-10', '/images/dosug/2018/1.jpg; /images/dosug/2018/2.jpg; /images/dosug/2018/3.jpg; /images/dosug/2018/4.jpg; /images/dosug/2018/5.jpg; /images/dosug/2018/6.jpg; /images/dosug/2018/7.jpg; /images/dosug/2018/8.jpg', '', '', '', '', 0, '', '0'),
(85, '16', 'Пополнение \"Копилки знаний\"', 'Команда АНО \"АПЦ РАСсвет\" во второй половине 2018 года снова пополнила \"копилку\" своих знаний. Подводим итоги!', '<p>Команда АНО \"АПЦ РАСсвет\" во второй половине 2018 года снова пополнила \"копилку\" своих знаний. Подводим итоги!</p>\r\n<p>В период с октября по декабрь включительно 9 человек прошли обучение по образовательной программе для родителей и начинающих специалистов (терапистов АВА) \"Курс обучения методу АВА для работы с детьми с аутизмом\". Кроме того, один специалист АНО \"АПЦ РАСсвет\" успешно окончил первый курс обучающей программы подготовки специалистов по Прикладному анализу поведения уровня ВСаВА \"Фундаментальные концепции, принципы и методы поведенческого анализа\".</p>\r\n\r\n<p>Екатерина Родыгина, Виктория Кузнецова, Виктория Иванова и Елена Шапилова - сертификаты с отличием за изучение курса \"Социальное проектирование в НКО\".</p>\r\n<p>Мария Зубарева - удостоверение о повышении квалификации по программе \"Тьютор в образовательной организации\".</p>\r\n<p>Екатерина Родыгина, Мария Максимова - сертификаты с отличием за изучение курса \"Поиск средств для НКО\".</p>\r\n<p>Екатерина Родыгина - сертификат за обучение по программе \"Организация социального обслуживания населения в Иркутской области: законодательный и организационный аспекты\".</p>\r\n<p>Михеева Елена - сертификат за обучение по программе \"Доступность. Качество. Непрерывность\" Совершенствование профессиональных педагогический компетенций.</p>\r\n\r\n<p>Поздравляем учредителей и специалистов \"Рассвета\" с успешным окончанием обучения!</p>\r\n\r\n<p>Более подробную информацию о достижениях членов нашей команды можно увидеть в разделе <a class=\"a-blue\" href=\"https://rassvet-apc.ru/team.php\" target=\"_blank\">КОМАНДА</a></p>', '/images/news/11.jpg', '2019-01-03', '', '', '/news.php', 'Новости', '', 0, '', '0'),
(86, '16', 'VI  церемония общественного признания \"Социальная звезда\"', '14 декабря 2018г  на VI церемонии общественного признания \"Социальная звезда\" председателю АНО \"АПЦ РАСсвет\" Родыгиной Екатерине торжественно вручили диплом победителя в конкурсе \"Миллион на добрые дела\".', '14 декабря 2018г  на VI церемонии общественного признания \"Социальная звезда\" председателю АНО \"АПЦ РАСсвет\" Родыгиной Екатерине торжественно вручили диплом победителя в конкурсе \"Миллион на добрые дела\". <br>\r\nЕкатерина представила проект \"Мини-группы  для детей с расстройствами аутистического спектра\", занявший I МЕСТО в номинации \"Безбарьерный Иркутск\".<br>\r\nТакже призёрами конкурса социально значимых проектов \"Миллион на добрые дела\" стали учредители АНО \"АПЦ Рассвет\".<br> II место заняли проекты Елены Михеевой (номинация \"Безбарьерный Иркутск\") и Марии Максимовой (номинация \"Здоровое поколение\"), III место - проекты Екатерины Никулиной, Елены Шапиловой (номинация \"Безбарьерный Иркутск\").\r\n<br>\r\nПодробнее с проектами призерами конкурса можно познакомиться на странице <a href=\"/pages/contests.php\">Победы в конкурсах</a>', '/images/news/MDD/4.jpg', '2018-12-14', '/images/news/MDD/3.jpg;\r\n/images/news/MDD/1.jpg;\r\n/images/news/MDD/6.jpg', '', '', 'Новости', '/news.php', 0, '', '0'),
(90, '13', '\"Аутизм – взгляд из другой реальности\"', 'Автор: Кира Ларина, источник: <a class=\"a-blue\" href=\"http://babr24.com/?IDE=184069&fbclid=IwAR2VAP5mmzLBqxbMJue_DCdCkKw8Fppc0uSrYS4GdJJQS4x_YmFphMTJnY4\" target=\"_blank\">\"Babr24.com\"</a><br>\r\nСреди нас живут те, кто лишен внутренней мотивации выполнять привычные для нас с вами действия. Это одна из особенностей детей с расстройством аутистического спектра (РАС или аутизм)...', '', '/images/smi/2.jpg', '2018-12-17', '', '', 'http://babr24.com/?IDE=184069&fbclid=IwAR2VAP5mmzLBqxbMJue_DCdCkKw8Fppc0uSrYS4GdJJQS4x_YmFphMTJnY4', 'Babr24.com, Кира Ларина', '', 1, '', '0'),
(91, '13', '\"Адаптационно-педагогический центр «Рассвет» открылся в Иркутске', 'Автор: Светлана Бурдинская, источник: \"Агентство социальной информации\" <a class=\"a-blue\" href=\"https://www.asi.org.ru/report/2018/03/22/irkutsk-adaptatsionnyj-tsentr-autizm-roditeli/\" target=\"_blank\">(asi.org.ru)</a><br>\r\nНовая автономная некоммерческая организация объединяет родителей детей с расстройствами аутистического спектра. Организация будет заниматься привлечением к сотрудничеству специалистов,готовых получать дополнительное образование в области прикладного анализа поведения.', '', '/images/smi/1.JPG', '2018-03-22', '', '', 'https://www.asi.org.ru/report/2018/03/22/irkutsk-adaptatsionnyj-tsentr-autizm-roditeli/', 'АСИ-Иркутск, Светлана Бурдинская', '', 1, '', '0'),
(93, '17', 'Поздравляем с Рождеством!', 'Поздравляем всех наших друзей со светлым праздником Рождества Христова! В подарок от нас перевод замечательной статьи Лиз Беккер \"Наслаждаясь магией Рождества – в стиле аутизма\". Перевод: Мария Зубарева ', '<p>Поздравляем всех наших друзей со светлым праздником Рождества Христова! В подарок от нас перевод замечательной статьи Лиз Беккер!</p>\r\n<p><b>\"Наслаждаясь магией Рождества – в стиле аутизма\"</b></p>\r\n<p>Автор: Лиз Беккер</p>\r\n<p>Перевод: Мария Зубарева, учредитель АНО \"АПЦ РАСсвет\"</p>\r\n<p>По мере приближения Рождества я не могу не думать о прошедших годах и о том, как мой сын Мэтт изменился в своем восприятии духа этого времени года. Мэтту почти 26 лет, так что есть над чем подумать. Размышляя обо всем нашем уникальном опыте, я задавалась вопросом: \"А другим родителям аутичных детей также весело?\"</p>\r\n<p>Я использую слово «веселье», потому что, глядя на эту ситуацию с моей точки зрения, из этого можно сделать такой замечательный фильм, я думаю, комедию об изучении истинного значения Рождества. Не поймите меня неправильно, несколько лет было слишком много семейной драмы. Некоторые из этих лет были фееричны в плане бюджета, в то время как другие заканчивались с очень ограниченными финансовыми средствами. Некоторые были полны слез - из-за всепоглощающей радости, а другие – полны слезами, потому что они были абсолютно душераздирающими. Но все, каждый из них, в итоге заканчивались теплом наполненного любовью дома, улыбками на детских лицах, наполненными животами и множеством незабываемых (и иногда довольно веселых) воспоминаний.</p>\r\n<p>В канун Рождества или незадолго до него мы всегда отправлялись в поездку, чтобы увидеть рождественские огни и украшения, и мы всегда дожидались, пока дети не уснут, чтобы положить игрушки и подарки под елку. Это было непростой задачей, так как каждый год в течение 10 лет Мэтт разбивал лагерь в прихожей со своей подушкой, одеялом и вентилятором (он всю жизнь спал с гудящим вентилятором у головы), чтобы дождаться прибытия Санты (возможно, мельком увидеть его) и нам приходилось проходить через эту блокаду, не разбудив его. Это была командная работа для двух человек - просто доставить посылки из точки А (шкаф в спальне) в точку Б (под деревом). Мэтт никогда не знал об этом, ни разу не просыпался, и это поддерживало магию Рождества, и иллюзия подарков, внезапно появляющихся под деревом, сохранялась до следующего сезона.</p>\r\n<p>Был год моего развода, когда мне нечего было дарить своим детям, не было денег на покупку подарков, не было украшенного и мерцающего дерева. Всего за несколько дней до Рождества друзья и семья собрались и принесли небольшую украшенную елку, подарили подарки моим детям и отвезли нас к себе домой на праздничное застолье. Всего за несколько дней до того, как Мэтт застал меня плачущей. В ответ он заполз ко мне на колени, положил руку мне на лицо и впервые произнес «мама». Это оказался один из лучших рождественских праздников в моей жизни.</p>\r\n<p>Было первое Рождество после появления аутизма в нашей жизни, когда Мэтт любил коробки больше, чем игрушки, которые в них были. Было несколько рождественских праздников, когда он изо всех сил боролся с Санта-Клаусом в торговом центре; было и другое Рождество – мы безрезультатно искали по магазинам в течение нескольких недель ту единственную игрушку, которую Мэтт хотел, и пришлось оставить ему записку под елкой, в которой обещали, что эта игрушка скоро у него появится. После того, как он научился писать, рождественские праздники стали намного лучше, потому что мы вдохновляли его писать Санте (ну, этот бородатый парень, с которым он изо всех сил боролся год назад). Эти письма дали нам представление о том, чего он действительно желал, и давали нам задание получить указанные предметы заранее, чтобы убедиться, что больше не придется оставлять под елкой записку. За эти годы Мэтт просил широкий ассортимент товаров: строительные блоки и наборы Lego, черепашки-ниндзя, охотники за привидениями, транспортные средства из Парка юрского периода, фигурки из «Истории игрушек», видеокассеты с записями «Паровозика Томаса» и аксессуары для поездов, модели космических челноков, модели самолетов, колесо обозрения, велосипед, видеокассеты с фильмом «Один дома» и художественные принадлежности, книги, одежда (в подростковом возрасте он осознал свою внешность и действительно хотел одежду!), а также спортивный инвентарь. Посмотрите на этот список еще раз - ничто в нем не говорит об аутизме... ничего похожего.</p>\r\n<p>Церковные спектакли и концерты были встречены истериками и срывами - Мэтт ненавидел громкость музыки и, хотя был увлечен пьесами, не хотел принимать участие в роли одного из пастухов. Вместо этого Мэтт ползал под скамьями или ходил по периметру или исследовал любой уголок церкви, куда он мог добраться. Это означало, что я постоянно искала, где находится Мэтт, пытаясь наблюдать за выступлениями других наших детей. И были моменты, когда мне приходилось что-то упускать – строчку, песню, часть праздника другого моего ребенка, чтобы утешать плачущего Мэтта или бежать за ним. Но дети всех возрастов время от времени капризничают или чего-то боятся, и их нужно утешать. Готова поспорить, что большинство родителей в то или иное пропускают какие-то дела своих детей. Это проблема не аутизма – это проблема детства вообще.</p>\r\n<p>И хотя визиты к Санте были травмирующими в детском возрасте, у Мэтта, наконец, возникло желание поговорить с большим парнем в красном костюме, и это почти мгновенно превратилось в обязательный ритуал. Действительно ли имело значение, что ему сейчас 10-12 лет, и он в два раза выше других детей в очереди? Имеет ли значение, что он будет только стоять рядом с Сантой и говорить, а не сидеть на коленях? Это никогда не волновало Санту, это никогда не волновало Мэтта и никогда не волновало нас.</p>\r\n<p>И вот однажды это случилось - Мэтт больше не хотел видеть Санту. Он стал скептически относиться к волшебной сказке и ему стало нужно что-то еще. Наконец-то пришло время. Мы все знаем, что это произойдет, и мы все знаем, что в конечном итоге мы должны сообщить новость нашим детям, но то, как вы это делаете, имеет значение – огромное значение. Как рассказать об этом детском обмане – то, о чем я думала годами – буквально. Когда пришло время сесть и поговорить, я была готов. Я сказала ему, что маленькие дети верят в Санта-Клауса, потому что это волшебно и весело. Я сказала ему, что когда ребенок становится взрослым, и начинает понимать, то что-то не так – это потому, что дети становятся умнее. Я сказала ему, что Санта – это взрослые, и когда дети становятся взрослыми, их работа заключается в том, чтобы держать\r\nволшебство в секрете и быть Сантой для других. Мэтту особенно понравилась эта идея. Он будет тайным Сантой и сохранит волшебство – он принял эту ответственность близко к сердцу.</p>\r\n<p>После нашей беседы, каждый год Мэтт становится олицетворением рождественского духа. Он охотится за подарками для каждого члена семьи, заворачивает их и кладет под елку. Мэтт покупает подарок даже для себя. Он заворачивает его в обертку и надевает на него метку – «Мэтту от Санты» - и в рождественское утро он открывает подарок, который он сам обернул, и удивительно удивлен и взволнован, находя именно то, что он хотел. Его радость от просмотра Чарли Брауна и Снупи, Гринча и Рудольфа заразительна, также как и его волнение от украшения дерева, гирлянды и упаковки подарков. Мэтт освещает наш дом и наши сердца. Все остальные наши дети выросли и переехали в свои дома и создают свою атмосферу волшебства. Тем не менее, даже когда наши дети выросли и наш дом почти пуст, у нас все еще много рождественского духа, освещающего каждую темную трещинку и щелочку. Мэтт каждый день напоминает нам о том, что важнее всего любовь – не подарки, или мерцающие огни, или рождественские песни, а глубокое желание дарить подарки, желание показывать огни и петь песни, то, что делает всё это таким волшебным. Аутизм не мешает появлению желаний и не препятствует ощущению любви. Аутизм – это просто поверхность. То, что находится внутри каждого из нас – вот что имеет значение, а не то, аутист ты или нет. Для меня и моего мужа Мэтт делает Рождество Рождеством.</p>\r\n<p>Я знаю, это звучит слишком мило и пушисто для кого-то из вас. Уверяю вас, так было не всегда. Мы чувствовали отчужденность дальних родственников, постоянное давление стресса, и преодолевали препятствия, которые возникали почти ежедневно из ниоткуда. Но это было «тогда». Что-то изменилось за это время, и это был не только Мэтт – но и я тоже. Теперь я та, кто катается с ним на волнах аутизма, находя в каждом опыте что-то смешное и смеясь над конформистской частью меня, которая раньше думала, что все должно быть просто идеально. Теперь я понимаю, что жизнь на самом деле такая, какой мы ее делаем, – разве с Рождеством должно быть иначе?</p>\r\n<p>Наслаждайтесь причудами, переживите стресс и будьте готов оглянуться назад и посмеяться. Подумайте, какие истории вы могли бы рассказать!</p>\r\n<p>Мэтт только что закончил упаковывать еще один подарок, который он купил для себя, и положил его под елку. Я не могу дождаться, чтобы увидеть его удивленное и радостное выражение лица, когда он откроет этот же подарок рождественским утром! Это Рождество – в стиле аутизма.</p>\r\n<p><a class=\"a-blue\" href=\"http://www.autismsupportnetwork.com/news/enjoying-magic-christmas-autism-style-3827882\">Ссылка на источник</a></p>', '/images/news/12.jpg', '2019-01-06', '', '', '', 'Новости', '/news.php', 0, '', '0'),
(94, '13', '\"Аутизм – диагноз, а не приговор\"', 'Автор: Кира Ларина, источник: <a class=\"a-blue\" href=\"http://babr24.com/?IDE=184684\" target=\"_blank\">\"Babr24.com\"</a><br>\r\nЕсли стоять на одном месте, то большая вероятность никуда не прийти. Так и с любым заболеванием, если пустить все на самотек, то ни о каких улучшениях говорить не придется. В корне неверно смотреть на расстройство аутистического спектра (РАС или аутизм) у детей как на вечное клеймо и не решаемую проблему...', '', '/images/smi/3.JPG', '2019-01-07', '', '', 'http://babr24.com/?IDE=184684', 'Babr24.com, Кира Ларина', '', 1, '', '0');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(102, '13', '\"Аутизм: помощь в действии\"', 'Автор: Кира Ларина, источник: <a class=\"a-blue\" href=\"http://babr24.com/?IDE=184941\" target=\"_blank\">\"Babr24.com\"</a>, <br>\r\nКопипаст <a class=\"a-blue\" href=\"https://irkutsk.news/novosti/2019-01-15/89792-autizm-pomosch-v-deistvii.html?amp=1\" target=\"_blank\">\"Irkutsk NEWS\"</a> <br>\r\n«Хочешь помочь своему ребенку – начинай действовать» - именно этого принципа придерживаются в «Адаптационно-педагогическом центре «РАСсвет»...', '', '/images/smi/4.JPG', '2019-01-24', '', '', 'http://babr24.com/?IDE=184941', 'Babr24.com, Кира Ларина', '', 1, '', '0'),
(104, '4', 'Обзор занятий в мини-группах за 2018 год', 'Проект Екатерины Родыгиной \"Мини-группы для детей с РАС\" стал победителем конкурса \"Миллион на добрые дела\".\r\nЗанятия в мини-группах основаны на мотивации детей с аутизмом и их общих увлечениях, в которых непосредственное участие принимают родители.', 'Проект Екатерины Родыгиной \"Мини-группы для детей с РАС\" стал победителем конкурса социально-значимых проектов <a href=\"/news/detail.php?id=86&back=news\" target=\"_blank\">\"Миллион на добрые дела\"</a> в номинации \"Безбарьерный Иркутск\".<br>\r\nЗанятия в мини-группах основаны на мотивации детей с аутизмом и их общих увлечениях, в которых непосредственное участие принимают родители. Так как детям с аутизмом сложно даётся общение с окружающими, то проект в первую очередь направлен на обучение детей взаимодействовать друг с другом, а в некоторых случаях преодолевать страх общения со сверстниками. В основе работы над формированием взаимодействия детей с аутизмом заложены принципы Прикладного анализа поведения. <br>\r\nПредставляем вашему вниманию обзор групповых занятий для детей с аутизмомо за 2018 год.<br>\r\nГрупповые занятия проводятся в мини-группах по 2-3 человека. Ребята учатся групповой работе, сотрудничеству, соблюдению очередности в играх, выполняют несложные групповые задания. На занятиях мамы выступают и в роли инструкторов, и в роли тьюторов.<br>\r\nВажные правила работы с детьми с РАС - системность и непрерывность. И они позволяют добиться хороших результатов!<br> Вот некоторые из них:\r\nЕще полгода назад Игорь и Ваня не могли заниматься вместе, т.к. Игорь избегал любого взаимодействия с другими детьми. \r\nПеред нами стояла задача научить детей сотрудничать и заниматься совместной деятельностью. И мы этого добились!<br>\r\nАдаптация Ники к новому помещению всегда проходит непросто, а ведь за время реализации проекта мы дважды меняли место дислокации. Длительное время Ника могла заниматься только в индивидуальном формате, но сейчас постепенно включается в первые групповые занятия. Основной упор на занятиях мы делаем на организацию совместных мотивационных заданий для детей, с чем Ника стала уже отлично справляться!<br>\r\nПоложено начало во взаимодействии Вани и Тимы. Теперь ребята с удовольствием садятся за совместное занятие, активно сотрудничают, помогают другу другу и играют вместе в перерывах между сессиями. Правда пока еще эта игра носит полевой характер, но в перспективе, когда у ребят сформируется более прочное привыкание друг к другу, мы планируем внести структуру и в их игру!<br>\r\nПроект \"Мини-группы\" для детей с аутизмом не только позволил детям с РАС освоить и обобщить навыки взаимодействия со сверстниками, уменьшить количество фобий, расширить игровой репертуар, но и сплотил их родителей и дал им возможность получить новые практические умения по работе со своими детьми.', '/images/group/2018/2018-12.jpg', '2018-12-20', '/images/group/2018/1.jpg;\r\n/images/group/2018/2.jpg;\r\n/images/group/2018/3.jpg;\r\n/images/group/2018/4.jpg;\r\n/images/group/2018/5.jpg;\r\n/images/group/2018/6.jpg;\r\n/images/group/2018/7.jpg;\r\n/images/group/2018/8.jpg;\r\n/images/group/2018/9.jpg;\r\n/images/group/2018/10.jpg;\r\n/images/group/2018/11.jpg;\r\n/images/group/2018/12.jpg', '', '', '', '', 0, '', ''),
(122, '12', 'Проект \"Фитнес для детей с РАС\"', 'В январе 2019 стартовал проект «Фитнес для детей с аутизмом». Занятия проходят в индивидуальной форме, со сбором данных и оценкой навыков, пишутся программы с отслеживанием динамики развития. В проекте принимают участие дети и подростки с различным уровнем возможностей.', '<p>В январе 2019 стартовал проект «Фитнес для детей с аутизмом». Занятия проходят в индивидуальной форме, со сбором данных и оценкой навыков, пишутся программы с отслеживанием динамики развития. В проекте принимают участие дети и подростки с различным уровнем возможностей.</p>\r\n<p>Занятия проводит Кузнецова Виктория Владимировна, дипломированный педагог-хореограф, инструктор групповых и индивидуальных фитнес-программ, получившая дополнительное образование по программам: \"Сопровождение ребенка с особенностями развития и его семьи\" (Центр Лечебной Педагогики г.Москва); \"Основы деятельности тьютора в работе с детьми с ОВЗ, 240 часов\" (АНМЦ \"Развитие и коррекция\", г.Москва); \"Адаптивный фитнес для детей с аутизмом\" (David S. Geslak, Exersice Connection\'s Challenging Autism); \"Курс обучения методу АВА для работы с детьми с аутизмом\" (Юлия Эрц, МА, ВСВА, Израиль). А также мама ребенка с аутизмом.</p>\r\n<p></p>\r\n<p>Идея проекта возникла почти спонтанно. Действительно, зачем специалисту просто так сидеть, раз есть все возможности такой проект запустить? Тренер есть, дети есть, оборудование в магазине, да и по сусекам поскребли и много всякого спортивного инвентаря наскребли, а то лежит, пылится, а тут дело нужное и веселое. А уж про полезность мы не будем, кто не знает, что спорт – это Здоровье? Думали-думали и приступили. И на деле выяснилось, что, как обычно, у страха глаза велики, можно было и раньше начать.</p> \r\n<p>Задумались, почему фитнес? И правда, звучит непривычно. Давайте немного разберемся: мы используем разнообразное оборудование и совершенно убеждены, что среда, обогащенная визуально и тактильно, подталкивает ребят с аутизмом заниматься с желанием. Кроме того, понимая пристрастие таких детей к получению сенсорных ощущений и нуждаемость в визуальной опоре, мы убедились, что скромным наименованием «Физкультура» мы никак не обойдемся. Ведь мы делаем все! Мы играем, бегаем по эстафетным препятствиям, осваиваем тренажеры и езду на беговеле, работаем по визуальным станциям, поднимаем вес в соответствии с физическими возможностями. В планах приобрести беговую дорожку, велотренажер, да и много чего. Силу, ловкость развиваем. И, конечно же, никуда без знаний в области Прикладного анализа поведения – слава Богу он есть и он в помощь.</p>\r\n<p>Всех секретов «фирмы» пока не раскрываем, оставляем за собой право интриги и профессиональной этики, а вот познакомиться с участниками нашего проекта настоятельно рекомендуем! </p>', '/images/AFK/fit/9.jpg', '2019-03-22', '/images/AFK/fit/1.jpg;\r\n/images/AFK/fit/2.jpg;\r\n/images/AFK/fit/5.jpg;\r\n/images/AFK/fit/4.jpg;\r\n/images/AFK/fit/8.jpg;\r\n/images/AFK/fit/10.JPG', '', '', '', '', 0, '', '0'),
(126, '22', 'Благодарим за подготовку и проведение фотосессии!', 'АНО АПЦ \"Рассвет\" готовится ко 2 апреля - \"Всемирному дню распространения информации об аутизме\". Выражаем огромную благодарность фотостудии \"Сreo\" за праздничное оформление нашего центра и фотографу Анастасии Кадыровой за проведение фотосессии! ', '<p>АНО АПЦ \"Рассвет\" готовится ко 2 апреля - \"Всемирному дню распространения информации об аутизме\". Специально к этой дате в нашем центре прошла фотосессия в поддержку людей с аутизмом.</p> <p>Выражаем огромную благодарность фотостудии \"Сreo\" и лично  Екатерине Медведевой и Марии Сокольниковой за подготовку помещения и красочное оформление!</p>   \r\n<p>А также благодарим фотографа Анастасию Кадырову за проведение фотосессии для наших семей!</p>', '/images/news/01.04.19/7.jpg', '2019-04-01', '/images/news/01.04.19/1.jpg;\r\n/images/news/01.04.19/2.jpg;\r\n/images/news/01.04.19/3.jpg;\r\n/images/news/01.04.19/5.jpg;\r\n/images/news/01.04.19/6.jpg;\r\n/images/news/01.04.19/7.jpg;\r\n/images/projects/02.04.19/1.jpg;\r\n/images/projects/02.04.19/2.jpg;\r\n/images/projects/02.04.19/3.jpg;\r\n/images/projects/02.04.19/4.jpg;\r\n/images/projects/02.04.19/5.jpg;\r\n/images/projects/02.04.19/6.jpg;\r\n/images/projects/02.04.19/7.jpg;\r\n/images/projects/02.04.19/8.jpg;\r\n/images/gratitude/32.jpg;\r\n/images/gratitude/31.jpg', '', '', 'Новости', 'news.php', 0, '', '0'),
(128, '5', 'Дети как дети!', 'Мы знаем об аутизме не понаслышке. Люди, которые никогда не сталкивались с аутизмом лично, часто думают, что дети с этим недугом сидят в углу, смотрят в одну точку и раскачиваются. Мы утверждаем, что это не так. Аутизм - это спектр. Все люди с РАС (расстройства аутистического спектра) очень разные.', '', '/images/projects/02.04.19/1.jpg', '2019-04-02', '', '', '/projects/02-04-19.php', '', '', 1, '', '0'),
(129, '13,5', 'Аутизм: пришло время говорить', 'Автор: Кира Ларина, источник: <a class=\"a-blue\" href=\"http://babr24.com/?lDE=187451\" target=\"_blank\">\"Babr24.com\"</a><br>\r\n2 апреля - Всемирный день информирования об аутизме (World Autism Awareness Day). Соответствующее постановление Генеральная Ассамблея ООН подписала еще в декабре 2007 года.\r\n\r\nИменно в этот день по всему миру проходят различные мероприятия с целью привлечения внимания людей к проблемам детей и взрослых с диагнозом аутизм. Среди организованных на тему аутизма круглых столов, дискуссий и семинаров, выделяется международная акция «Зажги синим».', '', '/images/smi/6.jpg', '2019-04-02', '', '', 'http://babr24.com/?lDE=187451', 'Babr24.com, Кира Ларина', '', 1, '', '0'),
(132, '17', 'Терапия погружением в виртуальную реальность', 'Предлагаем вашему вниманию перевод статьи \"Терапия погружением в виртуальную реальность демонстрирует длительный эффект в лечении фобий у детей с аутизмом\". Источник: Newcastle University. \"Immersive virtual reality therapy shows lasting effect in treatment of phobias in children with autism.\" ScienceDaily, 14 February 2019. Перевод: Мария Зубарева', '<p><b>\"Терапия погружением в виртуальную реальность демонстрирует длительный эффект в лечении фобий у детей с аутизмом\"</b></p>\r\n<p>Источник: <a class=\"a-blue\" href=\"https://www.sciencedaily.com/releases/2019/02/190214191939.htm\">Newcastle University. \"Immersive virtual reality therapy shows lasting effect in treatment of phobias in children with autism.\" ScienceDaily, 14 February 2019</a></p>\r\n<p>Перевод: Мария Зубарева, учредитель АНО \"АПЦ РАСсвет\"</p>\r\n<p>Новое исследование показывает, что среда виртуальной реальности с эффектом погружения помогает 45 процентам детей, страдающих аутизмом, освобождая их от страхов и фобий, и что такая терапия имеет долгосрочный эффект.</p>\r\n<p>Доказано, что виртуальная реальность помогает детям с аутизмом, и почти 45% из них остаются свободными от своих страхов и фобий через шесть месяцев после лечения.</p>\r\n<p>Кроме того, отдельное исследование впервые показало, что такое лечение работает для некоторых аутичных взрослых.</p>\r\n<p>«Голубая комната», разработанная специалистами Университета Ньюкасла, работающими совместно с фирмой-разработчиком инновационных технологий Third Eye NeuroTech, позволяет команде создать персонализированную объемную (360 градусов) среду, в которую включается страх, который может мучить человека с аутизмом в реальной жизни.</p>\r\n<p>Используя элементы управления iPad, в этой виртуальной среде человек может с комфортом перемещаться и изучать различные сценарии, работая с терапевтом, при этом полностью контролируя ситуацию. Такая виртуальная среда не требует специальных очков.</p>\r\n<p>Результаты исследований, финансируемых Национальным институтом исследований в области здравоохранения (NIHR), опубликованы сегодня в двух статьях в журнале «Аутизм и нарушения развития» (Journal of Autism and Developmental Disorders) и в «Аутизм в зрелом возрасте» (Autism in Adulthood).</p>\r\n<p>«Для многих детей и их семей тревога может управлять их жизнью, поскольку они пытаются избежать ситуаций, которые могут вызвать страхи или фобию их ребенка», - говорит профессор Джереми Парр (Jeremy Parr), руководитель исследования из Института нейробиологии Университета Ньюкасла.</p>\r\n<p>«Возможность предложить лечение NHS, которое работает, и увидеть, что и так хорошо справляются, дает надежду семьям, у которых очень мало вариантов лечения от тревожности».</p>\r\n<p>Аутизм может повлиять на обучение и развитие ребенка, часто приводя к нарушению социальных и коммуникативных навыков. Многие дети с аутизмом также испытывают страхи или фобии, которые могут мучить их, но часто это упускается из виду. Считается, что от таких фобий страдает около 25% детей с аутизмом. В проведенном эксперименте рассматривались такие фобии, как: поездки на общественном транспорте, школьные классы, собаки и воздушные шары.</p>\r\n<p>Эксперты Университета Ньюкасла описывают рандомизированное контролируемое исследование с участием 32 детей с аутизмом в возрасте от 8 до 14 лет. Половина из них сразу же получила лечение в «Голубой\r\n\r\nкомнате», а половина выступала как контрольная группа, получив отсроченное лечение через шесть месяцев.</p>\r\n<p>В сопровождении психолога они проходили четыре сеанса в неделю по индивидуальному сценарию в «Голубой комнате». Родители смогли наблюдать за процессом по видеосвязи.</p>\r\n<p>«Людям с аутизмом может быть трудно представить себе какую-либо ситуацию, поэтому «Голубая комната» так хорошо воспринимается. Мы под контролем представляем им ситуацию, связанную с их страхом, посредством виртуальной реальности, и мы сидим рядом с ними, чтобы помочь им научиться управлять своими страхами», - объясняет доктор Мораг Маски (Dr Morag Maskey), исследователь из Института нейробиологии Университета Ньюкасла.</p>\r\n<p>«Невероятно приятно видеть, какой эффект это может иметь для некоторых детей, преодолевая ситуацию, которая всего неделю назад вызывала такой стресс».</p>\r\n<p>При поддержке родителей после получения лечения детям были предложены аналогичные сценарии в реальном мире.</p>\r\n<p>Через две недели после лечения исследование показало, что четверо из первых 16 детей (25%) положительно отреагировали на лечение и смогли справиться с определенной фобией. Этот эффект остался в общей сложности у шестерых, показав улучшение по истечении шести месяцев (38%), однако один участник сообщил об усилении его фобии. Между тем, в контрольной группе у пяти участников, не прошедших терапию, за шесть месяцев фобии усилились.</p>\r\n<p>После этого контрольная группа была отправлена на терапию в «Голубой комнате». Результаты показали, что в целом у 40% детей, получавших лечение, улучшение наступило в течение 2 недель, а у 45% - в течение 6 месяцев.</p>\r\n<p>Такие успехи сопоставимы с другими методами лечения, и команда намерена дополнительно изучить, почему некоторые участники не реагируют положительно на терапию.</p>\r\n<p><b>«Голубая комната» для взрослых с аутизмом</b></p>\r\n<p>Впервые терапия в «Голубой комнате» была предложена взрослым людям с аутизмом. В отдельной публикации в журнале «Аутизм в зрелом возрасте» (Autism in Adulthood) той же командой исследователей было показано, что терапия виртуальной реальностью работает у пяти из восьми взрослых с аутизмом.</p>\r\n<p>Взрослые в возрасте от 18 до 57 лет получили четыре 20-минутных сеанса в «Голубой комнате» с персонализированным с помощью компьютера сценарием. Через шесть месяцев после сеансов терапии у пяти из восьми участников все еще наблюдались реальные ежедневные улучшения в отношении их страхов и фобий.</p>\r\n<p>Д-р Раджеш Надкарни (Dr Rajesh Nadkarni), исполнительный медицинский директор в Фонде «Northumberland, Tyne and Wear NHS Foundation», сказал: «Мы гордимся тем, что являемся партнером программы терапии «Голубая комната» Ньюкасла, которая помогает людям с аутизмом справляться со своими тревогами. Фонд «Northumberland, Tyne and Wear NHS Foundation Trust» имеет большой опыт в предоставлении национально признанных услуг в сфере аутизма, и мы приветствуем это новое исследование, демонстрирующее положительные эффекты этого инновационного лечения».</p>\r\n<p>Эдди Нельсон – директор «Third Eye NeuroTech», расположенной в графстве Дарем компании, занимающейся технологиями иммерсивной реальности. Эта компания предоставляет «Голубую комнату». Он говорит: «Редко, как бизнес, мы получаем возможность помочь молодым людям и их семьям таким драматичным и ощутимым образом. Но то, что мы видим в «Голубой комнате», - это очень взволнованные молодые люди и взрослые вначале, но после прохождения четырех этих специализированных сеансов они выходят, поборов свои страхи».</p>\r\n<p>Конкретные фобии, которые были рассмотрены в ходе терапии в «Голубой комнате», включали в себя: собак, ос/пчел, лифты, боязнь темноты, полетов, кукол, воздушных шаров, общественного транспорта, школы, захода в помещения.</p>\r\n<p>Наряду со службой Фонда NHS команда Университета Ньюкасла продолжает дальнейшие исследования эффективности и долгосрочного эффекта терапии «Голубой комнаты».</p>\r\n<p>Источник истории: материалы предоставлены Университетом Ньюкасла. https://www.ncl.ac.uk/</p>\r\n', '/images/TRANSLATE/1.jpg', '2019-04-05', '', '', '', 'Новости', '/news.php', 0, '', '0'),
(136, '17', 'Аутизм, маленький мальчик, и никто не виноват', 'Новый перевод статьи от учредителя АНО \"АПЦ Рассвет\" Марии Зубаревой. <br>Автор: Tulika Prasad. <br>Источник: www.autismsupportnetwork.com', '<p>Автор: Tulika Prasad.</p>\r\n<p>Источник: <a class=\"a-blue\" href=\"http://www.autismsupportnetwork.com/news/autism-little-boy-and-nobodys-fault-877832\">www.autismsupportnetwork.com</a></p>\r\n<p>Перевод: Мария Зубарева, учредитель АНО \"АПЦ РАСсвет\"</p>\r\n<p>Несколько недель назад мы были в крытом аквапарке. В бассейне, в котором мы плавали, было установлено баскетбольное кольцо, где играли несколько других детей. Мой сын был счастлив, просто наблюдая за их игрой, и снова и снова прыгал от восхищения.  Тогда один из ребят спросил, не хочет ли он присоединиться к ним. Мальчик был добрым и искренним. Мой сын никак не отреагировал. Малыш немного подождал, а затем двинулся дальше и продолжил играть со своей компанией. Мы перешли в другой бассейн, который привлек внимание моего сына. Позже тем вечером я вспоминала этот эпизод и задалась вопросом, на чьей стороне я была – на стороне моего сына, неспособного ответить из-за своего аутизма, несмотря на то, что он был явно заинтересован; или на стороне того мальчика, который пытался быть дружелюбным, а затем отступил. Кто виноват в том, что мой маленький мальчик не был ни в одной из тех стихийных игровых групп, в которых, я знаю, он так отчаянно хотел бы принимать участие? Интересно, что нужно исправить – моего сына или мир вокруг него?</p>\r\n<p>Каждый год я наблюдаю, как мой сын стоит рядом со своим праздничным тортом в день рождения, без поддержки друзей; я вижу его в его школе, идущего наедине со своим помощником, никакого «сообщника», который шепотом делится секретами, сидя на скамейке в кафе; в  уединении, вероятно, чтобы избежать сенсорной перегрузки, в то время как остальные его одноклассники рассматривают содержимое ланч-боксов друг друга. Я вижу его на семейных встречах и вечеринках, бесцельно валяющимся на диване в углу, вдали от остальных детей, которые толпятся вокруг бездомной собаки или притворяются пиратами. Я вижу его в одиночестве, без друзей; нет совместных игр, нет «домиков-укрытий», нет гонок на велосипедах, нет ничего. Я видела блеск в его глазах, когда к нему приближался ребенок, а затем пустоту, которая возникает, когда он не может общаться дальше. Я видела, как к нему подходили любопытные, дружелюбные и добрые дети, приветствовали его, делали ему комплименты или приглашали его присоединиться к ним. Эти дети хотели познакомиться его, протянуть ему руку, но молчание, которым аутизм наполнил жизнь моего сына, лишает его этой возможности. Кто виноват в одиночестве моего сына? Дети, которые пытались пообщаться и затем сдались, или аутизм моего сына?</p>\r\n<p>Когда я вошла в магазин с Айпадом, который видом своего разбитого экрана просто кричал о том, что с ним плохо обращались, молодой человек за прилавком спросил, что случилось. Я спокойно сказал, мой сын бросил его вниз по лестнице. Ему нравится смотреть, как вещи падают. Мужчина был ошеломлен моей беспечностью и выпалил: «Что за негодяй! Тебе нужно поговорить с ним об этом!» Мама внутри меня тут же встала на защиту. Я объяснила, что у моего сына аутизм и он невербален, и что навык понимания речи все еще находится в процессе освоения. Он, вероятно, либо проигнорировал эту информацию, либо не был достаточно осведомлен, потому что следующее, что он сказал, было: «Я бы отшлепал этого ребенка. Так он научится». Я не знала, что ответить – посмеяться над его полным невежеством или ужаснуться его предложению.</p>\r\n<p>Интересно, что другие люди вокруг могут подумать о моем сыне - негодяй? Избалованный? Наглый? Этот аутичный ребенок, который изо всех сил пытается осмыслить окружающий его мир и сталкивается с различными сенсорными проблемами каждую минуту каждого дня, помечен как «негодяй» не по своей вине. Но в то же время не виноват и тот продавец, который совершенно ничего не знал об аутизме или о том, что он означает.</p>\r\n<p>Я не могу утверждать, что знаю все о каждом диагнозе, поэтому не могу ожидать того же от других. Я бы ожидала немного больше сдержанности в его комментариях, но это к делу не относится. Факт в том, что каждый раз, когда у моего сына случается срыв, вполне вероятно, на него смотрит пара чьих-то глаз и осуждает его. Факт в том, что когда у него происходит сенсорный перегруз, он может подойти очень близко к вам или вступить в физический контакт, что может заставить вас назвать его чудаком. И это заставляет меня задуматься, кто виноват в этом – аутизм моего сына, который заставляет его меньше контролировать то, что он чувствует или делает, или полное невежество людей вокруг него, людей, от которых я не должна ожидать, что они поймут, что у него есть диагноз, просто посмотрев на него?</p>\r\n<p>Над моим мальчиком смеялись, его игнорировали, осуждали, унижали и оставляли без внимания, потому что у него диагноз, который он не может контролировать. Его также любили и ценили люди, которые добры и верят в него. Я видела и хорошее, и плохое. И он испытал это. Ему всего десять лет!</p>\r\n<p>Время от времени я сталкиваюсь с ситуациями, когда я стою в стороне, смотря на ситуацию с разных сторон, пытаясь понять, какая сторона оправдывает то, что произошло. У моего сына есть масса проблем, которые делают его уникальным, его потребности – другими, его навыки – разрозненными. Это также заставляет окружающий мир реагировать на него совершенно по-разному: одних – хорошо, других – не очень; некоторые – бесчувственны, некоторые – поддерживают, кто-то – информирован, другие – невежественны. Я не знаю, кого винить за причиненную боль. </p>\r\n<p>У моего сына аутизм, и это не его вина. Это и не моя вина, и ни чья либо еще. А когда никто не виноват, это очень сложная ситуация.</p>\r\n', '/images/TRANSLATE/2.bmp', '2019-04-12', '', '', '', 'Новости', '/news.php', 0, '', '0'),
(141, '3', 'Серия семинаров в рамках проекта \"Центр ПАП\"', '27 апреля 2019 года состоялся заключительный семинар АНО \"АПЦ Рассвет\" из серии мероприятий, осуществляемых в рамках проекта \"Центр прикладного анализа поведения для детей с расстройствами аутистического спектра\" при поддержке Фонда Президентских Грантов.', '<p>27 апреля 2019 года состоялся заключительный семинар АНО \"АПЦ Рассвет\" из серии мероприятий, осуществляемых в рамках проекта \"Центр прикладного анализа поведения для детей с расстройствами аутистического спектра\" при поддержке Фонда Президентских Грантов.</p>\r\n<p>Проект направлен на подготовку специалистов в области Прикладного анализа поведения с последующим оказанием эффективной помощи детям с РАС и их семьям. А одной из задач проекта является распространение информации об эффективных, опирающихся на научные принципы, методиках помощи людям с РАС среди родителей и специалистов, работающих с детьми с РАС. </p>\r\n<p>Первый семинар в рамках проекта состоялся 24 ноября. В семинаре участвовали 59 человек из числа родителей и специалистов, работающих с детьми с аутизмом. Спикер Елена Шапилова, член Правления АНО \"АПЦ РАСсвет\", рассказала аудитории о базисных принципах Прикладного анализа поведения и как они помогают решать проблемы в обучении детей с расстройствами аутистического спектра.</p>\r\n<p>Второй семинар \"Сказочное поведение\" состоялся 22 декабря. Спикер Екатерина Родыгина - председатель АНО \"АПЦ РАСсвет\" рассказала аудитории о том, что такое поведение и чем оно отличается от черты характера или состояния, о том, как определить функцию поведения у ребенка с расстройством аутистического спектра на примерах сказочных персонажей.</p>\r\n<p>В январе 2019 года ведущая семинара - Никулина Е.С., учредитель АНО \"АПЦ РАСсвет\", учитель-логопед (2 модуля сертифицированной программы подготовки специалистов по прикладному анализу поведения уровня ВСаВА) рассказала аудитории об использовании визуального расписания для детей с РАС.</p>\r\n<p>Семинар посетили 55 человек. Мы смогли довольно подробно поговорить о том, что такое визуальное расписание и где оно используется, каковы преимущества у этого метода и как начать использовать визуальное расписание. Как обычно, закрепили материал видео-примерами работы с детьми и с непосредственным участием слушателей семинара продемонстрировали способы введения расписания.</p>\r\n<p>Тема февраля \"Использование альтернативной системы коммуникации\" вызвала большой интерес у специалистов, работающих с детьми с РАС, и родителей. Семинар посетили 74 человека, среди которых нам было приятно видеть и постоянных слушателей и новых участников. Спикеры Елена Михеева и Екатерина Никулина поделились с аудиторией своим опытом использования системы альтернативной коммуникации с помощью карточек (PECS) и системы жестов.</p>\r\n<p>Мартовский семинар был в два раза полезнее! Ведь мы обсудили сразу две важных темы! Рассказали слушателям о побочных эффектах наказаний и подняли очень актуальную тему в нашей области: \"Ресурсный класс для детей с аутизмом\".\r\nБлагодарим спикеров семинара: Родыгину Е.А. - председателя АНО \"АПЦ РАСсвет\" и нашу гостью из Красноярска Пискун С.В. - генерального директора детского благотворительного фонда \"Живое дыхание\" за доступную и содержательную информацию.</p>\r\n<p>И на заключительном семинаре, разбирая тему \"Расширение мотивационной сферы\",  спикер семинара Никулина Екатерина Сергеевна ответила на следующие вопросы:</p>\r\n<p>Что такое мотивационная сфера? Для чего нужны поощрения? Как определить, что является мотивационным стимулом для ребенка? Что такое жетоны и при чем здесь мотивация? Как увеличить количество поощрений и зачем это нужно?</p>\r\n<p>Мы благодарны всем слушателям за проявленный интерес к нашим семинарам и будем с нетерпением ждать следующих встреч! Благодарим наших партнёров, библиотеку им. Молчанова-Сибирского, за возможность реализовать данное мероприятие!</p>', '/images/seminar/27.04.19/21.jpg ', '2019-04-27', '/images/seminar/27.04.19/1.jpg; /images/seminar/27.04.19/2.jpg; /images/seminar/27.04.19/3.jpg; /images/seminar/27.04.19/4.jpg; /images/seminar/27.04.19/5.jpg; /images/seminar/27.04.19/6.jpg; /images/seminar/27.04.19/7.jpg; /images/seminar/27.04.19/8.jpg', '', '', '', '', 0, '', '18'),
(142, '4', 'АВА для всех', 'АВА для всех? Да, именно так! Прикладной анализ поведения дает быстрые и устойчивые результаты в работе с нормотипичными детьми.', 'АВА для всех? Да, именно так! Прикладной анализ поведения дает быстрые и устойчивые результаты в работе с нормотипичными детьми.<br>  \r\nЗлата - самая младшая ученица АПЦ \"РАСсвет\". Она с удовольствием сотрудничает с инструкторами центра. \r\nБлагодаря методам АВА, Златочка быстро осваивает материал, появляются новые звуки и слоги.<br> \r\nПрограмма развития этой крохи очень насыщена. Занятия построены на игре, а так же на формировании учебного поведения, развитии внимания и собранности. Злата успешно и с радостью справляется с простыми и сложными задачками.<br> \r\nА еще она очень любит компанию наших ребят. Вместе с ними можно побегать, пошуметь, поиграть с кинетическим песком и попрыгать на батуте! ', '/images/ind/1/1.jpg', '2019-05-01', '/images/ind/1/6.jpg;\r\n/images/ind/1/5.jpg;\r\n/images/ind/1/12.jpg', '', '', 'Новости', '/news.php', 0, '', '0'),
(143, '17', '\"Аутизм как движущая сила в эпоху цифровых технологий\" ', 'В течение многих лет люди с РАС рассматривались вне нормы. Сейчас эта парадигма аутизма пересматривается, и новый подход стал появляться среди компаний сферы цифровых технологий...\r\n<br>Автор: Jonathan Kufman; Источник: www.autismsupportnetwork.com<br>\r\nПеревод Марии Зубаревой ', '<p>Автор: Jonathan Kufman</p>\r\n<p>Источник: <a class=\"a-blue\" href=\"http://www.autismsupportnetwork.com/news/autism-driving-force-digital-age-889332\">www.autismsupportnetwork.com</a></p>\r\n<p>Перевод: Мария Зубарева, учредитель АНО \"АПЦ РАСсвет\"</p>\r\n<p>В течение многих лет люди с расстройствами аутистического спектра рассматривались вне нормы, испытывая неловкость в общении, проблемы с общением с другими людьми или приспосабливаясь к чему-либо. Сейчас эта парадигма аутизма пересматривается, и новый подход стал появляться среди компаний сферы цифровых технологий. В 2012 году журналист Гарет Кук (Gareth Cook) написал статью «Преимущества аутизма» для журнала «New York Times Magazine » и познакомил читателей с Торкилом Сонне, датским предпринимателем, который действительно изменил правила игры, увидев необычайный потенциал людей с аутизмом, и создал для компаний платформу, на которой можно найти еще нераскрытый кадровый резерв для цифровой экономики.</p>\r\n<p>Компания Торкила Сонне «Specialisterne», с датского – «специалист», родилась из его личного опыта общения с сыном Ларсом, у которого был диагностирован аутизм. Сонне рано понял, что его сын обладает определенными навыками, которые включают в себя глубокую концентрацию и сосредоточенность, а также необходимость тщательного исполнения. В то время Сонне работал техническим директором телекоммуникационной компании, и именно эти навыки он искал в своих собственных сотрудниках. После того, как он обнаружил набор этих навыков у своего сына и поговорил с другими родителями, у которых были похожие истории, ему пришлось провести дальнейшее исследование, и в 2004 году родилась его компания. Философия компании «Specialisterne» заключалась в том, что взрослый человек с расстройством аутистического спектра мог не просто удержаться на работе, но потенциально мог бы стать лучшим в своем деле. Сонне идет дальше и заявляет, что по его мнению, теория основана на том, что он называет своей «моделью одуванчика». Сонне говорит, что «когда одуванчики появляются на газоне, мы называем их сорняками, но из зеленых побегов также можно сделать вкусный салат. Подобное можно сказать и о людях с аутизмом, – что очевидные слабости (скажем, иногда грубая прямота и зацикленность) также могут быть сильными сторонами с точки зрения рынка (прямолинейность, внимание к деталям)». Сонне считает, что пришло время каждому решить, видит ли он сорняк или траву? Даже с такой цветочной метафорой Сонне не питает иллюзий, что «Specialisterne» является прибыльной компанией. Он понял, что самым важным фактором является то, что для изменения мышления лидеров корпораций люди с аутизмом должны доказать свою ценность на открытом рынке. В качестве консультанта «Specialistrene» нанимает людей с аутистическим спектром и использует их таланты и особые характеристики для выполнения наиболее утомительных задач для организаций, таких как тестирование программного обеспечения, контроль качества, программирование и ввод данных, а также другие обязанности как в государственном, так и в частном секторах. С 2004 года мнение Торкила Сонне укрепилось, и в настоящее время «Specialistrene» работает более чем в 13 странах мира. </p>\r\n<p>Это новое видение не осталось незамеченным компаниями в Силиконовой долине и в других местах. В октябре 2018 года шесть компаний, в том числе Microsoft, EY, Ford Motor Company, SAP, DXC Technology и JPMorgan Chase, основали круглый стол «Аутизм на работе». Эти компании, прямо или косвенно, были первыми, кто внедрил модель Сонне, и они понимали необходимость обмена передовым опытом и оказания помощи другим организациям, чтобы увидеть отдачу от инвестиций в найм сотрудников с аутистическим спектром. Создавая механизм, позволяющий еще больше использовать эти возможности, несколько компаний в технологической отрасли прилагают целенаправленные усилия по созданию культуры интеграции путем разработки программ, которые не только предоставляют необходимую поддержку людям с аутизмом, но и создают среду, в которой новое видение использования работников с аутизмом может быть полностью реализовано. Одной из первых компаний, принявших на вооружение таланты людей с аутизмом, стала SAP, транснациональная компания, производящая программное обеспечение для управления бизнес-операциями и отношениями с клиентами. Хосе Веласко (Jose Velasco), вице-президент по управлению продуктами и руководитель программы «Аутизм на работе» в США, заявил, что «в 2013 году в Индии возникла значительная потребность в тестировании программного обеспечения, поэтому мы наняли там четырех человек в качестве пилотного проекта. Теперь мы нанимаем людей в спектре в 10 странах».</p>\r\n', '/images/TRANSLATE/3.jpg', '2019-05-04', '', '', '', 'Новости', '/news.php', 0, '', '0'),
(145, '2', 'Время кормить котов!', 'Наши ребята давно дружат с кото-кафе \"Время Котов\". Ранее мы уже рассказывали вам о том, как здорово и интересно ученики центра \"РАСсвет\" проводят время в гостях у пушистых друзей. \r\nВ этот раз мы пришли к котам уже в рамках нашего нового проекта \"Время кормить котов\" и не с пустыми руками!', 'Наши ребята давно дружат с кото-кафе \"Время Котов\". Ранее мы уже рассказывали вам о том, как здорово и интересно ученики центра \"РАСсвет\" проводят время в гостях у пушистых друзей.<br> \r\nВ этот раз мы пришли к котам уже в рамках нашего нового проекта \"Время кормить котов\" и не с пустыми руками! Каждый ребенок сам посеял, так называемую, \"кошачью траву\". Мы поливали ее и наблюдали за тем, как она растет. И сегодня у нас была возможность угостить лакомством любимых друзей.<br>\r\nДанный проект призван вовлечь разных по уровню развития детей с аутизмом в активную деятельность в наиболее доступной для них форме, помогая особенным детям ощутить свою социальную значимость через заботу о природе, познакомить их с опытом садовых работ, вовлечь детей в работу с ними и друг с другом.<br>\r\nУчебный процесс был организован таким образом, чтобы хватало времени и на теорию, и на практическое знакомство с окружающей средой. Дети учились через визуальное восприятие, наименовали, определяли на слух обучающий материал флоры и фауны. Затем дети на практике, каждый своими руками, провели весь цикл: посадили семечку в землю, наблюдали как из семечки появляется росток, из ростка – растение, учились поливать растения.<br>\r\nИтогом проекта и стало посещение котокафе, где на передержке находятся кошки, выброшенные или потерянные, и оказавшиеся бездомными. В этом котокафе волонтеры ухаживают за ними и ищут им новых хозяев. Ребята посетили кошек с экскурсией и гостинцами – принесли сочную травку, выращенную своими руками. \r\n\r\n', '/images/dosug/kats/3/10.jpg', '2019-05-08', '/images/dosug/kats/3/1.jpg; /images/dosug/kats/3/2.jpg; /images/dosug/kats/3/3.jpg; /images/dosug/kats/3/4.jpg; /images/dosug/kats/3/5.jpg; /images/dosug/kats/3/6.jpg; /images/dosug/kats/3/7.jpg; /images/dosug/kats/3/8.jpg;  /images/dosug/kats/3/9.jpg; /images/dosug/kats/3/10.jpg; /images/dosug/kats/3/11.jpg; /images/dosug/kats/3/12.jpg', '', '', '', '', 0, '', '0'),
(147, '2', 'Новые приключения с проектом \"Расширяя границы\"', 'Играли в боулинг, примеряли костюмы пожарных и катались на пожарной машине, ходили в гости к замечательным зверюшкам, множество новых впечатлений получили наши маленькие подопечные в рамках проекта \"Расширяя границы\".', 'Реализация проекта \"Расширяя границы\", который стартовал в сентябре 2018 года продолжается! В рамках этого проекта \"Рассвет\" организует инклюзивные мероприятия, направленные на адаптацию детей с аутизмом к посещению общественных мест, а также на формирование и развитие у них социальных навыков.<br>\r\nМы расширяем наш проект за счет поиска новых мест для посещения различной направленности, тем самым развивая интересы наших подопечных к различным активностям и видам деятельности.<br>\r\nУ детей с аутизмом важно формировать, развивать и закреплять процесс самостоятельной игры. Боулинг – это отличная возможность показать положительные стороны полезной динамичной игры. Играя, мы тренируем меткость, ловкость рук, развиваем общую скоординированность движений, что имеет важное значение для детей, которым, зачастую, специфические физические нагрузки даются с трудом. Боулинг — это не только спорт, но и отличный способ провести время. Спасибо боулинг-центру «Космодром» за предоставленную возможность детям центра РАСсвет посетить такое интересное мероприятие.<br>\r\nВ марте состоялась экскурсия в Пожарную часть №1, где у ребят была возможность погрузиться в профессию пожарного - примерить амуницию, аппарат для дыхания, робу, пропахшую дымом. Но самое главное - побывать внутри пожарной машины, в которой можно было все посмотреть и потрогать, а самое главное - прокатиться по территории части!\r\nБлагодарим руководство и сотрудников пожарной части за увлекательную, познавательную и безвозмездную экскурсию!<br>\r\n\"Огромное спасибо РАСсвету за организованные сенсорные ощущения, которые подарили нам Чудесные тёпленькие пушистики! Такие доброжелательные к детям, позволили себя погладить и покормить. Доволен не только мой Богдашка, но и я сама с удовольствием нервы успокоила). Солнцевы Богдан и Марина.\" Этот и другие отзывы оставили довольные посетители контактного зоопарка в ТЦ \"Сильвермолл\", экскурсия в который прошла 6 апреля. А мы в свою очередь благодарим директора Фонда \"Иркутский ботанический сад\" Сизых Ивана Александровича за предоставленную нам возможность организовать эту \"пушистую\" экскурсию!<br>\r\nА в мае наши ребята и друзья нашего центра побывали в гостях у дружелюбных, забавных нерп. Представление было насыщенным и вызвало бурю восторга. Нерпы играли в футбол, прыгали и ныряли, сдабривая зрителей порцией брызг. Нам очень понравилось! Благодарим иркутский нерпинарий и надеемся на новую встречу!', '/images/dosug/2019/5.jpg', '2019-05-11', '/images/dosug/2019/1.jpg; /images/dosug/2019/2.jpg; /images/dosug/2019/3.jpg; /images/dosug/2019/4.jpg; /images/dosug/2019/5.jpg; /images/dosug/2019/6.jpg; /images/dosug/2019/7.jpg', '', '', '', '', 0, '', '0'),
(153, '16', 'IV стратегическая сессия Ассоциации \"Аутизм Регионы\"', 'Член правления АНО \"АПЦ РАСсвет\" Елена Михеева приняла активное участие в работе 4 стратегической сессии Ассоциации \"Аутизм Регионы\", которая проходила 8-9 июня в Москве. В Бизнес-школе Сколково собрались представители 29 некоммерческих организаций, созданных родителями и близкими людей с РАС', '<p>Член правления АНО \"АПЦ РАСсвет\" Елена Михеева приняла активное участие в работе 4 стратегической сессии Ассоциации \"Аутизм Регионы\", которая проходила 8-9 июня в Москве.</p>\r\n\r\n<p>В Бизнес-школе Сколково собрались представители 29 некоммерческих организаций, созданных родителями и близкими людей с РАС и другими ментальными нарушениями, из 18 регионов России: Москвы, Санкт-Петербурга, Ивановской, Новосибирской, Белгородской, Воронежской, Московской, Ростовской, Липецкой, Иркутской, Костромской областей, из Северной Осетии-Алании, Татарстана, Башкортостана, Марий Эл, Краснодарского и Алтайского края, чтобы обсудить стратегии развития Ассоциации и управление ее ресурсами.</p>', '/images/news/strat/2.jpg', '2019-06-10', '/images/news/strat/1.jpg;\r\n/images/news/strat/2.jpg;\r\n/images/news/strat/3.jpg;\r\n/images/news/strat/4.jpg;\r\n/images/news/strat/5.jpg;\r\n/images/news/strat/6.jpg;\r\n/images/news/strat/7.jpg;\r\n/images/news/strat/8.jpg;\r\n/images/news/strat/9.jpg;\r\n/images/news/strat/10.jpg;\r\n/images/news/strat/11.jpg;\r\n/images/news/strat/12.jpg;\r\n/images/news/strat/13.jpg;\r\n/images/news/strat/14.jpg', '', '', 'Новости', '/news.php', 0, '', '0'),
(156, '2', 'Театр не имеет границ', 'АПЦ \"Рассвет\" принимает участие в масштабном инклюзивном проекте: \"Театр не имеет границ\"! В рамках проекта  инклюзивная группа детей имела возможность погрузится в таинственный мир закулисья Иркутского областного театра кукол «Аистёнок».', '<p>АПЦ \"Рассвет\" принимает участие в масштабном инклюзивном проекте: \"Театр не имеет границ\"! Партнёрами проекта являются Иркутский областной театр кукол «Аистёнок» и СКОШ №20.</p>\r\n<p>Суть проекта – поэтапно обучать детей с аутизмом понимать, как нужно играть, чтобы игра удалась. Учиться манипулировать игрушками правильно, развивая воображение. \r\nКопировать действия своих нормотипичных ровесников, «зеркалить» их поведение. Проект дает возможность обобщать полученные навыки в рамках совместной постановочной работы на \r\nсцене театра. Также одной из задач проекта является организация совместной творческой игровой деятельности детей с ОВЗ и обычных ребят с детьми с аутизмом.</p>\r\n<p>В рамках реализации проекта его участники и другие дети вместе со своими семьями посетили несколько интеграционных представлений в кукольном театре.</p>\r\n<p>В январе состоялась инклюзивная Новогодняя ёлка в кукольном театре \"Аистенок\". Для детей с РАС мероприятия подобного формата имеют огромное значение! Живое общение со Снегурочкой, \r\nсказочными пингвинами вызвало эмоциональный восторг. Мероприятие было построено и проведено талантливыми актерами, были учтены все особенности детей с ОВЗ. Каждый ребенок имел\r\nвозможность играть со сказочными героями вечера!</p>\r\n<p>В феврале ребята вновь посетили Иркутский областной театр кукол \"Аистенок\" и стали зрителями сказки: \"Наш домок-теремок\". \r\nПознакомились с творческой группой \"БИ-БА-БО\", учащимися СКОШ №20, которые во время перерыва в фойе театра показали свой спектакль \"Теремок\" с пальчиковыми куклами. </p>\r\n<p>В марте к Дню рождения АНО «АПЦ Рассвет» состоялся спектакль «Таинственный гиппопотам». История о том, как в далекой прекрасной стране Мираликундии живет львёнок Лёва, который очень хочет иметь друга, покорила и детей и взрослых. \r\nВо время антракта дети имели возможность познакомиться с куклами, побывав в роли кукловода. А также оставить свои впечатления о театре акварелью на бумаге. </p>\r\n<p>А в июне инклюзивная группа детей имела возможность погрузится в таинственный мир закулисья Иркутского областного театра кукол «Аистёнок».\r\nВзаимодействие друг с другом в необычной обстановке, куклы из различного материала, макеты, декорации и необычная обстановка театра вызвала массу положительных эмоций.</p>\r\n<p>Параллельно с посещением театра, в рамках индивидуальных, а затем и групповых занятий проводилось обучение участников проекта ставить миниспектакли за учебным столом. \r\nДети с аутизмом постепенно прошли несколько этапов для того момента, когда впервые стали сами ставить спектакли. От обучения через мотивационные предметы взаимодействовать \r\nс персонажами сказок, до постановки спектакля в группе из двух детей с аутизмом, умения взаимодействовать со сверстником и просто умения ждать своей очереди в постановке спектакля.</p>\r\n<p>Выражаем благодарность администрации Иркутского областного театра кукол \"Аистенок\" : Директору - Стрельцову А.С., Подкорытовой М.В., Чернышевой И.В.; \r\nавторам ( Филипповой А.В., Рудницкой Л.Б.) Отдельная благодарность научным руководителям проекта: Любиной М.Г, Родыгиной Е.А.</p>', '/images/dosug/theatre2/1.jpg', '2019-06-23', '/images/dosug/theatre2/2.jpg;\r\n/images/dosug/theatre2/3.jpg;\r\n/images/dosug/theatre2/4.jpg;\r\n/images/dosug/theatre2/5.jpg;\r\n/images/dosug/theatre2/6.jpg;\r\n/images/dosug/theatre2/7.jpg;\r\n/images/dosug/theatre2/8.jpg;\r\n/images/dosug/theatre2/9.jpg', '', '', '', '', 0, '', '0'),
(162, '13', 'Жутко громко и запредельно близко', 'Автор: Дарья Арчебасова <br>\r\nИсточник: ИА \"Иркутск Сегодня\" <a class=\"a-blue\" href=\"https://irk.today/2019/07/09/zhutko-gromko-i-zapredelno-blizko-naskolko-irkutsk-znakom-s-problemami-autizma/\" target=\"_blank\">(irk.today)</a><br>\r\nВ настоящее время аутизм неизлечим, но есть люди, которые помогают детям с аутизмом. Мы побывали в таком центре в Иркутске и поразились тому, какая сложная и невероятно нужная работу здесь идет.', '', '/images/smi/7.jpg', '2019-07-14', '', '', 'https://irk.today/2019/07/09/zhutko-gromko-i-zapredelno-blizko-naskolko-irkutsk-znakom-s-problemami-autizma/', '', '', 1, '', '0'),
(165, '3', 'Тренинги от \"Мозаики\"', 'Отличным мастер-классом и продуктивной трехдневной работой завершилась серия тренингов с участием специалистов АВА-центра \"Мозаика\", проводимых при поддержке Фонда президентских грантов.', '<p>Отличным мастер-классом и продуктивной трехдневной работой завершилась серия тренингов с участием специалистов АВА-центра \"Мозаика\", проводимых при поддержке Фонда президентских грантов.</p>\r\n<p>Напомним, что проект \"Центр прикладного анализа поведения для детей с РАС и их семей\" направлен на подготовку специалистов в области Прикладного анализа поведения с последующим оказанием эффективной помощи детям с РАС и их семьям. В ходе проекта для родителей и специалистов смежных специальностей проводились тематические семинары, а также практические тренинги с участием поведенческих аналитиков АВА-центра «Мозаика» из г.Новосибирск.</p>\r\n<p>Первый трехдневный тренинг «Анализ дефицитов и актуальных навыков ребенка» прошел с 30 ноября по 2 декабря 2018 года. Мы принимали в гостях поведенческих аналитиков Марию Печеркину и Марию Фризен.</p>\r\n<p>В первые два дня участники тренинга под чутким руководством специалистов «Мозаики» провели тестирование четверых детей и попрактиковались в постановке долгосрочных и краткосрочных целей и задач с позиции функциональности и относительно результатов тестирования. Третий завершающий день был целиком посвящен приемам работы над учебным поведением детей.</p>\r\n<p>Новый 2019 год начался для нас вторым обучающим тренингом для специалистов и родителей, который провела поведенческий аналитик \"Мозаики\" Мария Фризен. \r\nБыли подробно рассмотрены вопросы налаживания сотрудничества с ребенком, установления руководящего контроля, подбора поощрений и ввода жетонной системы. Тренинг, как обычно, был примечателен практическим применением знаний. Динамично, информативно и очень продуктивно мы провели эти три дня! Осваивать практику нам помогали удивительные девочки - Ника и Алиса, вечные двигатели и прыгатели, которые только начинают свое знакомство с АВА.</p>\r\n<p>Весело и плодотворно пролетел трехдневный тренинг по теме \"Вербальное поведение\", который снова проводила для нас Мария Фризен с 15 по 17 марта.\r\nСовершенствоваться в практических навыках нам помогали наши маленькие ученики - Тима и Андрей. В ходе практики мы учились расширять репертуар вокализаций невербального ребенка, развивать его запросы с помощью карточек PECS. А другому малышу с аутизмом, речь которого малофункциональна, показали силу слова, расширили репертуар его вокальных просьб. Он покорил нас своей скоростью обучения сложным вербальным навыкам. Как всегда тренинг прошел на высшем уровне. </p>\r\n<p>Тренинг по теме \"Нежелательное поведение\", под руководством Марии Печеркиной, прошел в нашем центре с 24 по 26 мая включительно. \r\nВ теоретической части тренинга были затронуты вопросы составления рабочего определения поведения, обзор форм и методов сбора данных, анализ полученных данных, построение гипотез относительно функций поведения, способы оценки прогресса.\r\nКроме того, мы имели возможность применить знания на практике с участием ребенка: собрать данные в ходе занятия с учеником центра Андреем, определить функцию поведения, составить проект плана по коррекции поведения избегания сложных и немотивационных заданий, а также начать применение данного плана на практике.</p>\r\n<p>И завершающий серию тренингов мастер-класс, который провели в июле специалисты \"Мозаики\", прошел на \"отлично\"! Программа как всегда была углубленной и индивидуально проработанной: поведение слушателя - аудиторное расширение, контекстные инструкции, программа \"Стой - иди\", пэринг, вокальные запросы и артикуляционная имитация. С интересом и вдохновением прорабатывали поведение слушателя с расширенными блоками, с позиционной подсказкой, переводили в такт, работали с карточкой \"ВСЁ\" в заданиях на визуальное восприятие. И, конечно, никуда без десенсебилизации.</p>\r\n<p>Мы благодарим наших специалистов и волонтеров за большое желание обучаться и повышать свою квалификацию, а родителей детей с РАС - за интерес к АВА и к нашему центру. И, конечно же, спасибо поведенческим специалистом АВА-центра \"Мозаика\" за увлекательный обучающий процесс!</p>', '/images/training/07.19/43.jpg', '2019-07-29', '/images/training/07.19/1.jpg; /images/training/07.19/2.jpg; /images/training/07.19/3.jpg; /images/training/07.19/4.jpg;  /images/training/07.19/5.jpg;  /images/training/07.19/6.jpg; /images/training/07.19/7.jpg; /images/training/07.19/8.jpg;  /images/training/07.19/9.jpg; /images/training/07.19/10.jpg', '', '', '', '', 0, '', '18');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(166, '4', 'Занятия в мини-группах в рамках проекта \"Центр ПАП\"', 'Занятия в мини-группах - одно из серии мероприятий, осуществляемых \"Рассветом\" в рамках проекта «Центр прикладного анализа поведения для детей с расстройствами аутистического спектра» \r\nпри поддержке Фонда президентских грантов. ', 'Занятия в мини-группах - одно из серии мероприятий, осуществляемых \"Рассветом\" в рамках проекта «Центр прикладного анализа поведения для детей с расстройствами аутистического спектра» \r\nпри поддержке Фонда президентских грантов. <br> \r\nЗанятия в мини-группах основаны на мотивации детей и их общих увлечениях. Группа формируется из 2-3 человек. Особое внимание уделяется обучению детей взаимодействовать друг \r\nс другом, так как детям с аутизмом сложно даётся общение со сверстниками, а также уступать, просить помощи, уметь ждать своей очереди и обобщать сформированные \r\nна индивидуальных занятиях навыки.<br> \r\nСовсем недавно к нам в центр пришли новенькие ребята, но уже смогли присоединиться к совместным занятиям! Большинство занятий проходят в форме игры. Причем эта игра должна быть непременно интересной. Совместные занятия не всегда проходят гладко, но, благодаря этому, \r\nмалейшие успехи более заметны и значимы как для родителей, так и для педагогов.<br> \r\nИ еще важный момент – мама может не только присутствовать на занятии, но и принимать в нем активное участие. Это нужно, с одной стороны, для того, чтобы ребенок чувствовал \r\nсебя в безопасности, а с другой – чтобы мама могла научиться на практике, чем заниматься с ребенком дома. Процесс, конечно, долгий. \r\nДля ребенка с аутизмом это ведь не просто игра в присутствии других людей, это, прежде всего, коммуникация. Уверяем, что со временем он захочет к вам присоединиться.<br> \r\nВ наших групповых занятиях участвуют не только дети с РАС, но и их братья и сестры. Ведь детям с аутизмом так важно научиться взаимодействию не только друг с другом, \r\nно и с нормотипичными детьми. Причем наши малыши получают огромное удовольствие от такого общения!<br> \r\nА ребятам постарше групповые занятия позволяют развивать не только коммуникативные и игровые навыки, но и академические. Ведь в компании друзей учиться веселее.<br> ', '/images/group/2019/9.jpg', '2019-07-07', '/images/group/2019/1.jpg;\r\n/images/group/2019/2.jpg;\r\n/images/group/2019/3.jpg;\r\n/images/group/2019/4.jpg;\r\n/images/group/2019/5.jpg;\r\n/images/group/2019/6.jpg;\r\n/images/group/2019/7.jpg;\r\n/images/group/2019/8.jpg', '', '', '', '', 0, '', '18'),
(171, '2', '\"Расширяя границы\": лето в городе', 'Лето в городе тоже бывает ярким, красочным и интересным. Прогулки, детские площадки, карусели и даже Колесо обозрения. И мы, оставшиеся в городе, это подтверждаем. Узнаем подробнее, в каких мероприятиях поучаствовали наши подопечные этим летом в рамках проекта \"Расширяя границы\".', '<p>Лето в городе тоже бывает ярким, красочным и интересным. Прогулки, детские площадки, карусели и даже Колесо обозрения. И мы, оставшиеся в городе, это подтверждаем. Узнаем подробнее, в каких мероприятиях поучаствовали наши подопечные этим летом в рамках проекта \"Расширяя границы\".</p>\r\n<p>В июне мы совершили экскурсию на ипподром, где познакомились с прекрасными животными! От них ребята получили не только общение, но и большое количество положительных эмоций, что для детей с аутизмом является огромным шагом к эмоциональному развитию.</p>\r\n<p>\r\nАвтономная некоммерческая организация \"АПЦ РАСсвет\" искренне благодарит главного тренера конно-спортивного клуба «Иркония» Помпура Марину Викторовну за эту возможность.</p>\r\n<p>В июле взрослые и дети погуляли по острову \"Конный\" и прокатились на Колесе обозрения. Наш город с высоты птичьего полёта никого равнодушным не оставил, и мы обязательно вернемся сюда еще не раз.</p>\r\n<p>А в самом начале сентября подопечные АНО \"АПЦ Рассвет\" и их друзья вновь посетили Иркутский зоосад. Ребята принесли питомцам угощение со своих огородов. Одна семья даже получила за это благодарственное письмо. Благодарим руководство Зоосада за очередную возможность встречи, прогулок по вашим уютным местам, общение с животными и отличное настроение детей и родителей!</p>\r\n<section class=\"video-detail\"><iframe src=\"https://vkvideo.ru/video_ext.php?oid=-165016766&id=456239062&hash=6e4f663e2ff99cb5\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen class=\"social-video\"></iframe>\r\n</section>', '/images/dosug/2019/8.jpg', '2019-09-15', '/images/dosug/2019/14.jpg;\r\n/images/dosug/2019/15.jpg;\r\n/images/dosug/2019/9.jpg;\r\n/images/dosug/2019/10.jpg;\r\n/images/dosug/2019/11.jpg;\r\n/images/dosug/2019/12.jpg;\r\n/images/dosug/2019/13.jpg', '', '', '', '', 0, '', ''),
(172, '12', 'Индивидуальный подход - основа фитнеса для детей с РАС', 'Дети с аутизмом имеют право получать качественную помощь, отвечающую их уникальным потребностям. Каждый ребенок обучается по индивидуальной программе, и не существует одинаковых подходов к выполнению упражнений также, как не существует двух одинаковых детей с аутизмом.', '<p>Дети с аутизмом имеют право получать качественную помощь, отвечающую их уникальным потребностям. Мы делаем так, чтобы каждый ребенок обучался по его индивидуальной программе, и не существует одинаковых подходов к выполнению упражнений также, как не существует двух одинаковых детей с аутизмом.</p>\r\n<p>Мы понимаем, что эффективность программы зависит от многих факторов, в том числе от правильной диагностики навыков и определяемых нами критериев успеха каждого ребенка. Для нас не существует учеников с низкой и высокой обучаемостью, но есть дети с индивидуальной динамикой. От того, сколько сил мы прикладываем, анализируя промежуточные результаты в обучении, зависит успех физической реабилитации в целом.</p>\r\n<p>\r\nПроект \"Фитнес для детей с РАС\" - это проект обучающий. Его цели и задачи - физическая реабилитация детей с аутизмом, а также их семей. Помогая ребенку, мы помогаем всей его семье, ведь самые близкие люди порой просто не знают как поступить и что еще придумать, чтобы ребенок делал шаги в развитии необходимых навыков.</p>\r\n<p>Мы видим, когда глаза родителей светятся, наблюдая за успехами детей, и это лучшая награда нашей работе.</p>\r\n<p>Инструктор по фитнесу <a class=\"a-blue\" href=\"https://rassvet-apc.ru/team/Viktoriya_Kuznecova.php\" target=\"_blank\">Кузнецова Виктория Владимировна</a></p>\r\n<section class=\"video-detail\"><iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456239070&hash=a82abfa11289e0d0\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen class=\"social-video\"></iframe>\r\n</section>', '/images/AFK/fit/11.jpg', '2019-09-29', '/images/AFK/fit/12.jpg;\r\n/images/AFK/fit/13.jpg', '', '', '', '', 0, '', '0'),
(173, '16', 'Конференция \"RusABA - 2019\"', 'Учредители АНО \"АПЦ РАСсвет\" Екатерина Никулина и Елена Шапилова в этом году стали частью грандиозного события - Профессиональной конференции поведенческих аналитиков \"Прикладной анализ поведения: теория и практика\", организованной Ассоциацией Специалистов Прикладного Анализа Поведения RusABA, которая прошла в Москве со 2 по 6 октября 2019 года.', '<p>Учредители АНО \"АПЦ РАСсвет\" Екатерина Никулина и Елена Шапилова в этом году стали частью грандиозного события - Профессиональной конференции поведенческих аналитиков \"Прикладной анализ поведения: теория и практика\", организованной Ассоциацией Специалистов Прикладного Анализа Поведения RusABA, которая прошла в Москве со 2 по 6 октября 2019 года.</p>\r\n<p>В программе конференции были не только очень содержательные, имеющие огромную практическую значимость доклады зарубежных и отечественных поведенческих специалистов, но и интереснейшие мастер-классы от мировых экспертов в области АВА. Подробнее о тематике докладов и мастер-классов <a class=\"a-blue\" href=\"https://proaba.ru/conf2019/\" target=\"_blank\">ЗДЕСЬ</a>.</p>\r\n<p>И по уже сложившейся традиции наши учредители привезли домой новые книги о методах Прикладного Анализа Поведения, которые только-только вышли в свет. \r\nКроме того, благодаря фонду \"Выход\" и Ассоциации \"Аутизм-регионы\" в нашем городе теперь есть ещё 10 пособий по организации ресурсного класса.</p>\r\n<p>С 13 по 19 октября в нашем центре прошла серия внутренних семинаров-тренингов \"По следам конференции RusABA-5\" для специалистов и волонтёров центра \"Рассвет\", на которых Екатерина Никулина и Елена Шапилова делились с присутствующими информацией и практическими находками, о которых узнали в ходе докладов и мастер-классов конференции.</p>\r\n<p>В ходе тренингов были освещены темы: \"Азы родительского мастерства. Те вещи, которые работают\" (обзор мастер-класса Расми Криппендорфа, ВСВА, Норвегия), \"Будем договариваться или всё о поведенческих контрактах\" (обзор мастер-класса Марины Азимовой, ВСВА, США), \"Как учить людей с аутизмом навыкам в области безопасности своего тела и его границ\" (обзор мастер-класса Барбары Гросс, ВСВА, США), \"Поэтапное обучение навыкам безопасного поведения\" (обзор мастер-класса Александры Жестковой, ВСаВА, Москва) и другие.</p>\r\n<p>На тренингах присутствовали от 12 до 14 человек, что оказалось даже больше запланированного количества, т.к. в них с удовольствием приняли участие не только специалисты центра, но и волонтеры из числа студентов и активно помогающих организации родителей.\r\n</p>\r\n<p>Мероприятия осуществлялись при поддержке Фонда президентских грантов.</p>', '/images/news/rusaba/1.jpg', '2019-10-20', '/images/news/rusaba/1.jpg;\r\n/images/news/rusaba/3.JPG;\r\n/images/news/rusaba/5.JPG;\r\n/images/news/rusaba/6.JPG;\r\n/images/news/rusaba/7.JPG;\r\n/images/news/rusaba/9.JPG;\r\n/images/news/rusaba/10.JPG;\r\n/images/news/rusaba/11.JPG;\r\n/images/news/rusaba/15.JPG;\r\n/images/news/rusaba/16.JPG;\r\n/images/news/rusaba/18.JPG;\r\n/images/news/rusaba/20.JPG;\r\n/images/news/rusaba/25.JPG;\r\n/images/news/rusaba/27.JPG;\r\n/images/news/rusaba/8.JPG;\r\n/images/news/rusaba/17.JPG;\r\n/images/news/rusaba/21.JPG\r\n\r\n\r\n', '', '', 'Новости', '/news.php', 0, '', '17'),
(184, '22', '\"Экобум\" в Иркутске', 'Мечты сбываются! Наконец мы приобрели столь необходимый нашему центру принтер! И сделать мы это смогли исключительно благодаря вам, вашим добрым сердцам и вашей щедрости! Суммы, собранной в ходе благотворительного марафона \"Экобум\", оказалось достаточно для приобретения принтера!', '<p>Друзья! Мечты сбываются! Наконец мы приобрели столь необходимый нашему центру принтер! И сделать мы это смогли исключительно благодаря вам, вашим добрым сердцам и вашей щедрости! Суммы, собранной в ходе благотворительного марафона <a class=\"a-blue\" href=\"/pages/eco_bum.php\" target=\"_blank\">\"ЭКОБУМ\"</a>, а это и средства от <a class=\"a-blue\" href=\"/news/detail.php?id=175&back=news\" target=\"_blank\">сдачи макулатуры</a> и благотворительные пожертвования, оказалось достаточно для приобретения принтера!</p>\r\n<p>Мы благодарим всех и каждого, сделавшего свой посильный вклад! Ведь недаром говорят \"Не имей сто рублей, а имей сто друзей!\" И все мы здесь надеемся на продолжение нашего знакомства. Будем дружить дальше!!!</p>', '/images/news/eco_bum/15.jpg', '2019-11-07', '/images/news/eco_bum/7.jpg;\r\n/images/news/eco_bum/21.jpg;\r\n/images/news/eco_bum/15.jpg;\r\n/images/news/eco_bum/16.jpg;\r\n/images/news/eco_bum/17.jpg;\r\n/images/news/eco_bum/18.jpg;\r\n/images/news/eco_bum/20.jpg\r\n', '', '/pages/eco_bum_report.php', 'Отчёты', '/project-reports.php', 1, '', '0'),
(191, '13', '\"Рассвет\" в Иркутске поможет детям с аутизмом учиться в обычных школах', 'Автор: Григорий Леорда Источник: \"Комсомольская правда\" <a class=\"a-blue\" href=\"https://www.irk.kp.ru/daily/27048/4113966/\" target=\"_blank\">(irk.kp.ru)</a><br>\r\nСотрудники Адаптационно-педагогического центра «Рассвет» в Иркутске в своей работе используют методику, которая считается одной из самых эффективных для обучения детей с аутизмом.', '', '/images/smi/9.jpg', '2019-10-29', '', '', 'https://www.irk.kp.ru/daily/27048/4113966/', '', '', 1, '', '0'),
(194, '2', 'Зимние радости с \"Рассветом\"', 'Новогодний утренник для ребенка с аутизмом? Мысли мамы: \"Это не для нас: шум, гам, громкая музыка, хаотичное движение - как минимум сразу придётся уйти,  или наблюдать ребенка в позе \"звезды\" и косые взгляды окружающих...\r\nА мы решились и организовали особый праздник с приятными хлопотами. Ведь его создатели - активные мамы и сильные папы нашего РАСсвета.', '<p>Новогодний утренник для ребенка с аутизмом? Мысли мамы: \"Это не для нас: шум, гам, громкая музыка, хаотичное движение - как минимум сразу придётся уйти,  или наблюдать ребенка в позе \"звезды\" и косые взгляды окружающих ...\r\nА мы решились и организовали особый праздник с приятными хлопотами. Ведь его создатели - активные мамы и сильные папы нашего РАСсвета.</p>\r\n<p>Праздник в \"Капитошке\" получился что надо: веселый и комфортный для всех! Задорный Дед Мороз и очаровательная Снегурочка окутали нас своим волшебством и добротой, с огромным мешком подарков от неравнодушных дарителей ООО \"Неополия\".\r\nМандарины и ароматная выпечка от пекарни \"КиМ\" подзарядили силами  и позитивом. Эмоции праздника запечатлели Лидия Лось и Наталья Тарасова.\r\nНовый год для каждого, Новый год для всех!</p>\r\n<p>Мероприятие проведено полностью на безвозмездной основе! Мы и наши дети благодарны всем, кто помог организовать этот праздник. Благодаря вам он стал настоящим новогодним подарком!</p>\r\n<p>Но на этом зимние радости для ребят не закончились! В новогодние каникулы их ждали новые встречи с друзьями и новые интересные мероприятия.</p>\r\n<p>В канун Нового 2020 года для ребят распахнул свои двери Дворец Детского и Юношеского творчества.\r\n\r\nСостоялось новогоднее представление \"Дед мороз меняет профессию\". Это был удивительный спектакль: сказочный сюжет, декорации и костюмы героев просто поражали воображение. Дети смотрели затаив дыхание.</p>\r\n<p>Красивые, новогодние и очень вкусные пряники наши ребята украшали на мастер-классе, который прошел 3 января в центре \"Рассвет\".\r\n\r\nМастер-класс провела \"пряничная фея\" Юлия Дмитриева. Ребята очень увлеклись росписью пряников! И все мы - и дети, и родители, были очень рады, что провели это морозное утро на таком замечательном, тщательно подготовленном Юлией мероприятии, в кругу своих друзей!\r\nОт всей души благодарим Юлию и надеемся на дальнейшее сотрудничество!</p>\r\n<p>Также семьи с детьми с аутизмом посетили музей \"На свалке\". Выход получился очень насыщенным и активным. Ребята посмотрели множество инсталляций и фигур из переработанных отходов, сделали потрясающие фотографии, покатались на горке и запаслись положительными эмоциями! Благодарим за приглашение руководство музея, нам было очень весело у вас!\r\n\r\n</p>\r\n<p>Дарите вашим детям время и эмоции, это самый ценный подарок!</p>\r\n<section class=\"video-detail\"><iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456239091&hash=e81a5767907e1298\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen class=\"social-video\"></iframe>\r\n</section>', '/images/dosug/2019/16.jpg', '2020-01-08', '/images/dosug/2019/16.jpg;\r\n/images/dosug/2019/17.jpg;\r\n/images/dosug/2019/18.jpg;\r\n/images/dosug/2019/19.jpg;\r\n/images/dosug/2019/20.jpg;\r\n/images/dosug/2019/21.jpg;\r\n/images/dosug/2019/22.jpg', '', '', '', '', 0, '', '0'),
(195, '16', '\"Миллион на добрые дела\" - 2019', 'Второй год подряд учредители и волонтеры АНО \"АПЦ РАСсвет\" становятся победителями и призерами конкурса социально значимых проектов \"Миллион на добрые дела\".', 'Второй год подряд учредители и волонтеры АНО \"АПЦ РАСсвет\" становятся победителями и призерами конкурса социально значимых проектов \"Миллион на добрые дела\". В этом году у нас 1 место в номинации \"Безбарьерный Иркутск\", которое заняла Екатерина Родыгина с проектом \"Мини-группы для детей с аутизмом\", а также три серебряных места в этой же номинации. От души поздравляем победителей и призеров!', '/images/news/MDD-2019/1.jpg', '2019-12-20', '/images/news/MDD-2019/1.jpg;\r\n/images/news/MDD-2019/2.jpg;\r\n/images/news/MDD-2019/3.jpg;\r\n/images/news/MDD-2019/4.jpg;\r\n/images/news/MDD-2019/5.jpg;\r\n/images/news/MDD-2019/6.jpg;\r\n/images/news/MDD-2019/7.jpg;\r\n/images/news/MDD-2019/8.jpg;\r\n/images/news/MDD-2019/9.jpg;\r\n/images/news/MDD-2019/10.jpg', '', '', 'Новости', '/news.php', 0, '', '0'),
(196, '16', 'Важные шаги в развитии инклюзивного образования', 'На сегодняшний день уже сформирован родительский запрос на создание особых условий для детей с РАС в дошкольных и школьных общеобразовательных учреждениях. \"АПЦ Рассвет\" в течение двух лет пытается наладить диалог с системой образования города Иркутска.', '<p>На сегодняшний день уже сформирован родительский запрос на создание особых условий для детей с РАС в дошкольных и школьных общеобразовательных учреждениях, на разработку адаптированной образовательной программы и подготовку тьюторов с последующим сопровождением ими детей с аутизмом. \"АПЦ Рассвет\" в течение двух лет пытается наладить диалог с системой образования города Иркутска. Однако, несмотря на активную деятельность, значительных результатов достичь пока не удается.</p>\r\n<p>Организация активно участвует в различных конференциях, круглых столах и встречах, посвященных вопросам образования и социализации детей с особыми потребностями. Основные темы выступлений включают необходимость создания ресурсных классов и групп для детей с РАС, важность ранней диагностики и коррекции, а также доступ к качественному образованию.</p>\r\n<p>Учредитель \"Рассвета\" Елена Михеева впервые выступила с докладом на тему: \"Опыт родителей в работе с детьми с РАС\", в рамках III Межрегиональной научно-практической конференции: \"Создание специальных условий образования для обучающихся с ограниченными возможностями здоровья в образовательном пространстве: результаты, опыт, перспективы\" в начале ноября 2018 года. Конференция проходила в Педагогическом институте Иркутского государственного университета. Она поделилась опытом родителей в воспитании и обучении таких детей, акцентируя внимание на необходимости ранней диагностики и коррекционных мероприятий.</p>\r\n<p>24 ноября 2018 года представитель \"Рассвета\" представила доклад в Байкальском международном салоне образования на круглом столе \"Инклюзивное образование глазами родителей\", подчеркнув необходимость создания в Иркутске Ресурсных классов и Ресурсных групп для детей с расстройствами аутистического спектра.</p>\r\n<p>28 марта текущего года наша организация выступала с докладом на тему: \"Дети с аутизмом в системе образования\" на III Межведомственной региональной научно-практической конференции.\r\nМы вновь говорили о необходимости открытия в нашем регионе Ресурсных классов и групп для детей с расстройством аутистического спектра и необходимости ранней диагностики и коррекционной работы.</p>\r\n<p>20 августа 2019 г прошла августовская конференция работников образования Иркутской области \"Региональное образование: достижение, перспективы\", куда был приглашен статс-секретарь-заместитель Министра просвещения РФ Зенькович Павел Станиславович.\r\nЧлен правления АНО \"АПЦ РАСсвет\" Елена Михеева выступала с докладом на тему \"О доступности получения образования детей с расстройствами аутистического спектра в Иркутской области\". На встрече обсуждалась проблема диагностики РАС в Иркутской области, а именно: отсутствие инструментально-научного подхода раннего выявления аутизма.\r\nБыли затронуты вопросы организации системы ранней помощи детям с РАС, а также проблемы создания \"Ресурсного класса\", который по результатам научных исследований и практики отдельных регионов является наиболее эффективной формой получения качественного образования для детей с аутизмом.</p>\r\n<p>5 октября на БМСО состоялся круглый стол по теме \"Доступность качественного образования для обучающихся с ОВЗ в Иркутской области: проблемы и перспективы развития\". Была приведена статистика за два года о количестве детей с ОВЗ в дошкольных и школьных образовательных учреждениях, озвучена нормативная база по обучению детей с ОВЗ. На встрече присутствовали общественные организации г.Иркутска, специалисты и студенты.</p>\r\n<p>Уполномоченный по правам детей Иркутской области, Семенова Светлана Николаевна, говорила о родителях, которые самостоятельно прорабатывают возможность создания ресурсного класса в муниципальной системе образования, пока безрезультатно, несмотря даже на прямое участие Семеновой С.Н. в налаживании диалога с системой образования города Иркутска. АНО \"АПЦ РАСсвет\" готов проводить последовательную работу по созданию таких классов и в г.Иркутск.</p>\r\n<p>23 декабря при заместителе Уполномоченного по правам ребенка в Иркутской области проведено заседание экспертного совета по проблемам детей с расстройством аутистического спектра.\r\nНа заседании присутствовали представители общественных организаций, ПМПК, МСЭ, ИРО, педагогического института и медицинского университета.</p>\r\n<p>Обсуждались вопросы диагностики, системы ранней помощи детям с РАС, а также проблемы создания ресурсных классов в г.Иркутске. По итогу встречи было решено выделить первоочередные задачи по сохранению законных прав детей с РАС на получение медицинских и образовательных услуг, а также провести серию семинаров и лекций ко Дню информирования об аутизме.</p>\r\n<p>\"АПЦ Рассвет\" продолжает вести работу по созданию условий для инклюзивного образования детей с РАС в Иркутске, вдохновляясь примерами успешных проектов в Новосибирске и Красноярске.</p>', '/images/news/23.12.19/1.jpg', '2019-12-24', '/images/news/23.12.19/2.jpg;\r\n/images/news/23.12.19/3.jpg;\r\n/images/news/23.12.19/4.jpg;\r\n/images/news/23.12.19/5.jpg', '', '', 'Новости', '/news.php', 0, '', '0'),
(208, '22', 'Спасибо \"Timeweb\"!', 'Благодарим компанию \"Timeweb\" за многолетнюю поддержку нашей организации! За что мы выбираем \"Timeweb\"?\r\nЗа индивидуальный подход к каждому клиенту!\r\n\r\n', '<p>Благодарим компанию \"Timeweb\" за многолетнюю поддержку нашей организации и предоставление услуг хостинга и домена для нашего сайта на благотворительной основе!</p> \r\n<p>Международный хостинг-провайдер Timeweb работает на рынке с 2006 года и на протяжении своей истории помогает множеству пользователей осваивать сеть Интернет и достигать своих целей за счет различного вида хостинговых услуг, регистрации доменов и конструктора сайтов.</p>\r\n<p>За что мы выбираем \"Timeweb\"?\r\nЗа индивидуальный подход к каждому клиенту!</p>\r\n', '/images/news/32.jpg', '2024-02-09', '/images/gratitude/57.jpg', '', '', 'Новости', 'news.php', 0, '', '0'),
(219, '5', 'Трогательные истории в поддержку детей с аутизмом', 'Чтобы привлечь внимание общества к проблеме аутизма, поддержать людей с аутизмом и их семьи, АНО \"АПЦ Рассвет\" совместно с фотографом Лидией Лось организует выставку и запускает флешмоб в социальных сетях. ', '<p>Друзья, у нас появился замечательный друг и помощник - Лидия Лось, фотограф и художник-керамист. Очень неожиданно, но синхронно в наших головах возникла одна и та идея создания крутого душевного проекта фотовыставки под названием “Аутизм. Семейный альбом”. Участники проекта - дети, которые посещают наш Центр, а также их семьи.\r\n</p>\r\n<p>Официальное открытие выставки было запланировано на 4 апреля 2020 года и приурочено ко Всемирному дню информирования о проблемах аутизма.</p>\r\n<p>Но, к сожалению, в рамках исполнения Указа губернатора Иркутской области от 18 марта 2020г номер 59-уг \"О введении режима функционирования повышенной готовности для территориальной подсистемы Иркутской области единой государственной системы предупреждения и ликвидации чрезвычайных ситуаций\", выставка не может состояться в том формате, в каком она изначально планировалась.</p>\r\n<p>Зная, сколько сил, труда и своих личных эмоций вложили в этот проект все  участники, мы не имеем право не показать его вам, пусть даже в виртуальном пространстве!</p>\r\n<p>Чтобы привлечь внимание общества к проблеме аутизма, поддержать людей с аутизмом и их семьи мы не только организуем выставку, но и запускаем флешмоб #аутизмсемейныйальбом и приглашаем вас к участию! Присоединяйтесь! Рассказывайте! Репостите! Снимайте видео, делайте фото, создайте свой семейный альбом! </p>', '/images/family_album/1.jpg', '2020-04-02', '', '', '', 'Новости', '/news.php', 0, '', '0'),
(221, '22', 'Благодарим всех участников \"Марафона игрушек\"', '27 декабря стартовал наш новогодний марафон игрушек. Целью мероприятия являлся сбор средств на приобретение игрушек в центр взамен сломанных. Готовы увидеть результаты? Мы с радостью покажем!', '<p>Друзья наши, вы даже не представляете, насколько вы круты! 27 декабря стартовал наш новогодний марафон игрушек. Целью мероприятия являлся сбор средств на приобретение игрушек в центр взамен сломанных. Ну что, готовы увидеть результаты?! Мы с радостью покажем!!!</p>\r\n<p>Во-первых, вы не просто собрали необходимую сумму, но и превысили её!!!\r\nПланировали 27000 собрали 30000, это ли не новогоднее чудо?!\r\nНу, а во-вторых, все игрушки по списку уже приобретены, сфотографированы и некоторые даже прошли первые пробы на профпригодность у наших строгих критиков!</p>\r\n<p>Что сказать, только огромное человеческое СПАСИБО! Это те самые моменты, когда понимаешь, как много может дать искреннее внимание и поддержка людей вокруг!!!\r\nМы счастливы, дети счастливы и, надеемся, вы тоже чуточку стали счастливее!</p>\r\n', '/images/help/toys/10.jpg', '2020-03-04', '/images/help/toys/1.jpg;\r\n/images/help/toys/2.jpg;\r\n/images/help/toys/3.jpg;\r\n/images/help/toys/4.jpg;\r\n/images/help/toys/5.jpg;\r\n/images/help/toys/6.jpg;\r\n/images/help/toys/7.jpg;\r\n/images/help/toys/8.jpg;\r\n/images/help/toys/9.jpg;\r\n/images/help/toys/10.jpg;\r\n/images/help/toys/11.jpg;\r\n/images/help/toys/12.jpg;\r\n/images/help/toys/13.jpg;\r\n/images/help/toys/14.jpg;\r\n/images/help/toys/15.jpg;\r\n/images/help/toys/16.jpg;\r\n/images/help/toys/17.jpg;\r\n/images/help/toys/18.jpg;\r\n/images/help/toys/19.jpg;\r\n/images/help/toys/20.jpg;\r\n/images/help/toys/21.jpg;\r\n/images/help/toys/22.jpg;\r\n/images/help/toys/23.jpg;\r\n/images/help/toys/24.jpg;\r\n/images/help/toys/25.jpg', '', '/pages/marafon_igr.php', 'Отчёты', '/project-reports.php', 1, '', '0'),
(224, '18', 'Мастерская \"Дом синего слона\"', '\"Дом синего слона\" заработал!\r\nДвери нашей мастерской гостеприимно открываются раз в неделю, мы комплектуем группу из наших ребят, где они проходят вместе с индивидуальным помощниками все этапы создания своими руками изящных функциональных вещиц.', '<p>\"Дом синего слона\" заработал!\r\nДвери нашей мастерской гостеприимно открываются раз в неделю, мы комплектуем группу из наших ребят, где они проходят вместе с индивидуальным помощниками все этапы создания своими руками изящных функциональных вещиц. Например, сегодня - чайный домик маме!</p>\r\n<p>Этапы работы были визуализированы для каждого: грунтование, окраска, трафаретизация и патинирование. Творческие руководители проекта Светлана Зайченко, Оксана Разгуляева и Виктория Кузнецова внимательно отслеживали выполнение этапов, подсказывая и помогая тьюторам определиться с действиями. Всё участники были очень заинтересованы, поэтому не было томительного ожидания! Во время сушки изделий, ребята спокойно передвигались по центру, отдыхали в игровой, качались на гамаке, копались в кинетическом песке и занимались не менее серьёзными делами.</p>\r\n<p>Мы старались и организовали всё так, чтобы все материалы, необходимые для работы были готовы заранее и ждали своего творца ещё до того, как он придёт на занятие. С нетерпением ждём следующих занятий и принимаем заявки от волонтёров, желающих попробовать себя в наших мастерских в роли тьютора и наставника у ребёнка! Возможно, синий слон - ваше тотемное животное!</p>\r\n<p></p>\r\n<p></p>', '/images/DSS/1.jpg', '2020-03-07', '/images/DSS/1.jpg;\r\n/images/group/07.03.20/2.jpg;\r\n/images/group/07.03.20/3.jpg;\r\n/images/group/07.03.20/4.jpg;\r\n/images/group/07.03.20/5.jpg;\r\n/images/group/07.03.20/6.jpg;\r\n/images/group/07.03.20/7.jpg;\r\n/images/group/07.03.20/8.jpg;\r\n/images/group/07.03.20/9.jpg;\r\n/images/group/07.03.20/10.jpg;\r\n/images/group/07.03.20/11.jpg;\r\n/images/group/07.03.20/12.jpg;\r\n/images/group/07.03.20/13.jpg;\r\n/images/group/07.03.20/14.jpg;\r\n/images/group/07.03.20/15.jpg;\r\n/images/group/07.03.20/16.jpg', '', '', '', '', 0, '', '0'),
(225, '16', 'Создание качественной помощи детям с РАС в Иркутске', 'Наши специалисты продолжают учиться и развиваться! И это не случайно, ведь наш проект \"Программы коррекции поведения и развития навыков\" нацелен на улучшение уровня компетенций специалистов, и, тем самым, создание более качественной помощи детям с аутизмом и их семьям.', '<p>Наши специалисты продолжают учиться и развиваться! И это не случайно, ведь наш проект \"Программы коррекции поведения и развития навыков как основа работы с детьми с аутизмом\" нацелен на улучшение уровня компетенций специалистов, и, тем самым, создание более качественной помощи детям с аутизмом и их семьям.</p>\r\n<p>За время реализации проекта, поддержанного Фондом Президентских Грантов в 2019 году, наши специалисты повысили свою квалификацию на различных курсах, семинарах и тренингах.</p> <p>Кураторы, инструкторы и волонтеры \"Рассвета\" прошли обучение по образовательным программам подготовки специалистов по Прикладному анализу поведения уровня ВСаВА, в период с июля по сентябрь включительно. 1 человек успешно окончил первый курс \"Фундаментальные принципы\" , 2 человека успешно окончили второй курс \"Процедуры и принципы обучения новым навыкам\", и 3 человека успешно окончили третий курс \"Оценка функции поведения\".</p>\r\n<p>Наш куратор и учредитель АНО \"АПЦ РАСсвет\" Елена Михеева в ноябре 2019 года приняла участие в семинаре по Денверской модели обучения (ESDM), модели ранней системы помощи детям с аутизмом. Говорили о компонентах ЕSDM, оценке целевых навыков, разработке плана вмешательства, роли родителей в ESDM. На семинаре Елена встретилась с давними друзьями и коллегами Львом Толкачёвым, Анной Трубициной, а главное, вживую пообщалась с нашим супервизором Марией Михалычевой.</p>\r\n<p>В марте текущего года Елена побывала в Москве на тренинге по использованию альтернативной системы коммуникации PECS, который проходил на базе инклюзивного центра \"Моя планета\".</p>\r\n<p>Спикером и практическим консультантом  тренинга выступила лучший специалист мирового уровня-Сибилл Байорат (Sibylle Bajorat), сертифицированный консультант и клинический директор Pyramid Educational Consultants of Germany UG, доцент и преподаватель прикладной психологии в Deutschen Universität in Armenien (DUA), специалист с многолетней практикой по введению PECS у детей и взрослых, и Ольга Попова, директор АНО «Моя Планета» - официальный представитель PECS® в России, клинический психолог, поведенческий аналитик. \r\n</p>\r\n<p>Это был такой мощный опыт, поток знаний и практических навыков в чистом виде, мы сразу же внедрили полученную информацию в свою работу! </p>\r\n<p>Также поздравляем всех наших кураторов, Веронику Сергеевну и Елену Сергеевну, прошедших обучение на четвертом заключительном модуле ВСаВА, инструктора по АФК Викторию Кузнецову с окончанием второго курса для специалистов по прикладному анализу поведения, а нашего инструктора Анну Попову и ассистента по АФК Дарью Шульгу с окончанием первого модуля!<p>\r\n<p>Обучение осуществлялось при поддержке Фонда президентских грантов.</p>', '/images/news/07.03.20/5.jpg', '2020-07-09', '/images/news/07.03.20/1.jpg;\r\n/images/news/07.03.20/2.jpg;\r\n/images/news/07.03.20/3.jpg;\r\n/images/news/07.03.20/4.jpg;\r\n/images/news/07.03.20/5.jpg;\r\n/images/news/07.03.20/6.jpg;\r\n/images/news/07.03.20/7.jpg;\r\n/images/news/07.03.20/8.jpg', '', '', 'Новости', '/news.php', 0, '', '17'),
(237, '3', 'Новая серия семинаров от \"Рассвета\"', 'В декабре 2019 года была запущена новая серия семинаров для родителей и специалистов от АНО \"АПЦ Рассвет\" при поддержке Фонда президентских грантов в рамках проекта \"Программы коррекции поведения и развития навыков как основа работы с детьми с аутизмом\". ', '<p>В декабре 2019 года была запущена новая серия семинаров для родителей и специалистов от АНО \"АПЦ Рассвет\" при поддержке Фонда президентских грантов в рамках проекта \"Программы коррекции поведения и развития навыков как основа работы с детьми с аутизмом\". </p>\r\n<p>Данный проект является логическим развитием успешно реализованного проекта \"Центра прикладного анализа поведения для детей с расстройствами аутистического спектра и их семей». Новый проект охватывает обучение родителей и специалистов эффективным научно доказанным методам коррекции поведения детей с аутизмом, что позволит им лучше разобраться в потребностях детей с аутизмом и выстроить с ними сотрудничество, позволяющее обеспечить продуктивный обучающий процесс и поддерживающую среду.</p>\r\n<p>На первом семинаре, который состоялся 7 декабря, спикер Никулина Екатерина Сергеевна (учитель-логопед, психолог, 3 курса ВСаВА) по многочисленным просьбам осветила тему аутизма, как современную проблему общества. Также мы использовали видео-ряд, повествующий о специфике нарушения, и о людях, находящихся в спектре. Мероприятие оказалось полезным и родителям, впервые столкнувшимся лицом к лицу с особенностями своих детей, и опытным родителям, и специалистам, работающим с детьми с РАС. Огромную помощь в проведении семинара оказали волонтеры - студенты медицинского университета.</p>\r\n<p>8 февраля 2020 состоялся семинар на тему \"Как начать работу с ребенком с аутизмом\". Данная тематика нашла живой отклик у присутствующих на мероприятии. \r\nСпикером семинара выступила Никулина Екатерина Сергеевна (логопед, 3 модуля BCaBA). В подготовке и проведении мероприятия вновь оказали неоценимую помощь \"волонтёры-медики\".</p>\r\n<p>По традиции, встречи проводили в гостеприимных стенах наших партнёров - библиотеки имени И.И. Молчанова-Сибирского.</p>\r\n<p>18 апреля состоялся первый онлайн-семинар на тему \"Нежелательное поведение и методы его коррекции\". Нет худа без добра. Мы долго не решались перейти в он-лайн формат. А в условиях карантина это оказался единственный способ продолжать делиться с вами нашими знаниями. Оказалось, всё не так страшно и сложно. Всё получилось самым лучшим образом. Благодарим всех участников вебинара за живой интерес и прекрасные отзывы, а спикера - Максимову Марию Андреевну, учредителя АНО \"АПЦ Рассвет\" (3 модуля BCaBA) за прекрасную подачу материала.  </p>\r\n<p>А уже через неделю, 25 апреля в 11 часов  по местному времени состоялся второй он-лайн вебинар, посвящённый теме: \"Развитие речи и альтернативная коммуникация у детей с аутизмом.\" Вебинар был проведен при поддержке Фонда президентских грантов и ОГКУ «Ресурсный центр по поддержке некоммерческих организаций Иркутской области». Спикером семинара выступила Михеева Елена Сергеевна, учредитель АНО \"АПЦ Рассвет\" (3 модуля ВСаВА).</p>\r\n<p>Развитие коммуникативных навыков имеет главенствующую роль у детей с РАС. Поэтому очень важно специалистам и родителям работать с ребёнком именно в этом направлении. Наш вебинар посетило 108 человек, мы получили очень много хороших отзывов. С нетерпением ждем новых встреч!</p>\r\n', '/images/seminar/25.04.20/4.jpg', '2020-04-25', '/images/seminar/25.04.20/1.JPG; \r\n/images/seminar/25.04.20/2.JPG; /images/seminar/25.04.20/3.JPG; /images/seminar/25.04.20/4.jpg;  /images/seminar/25.04.20/5.JPG;\r\n/images/seminar/25.04.20/6.JPG;\r\n/images/seminar/25.04.20/7.JPG;\r\n/images/seminar/25.04.20/8.JPG', '', '', '', '', 0, '', '17'),
(238, '3', 'Практические тренинги по методам АВА', 'Проект \"Программы коррекции поведения и развития навыков\" при поддержке Фонда президентских грантов нацелен на улучшение уровня компетенций специалистов и создание более качественной помощи детям с аутизмом и их семьям. В рамках проекта \"Рассвет\" провел несколько практических тренингов для специалистов и родителей. ', '<p>Проект \"Программы коррекции поведения и развития навыков\" при поддержке Фонда президентских грантов нацелен на улучшение уровня компетенций специалистов и создание более качественной помощи детям с аутизмом и их семьям. В рамках проекта \"Рассвет\" провел несколько практических тренингов для специалистов и родителей детей с РАС. Тренинги охватывали важные темы от установления сотрудничества с ребенком до методов обучения сложным вербальным навыкам. Все участники имели возможность потренироваться в практическом применении метод АВА в работе с детьми.</p>\r\n<p>29 и 30 ноября 2019 года в АВА-центре АНО \"АПЦ Рассвет\" прошел двухдневный тренинг  \"Оценка актуальных навыков ребенка и составление индивидуальной программы\". Тренинг провели Шапилова Е.А., Никулина Е.С. и Михеева Е.С., учредители АНО \"АПЦ Рассвет\", три модуля сертифицированной программы подготовки специалистов по прикладному анализу поведения уровня ВСаВА.</p>\r\n<p>Прикладной анализ поведения подразумевает детальную оценку навыков ребенка, тщательный выбор целей для обучения, планирование обучающего процесса и мониторинг развития навыков. Обзору качественных и современных инструментов - \"VB-MAPP\", \"ABLLS-R\" и \"AFLS\", предназначенных для этих целей, была посвящена теоретическая часть тренинга. Практическая часть тренинга: тестирование ребенка с помощью \"Протокола для оценки базовых речевых и учебных навыков \"ABLLS-R\".\r\n</p>\r\n<p>Приятно отметить, что на тренинге была отзывчивая и готовая включиться в работу аудитория из специалистов и родителей!\r\nБлагодарим фотографа Наталью Тарасову, которая помогла запечатлеть самые интересные моменты тренинга!</p>\r\n<p>В январе состоялся двухдневный тренинг \"Первые занятия с ребенком: поиск мотивации, ввод жетонов\". Один из наиболее сложных аспектов обучения детей с аутизмом – это привлечение их к участию в занятиях. Часто ребенок просто не позволяет нам его обучить. Из-за этого дети с РАС нередко получают ярлык необучаемых. О поведенческом взгляде на мотивацию, процедуре подкрепления желаемого поведения, установлении сотрудничества с ребенком и введении жетонной системы подкрепления рассказали слушателям учредители АНО \"АПЦ Рассвет\" Шапилова Е.А. и Максимова М.А.</p>\r\n<p>Михеева Е.С. и Родыгина Е.А. провели практическую часть мероприятия, где помогли участникам тренинга установить сотрудничество с нашим помощником Сашей, главным героем мероприятия. Участники тренинга также практиковались в проведении обучающих сессий с Сашей, направленных на формирование учебного поведения, обобщение навыков просьбы с помощью карточек PECS.</p>\r\n<p>О прошедшем тренинге вышла публикация в региональных СМИ: <a href=\"https://asi.org.ru/report/2020/01/31/irkutsk-trening-spetsialisty-roditeli-deti-s-autizmom/\" target=\"_blank\">Тренинг для специалистов и родителей детей с аутизмом прошел в Иркутске</a></p>\r\n<p>14-15 марта состоялся практический тренинг, посвященный методам обучения в АВА. Участниками тренинга были как начинающие специалисты, уже знакомые с основами прикладного анализа поведения, так и новички, только осваивающие азы данной научной дисциплины.</p>\r\n<p>Мы на практике убедились, что методы прикладного анализа поведения эффективны не только для детей с аутизмом, но и для детей с другими нарушениями и даже для слушателей семинара. В ходе тренинга участники смогли применить практические навыки в паре друг с другом, и в обучении двух замечательных девочек: Таси и Марины. У Таси расстройство аутистического спектра, в первый день тренинга мы обучали ее различать новые для нее глаголы на слух и называть их. У Марины нарушение слуха и ей сложно различать голоса незнакомых ей людей. Поэтому во второй день мы сделали акцент на поведении слушателя.</p>\r\n<p>И, конечно, такие встречи невозможны без вкусного чая с ароматными пирогами! Благодарим наших уже постоянных партнеров пекарню КИМ за чудесную выпечку и тёплое отношение, друзья, вы просто лучшие.\r\nМы уверены, что выходные принесли участникам тренинга много полезных знаний и положительных эмоций, Благодарим всех, кто был эти два дня с нами. До новых встреч, друзья!</p>\r\n<p>Завершающий 4-х дневный тренинг для специалистов и родителей центра по теме \"Вербальное поведение\" состоялся в конце апреля в дистанционном формате. </p>\r\n<p>При проведении таких тренингов мы стараемся раскрыть не только теоретические азы, но и отрабатываем практические навыки. Нами были рассмотрены многие аспекты темы \"Вербальное поведение\": от навыков коммуникации с помощью системы обмена карточками PECS до ответов на вопросы по картинке полным предложением.</p>\r\n<p>В практической части тренингов наши помощники: Ваня, Ника, Тася и Влад - помогали инструкторам совершенствовать свои навыки. Дети отлично справились со своей задачей, несмотря на то, что специалисты находились по ту сторону экрана!</p>\r\n<p>Четыре дня пролетели незаметно. Все встречи получились информативными, познавательными и эмоционально насыщенными. У нас по-другому не бывает! Спасибо всем участникам и ведущим!</p>\r\n<p>Мероприятия осуществлялись при поддержке Фонда президентских грантов.</p>', '/images/training/03.20/8.jpg', '2020-04-30', '/images/training/03.20/1.jpg; /images/training/03.20/2.jpg; /images/training/03.20/3.jpg; /images/training/03.20/4.jpg;  /images/training/03.20/5.jpg;  /images/training/03.20/6.jpg; \r\n/images/training/03.20/7.jpg; /images/training/03.20/9.jpg; /images/training/03.20/10.jpg;\r\n/images/training/03.20/11.jpg ', '', '', '', '', 0, '', '17'),
(240, '17', '\"Советы по руководству детьми, находящимися дома во время пандемии COVID-19\" ', 'В статье приведены некоторые советы, которые могут помочь вам справиться со своими детьми с аутизмом во время нахождения дома на время всеобщего закрытия общественных мест и социального дистанцирования/изоляции из-за пандемии COVID-19. <br>\r\nИсточник: www.autismspeaks.org <br>\r\nПеревод Марии Зубаревой ', '	<p>Источник: <a class=\"a-blue\" href=\"https://www.autismspeaks.org/news/tips-managing-children-home-during-covid-19\" target=\"_blank\"> www.autismspeaks.org </a></p> \r\n\r\n	<p>2 апреля 2020 </p>\r\n<p>Перевод: Мария Зубарева, учредитель АНО \"АПЦ РАСсвет\"</p>\r\n<p><i>Эта статья любезно предоставлена Университетом Рочестера, сайтом сети Autism Treatment организации Autism Speaks.</i> </p>\r\n<p>Ниже приведены некоторые советы, которые могут помочь вам справиться со своими детьми с аутизмом во время нахождения дома на время всеобщего закрытия общественных мест и социального дистанцирования/изоляции из-за пандемии COVID-19. </p>\r\n<p> <b>1. Создайте правила вашего дома. </b> Вы с вашим ребенком можете установить эти правила вместе. Вы можете использовать те же самые или аналогичные правила, которые применяются в классе или школе вашего ребенка. Вы с ребенком можете нарисовать или написать эти правила и повесить их где-то у себя дома. </p>\r\n<p><b>2. Установить расписание на день или распорядок дня для ребенка. </b> Постарайтесь разбить длинные неструктурированные периоды на более определенные занятия. Например, свободное время можно разделить на периоды для книг и пазлов, декоративно-прикладного искусства, настольных игр и так далее. Постарайтесь включить в расписание своего ребенка времяпрепровождение на свежем воздухе, а также физическую активность, если это позволяют погодные условия и вопросы безопасности. Не забудьте включить в распорядок дня приемы пищи. Зачастую дети в течение дня чаще хотят есть, когда они не ходят в школу. Сохраняйте размеры порций разумными и старайтесь не допускать перекусов вне запланированных приемов пищи. Мы хотим, чтобы вам хватило запасов еды надолго! </p>\r\n<p>- Запишите этот распорядок дня вместе с ребенком или составьте визуальное расписание из картинок (с помощью компьютера или нарисуйте сами). Регулярно просматривайте расписание вместе с ребенком. В сети интернет вы можете найти множество примеров расписаний в качестве подсказки. </p>\r\n<p>- Мы понимаем, что многим родителям придется совмещать удаленную работу из дома и управление распорядком дня детей. Постарайтесь составить расписание для ребенка с учетом ваших рабочих обязанностей, оставив время для тихих игр или электронных устройств на тот период, когда вам нужно будет выполнять другие обязанности. </p>\r\n<p><b>3. Мотивируйте своего ребенка. </b> Просить своего ребенка сделать школьные задания дома может быть непросто. Полезно будет подготовить что-то, что может мотивировать его/ее выполнить свои задания. Вы можете сделать это, просто составив расписание своего ребенка так, чтобы задания шли перед развлечениями (например, «Сначала школьные задания, потом игры на свежем воздухе»). Вы также можете создать систему поощрений ребенка за выполнение заданий. Например, вы можете сделать таблицу «звезд» или стикеров и предоставлять ребенку какой-то сюрприз или дополнительное время с гаджетами, если ребенок соберет все «звезды»/стикеры. </p>\r\n<p><b>4. Перемещайте и упорядочивайте игрушки своего ребенка. </b> У детей часто бывают множество игрушек по всему дому, но они иногда забывают о них. Может быть полезно упорядочить и ограничить число игрушек, одновременно находящихся в доступе у ребенка. Затем вы можете менять или перемещать игрушки каждый день или через день. Например, вы можете сложить игрушки ребенка в корзины для игрушек. Вы можете убрать все корзины, кроме одной или двух. И потом каждый день вы можете доставать ребенку новую корзину. Это поможет поддерживать новизну игрушек и интерес ребенка к ним. </p>\r\n<p><b>5. Помогите своему ребенку приступить к занятиям. </b> Некоторым детям сложно придумать свои собственные идеи для игровой деятельности, подобрать необходимый материал и начать игру. Помогите ребенку составить план того, чем он хотел бы заниматься в свое свободное время. Вы можете подсказать ему несколько идей, создав для них «доску выбора» или «меню вариантов», или просто записав некоторые варианты на бумаге. Как только дети решат, чем хотят заняться, помогите им собрать материалы, которые им понадобятся, и приступить к выполнению задания. Когда они приступят к заданию, вы можете время от времени проверять их и хвалить за то, что они продолжают выполнять задание. </p>\r\n<p><b>6. Установите ограничения на время, проведенное перед экраном. </b> Ограничение времени перед экраном может быть одной из самых больших сложностей, возникающих у семей во время перерывов между занятиями. Может быть полезно установить четкие лимиты еще до начала рабочего дня и почаще пересматривать их вместе с ребенком. Вы можете сделать это, запланировав «экранное» время на определенное время дня и разрешая проводить время перед экраном только в эти периоды. Вы также можете разрешить своему ребенку определенное количество «экранного» времени (например, 1 час) и отслеживать это время в течение всего дня. Использование таймеров, таких как визуальный таймер (для этого существует множество приложений) может быть полезным для установки таких ограничений. </p>\r\n<p><b>7. Управляйте конфликтами между братьями и сестрами. </b> Постарайтесь запланировать некоторые отдельные занятия для каждого из детей периодически в течение дня. Возможно, вам придется контролировать групповые занятия. Хвалите детей и позвольте им вместе добиваться награды, вежливо разговаривая друг с другом, не дерясь друг с другом и работая сообща для достижения общей цели. </p>\r\n<p>В этом <a class=\"a-blue\" href=\"https://www.autism.org/coronavirus-impact-home-all-day-basic-supports-and-strategies-to-get-us-through-and-keep-us-sane/9\" target=\"_blank\">онлайн-семинаре</a> есть много полезных и реалистичных советов о том, как руководить детьми во время COVID-19.</p>\r\n<p>Но пожалуйста, будьте добры к себе и помните: каковы бы ни были ваши личные, профессиональные или родительские роли, сейчас хорошее время для всех нас, чтобы не забывать давать себе передышку и быть добрыми к самим себе. Ни от одного родителя не стоит ожидать, что он полностью возьмет на себя роль учителя и будет обеспечивать тот же уровень обучения, что и в школе. </p>\r\n<p>Может так случиться, что вам нужно будет предоставить ребенку больше «экранного» времени, чтобы завершить свою работу, или отвлечь детей играми на свежем воздухе, чтобы уделить несколько минут самому себе. Мы надеемся, вы попытаетесь разрешить себе время от времени «отпускать» свои высокие стандарты. Виртуальная связь с другими родителями также может быть полезна в борьбе за этот новый уровень заботы о нашем сообществе детей дома. (У вашего школьного района или родительской группы может быть страничка группы в Facebook). </p>\r\n<p>Забота о себе особенно важна для каждого в эти неопределенные времена. Ниже приведены несколько идей курсов развития осознанности и управления стрессом и приложений, которые могут быть полезны. Большинство этих сервисов являются бесплатными, недорогими или предлагают бесплатную подписку на ограниченное время. Многие из этих практик очень короткие – даже 2-3 минуты в течение дня могут иметь значение. </p>', '/images/TRANSLATE/4.jpg', '2020-05-06', '', '', '', 'Новости', '/news.php', 0, '', '0');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(245, '4', 'Совместные занятия с типично развивающимися сверстниками', 'Одной из задач нашего проекта при поддержке Фонда президентских грантов является подготовка детей с аутизмом к инклюзивному образованию: обобщение навыков в мини-группах с участием нормально развивающихся сверстников и последующая генерализация этих навыков в общеобразовательной среде. ', '<p>Занятия в группе - неотъемлемая часть обучения детей с аутизмом, часто имеющих дефицит в коммуникации и проблемы взаимодействия со сверстниками. \r\nСреди наших учеников есть ребята, которые впервые стали заниматься в группе детей благодаря именно интенсивному поведенческому вмешательству - \r\nцеленаправленным действиям по созданию благоприятной обучающей среды и поддержанию мотивации к занятиям.</p>\r\n<p>Всё больше ребят центра \"РАСсвет\" успешно включаются в работу группы. Одна из наших целей - развитие мотивации к взаимодействию друг с другом. \r\nМы обучаем общаться, обращаться с просьбой не только к взрослым, но и к сверстникам, выполнять совместные задания с ожиданием своей очереди, \r\nиспользуя методы Прикладного анализа поведения.</p>\r\n<p>Одной из задач нашего проекта при поддержке Фонда президентских грантов является подготовка детей с аутизмом к инклюзивному образованию: обобщение навыков в мини-группах с участием нормально развивающихся сверстников и последующая генерализация этих навыков в общеобразовательной среде. </p>\r\n<p>Принимая участие в нашем проекте, нейротипичные дети выступают не только в роли учеников, но и в роли учителей и наставников для ребятишек с аутизмом. \r\nИ вместе с тем сами учатся общению с такими детьми. Это очень здорово, когда барьеры в общении сокращаются и людям проще понимать друг друга!</p>\r\n<p>Приготовить задание, которое было бы одинаково интересно нейротипичному ребёнку и ребёнку с аутизмом - задача не из простых. Но мы и эту задачу готовы успешно решить! И даже в дистанционном формате!</p>\r\n<p>Наши инстукторы и кураторы - активные участники процесса, родители - всегда на подхвате! Учиться - это здорово!</p>\r\n<p>Проект осуществляется при поддержке Фонда Президентских Грантов.</p>', '/images/group/2019-2020/2.jpg', '2020-05-25', '/images/group/2019-2020/1.jpg;\r\n/images/group/2019-2020/3.jpg;\r\n/images/group/2019-2020/4.jpg;\r\n/images/group/2019-2020/5.jpg;\r\n/images/group/2019-2020/6.jpg;\r\n/images/group/2019-2020/7.jpg;\r\n/images/group/2019-2020/8.jpg;\r\n/images/group/2019-2020/2020-6.jpg', '', '', '', '', 0, '', '17'),
(248, '4', 'Индивидуальные занятия в режиме онлайн', 'Режим самоизоляции не помеха систематическим индивидуальным занятиям. Наши ученики уже освоили занятия на расстоянии и показывают хорошие результаты. Ребята закрепляют уже освоенные навыки, а также успешно осваивают новые. А как радуются дети, увидев знакомых инструкторов на экранах мониторов! ', '<p>Режим самоизоляции не помеха систематическим индивидуальным занятиям. Наши ученики уже освоили занятия на расстоянии и показывают хорошие результаты. Ребята закрепляют уже освоенные навыки, а также успешно осваивают новые. А как радуются дети, увидев знакомых инструкторов на экранах мониторов! </p>\r\n<p>Дистанционные индивидуальные уроки приносят свои результаты: инструкторы по-прежнему любимые друзья, а все навыки методично наращиваются.\r\nПосмотрите, как весело и ловко ребята проходят каждый этап занятий.</p>\r\n<p>Представляем вашему вниманию фрагменты с дистанционных индивидуальных занятий Влада и Богдана. Они хорошо научились понимать инструкции через экран монитора, и главное, выполнять их.</p>\r\n<p>Дистанционные занятия осуществляются при поддержке Благотворительного фонда Сбербанка \"Вклад в будущее\"</p>\r\n<section class=\"video-detail\"><iframe src=\"https://rutube.ru/play/embed/30b7374b4d7241a6fee3a8233e19b48e/\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen class=\"social-video\"></iframe></section>', '/images/ind/2.jpg\r\n', '2020-06-15', '', '\r\n', '', '', '', 0, '', '15'),
(252, '4', 'Уроки творчества онлайн', 'Благодаря проекту \"Скоро в школу - подготовка детей с аутизмом к обучению в инклюзивной среде\" при поддержке Фонда \"Вклад в будущее\", мы познакомились с замечательным учителем по изобразительному искусству и труду Александрой, которая обучает наших детей творчеству.', '<p>Благодаря проекту \"Скоро в школу - подготовка детей с аутизмом к обучению в инклюзивной среде\" при поддержке Фонда \"Вклад в будущее\", мы познакомились с замечательным \r\nучителем по изобразительному искусству и труду Александрой, которая обучает наших детей творчеству.</p>\r\n<p>В связи с ситуационной обстановкой занятия проходят дистанционно. Но нет худа без добра, при подготовке к занятиям мы готовим видео материалы и ведём запись занятий, \r\nчто теперь даёт возможность поделиться с вами нашими наработками, а также фрагментами занятий наших детей. </p>\r\n<p>Помните высказывание И. Гёте: \"Никто не знает, каковы его силы, пока их не использует\". Наши дети используют свои силы на максимум. \r\nДистанционные уроки ИЗО и труда в индивидуальном и групповом формате каждую неделю? Не вопрос! Наш терпеливый и талантливый педагог Александра может с лёгкостью подтвердить огромный \r\nтворческий потенциал своих учеников и большое желание учиться чему-то новому. А мы просто рады за всех участников этого процесса! Присоединяйтесь к просмотру и творите вместе с нами!</p>\r\n<p>Если вы не знаете, как слепить веселого инопланетянина или как нарисовать доброго медвежонка Винни Пуха - наши ребята покажут вам на совместных групповых занятиях. Мелкая моторики крайне важна для комплексного развития, и не всё ребятам даётся так легко, как может показаться на первый взгляд. Но они стараются, и в итоге получаются вот такие замечательные работы.</p>\r\n<p>Проект осуществляется при поддержке Благотворительного фонда Сбербанка \"Вклад в будущее\"</p>\r\n<section class=\"video-detail\">\r\n								<iframe src=\"https://rutube.ru/play/embed/e6e6023d0ad78723d16c1ba123669f80/\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen class=\"social-video\"></iframe>\r\n			</section>', '/images/group/2020-6/10.jpg\r\n', '2020-06-20', '/images/ind/IZO/1.jpg;\r\n/images/ind/IZO/3.jpg;\r\n/images/ind/IZO/9.jpg;\r\n/images/ind/IZO/12.jpg', '', '', '', '', 0, '', '15'),
(257, '12', 'Проект \"Движение\": тренировки онлайн', 'В марте этого года в АНО «АПЦ РАСсвет» стартовал спортивный проект «Движение». Из-за пандемии занятия АФК проходят в дистанционном формате. Родителям тоже пришлось подключиться к тренировкам, чтобы в домашних условиях помогать своим детям.', '<p>В марте этого года в АНО «АПЦ РАСсвет» стартовал спортивный проект «Движение». Из-за пандемии с начала апреля занятия АФК проходят в дистанционном формате. Это непростое испытание для всех участников процесса. Родителям тоже пришлось подключиться к тренировкам, чтобы в домашних условиях помогать своим детям.\r\n</p>\r\n<p>Как проходят занятия? Тренер ведет тренировку дистанционно в онлайн режиме, а тьюторами становятся родители – ведь моделировать свое тело в пространстве с опорой на монитор компьютера для ребенка довольно сложно. Наши ученики не теряют контакт со своим учителем, а это значит, что можно продолжать нарабатывать навыки. Все упражнения адаптированы для каждого ребёнка, так же как и время занятий. А специалисты АФК подбирают программу исходя из индивидуальных особенностей учеников. Все эти факторы в совокупности дают превосходный результат!\r\n</p>\r\n<p>Вопрос со спортоборудованием также решён: вместо гантелей используют бутылки с водой, а гимнастические палки заменили на швабры и сложенные вдвое банные полотенца. Девиз наших ребят: \"Возможно всё! На невозможное требуется чуть дольше времени\"!</p>\r\n<p>Пока сидим дома, самое время потренироваться спокойно и осознанно. Мы сделали для вас подборку упражнений, адаптированных для ребёнка с аутизмом. И начинаем делиться нашими комплексами! </p>\r\n<p>Итак, комплекс №1 \"Фитбол + растяжка спины\". Делаем \"бутерброд\": сложное+простое!</p>\r\n<p>Комплекс №2: Не самый простой триплекс \"Сильный пресс\" для ребёнка, но можно усилить мотивацию ценностью поощрения! А для мам эти упражнения просто находка - делайте каждое в течение минуты ежедневно.</p>\r\n<p>Комплекс №3 \"Сильная и гибкая спина\". Крепкий мышечный корсет это наша защита от внешнего воздействия негативных факторов. Гибкость нам необходима для сохранения суставов и связок подвижными долгое время. Сидение за компьютером и уроками, высокие физические нагрузки в быту ослабляют способность мышц спины держать позвоночник в анатомически правильном положении. Занимайтесь этими упражнениями ежедневно!</p>\r\n<p>Проект \"Движение\" осуществляется при поддержке Фонда президентских грантов.</p>', '/images/AFK/fit/16.jpg', '2020-07-25', '/images/AFK/fit/14.jpg;\r\n/images/AFK/fit/15.jpg;\r\n/images/AFK/fit/18.jpg;\r\n/images/AFK/fit/17.jpg\r\n', '', '', '', '', 0, '', '0'),
(269, '4', 'Скоро в школу', 'Подготовка к школе детей с расстройством аутистического спектра имеет свои особенности. В Иркутске АНО «АПЦ Рассвет» – организация, созданная родителями детей с ментальными нарушениями, при поддержке благотворительного фонда Сбербанка «Вклад в будущее» реализует проект «Скоро в школу». Педагоги центра рассказали о плюсах и минусах дистанционной подготовки первоклашек.', '<p>Как подготовить ребенка с аутизмом к школе в дистанционном режиме.</p>\r\n<p>На этой неделе стало известно, что российские школы начнут работать с 1 сентября в привычном очном формате. Многие родители и учителя выдохнули с облегчением: польза от уроков в онлайн режиме вызывала у всех большие споры, хотя из-за пандемии решение перейти с традиционного обучения на дистанционное было обоснованным и неизбежным. И пока родители скупают канцелярию к грядущему учебному году, а школьники торопятся закончить летние читательские дневники, будущие первоклассники готовятся к началу новой жизни.</p>\r\n<p>Подготовка к школе детей с расстройством аутистического спектра имеет свои особенности, для помощи одному ребенку часто привлекают сразу несколько специалистов. В Иркутске АНО «АПЦ Рассвет» – организация, созданная родителями детей с ментальными нарушениями, при поддержке благотворительного фонда Сбербанка «Вклад в будущее» реализует проект «Скоро в школу». Педагоги центра рассказали о плюсах и минусах дистанционной подготовки первоклашек.</p>\r\n<p>В Иркутске режим самоизоляции длится с марта, и тогда же стартовал проект «Скоро в школу». Это было очень своевременно - даже дети с РАС, посещающие подготовительные группы в коррекционных детских садах, вынуждены были находиться дома без обучения и социализации. Педагоги «РАСсвета» с помощью АВА-терапии начали формировать у своих воспитанников учебное поведение, академические и бытовые навыки, необходимые для школьной жизни. По ту сторону экрана своих чад активно поддерживали родители. Большая нагрузка легла на родителей детей с низким уровнем развития навыков, им пришлось принимать непосредственное участие в занятиях – рука в руке. Зато они лучше узнали, как работать с ребенком дома.</p>\r\n<p>- Обучение идет индивидуально и в малых группах по 4-5 человек. Практика показала, что дети с удовольствием занимаются через экран компьютера, и такое обучение принесло существенные результаты, материал усваивается. Например, наш 8-летний воспитанник Иван, не использующий речь, научился печатать на компьютере и отвечает на вопросы с помощью электронного приложения PECS 4+,- рассказывает Елена Сергеевна Михеева, один из кураторов центра. - Минусом реализации проекта явилось отсутствие живого общения, к тому же дома невозможно создать школьную атмосферу, то есть полного эффекта адаптации нет. Но при этом дети не растеряли уже освоенные ранее на очных занятиях навыки и научились ждать своей очереди, выступать учителями по отношению к друг другу, выполнять совместно учебную деятельность. Мы надеемся, что период самоизоляции подойдет к концу, и занятия в группах возобновятся в стенах центра.</p>\r\n<p>Конечно, в процессе обучения возникают и проблемы технического характера - перебои в работе компьютеров и интернета. Решаются они обычно, либо участники занятия повторно переключаются, урок переносится на другое время. Это не всегда удобно, но педагоги уже привыкли к такого рода неожиданностям.\r\n</p>\r\n<p>- У нас увеличилась нагрузка на подготовку к занятиям: помимо учебного материала, необходимо подготовить новую презентацию к каждому занятию, а также продумать, как замотивировать ребенка все занятие находиться у экрана. Одно занятие приходится делить на несколько этапов, в результате возникла необходимость в привлечении дополнительных педагогов в проект, - делится опытом Екатерина Андреевна Родыгина, председатель правления «АПЦ РАСсвет» - Возросла потребность в учебных материалах, которых не было у родителей дома. Материалы для творческих занятий, некоторые мотивационные игрушки были отвезены волонтерами детям домой. В том числе необходимо было напечатать карточки каждому ребенку и дублирующий набор педагогу и направить волонтеров домой к каждому ребенку - возникли незапланированные расходы на бумагу и ламинирующую пленку.</p>\r\n<p>Самоизоляция в Иркутской области вновь продлена, поэтому основная идея проекта по адаптации к привычному учебному процессу в школе полностью не решена. Однако специалисты «АПЦ РАСсвет» оптимистично настроены на завершение проекта, то, что зависит от них самих они выполняют с полной отдачей.</p>\r\n<p></p>', '/images/group/2020-7/1.jpg', '2020-07-31', '/images/group/2020-7/1.jpg;\r\n/images/group/2020-7/3.jpg;\r\n/images/group/2020-7/5.jpg;\r\n/images/group/2020-7/6.jpg', '', '', 'Новости', 'news.php', 0, '', '15'),
(274, '4', 'Дистанционные занятия в группе', 'В последний месяц лета школьники и их родители готовятся к новому учебному году. А у наших ребят занятия в самом разгаре! Дистанционное обучение помогает не забыть уже пройденный материал, и узнать много новых, полезных вещей.', '<p>В последний месяц лета школьники и их родители готовятся к новому учебному году. А у наших ребят индивидуальные и групповые занятия в самом разгаре! Дистанционное обучение помогает не забыть уже пройденный материал, и узнать много новых, полезных вещей: от простого счета, до правильного употребления частей речи.</p>\r\n<p>Ребята пишут, читают, считают и отвечают на сложные вопросы. Ведь они готовятся стать первоклассниками!</p>\r\n<p>Как превратить математику и чтение в увлекательную игру, воссоздать школьную атмосферу на дистанционном уроке и подружиться на расстоянии? Ответы на эти вопросы узнаете, посмотрев наше видео с групповых занятий Таси, Сони, Яны, Влада и Богдана. Мальчишки и девчонки, а также их родители учатся вместе с нашими педагогами и получают удовольствие от самого процесса!</p>\r\n<p>Следующие ролики об успехах наших первоклассников будут сниматься уже в ресурсном классе!</p>\r\n<p>Проект \"Скоро в школу\" осуществляется при поддержке благотворительного фонда Сбербанка \"Вклад в будущее\"</p>\r\n<section class=\"video-detail\"><iframe src=\"https://rutube.ru/play/embed/0fcd4c846bd08cc7e12326df5d80c0bd/\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen class=\"social-video\"></iframe></section>', '/images/ind/6.jpg', '2020-08-28', '', '', '', '', '', 0, '', '15'),
(277, '22,19', 'День знаний в ресурсном классе', 'Выражаем искреннюю благодарность начальнику управления образования Иркутского района Зарипову Роману Рафаиловичу, директору МОУ ИРМО \"СОШ поселка Молодежный\" Власиевской Наталье Викторовне за содействие в открытии первого ресурсного класса для детей с аутизмом в Иркутском районе!', '<p>Вот и настал этот день! С волнением и трепетом мы зашли сегодня в гостеприимные стены прекрасной \"космической\" школы! В классе детей встретили учитель Михеева Елена Сергеевна и приветливые тьюторы. И начался наш особый классный час с любимыми играми, аппликациями и угощениями. К нам заглянули с поздравлениями и директор школы Власевская Наталья Викторовна, и курирующий наш класс завуч Персидская Анастасия Сергеевна. Ребята весело провели время в школе. Завтра мы снова соберемся все вместе и начнем нашу новую школьную жизнь, в нашем новом ресурсном классе!</p>\r\n<p>Первому ресурсному классу в Иркутском районе быть! Наконец, мы с радостью и гордостью можем это сказать. Надеемся, что с открытием нашего первого, в любом смысле этого слова, класса, начнется новая веха в образовании детей с аутизмом в Иркутске!</p>\r\n<p>Мы выражаем глубокую признательность и искреннюю благодарность начальнику управления образования администрации Иркутского районного муниципального образования Зарипову Роману Рафаиловичу, директору МОУ ИРМО \"СОШ поселка Молодежный\" Власиевской Наталье Викторовне, заместителю директора по учебно-воспитательной работе Кадниковой Марине Васильевне и всему коллективу школы!</p>\r\n<p>Особая благодарность группе волонтеров из Иркутска - команде благотворительного проекта «Рассвет», которые проявили инициативу и вызвались помочь нам с закупкой оборудования для класса. В течение месяца молодые люди вели сбор средств в соцсетях! Наши новые друзья пришли в гости к первоклассникам МОУ ИРМО \"СОШ поселка Молодежный\", и не с пустыми руками! Благодаря новому оборудованию адаптация к школе учеников ресурсного класса пройдет в комфортных условиях!</p>\r\n<p>Большое спасибо Вам за доброту и внимание к детям с расстройством аутистического спектра. От души желаем Вам здоровья и успехов во всех начинаниях!</p>\r\n<p>Поздравляем всех учителей, учеников и их родителей с началом учебного года! Желаем только приятных впечатлений и успехов в освоении новых знаний!</p>\r\n<p>Спасибо за фото нашему давнему другу, фотографу-документалисту Лидии Лось!</p>', '/images/RK/20.09.01/1.jpg', '2020-09-01', '/images/RK/20.09.01/9.jpg;\r\n/images/RK/20.09.01/2.jpg;\r\n/images/RK/20.09.01/3.jpg;\r\n/images/RK/20.09.01/4.jpg;\r\n/images/RK/20.09.01/5.jpg;\r\n/images/RK/20.09.01/6.jpg;\r\n/images/RK/20.09.01/7.jpg;\r\n/images/RK/20.09.01/8.jpg', '', '', 'Новости', 'news.php', 0, '', '0'),
(281, '18', '\"Дом синего слона\" онлайн', 'Что делать, если невозможно привести ребёнка на занятие творчеством? Правильно - можно устроить дистанционное занятие! Материалы для поделки - коробочки для специй - привезли волонтёры, назначено время встречи и можно начинать творить!', '<p>Что делать, если невозможно привести ребёнка на занятие творчеством? Правильно - можно устроить дистанционное занятие! Материалы для поделки - коробочки для специй - привезли волонтёры, назначено время встречи и можно начинать творить!</p>\r\n<p>Работали в несколько этапов: зашкуривали, покрывали тонированной грунтовкой, делали милый французский декупаж и, конечная станция, - лакирование! Изделия получились домашние, добротные и крайне практичные! Оцените труд учеников мастерской!</p>', '/images/DSS/20.07.25/15-1.jpg', '2020-07-25', '/images/DSS/20.07.25/1.jpg;\r\n/images/DSS/20.07.25/2.jpg;\r\n/images/DSS/20.07.25/3.jpg;\r\n/images/DSS/20.07.25/4.jpg;\r\n/images/DSS/20.07.25/5.jpg;\r\n/images/DSS/20.07.25/6.jpg;\r\n/images/DSS/20.07.25/7.jpg;\r\n/images/DSS/20.07.25/8.jpg;\r\n/images/DSS/20.07.25/8-1.jpg;\r\n/images/DSS/20.07.25/9.jpg;\r\n/images/DSS/20.07.25/10.jpg;\r\n/images/DSS/20.07.25/11.jpg;\r\n/images/DSS/20.07.25/12.jpg;\r\n/images/DSS/20.07.25/13.jpg;\r\n/images/DSS/20.07.25/14.jpg;\r\n/images/DSS/20.07.25/15.jpg;\r\n/images/DSS/20.07.25/16.jpg;\r\n/images/DSS/20.07.25/17.jpg;\r\n/images/DSS/20.07.25/18.jpg', '', '', '', '', 0, '', '0'),
(287, '19', 'Как прошел первый месяц в ресурсном классе', 'Начало учебного года — важный этап для всех школьников, особенно для тех, кто впервые переступил порог школы. В сентябре учителя проверяют знания ребят через контрольные работы, а педагоги центра «Рассвет» завершают проект «Скоро в школу», проводя тестирование ABLLS-R. ', '<p>Начало учебного года — важный этап для всех школьников, особенно для тех, кто впервые переступил порог школы. В сентябре учителя проверяют знания ребят через контрольные работы, а педагоги центра «Рассвет» завершают проект «Скоро в школу», проводя тестирование ABLLS-R. Этот тест помогает оценить изменения в поведении и учебных навыках будущих первоклассников после занятий, начавшихся ещё весной.\r\n</p>\r\n<p>Педагоги отмечают значимость комплексной проверки всех сфер развития ребёнка, чтобы создать оптимальные условия для индивидуального обучения. Каждый ученик получит свою поведенческую программу, учитывающую его потребности и дефициты.</p>\r\n<p>Помимо традиционных уроков, в школе посёлка Молодёжный прошёл необычный классный час в 1\"Г\" классе, который провела педагог «АПЦ РАСсвет» и учительница первого иркутского ресурсного класса Елена Михеева.  С учениками обсуждали не учебу и оценки, а то, какими разными бывают люди, и как важно относится друг к другу с пониманием и поддержкой. \r\n</p>\r\n<p>- У нас у всех есть индивидуальные запросы, особенности, предпочтения в еде, одежде, музыке и т.д. Но мы находим общий язык, общие интересы. У детей с расстройством аутистического спектра мозг работает иначе, чем у большинства других детей. Аутичный ребенок может привлекать внимание своими вокализациями, взмахами руками, даже тем, что носит наушники на перемене. Такое необычное поведение вызвано особенностями развития, мироощущением. Эту мысль я старалась донести до первоклассников - рассказала Елена Михеева.</p>\r\n<p>Занятия в ресурсном классе проходят в рамках проекта \"Скоро в школу\", при поддержке Благотворительный фонд «Вклад в будущее».</p>', '/images/RK/20.09.21/17.jpg', '2020-09-25', '/images/RK/20.09.21/1.jpg;\r\n/images/RK/20.09.21/2.jpg;\r\n/images/RK/20.09.21/3.jpg;\r\n/images/RK/20.09.21/4.jpg;\r\n/images/RK/20.09.21/5.jpg;\r\n/images/RK/20.09.21/6.jpg;\r\n/images/RK/20.09.21/7.jpg;\r\n/images/RK/20.09.21/8.jpg', '', '', 'Новости', 'news.php', 0, '', '15'),
(291, '20', 'Самостоятельный ребёнок', 'Каждый родитель хотел бы, чтобы его ребенок был самостоятельным – сам одевался, кушал ложкой, умывался, чистил зубы и тому подобные важные мелочи, из которых строится повседневный быт. Как обучить ребенка с аутизмом навыкам самообслуживания.', '<p>В октябре в «РАСсвете» стартовал новый проект, заручившийся поддержкой Фонда президентских грантов «Скорая помощь для детей с аутизмом и их семей». В его рамках мы открываем информационный марафон, в ходе которого регулярно будем публиковать статьи о развитии специальных навыков у детей с расстройством аутистического спектра (РАС). И первая тема нашего марафона - «навыки самообслуживания».</p>\r\n<p>Каждый родитель хотел бы, чтобы его ребенок был самостоятельным – сам раздевался-одевался, кушал ложкой, держал кружку, убирал за собой посуду со стола, умывался, расчесывался, чистил зубы, ходил в туалет без напоминания и тому подобные важные мелочи, из которых строится повседневный быт. И если нейротипичные дети учатся базовым вещам, наблюдая за взрослыми и подражая им, то у детей с РАС зачастую отсутствует мотивация и имитация, а проблемы с мелкой и крупной моторикой и боязнь неудачи только осложняют процесс обучения.</p>\r\n<p>Специалисты ABA-терапии (метод коррекции поведения, основывается на принципах поведенческого анализа) рекомендуют перед началом обучения ребенка какому-то навыку составить четкий план действий, подготовить все нужные предметы, смоделировать ситуацию. Например, чтобы научить ребенка мыть руки, нужно расписать всю цепочку действий: зайти в ванную, закатать рукава, открыть кран, намочить руки, взять мыло, намылить руки, положить мыло, потереть руки, смыть мыло с рук под водой, закрыть кран, взять полотенце, вытереть полотенцем руки, откатать рукава. Эти подробности нужны, чтобы скорректировать поведение ребенка, и научить его выполнять все самостоятельно и в правильной  последовательности. Стоит понаблюдать, какие из этих шагов ребенок может выполнить, а какие требуют подсказки и помощи. От уровня развития ребенка зависит то, какие подсказки ему понадобятся – поэтапные сюжетные картинки над раковиной или физическая помощь рука в руке.</p>\r\n<p>Не рекомендуется торопить ребенка и перегружать его лишней информацией, все подсказки должны быть четкими. Когда у ребенка сформируется самостоятельный навык, важно закрепить и обобщить его, то есть, пробовать тот же набор действий в разных местах. Если брать пример с мытьем рук, то можно практиковать этот навык в гостях, уборных кафе, детсадах и школах. Те, кто находятся рядом с ребенком и помогают ему, должны придерживаться одного плана: родители, бабушки с дедушками, воспитатели, тьюторы должны работать по одной схеме, чтобы навык закрепился.</p>\r\n<p>- Нужно подумать и о системе поощрений. Что станет для ребенка стимулом повторять снова и снова непонятные или даже неприятные ему действия? Социальная похвала - «Молодец, у тебя все получается!», «Ты правильно сделал», «Я тобой горжусь!» может не сработать сразу. Стоит попробовать  в качестве поощрения вкусные перекусы: фрукты, печенье, конфеты (необязательно скармливать сразу килограмм сластей за одну цепочку действий, достаточно небольшого кусочка). Или выдавать любимую игрушку, включать мультфильм или читать книгу, главное, чтобы доступ к ним появлялся лишь после отработки навыка.  Возможно, ребенка больше порадуют тактильные и сенсорные ощущения: щекотка, поглаживание по спине, кружение в объятиях, прыжки на батуте, качание на качелях, закутывание в плотное одеяло, включение определенной музыки или диско шара. Чтобы понять, чего больше всего хочет ребенок, нужно заранее протестировать его на мотивационные стимулы, - объясняет Екатерина Никулина, куратор «АПЦ РАСсвет».</p>\r\n<p>Возможны и такие ситуации, когда ребенок, несмотря на поощрение, может отказаться делать то, о чем вы его попросили, устроить истерику. Близким людям бывает сложно реагировать спокойно на бурное проявление эмоций ребенка. Но чтобы не подкреплять негативное поведение, нужно оставаться нейтральным на любые выпады и продолжать делать начатое до конца.</p>\r\n<p>- Еще одно немаловажное условие – положительный настрой, радоваться любому успеху ребенка. Если  возникают затруднения в выполнении какого-то действия в цепочке, нужно задуматься об их причине. Может быть, стоит разбить задачу еще на несколько маленьких этапов, или поменять вид подсказки и время ее подачи. Но самое главное – продолжать верить в ребенка, - утверждает Екатерина Никулина.</p>\r\n<p>Подробнее об отработке навыков самообслуживания вы можете почитать на следующих ресурсах:\r\n<p><a href=\"https://outfund.ru/tag/navyki-samoobsluzhivaniya/\" target=\"_blank\">Сайт Фонда \"Выход\"</a></p>\r\n<p><a href=\"https://rassvet-apc.ru/literature.php\" target=\"_blank\">Полезная литература</a></p>\r\n<p>\r\nФото Л. Лось и из личных архивов сотрудников \"АПЦ РАСсвет\"\r\n</p>\r\n', '/images/SP/1/1.jpg', '2020-10-09', '/images/SP/1/2.jpg;\r\n/images/SP/1/3.jpg;\r\n/images/SP/1/4.jpg;\r\n/images/SP/1/5.jpg;\r\n/images/SP/1/6.jpg;\r\n/images/SP/1/7.jpg;\r\n/images/SP/1/8.jpg', '', '', 'Новости', 'news.php', 0, '', '13'),
(292, '16', 'Новые знания', 'Екатерина Родыгина, председатель АНО «АПЦ РАСсвет», приняла участие в конференция «Прикладной анализ поведения: теория и практика». Михеева Елена, член правления, поведенческий аналитик АНО \"АПЦ РАСсвет\", обучилась на практическом тренинге PECS уровень 2.', '<p>Ежегодная международная конференция «Прикладной анализ поведения: теория и практика» от Ассоциации специалистов Анализа Поведения прошла с 9 по 11 октября в Москве в онлайн-формате. В ней приняла участие Екатерина Родыгина, председатель АНО «АПЦ РАСсвет». Ведущие зарубежные и российские специалисты, а также талантливые педагоги с авторскими разработками стали спикерами конференции. Более 20 докладчиков в прямом эфире делились опытом, находками, исследованиями и новыми достижениями в ава-терапии.</p>\r\n<p>- Программа была очень насыщенной, кроме докладов, спикеры отвечали на вопросы слушателей в чате - эта обратная связь очень важна на конференциях. Сложно выделить какие-то презентации, все оказалось полезным, интересным. Мне понравился доклад известного сертифицированного поведенческого аналитика Роберта Шрамма по руководящему контролю в сложных случаях. Хотя тема эта изучена вдоль и поперек, автор раскрыл новые нюансы, на которые стоит обратить внимание. Также помог упорядочить уже имеющиеся знания материал по приоритетам в поведенческой программе специалиста из России Наталии Щербаковой. Когда у ребенка выявляется много дефицитов, сложно написать в программе, каким навыкам стоит обучать в первую очередь, выбирать приоритеты сложно, но нужно. Доклады о подростках с аутизмом и о том, как работать со сном у детей с РАС – мне также были интересны, - поделилась впечатлениями от конференции Екатерина Родыгина.</p>\r\n<p>Кроме того, с 28 сентября по 6 октября Михеева Елена, член правления, поведенческий аналитик АНО \"АПЦ РАСсвет\", обучилась на практическом тренинге PECS уровень 2. Тренинг проводили доктор Sibylle Bajorat, клинический директор европейского подразделения компании Pyramid Education USA и Ольга Попова, поведенческий аналитик, психолог, директор центра “Моя Планета”. Несмотря на онлайн-формат, тренинг оказался насыщенным и продуктивным благодаря домашним заданиям и активной практике.  </p>\r\n<p>Мы не устаем подчеркивать важность постоянного профессионального роста и обмена знаниями в сфере анализа поведения и терапии для детей с особенностями развития.</p>\r\n', '/images/news/12.10.20/00.jpg', '2020-10-12', '/images/news/12.10.20/1.jpg;\r\n/images/news/12.10.20/2.jpg;\r\n/images/news/12.10.20/3.jpg;\r\n/images/news/12.10.20/4.jpg\r\n', '', '', 'Новости', 'news.php', 0, '', '0'),
(294, '4', 'Маленькие достижения для больших побед', 'Самая обсуждаемая тема в родительских чатах сегодня – придется ли детям вновь из-за карантина перейти на дистанционное обучение в школах. И пока чиновники в сотый раз заверяют, что никаких опасений быть не может, а СМИ нагнетают обстановку медицинскими сводками, педагоги «АПЦ РАСсвет» делятся опытом, как помогать ребенку с аутизмом развиваться в онлайн и офлайн-режимах.', '<p>Самая обсуждаемая тема в родительских чатах сегодня – придется ли детям вновь из-за карантина перейти на дистанционное обучение в школах. И пока чиновники в сотый раз заверяют, что никаких опасений быть не может, а СМИ нагнетают обстановку медицинскими сводками, педагоги «АПЦ РАСсвет» делятся опытом, как помогать ребенку с аутизмом развиваться в онлайн и офлайн-режимах. В рамках информационного марафона, мы продолжаем знакомить вас с работой центра и с успехами наших воспитанников. Сегодня расскажем про достижения Влада в период пандемии.</p>\r\n<p>- Владу 9 лет, он учится в школе 8 вида, общается с людьми посредством альтернативной системы коммуникации карточек PECS, мальчик не вокальный. Занятия АВА-терапии в центре он посещает 2 года. С начала самоизоляции, когда «РАСсвет» перешел на удаленную работу, Влад стал заниматься со своим инструктором в дистанционном режиме. Конечно, гладким этот переход не назовешь: возникли трудности с поведением слушателя, которые и до этого давались с трудом. Влад отказывался от сотрудничества с педагогом через монитор компьютера, не хотел выполнять даже простые инструкции, которые уже были отработаны ранее в центре. Потребовалась помощь мамы мальчика, и мы ее активно вовлекали в занятия, давали рекомендации, как нужно себя вести с сыном на уроках и в обычной жизни, чтобы закрепить материал и обобщить его, - рассказывает Елена Михеева, куратор Влада.</p>\r\n<p>Подтянуть дисциплину на занятиях и дать понять ребенку, какой объем работы ему предстоит сделать, помогло визуальное расписание. Перед началом урока Владу показывали планшетку с визуальным расписанием в виде карточек с изображением того, что ему придется делать. Особенно мальчику понравилось выполнять творческие задания: лепить, рисовать, раскрашивать и делать аппликации.</p>\r\n<p>- Сейчас Влад вновь приходит на занятия в центр, у него есть большие успехи в навыках самообслуживания: он аккуратно складывает одежду, учится застегивать пуговицы, режет овощи и фрукты, которые потом с удовольствием съедает. Все это делает самостоятельно. Мы продвинулись вперед в навыке визуального восприятия, Влад стал собирать 3Д модели по образцу из 3-4 частей, предложенному педагогом. В дальнейшем мальчик сможет, ориентируясь на картинку-схему, собрать модель из конструктора. Также у Влада улучшились навыки слушателя, он выполняет простые инструкции – это база, то, на чем строится наша бытовая деятельность: «возьми ложку», «принеси чашку», «вытри стол», «выкинь мусор», «надень куртку» и т.п. вещи. Мы поменяли стратегию обучения в поведении слушателя, стали использовать контекстную инструкцию. И этот метод дал хороший результат, - добавляет Елена Михеева.</p>\r\n<p>Влад изучает цифры, счет, готовится к обучению по методу глобального чтения (ребенок видит, как написано слово и запоминает его визуальный образ). Куратор мальчика считает, что это поможет в будущем научить Влада печатать на телефоне короткие смс, и в компьютере небольшие тексты. Такие знания в современном мире точно пригодятся. АВА-терапия как раз и предназначена для того, чтобы расширить функционал ребенка с аутизмом, сделать его жизнь легче.</p>\r\n', '/images/SP/3/3.jpg', '2020-10-15', '/images/SP/3/1.jpg;\r\n/images/SP/3/2.jpg;\r\n/images/SP/3/4.jpg', '', '', 'Новости', 'news.php', 0, '', '13'),
(295, '4', 'Навыки для самостоятельной жизни', 'В «АПЦ РАСсвет» в этом году условия и приоритеты обучения детей с аутизмом сместились именно на медицинскую, санитарно-гигиеническую тематику. Благодаря выигранному гранту Фонда Владимира Потанина сотрудники центра приобрели необходимую технику для проекта десенсибилизации. ', '<p>Какие знания пригодятся ребенку для успешной жизни в будущем? Кто-то отдает своих наследников на занятия по робототехнике и программированию, кто-то – на курсы иностранных языков, ментальной математики или в спортивные секции. Однако опыт 2020 года говорит о том, что самое главное, чему мы можем научить детей – беречь свое здоровье. И не важно, особый это ребенок, или нейротипичный.</p>\r\n<p>В «АПЦ РАСсвет» в этом году условия и приоритеты обучения детей с аутизмом сместились именно на медицинскую, санитарно-гигиеническую тематику. Благодаря выигранному гранту Фонда Владимира Потанина сотрудники центра приобрели необходимую технику для проекта десенсибилизации. Белая кушетка для осмотра в игровой форме пришлась многим ребятам по душе, они учатся постепенно привыкать сидеть на ней, видеть рядом человека в белом халате, открывать рот, чтобы в него заглянул «врач» и к другим подобным нехитрым манипуляциям. Все это поможет в дальнейшем снизить стрессовую нагрузку на ребенка в обычной поликлинике: осмотр врача, медицинский кабинет уже не будут казаться ему чем-то новым, непривычным и опасным.</p>\r\n<p>- Навык ношения маски неожиданно оказался жизненно важным. Не каждый ребенок согласится носить маску, а для детей с аутизмом сенсорные ощущения от постороннего предмета на лице вообще могут спровоцировать нежелательное поведение, - рассказывает Екатерина Никулина, куратор «АПЦ РАСсвет». – Для воспитанника нашего центра Игоря мы специально включили в программу обучения навык ношения медицинской маски. Пока мы его отрабатываем за столом, не выходя в естественную среду. Буквально спустя пару занятий Игорь начал надевать маску и находиться в ней, не проявляя нежелательного поведения в течении 20-30 сек, а на данный момент этот период увеличен до 2-х минут, и мы продолжаем работать над увеличением времени ношения маски.</p>\r\n<p>Игорю 7 лет, он посещает занятия в «РАСсвете» уже 2 года, и за это время научился многим действительно важным навыкам, тому, что пригодится мальчику в реальной жизни. Например, застегивать пуговицы. Конечно, сейчас есть альтернатива в одежде: вместо рубашек можно носить футболки, вместо кардиганов и пиджаков – толстовки или кофты на молнии. Но школьная форма остается классической, и в ней есть одежда с пуговицами. Так что для будущего первоклассника Игоря этот навык оказался нужным. В центре есть специальный тренажер – кофта с огромными пуговицами. Первый шаг – научить ребенка застегивать и расстёгивать ее оказался самым сложным. Когда мальчик стал справляться с задачей на отлично, инструкторы подыскали ему одежду с меньшими по размеру пуговицами. Сейчас Игорь достаточно быстро застегивает пуговицы на своей школьной рубашке, и радуется, когда учитель и тьюторы хвалят его за самостоятельность.</p>\r\n<p>- Также у Игоря давно уже был сформирован важный навык «мытьё рук» с кусковым мылом, но были сложности с использованием жидкого: ему было тяжело выдавливать мыло из бутылки, и чаще всего он просто пропускал это звено в цепочке. Но нам нужно, чтобы ребёнок, когда вырастет, как можно меньше нуждался в нашей помощи и был самостоятелен. Поэтому мы работали над этим навыком, используя визуальное расписание, обобщили его, и теперь Игорь может помыть руки любым видом мыла, в любом месте, - говорит Екатерина Никулина.</p>\r\n<p>В начале осени Игорь пошел в первый класс, естественно, что в школе ребенку понадобятся не только бытовые навыки, но и академические. За время самоизоляции Игорь улучшил свои знания в понимании речи, усовершенствовал графомоторные навыки: стал увереннее, и практически без напоминания правильно держать ручку, рисовать по точкам вертикальные и горизонтальные линии, на следующих этапах обучения появятся буквы. Также у первоклассника сформировали навык самозанятости, и теперь он может в течение 5 минут заниматься сам функциональной деятельностью - с удовольствием и без присмотра родителей.\r\n</p>\r\n<p>О том, как развивать самозанятость у детей с аутизмом читайте в наших следующих статьях в рамках информационного марафона при проддержке Фонда президентских грантов.</p>\r\n', '/images/SP/1.jpg', '2020-10-18', '', '/images/SP/1.jpg;9CpGNxWVo40', '', 'Новости', 'news.php', 0, '', '13'),
(296, '20', 'Особенность, как преимущество', 'Специалист АНО «АПЦ РАСсвет» Мария Максимова рассказала об особенностях визуального восприятия у детей с РАС, в рамках информационного марафона программы «Скорая помощь для детей с аутизмом и их семей» при поддержке Фонда президентских грантов.', '<p>Каждый день мы ощущаем жизнь через зрительные образы, запахи, прикосновения и звуки. Нас окружает море информации, которую мы учимся расшифровывать буквально с пеленок. С каждым годом наш информационный опыт растет, и то, что удивляет ребенка, например, смена сезонов, первый снег, для взрослого – привычная картина мира. А теперь представьте, что человек может сконцентрироваться только на одном канале восприятия, а когда подключаются другие, концентрация наоборот падает. Как нейротипичному школьнику помогает усваивать урок одновременно лекция педагога со зрительным контактом глаза в глаза, презентация на доске и запись правил в тетрадь, так ученику с расстройством аутистического спектра (РАС) такое обилие информации напротив скорее помешает понять тему занятия.\r\n</p>\r\n<p>Специалист АНО «АПЦ РАСсвет» Мария Максимова расскажет об особенностях визуального восприятия у детей с РАС, в рамках информационного марафона программы «Скорая помощь для детей с аутизмом и их семей» при поддержке Фонда президентских грантов.</p>\r\n<p>- Визуальное восприятие - это способность различать визуальные стимулы. Этот канал восприятия, как правило, является ведущим в распознавании информации у детей с аутизмом. Например, не вокальный воспитанник центра Ваня очень силен в обучении на основе визуального восприятия, он буквально с первых примеров запоминает новый материал. Именно визуальное восприятие влияет на выбор формы общения: когда неговорящий ребенок может успешно пользоваться альтернативной системой коммуникаций PECS, используя карточки, - рассказывает Мария Максимова.</p>\r\n<p>Дети с РАС могут с легкостью замечать, что книги на полке лежат не по порядку или что предметы на столе расположены по-другому. Способность сосредотачиваться на мелочах и замечать последовательности - особенность визуального восприятия у детей с аутизмом, которая в будущем может стать их преимуществом: сделает взрослых людей с РАС с высоким уровнем навыков способными к работе, ориентированной на детали. Поэтому развитие навыков визуального восприятия - основа для успешного обучения другим важным навыкам, таким, как поведение слушателя, такт (называние, описание предметов и событий) , интравербальное поведение (навык отвечать на вопросы, вести диалог). </p>\r\n<p>- Для развития навыков визуального восприятия применяются упражнения на сопоставление идентичных предметов и картинок, сопоставление похожих (неидентичных) предметов и картинок, сопоставление картинки и предмета. Ведь если ребенок не умеет сопоставлять одноименные визуальные стимулы (разного вида машинки, разного вида кошек), то мы не сможем обучить его различению стимулов. Потому что кошкой для него будет именно та кошка, которую он выучил на картинке. Впоследствии детей с аутизмом обучают сопоставлять предметы или картинки, имеющие одинаковые свойства (цвет, размер, форму и др.), функции или категории, - делится опытом Мария Максимова.- К навыкам визуального восприятия относятся также умение собирать картинки из нескольких частей, а также умение спонтанно соотносить любую часть своей работы с аналогичной частью работы сверстника. Например, сверстник построил башню из красных блоков, и ребёнок с аутизмом выбирает красные блоки для своей башни. Умение собирать различные модели из различных материалов, а также сортировать предметы по категориям, например, обувь, одежда, еда, животные и т.д. </p>\r\n<p>Метод обучения сопоставлению визуальных стимулов основан на постепенном увеличении стимулов для сравнения - от 3 до 8 и более, постепенное усложнение навыка (от одинаковых до похожих картинок, затем до связанных по ассоциации и т.д.), обобщении навыков в быту (сортировка столовых приборов, одежды, грязного и чистого белья). В дальнейшем этот навык помогает развивать самозанятость ребенка: собирать пазлы, детали конструктора по образцу без речевых инструкций, физической подсказки и постоянного присутствия взрослого.</p>\r\n<p>Работа над визуальным восприятием может быть использована для развития навыка глобального чтения, и для введения и использования визуального расписания – что также увеличивает самостоятельность удетей с РАС и снижает их тревожность, нежелательное поведение, а значит, улучшает качество жизни ребенка.\r\n</p>\r\n', '/images/SP/4/3.jpg', '2020-10-22', '/images/SP/4/1.jpg;\r\n/images/SP/4/4.jpg;\r\n/images/SP/4/5.jpg;\r\n/images/SP/4/3.jpg', '', '', 'Новости', 'news.php', 0, '', '13'),
(297, '4', 'Полезные занятия', 'Перед тем, как составлять программу обучения, АВА-специалисты проводят оценку актуальных навыков ребенка и консультируются с его родителями на тему того, как можно качественно улучшить жизнь всей семьи с помощью занятий. В рамках информационного марафона на примере четырёхлетнего воспитанника «АПЦ РАСсвет» Саввы расскажем о том, как эффективна система ава-терапии.', '<p>Есть три распространённых запроса от родителей детей с аутизмом, которые они предъявляют педагогам. Во-первых, научить ребенка говорить. Многие считают, что стоит их чаду заговорить, как все проблемы, связанные с расстройством аутистического спектра (РАС) закончатся. Во-вторых, убрать из поведения ребенка все его «странности», такие как стереотипные движения, истерики, вокализации. И, в-третьих, сделать ребенка максимально похожим на других нейротипичных детей: чтобы он обладал теми же знаниями и умениями, умел играть в коллективе и т.д. Разумеется, делается это из лучших побуждений и от недостатка информации. РАС – это то, что остается с человеком на всю жизнь, поэтому то, что действительно важно – научить ребенка функционировать в реальном мире, развивать свои возможности и сглаживать «острые углы». Можно научить малыша считать в уме, читать книжки и рассказывать наизусть стихи, но если при этом он не будет уметь самостоятельно кушать, ходить в туалет, просить о помощи, и выполнять другие базовые действия, грош цена его образованности.</p>\r\n<p>Цели обучения должны быть индивидуальными и зависеть от возраста ребенка, его физической формы, ментальных особенностей, а также практической пользы от приобретения того или иного навыка. Перед тем, как составлять программу обучения, специалисты ава-терапии проводят оценку актуальных навыков ребенка и консультируются с его родителями на тему того, как можно качественно улучшить жизнь всей семьи с помощью занятий. В рамках информационного марафона на примере четырёхлетнего воспитанника «АПЦ РАСсвет» Саввы расскажем о том, как эффективна система ава-терапии.</p>\r\n<p>- Этим летом Савва прошел курс интенсива в центре: занятия по ава-терапии с ним проходили ежедневно на протяжении месяца по 4 часа в день. Одним из запросов родителей ребенка было – самостоятельное надевание штанов без нежелательного поведения. Мальчик нервничал из-за того, что у него не получалось одеваться, и каждый выход из дома сопровождался истерикой. Во время интенсивных занятий был сформирован навык надевания и снимания штанов спокойно, т.е., не проявляя нежелательного поведения (крика, сползания со стула, нытья). Савва достиг больших успехов в этом навыке. Иногда ему ещё требуется небольшая помощь в натягивании брючины с резинкой на ногу, но в 80% случаев он справляется с поставленной целью самостоятельно, - комментирует Екатерина Никулина, куратор «АНЦ РАСсвет».</p>\r\n<p>Для отработки и обобщения навыка родителям рекомендовали дома во время переодевания использовать стул по росту ребёнка, надевать штаны без резинки на концах брючин и как можно чаще тренировать данный навык. Например, во время сбора на прогулку и после нее, после сна снимать пижамные штаны и т.д.</p>\r\n<p>- Была начата работа над навыком мытьё рук. Во время сбора данных выяснилось, что Савва не выполняет ни одного шага самостоятельно. Мы применили метод обучения формирование «цепочки поведения»: выстроили алгоритм действий и в одинаковой последовательности выполняли его. Шаг за шагом мальчик научился мыть руки, и кусковым мылом, и жидким. Для увеличения самостоятельности в навыках самообслуживания, таких как – мытьё рук, самостоятельное одевание, посещение туалета и т.д., мы рекомендовали убрать вербальные подсказки, заменив их физическими в нужных моментах, и постепенно убирая их с помощью временной задержки или снижения интенсивности (частичная физическая подсказка). Если Савва не может выполнить какой-то шаг самостоятельно, нельзя делать его вместо него, нужно сделать его рука в руку с помощью полной физической подсказки, - говорит Екатерина Никулина.</p>\r\n<p>Когда у Саввы возникали затруднения в чем-то, например, падала из рук ложка, терялась любимая игрушка, мальчик кричал. Такая реакция в обществе неприемлема. Поэтому на занятиях его начали учить обращаться за помощью. </p>\r\n<p>- Что бы сформировать запрос о помощи дается инструкция, которую ребёнок не может выполнить без посторонней помощи или задание, в котором он обычно испытывает затруднения. В момент, когда Савва затрудняется в выполнении задания или допускает ошибку, даётся подсказка «помоги». Как только Савваповторяет «помоги»– ему тут же оказывается помощь в выполнении задания, и подсказка постепенно убирается с помощью временной задержки, - рассказывает Екатерина Никулина.</p>\r\n<p>Сегодня Савва просит о помощи, обращаясь адресно: к маме, папе, педагогу. Как результат – снизилось нежелательное поведение. Мальчику еще многому предстоит научиться, и каждый новый навык приблизит его к самостоятельной жизни.</p>\r\n\r\n\r\n', '/images/SP/2.jpg', '2020-10-23', '', '/images/SP/2.jpg;V2pLmYn9KHs', '', 'Новости', 'news.php', 0, '', '13');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(300, '20', 'Развитие самозанятости у ребенка с аутизмом', 'Самозанятость – это не про форму уплаты налогов, а про то, что будет делать ребенок, когда с ним рядом не будет взрослых: воспитателей, родителей, педагогов. Чтобы он не зацикливался на стереотипных движениях и созерцании улицы в окне, нужно научить его заниматься чем-то развивающим и полезным.', '<p>Чем занят маленький ребенок в течение дня? У него, как и у взрослого 1000 дел: гулять, играть в игрушки, рисовать на бумаге и других доступных поверхностях, находить конфеты и всякие мелкие штуки, которые хочется проглотить или засунуть в нос, рассыпать по дому все, что рассыпается, примерять родительские вещи и обувь, бегать, петь, танцевать, устраивать представление… Всего не опишешь. Причем, все эти дела, так или иначе, развивают малышей, и родительское терпение тоже. Другой ответ будет, если ребенок нейроотличный. Дети с аутизмом, если с ними специально не заниматься, скорее всего, будут повторять одно и то же: открывать и закрывать двери, трясти руками, кружится, пересматривать любимую песню или заставку в мультфильме, вокализировать, крутить колеса у машинки, облизывать свои ладони и выполнять другие подобные аутостимулятивные действия. Аутостимуляция имеет навязчивый характер, она проявляется особенно ярко, когда ребенок волнуется или, напротив, получает удовольствие от этих действий или звуков.</p>\r\n<p>- Дети с аутизмом во время аутостимуляций получают сенсорное удовольствие, но не развивают свои социальные навыки. Такое поведение тормозит их развитие в целом в дальнейшем. С ребенком с расстройством аутистического спектра (РАС) необходимо регулярно заниматься, корректировать поведение, обучать полезным навыкам. Понятно, что не у всех родителей есть средства нанимать специалистов для развивающих занятий, и времени на подобные уроки после работы или домашних дел у них остается немного. Поэтому важно формировать у ребенка навык самозанятости,- рассказывает Елена Михеева, специалист АНО «АПЦ РАСсвет».</p>\r\n<p>Самозанятость – это не про форму уплаты налогов, а про то, что будет делать ребенок, когда с ним рядом не будет взрослых: воспитателей, родителей, педагогов. Чтобы он не зацикливался на стереотипных движениях и созерцании улицы в окне, нужно научить его заниматься чем-то развивающим и полезным.</p>\r\n<p>- Для примера возьмем воспитанника нашего центра Ваню: мальчик любит смотреть мультфильмы на планшете и компьютере, болтать руками. Если позволить, он будет заниматься этим весь день. Наша задача, расширить сферу его интересов, показать, что вокруг есть много всего любопытного. При этом педагог не должен давить на ребенка, занятия нужно подбирать индивидуально, исходя из возможностей конкретного воспитанника. Начинать лучше с небольших и нетрудных для ребенка заданий. 2 года назад Ваня складывал разрезанные картинки из 2-3-х частей. Сейчас он самостоятельно собирает пазлы из 35 деталей. К тем же цифрам он раньше не проявлял интерес, но надо было готовить его к школе, и в его программу обучения ввели легкие математические задания. Оказалось, что мальчик с удовольствием запоминает цифры и выполняет задания с ними. Цифры стали для него мотивационным стимулом, теперь с их помощью можно обучать Ваню чему-то новому, тому, к чему у него пока нет интереса, - объясняет Елена Михеева.</p>\r\n<p>Итак, с чего начинать работу над самозанятостью? С задания, которое ребенок гарантированно сможет сделать сам, чтобы у него было ощущение успеха во время выполнения занятия. Это может быть маленькая пирамидка, сортер, вкладыши с формами, животными или машинками, 2-х составные пазлы, шнуровка, раскладывание крупных бусин по цветам, раскраска, и т.п. Конечно, стоит выбирать тот тематический материал, к которому ребенок проявляет интерес. Например, если он интересуется древними рептилиями, можно дать ему раскрасить трафарет с формой динозавра, или приклеить на изображение динозавра шарики из пластилина, чтобы получилась аппликация. После успешного окончания задания, ребенок получает поощрение: что-то вкусное, любимую игрушку, игру (щекотку, догонялки, прыжки на батуте и т.д.) на выбор и похвалу от взрослого.</p>\r\n<p>Постепенно заданий становится больше, их дает уже не педагог, учебный материал ребенок забирает сам со стола: либо это отдельные коробки с приклеенной картинкой, обозначающей, что внутри, либо это папки, также с визуальным расписанием задания на обложке, либо задания разложены по порядку на полке или столе. В зависимости от возраста ребенка и уровня развития его навыков, визуальная поддержка занятия может быть в письменной форме, тогда визуальное расписание будет состоять из слов, или же из картинок.</p>\r\n<p>- Я рекомендую вместе с визуальным расписанием вводить карточку «всё»: когда задание выполнено, ребенок приносит такую карточку инструктору, и это символизирует окончание работы. Педагог должен постепенно отдаляться на расстояние во время выполнения самостоятельного задания, сначала отсаживаться от стола, потом отходить в другой угол комнаты, чтобы затем, уже в домашних условиях мама или папа могли дать ребенку задание и выйти в другую комнату по своим делам. Окончив работу, ребенок приносит родителю карточку «всё», взрослый, таким образом, может отследить время выполнения задания и будет знать, что сейчас нужно либо продолжить обучение, либо сменить вид деятельности, - говорит Елена Михеева.</p>\r\n<p>Максимальное количество контейнеров или коробок с заданиями на самозанятость – 5. Постепенно занятия меняются, усложняются и разбавляются не только мотивационными. Например, если ребенку нравится рисовать, но не нравится писать прописи, то ему предложат сделать и то и другое. А так как цепочка поведения уже сформирована, он выполнит всю работу. Главное, чтобы задания не были сверхсложными или непонятными.</p>\r\n<p>- С дошкольного и младшего школьного возраста мы начинаем развивать навыки самозанятости в бытовой сфере, так как у детей с аутизмом, так же как у их нейротипичных сверстников должны быть домашние обязанности: вынести мусор, накормить кошку, пылесосить дорожки, мыть пол и т.п. Нагрузка зависит от возраста и физических возможностей ребенка. Воспитанник центра Влад в свои 9 лет научился, благодаря занятиям ава-терапией аккуратно складывать свои вещи в шкаф. Это был запрос родителей, навык полезный для повседневной жизни. Начали работу мы с ним с того, что научили ребенка складывать трусы, затем – футболки, рубашки. У мамы заранее спросили, как выглядит его шкаф, чтобы понимать, какую топографию в цепочке мы должны сформировать, - рассказывает Елена Михеева.\r\n</p>\r\n<p>Развитие самозанятости в идеале перерастает в какую-то профессиональную деятельность. Ребенка учат выполнять самостоятельно определенную последовательность действий без подсказок и помощи со стороны. Конечно, лучше, чтобы какая-то визуальная поддержка осталась. Тот же список дел, который и мы сами составляем себе порой, для людей с РАС может помочь снять тревогу и уменьшить стресс.</p>\r\n<p>О развитии других необходимых навыков у детей с аутизмом читайте в рамках нашего информационного марафона при поддержке Фонда президентских грантов.</p>\r\n\r\n\r\n', '/images/SP/3.jpg', '2020-10-27', '', '/images/SP/3.jpg;ABOOzD-fIlk', '', 'Новости', 'news.php', 0, '', '13'),
(302, '4', 'Домашние уроки гигиены', 'Сегодня в рамках информационного марафона об успехах воспитанника нашего центра Влада расскажет его мама, а также учредитель и специалист АНО «АПЦ РАСсвет» Елена Шапилова. Чтобы самостоятельно заниматься с сыном, Елена прошла три дистанционных курса обучающей программы подготовки специалистов по Прикладному Анализу Поведения уровня ВСаВА.', '<p>Вы замечали, что дети с аутизмом по-разному себя ведут дома, в гостях, на учебе или в детском саду. Ребенок может стараться при чужих людях, выполнять все задания в школе или слушаться воспитателя, а затем дома устраивать одну за другой истерики от переутомления. Другой, напротив, может сосредоточиться и контактировать лишь в привычных условиях в кругу семьи, а в незнакомых местах его будет накрывать волной паники. В АВА-терапии есть правило: обобщать навыки ребенка, проигрывать одну и ту же ситуацию в разных местах, с разными людьми, чтобы закрепить полученные знания. Это значит, что одних занятий с инструктором в развивающем центре не достаточно, те, кто постоянно находятся рядом с ребенком: родители, бабушки с дедушками, няня, должны работать в команде с педагогом, использовать те же принципы воспитания и обучения. Поэтому на частных консультациях в АНО «АПЦ РАСсвет» и на семинарах для родителей и специалистов сотрудники центра всегда обращают внимание на то, что родственникам детей с аутизмом необходимо изучить начальную базу прикладного анализа поведения.</p>\r\n<p>Сегодня в рамках информационного марафона об успехах воспитанника нашего центра Влада расскажет его мама, а также учредитель и специалист АНО «АПЦ РАСсвет» Елена Шапилова. Чтобы самостоятельно заниматься с сыном, Елена прошла три дистанционных курса обучающей программы подготовки специалистов по Прикладному Анализу Поведения уровня ВСаВА.</p>\r\n<p>- Владу 8 лет, с помощью методов АВА он обучается не только речевым и академическим навыкам, но и навыкам гигиены и самообслуживания. Как и у большинства наших детей, у Влада самостоятельно (методом проб и ошибок) формировались только навыки в той деятельности, которая является мотивационной. Например, он рано научился самостоятельно одеваться и обуваться, т.к. очень любил игру \"вытащить всё из шкафа и примерить на себя\". В более позднем возрасте самостоятельно научился застегивать пуговицы и гладить свои вещи, т.к. эта деятельность также была мотивационной.\r\nНо со многими другими навыками самообслуживания и гигиены дело обстояло гораздо сложнее. Навык сморкаться, мыть руки, чистить зубы и многие другие дались не просто. Некоторые проблемы есть до сих пор, но мы над этим работаем в центре и дома, - рассказывает Елена.</p>\r\n<p>Во время весенней самоизоляции дистанционные индивидуальные и групповые занятия со специалистами центра позволили поддерживать и обобщать уже освоенные на очных занятиях вербальные и академические навыки Влада, благодаря чему они не были утрачены. Также дома родные мальчика продолжали работать над формированием и улучшением его бытовых и гигиенических навыков. Например, отрабатывали навык мытья рук. Ранее цепочка поведения \"мыть руки\" уже была сформирована у Влада, но для \"современного\" смесителя, а при использовании смесителя с \"барашками\", Влад постоянно пропускал звено \"включить горячую воду\". С этой проблемой помогли справиться визуальные подсказки с изображением всех звеньев цепочки, которые прикрепили над раковиной. Визуальные подсказки висят над каждой раковиной и в центре, это помогло после выхода из режима самоизоляции успешно обобщать навык и там. </p>\r\n<p>- В настоящее время мы работаем над усовершенствованием навыка чистить зубы. Все звенья в цепочке Влад выполняет самостоятельно, но процесс этот длится недолго. Поэтому мы стараемся увеличить время чистки зубов и улучшить качество исполнения, используя метод формирования реакции, - делится своей историей Елена.</p>\r\n<p>- Владу, как и другим детям с аутизмом, сложно даются навыки формирования спокойного отношения к различным медицинским манипуляциям и гигиеническим процедурам. Например, мы долго работали над его терпимостью к стрижке. Сначала мы научили Влада спокойно относиться к стрижке ножницами. Следующим шагом была стрижка машинкой. У Влада есть страх - он боится \"жужжащих\" звуков, будь то шмель, газонокосилка, фен или машинка для стрижки. И одним из наших последних достижений стала возможность стричь волосы машинкой и сушить феном. Пока еще не очень продолжительное время, но Влад позволяет это делать без криков и слёз. Сейчас мыработаем над увеличением продолжительности, постепенно наращивая время этих процедур. А благодаря недавно приобретенным для проекта десенсибилизации медицинским инструментам и оборудованию, надеемся с помощью наших специалистов также успешно справиться с боязнью стоматологических манипуляций, - добавляет Елена Шапилова.</p>\r\n\r\n\r\n\r\n', '/images/SP/pic/8.jpg', '2020-10-29', '', '/images/SP/3.jpg;1Z5geeqxGPg', '', 'Новости', 'news.php', 0, '', '0'),
(303, '3', 'Гигиена и Самообслуживание: Путь к Независимости для Детей с РАС', 'Этой осенью в «АПЦ РАСсвет» прошли обучающие мероприятия, которые стали важным шагом в подготовке детей с аутизмом к самостоятельной жизни, освещая ключевые аспекты обучения навыкам самообслуживания и гигиеническим привычкам.', '<p>Этой осенью в «АПЦ РАСсвет» прошли обучающие мероприятия, которые стали важным шагом в подготовке детей с аутизмом к самостоятельной жизни, освещая ключевые аспекты обучения навыкам самообслуживания и гигиеническим привычкам.</p>\r\n<p>В субботу, 26 сентября, на YouTube-канале Рассвета врач по гигиеническому воспитанию Олег Кривобоков провел вебинар, посвященный методам обучения детей с РАС бытовым навыкам и соблюдению санитарно-гигиенических правил. Более полусотни слушателей узнали о важности гигиены и способах привить любовь к ней своим детям. В течение часа спикер осветил ключевые темы: какие навыки самообслуживания следует развивать в первую очередь, при каких условиях лучше формировать новые привычки и как использовать визуальное расписание в обучении.</p>\r\n<p>Вебинар включал не только теоретические аспекты, но и практические советы с видео-примерами. Олег Кривобоков поэтапно объяснил, как научить ребенка сморкаться и раздеваться.</p>\r\n<p>Вторая часть мероприятия была посвящена ответам на вопросы слушателей. Как поступать, если ребенок боится мыть руки или ходить в туалет? С чего начинать приучение к гигиене? Как расширить пищевой рацион? Как научить ребенка следить за своим внешним видом? На эти и другие вопросы отвечали Олег Кривобоков и председатель «РАСсвета» Екатерина Родыгина.</p>\r\n<p>Как подготовить ребенка с аутизмом к самостоятельной жизни? Чему научить в первую очередь, и какую методику использовать?\r\nВопросы, которые постоянно задают себе родители и педагоги детей с расстройством аутистического спектра, обсудили на вебинаре «Обучение навыкам самообслуживания детей с РАС и другими особенностями развития» 31 октября.</p>\r\n<p>Онлайн-семинар вновь проходил на YouTube канале АНО «АПЦ РАСсвет» и транслировался на странице центра в Instagram. Более 100 слушателей в прямом эфире изучали пошаговые инструкции от спикера АВА-инструктора Юлии Якушевой и председателя «РАСсвета» Екатерины Родыгиной. В ходе полуторачасового вебинара были рассмотрены базовые навыки, навыки домашней и общественной жизни, сбор данных и тестирование, метод обучения прямыми цепочками и десенсибилизация. Спикеры разобрали кейс-ситуации по гигиене и медицинским манипуляциям, основанные на реальных занятиях в центре.</p>\r\n<p>Для инструкторов центра и тьюторов ресурсного класса в октябре прошел тренинг, посвященный вопросам самообслуживания у детей с аутизмом. Утром специалисты собрались за своими компьютерами, чтобы прослушать лекцию на эту важную тему, а затем продолжили обучение в стенах центра, где состоялся практический тренинг.</p>\r\n<p>На мероприятии опытные специалисты и новички в области прикладного анализа поведения обсудили, как наиболее эффективно и быстро научить детей самостоятельности. На первый взгляд, может показаться, что достаточно просто показать ребенку, как мыть руки, или объяснить на словах, как полоскать рот и чистить зубы. Однако для большинства детей с аутизмом такие гигиенические навыки могут представлять собой значительные трудности.</p>\r\n<p>Поэтому важно заранее продумывать все шаги обучения и готовиться к процессу. Некоторым детям могут помочь физические подсказки, другим — визуальные расписания. Каждый ребенок уникален, и к нему необходим индивидуальный подход для успешного освоения навыков самообслуживания.</p>\r\n<p>Мероприятия проводились при поддержке Благотворительного фонда Владимира Потанина и Фонда президентских грантов в рамках проекта \"Скорая помощь для детей с аутизмом и их семей\".</p>\r\n', '/images/seminar/31.10.20/4.jpg', '2020-11-02', '/images/seminar/31.10.20/1.jpg;\r\n/images/seminar/31.10.20/2.jpg;\r\n/images/seminar/31.10.20/3.jpg;\r\n/images/seminar/31.10.20/5.jpg', '', '', '', '', 0, '', '13'),
(305, '20', 'Развитие моторики у детей с аутизмом', 'У детей с аутизмом есть свои особенности в освоении моторных стереотипных действий, иначе говоря, им бывает сложно повторить движения за взрослым или сверстником, будь то черчение карандашом по точкам или игра в мяч. Занятия с применением методов АВА-терапии включают множество упражнений на развитие мелкой и крупной моторики. Сегодня сотрудники АНО «АПЦ РАСсвет» поделятся опытом в этом направлении.', '<p>Развитие крупной и мелкой моторики полезно всем без исключения детям, и нейротипичным, и нейроотличным. Активное движение способствует развитию речи, координации и позволяет снизить эмоциональное напряжение. Взрослому человеку порой достаточно отлежаться на диване с книжкой или перед монитором в выходные дни, чтобы снять стресс рабочих будней. А ребенку, напротив, нужен активный отдых: прыжки, прогулки, лазанье по всевозможным поверхностям (даже вертикальным), игры с песком, крупами или грязью во дворе. У детей с аутизмом есть свои особенности в освоении моторных стереотипных действий, иначе говоря, им бывает сложно повторить движения за взрослым или сверстником, будь то черчение карандашом по точкам или игра в мяч. Занятия с применением методов АВА-терапии включают множество упражнений на развитие мелкой и крупной моторики. Сегодня сотрудники АНО «АПЦ РАСсвет» поделятся опытом в этом направлении.\r\n</p>\r\n<p>- На уроках адаптивной физкультуры мы используем различные задания для моторного развития, и каждое из них подбираем с учётом возраста ребёнка и его индивидуального уровня способностей на данный момент. Например, с 3-х летним Вадимом стояла задача обучить его двигаться с переменой траекторией движения и ориентируясь на высоту препятствий. В итоге Вадим научился без физической помощи взбираться и слезать с препятствий, видеть предметы, которые стоят на его пути и обходить их, теперь он с уверенностью ловит равновесие на узкой доске, - рассказывает Виктория Кузнецова, - фитнес-инструктор «РАСсвета».</p>\r\n<p>В различных играх и упражнениях на развитие крупной моторики иметь хорошее чувство равновесия очень важно. Проверить, насколько оно сформировано поможет простой тест: нужно попросить ребенка закрыть глаза и посмотреть, сможет ли он неподвижно стоять с закрытыми глазами, не шатаясь и не падая. Задания на развитие равновесия также просты, для начала можно попробовать проводить ребенка по тонкой линии, чтобы он не наступал за ее границы. Более «продвинутый» уровень - балансировка на специальных качелях.</p>\r\n<p>- С 7-летним Игорем мы используем упражнения для развития моторной имитации движений тренера. Используем весовое оборудование, чтобы ребёнку было проще ориентироваться в задаче, опираясь на собственные ощущения. Сейчас Игорь с лёгкостью имитирует боковой наклон с гимнастической палкой, сгибание и подъем рук с гантелями, хотя несколько месяцев назад тренеру приходилось удерживать руки Игоря на палке, чтобы он сжимал её как следует, - продолжает Виктория. - Другому воспитаннику Богдану нравятся сложно-моторные задания, содержащие несколько модификаций движений в одной серии. Ему мы с удовольствием предлагаем выполнять задачки из, так называемого, нейрофитнеса - такие упражнения комплексно влияют на развитие моторных способностей, а также хорошо прорабатывают сферу внимания, наблюдательности, координации и гибкости мышления.</p>\r\n<p>Маршировка, игры в мяч, прыжки на батуте, езда на велосипеде, самокате, логоритмика, ползанье по туннелю, игры на имитацию движений животных, людей и техники («полетим, как самолет», «прыгаем, как зайчик», «крутим руль, как водитель»), шаги в коробку и из неё, произвольная полоса препятствий – вот что рекомендуют специалисты для работы с детьми с аутизмом. Все эти задания можно продумать и выполнить в домашних условиях или на обычной детской площадке. \r\n</p>\r\n<p>Трудности с мелкой моторикой у детей выражаются не только в том, что им бывает трудно держать ручку в руке и писать буквы, но и в бытовых навыках, таких как чистка зубов, надевание одежды, удержание ложки и вилки и других, важных для самостоятельной жизни умений. Поэтому для развития навыков самообслуживания воспитанников «РАССвета» обучают застегивать и расстегивать пуговицы, кнопки, липучки и замки на молнии на одежде и обуви, завязывать шнурки, надевать носки, наливать воду в кружку из графина и бутылки (предварительно открыв крышечку), развешивать белье с помощью прищепок, кушать столовыми приборами и нарезать продукты ножом. Конечно, учат не всему сразу, а постепенно, в зависимости от возраста и развития навыков ребенка. </p>\r\n<p>- Мы учим детей начальным навыкам кулинарии- чистить мандарины, бананы, и некоторые другие продукты: яйца, сосиски и т.п. Выбираем то, что нравится есть самому ребенку, чтобы продукт был для него мотивационным. Игорь, например, любит есть яйца. Научили его, что сначала нужно яйцом постучать по столу, потом отщипывать скорлупу по кусочкам. С 3-4-х лет уже можно учить ребенка мандарины чистить: взять его руку, пальчиком проделать дырку сверху в середине фрукта, и двумя пальцами захватить кожуру и тянуть ее вниз. Физическая подсказка – рука ребенка в руке взрослого сначала будет полной, потом частичной, и постепенно сойдет на нет. С бананами похожая ситуация, только верхушку надо отрезать ножом, т.к. отрывать ее даже взрослому бывает тяжело, а процесс чистки для ребенка изначально должен быть мотивационным и не сложным, - говорит Екатерина Никулина, куратор АНО «АПЦ РАСсвет».</p>\r\n<p>Открывать и закрывать замки на молниях сотрудники центра советуют учить ребенка с 4-х лет, причем не за столом на одежде-тренажере, а в обычных бытовых условиях, опять же с полной физической подсказкой во время одевания сразу на себе, а не со стороны. Лучше выбирать для этого куртку с большим и прочным замком, чтобы маленьким пальчикам было удобно держать бегунок иди «собачку». Как только ребенок научится тянуть бегунок вверх до конца, наставнику нужно будет лишь помогать вставлять застежку в замочек, поддержка уже не должна быть полной. Со временем ребенок освоит этот навык целиком, нужно только набраться терпения. </p>\r\n<p>- Завязыванию шнурков можно учить с 5-7 лет, в зависимости от физических возможностей ребенка, все-таки это сложное занятие. И здесь есть подводный камень: все люди по-разному это делают, нет единого алгоритма действий. Я прошу родителей ребенка показать, как они завязывают шнурки, исходя их этой модели, расписываю цепочку действий, которую мы будем с ребенком отрабатывать. Потому что этот навык чаще отрабатывается не с педагогом, а в кругу семьи. И еще один момент, дети с аутизмом легко привыкают выполнять какую-то инструкцию в одном и тоже месте. Но чтобы обобщить навык нужно научить ребенка выполнять его в разных условиях. Стулья и лавочки есть не во всех домах и учреждениях, поэтому я советую учить ребенка завязывать шнурки, сидя на корточках, - делится опытом Екатерина. </p>\r\n<p>Всевозможные творческие, академические задания и навыки самозанятости также входят в понятие мелкой моторики. О том, как важна самозанятость для детей с аутизмом мы писали ранее: чтобы ребенок не зацикливался на стереотипном поведении, нужно найти ему развивающее занятие по душе и по способностям. Лепка из пластилина, теста, глины, сбор пазлов, конструктора, выкладывание мозаики, шнуровка с бусами, раскраски, наклейки и другие задания помогут детям развить координацию движений рук и глаз, ритмичность и гибкость.</p>\r\n<p>- Воспитанница центра Тася освоила подходящий для ее возраста – девочке 11 лет, навык – складывать оригами. Из обычного альбомного листа она делает по образцу модели кошки, собаки, зайца. Это занятие включает в себе развитие моторики, визуального восприятия и навыка самозанятости. Можно подбирать задания, отталкиваясь от требований к норме: что-то, что должен уметь делать ребенок в свои годы. Конечно, делая скидку на физические возможности. Учить 15-летнего подростка складывать пирамидку не этично. Даже если он не умеет ее складывать, этот процесс ему будет не интересен и абсолютно не нужен - рассказывает Екатерина Никулина.\r\n</p>\r\n<p>О развитии других необходимых навыков у детей с аутизмом читайте в рамках нашего информационного марафона при поддержке Фонда президентских грантов.</p>\r\n<p></p>\r\n<p></p>\r\n\r\n\r\n\r\n', '/images/SP/5/6.jpg', '2020-11-06', '/images/SP/5/1.jpg;\r\n/images/SP/5/2.jpg;\r\n/images/SP/5/3.jpg;\r\n/images/SP/5/4.jpg;\r\n/images/SP/5/5.jpg\r\n', '', '', 'Новости', 'news.php', 0, '', '0'),
(307, '4', 'Успехи Влада', 'Научить ребенка с аутизмом спокойно сидеть за столом, держать кружку, глотать воду – это целое дело. Здесь на помощь родителям приходит АВА-терапия, её методика направлена на системный подход в обучении и правильный выбор поощрения. Сегодня в рамках информационного марафона о методах прикладного анализа поведения расскажем на примере занятий с 8-летним воспитанником адаптационно-педагогического центра «РАСсвет» Владом.', '<p> Первый год жизни ребенка для его семьи наполнен маленькими чудесами: первый раз улыбнулся, начал гулить, вот он пополз, встал, сделал самостоятельный шаг. Чадо растет, и родительские ожидания в его адрес тоже, согласитесь, никто не будет умиляться, глядя, как трехлетка просто сидит за столом и пьет из кружки воду. Но родители детей с особенностями развития знают, что даже самые простые самостоятельные действия их ребенка – это большой труд и счастье. Научить ребенка с аутизмом спокойно сидеть за столом, держать кружку, глотать воду – это целое дело. Здесь на помощь родителям приходит АВА-терапия, её методика направлена на системный подход в обучении и правильный выбор поощрения. Каждый навык раскладывается по частям, затем из них формируется единая поведенческая цепочка. Выполнив инструкцию, ребенок получает то, что он очень сильно желает. Эта схема применима ко всем навыкам: приучения к туалету, мытья рук, одеванию, потреблению пищи и т.д. Сегодня в рамках информационного марафона о методах прикладного анализа поведения расскажем на примере занятий с 8-летним воспитанником адаптационно-педагогического центра «РАСсвет» Владом.\r\n</p>\r\n<p>Влад посещает занятия АВА-терапии третий год, по запросу родителей сотрудники центра обратили внимание на неприемлемое поведение мальчика во время приема пищи: он не сидел подолгу на одном месте, вскакивал и убегал из-за стола, выплевывал еду, даже если хотел кушать, выплевывал питье. Это типичный пример самостимулятивного поведения – когда ребенок получает удовольствие от каких-то ощущений, и повторяет их снова и снова.</p>\r\n<p>Мы подключили жетонную систему поощрений во время кормления. Давали жетоны за самые простые действия: сел за стол – получил жетон, взял ложку сам – снова жетон. Еду Владу давали ту же, что он ест обычно дома: хлопья с молоком, сырники и т.п. Не заставляли есть, когда мальчик уже был сыт, но и не давали встать из-за стола, пока жетоны не собраны. Скажем, устанавливали сразу условие – съесть 3 ложки самостоятельно и чисто. Если видели, что Влад собирается выплюнуть еду, напиток, блокировали эти действия - отводили от него чашку, кружку, – рассказывает Вероника Артамонова, сотрудник АНО «АПЦ РАСсвет». – Постепенно от жетонов мы ушли, и теперь Влад ест без них. Кроме того, мальчик научился пользоваться бумажной салфеткой. Если замарается, вытирает себе лицо. Салфетку кладем сразу рядом с тарелкой. Сейчас работаем над тем, чтобы Влад выкидывал грязную салфетку в урну.\r\n</p>\r\n<p>Навык очищать салфеткой лицо оказался полезным и в других случаях. Влад вытирает бумажным платочком нос, когда у него выделяется носовая слизь после плача или чихания. А еще мальчик учится сморкаться. Пока он находится на начальном этапе освоения навыка.</p>\r\n<p>Учим дуть Влада на кусочек ваты ртом. В зависимости от реакции ребенка на различные раздражители, можно задувать свечки, дуть в свистульки. Но Влад не любит громкие звуки, поэтому мы выбрали безобидную вату. Следующий шаг – поднести к лицу ребенка поднос с ватой и на выдохе прикрывать ему рот ладошкой, чтобы он выдыхал носом. Когда этот этап будет успешно пройден, можно попеременно закрывать то одну, то другую ноздрю на выдохе. Затем нужно учить ребенка самостоятельно закрывать ноздри, и в конце уже выполнять все действия с платком, - описывает последовательность в обучении навыка Вероника Артамонова.</p>\r\n<p>У Влада есть проблемы с пониманием обращенной к нему речи, он быстро и часто переключается с одного на другое, и забывает, что нужно сделать, то есть удержать его внимание на занятии сложно. Визуальные подсказки, как показал опыт, тоже малоэффективны для мальчика. Поэтому, в обучении инструкторы в основном используют физические подсказки. Как, например, работа с навыком мытья рук.</p>\r\n<p> Влад выполняет всю цепочку, но иногда забывает какой-то этап. Как только видим, что есть заминка в процессе, даем ребенку физическую подсказку – направляем его руки куда надо. Также у мальчика есть нежелательное поведение при мытье рук: если он видит губку, мыло или другой предмет на раковине, хватает и швыряет его прочь. Поэтому мы заранее следим, чтобы ничего лишнего на раковине не было, а кусковое мыло заменили жидким, огромный флакон не так-то просто быстро схватить и выкинуть. Есть еще одна причина, по которой мы перешли на жидкое мыло. Влад любит иногда экспериментировать со вкусом – видит кусковое мыло и пытается его облизать. Может, его привлекает цвет, может, запах. В любом случае, это поведение проще предупредить, заблокировать, - говорит Вероника Артамонова.\r\n</p>\r\n<p> Влад уже научился самостоятельно расчесываться и сейчас осваивает другие навыки: застегивать молнию на одежде, надевать шапку по инструкции. Как бы трудно ни было ребенку, он с удовольствием идет на занятия. А это уже половина успеха.</p>\r\n<p>На занятиях в «РАСсвете» Влад участвует в проекте десенсибилизации, открытом благодаря поддержке Благотворительный фонд Владимира Потанина. Ребенка учат спокойно проходить осмотр и лечение у стоматолога. Из-за нежелательного поведения в кабинете врача, в прошлом Владу лечили зубы под медикаментозным сном. Сейчас ему снова требуется осмотр стоматолога.</p>\r\n<p>- Влад даже на кресле спокойно не мог усидеть. Поэтому в начале обучения мы усаживали его на кушетку, чтобы он привыкал. Затем, используя стоматологическое зеркало, мы постепенно приближали его ко рту ребенка. Пока навык до конца не освоен. На следующих этапах нужно ввести в манипуляции перчатки, белый халат и другие стоматологические инструменты, удлинить время осмотра. Но первые шаги уже сделаны, - рассказывает Екатерина Родыгина, руководитель АНО «АПЦ РАСсвет».</p>\r\n<p>Проект информационного марафона реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n\r\n\r\n', '/images/SP/6/8.jpg', '2020-11-11', '/images/SP/6/1.jpg;\r\n/images/SP/6/2.jpg;\r\n/images/SP/6/3.jpg;\r\n/images/SP/6/4.jpg;\r\n/images/SP/6/5.jpg;\r\n/images/SP/6/6.jpg;\r\n/images/SP/6/7.jpg;\r\n/images/SP/6/8.jpg\r\n', '', '', 'Новости', 'news.php', 0, '', '13'),
(309, '20', 'Стрижка и массаж для детей с аутизмом', 'Многие дети с расстройством аутистического спектра (РАС) испытывают сильный стресс во время стрижки. Причин может быть много, к сожалению, не все дети могут рассказать, что именно их не устраивает. А показать, продемонстрировав нежелательное поведение, вполне. То же касается и такой, казалось бы, приятной процедуры, как массаж. О том, как правильно и постепенно приучать ребенка с РАС к массажу и стрижке расскажут специалисты АНО «АПЦ РАСсвет».', '<p>Помните смешной советский мультфильм о заросшем шерстью льве, который пришел на стрижку к парикмахеру-журавлю. Птице пришлось хорошенько потрудиться, чтобы подстричь нетерпеливого, непоседливого клиента. Многие дети с расстройством аутистического спектра (РАС) испытывают сильный стресс во время стрижки, и ведут себя не лучше того льва из мультфильма. Кому-то не нравится шум и вибрация машинки или звук стригущих ножниц, кто-то чувствует боль от обычных прикосновений и расчесывания волос, другие могут не любить тактильные ощущения от накидки, полотенца, состриженных колючих волосков или брызг воды из пульверизатора. Причин может быть много, к сожалению, не все дети могут рассказать, что именно их не устраивает. А показать, продемонстрировав нежелательное поведение, вполне. То же касается и такой, казалось бы, приятной процедуры, как массаж. О том, как правильно и постепенно приучать ребенка с РАС к массажу и стрижке расскажут специалисты АНО «АПЦ РАСсвет».</p>\r\n<p>- На подготовительном этапе педагог обучает ребенка сохранять спокойствие, используя высокомотивационные стимулы, позволяющие создать для ребенка комфортную и расслабляющую атмосферу. В такой обстановке воспитанник испытывает положительные эмоции и параллельно обучается понимаю слова #спокойный. Обучение быть спокойным очень важный шаг, без него недопустимо продолжать дальнейшую работу, иначе мы рискуем усугубить страхи ребенка - рассказывает Екатерина Родыгина, руководитель АНО «АПЦ РАСсвет». – На втором этапе выявляются ситуации и объекты, вызывающие у воспитанника эмоциональный дискомфорт и страх. На основании наблюдения и беседы с родителями создается своеобразный алгоритм введения раздражителя в занятия. Например, если триггер – это приближение ножниц к волосам ребенка, то нужно проверить к какому виду этого стимула у него не проявляется эмоциональная реакция. Для этого нужно ответить на вопросы: когда ребенок видит ножницы перед собой, он плачет? Если да, то на каком расстоянии находятся ножницы, когда он начинает плакать? И если ребенок плачет, не важно, на каком расстоянии ножницы – плачет ли он, когда видит эти ножницы на картинке? И т. д. После детальной проработки информации мы переходим непосредственно к десенсибилизации.</p>\r\n<p>- Десенсибилизацию (поведенческая методика для снижения уровня тревожности и страхов) мы применяем в обучении детей, как в самом центре, так и в домашних условиях. Проект десенсибилизации стал возможен благодаря гранту Благотворительный фонд Владимира Потанина. Воспитанник «РАСсвета» Ваня раньше очень боялся ножниц, точнее, их звука и прикосновений во время стрижки. Даже подстричь ногти ему было большой проблемой: он весь сжимался, прятал руки. Родители стригли ребенка ночью, пока мальчик спал. На то, чтобы приучить его к стрижке ушло 2 года, но теперь Ваня хотя и испытывает стресс во время этой процедуры, может с ним справляться и сидеть спокойно, - говорит Елена Михеева, куратор центра «РАСсвет».</p>\r\n<p>Во время занятий на стол, за которым сидел Ваня, инструктор клал ножницы. За то, что мальчик спокойно на них реагировал, он получал жетон. Затем, со временем, их подкладывали все ближе к ребенку. Следующий этап – Ване давали инструкцию – дотронуться до ножниц, за выполнение он получал жетон. Потом нужно было дотрагиваться раскрытыми ножницами до кистей рук, предплечья, плеч и головы ребенка… Если в какой-то момент Ваня начинал нервничать, инструктор делал паузу. Например, когда к мальчику поднесли ножницы на уровень плеч, он показал тревогу. Поэтому инструктор на следующем занятии спустился до уровня локтевого сустава.</p>\r\n<p>- Нельзя давить на ребенка, но при этом нужен систематический подход при десенсибилизации. Если вы действительно хотите приучить ребенка спокойно стричься, посещать различных врачей, кабинет массажа, и т.п., раз или два в неделю проводить десенсибилизацию не достаточно. Маленькие шаги нужно делать каждый день. Поэтому важно заниматься с ребенком не только в центре, но и дома, - уточняет Елена Михеева.</p>\r\n<p>Когда Ваня спокойно реагировал на ножницы, находящиеся рядом с его головой, инструктор попробовала отстричь прядку волос. После прохождения этого этапа, мальчик стригся уже без нежелательного поведения. Он привык к ножницам и к новым ощущениям. Однако визуальная поддержка в виде сбора жетонов все еще осталась.\r\n</p>\r\n<p>- При обобщении нового навыка мы дополнили его стрижкой с помощью машинки. Так же, как и с ножницами – подкладывали во время занятий ее на стол, давали жетоны, дотрагивались выключенной машинкой до рук и головы Вани. После того, как убедились, что негативной реакции на прибор у мальчика нет, стали включать машинку и подносить ее к руке, ко лбу, к затылку и ушам ребенка. Виски, и область ото лба до макушки мальчик спокойно дает себе подстричь, а вот затылочная часть стрижется за жетоны,- рассказывает Елена Михеева.</p>\r\n<p>В зависимости от уровня развития навыков ребенка, перед стрижкой можно использовать дополнительную визуальную поддержку: показывать карточку с изображением ножниц, машинки, или фотографию парикмахерской и мастера, если стричься нужно не на дому, а в салоне. Также может подойти социальная история в небольших видео или картинках и рассказанная устно, где каждый этап расписывается подробно и сопровождается иллюстрацией. Например: «Сегодня мы пойдем в парикмахерскую, там мы снимем верхнюю одежду и повесим ее в \r\nшкаф, ты сядешь на высокое кресло, мастер Ольга наденет на тебя накидку…».</p>\r\n<p>Разумеется,предварительно родителю нужно провести «расследование» в салоне. Чтобы выяснить все подробности и обговорить с мастером детали. Возможно, стоит перед стрижкой несколько раз посетить парикмахерскую, чтобы ребенок привык к обстановке, увидел со стороны сам процесс, просто посидел в зоне ожидания, в кресле, с накидкой и без нее.\r\n</p>\r\n<p>- Можно совмещать неприятные стимулы (звук машинки, ощущение чужих прикосновений к голове) с приятными – давать перед началом стрижки ребенку в руки любимую игрушку, книжку, гаджет с мультфильмом. А после стрижки всегда необходимо давать поощрение. Например, наш воспитанник Игорь очень любит кальмаров. Но получает он их только после стрижки, - объясняет Елена Михеева.</p>\r\n<p>Массаж дети с РАС тоже могут сложно переносить из-за сенсорных ощущений. При работе с ребенком инструктор создает необходимые условия и делает базовый замер, выясняя, с какого этапа начинается нежелательное поведение. Если воспитанник спокойно ложится на кушетку, но не дает притронуться к себе, десенсибилизацию начинают проводить именно с этого момента: с легких касаний и поглаживаний. В процессе приучения к массажу также можно подключить социальные истории и визуальное расписание для поддержки, либо показывать примеры других людей.\r\n</p>\r\n<p>- Чтобы научить ребёнка спокойно реагировать на массаж, я укладываю его на мат, на живот, 5-10 минут делаю легкие поглаживания, чтобы он привык лежать в такой позе. Ребенок получает социальное поощрение в виде похвалы – «Молодец, хорошо лежишь, спокойно». Привыкание к процессу происходит за 2-3 дня. Постепенно время увеличивается до 30-40 минут. Во время занятий с детьми я подбираю приятные им стимулы. Вадим смотрит мультфильмы по телефону во время десенсибилизации, Владу включаю релаксирующую музыку, свет приглушаю, иногда включаю диско-шар, чтобы по комнате разноцветные огоньки кружились, многим ребятам это нравится, они получают удовольствие от этих сенсорных ощущений, - поделилась опытом Дарья Шульга, ассистент фитнес-инструктора АНО \"АПЦ РАСсвет\".</p>\r\n<p>Проект информационного марафона реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n\r\n\r\n', '/images/SP/5.jpg', '2020-11-15', '', '', '', 'Новости', 'news.php', 0, '', '13'),
(311, '12', 'Выше, быстрее, сильнее', 'При помощи введения поведенческих технологий в занятия адаптивной физкультурой 15 детей на протяжении 8 месяцев дважды в неделю укрепляли свое здоровье, учились чувствовать свое тело и выполнять инструкции тренера. И, конечно, развивались развлекаясь. ', '<p>В марте этого года в АНО «АПЦ РАСсвет» стартовал спортивный проект «Движение». При помощи введения поведенческих технологий в занятия адаптивной физкультурой 15 детей на протяжении 8 месяцев дважды в неделю укрепляли свое здоровье, учились чувствовать свое тело и выполнять инструкции тренера. И, конечно, развивались развлекаясь. </p>\r\n<p>Каждый ребенок прошел оценку физических навыков и получил индивидуальную программу работы на АФК. Так, Влад Г. делает значительные успехи в заданиях с использованием спортивного оборудования. Мальчику нравятся упражнения на растяжку и гибкость. За весь период работы проекта удалось обучить Влада правильно использовать тренажёры, свободные веса, выполнять как короткие инструкции, так и длительные серии заданий. У Игоря есть трудности с правильным распределением мышечного тонуса в верхних и нижних конечностях. Его программа включает упражнения на попеременное осознанное напряжение и расслабление рук и ног, а также растяжение мышц. Игорь научился выполнять трехкомпонентную имитацию, может самостоятельно воспроизвести упражнение по памяти. </p>\r\n<p>Самому младшему участнику проекта 3-хлетнему Вадиму вначале было сложно сосредоточиться на инструкторе и задании, он не откликался на имя. Были дни, когда он отказывался сотрудничать и плакал. Движения мальчика были раскоординированы, хаотичны. Сейчас Вадим с удовольствием идет на занятие, легко выполняет серии заданий, ранее представляющих для него большую сложность, ловит и бросает мяч, участвует в эстафетных играх с другими детьми, смотрит в глаза в ответ на свое имя, улыбается, пробует произносить слова за инструктором.</p>\r\n<p>Успехи детей связаны с тем, что фитнес-инструктор Виктория Кузнецова и ее ассистент Дарья Шульга нашли к каждому воспитаннику свой подход. </p>\r\n<p>Влад Ш. - ловкий и сильный мальчик, но у него есть некоторые сложности с удержанием внимания и сохранением учебного поведения. Поэтому мы используем на занятиях игровые задания, в которых Влада увлекает сам процесс. Благодаря этому увеличилось время сотрудничества с ребенком, он стал более внимательным и способен долгое время тренироваться без нежелательного поведения, - рассказала Виктория Кузнецова. – Другой наш воспитанник - Даниил - сообразительный и стремительный, но у него также имеются трудности с сосредоточением на инструкциях тренера, наблюдается быстрая утомляемость во время выполнения монотонных упражнений. Ключик к нему - это задания с проигрыванием сценариев из русских народных сказок. Таким образом, мы поддерживаем мотивационный настрой ребёнка во время всей тренировки и выполняем большие объёмы заданий.</p>\r\n<p>Конец года всегда богат событиями, хорошо когда радостными! Поделимся добрыми новостями! В \"АПЦ РАСсвет\" обновился спортивный инвентарь: массажёры для ног, спины и головы нашли свое место на полочках нового стеллажа из сосны. И уже пришлись по душе ребятам, посещающим занятия АФК!</p>\r\n<p>Проект «Движение» реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.\r\n</p>\r\n', '/images/AFK/fit/20.jpg', '2020-12-11', '/images/AFK/fit/21.jpg;\r\n/images/AFK/fit/22.jpg', '', '', '', '', 0, '', '0'),
(312, '4', 'Обучение Таси бытовым навыкам', 'В АНО «АПЦ РАСсвет» обучаются воспитанники обоих полов. АВА-терапия улучшает поведение ребенка вне зависимости от гендерных различий. Сегодня Екатерина Никулина - куратор «РАСсвета» и по совместительству мама 11-летней воспитанницы Таисии, расскажет об обучении девочки.  ', '<p>Еще пару лет назад считалось, что расстройство аутистического спектра (РАС), встречается у мальчиков в 4 раза чаще, чем у девочек. А значит, ребенок мужского пола более подвержен этому психическому нарушению. Однако позже ученые выяснили, что диагностирование мальчикам проводили в 3-6 раз больше, чем девочкам: многие дети просто не попали в сводки официальной статистики. В АНО «АПЦ РАСсвет» обучаются воспитанники обоих полов. АВА-терапия улучшает поведение ребенка вне зависимости от гендерных различий. Сегодня Екатерина Никулина - куратор «РАСсвета» и по совместительству мама 11-летней воспитанницы Таисии, расскажет об обучении девочки.  </p>\r\n<p>- С начала пандемии Тася занималась дистанционно, и на первых порах ей было очень трудно привыкнуть к такому формату. Поэтому долгое время занятия были поддерживающего характера, на онлайн-уроках повторяли то, что успели пройти в центре, закрепляли материал. В конце лета у Таси появились новые навыки самообслуживания: она научилась за самоизоляцию самостоятельно мыть голову, мыть посуду, пылесосить, поливать цветы, протирать пыль с телевизора, тумбочки и стола, раскладывать продукты после похода в магазин, пользоваться гигиенической помадой, дезодорантом, - рассказывает Екатерина Никулина.</p>\r\n<p>У девочки снижено понимание речи, поэтому основной вид подсказок для неё – визуальный. В ванной комнате дома у Таси на стене расположена визуальная поддержка: в картинках изображены части тела, которые нужно помыть. Хотя навык самостоятельно принимать ванну ребенок освоил уже в 9 лет. Также как и навык пользоваться дезодорантом. В это время у Таси начались подростковые изменения в гормональном фоне, увеличилось потоотделение.</p>\r\n<p>- Обучение навыку пользоваться шариковым дезодорантом заняло 3 дня. Сейчас Тася использует его на постоянной основе – после ванной, утром. Принцип обучения этому навыку – формирование цепочки поведения. Если использовать полную физическую подсказку, это будет выглядеть так: правой рукой ребенка нужно взять дезодорант, поднять его левую руку, правой рукой поднести  дезодорант к левой подмышечной впадине, сделать мазки вертикальными движениями снизу вверх. Затем поменять руки, - описывает обучение Екатерина Никулина. - Некоторые дети любят на вкус все пробовать, и если ребенок пристрастится лизать шариковый дезодорант, нужно менять его тип: попробовать использовать спрей или кремовый аналог.</p>\r\n<p>Дезодорант для Таси подобрали быстро, поскольку мама девочки не любит ярких ароматов, купили косметическое средство без запаха. Зато с подбором зубной пасты и с обучением навыка чистить зубы возникли трудности.</p>\r\n<p>- Тасю начали учить чистить зубы с 7 лет.  Ей не нравился этот процесс изначально, она сжимала челюсти, не давала доступа к зубам. Помогли нам жетоны и метод формирования реакций. Я давала Тасе четкие инструкции: открой рот, возьми щетку, поднеси ко рту, подвигай щеткой с пастой во рту, вытащи щетку. За каждое выполненное действие Тася получала жетон, собрав 10 жетонов – значимое для неё поощрение. Трудность возникала при выборе внешнего вида и вкуса зубной пасты. Иногда Тася лишь взглянув на тюбик, отказывалась им воспользоваться. Мы покупали много разных паст на пробу, то, что не подходило, отдавали родственникам.  Пасту брали специальную детскую, которую можно проглотить без вреда здоровью. Сейчас Тася отвергает только мятные пасты, зубная щетка у нее по-прежнему детская, небольшая по размеру и с мягкой щетиной, - говорит Екатерина Никулина.\r\n</p>\r\n<p>Как и большинство детей Тася чистит зубы очень быстро, лишь бы скорее закончить неприятную процедуру. Поэтому девочке купили небольшие песочные часы на 2 минуты. Пока песок сыпется вниз, нужно продолжать чистку. Такая визуальная поддержка отличный вариант для всех детей, кто не умеет ориентироваться во времени по обычным часам. Также всегда нужно помнить о мотивации. Ради чего ребенок согласится на неприятную, но необходимую процедуру?</p>\r\n<p> - В более раннем возрасте у Таси были трудности с самостоятельным натягиванием колготок, она надевала их на ступню, но дальше икр дело не двигалось. В итоге Тася освоила навык всего за неделю за счет мотивации: мы говорили, что нужно собираться к бабушке в гости, на прогулку, на качелях или с горки кататься. Тася все это очень любит, поэтому она старалась собраться как можно скорее, - вспоминает Екатерина Никулина.</p>\r\n<p> – Похожая история у нас была с медицинской маской. Мы поехали в гости к бабушке на автобусе, надели маски. В транспорте Тася свою почти сразу сняла. Мы вышли на следующей остановке. Я сказала: «Надевай маску, иначе не поедем к бабушке, а пойдем домой». Тася в конце концов согласилась. Если бы  отказалась, мы бы действительно пошли домой, - добавляет Екатерина.\r\n</p>\r\n<p>В домашних условиях носить маску Тасю обучали с помощью жетонов: выдавали их не за надевание маски, а за то, что девочка сидела в ней продолжительное время. Сначала давали жетон за 2-3- секунды ношения, постепенно время увеличили до нескольких минут. Теперь, когда девочка выходит из дома, всегда берет с собой маску с полочки в коридоре, и не забывает надевать ее в помещении. Приобретенный с помощью десенсибилизации  навык стал полезной привычкой.</p>\r\n<p>Проект информационного марафона реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.\r\n</p>\r\n<p>Проект десенсибилизации «Без паники! Идем к врачу» реализуется при поддержке Благотворительный фонд Владимира Потанина. </p>\r\n\r\n\r\n', '/images/SP/pic/2.jpg', '2020-11-19', '', '/images/SP/6.jpg;kabOC9dfgiM', '', 'Новости', 'news.php', 0, '', '13');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(313, '4', 'Поиграем в докторов', 'Сегодня мы подготовили для вас небольшое видео с занятия в «АПЦ РАСсвет». Посмотрите, как Ваня и Влад перевоплощаются в докторов и пациентов. Глядя на них сложно поверить, что еще месяц назад Влад не мог просто посидеть в кресле на осмотре у врача, дать полечить себе зубы. Сейчас мальчик спокойно сидит на тренировочной кушетке в центре и стоически терпит стоматологическое зеркало во рту.', '<p>Как научить ребенка с аутизмом не бояться стоматолога? С помощью игры! Чтобы белый халат врача не внушал страх, нужно его примерить. Если от одного взгляда на медицинскую кушетку трясутся коленки, надо посидеть на ней с любимой игрушкой. Конечно, не все так легко устраивается, процесс десенсибилизации занимает продолжительное время, и требует усилий и внимания со стороны педагога. Но результат стоит того. </p>\r\n<p>Сегодня мы подготовили для вас небольшое видео с занятия в «АПЦ РАСсвет». Посмотрите, как Ваня и Влад перевоплощаются в докторов и пациентов. Глядя на них сложно поверить, что еще месяц назад Влад не мог просто посидеть в кресле на осмотре у врача, дать полечить себе зубы. Сейчас мальчик спокойно сидит на тренировочной кушетке в центре и стоически терпит стоматологическое зеркало во рту. Ваня тоже отлично справляется с обеими ролями, не сжимается от чужих прикосновений, идет на контакт. В будущем педагог собирается поработать над тем, чтобы Ваня шире открывал рот при осмотре. </p>\r\n<p>А вы бы пошли на осмотр к таким бравым докторам?</p>\r\n<p>Проект информационного марафона реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p>Проект десенсибилизации «Без паники! Идем к врачу» реализуется при поддержке Благотворительный фонд Владимира Потанина.</p>\r\n<section class=\"video-detail\"><iframe src=\"https://rutube.ru/play/embed/2cb51382196258db714caa595386e3a9/\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen class=\"social-video\"></iframe>\r\n</section>\r\n\r\n\r\n', '/images/SP/pic/1.jpg', '2020-11-20', '', '', '', 'Новости', 'news.php', 0, '', '0'),
(315, '20', 'Различие между карточками PECS и визуальным расписанием', 'У большинства детей с аутизмом визуальное восприятие хорошо развито, ориентируясь на него, педагоги и родители могут выстраивать общение с ребенком, помогать ему обучаться новому и закреплять полученные навыки визуальными подсказками. О различиях между PECS (альтернативной системой коммуникации с помощью карточек) и визуальным расписанием  расскажет специалист АНО «АПЦ РАСсвет» Елена Михеева в рамках информационного марафона проекта «Скорая помощь для детей с аутизмом и их семей».', '<p>Перед тем, как пойти в магазин, пишете ли вы список продуктов? Или, может быть, в ежедневнике составляете план дел на день, неделю, месяц? Кто-то называет это инструментом тайм-менеджмента. В прикладном анализе поведения есть другой термин – визуальная поддержка. Это понятие намного шире обычного списка, и включает в себя всевозможные дорожные указатели, таблички с названиями улиц, схемы по сборке мебели, календари, стикеры с иностранными словами, которые вы хотите выучить, и приклеиваете рядом с рабочим местом…Все то, что помогает нам наглядно ориентироваться в пространстве и времени. </p>\r\n<p>У большинства детей с аутизмом визуальное восприятие хорошо развито, ориентируясь на него, педагоги и родители могут выстраивать общение с ребенком, помогать ему обучаться новому и закреплять полученные навыки визуальными подсказками. О различиях между PECS (альтернативной системой коммуникации с помощью карточек) и визуальным расписанием  расскажет специалист АНО «АПЦ РАСсвет» Елена Михеева в рамках информационного марафона проекта «Скорая помощь для детей с аутизмом и их семей».</p>\r\n<p>- PECS – это английская аббревиатура, она переводится, как «коммуникационная система обмена изображениями». Это альтернативный способ общения, в котором вместо слов люди передают друг другу карточки с картинками. PECS вводят в обучение детей, у которых есть трудности с речью. Визуальное расписание также может иметь форму карточек, но его цель не создание коммуникации, а помощь в конкретном деле: выполнении задания, гигиенической процедуры, вроде мытья рук, чистки зубов и т.п., - объясняет Елена Михеева. – В центре мы вводим визуальное расписание в работу с каждым воспитанником. Это делает ребенка более самостоятельным, помогает ему перейти от одного вида деятельности к другому, независимо от того, есть ли с ним рядом кто-то или нет. </p>\r\n<p>Также визуальное расписание снижает уровень тревожности у детей с аутизмом. Обычный календарь, в котором картинками или надписями отмечены предстоящие мероприятия: занятия, посещение врача, бассейна, приход гостей или поездка в игровой центр, предупреждает волнение о неизвестности в будущем. Сверяясь периодически со своим расписанием (здесь важно, чтобы оно всегда было в свободном доступе – месте, где его легко можно увидеть), ребенок успокаивается. Тот же визит к стоматологу или поездка отца в командировку уже не станут неприятной неожиданностью для него. </p>\r\n<p>- Дети с высоким уровнем развития навыков в старшем возрасте могут составлять себе расписание на день сами, что тоже способствует их самостоятельности. Конечно, в любом распорядке возможны непредвиденные изменения: кто-то заболел, встреча перенеслась, закрылся на карантин бассейн. Если ребенок лишается чего-то приятного, он может сильно расстроиться, кричать, раскидывать вещи. Чтобы смягчить эмоциональный взрыв, научить толерантности, нужно всегда иметь альтернативу. Не можем пойти на занятия сегодня? Пойдем кататься на самокатах, или с горки на санках, или заменим занятия на другое приятное для ребенка времяпровождение,- говорит Елена. </p>\r\n<p>- У меня с собой всегда есть прицепленные на металлическое кольцо ламинированные карточки с различными вариантами событий. Если я с ребенком еду в гости, и по дороге мне приходит сообщение, что на почту пришла посылка и ее нужно забрать, я показываю карточку с изображением почты сыну, и объясняю, что сейчас мы заедем за посылкой, а потом все равно поедем в гости, как собирались. В этом случае сын не расстраивается и не пугается изменения нашего маршрута, - добавляет Елена. </p>\r\n<p>Для того чтобы научить ребенка не бояться изменений в расписании, Елена советует использовать карточку «сюрприз»: устанавливать карточку с изображением подарочной коробки с бантом в обычный распорядок дня. Когда наступает время сюрприза, ребенок получает какой-то приятный мотивационный стимул: любимую игру, музыку, мультфильм и т.п. Со временем изменения в расписании не будут ассоциироваться с чем-то пугающим.</p>\r\n<p>- Визуальное расписание помогает освоить новые навыки. Оно пошагово иллюстрируют, что нужно сделать ребенку, напоминает о каждом этапе. Хочу особо отметить, что изображения на карточках лучше делать обобщенными, а не конкретными. Но это зависит от уровня развития навыков ребенка. С карточками PECS тоже самое. Допустим, ребенок любит печенье, у него должна быть карточка с изображением любимого лакомства. Лучше всего, если это будет картинка с миской с разными видами печенья. Но если у ребенка есть трудности с обобщением, и он не понимает такое изображение, придется ввести в занятия фото с конкретным печеньем, которое он постоянно ест, - рассказывает Елена.</p>\r\n<p>Карточки PECS и карточки визуального расписания могут содержать одинаковые символы, например, картинка с бассейном может быть и в расписании, и в папке PECS для построения фразы: «я хочу в бассейн». Поэтому, чтобы не путать ребенка лучше делать карточки для расписания и для общения разных размеров. </p>\r\n<p>- Когда к нам в центр приходит новый воспитанник, мы тестируем его навыки, и проверяем, насколько развита у него имитация, визуальное восприятие. Если последнее развито выше, начинаем вводить карточки PECS в работу. Кстати, по статистике зарубежных исследований, 70% детей начинают говорить после введения в занятия карточек PECS. То есть изначально ребенок может не использовать речь совсем. На первых этапах во время обмена карточками только педагог проговаривает то, что изображено на картинке, затем постепенно он мотивирует ребенка произносить звуки, слоги, фразы (в зависимости от возможностей), отображающие карточки. В «РАСсвете» Игорь и Ника пользуются карточками PECS для расширения фразовой речи, - делится опытом Елена.</p>\r\n<p>Как уже было отмечено выше, карточки PECS отличаются от визуального расписания своей целью: главная задача альтернативной системы коммуникации – помочь ребенку спонтанно комментировать происходящее вокруг. Не только различать картинки и выдавать запросы: я хочу пить, в туалет, смотреть мультфильм и т.п., хотя и это, безусловно, важно. Не только отвечать на обращенные к нему вопросы, но свободно общаться и инициировать общение, делиться мнением, рассказывать о своих чувствах. Однако таких результатов достигают далеко не все дети. </p>\r\n<p>- Самый «продвинутый» пользователь PECS в «РАСсвете» - 8-летний Ваня. Мальчик находится на VI этапе освоения альтернативной системы коммуникации, составляет предложения, расширяет свой репертуар за счет карточек «я вижу», «я слышу», «я чувствую». Он, как и другие дети, начинал с бумажных карточек, а сейчас кроме того пользуется специальной программой на планшете. В ней заложено около 600 различных картинок. Использовать гаджеты удобно, когда запас карточек в альбоме становится большим, когда ребенок уже подрос – согласитесь, подросток будет органичнее смотреться в школе с электронным планшетом, а не бумажным. Однако в домашних условиях бумажный аналог вполне сгодится, - говорит Елена Михеева.</p>\r\n<p>Проект «Скорая помощь для детей с аутизмом и их семей» реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n\r\n\r\n\r\n', '/images/SP/7/1.jpg', '2020-11-25', '/images/SP/7/1.jpg;\r\n/images/SP/7/2.jpg;\r\n/images/SP/7/3.jpg;\r\n/images/SP/7/4.jpg\r\n', '', '', 'Новости', 'news.php', 0, '', '13'),
(317, '12,3', 'Обучающие мероприятия проекта \"Движение\"', 'Одной из задач нашего проекта \"Движение\" при поддержке Фонда Президентских Грантов является распространение опыта по физической реабилитации детей с аутизмом путем проведения семинаров, WorkShop и тиражирования разработанного в ходе проекта методического материала.', '<p>Одной из задач нашего проекта \"Движение\" при поддержке Фонда Президентских Грантов является распространение опыта по физической реабилитации детей с аутизмом путем проведения семинаров, WorkShop и тиражирования разработанного в ходе проекта методического материала.</p>\r\n<p>Первый вебинар в рамках проекта состоялся 16 мая на платформе Youtube канала АНО \"АПЦ РАСсвет\". Тема вебинара \"Использование поведенческих методов анализа и системы поощрений при построении программ физической реабилитации\". Более 100 человек приняли в нем участие. Благодарим всех участников, встреча получилась очень информативной и содержательной.  Спикером семинара выступила Кузнецова Виктория Владимировна, фитнес-инструктор \"АПЦ РАСсвет\". Виктория рассказала, почему необходимо адаптировать физические занятия для детей с РАС, а также дала практические рекомендации и примеры упражнений АФК.</p>\r\n<p>28 ноября на базе АНО «АПЦ РАСсвет» состоялся WorkShop «Организация занятий адаптированной физкультуры для детей с аутизмом с применением прикладного анализа поведения». Мастер-класс прошел в рамках проекта «Движение». </p>\r\n<p>Мероприятие планировалось проводить в очном формате, однако, из-за пандемии в центре собрались лишь 10 педагогов, еще 44 человека присутствовали на встрече онлайн. Но это не помешало участникам WorkShop активно задавать вопросы и делиться впечатлениями. Тому способствовало тонны нужной и интересной информации и открытый урок с 8-летним воспитанником «РАСсвета» Владом. Несмотря на сильное волнение, мальчик хорошо справился с заданиями, и вместе с тренером наглядно продемонстрировал особенности проведения физкультуры у ребенка с аутизмом. </p>\r\n<p>Мастер-класс проводили сотрудники «АПЦ РАСсвет»: фитнес-инструктор Виктория Кузнецова и ассистент фитнес-инструктора Дарья Шульга. Они рассказали о важной роли поощрения в работе с детьми с РАС, на примерах рассмотрели, что лучше использовать в виде награды за успешно выполненное задание. Также подробно разобрали, как коротко и правильно давать инструкцию ребенку, какую поддержку ему нужно оказать: жестовую, физическую, карточку с изображением задания. Видеоролики с уроков АФК помогли участникам WorkShop глубже погрузиться в тему. На возникающие у педагогов вопросы в онлайн-режиме отвечала Екатерина Родыгина – руководитель АНО \"АПЦ РАСсвет\". </p>\r\n<p>Всем участникам мероприятия организаторы приготовили подарки – новые иллюстрированные методические пособия о том, как проводить занятия по АФК с детьми с РАС. Тем, кто обучался на мастер-классе дистанционно, методички отправят по почте. Надеемся, что новые знания станут хорошей поддержкой в практике педагогов.</p>\r\n<p>Проект «Движение» реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n', '/images/seminar/28.11.20/0.jpg', '2020-11-29', '/images/seminar/28.11.20/1.jpg;\r\n/images/seminar/28.11.20/2.jpg;\r\n/images/seminar/28.11.20/3.jpg;\r\n/images/seminar/28.11.20/4.jpg', '', '', '', '', 0, '', '0'),
(318, '20', 'Медикаментозная помощь людям с РАС', '«Волшебной таблетки» от аутизма нет, однако лекарства способны помочь качественно улучшить жизнь человека с РАС, снизить тревожность, агрессию, повышенную импульсивность, нарушение сна и т.д. В рамках информационного марафона разберем основные направления медикаментозной коррекции при аутизме. В этом нам поможет Сергей Клинов, иркутский врач психиатр, психиатр-нарколог. ', '<p>Расстройство аутистического спектра ученые описали еще в 40-х годах прошлого века, с тех пор знания о нем значительно пополнились. Хотя конкретную причину развития аутизма пока до конца не выяснили, известно, что в его основе лежат генетические механизмы, которые приводят к нарушениям работы центральной нервной системы. «Волшебной таблетки» от аутизма нет, однако лекарства способны помочь качественно улучшить жизнь человека с РАС, снизить тревожность, агрессию, повышенную импульсивность, нарушение сна и т.д. В рамках информационного марафона разберем основные направления медикаментозной коррекции при аутизме. В этом нам поможет Сергей Клинов, иркутский врач психиатр, психиатр-нарколог. </p>\r\n<p>- Назначать препараты может только врач, даже если лекарственные средства продаются без рецепта. У всех медикаментов есть побочные действия, поэтому соизмерять риск и необходимость назначения должен опытный специалист. Назначаемые медикаментозные средства, как правило, применяются комплексно, а не монотерапией, - поясняет врач. - Так, ноотропные средства назначают для ускорения и стимуляции умственной деятельности. Их назначения показаны при задержке речевого развития, нарушениях памяти, внимания, сниженной способности к обучению. Некоторые из них, обладая седатирующим эффектом, подходят и при гиперактивности, присущей многим детям с РАС.</p>\r\n<p>Антидепрессанты назначают при апатии, внутреннем напряжении, нарушениях сна, снижении аппетита. Выбор медикаментов зависит от индивидуального состояния пациента. Антидепрессанты по характеру действия, различают, как стимулирующие, так и седативные, а также сбалансированного действия. Как правило, стимулирующие назначают в утренние часы, для активации психической деятельности, снижения двигательной заторможенности в дневное время, а седативные для снижения напряженности, тревожности, двигательного беспокойства, восстановления и нормализации сна в вечернее.</p>\r\n<p>Транквилизаторы используют для снятия психоэмоционального напряжения, раздражительности, тревоги и страха. Как правило, многие из них, обладают снотворным эффектом и вызывают в той или иной мере зависимость, поэтому их выписывают короткими курсами и с перерывами. </p>\r\n<p>- Антипсихотики или нейролептики, применяются при РАС в качестве корректоров поведения. Они помогают справиться с тревожными и аффективными состояниями, сопровождающимися двигательным возбуждением, беспокойством, склонностью к агрессивным и аутоагрессивным действиям. По сравнению с вышеописанными средствами, нейролептики имеют более выраженное седативное действие, снижают реакции на внешние раздражители, - рассказывает Сергей Клинов. </p>\r\n<p>Для большего эффекта, врачи рекомендуют сочетать медикаментозную помощь с различными методиками коррекции поведения, например, с ава-терапией. </p>\r\n<p>Проект информационного марафона реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n\r\n\r\n\r\n\r\n\r\n', '/images/SP/8.jpg', '2020-11-30', '', '', '', 'Новости', 'news.php', 0, '', '13'),
(320, '20', 'Обучение сморкаться и полоскать рот', 'С наступлением холодов сезон простуд, носовых платков и чая с медом вновь открылся. Рассмотрим актуальную для зимы тему: как научить ребенка с аутизмом полоскать рот и сморкаться. \r\nЕсли ребенок умеет дышать через рот, и у него хорошо развит навык имитации, обучение сморканию можно проводить через демонстрацию - взрослый показывает, ребенок повторяет. ', '<p>С наступлением холодов сезон простуд, носовых платков и чая с медом вновь открылся. Рассмотрим актуальную для зимы тему: как научить ребенка с аутизмом полоскать рот и сморкаться. </p>\r\n<p>Если ребенок умеет дышать через рот, и у него хорошо развит навык имитации, обучение сморканию можно проводить через демонстрацию - взрослый показывает, ребенок повторяет. Сначала нужно сделать глубокий вдох через рот, прикрыть губы ладонью и выдохнуть через нос. Повторить эти движения несколько раз. Вновь сделать глубокий вдох ртом, закрыть его, закрыть одну ноздрю пальцем и выдохнуть носом. Повторить движения, чередуя закрывание ноздрей. </p>\r\n<p>- Есть дети, не терпящие прикосновения чужих рук к лицу, дети, у которых имеются трудности с выдыханием через рот. В этих случаях необходим другой способ обучения сморканию. Возьмите поднос с бортиком или обычную крышку от обувной коробки, положите на нее небольшой кусочек ваты, и поиграйте: дуйте с ребенком по очереди на вату, наблюдайте, у кого она дальше переместится. Ребенок будет видеть причинно-следственную связь, и ему проще будет понять, что такое выдох, что от него требуется, - делится советом Екатерина Родыгина, директор АНО «АПЦ РАСсвет». – Затем, самое сложное – переход от выдыхания через рот до выдыхания через нос. Здесь поможет небольшой трюк: тот же поднос или крышку с ватой поднесите к лицу ребенка так, чтобы бортик прикрывал ему рот, и дайте инструкцию дуть на вату. </p>\r\n<p>На заключительном этапе в обоих вариантах обучения подключается платок, бумажная или хлопковая салфетка – в зависимости от тактильных предпочтений ребенка. Сделать глубокий вдох через рот, закрыть его и выдохнуть через нос, прикрывая нос платком. Повторять и закреплять навык нужно несколько раз в день, до его полного формирования. </p>\r\n<p>Во время обучения навыку полоскания рта надо проговаривать инструкцию: «Полощи рот». Перед раковиной налить в стакан воды, дать ребенку отпить из него, сделать физическую подсказку – легонько надавить своими подушечками пальцев рук на щечки ребенка, чтобы он непроизвольно открыл рот, и вода вылилась до того, как он ее проглотит. </p>\r\n<p>- Лучше проводить обучение не в контексте чистки зубов, а в игровой форме. Предлагать ребенку задачу: «давай играть, кто дальше выплюнет воду», подключать имитацию, - подсказывает Екатерина Родыгина.</p>\r\n<p>Помните, что навыки сморкания и полоскания рта нужно осваивать в здоровом состоянии, до того, как чадо начнет шмыгать носом и жаловаться на боль в горле.\r\n</p>\r\n<p>Проект информационного марафона реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n\r\n\r\n\r\n\r\n\r\n', '/images/SP/10.jpg', '2020-12-04', '', '/images/SP/10.jpg;G35NkQLN8Kg', '', 'Новости', 'news.php', 0, '', '13'),
(324, '18', '«Дом синего слона»: пространство возможностей', 'Каждый день в стенах мастерской «Дом синего слона» рождается нечто особенное. Здесь дети с аутизмом находят не только радость творчества, но и путь к самостоятельной жизни. Наш проект, победивший в конкурсе «Школа филантропии», стал местом, где мечты становятся реальностью.', '<p>Каждый день в стенах мастерской «Дом синего слона» рождается нечто особенное. Здесь дети с расстройствами аутистического спектра находят не только радость творчества, но и путь к самостоятельной жизни. Наш проект, победивший в конкурсе «Школа филантропии» Благотворительного фонда Владимира Потанина, стал местом, где мечты становятся реальностью, а маленькие шаги ведут к большим достижениям.</p>\r\n<p>Интерьерные подушки: тепло осени в каждом стежке</p>\r\n<p>Осень — время уютных вечеров и теплых объятий. Именно такое настроение мы хотим передать через наши интерьерные подушки. Мягкие, словно облака, с наполнением из лебяжьего пуха, они создают атмосферу домашнего комфорта. Чехлы из натуральных тканей с яркими принтами — результат труда маленьких мастеров, чьи руки вкладывают душу в каждое изделие. Эти подушки не просто украшение интерьера, они — символ любви и заботы, которые объединяют семьи и друзей.</p>\r\n<p>Глина: искусство чувств и эмоций</p>\r\n<p>Работа с глиной — это больше, чем просто творчество. Это возможность ощутить мир через прикосновения, почувствовать его пластичность и податливость. Наши ребята с увлечением лепили, тонировали и патинировали глиняные подвески. Для некоторых это стало настоящим открытием, как, например, для Влада, чьи ладони покрылись розовым акрилом, или Тимофея, который внимательно изучал золотые блики на своей руке. А Рома, как всегда, показал себя настоящим мастером, чья аккуратность и терпение поражают нас снова и снова.</p>\r\n<p>Барельефы: полет фантазии в космос</p>\r\n<p>Когда речь идет о барельефах, многие представляют гипсовые формы. Но наша педагог Светлана решила пойти дальше и предложила создать картины с космическим фоном из акриловых красок и гипсовых планет. Процесс был долгим, но интересным: дети с энтузиазмом сушили краску феном, шутили и делились впечатлениями. Эта работа стала не только творческим экспериментом, но и важным этапом в развитии навыков терпения и сотрудничества.\r\n</p>\r\n<p>Вязание: возвращение к истокам</p>\r\n<p>В наше время, когда всё можно купить, мы часто забываем, что вязаные вещи можно создать своими руками. В мастерской «Дом синего слона» мы решили возродить эту традицию. Несмотря на опасения, что монотонность процесса может не привлечь детей, первое занятие прошло на ура! Ребята с удовольствием осваивали основы работы на луме, создавая первые петли и узоры.</p>\r\n<p>Часы: время, застывшее в искусстве</p>\r\n<p>Одним из самых запоминающихся проектов стали настенные часы. Эта работа объединила в себе элементы творчества и инженерии. Дети учились работать с мелкими деталями, развивая мелкую моторику и концентрацию внимания. Созданные ими часы — не просто декоративный элемент, а символ времени, проведенного вместе, времени, которое остается в памяти навсегда.</p>\r\n<p>Каждая встреча в мастерской — это шаг вперед. Мы постепенно передаем контроль от родителей к тьюторам, помогая детям становиться более самостоятельными. Нам помогают волонтеры, такие как Яна и Анна, которые вносят свою энергию и энтузиазм в общее дело.</p>\r\n<p>«Дом синего слона» — это не просто мастерская, это дом, где каждый ребенок находит свое место, раскрывает таланты и обретает уверенность в себе. Мы гордимся нашими учениками и их достижениями, и надеемся, что их творчество будет вдохновлять многих на пути к саморазвитию и гармонии.</p>\r\n<p>Вырученные средства от продажи изделий идут на развитие мастерской, позволяя нам продолжать нашу миссию поддержки детей с РАС.</p>\r\n<p>Проект реализуется победителем конкурса \"Школа филантропии\" благотворительной программы \"Эффективная филантропия\" Благотворительного фонда Владимира Потанина.</p>', '/images/DSS/20.12.05/4.jpg', '2020-12-07', '/images/DSS/20.12.05/1.jpg;\r\n/images/DSS/20.12.05/2.jpg;\r\n/images/DSS/20.12.05/3.jpg;\r\n/images/DSS/20.12.05/5.jpg;\r\n/images/DSS/20.12.05/6.jpg;\r\n/images/DSS/20.12.05/7.jpg;\r\n/images/DSS/20.12.05/8.jpg;\r\n/images/DSS/20.12.05/9.jpg;\r\n/images/DSS/20.12.05/10.jpg;\r\n/images/DSS/20.12.05/11.jpg', '', '', '', '', 0, '', '0'),
(327, '17', 'Аутизм и работа над сдачей анализа крови', 'Учредитель АНО «АПЦ РАСсвет» Мария Зубарева сделала перевод интересной статьи про помощь ребенку с аутизмом во время сдачи анализа крови. Читаем, берем на заметку!<br>\r\nИсточник: www.autismsupportnetwork.com<br>\r\nАвтор: Shelly Green <br>\r\nПеревод: Мария Зубарева ', 'Учредитель АНО «АПЦ РАСсвет» Мария Зубарева сделала перевод интересной статьи про помощь ребенку с аутизмом во время сдачи анализа крови. Читаем, берем на заметку!<br>\r\nАвтор: Shelly Green <br>\r\nПеревод: Мария Зубарева \r\n<p>Мне позвонила одна из родителей с просьбой помочь её сыну. В то утро ему нужно было сдать кровь на анализ, а её муж, который раньше помогал удерживать сына, был в отъезде. Ребенок, 16-ти лет, с диагнозом синдром Аспергера и с двумя кохлеарными имплантами, был не способен выдержать процедуру сдачи крови без физического вмешательства.</p>\r\n<p>Я тут же задалась вопросом: \"Как я прохожу через процедуру сдачи крови?\", и поехала в местный магазин, чтобы купить маркеры и бумагу. Сразу же, у стойки, я нарисовала на бумаге визуальную опору, чтобы быть подготовленной к моменту приезда в больницу.</p>\r\n<p>Когда я приехала, то увидела своего ученика, ростом 6 футов (около 183 см), сжимающего плюшевую игрушку. Мы начали действовать вместе. Я показала ему визуализацию и объяснила различные ощущения, которые он, возможно, будет испытывать: нервозность, дискомфорт, боль и отсутствие боли. Используя визуальную шкалу времени, я подчеркнула, как мало времени будет потрачено на боль. Когда моего ученика вызвали на процедуру, мы разместили изображение прямо перед ним, как точку фокусировки. Он сразу же вытащил свои импланты, чтобы убрать лишние раздражители. Хорошо, что мы использовали визуальную опору, так как теперь он был полностью глухим!</p>\r\n<p>Я проводила пальцем по шкале времени по мере того, как шла процедура, и вскоре (6 минут и 35 секунд, если быть точной), он уже находился в зоне \"отсутствия боли\" и был готов выйти из здания.</p>\r\n<p>Самой лучшей частью этой ситуации было увидеть его гордость за свои достижения! Он смог пройти через эту процедуру и заменить негативные мысли о процедуре взятия крови на мысль \"Это было не так уж плохо\".</p>\r\n<p>В завершение этого случая я убедилась, насколько эти дети могут быть чудесными и как они могут стремиться добиться большего, когда им дают нужную для этого информацию!</p>\r\n<p>Шелли Грин работает в сфере специального образования последние 30 лет. Ей нравится помогать другим учителям и администраторам понимать особенности работы с детьми с расстройствами аутистического спектра.  Шелли получает огромное удовольствие, придумывая способы сделать абстрактные социальные концепции одновременно конкретными и наглядными. Используя эту стратегию, она добилась успеха в охвате и обучении этой особой группы населения.</p>\r\n\r\n<p>Источник: <a class=\"a-blue\" href=\"http://www.autismsupportnetwork.com/news/autism-and-working-through-getting-blood-work-32938822\" target=\"_blank\"> www.autismsupportnetwork.com</a></p> \r\n\r\n	', '/images/TRANSLATE/5.jpg', '2020-12-24', '', '', '', 'Новости', '/news.php', 0, '', '0'),
(328, '2', 'Новый год с Айболитом', 'Осталась неделя до Нового года… Пора звать Деда Мороза! Ох, из-за коронавируса всех волшебных дедов и их внучек запретили пускать на утренники. Неужели дети останутся без праздника? Только не в «РАСсвете»! ', '<p>Осталась неделя до Нового года… Пора звать Деда Мороза! Ох, из-за коронавируса всех волшебных дедов и их внучек запретили пускать на утренники. Неужели дети останутся без праздника? Только не в «РАСсвете»! </p>\r\n<p>Вместо елочных шаров и гирлянд – медицинские маски и градусники, а на смену Деду Морозу пришел Айболит. Нестандартный праздник прошел в 3 этапа для 3 малых групп воспитанников центра, чтобы соблюсти все санитарные правила. Безопасность и здоровье превыше всего!</p>\r\n<p>Делали гимнастику, собирали витамины, медицинские инструменты, дули на снежинки и помогали доктору осматривать друг друга, как настоящие врачи. Программа пришлась по душе и дошколятам и младшим школьникам. Заодно повторили как себя вести при посещении врачебного кабинета.</p>\r\n<p>А в конце праздника – полезное угощение и подарки!</p>\r\n<p>Проект реализовался при поддержке Фонда президентских грантов и Благотворительного фонда Владимира Потанина.</p>', '/images/SP/8/19.jpg', '2020-12-24', '/images/SP/8/1.jpg;\r\n/images/SP/8/2.jpg;\r\n/images/SP/8/3.jpg;\r\n/images/SP/8/4.jpg;\r\n/images/SP/8/5.jpg;\r\n/images/SP/8/6.jpg;\r\n/images/SP/8/7.jpg;\r\n/images/SP/8/8.jpg', '', '', 'Новости', 'news.php', 0, '', '13'),
(330, '22', 'Подарки от добрых друзей', 'Пришла пора дарить и получать подарки! Спасибо нашим друзьям и постоянным спонсорам: торговой компании ООО \"ТД Элит Трейд\" и ООО \"Иркутскэнергосбыт\" за создание новогоднего настроения и классные подарки! Детям они очень понравились!', '<p>Пришла пора дарить и получать подарки! Спасибо нашим друзьям и постоянным спонсорам: торговой компании ООО \"ТД Элит Трейд\" и ООО \"Иркутскэнергосбыт\" за создание новогоднего настроения и классные подарки! Детям они очень понравились!</p>', '/images/news/TD2020/1.jpg', '2020-12-28', '/images/news/TD2020/1.jpg;\r\n/images/news/TD2020/2.jpg;\r\n/images/news/TD2020/3.jpg;\r\n/images/news/TD2020/4.jpg;\r\n/images/news/TD2020/5.jpg;\r\n/images/news/TD2020/6.jpg;\r\n/images/news/TD2020/7.jpg;\r\n/images/news/TD2020/8.jpg;\r\n/images/news/TD2020/9.jpg;\r\n/images/news/TD2020/10.jpg;\r\n/images/news/TD2020/11.jpg;\r\n/images/news/TD2020/12.jpg;\r\n/images/news/TD2020/13.jpg;\r\n/images/news/TD2020/14.jpg;\r\n/images/news/TD2020/15.jpg;\r\n/images/news/TD2020/16.jpg;\r\n/images/news/TD2020/17.jpg', '', '', 'Новости', 'news.php', 0, '', '0'),
(331, '16', 'Круглый стол \"Проблемы расстройств аутистического спектра у детей\"', '24 декабря в Иркутске в дистанционном формате состоялся круглый стол \"Проблемы расстройств аутистического спектра у детей\". Организовали мероприятие кафедра педиатрии ИГМУ и аппарат Уполномоченного по правам ребенка в Иркутской области.\r\nСотрудники АНО «АПЦ РАСсвет» приняли участие во встрече.', '<p>24 декабря в Иркутске в дистанционном формате состоялся круглый стол \"Проблемы расстройств аутистического спектра у детей\".</p>\r\n<p>Организовали мероприятие кафедра педиатрии ИГМУ и аппарат Уполномоченного по правам ребенка в Иркутской области.\r\nСотрудники АНО «АПЦ РАСсвет» приняли участие во встрече, наравне с преподавателями Педагогического института ИГУ, руководителями специальных коррекционных школ Иркутска и Братска, Центра психолого-педагогической, медицинской и социальной помощи Иркутской области. Также на круглом столе отметились представители первого ресурсного класса области и родители детей с РАС.</p>\r\n<p>- Прошла одна из первых встреч в рамках этой тематики. Радует, что к обсуждению было привлечено столько сторон. Надеюсь, что на дальнейших мероприятиях – уже запланирована региональная научно-практическая конференция на начало года – будут присутствовать сотрудники Департамента образования и Министерства образования. Именно с ними нужно решать вопрос о создании новых ресурсных классов. Мы готовы к этому диалогу, - прокомментировала Екатерина Родыгина, директор «АНО «АПЦ РАСсвет».</p>\r\n\r\n', '/images/news/24.12.20/1.jpg', '2020-12-27', '/images/news/24.12.20/1.jpg;\r\n/images/news/24.12.20/2.jpg;\r\n/images/news/24.12.20/3.jpg', '', '', 'Новости', 'news.php', 0, '', '0'),
(332, '16', '\"Миллион на добрые дела\" - 2020', 'Традиционная церемония признания «Социальная звезда» состоялась в Иркутске накануне Нового года. На мероприятии чествовали общественные организации и активистов, реализовавших интересные и полезные для города и горожан проекты в течение года. Среди награжденных были и победители конкурса «Миллион на добрые дела»: учредители и волонтеры АНО «АПЦ РАСсвет».', '<p>Традиционная церемония признания «Социальная звезда» состоялась в Иркутске накануне Нового года. На мероприятии чествовали общественные организации и активистов, реализовавших интересные и полезные для города и горожан проекты в течение года. Среди награжденных были и победители конкурса «Миллион на добрые дела»: учредитель АНО «АПЦ РАСсвет» Мария Максимова, волонтеры Евгения Гдадышева, Лидия Лось, Светлана Зайченко заняли почетные третьи места с проектами «Новый год для каждого, новый год – для всех», «Экобум», «Аутизм. Семейный альбом», «Мастерские «Дом синего слона». Председатель «РАСсвета» Екатерина Родыгина выиграла 1 место с проектом «Скоро в школу».</p>\r\n<p>Мероприятие проходило в несколько этапов, чтобы не допустить большого скопления народа и для соблюдения мер безопасности, связанных с пандемией.</p>\r\n<p>-В этом году не было обычного большого концерта, к сожалению, мы не смогли встретиться со многими коллегами и друзьями-активистами, чтобы познакомиться, поделиться опытом и просто поговорить. Зато церемония прошла в уютной обстановке, в своем теплом кругу. Я благодарна за то, что городская администрация ценит наши проекты и отмечает их важность для развития Иркутска, - прокомментировала Екатерина Родыгина.\r\n</p>\r\n', '/images/news/28.12.20/1.jpg', '2020-12-29', '/images/news/28.12.20/1.jpg;\r\n/images/news/28.12.20/2.jpg;\r\n/images/news/28.12.20/3.jpg;\r\n/images/news/28.12.20/4.jpg;\r\n/images/news/28.12.20/5.jpg;\r\n/images/news/28.12.20/6.jpg', '', '', 'Новости', 'news.php', 0, '', '0'),
(334, '19', 'Итоги первого полугодия в РК', 'Восемь детей с диагнозом аутизм в сентябре 2020 года переступили порог МОУ ИРМО \"СОШ посёлка Молодежный\", благодаря неравнодушию департамента образования Иркутского района и администрации школы к проблеме социальной адаптации детей с РАС. И сегодня мы хотим подвести итоги первого полугодия в нашем первом ресурсном классе (РК) Иркутского района.', '<p>Несмотря на то, что в областном центре до сих пор отказывают в открытии ресурсных классов, 8 детей с диагнозом аутизм в сентябре 2020 года переступили порог МОУ ИРМО \"СОШ посёлка Молодежный\", благодаря неравнодушию департамента образования Иркутского района и администрации школы к проблеме социальной адаптации детей с РАС. И сегодня мы хотим подвести итоги первого полугодия в нашем первом ресурсном классе (РК) Иркутского района.</p>\r\n<p>Период адаптации к школе проходил не просто. Новый класс, новые люди, уроки, школьные переменки, на которых все куда-то бегут и шумят. Первое время комната сенсорной разгрузки была постоянно занята. Но благодаря слаженной работе коллектива РК, консультациям и советам нашего супервизора - Наталии Мальтинской, помощи волонтеров в приобретении необходимого оборудования, удалось в сжатые сроки начать обучающий процесс. И даже период занятий в дистанционном режиме не помешал продолжить обучение и социальную адаптацию наших детей.</p>\r\n<p>- За прошедшие полгода дети из ресурсного класса многому научились, и прежде всего групповым и социальным навыкам. Кто-то из учеников стал более самостоятелен на групповых занятиях, кому-то еще требуется помощь тьютора, но ребята уже спокойно сидят на уроке рядом, встают из-за парты, приветствуя учителя, выходят к доске, учатся отвечать хором на фронтальные вопросы. Если в начале года они перемещались в столовую, на уроки музыки и физкультуры за руку с тьюторами, то теперь ходят по коридору парами, как и другие первоклассники. В столовой ребята научились спокойно ждать своей очереди, выполнять гигиенические процедуры и уносить за собой посуду. В начале учебного дня дети с удовольствием участвуют в \"утреннем круге\" - нашем приветственном ритуале, а на уроках физкультуры - в эстафетах и подвижных играх. На переменах специалисты РК помогают первоклассникам взаимодействовать не только друг с другом, но и с ребятами из других классов, так что теперь они играют вместе. Самое главное достижение - это то, что дети с удовольствием посещают школу, - говорит куратор ресурсного класса Мария Максимова. </p>\r\n<p>Первое полугодие закончилось тем, что дети вместе наряжали елку и готовились к школьному празднику. В связи с противоковидными ограничениями, утренник прошел внутри одного класса, но создал настоящее новогоднее настроение: дети водили хоровод, читали стихи, участвовали в конкурсах и получили долгожданные подарки от Деда Мороза.</p>\r\n<p>АНО «АПЦ РАСсвет» участвует в проекте «Инклюзивная адаптация».\r\n\r\nБлагодарим организаторов и партнеров проекта:\r\nФонд «Выход». Аутизм в России\r\nАссоциация \"Аутизм Регионы\"\r\nПроект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/RK/20.12.30/1.jpg', '2020-12-30', '/images/RK/20.12.30/2.jpg;\r\n/images/RK/20.12.30/3.jpg;\r\n/images/RK/20.12.30/4.jpg;\r\n/images/RK/20.12.30/5.jpg;\r\n/images/RK/20.12.30/6.jpg;\r\n/images/RK/20.12.30/7.jpg;\r\n/images/RK/20.12.30/8.jpg;\r\n/images/RK/20.12.30/9.jpg', '', '', 'Новости', 'news.php', 0, '', '0'),
(348, '20', 'Немного о туалетном тренинге', 'Для мам и пап детей с аутизмом туалет, чаще всего, больная тема. Какает стоя, терпит по несколько часов, но писает только в подгузник, кричит при виде унитаза или горшка и ни за что не садится на него…\r\nСегодня поделимся опытом туалетного тренинга с трехлетним воспитанником «РАСсвета» Вадимом, и подскажем лайфаки из АВА-терапии.', '<p>Для мам и пап детей с аутизмом туалет, чаще всего, больная тема. Какает стоя, терпит по несколько часов, но писает только в подгузник, кричит при виде унитаза или горшка и ни за что не садится на него… Были ли у ваших детей подобные проблемы? А может, есть и сейчас? Трудно объяснить ребенку, который плохо понимает обращенную речь, зачем нужно ходить в туалет, а не в штаны. Некоторым детям с аутизмом даже нравятся ощущения от мокрых штанов.</p>\r\n<p>Сегодня поделимся опытом туалетного тренинга с трехлетним воспитанником «РАСсвета» Вадимом, и подскажем лайфаки из АВА-терапии.</p>\r\n<p>- Мальчиков, как и девочек, мы приучаем справлять нужду сидя, так как у многих детей с РАС есть особенность – какать стоя. И если ребенок привык к такому поведению, очень сложно потом его переучивать. Вам надо заранее подготовиться: найти мотивирующие вещи для малыша – то, чем он сможет заняться, сидя на горшке: игрушки, книжки, сенсорные предметы, мультики в гаджете. Вадиму нравятся книжки с окошками. Поэтому первое, что мы делали: учили его спокойно сидеть на горшке, садили прямо в штанишках, и давали ему полистать любимую книжку. Он мог получить это поощрение только в туалете, и нигде в другом месте, - рассказывает куратор «РАСсвета» Юлия Шестакова.</p>\r\n<p>Никогда не усаживайте и не удерживайте ребёнка на горшке или унитазе насильно: это только вызовет нежелательное поведение, и у ребенка закрепится ассоциация – туалет=плохое место.</p>\r\n<p>- Итак, мы садили Вадима вместе с книжкой на горшок и хвалили его за то, что он так хорошо сидит. Время нахождения мальчика на горшке постепенно увеличивалось – от 10 секунд до нескольких минут. Затем мы стали приспускать ему штанишки\r\n перед тем, как посадить на горшок, и следили за реакцией ребенка – комфортно ли ему? Реакция была нормальной, поэтому стали высаживать ребенка без штанишек, - продолжает Юлия.\r\n</p>\r\n<p>Можно использовать в виде поощрения еду – сладость или что-то другое, что любит ребенок. Но не во время его сидения на горшке, чтобы не сформировалась привычка есть и писать или какать одновременно. Можно дать кусочек фрукта или конфетку после того, как ребенок посидел на горшке.</p>\r\n<p>- Несколько дней педагоги центра вели сбор данных: сколько раз Вадим писает за время занятия, сколько времени проходит между тем, как он попил, и намочил штаны? От подгузника решили отказаться. Чаще обычного поили мальчика водой и соком – ведь чем больше он выпьет, тем чаще захочет сходить в туалет, больше будет возможностей для тренировки. После того, как провели анализ данных, стали высаживать Вадима уже ориентируясь по конкретному времени. К тому моменту к горшку он уже привык, сидел на нем спокойно. Случались промашки, но за успехи и выполнение инструкций педагоги его всегда щедро хвалили,- комментирует куратор центра Юлия Шестакова.</p>\r\n<p>Во время обучения туалетного навыка нужно часто предлагать ребенку напитки, которые он любит или те, которые могут ему понравиться. Стараться поить по чашке в час. Чтобы вызвать жажду, можно угощать ребенка солеными снеками: орешками, чипсами, попкорном, крендельками.</p>\r\n<p>- Вадим уверено освоил горшок за полгода. Постепенно педагоги убирали поощрения в виде книг из процесса, оставляли только устную похвалу. В идеале, нужно научить ребенка пользоваться туалетом самостоятельно, без напоминаний, постоянной помощи и похвал. Этот процесс может затянуться, но родителям и педагогам надо работать в одном ключе и сохранять спокойствие, - говорит куратор.</p>\r\n<p>Перед началом обучения найдите удобный горшок – чтобы ребенок мог сам легко выливать из него содержимое. Горшок всегда должен находиться в санузле, но подводить к нему ребенка нужно из разных комнат, мест дома, чтобы он умел добираться до пункта назначения без затруднений.</p>\r\n<p>Возле горшка или унитаза можно закрепить на стене визуальную подсказку: в фотографиях или рисунках показать очередность действий. Физическую подсказку взрослый оказывает после того, как инструкция прозвучала («сядь на горшок»), но ребенок не смог ее выполнить.</p>\r\n<p>Если ребенок любит куклы или мягкие игрушки, можно сначала садить на горшок их. Затем гладить и хвалить их, вовлекать в эту игру ребенка. Есть «писающие» пупсы, с ними туалетный тренинг может пройти еще быстрее. Так малыш увидит весь процесс со стороны, и вашу положительную реакцию на «приучение» куклы к горшку.</p>\r\n<p>Также родителям советуем почитать статьи на тему туалетного тренинга:</p>\r\n<p><a class=\"a-blue\" href=\"https://autism-aba.blogspot.com/2011/11/bowel-toilet-training.html\" target=\"_blank\">\"Большой\" туалетный тренинг - как решить эту проблему при аутизме?</a> </p>\r\n<p><a class=\"a-blue\" href=\"https://outfund.ru/tualetnyj-trening-za-36-chasov-dlya-detej-s-autizmom/\" target=\"_blank\">«Туалетный тренинг за 36 часов» для детей с аутизмом</a></p>\r\n<p>Есть отличная книга «Туалетный тренинг. Как ребенку освоить горшок за день», авторы Азрин и Фокс.</p>\r\n<p>А детям можно включать обучающие мультфильмы. На YouTube есть целая подборка, но в ней только первые 10 на тему туалета.</p>\r\n<p><a class=\"a-blue\" href=\"https://www.youtube.com/playlist?list=PLNVuocAgnZ4HN4XwcGoxxxOZewPJ5l7eb\" target=\"_blank\">Приучение к горшку</a> </p>\r\n<p></p>', '/images/SP/12.jpg', '2021-02-04', '', '', '', 'Новости', 'news.php', 0, '', '13'),
(352, '13', 'Родители детей с аутизмом в Иркутске надеются на появление ресурсного класса в общеобразовательных школах', 'Родители детей с аутизмом объединились для продвижения одного очень важного и нужного дела – открытия ресурсных классов в общеобразовательных школах Иркутска.\r\nЭту тема была освещена в вечернем эфире программы \"Вести - Иркутск\" 11 февраля.', '<p>Родители детей с аутизмом объединились для продвижения одного очень важного и нужного дела – открытия ресурсных классов в общеобразовательных школах города Иркутска.\r\nЭту тема была освещена в вечернем эфире программы <a class=\"a-blue\" href=\"https://vestiirk.ru/news/roditeli-detei-s-autizmom-v-irkutske-nadeiutsia-na-poiavlenie-resursnogo-klassa-v-obshcheobrazovatelnykh-shkolakh/\" target=\"_blank\">\"Вести - Иркутск\"</a> 11 февраля.</p>\r\n<p> Сотрудники АНО \"АПЦ Рассвет\" и родители детей, посещающих центр, на протяжении часа разговаривали с журналистом, и понятно, что вся информация не уместилась в репортаже. Поэтому мы резюмируем то, что осталось за кадром.</p>\r\n<p>Людям, непосвященным в проблему, может показаться, что вся эта история незначительна, есть ведь домашнее обучение, коррекционные школы… Но в случае детей с диагнозом РАС эти варианты – не альтернатива. Модель ресурсного класса включает в себя индивидуальный подход, образовательные программы, помощь тьютора, обучение с научным подходом (речь о прикладном анализе поведения, или АВА), частое общение с нейротипичными сверстниками – все то, что нужно ребенку с аутизмом, чтобы адаптироваться к жизни в обществе и найти свое место в нем. Запирать детей с ОВЗ в спец школах, а чаще всего дома, не выход.</p>\r\n<p>Нейротипичным школьникам (да и взрослым – родителям и учителям) общение с нейроотличными детьми – разовьет толерантность и креативность. Также в модели ресурсного класса присутствует сенсорная комната – помещение, в котором ребенок, здоровый или с каким-то диагнозом, может отдохнуть и получить важные для него ощущения. То есть ресурсный класс выгоден всей школе.</p>\r\n<p>И наконец, комментарий представителя департамента образования городской администрации о том, что у чиновников не было списка с количеством детей с диагнозом РАС, кого родители хотели бы записать в ресурсный класс. Список, конечно, был. Борьба за открытие ресурсного класса началась еще 3 года назад, тогда родители-активисты, в том числе и представители «РАСсвета», начали обращаться в департамент образования за помощью. С диагностикой аутизма дела обстоят плохо по всей области, и все же 14 подписей в прошлом были отданы чиновникам. Сейчас мы собрали около 60 подписей заинтересованных родителей за 2 недели! И продолжаем их собирать, чтобы впредь никто не заявлял, что детей с таким диагнозом в Иркутске нет.</p>\r\n<p>Спасибо тем, кто помогает с открытием ресурсных классов! Первопроходцам всегда тяжело, но мы справимся! Присоединяйтесь все те, кому есть что сказать в защиту интересов своих детей!</p>\r\n', '/images/smi/16.jpg', '2021-02-12', '', '', '', '', '', 0, '', '0');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(362, '19', 'Отзыв тьютора о буднях РК', 'В конце февраля школьные занятия в самом разгаре. О буднях в ресурсном классе МОУ ИРМО \"СОШ посёлка Молодежный\" рассказала тьютор Екатерина Балагурова. <br>\r\n- У меня еще никогда не было работы, на которую даже в шесть утра ежедневно я просыпалась бы с желанием. Теперь есть, и это - наш ресурсный класс...', '<p>В конце февраля школьные занятия в самом разгаре. О буднях в ресурсном классе МОУ ИРМО \"СОШ посёлка Молодежный\" рассказала тьютор Екатерина Балагурова. </p>\r\n<p>- У меня еще никогда не было работы, на которую даже в шесть утра ежедневно я просыпалась бы с желанием. Теперь есть, и это - наш ресурсный класс.</p>\r\n<p>- Главным достижением нашей команды за полгода учебы, я считаю, само желание детей приходить в школу. Может показаться, что это пустяк, но, на мой взгляд, это и было нашей главной задачей на этапе старта обучения. Люди с аутизмом очень тяжело адаптируются к новым условиям, часто боятся новых людей, помещений, действий. Тогда начинаются протесты, истерики, слезы и просто отказ от работы. Если такой \"страх нового\" закрепится в мозге и теле ребёнка, любая образовательная работа будет бессмысленна.</p>\r\n<p>- Как практикующий детский психолог, я знакома с термином \"психологическое поглаживание\". Это одобрение, знак внимания, проявление условного принципа \"с тобой всё окей, ты с нами, мы тебя принимаем\". Для каждого человека такие поглаживания необходимы ежедневно, для детей с особенностями развития - тем более. И мне кажется, наш ресурсный класс - это и есть «класс поглаживаний».</p>\r\n<p>- Утром мы встречаем каждого ребенка лично, обращаемся по имени, совершаем ритуал-игру на приветствие. На переменах мы разрешаем детям брать игрушки, строить замки из песка и конструктора, бегать, играя в догоняшки в коридоре, шуметь, отдыхать в кресле-мешке. Сейчас у нас сформирован класс детей, для которых школа - приятное безопасное место, где можно свободно учиться, получать знания, играть, общаться, проявлять себя.</p>\r\n<p>- Когда ребёнок выполнил задание неверно, мы не говорим привычное для школ: \"Нет, всё не так, оценка \"2\". Мы говорим: \"Это не то, что нужно, давай еще раз\". Вся система прикладного анализа поведения, на основе которой мы работаем в классе, основана на поощрении, а не наказании. И это её главное отличие и преимущество перед стандартной школьной системой, которая, на мой взгляд, давно проживает глубокий кризис. Мы всегда делаем акцент на успехе. Уверена, обучение всех, в том числе нейротипичных детей, на мотивации, а не на страхе ошибки, позволило бы достичь гораздо больших успехов во всем нашем образовании.</p>\r\n<p>- Дети из регулярных классов смотрят на учеников ресурсного класса с большим интересом, им нравится заходить в наш кабинет, где много игрушек и всё можно брать, они не боятся общения, играют вместе на переменах, после уроков помогают нашим ребятам одеваться. А это и было главным желанием родителей наших деток - инклюзия, равное общение.</p>\r\n<p>- Мы еще в самом начале пути. Многое впереди, нам только предстоит выход (пусть даже на несколько минут на этапе начала) в регулярные классы. Но работа движется, а ошибок и трудностей мы не боимся. Мы все видим, что ресурсный класс был нужен детям с РАС. Спасибо единственной школе, которая согласилась нас принять. Спасибо родителям, отстоявшим эту инициативу и помогающим нам во всем. Спасибо всей нашей команде, каждый член которой точно знает, для чего он здесь. </p>\r\n\r\n<p>АНО «АПЦ РАСсвет» участвует в проекте «Инклюзивная адаптация».</p>\r\n\r\n<p>Благодарим организаторов и партнеров проекта:\r\nФонд «Выход». Аутизм в России, \r\nАссоциация \"Аутизм Регионы\".</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/RK/21.02.27/1.jpg', '2021-02-27', '/images/RK/21.02.27/1.jpg;\r\n/images/RK/21.02.27/2.jpg;\r\n/images/RK/21.02.27/3.jpg;\r\n/images/RK/21.02.27/4.jpg;\r\n/images/RK/21.02.27/5.jpg;\r\n/images/RK/21.02.27/6.jpg;\r\n/images/RK/21.02.27/7.jpg', '', '', 'Новости', 'news.php', 0, '', '0'),
(370, '16', 'Стратегическая сессия Ассоциации «Аутизм-Регионы»', 'В субботу 6 марта прошла стратегическая сессия Ассоциации «Аутизм-Регионы», на которой обсуждали, как в ближайшие 5 лет поддержать людей с аутизмом и их семьи. \r\nМозговой штурм устроили в дистанционном формате 20 участников сессии - лидеры НКО из разных регионов России. «АПЦ РАСсвет» представили его учредители: Екатерина Родыгина и Елена Михеева. ', '<p>«Чтобы выполнить большой и важный труд, необходимы две вещи: ясный план и ограниченное время», - сказал в прошлом столетии писатель Элберт Хаббарт. За прошедший век мысль о важности планирования не потеряла своей актуальности. В субботу 6 марта прошла стратегическая сессия Ассоциации «Аутизм-Регионы», на которой обсуждали, как в ближайшие 5 лет поддержать людей с аутизмом и их семьи. \r\n</p>\r\n<p>Представить желаемое будущее, составить четкий план действий для его осуществления и расписать какие системы, знания, информация и ресурсы должны будут существовать и как работать…Мозговой штурм устроили в дистанционном формате 20 участников сессии - лидеры НКО из разных регионов России. «АПЦ РАСсвет» представили его учредители: Екатерина Родыгина и Елена Михеева. </p>\r\n<p>«Все дети с РАС имеют доступ в сады и школы», «Свободный доступ к ранней диагностике и помощи», «В каждой школе, ДОУ и ВУЗе есть специалист ПАП», «Существует система сопровождаемого проживания. Реальная и доступная»… Это малая часть из желаемого, но вполне осуществимого образа будущего, которое представили стратеги Ассоциации.</p>\r\n<p>- Мы будем работать над поставленными целями. Это нелегкий труд, но у нас есть поддержка коллег по всей стране, готовых поделиться опытом и протянуть руку помощи, - прокомментировала Екатерина Родыгина, руководитель «РАСсвета». </p>\r\n\r\n\r\n', '/images/news/07.03.21/1.jpg', '2021-03-07', '/images/news/07.03.21/1.jpg;\r\n/images/news/07.03.21/2.jpg;\r\n/images/news/07.03.21/3.jpg;\r\n/images/news/07.03.21/4.jpg', '', '', 'Новости', 'news.php', 0, '', '0'),
(373, '16', 'Познакомимся поближе: Виктория Кузнецова', 'Сегодня будем знакомиться ближе с фитнес-инструктором «РАСсвета» Викторией Кузнецовой, одной из учредителей нашего центра, мамой подростка с РАС и творческой личностью. Вика рассказала о себе, своих учениках и способах восполнить внутренний ресурс. ', '<p>Сегодня будем знакомиться ближе с фитнес-инструктором «РАСсвета» Викторией Кузнецовой, одной из учредителей нашего центра, мамой подростка с РАС и творческой личностью. Вика рассказала о себе, своих учениках и способах восполнить внутренний ресурс. </p>\r\n<p> - Я работаю инструктором адаптивной физкультуры. Самое интересное в моей деятельности - это возможность отрабатывать на занятии не только навыки физического мастерства. Мы работаем над визуальным восприятием, поведением слушателя, сотрудничеством, учим взаимодействовать в игре с ровесниками, обучаем ребят аккуратности, внимательности и безопасному поведению. Всё это, и многое другое, возможно отработать на обычной физкультуре, если применять методы и принципы прикладного анализа поведения.</p>\r\n<p>- Так называемые рабочие \"фишки\" у нас тоже имеются. Часто возникновение и применение идей, увеличивающих продуктивность занятия, \"подсказывает\" сам ученик, ведь каждый из них индивидуален и любит что-то свое. Нужно внимательно изучать детей, их поведение и увлечения, любить и уважать своих учеников.</p>\r\n<p>- Очень бы хотелось понять каждого своего ученика изнутри, узнать, что на самом деле он чувствует, сколько знает, из чего сделан его внутренний мир. Я думаю, что мамы детей с особенностями развития меня поймут. Кажется, что делаешь всё умело, правильно, но эмпатические связи у таких детей нарушены, поэтому никогда нельзя быть уверенным, что им на самом деле хорошо.</p>\r\n<p>- Я хореограф по образованию и призванию, работать с детьми с аутизмом я стала только благодаря сыну. Конечно, я нахожу свою работу удивительной, нескучной, но танец навсегда останется моим самым главным увлечением жизни. Ещё я люблю свой сад и с нетерпением жду весны, когда начнётся вся эта возня с растениями, меня это успокаивает. Люблю читать фантастику и смотреть европейские фильмы, \"нудных\" британцев или французов со странным юмором, чуждым русскому менталитету. Именно в эти нечастые минуты отдыха я, наконец-то, не думаю об аутизме ребёнка.\r\n</p>\r\n<p>- Есть мечты и цели в жизни. Они все крутятся вокруг благополучия моей семьи, близких и моих личных интересов, конечно. Хочу своим детям показать Питер летом, там так красиво. Сын бы шагал по мостовым в своих шумопоглащающих наушниках, улыбался, держал сестру за руку, а я бы просто радовалась за них.</p>\r\n<p>- Виктория - профессионал своего дела, но также она проявляет инициативу в других областях, и справляется с ней успешно. Мастерские \"Дом синего слона\" - её идея, проект, в который она вкладывает много своей энергии, - говорит Екатерина Родыгина, руководитель \"РАСсвета\".</p>\r\n\r\n\r\n\r\n', '/images/team/Kuznecova/foto/1.jpg', '2021-03-09', '', '', '', 'Команда', 'team.php', 0, '', '0'),
(383, '12', 'Подводим итоги проекта \"Движение\"', 'Подводим итоги проекта социальной адаптации и физической реабилитации детей с аутизмом «Движение»! Он стартовал в «АПЦ РАСсвет» в прошлом году в марте, в нем приняло участие 15 детей из Иркутска. Для каждого ребенка была составлена индивидуальная программа тренировок. ', '<p>Подводим итоги проекта социальной адаптации и физической реабилитации детей с аутизмом «Движение»! Он стартовал в «АПЦ РАСсвет» в прошлом году в марте, в нем приняло участие 15 детей из Иркутска. Для каждого ребенка была составлена индивидуальная программа тренировок. Важно было не только улучшить физическую форму ребятишек, но и найти к ним подход, замотивировать на занятие, научить пользоваться различными тренажерами и выполнять упражнения по инструкции педагога. \r\n</p>\r\n<p>Про успехи одного из участников проекта рассказала инструктор АФК Виктория Кузнецова:\r\n- Владу сложно удерживать внимание, сосредоточиться на выполнении задания даже несколько минут. Плюс, его аутостимуляции - самая большая преграда в обучении. В течение года мы делали акцент на обучении Влада выполнять фронтальные инструкции от начала до конца без ухода в аутостимулятивные действия. Его занятия состояли из цепочек заданий, которые он должен делать друг за другом, сохраняя внимание и собранность, оставаться спокойным.</p>\r\n<p>Влад даже поучаствовал в WorkShop для педагогов спортивных центров, в качестве «модели» - открытый урок с мальчиком помог тренерам увидеть на практике, как проводить тренировку с ребенком с аутизмом, какие методики использовать. Мастер-класс, кстати, также проходил в рамках проекта «Движение». </p>\r\n<p>- Сейчас Влад уверенно выполняет упражнения, требующие статического положения тела, понимания расположения частей тела, моторного планирования, баланса. Но ему почти всегда требуются визуальные и физические подсказки для запуска реакции. Если в начале обучения план тренировок Влада был максимально упрощенным и сводился к установлению и поддержанию руководящего контроля и мотивации, то сейчас его программа включает более 20 заданий с компонентами самостоятельности, - добавляет Виктория.\r\n</p>\r\n<p>Занятия АФК не только улучшают физическую форму детей, но и развивают их коммуникативные навыки. Проверено на опыте наших воспитанников, а также тренеров – Виктории Кузнецовой и Дарьи Шульги.</p>\r\n<p>- Занятия посещают дети разных возрастов, но у всех у них в той или иной мере есть трудности с общением, взаимодействием с окружающим миром. Например, дошкольник Семен испытывает сильный страх в присутствии чужих людей, в новых помещениях. Раньше его было трудно перевести из одной комнаты центра в другую – переход сопровождался криками и плачем. Замотивировать ребенка ничем не удавалось. Недавно он заменил в проекте «Движение» одного из участников, и стал посещать АФК 4 раза в неделю. Сейчас он заходит в спортивный зал самостоятельно, без мамы, и способен выполнить с физической подсказкой от 4 до 6 заданий. Раньше Сема боялся детей. Сейчас, если во время занятия в зал заходят другие воспитанники, мальчик спокойно на них реагирует, и продолжает выполнять упражнения, - рассказывает Виктория Кузнецова.</p>\r\n<p>Из-за пандемии часть занятий АФК проходила в дистанционном формате. Это было непростое испытание для всех участников процесса. Родителям тоже пришлось подключиться к тренировкам, чтобы в домашних условиях помогать своим детям. Карантин внес правки и в проведение летней эстафеты. Командные соревнования задумывались для того, чтобы научить детей с аутизмом взаимодействовать в группе на уроках физкультуры.\r\n</p>\r\n<p>- Массовые эстафеты были распределены таким образом, чтобы не нарушать условия карантина: минимальное количество детей на массовом мероприятии, средства индивидуальной защиты, свежий воздух. На эстафетах ребята демонстрировали умение ждать своей очереди, ловкость, меткость и скорость. Все участники отлично справились с заданиями, поскольку предварительное дробное обучение позволило подготовить детей к предстоящей эстафете, дало понимание о том, что им предстоит, сделало окружающую среду понятной и предсказуемой, - прокомментировала Виктория Кузнецова.</p>\r\n<p>Проект адаптивной физкультуры для детей с аутизмом будет  продолжать свою работу. Оставайтесь с нами на связи, и мы расскажем о нем вновь! </p>\r\n<p>Проект \"Движение\" реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n\r\n', '/images/AFK/21.02.02/0.jpg', '2021-02-25', '/images/AFK/21.02.02/3.jpg;\r\n/images/AFK/21.02.02/1.jpg;\r\n/images/AFK/21.02.02/6.jpg;\r\n/images/AFK/21.02.02/7.jpg;\r\n/images/AFK/21.02.02/12.jpg;\r\n/images/AFK/21.02.02/14.jpg;\r\n/images/AFK/21.02.02/10.jpg;\r\n/images/AFK/fit/19.jpg\r\n', '', '', '', '', 0, '', '0'),
(387, '16', 'Познакомимся поближе: Анна Леонтьева', 'Среди сотрудников центра «РАСсвет» случайных людей не бывает: здесь педагоги трудятся не просто за зарплату, а по призванию. Психолог Анна Леонтьева осознанно подошла к выбору места работы. Попробовав себя в разных направлениях, она решила работать с детьми с расстройством аутистического спектра в нашем центре. Почему? Читайте ее интервью!', '<p>Среди сотрудников центра «РАСсвет» случайных людей не бывает: здесь педагоги трудятся не просто за зарплату, а по призванию. Психолог Анна Леонтьева осознанно подошла к выбору места работы. Попробовав себя в разных направлениях: детской, социальной, клинической психологии, геронтологии и нейропсихологии, она решила работать с детьми с расстройством аутистического спектра в нашем центре. Почему? Читайте ее интервью!</p>\r\n<p>- Я работаю психологом 5 лет. Еще на начальных этапах профессионального опыта, работая с ребенком ОВЗ, я увидела и почувствовала его благодарность, его эмоции, которые он пережил на занятии, и обращенное ко мне \"спасибо \"… Меня это очень впечатлило, и осталось не забываемым моментом в памяти. Но на тот момент моей целью была другая аудитория. Реализуя её, и набравшись опыта, в 2020 году я пришла к осознанию того, что готова развиваться в клинической психологии и нейропсихологии. Таким образом, в моей жизни появился \"РАСсвет\".</p>\r\n<p>- Сейчас я регулярно занимаюсь с детьми с РАС разных возрастов. Самые мои любимые фишки в работе: развитие сенсорного восприятия и межполушарных связей.</p>\r\n<p>- Самое интересное в моей работе - прививать осознание того, что жизнь - самая большая ценность. Этому нужно учить не только взрослых людей, но и детей, конечно, учитывая их индивидуальные особенности развития. Я помогаю активировать личностные душевные ресурсы, устранить эмоциональные блоки. Результаты потрясающие. Я кайфую от того блеска в глазах, с которым уходят мои клиенты.</p>\r\n<p>- Аня – олицетворение спокойствия и равновесия - то, в чем так нуждаются наши дети, - добавляет Екатерина Родыгина, руководитель «АПЦ РАСсвет».</p>\r\n\r\n\r\n', '/images/team/Leonteva/foto.jpg', '2021-03-22', '', '', '', 'Команда', '/team.php', 0, '', '0'),
(393, '13', '2 апреля в Иркутске начнет работать фотовыставка \"Аутизм. Семейный альбом\"', '\"2 апреля отметят Международный день информирования о проблеме аутизма. В Иркутске специально к нему организовали фотовыставку.\" Репортаж и публикации о предстоящей выставке \"Аутизм. Семейный альбом\" вышли в региональных новостях.', '<p>Репортаж и публикации о предстоящей выставке \"Аутизм. Семейный альбом\" вышли в региональных СМИ.</p>\r\n<p>\"2 апреля отметят Международный день информирования о проблеме аутизма. В Иркутске специально к нему организовали фотовыставку. На ней снимки тех, кто живет с этим диагнозом. Дети и их родители. Готовили экспозицию два года.\"</p>\r\n<p>Сюжет о подготовке к выставке был показан в  в эфире программы \"Сей час\" на канале \"АИСТ ТВ\" 31 марта. Автор репортажа Дарья Пастушенко взяла интервью у организаторов и участников проекта.</p>\r\n<p><a href=\"https://aisttv.ru/page/2-aprelia-v-irkutske-nachnet-rabotat-fotovystavka-autizm-semeinyi-albom/\" target=\"_blank\">СМОТРЕТЬ СЮЖЕТ</a></p>\r\n<p>Также анонсы выставки были опубликованы в популярных интернет-изданиях Иркутска.</p>\r\n<p>\"Верблюд в огне\" <a href=\"https://verbludvogne.ru/news/fotoproekt-110321/\" target=\"_blank\">(verbludvogne.ru)</a></p>\r\n<p>\"Глагол. Иркутское обозрение\" <a href=\"https://glagol38.ru/text/16-03-2021/008\" target=\"_blank\">(glagol38.ru)</a></p>', '/images/smi/17.jpg', '2021-03-31', '', '', 'https://aisttv.ru/page/2-aprelia-v-irkutske-nachnet-rabotat-fotovystavka-autizm-semeinyi-albom/', '', '', 0, '', '0'),
(395, '5', 'Фотовыставка \"Аутизм. Семейный альбом\"', 'Информирование о РАС происходит в первую очередь, благодаря родителям детей с аутизмом. 2 апреля в ТВЦ «Revoлюция» открылся совместный проект «АПЦ РАСсвет» и фотографа Лидии Лось – фотовыставка «Аутизм. Семейный альбом». ', '<p>2 апреля дата для всех, кто в теме РАС, значимая - Всемирный день распространения информации об аутизме. Информирование привлекает внимание к проблемам аутизма, разрушает стереотипы о людях с РАС, способствует развитию инклюзивного общества.</p>\r\n<p>Делимся с вами записью эфира иркутской радиостудии \"Вести ФМ\" на тему мифов об аутизме. На вопросы ведущей Дины Оккерт отвечала председатель \"РАСсвета\" Екатерина Родыгина. </p>\r\n<p><audio controls=\"controls\" src=\"/assets/audio/0104_autism.mp3\"></audio></p>\r\n\r\n<p>Информирование о РАС происходит в первую очередь, благодаря родителям детей с аутизмом: тех, кто создает всевозможные организации помощи и поддержки, участвует в лоббировании новых гуманных законов, отстаивает права людей с ограниченными возможностями здоровья и т.д.</p>\r\n<p>2 апреля в ТВЦ «Revoлюция» открылся совместный проект «АПЦ РАСсвет» и фотографа Лидии Лось – фотовыставка «Аутизм. Семейный альбом», приуроченная к Всемирному дню информирования об аутизме. Лидия Лось - профессиональный фотограф, чьи снимки вы часто можете видеть в нашей ленте. Она предпочитает репортажную съемку – живые кадры, трогающие за душу.</p>\r\n<p>На выставке вы можете увидеть истории 11 иркутских семей, воспитывающих детей с аутизмом.  Это не просто фотографии детей с РАС и их родителей, это попытка открыться и показать миру повседневную жизнь связанных с аутизмом людей: в чем-то других, в чем-то таких же, как все. Фотографии отразили повседневные дела обычных пап, мам и их необычных деток. Это очень честный и откровенный проект, в нем нет отверженных гениев из голливудских фильмов про людей с аутизмом или абсолютно беспомощных, непонятых. Только реальные истории.</p>\r\n<p>В день открытия всех гостей ждало зажигательное выступление иркутского музыкального бэнда, выступление актёров студии В. Лопаева, ярмарка хендмейда и дегустация вкусняшек!\r\nА третьего апреля во время посещения выставки гости смогли поучаствовать в мастер-классах, посмотреть мультфильмы и послушать час чтения в книжном магазине \"Кукуля\".</p>\r\n<p>Выставка продлится до 19 апреля, вход на неё свободный. Приходите сами, берите детей, друзей и хорошее настроение!</p>\r\n<p>В месяц информирования об аутизме «АПЦ РАСсвет» также присоединяется к специальному проекту Ассоциации \"Аутизм Регионы\" – флешмобу в социальных сетях.</p>\r\n<p>У каждого родителя ребенка с аутизмом есть своя «подборка» историй о том, как люди реагируют на поведение ребенка или просто информацию о его диагнозе, о том, как приходится подстраиваться под ситуацию и придумывать разные хитрости, чтобы существовать в обществе. Иногда воспоминания добрые, а иногда от них хочется поскорее избавиться.\r\n</p>\r\n<p>Поучаствуйте во флешмобе! Поделитесь новой или уже рассказанной ранее историей на своей странице в социальных сетях. Поставьте хештег #ятебяпонимаю и #Япростохочупомочьсвоемуребенку, опубликуйте любое фото, подходящее к случаю.\r\n</p>\r\n<p><b>История мамы Оксаны и ее дочки Сони:</b></p>\r\n<p><a href=\"https://vk.com/wall39653218_62\" target=\"_blank\">Читать в VK</a></p>\r\n<p>Расстройство аутистического спектра (РАС) – это комплексные нарушения психического развития, которые характеризуются постоянным дефицитом в способности инициировать и поддерживать взаимное социальное взаимодействие и социальную коммуникацию, а также рядом ограниченных, повторяющихся и негибких схем поведения и интересов.</p>\r\n<p>В заключение немного цифр для размышлений:\r\nЦентр по контролю и профилактике заболеваний США опубликовал в 2020 году статистику – РАС встречается у каждого 54-го ребёнка, что на 10% больше, чем в 2018 году. Минздрав РФ сообщает о росте числа людей с РАС в России на 4,5%, но пока наша статистика уступает показателям других стран, и высокая доля детей остаётся не диагностированной. По данным Федеральной службы государственной статистики детское население Иркутской области – 608231 человек, прогнозируемое количество детей с РАС – 6082. Однако официально диагноз имеют лишь 76 детей.</p>\r\n\r\n\r\n', '/images/family_album/4.jpg', '2021-04-04', '/images/family_album/02.04.2021/1.jpg;\r\n/images/family_album/02.04.2021/2.jpg;\r\n/images/family_album/02.04.2021/3.jpg;\r\n/images/family_album/02.04.2021/4.jpg;\r\n/images/family_album/02.04.2021/5.jpg;\r\n/images/family_album/02.04.2021/6.jpg;\r\n/images/family_album/02.04.2021/7.jpg;\r\n/images/family_album/02.04.2021/8.jpg', '', '', 'Новости', 'news.php', 0, '', '0'),
(396, '3', 'Семинар «Методы обучения детей с аутизмом посещению медицинских учреждений и выполнению лечебных манипуляций»', 'О том, как важно поэтапно приучать ребенка с РАС к процедурам во врачебных кабинетах, и на какие мелкие шаги разбить поход в поликлинику рассказала на семинаре 3 апреля поведенческий специалист, куратор ресурсного класса МОУ ИРМО \"СОШ поселка Молодежный\" Мария Андреевна Максимова. ', '<p>О том, как важно поэтапно приучать ребенка с РАС к процедурам во врачебных кабинетах, и на какие мелкие шаги разбить поход в поликлинику рассказала на семинаре 3 апреля поведенческий специалист, куратор ресурсного класса МОУ ИРМО \"СОШ поселка Молодежный\" Мария Андреевна Максимова. </p>\r\n<p>Семинар «Методы обучения детей с аутизмом посещению медицинских учреждений и выполнению лечебных манипуляций» организовал «АПЦ РАСсвет». Именно на опыте работы с воспитанниками центра основывался доклад спикера: принесшие результаты протоколы поведения, лайфхаки и небольшие интерактивные включения с аудиторией родителей и педагогов.</p>\r\n<p>Семинар проходил в ТВЦ «Революция», его посетили более 100 человек, часть из них - дистанционно. Несколько человек, просто проходивших мимо места мероприятия, тоже решили присоединиться и прослушать спикера. Это показатель того, что интерес к теме аутизма у иркутян есть! </p>\r\n<p>Каждый зарегистрированный участник по окончанию семинара получил сертификат и книжку с социальной историей, разработанной сотрудниками «РАСсвета». Эта книжка поможет ребенку в игровой форме познакомиться с правилами поведения в медицинских учреждениях.</p>\r\n<p>Спасибо, что были с нами на семинаре! Особая благодарность волонтёрам из пединститута Подгорной Софье, Сукневой Анастасии. Нам важна поддержка.</p>\r\n<p>Мы продолжим обучаться и развиваться с вами ради наших детей! </p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов. \r\n</p>\r\n', '/images/seminar/03.04.2021/2.jpg', '2021-04-05', '/images/seminar/03.04.2021/1.jpg;\r\n/images/seminar/03.04.2021/3.jpg;\r\n/images/seminar/03.04.2021/4.jpg;\r\n/images/seminar/03.04.2021/5.jpg', '', '', '', '', 0, '', '13'),
(397, '18', 'Ярмарка в день информирования об аутизме', 'Что ж, мы отлично провели время на ярмарке-продаже изделий Дома Синего Слона, приуроченной ко 2 апреля, дню информирования о проблеме аутизма!\r\nОчень приятно, что творения учеников нашли отклик в сердцах горожан и всех неравнодушных к нашей теме людей!', '<p>В Иркутске все ещё сильны стереотипы, из-за которых дети с аутизмом вызывают негативные чувства у людей. А мы уверенно говорим: у каждого ребёнка с аутизмом, независимо от возраста, богатый внутренний мир, и он борется с аутистическими преградами изо всех сил, чтобы показать этот мир окружающим с самой лучшей стороны. Специалисты, профессионально владеющие методиками обучения и социализации детей с РАС, помогают этим детям раскрыться настолько, насколько это возможно, чтобы быть принятым в общество.</p>\r\n<p>2 апреля, в день информирования социума о проблеме аутизма, открылась выставка фото-работ Лидии Лось о жизни семей, воспитывающих детей с РАС. Самые искренние истории, без прикрас и напускной романтизации. </p>\r\n<p>Так же все желающие могли приобрести хендмейд на ярмарке-продаже изделий \"Дома Синего Слона\". Все вырученные средства будут вложены в приобретение материалов для дальнейшего развития мастерских. \r\nОчень приятно, что творения учеников нашли отклик в сердцах горожан и всех неравнодушных к нашей теме людей!</p>\r\n<p>К сожалению, не каждая работа была выставлена на продажу, ведь мы понимаем - это делали ДЕТИ! Ребята ещё только обучаются, мы всеми силами им помогаем, и впереди запланировано множество высот, которые предстоит покорить!</p>\r\n<p>За реализацию проекта мастерских для детей с аутизмом огромная благодарность нашему куратору Виктории Кузнецовой, педагогам: Светлане Зайченко, Оксане Разгуляевой. Волонтеру Яне Чернечиковой.</p>\r\n<p>Нашим инста-хранителям: Татьяне Лимановской, Оксане Разгуляевой.\r\nРуководителю АНО АПЦ \"РАСсвет\" Екатерине Родыгиной и всем добрым людям организации @rassvet.apc !</p>\r\n<p>Отдельно хочется поблагодарить за поддержку наших партнёров и друзей: фотографа Лидию Лось, ИОООРДОВ \"Радуга\", @lent_tochka, @fresh_creative_</p>\r\n<p>До скорой встречи, ждите новые обзоры занятий!\r\n</p>\r\n<p>Проект реализуется победителем конкурса \"Школа филантропии\" благотворительной программы \"Эффективная филантропия\" Благотворительного фонда Владимира Потанина.\r\n</p>\r\n', '/images/DSS/21.04.02/2.jpg', '2021-04-05', '/images/DSS/21.04.02/1.jpg;\r\n/images/DSS/21.04.02/2.jpg;\r\n/images/DSS/21.04.02/3.jpg;\r\n/images/DSS/21.04.02/4.jpg;\r\n/images/DSS/21.04.02/5.jpg;\r\n/images/DSS/21.04.02/6.jpg;\r\n/images/DSS/21.04.02/7.jpg;\r\n/images/DSS/21.04.02/8.jpg;\r\n/images/DSS/21.04.02/9.jpg;\r\n/images/DSS/21.04.02/10.jpg;\r\n/images/DSS/21.04.02/11.jpg;\r\n/images/DSS/21.04.02/12.jpg', '', '', '', '', 0, '', '0'),
(398, '13', 'Домашний альбом. Фотовыставка о семьях с особенными детьми открылась в Иркутске', 'Выставка «Аутизм. Семейный альбом» продолжает работать в ТВЦ «Revoлюция» на Карла Маркса, 40. Сюжет о выставке можно посмотреть в выпуске новостей от «Вести-Иркутск» от 7 апреля. Спасибо журналисту Юлии Шавельковой и команде «Вести-Иркутск» за внимательность к теме аутизма.', '', '/images/smi/18.jpg', '2021-04-08', '', '', 'https://vestiirk.ru/news/domashnii-albom-fotovystavka-o-semiakh-s-osobennymi-detmi-otkrylas-v-irkutske/', '', '', 1, '', '0'),
(399, '19', 'Апрельские новости: супервизия и инклюзия', 'На прошлой неделе к нам из Новосибирска приезжала супервизор нашего центра и ресурсного класса - Наталия Александровна Мальтинская – сертифицированный поведенческий аналитик уровня ВСВА. ', '<p>В апреле к нам приезжала Наталия Александровна Мальтинская, сертифицированный поведенческий аналитик, супервизор нашего центра и ресурсного класса. Супервизия напоминает курсы повышения квалификации для специалистов. Наталия Александровна внимательно изучила программы наших учеников, посетила занятия и дала ценные советы сотрудникам центра и педагогам ресурсного класса. В ходе занятий она также консультировала педагогов и инструкторов, помогая корректировать подходы к обучению.</p>\r\n<p>Особенно полезной была работа в ресурсном классе, где специалисты смогли обсудить методы формирования навыков коммуникации учеников ресурсного класса с другими школьниками и педагогами. Это важно для успешной интеграции детей с особыми потребностями в обычную школьную среду. </p>\r\n<p>В рамках подготовки к инклюзии в посёлке Молодёжный были проведены «уроки добра», направленные на повышение осведомлённости школьников о детях с аутизмом. Благодаря таким урокам ребята начали лучше понимать особенности поведения сверстников с расстройствами аутистического спектра и стали проявлять интерес к дружбе с ними.</p>\r\n<p>Итогом работы ресурсного класса стало важное событие — один ученик впервые вышел на урок в обычный класс. Мы гордимся успехами Влада и верим, что впереди его ждут отличные оценки и новые друзья! </p>\r\n', '/images/RK/vlad.jpg', '2021-04-19', '/images/training/04.2021/1.jpg;\r\n/images/training/04.2021/2.jpg;\r\n/images/training/04.2021/3.jpg;\r\n/images/training/04.2021/4.jpg;\r\n/images/training/04.2021/5.jpg;\r\n/images/training/04.2021/6.jpg;\r\n/images/training/04.2021/7.jpg;\r\n/images/training/04.2021/8.jpg', '', '', '', '', 0, '', '0'),
(400, '18', 'Творчество без границ', 'Прошедшие недели были наполнены творчеством и радостью. Ребята мастерски работали с тканями, создавая уникальные украшения для будущих эко-сумок, оформляли деревянные шкатулки,  создавали подставки для чашек и небольшие заварники. Завершающим занятием стала роспись камней. ', '<p>Прошедшие недели были наполнены творчеством и радостью. Ребята мастерски работали с тканями, создавая уникальные украшения для будущих эко-сумок. Каждый рисунок стал особенным, а благодаря фиксации изображений на ткани, сумки теперь готовы к любым погодным условиям — будь то дождь или снег. Внутренние карманы для мобильных телефонов получились удобными и практичными, а индивидуальные надписи от каждого участника мастерской добавили им уникальности. Даже маленькие сладости теперь найдут своё уютное местечко в этих карманах!</p>\r\n<p>Следующим этапом стало оформление деревянных шкатулок. Морилка подарила древесине потрясающие оттенки, подчеркнув её фактуру и добавив глубину цвету. Работа была поделена на два этапа, чтобы каждый мог внести свою лепту в создание уникальных предметов искусства. Декупажирование шкатулок и установка фурнитуры превратили обычные коробки в настоящие произведения искусства. Один из учеников настолько впечатлил своего учителя, что созданная им шкатулка была сразу же продана счастливому покупателю.</p>\r\n<p>Бусины из дерева также оказались прекрасным материалом для творчества. Из них создавались подставки для чашек и небольшие заварники. Благодаря разнообразию схем, доступных в интернете, ребята смогли воплотить свои идеи в жизнь, наслаждаясь процессом и радуясь результатам своей работы.</p>\r\n<p>Акриловые краски стали настоящим открытием для наших учеников. Эти универсальные материалы позволяют наносить рисунки на любые поверхности, превращая их в водостойкую плёнку после высыхания. Педагоги отметили, что дети выбирают сюжеты и абстрактные композиции самостоятельно, ориентируясь на свои предпочтения и настроение.</p>\r\n<p>Дотсы, миниатюрные палочки с металлическими шариками на концах, позволили ребятам создать точечные узоры на обложках ежедневников. Трафареты помогли добиться точности и красоты рисунков.\r\n</p>\r\n<p>Завершающим занятием стала роспись камней. Эта простая, но требующая внимания и вдохновения работа помогла детям развить навыки концентрации и расслабления. Монотонность действий положительно влияет на нервную систему, что делает такие занятия полезными не только с творческой точки зрения, но и с психологической.\r\n</p>\r\n<p>Проект завершился, но впереди нас ждёт ещё больше интересных идей и творческих начинаний. Анализируя успехи и ошибки, мы планируем новые занятия, выбираем материалы и готовимся к новым свершениям. Впереди нас ждут увлекательные приключения в мире творчества, где границы возможностей стираются, уступая место фантазии и вдохновению.</p>\r\n\r\n<p>Проект реализуется победителем конкурса \"Школа филантропии\" благотворительной программы \"Эффективная филантропия\" Благотворительного фонда Владимира Потанина.</p>\r\n', '/images/DSS/21.04.11/2.jpg', '2021-04-21', '/images/DSS/21.04.11/1.jpg;\r\n/images/DSS/21.04.11/2.jpg;\r\n/images/DSS/21.04.11/3.jpg;\r\n/images/DSS/21.04.11/4.jpg;\r\n/images/DSS/21.04.11/5.jpg;\r\n/images/DSS/21.04.11/6.jpg;\r\n/images/DSS/21.04.11/7.jpg;\r\n/images/DSS/21.04.11/8.jpg;\r\n/images/DSS/21.04.11/9.jpg;\r\n/images/DSS/21.04.11/10.jpg', '', '', '', '', 0, '', '0'),
(401, '22', 'Благодарим за содействие в организации и проведении фотовыставки ', 'Благодарим за содействие в организации и проведении фотовыставки <a href=\"https://rassvet-apc.ru/events/detail.php?id=395\" target=\"_blank\">«Аутизм. Семейный альбом»</a>, приуроченной к Всемирному дню информирования о проблеме аутизма: фотографа Лидию Аркадьевну Лось, администрацию и сотрудников ТВЦ «Галерея Revoлюция» и всех, кто поучаствовал в открытии выставки и организации мероприятий в рамках проекта!', '<p>Благодарим за содействие в организации и проведении фотовыставки <a href=\"https://rassvet-apc.ru/events/detail.php?id=395\" target=\"_blank\">«Аутизм. Семейный альбом»</a>, приуроченной к Всемирному дню информирования о проблеме аутизма: </p>\r\n<p>Фотографа Лидию Аркадьевну Лось</p>\r\n<p>Администрацию и сотрудников ТВЦ «Галерея Revoлюция»</p>\r\n<p>Организатора выставки и волонтера АНО «АПЦ РАСсвет» Евгению Гладышеву</p>\r\n<p>Руководителя компании BaikalStageGroup Юрия Валерьевича Копняева - за предоставление музыкального оборудования</p>\r\n<p>За выступление - АНО «Центр современного танца \"PROДвижение\" и его генерального директора Владимира Николаевича Лопаева </p>\r\n<p>За музыкальное выступление - Марию Корневу, Евгения Афанасьева, Константина Токарского</p>\r\n<p>Ведущего церемонии открытия Аркадия Майляна</p>\r\n<p>За фуршет - сыроварню “Папа Карло”, и ее руководителей Александра Александровича Богданова и Андрея Николаевича Морского, и ремесленную пекарню “Катин хлеб” и ее директора Екатерину Федоровну Демину</p>\r\n<p>За цветы на открытии - ООО “Левада” и ее директора Светлану Владимировну Люткину, «Цветочный BAZAAR», индивидуального предпринимателя Инну Алексеевну Боровую</p>\r\n<p>За сладкие подарки Людмилу Белинскую, Кристину Степановну Прокудину и Дарью Викторовну Киршеву</p>\r\n<p>За кофейные подарки – Кофейное пространство «Доза кофе» (Dose Coffee) и лично Алину Олеговну Фирсову  </p>\r\n<p>За печать баннера ООО «Рекламное Агентство «МИР», и его директора Владимира Геннадьевича Маняхина </p>\r\n<p>За воздушные шары - Надежду Андреевну Колосову </p>\r\n<p>За проведение бесплатных мастер-классов - Екатерину Сергеевну Тицкую, Татьяну Сергеевну Матвееву, Гульфину Файзухаковну Ремову</p>\r\n<p>За организацию онлайн-встречи с писателем Марией Дубовой, и обзор книг на тему аутизма - сотрудников и директора магазина \"Кукуля\" Марию Андреевну Бызову</p> \r\n<p>За помощь в проведении мероприятий - волонтеров из студенческого педагогического отряда “Альтаир” им. В.П. Правика, Иркутского регионального Волонтерского центра. </p>\r\n<p>Большое спасибо Вам всем за понимание и доброту! Вы делаете мир лучше!</p>\r\n', '/images/family_album/02.04.2021/1 (2).jpg', '2021-04-14', '', '', '', 'Новости', 'news.php', 0, '', '0'),
(404, '22', 'Благодарим коллектив ЕЭИСЦ \"Иркутской Энергосбытовой компании\" ', 'Сердечно благодарим коллектив ЕЭИСЦ \"Иркутской Энергосбытовой компании\" за оказанную помощь нашим ребятам в подготовке к учебному году, выражаем Вам свое уважение и признательность за неравнодушие и доброту.', '<p>Сердечно благодарим коллектив ЕЭИСЦ \"Иркутской Энергосбытовой компании\" за оказанную помощь нашим ребятам в подготовке к учебному году, выражаем Вам свое уважение и признательность за неравнодушие и доброту.</p>\r\n', '/images/gratitude/foto/1.jpg\r\n', '2020-09-06', '/images/gratitude/53.jpg', '', '', 'Новости', 'news.php', 0, '', '0'),
(407, '16', 'Научно-практическая конференция «Проблемы диагностики, коррекции, реабилитации детей с РАС»', '22 апреля в Иркутске прошла научно-практическая конференция «Проблемы диагностики, коррекции, реабилитации детей с РАС». Организовали мероприятие аппарат Уполномоченного по правам ребенка в Иркутской области и Иркутский государственный медицинский университет. ', '<p>22 апреля в Иркутске прошла научно-практическая конференция «Проблемы диагностики, коррекции, реабилитации детей с РАС». Организовали мероприятие аппарат Уполномоченного по правам ребенка в Иркутской области и Иркутский государственный медицинский университет.</p>\r\n<p>Конференция прошла в очно-дистанционном формате, для удобства слушателей: родителей детей с аутизмом, участковых педиатров, психиатров, педагогов и воспитателей образовательных учреждений, представителей общественных организаций, ПМПК, МСЭ, учреждений социальной защиты. </p>\r\n<p>С докладами выступили эксперты медицинской и педагогической сферы: обсудили основные вопросы по комплексному подходу в диагностике и коррекции РАС в Иркутской области, необходимость ранней помощи детям с аутизмом, их реабилитацию и организацию образовательного процесса. Об опыте ресурсного класса и о роли прикладного анализа поведения рассказала Елена Михеева, учитель ресурсного класса СОШ п. Молодежный, член правления АНО «АПЦ РАСсвет». Председатель АНО «АПЦ РАСсвет» Екатерина Родыгина выступила с докладом «РАС: актуальные сведения о диагностике и реабилитации, способы их внедрения». </p>\r\n<p>На конференции были проговорены не только основные проблемы, связанные с аутизмом, но и выявилось компетентность специалистов и их взгляды на обсуждаемые вопросы. Если вы не присутствовали на мероприятии, есть возможность прослушать его запись: в ближайшее время на сайте Уполномоченного будут размещены видеозапись конференции и презентационные материалы спикеров для общего пользования.</p>\r\n<p><a href=\"https://irdeti.ru/press-center/news/4530:konferentsiya-problema-diagnostiki-korrektsii-i-reabilitatsii-detej-s-ras&catid=160\" target=\"_blank\">Ссылка на материалы конференции</a></p>', '/images/news/22.04.21/3.jpg', '2021-04-23', '/images/news/22.04.21/1.jpg;\r\n/images/news/22.04.21/2.jpg;\r\n/images/news/22.04.21/3.jpg;\r\n/images/news/22.04.21/4.jpg;\r\n/images/news/22.04.21/5.jpg', '', '', 'Новости', 'news.php', 0, '', '0'),
(409, '12', 'Мероприятия проекта \"Движение вверх!\"', 'Дети с аутизмом – маленькие моторчики 24/7 и 365 дней в году. Чтобы направить их неиссякаемую энергию в нужное русло в апреле в «РАСсвете» стартовал новый проект по адаптивной физкультуре «Движение вверх!» при поддержке Благотворительного фонда Сбербанка \"Вклад в будущее\". ', '<p>Дети с аутизмом – маленькие моторчики 24/7 и 365 дней в году. Чтобы направить их неиссякаемую энергию в нужное русло в апреле в «РАСсвете» стартовал новый проект по адаптивной физкультуре «Движение вверх!» при поддержке Благотворительного фонда Сбербанка \"Вклад в будущее\". \r\n</p>\r\n<p>Позади тестирования участников проекта, составление индивидуальных программ обучения по итогам тестирования. 20 мальчишек и девчонок вместе с нашими тренерами Викторией Кузнецовой и Дарьей Бабкиной активно занимаются спортом с элементами лечебной гимнастики для позвоночника и стоп.</p>\r\n<p>Благодаря поддержке фонда \"Вклад будущее\" в спортивном зале «РАСсвета» яркие и многофункциональные обновки: будо-маты для покрытия пола, шведская стенка с кольцами и канатом, фитболы, мягкие модули – конструктор, диски для скольжения, платформа-балансир БОСУ, йога-гамак и скейтборд – всё это послужит для развития детей с аутизмом и укрепления их здоровья.</p>\r\n<p>Тренировки с детьми с аутизмом продолжались всё лето. Ребята выполняют простые и действенные упражнения. Все они подобраны в соответствии с актуальными потребностями каждого ребенка.</p>\r\n<p>Например, первокласснице Соне нужны упражнения, влияющие на развитие когнитивных способностей, поэтому её план тренировок включает ползание под дугами. Движения по диагонали при ползании, когда вперед идет противоположная нога и рука, активно развивают способности к письму, умственные способности, а также способность рассуждать логически.</p>\r\n<p>Ваня выполняет позу планки на прямых руках, лежа на большом мяче . Это снижает нагрузку на ось тела в горизонтальном положении, такой вариант упражнения подходит для детей с избыточной массой тела. Кроме того, из такого положения можно обучать любого ребёнка отжиманию на руках. В перспективе от помощи мяча можно отказаться. Правильная техника этого упражнения подразумевает развитие умения осознанно напрягать пресс, поддерживая при этом нормальный ритм дыхания. Результатом регулярного выполнения планки является укрепление внутренних мышц корпуса и улучшение осанки.</p>\r\n<p>Проект решает несколько задач за раз: социальная адаптация, работа в группе, развитие физической формы и мышления. В спортивном зале «РАСсвета» проходят специальные занятия в минигруппах для социальной адаптации и развития физического потенциала детей с аутизмом. Во время индивидуальных уроков к выполнению заданий постепенно подключаются нейротипичные сверстники, сначала на 15 минут, а затем и на весь урок – 40 минут. Проводить занятие помогают волонтеры, которые выполняют для наших воспитанников роль коммуникативных помощников.</p>\r\n<p>- Мы считаем, и готовы подтвердить это на практике, что дети с аутизмом гораздо быстрее усваивают навыки социального поведения, копируя действия именно своих ровесников. Обучая, например, игре с мячом в спортивном состязании, мы сможем подготовить ребёнка к игре с нейротипичными ровесниками на обычной игровой площадке или на общем спортивном празднике в детском саду и школе, в спортивной секции. Кроме того, положительный эффект от общения с детьми с РАС наблюдается и у детей с нормой развития - общество перестаёт бояться детей с ОВЗ. И это даёт дополнительные шансы для установления доброжелательных взаимоотношений таких разных детей, - рассказывает Виктория Кузнецова, инструктор АФК \"АПЦ РАСсвет\".</p>\r\n<p>В рамках проекта «Движение вверх!» 20 детей с РАС проходят регулярные занятия с психологом для коррекции нежелательного поведения, возникающего на занятиях по физкультуре.</p>\r\n<p>Кабинет психолога в «РАСсвете» напоминает магазин игрушек, но у каждой из них есть назначение, а иногда и не одно. Например, магнитные лабиринты с узорами разной сложности не только развивают графомоторику, но и запускают работу межполушарных связей. </p>\r\n<p>- Когда дети выполняют упражнения развития межполушарного взаимодействия, они улучшают свои творческие способности, в мозге вырабатываются новые нейронные связи, нервная система становится более стабильной. Эффект от таких занятий виден сразу – дети успокаиваются и расслабляются, - делится опытом Анна Леонтьева, психолог «РАСсвета».</p>\r\n<p>Многие сенсорные игры после занятий в кабинете психолога переходят в разряд поощрений для детей с РАС. Массажные мячики с шипами, тактильные кубики, пересыпание крупы и др. вещи ребенок просит после окончания занятия или в перерыве. Просьба – это уже коммуникация. То есть, сообщая педагогу о своем желании поиграть с любимой игрушкой, ребенок развивает свои коммуникативные навыки. А это уже положительно влияет на коррекцию нежелательного поведения.</p>\r\n<p>В рамках проекта \"Движение вверх\" прошли и инклюзивные летние эстафеты, которые стали доброй традицией центра. Соревнования проходили в солнечный погожий денёк на детской площадке, оборудованной тренажёрами для общего пользования. Свежий воздух, хорошая погода и прохлада от реки (тренер АФК тщательно выбирал место) стали дополнительными стимулами для ребят. Также пригодилось оборудование с помощью которого дети, участники проекта \"Движение вверх\", на занятиях АФК осваивали специальные упражнения: эстафетные дорожки с препятствиями.</p>\r\n<p>Кроме помощи тьюторов, юные спортсмены получили визуальную поддержку: для удобства движения по линии эстафеты ключевые точки были визуализированы карточками с цифрами. В конце весёлого соревнования взрослые и дети тянули канат. Все получили море удовольствия и заряд бодрости.</p>\r\n<p>В сентябре на конференции \"Мир добрыми глазами\", организованной ИОООРДОВ \"Радуга\" и ИРО МОО \"Равные возможности\", инструктор по адаптивной физкультуре и спикер центра \"РАСсвет\" Дарья Бабкина поделилась опытом проведения спортивных занятий для детей с аутизмом. Она подробно рассказала о применении методик прикладного анализа поведения (ABA), включая использование визуальных средств поддержки, которые помогают детям лучше усваивать учебные материалы и успешно развивать физические навыки, зрительное восприятие, математические способности и даже речь.</p>\r\n<p></p>\r\n<p>Пожелаем успехов всем участникам «Движения вверх!»</p>\r\n<p>Проект осуществляется при поддержке Благотворительный фонд Сбербанк \"Вклад в будущее\". \r\n</p>\r\n\r\n', '/images/AFK/2021/1.jpg', '2021-08-13', '/images/AFK/2021/8.jpg;\r\n/images/AFK/2021/2.jpg;\r\n/images/AFK/2021/3.jpg;\r\n/images/AFK/2021/4.jpg;\r\n/images/AFK/2021/5.jpg;\r\n/images/AFK/2021/6.jpg;\r\n/images/AFK/2021/7.jpg;\r\n/images/AFK/2021/13.jpg', '', '', '', '', 0, '', '0'),
(410, '3', 'Новые горизонты общения: система PECS в АНО \'АПЦ РАСсвет', 'Новые учебные пособия для визуальной поддержки прибыли этой зимой в АНО \"АПЦ РАСсвет\". В том числе пособия для осуществления общения с помощью карточек. А в апреле в центре прошёл практический тренинг по системе альтернативной коммуникации PECS. ', '<p>Новые учебные пособия для визуальной поддержки прибыли этой зимой в АНО \"АПЦ РАСсвет\". В том числе пособия для осуществления общения с помощью карточек. Материалы приобретены в рамках проекта \"Скорая помощь для детей с аутизмом и их семей\", он реализуется при поддержке Фонда президентских грантов. </p>\r\n<p>Особое внимание стоит уделить новой технологии, которая пришла в наш центр — планшету Apple iPad с установленным приложением PECS 4+. Это обновление позволит детям и подросткам, использующим более 100 карточек для общения, значительно улучшить взаимодействие. Важно отметить, что планшет предназначен исключительно для системы альтернативной коммуникации, а для игр следует использовать другие устройства.</p>\r\n<p>У большинства детей с аутизмом хорошо развито визуальное восприятие, ориентируясь на него педагоги и родители могут выстраивать общение с ребенком. PECS – это английская аббревиатура, она переводится, как «коммуникационная система обмена изображениями». Это альтернативный способ общения, в котором вместо слов люди передают друг другу карточки с картинками. PECS вводят в обучение детей, у которых есть трудности с вокальной речью.</p>\r\n<p>В апреле в центре прошел практический тренинг по системе альтернативной коммуникации PECS, который провела Елена Михеева, поведенческий аналитик с дипломом международного экзамена \"The Picture Exchange Communication System\". Елена поделилась своим опытом и знаниями с родителями детей с аутизмом и специалистами, работающими с ними.\r\n</p>\r\n<p>Участники тренинга изучили шесть основных этапов освоения PECS, узнали, какие карточки вводить в первую очередь, и разобрали особенности бумажного и электронного вариантов альбома с карточками. Теоретические знания были закреплены практическими занятиями: сначала участники тренировались друг с другом, а затем работали с воспитанником \"РАСсвета\" Игорем.</p>\r\n<p>Мы выражаем благодарность всем, кто принял участие в нашем тренинге! Надеемся, что за эти 4 часа вы смогли прокачать свои знания. Если у вас возникнут вопросы в процессе работы с ребенком, не стесняйтесь писать — мы постараемся помочь. Следите за нашими анонсами, впереди много интересного и полезного!</p>\r\n\r\n', '/images/training/24.04.2021/1.jpg', '2021-04-26', '/images/training/24.04.2021/2.jpg;\r\n/images/training/24.04.2021/3.jpg;\r\n/images/training/24.04.2021/4.jpg;\r\n/images/training/24.04.2021/5.jpg', '', '', '', '', 0, '', '13');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(412, '4', 'Экскурсия в «Альтернативную стоматологию»', 'На этой неделе в рамках проекта «Скорая помощь» трое ребят посетили «Альтернативную стоматологию» для обобщения навыка посещения зубного врача. Осмотрелись, по очереди посидели в стоматологическом кресле, с помощью жетонов дали осмотреть себя сначала сотрудникам «РАСсвета», а затем и врачу.', '<p>Знаете, каких врачей больше всего боятся люди? Стоматологов! В различных российских и зарубежных опросах на эту тему вот уже несколько лет лидируют зубные врачи. Причем, сильный страх оказаться в стоматологическом кресле взрослые приобретают еще в детстве. Есть даже такое название: дентофобия – боязнь стоматологического лечения. Доводить дело до фобии, конечно, не стоит. В «РАСсвете» научились справляться с детскими страхами врачебных манипуляций. </p>\r\n<p>На этой неделе в рамках проекта «Скорая помощь» трое ребят посетили «Альтернативную стоматологию» для обобщения навыка посещения зубного врача. Осмотрелись, по очереди посидели в стоматологическом кресле, с помощью жетонов дали осмотреть себя сначала сотрудникам «РАСсвета», а затем и врачу. У одного нашего мальчика страх все еще очень большой, и осмотр его зубов занял всего пару секунд, но то, что он позволил незнакомому врачу заглянуть в свой рот уже большой успех. Есть над чем работать дальше!</p>\r\n<p>Доктор была доброжелательна и приветлива с ребятами, в конце посещения она вручила каждому ребенку подарки: машинки и набор для рисования фрески из песка. Дети поняли, что поход к стоматологу не обязательно связан со страхом и болью, что обстановка врачебного кабинета может быть комфортной, а после успешного осмотра их поощрят подарком. </p>\r\n<p>Спасибо сотрудникам «Альтернативной стоматологии» и лично Надежду Фёдоровну Скоробогач за теплый прием и помощь с десенсибилизацией детей с РАС. </p>\r\n<p>Проект «Скорая помощь» реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<section class=\"video-detail\"><iframe src=\"https://rutube.ru/play/embed/16d468160d7e2878c49e8af13bacc856/\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen class=\"social-video\"></iframe>\r\n</section>\r\n\r\n', '/images/SP/9/3.jpg', '2021-05-05', '/images/SP/9/1.jpg;\r\n/images/SP/9/2.jpg;\r\n/images/SP/9/3.jpg;\r\n/images/SP/9/4.jpg;\r\n/images/SP/9/5.jpg', '', '', '', '', 0, '', '13'),
(413, '16', 'Познакомимся поближе: Дарья Бабкина', 'Дорогие друзья, продолжаем знакомить вас с сотрудниками центра «РАСсвет». Сегодня о себе расскажет наша длинноногая \"фея\", мотиватор для детей \"number one\" и фитнес-инструктор занятий по адаптивной физкультуре Дарья Бабкина. ', '<p>Дорогие друзья, продолжаем знакомить вас с сотрудниками центра «РАСсвет». Сегодня о себе расскажет наша длинноногая \"фея\", мотиватор для детей \"number one\" и фитнес-инструктор занятий по адаптивной физкультуре Дарья Бабкина. </p>\r\n<p>- Я давно работаю в сфере реабилитации. У меня было много пациентов с различными заболеваниями: от инсульта до ДЦП, помогая им, я не раз слышала о детках с аутизмом, и всегда хотела попробовать с ними поработать. Потом вдруг увидела объявление от «АПЦ РАСсвет»: «требуется помощник», пошла, увидела, как работают специалисты, понравилось, прошла обучение на дистанционных курсах по АВА-терапии, поняла, в чем необычность детей с РАС, и что хочу продолжать узнавать их и помогать им развиваться.</p>\r\n<p>- Самое тяжелое в моей работе – вставать в 5 утра, чтобы добраться до неё – я живу далеко о центра. </p>\r\n<p>- В работе интересно все: от первого знакомства с ребёнком, до его результатов на занятиях АФК. Когда видишь, что есть прогресс, хочется работать дальше, и я понимаю, что ребёнок может, не всегда хочет, но может выполнять задачи, которые перед ним поставили.</p>\r\n<p>- В зависимости от физических особенностей ребенка, я встраиваю в его занятия АФК специальные упражнения. Например, если у ребенка плоскостопие, сколиоз, я приучаю его к лечебному массажу.\r\n</p>\r\n<p>- Про досуг… Очень люблю рисовать: рисую на одежде, картины. А ещё я крутая тётка и люблю проводить время с племянниками.</p>\r\n<p>- Если говорить о банальной мечте, то хочу большой дом. А так очень мечтаю открыть центр реабилитации. А еще, поехать во Францию, на свой день рождения… проснувшись, позавтракать капучино с круассаном, глянуть на Эйфелеву башню… а потом можно ехать домой!\r\n</p>\r\n<p>-Даша на все руки мастер. Она и массаж сделает, и после операции восстановит, и к нашим детям подход найдёт,- рассказывает Екатерина Родыгина, руководитель \"АПЦ РАСсвет\". </p>\r\n\r\n', '/images/team/shulga/foto.jpg', '2021-05-12', '', '', '', 'Команда', '/team.php', 0, '', '0'),
(414, '13', '«Сдайте его в интернат, родите себе другого». Как живут и учатся дети с аутизмом в Иркутске', 'Автор: Екатерина Балагурова <br>\r\nИсточник: \"Верблюд в огне\" <a class=\"a-blue\" href=\"https://verbludvogne.ru/articles/autism/\" target=\"_blank\">(verbludvogne.ru)</a><br>\r\nБольшая статья о жизни иркутских семей, воспитывающих детей с РАС, о создании и работе первого ресурсного класса. Спасибо за внимание к теме аутизма, за грамотное освещение событий.', '', '/images/smi/19.jpg', '2021-05-13', '', '', 'https://verbludvogne.ru/articles/autism/', '', '', 1, '', '0'),
(417, '13', 'Особенный класс для особых ребят', 'Автор: Татьяна ПОДКУЙЧЕНКО <br>\r\nИсточник: \"Ангарские огни\" <a class=\"a-blue\" href=\"https://angarogni.ru/?p=11972\" target=\"_blank\">(angarogni.ru)</a><br>\r\nНовая статья в газете \"Ангарске огни\" посвящёна будням учеников с аутизмом и позитивному и энергичному настрою их педагогов. Переходите по ссылке чтобы прочитать её.\r\n', '', '/images/smi/20.jpg', '2021-05-24', '', '', 'https://angarogni.ru/?p=11972', '', '', 1, '', '0'),
(419, '19', 'Конец первого учебного года', 'Учебный год в первом в области ресурсном классе подходит к концу. \r\nЧтобы оценить результаты нашей работы, проводим тестирование по итогам года, оцениваем навыки каждого ученика, делаем выводы об освоенных целях и намечаем цели на следующий год.', '<p>Учебный год в первом в области ресурсном классе подходит к концу. А вместе с ним и проект \"Инклюзивная адаптация\", в котором \"АПЦ Рассвет\" принимал участие. Мы благодарим организаторов и партнеров проекта: Фонд «Выход». Аутизм в России, Ассоциация \"Аутизм Регионы\". Благодаря Вашей поддержке первый год в первом ресурсном классе Иркутской области прошёл организованно и слаженно.</p>\r\n<p>Чтобы оценить результаты нашей работы, провели итоговое тестирование, оцениваем навыки каждого ученика, делаем выводы об освоенных целях и намечаем цели на следующий год.  Очень радует, что наши ребята хорошо продвинулись не только в вербальных и академических навыках, но и в навыках сотрудничества, социального взаимодействия и навыках работы в группе. А ведь это и есть основная цель нашей работы! Конечно, были сложности и трудные задачи, с которыми нам помогала справиться Наталия Мальтинская - наш супервизор и консультант.</p>\r\n<p>Сегодня наши первоклашки получили свои первые учебные грамоты и отправились на каникулы. Желаем им прекрасно отдохнуть и набраться сил. Увидимся в августе, на летней школьной площадке.</p>\r\n<p>АНО «АПЦ РАСсвет» участвует в проекте «Инклюзивная адаптация».</p>\r\n<p>Благодарим организаторов и партнеров проекта:\r\nФонд «Выход». Аутизм в России, \r\nАссоциация \"Аутизм Регионы\".</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/RK/21.05.28/6.jpg', '2021-05-28', '/images/RK/21.05.28/1.jpg;\r\n/images/RK/21.05.28/2.jpg;\r\n/images/RK/21.05.28/3.jpg;\r\n/images/RK/21.05.28/4.jpg;\r\n/images/RK/21.05.28/5.jpg;\r\n/images/RK/21.05.28/7.jpg;\r\n/images/RK/21.05.28/8.jpg;\r\n/images/RK/21.05.28/9.jpg', '', '', 'Новости', 'news.php', 0, '', '0'),
(421, '2', 'Верховая езда в КСК \"Битва\"', 'В «РАСсвете» появились всадники: по воскресеньям воспитанники центра осваивают верховую езду с помощью конноспортивного клуба «Битва» АНО «Победный Аллюр». ', '<p>В «РАСсвете» появились всадники: по воскресеньям воспитанники центра осваивают верховую езду с помощью конноспортивного клуба «Битва» АНО «Победный Аллюр». На первых порах все к друг другу привыкают: лошади к седокам, дети – к лошадям, инструкторы к особенностям РАС у ребят. В дальнейшем занятия перерастут в большой и важный проект по реабилитации детей с аутизмом посредством адаптивной верховой езды. Мы рады участвовать в таком хорошем деле. </p> \r\n<p>- Год назад еще появилась идея работать с детьми с ментальными особенностями. Мы выиграли грант на проект «Доступная иппотерапия». Сейчас нарабатываем опыт, хотим быть полезными, - комментирует руководитель КСК «Битва» Иван Мишарин.</p> \r\n\r\n', '/images/dosug/2021/31.05/5.jpg', '2021-05-31', '/images/dosug/2021/31.05/1.jpg;\r\n/images/dosug/2021/31.05/2.jpg;\r\n/images/dosug/2021/31.05/3.jpg;\r\n/images/dosug/2021/31.05/4.jpg;\r\n/images/dosug/2021/31.05/5.jpg;\r\n/images/dosug/2021/31.05/6.jpg;\r\n/images/dosug/2021/31.05/7.jpg;\r\n/images/dosug/2021/31.05/8.jpg;\r\n/images/dosug/2021/31.05/9.jpg;\r\n/images/dosug/2021/31.05/10.jpg;\r\n/images/dosug/2021/31.05/11.jpg', '', '', '', '', 0, '', '0'),
(423, '2', 'Новая встреча с Иркутским зоосадом', 'В пятницу 4 июня ребята «РАСсвета» вместе со своими родителями посетили Иркутский зоосад в рамках проекта «Расширяя границы». Дети с удовольствием провели время на открытом воздухе: посмотрели на животных и покормили их, поиграли в песочнице и на маленьком сеновале, погладили козленка, бегающего по дорожкам зоопарка, пообщались друг с другом.', '<p>В пятницу 4 июня ребята «РАСсвета» вместе со своими родителями посетили Иркутский зоосад в рамках проекта «Расширяя границы». Погода, наконец, установилась солнечная и жаркая, так что дети с удовольствием провели время на открытом воздухе: посмотрели на животных и покормили их, поиграли в песочнице и на маленьком сеновале, погладили козленка, бегающего по дорожкам зоопарка, пообщались друг с другом. Приятно было даже просто перекинуться приветами с теми, кого давно не видели и с кем расстались недавно из-за начала каникул – всего в выезде поучаствовало 55 человек.</p> \r\n<p>В Иркутский зоосад воспитанники «РАСсвета» приезжают каждый год – спасибо его администрации за гостеприимство и бесплатные билеты. И каждый раз мы видим классные изменения в обстановке – качели, скульптурные композиции из дерева, гамак, беседка со столом и меловой доской…В этот раз дети и взрослые оценили чайный домик – уютное место с красивым интерьером, где можно отдохнуть от солнцепека или спрятаться от непогоды, перекусить и поболтать. </p>\r\n<p>От первой совместной летней прогулки все получили массу удовольствия, сенсорно насытились и зарядились хорошим настроением!</p>\r\n\r\n\r\n', '/images/dosug/2021/05.06/7.jpg', '2021-06-05', '/images/dosug/2021/05.06/1.jpg;\r\n/images/dosug/2021/05.06/2.jpg;\r\n/images/dosug/2021/05.06/3.jpg;\r\n/images/dosug/2021/05.06/4.jpg;\r\n/images/dosug/2021/05.06/5.jpg;\r\n/images/dosug/2021/05.06/6.jpg;\r\n/images/dosug/2021/05.06/7.jpg;\r\n/images/dosug/2021/05.06/8.jpg;\r\n/images/dosug/2021/05.06/9.jpg;\r\n/images/dosug/2021/05.06/10.jpg;\r\n/images/dosug/2021/05.06/11.jpg;\r\n/images/dosug/2021/05.06/12.jpg;\r\n/images/dosug/2021/05.06/13.jpg;\r\n/images/dosug/2021/05.06/14.jpg;\r\n/images/dosug/2021/05.06/15.jpg;\r\n/images/dosug/2021/05.06/16.jpg;\r\n/images/dosug/2021/05.06/17.jpg;\r\n/images/dosug/2021/05.06/18.jpg;\r\n/images/dosug/2021/05.06/19.jpg', '', '', '', '', 0, '', ''),
(424, '22', 'Благодарим за организацию бесплатного посещения Иркутского зоосада', 'Благодарим директора Иркутского зоосада Сизых Ивана Александровича за организацию бесплатного посещения зоопарка для детей с расстройствами аутистического спектра.', '<p>Благодарим директора Иркутского зоосада Сизых Ивана Александровича за организацию бесплатного посещения зоопарка для детей с расстройствами аутистического спектра.</p> \r\n<p>Спасибо за понимание, неравнодушное отношение к детям с особенностями развития, за чуткость и способность сопереживать, умение и желание поддержать тех, кто в этом нуждается.</p>\r\n<p>Желаем Вам крепкого здоровья, благополучия и успехов!</p>\r\n\r\n\r\n', '/images/dosug/2021/05.06/4.jpg', '2021-06-05', '/images/gratitude/56.jpg', '', '', '', '', 0, '', '0'),
(425, '20', 'Безопасность на прогулке', 'Лето – пора длинных прогулок и активного отдыха на природе. Но даже во время отдыха родителям детей с аутизмом нельзя ослаблять контроль: приходится постоянно следить, чтобы ребенок не выбегал на проезжую часть и не убегал в принципе.', '<p>Лето – пора длинных прогулок и активного отдыха на природе. Но даже во время отдыха родителям детей с аутизмом нельзя ослаблять контроль: приходится постоянно следить, чтобы ребенок не выбегал на проезжую часть и не убегал в принципе, ведь недостаточно объяснить ему правила поведения на улице, а иногда это просто бесполезно. В России ежегодно пропадает более 30 тысяч детей – это статистика поисково-спасательного отряда «Лиза Алерт». Потерять ребенка очень страшно, и, к сожалению, очень легко. Чтобы этого не произошло, подготовьтесь заранее к выходам на улицу.</p>\r\n<p>В качестве предупредительной меры можно подготовить социальные истории о том, как себя нужно вести на улице, в магазине, парке или на природе. Показывать их ребенку каждый день и проговаривать его действия в случае, если он потеряется. А также проигрывать подобные ситуации и смотреть мультфильмы на эту тему.\r\n</p>\r\n<p>Если ребёнок мало или совсем не говорит, плохо понимает обращенную речь, следует нашить на его одежду нестираемые бирки с телефоном родителя, лучше всего – сразу на несколько предметов. Такие бирочки делает в Иркутске фирма <a href=\"https://www.instagram.com/lent_tochka/\" target=\"_blank\">@lent_tochka</a>. Она сотрудничает с «АПЦ РАСсвет» и безвозмездно помогает нашим родителям.</p>\r\n<p>Альтернатива биркам - браслеты-идентификаторы с контактной информацией родителей ребенка. Браслеты есть с застежкой, виниловые, силиконовые, светоотражающие, водонепроницаемые, с гравировкой или надписью обычной шариковой ручкой. Их можно заказать на Wildberries, AliExpress или других сайтах. Вместо браслета можно сделать кулон на шею – если у ребенка повышенная чувствительность на руках.</p>\r\n<p>Более дорогой вариант - специальные GPS-устройства, которые отслеживают местонахождение ребенка и передают информацию на телефоны родителей. Есть компактные радиомаяки, их можно прикреплять к самокату или велосипеду ребенка, на его обувь или рюкзак. Есть часы и браслеты с GPS трекером. Продаются такие устройства в магазинах электроники.</p>\r\n<p>Более простой выход, но для детей помладше – поводок на запястье или на пояс – один браслет одевается на руку взрослого, другой - на руку или пояс ребенка, между ними расположен ремень в виде пружины – от 1,5 до 2,5 метров длиной. Может быть выглядит это не очень эстетично, зато работает наверняка. Детские ручные ремни также можно заказать в интернете.\r\n</p>\r\n<p>И ¸конечно, необходимо заранее обдумать действия взрослых при потере ребенка: сделать листовки с описанием его внешности и проблем, фотографировать его перед каждой прогулкой, чтобы показывать свежий снимок прохожим и полиции, иметь телефон волонтерской организации, занимающейся поиском пропавших людей. </p>\r\n<p>Также предлагаем ознакомиться с несколькими интересными статьями на эту тему:</p>\r\n<p><a href=\"https://autism-aba.blogspot.com/2013/04/managing-wandering.html\" target=\"_blank\">УПРАВЛЕНИЕ ПОВЕДЕНИЕМ АУТИЧНЫХ ДЕТЕЙ, СКЛОННЫХ К ПОБЕГАМ И БЛУЖДАНИЮ</a></p>\r\n<p><a href=\"https://nakedheart.online/articles/mindi-sheitauer-pomosh-pri-ubeganii-detei-s-autizmom\" target=\"_blank\">Минди Шейтауэр: Помощь при убегании детей с аутизмом</a></p>\r\n<p><a href=\"https://outfund.ru/rebenok-s-autizmom-poteryalsya/\" target=\"_blank\">Как уменьшить риск того, что ребенок с аутизмом потеряется?</a></p>\r\n<p>Безопасных и спокойных прогулок вам этим летом!</p>\r\n<p>Проект «Скорая помощь» реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n\r\n\r\n', '/images/SP/13.jpg', '2021-06-07', '', '', '', '', '', 0, '', '13'),
(426, '22', 'Благодарим за подарки для наших ребят', 'В «РАСсвет» прибыли подарки от наших друзей - компании \"Иркутскэнергосбыт\": принадлежности для творчества и сенсорные игрушки. Ребята презенты уже оценили и попробовали в деле. Фотоотчет с пробы прилагается. Спасибо, что помните о нас! ', '<p>В «РАСсвет» прибыли подарки от наших друзей - компании \"Иркутскэнергосбыт\": принадлежности для творчества и сенсорные игрушки. Ребята презенты уже оценили и попробовали в деле. Фотоотчет с пробы прилагается. Спасибо, что помните о нас! </p>\r\n\r\n\r\n', '/images/news/08.06.21/4.jpg', '2021-06-08', '/images/news/08.06.21/4.jpg;\r\n/images/news/08.06.21/1.jpg;\r\n/images/news/08.06.21/2.jpg;\r\n/images/news/08.06.21/3.jpg;\r\n/images/news/08.06.21/5.jpg;\r\n/images/news/08.06.21/6.jpg', '', '', '', '', 0, '', '0'),
(427, '4', 'Подготовка к школе', '\"Готовь сани летом, а телегу-зимой\" - вспомнили народную мудрость специалисты \"РАСсвета\" и вновь организовали групповую подготовку к школе детей с аутизмом. \r\nВ этом году в Иркутске откроют новые ресурсные классы, поэтому групповой формат занятий особенно актуален для начинающих школьников.', '<p>\"Готовь сани летом, а телегу-зимой\" - вспомнили народную мудрость специалисты \"РАСсвета\" и вновь решили организовать групповую подготовку к школе детей с аутизмом. Формат занятий для нас новый и очень интересный - нужно обучить ребёнка не только академических навыкам и правилам учебного поведения, но и взаимодействию с товарищами по классу, с педагогом.</p>\r\n<p>В этом году в Иркутске откроют новые ресурсные классы (стараниями активных родителей и специалистов нашего центра), поэтому групповой формат занятий особенно актуален для начинающих школьников.</p>', '/images/group/2021-6/4.jpg', '2021-06-11', '/images/group/2021-6/1.jpg;\r\n/images/group/2021-6/2.jpg;\r\n/images/group/2021-6/3.jpg;\r\n/images/group/2021-6/4.jpg;\r\n/images/group/2021-6/5.jpg;\r\n/images/group/2021-6/6.jpg;\r\n/images/group/2021-6/7.jpg;\r\n/images/group/2021-6/8.jpg;\r\n/images/group/2021-6/9.jpg', '', '', '', '', 0, '', ''),
(428, '16', 'Познакомимся поближе: Екатерина Родыгина', 'Друзья, сегодня предлагаем познакомиться поближе с центростремительной силой «РАСсвета» - одной из основательниц и председателем нашего центра Екатериной Родыгиной. ', '<p>Друзья, сегодня предлагаем познакомиться поближе с центростремительной силой «РАСсвета» - одной из основательниц и председателем нашего центра Екатериной Родыгиной. </p>\r\n<p>Екатерина окончила Иркутский государственный медицинский университет по специальности медико-профилактическое дело, работала в этой сфере более 10 лет. После рождения сына любимую работу пришлось сменить. Екатерина прошла полную программу по Прикладному анализу поведения ВСаВА и приступила к созданию комплексной помощи детям с аутизмом в городе Иркутске. Интервью с Екатериной читайте ниже.</p>\r\n<p>- Я начала заниматься организацией центра не только потому, что у моего ребенка РАС, у меня всегда была потребность помогать. Мой девиз с детства: \"Даже маленькое доброе дело меняет этот мир к лучшему. Даже фантик от конфеты, брошенный в урну, а не на дороге, сделает наш город чище. Даже потушенный костёр, пусть и не мной разведенный, предотвратит пожар\". До рождения детей я поддерживала друзей и знакомых, попавших в кризисные ситуации, помогала бездомным животным найти новых хозяев. Еще в студенчестве работала в реанимации, ушла оттуда потому, что сильно переживала за каждого пациента. Когда родился сын, и у него оказался диагноз аутизм, я поняла, что в этой сфере могу реализовать свой потенциал, оказать помощь людям, которые нуждаются в поддержке и не знают куда пойти и что делать со своей бедой. С момента задумки о создании нового центра до ее реализации прошел год. «РАСсвет» открылся 5 марта 2018 года.</p>\r\n<p>- Что с моим ребенком что-то не так стала замечать, когда ему еще было полгода, врачи мне не верили. К полутора годам сын показывал типичную для РАС картину: стереотипные движения, повышенная чувствительность к звукам, страх общения со сверстниками и т.п. Когда мы обратились за постановкой диагноза к врачам, оказалось, что в Иркутске аутизм не ставят. Я поняла, что надо решать эту проблему. Официальный диагноз для сына мы получили, но все еще в процессе решения проблемы диагностирования аутизма в Иркутске как таковой. Проблемы, связанные с особенностями ребенка мотивируют на развитие и движение вперед очень сильно. Это дополнительный стимул - помогая другим детям, я помогаю и своему сыну тоже.\r\n</p>\r\n<p>- Подруги смеются над моей замороченностью с планами – их очень много. Говорят, что у меня даже после смерти все расписано. Все действительно так. У меня все по полочкам разложено, я знаю, куда иду и зачем, знаю, что нужно будет делать, когда моим детям будет 20, 30 и 40 лет…Мне нужно создать для них такие условия, чтобы они смогли обойтись без моей помощи. Ведь родители не вечны. </p>\r\n<p>- Самое интересное в моей работе – поиск ресурсов для реализации всех идей. Самое сложное – бороться с выгоранием родителей и педагогов. У меня тоже бывает моменты, когда дела накапливаются вместе с усталостью, сроки поджимают, хочется все бросить, побыть в одиночестве, не решая проблем.\r\n</p>\r\n<p>Фото Лидия Лось</p>\r\n\r\n', '/images/team/Rodygina/foto.jpg', '2021-06-25', '', '', '', 'Команда', '/team.php', 0, '', '0'),
(431, '16', 'Познакомимся поближе: Елена Михеева', 'Эта статья об одном из учредителей «РАСсвета», поведенческом специалисте, учителе первого ресурсного класса в Иркутской области, и маме ребенка с РАС – Елене Михеевой. В ней сочетается правило трёх «Т»: талант, труд и творчество. Историю Елены читайте в её интервью.', '<p>Эта статья об одном из учредителей «РАСсвета», поведенческом специалисте, учителе первого ресурсного класса в Иркутской области, и маме ребенка с РАС – Елене Михеевой. В ней сочетается правило трёх «Т»: талант, труд и творчество. Историю Елены читайте в её интервью.</p>\r\n<p>- Обучение прикладному анализу поведения я начала в 2016 году. Прошла 4 курса подготовки специалистов по ПАП. Сейчас учусь на 5 курсе Пединститута ИГУ. У меня никогда не возникало желания или мысли прекратить обучение. Также как и у других основателей нашего центра. У нас есть большая мотивации развиваться из-за наших детей. А когда «РАСсвет» начал работать и к нам стали обращаться родители с детьми с аутизмом, ответственности и стимула развиваться стало еще больше. </p>\r\n<p>- Лучшая оценка моей работы, как педагога - видеть динамику развития ребенка, которого ты обучаешь. Это сложно – находить индивидуальный подход к каждому ребенку. Любой материал нужно адаптировать под конкретного ученика, что занимает много времени. Требуется много терпения и креативности, но результат всегда того стоит. И нужно понимать, что без индивидуальных занятий к групповым сразу перейти не получится. Сейчас я испытываю большой интерес именно к формату групповых занятий.</p>\r\n<p>- У невербальных детей с РАС в нашем центре мы вводим  альтернативную систему коммуникации, в 90% случаев это PECS . Как правило, у детей использующих альтернативную систему коммуникации, бывают затруднения с пониманием обращенной речи.  PECS система работает и в обратную сторону, т.е и взрослые при обращении к ребенку могут использовать карточки PECS и составлять предложения, общаться с ребёнком. Например, спросить: «Пойдем гулять в парк?». Дети, осваивающие PECS, и взрослые, помогающие им в этом, могут взять из этого опыта очень многое.</p>\r\n<p>- Наша работа никогда не закончится. Цели будут достигаться, меняться вместе с взрослением наших детей. Сейчас общая цель у нас в «РАСсвете» – утвердить модель «Ресурсный класс», чтобы государство на всех уровнях поддерживало инклюзивное образование, и чтобы это образование показывало хорошие результаты на практике. Также надо создавать тренировочные квартиры, решать вопросы с занятостью детей с РАС по достижению возраста 18+.</p>\r\n<p>- Елена - одна из главных боевых единиц \"РАСсвета\", моя \"правая рука\" и самый верный друг. Она меня выручает и на публичных выступлениях, и в реализации всех задумок, помогает с образовательным направлением и просто поддерживает в любых ситуациях. Она даже не подозревает, как часто вдохновляет меня идти дальше и не сдаваться, - рассказывает Екатерина Родыгина, руководитель \"АПЦ РАСсвет\". </p>\r\n\r\n\r\n', '/images/team/Miheeva/foto/1.jpg', '2021-07-03', '/images/team/Miheeva/foto/1.jpg;\r\n/images/team/Miheeva/foto/2.jpg;\r\n/images/team/Miheeva/foto/3.jpg;\r\n/images/team/Miheeva/foto/4.jpg', '', '', 'Команда', '/team.php', 0, '', '0'),
(432, '4', 'Повторенье – мать ученья или метод дискретных проб', 'Лето в разгаре! Так же как и работа в «РАСсвете»: новые проекты, занятия, интенсивы… Сегодня поделимся с вами моментами обучения детей с аутизмом и расскажем об одном из методов АВА-терапии, который мы постоянно применяем на практике с нашими ребятами.', '<p>Лето в разгаре! Так же как и работа в «РАСсвете»: новые проекты, занятия, интенсивы… Сегодня поделимся с вами моментами обучения детей с аутизмом и расскажем об одном из методов АВА-терапии, который мы постоянно применяем на практике с нашими ребятами.</p>\r\n<p>Обучение отдельными блоками (дискретными пробами) или коротко - DTT (Discrete Trial Teaching) - это структурированная техника, основанная на многократном повторении целевой реакции.</p>\r\n<p>\r\n- Учебная сессия DTT состоит из нескольких блоков. Каждый обучающий блок состоит из трёх основных частей: стимул (инструкция, вопрос или карточка), реакция ребенка и последствие. Если ребенок ответил правильно, то получает поощрение. Это может быть жетон, похвала, вкусняшка и т.д, в зависимости от ребенка. Если он ответил неправильно, то поощрение не получает, а инструктор проводит процедуру коррекции ошибки. Это обязательное условие, ни в коем случае нельзя поощрять неправильный ответ ребенка, ведь именно через последствия ребенок учится отличать правильный ответ от неправильного. И если не будет различимых последствий после верных и неверных реакций ребенка, то он не будет обучаться, - рассказывает специалист «РАСсвета» Елена Шапилова.</p>\r\n<p>Чтобы создать условия успешного обучения, а для детей с РАС это обязательный пункт, в обучении отдельными блоками, как и в других методах АВА, используются подсказки. Подсказка даётся на начальном этапе обучения незамедлительно после инструкции, до ответа ребенка. И затем постепенно, но достаточно быстро убирается.</p>\r\n<p>В первом видео инструктор обучает 4-летнего Вадима \"поведению слушателя\", учит различать на слух название мотивационного стимула – любимого лакомства мальчика – кураги. Вадиму предлагают выбрать из 2 предметов, лежащих на столе – курагу. Здесь очень хорошо видно, как инструктор дает подсказку ребенку и затем убирает её. После каждой правильной реакции Вадим получает жетон, таким образом, его поведение подкрепляется, ведь малыш уже знает, что после сбора всех жетонов он получит любимую игрушку.\r\n</p>\r\n<p>Процедура обучения методом отдельных блоков производится в несколько этапов:</p>\r\n<p>I. Работа ведется с одним стимулом (инструкцией, вопросом или предметом, который нужно показать или назвать). Подсказка, незамедлительная в первом блоке, убирается от блока к блоку (например, с помощью временной задержки). Работа продолжается до первого самостоятельного ответа ребенка.</p>\r\n<p>II. Продолжается работа над тем же целевым стимулом с введением отвлекающих стимулов или инструкций, на которые переключается внимание ребенка.</p>\r\n<p>\r\nIII. Работа ведется уже с двумя или более целевыми стимулами, каждый из которых ранее был освоен в изоляции, т.е. прошел процедуру обучения на 1 и 2 этапах.</p>\r\n<p>DTT - отличная техника, которая подходит не только детям с аутизмом, но и нейротипичным детям для заучивания чего-либо, например, таблицы умножения. Ученики ресурсного класса Иркутска тоже работают по методу отдельных блоков для освоения разных навыков: Игорь учит глаголы, моторные инструкции и др., Тася – части тела, буквы, Ника – слоги. Несколько ребят учат состав числа, чтобы довести навык до автоматизма и уже не использовать визуальную подсказку или счетный материал.</p>\r\n<p>На втором видео первоклассница из ресурсного класса Соня учится различать предлоги с помощью DTT. Когда девочка ошибается - тьютор проводит процедуру коррекции ошибки: завершает блок с ошибкой, повторяет блок с немедленной подсказкой, отвлекает ребенка простыми инструкциями и снова даёт целевую инструкцию, поощряя правильный ответ ребенка.</p>\r\n<p>Проект «Центр развития инклюзивного образования для детей с расстройством аутистического спектра по модели «Ресурсный класс» реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n', '/images/RK/21.07.06.jpg', '2021-07-06', '', '/images/RK/21.07.06.jpg;agRVyincums;\r\n/images/RK/21.07.06.jpg;tEFOp6kqUNw', '', 'Новости', 'news.php', 0, '', '10'),
(435, '16', 'Познакомимся поближе: Мария Максимова', 'Иногда, чтобы стать преподавателем, нужна личная мотивация – рождение ребенка с особенностями развития. Среди специалистов «РАСсвета» подобную историю профессионального становления имеет Мария Максимова – один из учредителей центра, куратор ресурсного класса школы в поселке Молодежный. ', '<p>Говорят, что хорошим учителем надо родиться – иметь талант, призвание и любовь к детям. Но иногда, чтобы стать хорошим преподавателем, нужна личная мотивация – рождение ребенка с особенностями развития. Тогда вовлеченные в процесс образования родители или родственники имеют большие шансы со временем превратиться в грамотного и замотивированного педагога. </p>\r\n<p>Среди специалистов «РАСсвета» подобную историю профессионального становления имеет Мария Максимова – один из учредителей центра, куратор ресурсного класса школы в поселке Молодежный. Будучи студенткой, она видела свое будущее в экономике, получила соответствующее образование, работала по специальности. Пока не родилась дочка с расстройством аутистического спектра. Историю Марии читайте в ее интервью.</p>\r\n<p>- Я работала с 18 лет, но после рождения ребенка так и не смогла вернуться в свой коллектив – начала обучение в новой сфере, постепенно прошла 3 курса подготовки специалистов по ПАП. Вместе с другими родителями детей с РАС мы объединились и со временем организовали наш центр. Сначала работали на голом энтузиазме и на паре десятков съемных квадратных метров. Но главное – желание, мы очень хотели помочь нашим детям, а в итоге стали помогать и другим семьям. \r\n</p>\r\n<p>- Работа с ребенком с аутизмом – марафон на длинную дистанцию, пройти его можно, только грамотно распределив ресурсы. Эффект от быстрого старта и рывка может так же быстро испариться, как он и образовался, если у тебя не останется сил и мотивации дойти до цели.</p>\r\n<p>- Я люблю замечать и удивляться самым обычным, на первый взгляд, мелочам в развитии ребенка с аутизмом. Сколько бывает приложено трудов, чтобы добиться от ученика произнесенного слога, ответного взгляда и повтора движения за педагогом. Даже самые маленькие шажочки вперед важны.\r\n</p>\r\n<p>- При взаимодействии с ребенком с аутизмом необходимо думать на пару шагов вперед, потому что одно неверное решение может привести к нежелательному поведению ученика, которое сформируется и быстро закрепится из-за ошибки взрослого. Поэтому нужно всегда быть внимательным, наблюдая реакции ребенка и контролируя свое поведение. Это сложно.</p>\r\n<p>- Я люблю рукоделие, пробовать разные материалы и техники. Но на хобби у меня практически нет времени. Частично реализовать свои творческие порывы помогают занятия с ребенком в мастерской «Дом синего слона».\r\n</p>\r\n<p>- Создать инклюзивную среду, сделать жизнь детей, а затем и взрослых людей с РАС более простой в плане нахождения поддержки, получения образования, трудоустройства – такая большая цель не только у меня, но и у всех специалистов «РАСсвета».</p>\r\n<p>- Мария в команде практически с самого начала совместного пути создания будущего наших детей, ещё до \"РАСсвета\". С тех пор мы бок о бок работали, учились, организовывали. И все вместе старели... Все, кроме Маши. Я очарована её неугасаемой молодостью и в то же время строгостью и стойкостью. Маша отличный спикер семинаров: выбирает для доклада яркие запоминающийся примеры, даже зная тему от и до, мне всё равно интересно её слушать. А ещё у неё классный почерк! Открою секрет, если наши сертификаты с семинаров заполнены красивым почерком, за этим стоит Маша, - говорит Екатерина Родыгина, руководитель \"АПЦ РАСсвет\". </p>\r\n\r\n', '/images/team/Mariya/foto.jpg', '2021-07-20', '', '', '', 'Команда', '/team.php', 0, '', '0'),
(437, '2', 'Летнее приключение', 'Прекрасная погода, небольшая прогулка по городу и поездка на лодке по Ангаре на остров Бабр – настоящее приключение для ребят «РАСсвета». Вместе с захватывающими ощущениями дети получили важный опыт и знания – отработали навыки безопасного поведения на улице и у водоема. На этой неделе педагоги центра провели тематический праздник «Дядя Степа – милиционер». ', '<p>Прекрасная погода, небольшая прогулка по городу и поездка на лодке по Ангаре на остров Бабр – настоящее приключение для ребят «РАСсвета». Вместе с захватывающими ощущениями дети получили важный опыт и знания – отработали навыки безопасного поведения на улице и у водоема. На этой неделе педагоги центра провели тематический праздник «Дядя Степа – милиционер». .</p>\r\n<p>Помните популярного сказочного советского героя – дядю Степу? Этакий «дедушка» современного мультяшного Аркадия Паровозова: выручал всех из беды, призывал к ответу хулиганов. Отличный пример для подражания…В «РАСсвете» решили воспользоваться моделью старой закалки, чтобы в игровой форме обобщить навыки, которые отрабатывали с воспитанниками на протяжении года. \r\n</p>\r\n<p>Вместо каланчи дяди Степы – высокая и стройная Тётя Даша – милиционер показывала дорогу и с помощью жезла регулировала движение детей: поднятый жезл – стоим на месте, отведенный в сторону – можно идти вперед! Правила поведения на улице и возле воды простые, но для детей с РАС бывают трудновыполнимы: переходить дорогу в положенном месте, ориентируясь на светофор, ждать взрослого и не убегать вперед, не забегать в воду без разрешения и сопровождения. Чтобы ребенок понял и запомнил правила, нужно подавать их в интересной форме. </p>\r\n<p>Дома и на занятиях вы можете вводить правила поведения в дороге с помощью игры «Стой-иди». Для этого понадобятся 2 большие карточки красного и зеленого цвета. Когда взрослый показывает зеленую карточку, ребенок должен идти, затем ведущий показывает красную карточку – надо остановиться. Если у ребенка есть проблемы с пониманием речи, визуальная поддержка такими карточками – отличный вариант для безопасных выходов на улицы. </p>\r\n<p>- Чтобы у ребенка не было соблазна купаться в любом водоеме в поле зрения, важно учить его развлекаться с водой безопасным способом: кидать камешки в воду, кормить рыб, если они там водятся, - дает подсказку Екатерина Родыгина, руководитель «АПЦ РАСсвет».</p>\r\n<p>На острове Бабр, куда воспитанники «РАСсвета» отправились вместе с родителями, педагогами, волонтерами и с тётей Дашей-милиционером, находится Иркутское форелевое хозяйство. Рыбу можно кормить, можно ловить, готовить и есть – на выбор посетителей. А еще на территории хозяйства живут кролики, еноты и куры – их тоже можно кормить (взять морковку и/или зерновую смесь с собой) и наблюдать за их повадками. Ребята «РАСсвета» с удовольствием познакомились со всеми животными острова и успели поиграть в коммуникативные игры. Фото-отчет с поездки смотрите в карусели.</p>\r\n<p>Проект «Скорая помощь» реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n\r\n\r\n', '/images/SP/10/1.jpg', '2021-07-24', '/images/SP/10/1.jpg;\r\n/images/SP/10/2.jpg;\r\n/images/SP/10/3.jpg;\r\n/images/SP/10/4.jpg;\r\n/images/SP/10/5.jpg;\r\n/images/SP/10/6.jpg;\r\n/images/SP/10/7.jpg;\r\n/images/SP/10/8.jpg;\r\n/images/SP/10/9.jpg;\r\n/images/SP/10/10.jpg;\r\n/images/SP/10/11.jpg;\r\n/images/SP/10/12.jpg;\r\n/images/SP/10/13.jpg', '', '', '', '', 0, '', '13'),
(438, '16', 'Познакомимся поближе: Елена Шапилова', ' Продолжаем знакомство с коллективом «РАСсвета». Сегодняшний пост посвящен Елене Шапиловой – одной из учредителей центра, опытному педагогу и маме ребенка с РАС. Интервью с Еленой читайте ниже. ', '<p> Продолжаем знакомство с коллективом «РАСсвета». Сегодняшний пост посвящен Елене Шапиловой – одной из учредителей центра, опытному педагогу и маме ребенка с РАС. Интервью с Еленой читайте ниже </p>\r\n<p>-У меня педагогическое образование, окончила математический факультет Иркутского пединститута и 20 лет проработала в школе учителем математики. Как и многим родителям детей с особенностями, с работы пришлось уйти, когда стало понятно, что на воспитание и развитие ребенка с аутизмом катастрофически не хватает времени и знаний. Так началось моё обучение в области прикладного анализа поведения. Нашего центра тогда еще не существовало, как и других центров, занимающихся АВА в Иркутске. Мы с другими родителями - будущими учредителями АНО \"АПЦ Рассвет\" - учились, практиковались на своих детях, ездили на интенсивы и стажировки в Новосибирск. А потом создали свой АВА-центр, о котором так давно мечтали.</p>\r\n<p>- На сегодняшний день у меня за плечами 3 курса обучающей программы подготовки специалистов по прикладному анализу поведения, опыт работы инструктором и куратором центра \"Рассвет\". С момента создания первого ресурсного класса в Иркутске, где учится и мой сын, я работаю там тьютором. Продолжаю и свою работу в центре - веду дистанционные занятия и сайт организации.\r\n</p>\r\n<p>- Кстати, работе с сайтом тоже пришлось научиться с нуля. Когда мы начинали, денег на привлечение сторонних специалистов у организации не было, поэтому всё, что могли, мы делали сами или привлекали волонтёров. Одним из волонтёров стал мой старший сын Игорь, тогда еще студент кибернетического факультета ИРНИТУ. Он то и помог нам с созданием сайта. Сейчас он уже работает по профессии в серьезной организации, но всё еще помогает мне с сайтом, если возникает такая необходимость. \r\n</p>\r\n<p>- Самое интересное в работе поведенческого специалиста, на мой взгляд, это придумывание стратегии по формированию какого-либо сложного навыка. И это же, наверное, и самое сложное. Я с детства люблю головоломки, наверное, поэтому мне и близка математика, и придумывание стратегий по формированию навыков тоже что-то из этой серии. </p>\r\n<p>- Сложность в том, что не всегда тот план, который созревает в голове, удается с легкостью реализовать на практике. Тогда приходится возвращаться назад, разбивать навык на более мелкие этапы, придумывать новые подсказки и способы их затухания. Но когда твой ученик осваивает этот сложный навык, все труды вознаграждаются, и получаешь настоящий кайф от проделанной работы.</p>\r\n<p>- Я стараюсь находить время и для увлечений. По-моему, без этого нельзя, иначе очень быстро наступит полное выгорание. Я люблю детективы - читать и смотреть. Конечно, порой время найти не просто, поэтому часто совмещаю приятное с полезным. Например, фильмы или сериалы смотрю, когда готовлю. А книги слушаю в аудио-варианте во время уборки или другой физической работы. Кстати, я не очень люблю готовить и убираться, так что тут еще и своеобразный \"пэринг\" - эти занятия становятся гораздо более приятными в сочетании с любимыми увлечениями. </p>\r\n<p>- Наверное, будет очень громко сказано, но моя мечта - это грамотное толерантное общество, в котором людей с особенностями будут принимать такими, какие они есть. Это глобально. Ну а моя главная цель - вырастить сына самостоятельным человеком, постараться дать ему столько знаний и практических навыков, чтобы он мог быть самостоятельным не только в быту, но и получить какую-то профессию, жить в обществе.\r\n</p>\r\n<p>- К любому делу Лена подходит с особой тщательностью. Когда ей ставишь задачу, нужно сразу предупредить, что достаточно выполнить на троечку. Так как эта троечка всегда равна крепкой пятёрке. Она честна и прямолинейна. И никогда не будет умалчивать то, что считает несправедливым. Её мнение помогает нам прийти к истине при решении любых вопросов. Для меня она не только незаменимая часть команды, но и важный человек в моей жизни, - рассказывает Екатерина Родыгина, руководитель«РАСсвета».</p>\r\n\r\n\r\n', '/images/team/Shapilova/foto/1.jpg', '2021-07-28', '/images/team/Shapilova/foto/1.jpg;\r\n/images/team/Shapilova/foto/2.jpg;\r\n/images/team/Shapilova/foto/3.jpg', '', '', 'Команда', '/team.php', 0, '', '0'),
(443, '12', 'Игры с мячом для ребёнка с РАС', 'Хотите научить играть ребёнка с РАС в простые игры с мячом, но не знаете с чего начать? Тренер по АФК центра \"РАСсвет\" Виктория Кузнецова подготовила несколько простых в исполнении упражнений и лайфаков.   ', '<p>Хотите научить играть ребёнка с РАС в простые игры с мячом, но не знаете с чего начать? Тренер по АФК центра \"РАСсвет\" Виктория Кузнецова подготовила несколько простых в исполнении упражнений и лайфаков.    </p>\r\n<p>Чтобы обучить ребенка пинать  футбольный мяч, нужно разбить действие на этапы и снизить сложность.</p>\r\n<p>- Вместо настоящих ворот можно использовать напольные спортивные дуги.</p>\r\n<p>- Сначала необходимо научить ребенка видеть цель: он должен уметь закатить мяч руками в «ворота». Затем начинается обучение пинанию мяча по показу взрослого «сделай так» и с полной физической подсказкой.</p>\r\n<p>- Важно поощрять каждое произведенное действие ребенка. После того, как ребенок поймет принцип движения, поощряются только попытки попасть по воротам. После уверенного действия в ворота поощряются только ситуации, когда ребенок забивает «гол». Любая критика неудачных попыток недопустима. На этапах обучения важное место отводится поощрениям (похвала, вкусняшка и пр), которая будет поддерживать мотивацию ребенка играть. </p>\r\n<p>Чтобы научить ребёнка бросать мяч в кольцо, также необходима подготовка. Эта игра сложна даже для детей с нейротипичным развитием, так как кольцо расположено параллельно горизонтальной плоскости.</p>\r\n<p>- На первых этапах обучения можно поступить так:  на большом куске ватмана или обоев чертим несколько кругов разного диаметра, вырезаем и вешаем лист дома в дверном проеме или на улице на турнике на скотч. Дети забрасывают пластмассовые мячики разного диаметра, параллельно их можно учить спокойно ждать своей очереди, а можно сделать эту игру веселой неразберихой.</p>\r\n<p>- Старайтесь поддерживать мотивацию ребенка эмоциональной похвалой.</p>\r\n<p>- Когда будете переносить навык, освоенный дома, в естественную среду, необходимо снизить требования и поощрять само желание сотрудничества на улице со взрослыми и ровесниками. И уже после того, как ребёнок освоится и будет самостоятельно проявлять желание поиграть, можно добавить в игру момент соревнования.</p>\r\n<p>Проект \"Движение вверх!\" осуществляется при поддержке Благотворительный фонд Сбербанк \"Вклад в будущее\".</p>\r\n\r\n\r\n', '/images/AFK/2021/9.jpg', '2021-08-06', '', '', '', '', '', 0, '', '0'),
(450, '19', 'Отлично подготовились', 'Лето закончилось, а вместе с ним и летняя площадка \"Космос\" на базе школы поселка Молодёжный. Последние дни площадки ученики из ресурсного класса проводили всё больше времени на общешкольных мероприятиях наравне с другими ребятами. ', '<p>Есть 2 типа родителей школьников: одни считают, что каникулы предназначены только для отдыха, другие же полагают, что даже летом нельзя забывать об учебе: дневники чтения, рабочие тетради с примерами и т.п. задания детям помогают оставаться в тонусе. Для детей с РАС длительные перерывы в обучении могут дать эффект «отката», когда приобретенные по́том и трудом навыки распадаются, и учебное поведение нужно заново формировать.</p>\r\n<p>В июле в «РАСсвете» открылся мини-курс по подготовке учеников ресурсного класса к новому учебному году. Первоклашки, отучившиеся год в школе поселка Молодежный, после месячного перерыва вновь вернулись к школьным занятиям, но уже в стенах нашего центра.</p>\r\n<p>Повторяли всё, что уже изучали ранее, но в другой обстановке, с другим учителем и с помощью других учебных пособий. Это называется генерализацией навыков или обобщением. Так же, как и в школе, на доске всегда есть визуальное расписание уроков, следовать которому ребята привыкли, и оно помогает большинству детей с аутизмом ориентироваться в ходе занятия.</p>\r\n<p>Помимо учебных заданий, первоклассники играли в настольные и активные коммуникативные игры, выходили на совместные прогулки, учились полезным бытовым навыкам: почистить и нарезать овощи для салата в рамках урока технологии труда и т.д. Дети проводили время в кругу друзей и с пользой.</p>\r\n<p>Остаток каникул ученики ресурсного класса вместе с другими ребятами провели в детском лагере \"Космос\", на базе школы посёлка Молодежный. Дети играли и общались в стенах родной школы и на её территории. В программе лагеря различные творческие занятия , зарядка, эстафеты и, конечно, обед по расписанию.</p>\r\n<p>Последние дни площадки ученики из ресурсного класса проводили всё больше времени на общешкольных мероприятиях наравне с другими ребятами. Чтобы дети с аутизмом могли гармонично вливаться в общество сверстников специалисты нашего центра и тьюторы класса проделали большой путь: сначала в кабинетах \"РАСсвета\", затем в школьных классах.\r\n</p>\r\n<p>Занятия в \"Рассвете\" и площадка подготовили детей к первому сентября, а также развили коммуникативные и социальные навыки ребят. С нетерпением ждём нового учебного года! </p>\r\n<p>Проект \"Центр развития инклюзивного образования для детей с расстройством аутистического спектра по модели\" Ресурсный класс\" реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/RK/21.08.30/1.jpg', '2021-08-30', '/images/RK/21.07.14/1.jpg;\r\n/images/RK/21.08.30/3.jpg;\r\n/images/RK/21.07.14/13.jpg;\r\n/images/RK/21.07.14/5.jpg;\r\n/images/RK/21.08.30/2.jpg;\r\n/images/RK/21.08.30/7.jpg;\r\n/images/RK/21.08.30/8.jpg;\r\n/images/RK/21.08.17/6.jpg', '', '', 'Новости', 'news.php', 0, '', '10'),
(452, '22', 'Спасибо за помощь!', 'Ремонт - дорогое удовольствие, а если он не запланированный... Спасибо фирме \"Балкон центр\", быстро и бесплатно заменившей разбитое стекло в нашем кабинете. ', '<p>На занятиях, как и в обычной жизни, бывают казусы: где-то ребёнок может сильно хлопнуть, бросить игрушку на пол, или нечаянно разбить стекло - что и произошло недавно в центре. Ремонт - дорогое удовольствие, а если он не запланированный...  Спасибо фирме \"Балкон центр\" - мы обратились с просьбой к её директору Евгению Николаевичу Казанцеву, он дал распоряжение, и мастер быстро и бесплатно заменил разбитое стекло в нашем кабинете.</p>\r\n<p>Продолжаем работать с поведением детей, продолжаем находить хороших людей вокруг!</p>\r\n\r\n\r\n', '/images/gratitude/foto/2021.09.09/2.jpg', '2021-09-09', '/images/gratitude/foto/2021.09.09/1.jpg;\r\n/images/gratitude/foto/2021.09.09/2.jpg;\r\n/images/gratitude/foto/2021.09.09/3.jpg', '', '', '', '', 0, '', '0'),
(453, '4', 'Тематический праздник \"Мойдодыр\"', '15 сентября - день санитарно-эпидемиологической службы России мы отметили с размахом: в самом центре провели для наших воспитанников тематический праздник \"Мойдодыр\" - обобщили гигиенические нывыки в игровой форме, а после отлично провели время на прогулке, в кафе и на детской площадке острова \"Юность\"', '<p>15 сентября - День образования санитарно-эпидемиологической службы России. Сегодня ей исполнилось 99 лет, организация перешагнула почти вековой рубеж...</p>\r\n<p> В \"РАСсвете\" к санитарным нормам и гигиене относятся с ноткой профессионализма: во-первых, среди руководства центра есть бывшие врачи Роспотребнадзора, во-вторых, на занятиях наши педагоги отводят значимую часть обучению детей с аутизмом бытовым навыкам и основам личной гигиены. Поэтому День СЭС мы отметили с размахом: в самом центре провели для наших воспитанников тематический праздник \"Мойдодыр\" - обобщили гигиенические навыки в игровой форме, а после отлично провели время на прогулке, в кафе и на детской площадке острова \"Юность\"</p>\r\n<p>Проект \"Скорая помощь для детей с аутизмом и их семей\" реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских</p>\r\n\r\n\r\n\r\n', '/images/SP/11/9.jpg', '2021-09-15', '/images/SP/11/1.jpg;\r\n/images/SP/11/2.jpg;\r\n/images/SP/11/3.jpg;\r\n/images/SP/11/4.jpg;\r\n/images/SP/11/5.jpg;\r\n/images/SP/11/6.jpg;\r\n/images/SP/11/7.jpg;\r\n/images/SP/11/8.jpg;\r\n/images/SP/11/9.jpg;\r\n/images/SP/11/10.jpg', '', '', '', '', 0, '', '13');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(455, '13,19', 'Поговорим о ресурсном классе...', 'В этом учебном году в иркутской школе №33 впервые открылся ресурсный класс. Инициаторами выступили родители детей с аутизмом и адаптационно-педагогический центр \"РАСсвет\". О том, как проходят занятия учеников ресурсного класса школы №33 вы можете посмотреть в сюжете программы \"Вести Иркутск\".', '<p>В этом учебном году в иркутской школе №33 впервые открылся ресурсный класс. Инициаторами выступили родители детей с аутизмом и адаптационно-педагогический центр \"РАСсвет\".</p>\r\n<p>Трудности при организации учебного процесса были стандартными: нужно было договориться с чиновниками образовательной сферы, найти школу, в которой согласны обучать нейроотличных детей с использованием Прикладного анализа поведения, обеспечить финансирование (здесь кроме родительских средств помогли выигранные гранты и спонсоры) и закупить необходимое оборудование в класс, отыскать и обучить специалистов (педагога и тьюторов), найти куратора и супервизора, подготовить будущих первоклашек к школьной жизни на специальных групповых занятиях... И ещё учесть 1000 мелочей, без которых этот грандиозный проект бы просто не удался.</p>\r\n<p>О том, что все прошло успешно говорить ещё рано, хотя 1 сентября ученики ресурсного класса впервые посетили обычную городскую линейку и приступили к занятиям. Впереди команду иркутского ресурсного класса ждёт множество трудных вопросов, которые нужно будет решать, задействуя все силы. А пока жизнь продолжается, и большие победы достигаются маленькими шагами.</p>\r\n<p>О том, как проходят занятия учеников ресурсного класса школы №33 вы можете посмотреть в сюжете программы <a class=\"a-blue\" href=\"https://vestiirk.ru/news/pervyi-resursnyi-klass-dlia-detei-s-autizmom-otkryli-v-irkutske/\" target=\"_blank\">\"Вести - Иркутск\"</a> от 20 сентября.</p>\r\n<p>Ресурсный класс в Иркутске открылся благодаря проекту \"Ресурсный класс - Регионы. Расширение практики доступного инклюзивного образования детей с РАС и другими ментальными нарушениями\". Благодарим организаторов и партнеров проекта: Фонд \"Выход\", Ассоциация \"Аутизм - Регионы\" и Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n', '/images/smi/22.jpg', '2021-09-22', '', '', '', '', '', 0, '', '0'),
(457, '3', 'Прощаемся с проектом', 'На прошедшей на днях конференции \"Мир с добрыми глазами\" специалисты \"РАСсвета\" Екатерина Родыгина и Юлия Шестакова провели мастер-класс по методам обучения детей с аутизмом жизненно важным навыкам. Родители и педагоги узнали, как пошагово научить ребёнка с РАС самообслуживанию, гигиеническим процедурам, посещению врача и т.п. умениям.', '<p>На прошедшей на днях конференции \"Мир с добрыми глазами\" специалисты \"РАСсвета\" Екатерина Родыгина и Юлия Шестакова провели мастер-класс по методам обучения детей с аутизмом жизненно важным навыкам. Родители и педагоги узнали, как пошагово научить ребёнка с РАС самообслуживанию, гигиеническим процедурам, посещению врача и т.п. умениям.</p>\r\n<p>Мастер класс состоялся в рамках проекта \"Скорая помощь для детей с аутизмом и их семей\". Для нашего центра это очень значимый проект, его создавали ориентируясь на проблемы и запросы родителей детей с РАС в не самое легкое время - начало периода пандемии.</p>\r\n<p>Проект был направлен на восстановление у 15 детей с РАС утраченных в период самоизоляции навыков общения, самообслуживания, безопасности, самозанятости, речевых, бытовых и санитарно-гигиенических навыков. А также на обучение навыкам сморкаться, полоскать рот, носить маску, спокойно посещать врача ждать своей очереди в поликлинике, давать возможность проводить медицинский осмотр.</p>\r\n<p>За год работы с проектом специалисты \"РАСсвета\" вложили в него много сил: проводили вебинары, практические тренинги, тематические мероприятия, создали с нуля социальную историю по посещению поликлиники, провели большой информационный марафон и, конечно, работали непосредственно с детьми с РАС, развивая их бытовые навыки на регулярных занятиях.</p>\r\n<p>В сентябре проект \"Скорая помощь\" подошёл к концу. Для тестирования базовых навыков детей в начале и в конце проекта использовался протокол ABLLS-R, который позволяет оценить не только речевые и учебные навыки, но и навыки самообслуживания (в том числе приема пищи, переодевания, ухода за собой).</p>\r\n<p>Например, в сфере ухода за собой оценивались такие навыки (от более простых, к более сложным), как умение мыть руки, вытирать их, умываться, вытирать лицо, расчёсывать волосы, чистить зубы, сморкаться. Изначально на основании тестирования для каждого ребенка была составлена программа обучения необходимым навыкам, над которыми затем велась работа.</p>\r\n<p>Проект завершён, но работа \"РАСсвета\" продолжается, и впереди нас ждут новые проекты и новые профессиональные вершины.</p>\r\n<p>Проект реализовался с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n\r\n\r\n\r\n', '/images/SP/12/0.jpg', '2021-09-27', '/images/SP/12/0.jpg;\r\n/images/SP/12/1.jpg;\r\n/images/SP/12/2.jpg;\r\n/images/SP/12/3.jpg;\r\n/images/SP/12/4.jpg;\r\n/images/SP/12/5.jpg', '', '', '', '', 0, '', '13'),
(465, '18', 'Творим с душой', 'Уют и тепло — вот что наполняет мастерские \"Дома синего слона\". Здесь дети с аутизмом раскрываются через творчество, воплощая идеи, которые становятся частью их повседневной жизни. Работая вместе, ребята учатся создавать уникальные вещи, привнося в мир красоту и функциональность.', '<p>Уют и тепло — вот что наполняет мастерские \"Дома синего слона\". Здесь дети с аутизмом раскрываются через творчество, воплощая идеи, которые становятся частью их повседневной жизни. Работая вместе, ребята учатся создавать уникальные вещи, привнося в мир красоту и функциональность.</p> \r\n<p>Недавно мы представили новую серию текстильных изделий для дома, сочетающих четыре ключевых принципа: экологичность, качество, красота и доступность. Среди новинок — удобные органайзеры для мобильных телефонов и зарядок. Эти маленькие помощники решают вечную проблему запутанных проводов и позволяют легко разместить телефон рядом с розеткой. Изготовленные вручную из натуральных материалов, они украсят любой интерьер и станут незаменимыми спутниками в путешествиях.</p>\r\n<p>Еще одно направление нашей деятельности — изготовление мыла ручной работы. Этот процесс дарит ребятам радость творчества и развивает мелкую моторику. Они выбирают формы, добавляют ароматы и создают уникальные дизайны, которыми потом гордятся.</p>\r\n<p>Но мы не останавливаемся на достигнутом. Сейчас готовимся к новому проекту — работе с тканями. Ребята осваивают основы шитья, переносят выкройки с помощью портновских мелков и готовятся к созданию новых шедевров.\r\n</p>\r\n<p>Подготовка к новому году ещё впереди, но нельзя терять навыки в мелкой моторике. Наши мастера создают оригинальные игрушки и сувениры, которые невозможно найти ни в одном магазине. Например, забавные зайцы из деревянных бусин и яркие детские брелоки. Каждое изделие уникально и несет частичку души тех, кто его создал.\r\n</p>\r\n<p>Присоединяйтесь к нам в этом творческом пути! Вместе мы создаем нечто большее, чем просто вещи — мы дарим радость и вдохновение.</p>', '/images/DSS/21.10.14/2.jpg', '2021-10-14', '/images/DSS/21.10.14/1.jpg;\r\n/images/DSS/21.10.14/2.jpg;\r\n/images/DSS/21.10.14/3.jpg;\r\n/images/DSS/21.10.14/4.jpg;\r\n/images/DSS/21.10.14/5.jpg', '', '', '', '', 0, '', '0'),
(469, '19', 'Кто такой АВА-тьютор. Взгляд на профессию изнутри', 'Поговорим о роли АВА-тьютора в инклюзивной среде. Многие родители и педагоги не понимают функций тьютора в школе: то ли это ассистент учителя, то ли нянька для ребёнка с ОВЗ. Конечно, ни то, ни другое. Специалисты нашего центра: куратор \"РАСсвета\" Юлия Шестакова и тьютор ресурсного класса школы посёлка Молодежный Никита Ершов порассуждали на тему тьюторства.', '<p>Поговорим о роли АВА-тьютора в инклюзивной среде. Многие родители и педагоги не понимают функций тьютора в школе: то ли это ассистент учителя, то ли нянька для ребёнка с ОВЗ. Конечно, ни то, ни другое. Специалисты нашего центра: куратор \"РАСсвета\" Юлия Шестакова и тьютор ресурсного класса школы посёлка Молодежный Никита Ершов порассуждали на тему тьюторства.</p>\r\n<p>Юлия говорит:</p>\r\n<p>- Тьютор для ребенка – это, в первую очередь, коммуникативный партнер, который помогает ученику перенести навыки, отработанные в учебной среде в естественную среду: в общении со сверстниками, учителями и другими людьми. Он сопровождает ребенка на протяжении всего учебного процесса. Во-первых, в режимных моментах школьной жизни: в столовой, раздевалке, туалете и т.п. ученикам бывает необходима помощь взрослого. Надо понимать, что любой режимный момент является наилучшей возможностью для обучения ребенка в естественных условиях самостоятельности. Поэтому присутствие тьютора позволяет ученику быстрее освоить бытовые навыки.</p>\r\n<p>Во-вторых, в общеобразовательном классе и на занятиях в ресурсном классе, тьютор создает условия, при которых ребенок с большей вероятностью выполнит инструкцию педагога верно.</p>\r\n<p>В-третьих, на переменах, при совместном проведении досуга, тьютор не только помогает ребёнку учиться играть или общаться с ровесниками, но и привлекает одноклассников, объясняя, как они могут найти общий язык.\r\n</p>\r\n<p>Это только часть обязанностей АВА-тьютора в инклюзивной среде. Важную роль он также решает в реализации образовательной программы ребенка: выполняет индивидуальную часть образовательной программы, работает с нежелательным поведением ребенка, ведет учет и сбор данных, помогает тестировать уровень развития навыков детей...</p>\r\n<p>Тьютор в классе – это специалист, который контролирует поведения ребенка. Основная задача тьютора – стать ненужным ученику, развить у ребенка самостоятельность.</p>\r\n<p>Никита говорит:\r\n</p>\r\n<p>- Для меня работа тьютором – это «вспомнить всё» и узнать ещё больше нового и интересного. Мы, тьюторы, для детей становимся как бы внешним воображением, познание, любопытством, их глазами, руками, голосом, гидом по окружающему миру. Чтобы показать детям, что весь мир вокруг может быть интересен, и что он стоит того, чтобы напрягаться, стараться, учиться – нужно и в самих себе пробуждать любознательность.</p>\r\n<p>И вот тут может возникнуть вопрос: «а как же можно в самом себе такой интерес пробудить?». Лично я вспоминаю себя ребёнком, когда всё вокруг казалось загадочным и необычным, как я удивлялся и радовался, когда что-то получалось, как грустил, когда не получалось, но всё равно упорно старался, пока не достигал понимания и умения.</p>\r\n<p>Теперь я как будто бы заново проживаю вместе с учеником детство, мы вместе пробуем освоить различные навыки, достичь в них успехов, расширить горизонты познаний. Когда вы с учеником настраиваетесь на одну волну, вы начинаете черпать из этого силы, удовлетворение, да и просто вместе радуетесь, когда наконец что-то начинает получаться.</p>\r\n<p>Такая сонастроенность притягивает, как магнит, детей из регулярных классов. Они видят, что у нас в классе интересно и весело, они заглядываются на наши игры на переменах и постепенно начинают в них участвовать. Нейротипичными детям точно так же хочется разобраться во всём необычном и загадочном, особенно, если это такие необычные сверстники и такие необычные и общительные взрослые рядом с ними.</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n\r\n', '/images/RK/20.09.01/5.jpg', '2021-10-25', '/images/RK/21.09.01/5.jpg;\r\n/images/RK/20.09.21/1.jpg;\r\n/images/RK/21.09.28/3.jpg;\r\n/images/RK/21.08.30/2.jpg;\r\n/images/RK/21.08.17/7.jpg;\r\n/images/RK/21.09.25/8.jpg', '', '', '', '', 0, '', '10'),
(471, '20', 'Учим распознавать эмоции', 'Для детей с РАС актуальна проблема распознавания эмоций: им сложно определять эмоциональные состояния других людей и адекватно реагировать на них (веселиться, сочувствовать и т.д). \r\nА недостаточное развитие этого навыка влияет на коммуникацию, обучаемость и на социализацию ребёнка с РАС. ', '<p>Для детей с РАС актуальна проблема распознавания эмоций: им сложно определять эмоциональные состояния других людей и адекватно реагировать на них (веселиться, сочувствовать и т.д). </p>\r\n<p>Рассматривая умение распознавать эмоции как социальный навык, делаем вывод, что его недостаточное развитие влияет на коммуникацию, обучаемость и на социализацию ребёнка с РАС. </p>\r\n<p>Представьте школьника с аутизмом, который не может подружиться со сверстником, потому что не смотрит на его лицо и в глаза, не всегда адекватно понимает обращение к себе (может закричать, когда его попросили о чём то, может смеяться, когда другие плачут и т.д). </p>\r\n<p>Смотреть в лицо и глаза, определять эмоции - этому можно научить ребёнка на занятиях АВА. </p>\r\n<p>Психолог ресурсного класса школы в пос. Молодёжный Любовь Хацкевич подготовила игры для развития навыка распознавания эмоций. В них вы можете играть дома или на занятиях, в группе или с одним ребёнком. </p>\r\n<p>\"Нарисуй моё лицо\" </p>\r\n<p>Материалы: лист бумаги с нарисованным кругом, карандаш, краски, поддерживающие карточки - подсказки с изображением эмоций. \r\nИнструкция: \"Нарисуй, что показываю\". \r\nРебёнок смотрит на ваше лицо и определяет эмоцию, которую вы показали, переносит её на лист. </p>\r\n<p>\"Сделай как я\" </p>\r\n<p>Инструкция: \"А сейчас мы: <br>\r\n- Хмуримся, как осенняя туча; \r\nкак рассерженный человек; как злая волшебница. <br>\r\n- Улыбаемся, как солнышко; как хитрая лиса; как радостный человек; как будто увидел чудо.<br> \r\n- Злимся, как ребенок, у которого отняли мороженое; как человек, которого ударили. <br>\r\n- Пугаемся как ребенок, потерявшийся в лесу; как зайчик, увидевший волка; как котенок, на которого лает собака. <br>\r\n- Устали, как человек, идущий в гору; как человек, поднявший большой груз; как паук, притащивший большую муху. <br>\r\n- Отдыхаем как ребенок, который много помогал маме; как уставший воин после победы\". </p>\r\n<p>Инструктор проговаривает и показывает эмоции, которые нужно показать - дети повторяют. \r\n</p>\r\n<p>\"Зеркало\" </p>\r\n<p>Материалы: зеркало, карточки с эмоциями. \r\nПедагог передает зеркало и предлагает ребенку изобразить эмоцию: грусть, удивление, страх и т.д. Перед выполнением можно показать ребёнку картинку с изображением заданной эмоции, обратив внимание на положение бровей, глаз, рта.</p>\r\n<p>Проект реализуется при поддержке Благотворительный Фонд «Абсолют-Помощь» </p>\r\n\r\n\r\n\r\n\r\n', '/images/Absolut/2/1.jpg', '2021-10-30', '/images/Absolut/logo.jpg;\r\n/images/Absolut/2/1.jpg;\r\n/images/Absolut/2/2.jpg;\r\n/images/Absolut/2/3.jpg;\r\n/images/Absolut/2/4.jpg;\r\n/images/Absolut/2/5.jpg', '', '', 'Новости', '/news.php', 0, '', '9'),
(473, '19', 'Обучение специалистов', 'В этом учебном году в Иркутске и Иркутском районе в трёх общеобразовательных школах заработали ресурсные классы для детей с аутизмом. Наполняемость одного класса до 8 учеников. Все ресурсные классы находятся под курацией и супервизией опытных поведенческих специалистов.', '<p>В этом учебном году в Иркутске и Иркутском районе в 3 общеобразовательных школах заработали ресурсные классы для детей с аутизмом. Наполняемость одного класса до 8 учеников. Каждому прописан тьютор, индивидуальная программа обучения и поддержка педагога, изучившего основы Прикладного анализа поведения (ПАП). Все ресурсные классы находятся под курацией и супервизией опытных специалистов ПАП.</p>\r\n<p>Несмотря на то, что это уже большой шаг в развитии инклюзивного образования, запрос от родительского сообщества на новые ресурсные классы в несколько раз больше, чем могут позволить себе школы Приангарья.</p>\r\n<p>Причин, по которым сейчас невозможно открыть больше ресурсных классов, несколько. Это и дефицит педагогических кадров в регионе, и большая наполняемость классов, не хватка финансирования школ. А также особая ответственность, которую не каждый директор и учитель на себя возьмёт, ведь работать с трудным поведением учеников с аутизмом в пед. ВУЗах и колледжах не учат.</p>\r\n<p>Получается, чтобы открыть ресурсный класс, нужно сначала подготовить кадры: найти супервизора и куратора, учителя готового пройти дополнительное обучение по ПАП, и тьюторов. Последних тоже нужно обучить ПАП. Это процесс долгий и финансово затратный. Он полностью ложится на плечи родителей детей с аутизмом.</p>\r\n<p>Руководитель АПЦ \"РАСсвет\" Екатерина Родыгина рассказала об обучении тьюторов для ресурсного класса школы посёлка Луговой Иркутского района.</p>\r\n<p>- Мы искали тьюторов в течение 3 месяцев, с того момента, как стало известно, что ресурсному классу в этой школе быть. И сейчас ещё продолжаем искать... Не каждый справляется с работой из-за особенностей поведения учеников с РАС.</p>\r\n<p>Наш центр по собственной инициативе, безвозмездно организовал стажировку для тьюторов: учили, делились опытом, наработками, практиковались в группах с детьми. Уже на этом этапе кто-то не выдерживал и уходил.</p>\r\n<p>Есть родители, согласные стать тьюторами, но они не подходят на роль тьютора непосредственно своему ребёнку, так как он должен научиться взаимодействовать с другими взрослыми, педагогами. Если у ребёнка установлен руководящий контроль только с мамой, он не сможет выйти в инклюзию и встроиться в социум.</p>\r\n<p>Есть и ещё одна причина, по которой родитель не должен быть тьютором своего ребенка: как бы мама или папа не старались, они не смогут скрывать свои эмоции, тем самым будут подкреплять нежелательное поведение ребёнка, вместо того, чтобы его убрать.</p>\r\n<p>Куратор ресурсного класса школы посёлка Луговой Вероника Артамонова поделилась, чему обучает тьюторов вне уроков.</p>\r\n<p>- Кроме семинаров и тренингов \"РАСсвета\" и других обучающих программ, куратор также регулярно проводит обучение для тьюторов класса.</p>\r\n<p>Введение жетонов, дискретные пробы, азы альтернативной системы коммуникации PECS (формирование просьб и наименований с помощью карточек для невербальных детей и детей с затрудненным пониманием обращенной речи), работа над навыком поведения слушателя у ребёнка с РАС... Вот основной, но не полный список того, что необходимо знать тьютору для работы в ресурсном классе.\r\n</p>\r\n<p>Благодарим организаторов и партнёров проекта \"Эстафета компетенции НКО\": Фонд\"Выход\", Ассоциация \"Аутизм-Регионы\". Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n\r\n\r\n', '/images/RK/21.11.03/2.jpg', '2021-11-03', '/images/RK/21.11.03/2.jpg;\r\n/images/RK/21.11.03/1.jpg;\r\n/images/RK/21.11.03/3.jpg;\r\n/images/RK/21.11.03/4.jpg;\r\n/images/RK/21.11.03/5.jpg;\r\n/images/RK/21.11.03/6.jpg', '', '', '', '', 0, '', '0'),
(474, '19', 'Обучение академическим навыкам детей с РАС в школе', '\"Потянет ли мой ребёнок учебную программу?\" Родители часто волнуются перед тем, как их \"особый\" ребёнок пойдёт в первый класс. Чтобы снизить уровень стресса родителей, поделимся опытом педагогов ресурсного класса школы посёлка Молодежный Иркутского района.', '<p>\"Потянет ли мой ребёнок учебную программу?\" Родители часто волнуются перед тем, как их \"особый\" ребёнок пойдёт в первый класс. Чтобы снизить уровень стресса родителей, поделимся опытом педагогов ресурсного класса школы посёлка Молодежный Иркутского района. </p>\r\n<p>Весь сентябрь в классе кипела работа по оценке навыков, с которыми ребята приступили к своему второму году обучения. Под прицелом внимания педагогов не только академические навыки детей, но и социальные навыки, навыки работы в группе и навыки следования распорядку дня в классе, такие важные как для обучения в ресурсной зоне, так и для выхода в инклюзию.</p>\r\n<p>К октябрю тестирования были завершены, индивидуальные программы на новый учебный год написаны, календарно-тематические планы по учебным предметам составлены. Началась работа по реализации программ и формированию новых навыков.</p>\r\n<p>В общеобразовательной школе для обучения детей с РАС академическим навыкам, прежде всего, команда педагогов ориентируется на адаптированную образовательную программу по каждому образовательному маршруту. Для ребёнка с аутизмом характерно мозаичное развитие, чтобы сформировать у него преднавыки или \"закрыть какой-то дефицит\" для дальнейшего развития педагоги пользуются одним из инструментов Прикладного Анализа поведения - руководством и протоколом ABLLS-R. С его помощью и тестируются базовые навыки детей, разрабатывается индивидуальная часть образовательной программы, достижение целей которой помогает быстрее вывести ребенка с аутизмом на следующую ступень развития.</p>\r\n<p>В ресурсном классе детей с РАС обучают точно такому же набору основных академических навыков, как и нейротипичных учеников начальной школы: окружающий мир, математика, русский язык и чтение. </p>\r\n<p>Конечно, у всех ребят ресурсного класса школы в Молодёжном разный уровень образовательной программы и освоения материала. Например, Влад учится решать примеры в пределах 10 в уме, Соня - с использованием счётного материала, Стёпа уже уверенно решает задачи и осваивает сложение с переходом через десяток, а кто-то из учеников еще учится соотносить количество предметов с соответствующим числом и осваивает операции \"прибавить\" и \"отнять\" на предметном материале. </p>\r\n<p>Важно понять, что любой академический навык, которому обучается ребенок с РАС, можно сформировать с использованием методов прикладного анализа поведения, т.к. именно он помогает эффективно осваивать новые знания. Так, для обучения Стёпы сложению с переходом через десяток, учитель использует метод обучения цепочке реакций с использованием визуальных подсказок. На первоначальном этапе на каждом шаге цепочки Стёпе предоставлялись незамедлительные подсказки, которые постепенно убирались, до тех пор, пока мальчик не начал выполнять все действия в цепочке самостоятельно. </p>\r\n<p>Освоению академических навыков помогает индивидуальная адаптация материала под конкретного ученика ресурсного класса. </p>\r\n<p>Многие ребята уже научились читать и сейчас продолжают совершенствовать навык чтения, каждый на своём уровне. Соня учится отвечать на вопросы по тексту, Стёпа работает над техникой чтения, а Влад осваивает пересказ текста с использованием визуальной опоры. \r\nРебята, которые еще не умеют читать, учатся этому навыку разными способами: Ваня учится чтению через печатание, Тася осваивает глобальное чтение, Ника учит буквы и пробует свои силы в печатании.</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n\r\n\r\n\r\n', '/images/RK/21.11.10.jpg', '2021-11-10', '/images/RK/21.09.29/1.jpg;\r\n/images/RK/21.12.21/1.jpg;\r\n/images/RK/21.09.29/3.jpg;\r\n/images/RK/21.09.29/4.jpg;\r\n/images/RK/21.09.29/5.jpg;\r\n/images/RK/21.09.29/6.jpg;\r\n/images/RK/21.12.21/6.jpg', '', '', '', '', 0, '', '10'),
(476, '20', 'Как научить ребёнка с РАС собирать по образцу', 'Ребёнок не умеет играть, не любит творческие занятия, не может занять себя чем-то полезным - с такими проблемами сталкиваются многие родители детей с РАС.\r\nНо неумение и нежелание играть - это одна сторона медали. Как только дети становятся успешными в какой-то деятельности, она им начинает нравится.', '<p>Ребёнок не умеет играть, не любит творческие занятия, не может занять себя чем-то полезным - с такими проблемами сталкиваются многие родители детей с РАС.\r\nНо неумение и нежелание играть - это одна сторона медали. Как только дети становятся успешными в какой-то деятельности, она им начинает нравится.</p>\r\n<p>Куратор \"РАСсвета\" Юлия Шестакова расскажет, как научить ребёнка с РАС собирать пазлы, конструктор и модели по образцу.</p>\r\n<p>- Обучение конструированию  является первым этапом в развитии навыков сборки по образцу. Существует схема, которая позволяет понять с чего стоит начинать обучение ребенку творчеству:</p>\r\n<p>Конструирование из строительных материалов<br>\r\nКонструирование из конструкторов<br>\r\nАппликация из бумаги<br>\r\nЛепка из пластилина<br>\r\nПоделки из природного и бросового материла<br>\r\nРисование<br>\r\nПоделки в смешанной технике<br>\r\nСюжетно-ролевая игра<br>\r\nСловесное творчество<br>\r\nРазвивающие речевые игры. </p>\r\n<p>Обучение конструированию начинается в тот момент, когда ребенок может повторять движения с предметами за взрослым и различает предметы по цвету и форме.</p>\r\n<p>Рассмотрим кратко процедуру обучения. Учитель сидит за столом напротив ученика. У каждого есть свой собственный набор материалов, расположенный на столе сбоку.  Учитель собирает конструкцию из деталей, ученик должен повторить тоже самое из своих деталей.</p>\r\n<p>Используйте части конструкторов разнообразных форм и цветов. Начните с 2-3 частей и постепенно увеличивайте их количество. Когда конструкции построены, их нужно включись в игру. </p>\r\n<p>В первую очередь, детей необходимо обучить собирать башню. Затем, раскладывать последовательно детали. Цель – развить и закрепить навык обобщенной имитации, а не научить собирать определенные конструкции. Учитель заканчивает один этап строительства и ждет, пока ученик за ним повторит. Только потом идёт следующий этап.\r\n</p>\r\n<p>Учите правильной имитации всех вариантов расположения деталей, начиная с самых простых и переходя к более сложным:</p>\r\n<p>Сверху<br>\r\nСлева или справа<br>\r\nСпереди или сзади<br>\r\nПоложить строительный блок определенной стороной. </p>\r\n<p>Дальше идёт обучение ребенка собирать конструкцию из небольших цветных кубиков; копирование конструкций из одинаково окрашенных строительных блоков; копирование с плоских форм (по фото или рисунку); создание определенных построек и моделей (стола, стула, гаража, моста). Постепенно в задания добавляются элементы игры: загнать грузовик в гараж, водить «машину» и т.п.</p>\r\n<p>Самым последним этапом обучения является конструирование по памяти. Постройку показывают ребенку в течение 5 секунд, а затем прячут ее за перегородку. Ученик должен повторить постройку по памяти.</p>\r\n<p>Проект \"Социальные навыки и альтернативная коммуникация как основа инклюзивного образования для детей с РАС\" реализуется при поддержке Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n\r\n\r\n\r\n\r\n', '/images/Absolut/2.jpg', '2021-11-19', '/images/Absolut/logo.jpg', '/images/Absolut/2.jpg;OUjIRQha59Y', '', 'Новости', '/news.php', 0, '', '9'),
(478, '12', 'Занятия на скалодроме', 'Девиз \"Выше, сильнее, быстрее\" на занятиях адаптивной физкультурой в \"РАСсвете\" приобрёл дополнительный смысл, после того, как в спортзале установили скалодром. Даже те ребята, кто поначалу опасался подниматься по вертикальной поверхности, со временем полюбили новый вид тренировки.', '<p>В спортивном зале \"РАСсвета\" появилось новое оборудование! Эспандеры для ног, гантели, гимнастические коврики, степы и... скалодром! Теперь любители забраться повыше смогут развивать свои умения, получат новые мотивирующие задания и выплеснут часть своей неиссякаемой энергии. А те, кто боится высоты, поработают над страхами, улучшат физическую форму.</p>\r\n<p>Занятия на скалодроме помогают детям концентрировать внимание, включать стратегическое мышление и зрительную память, слушать и выполнять инструкции.</p>\r\n<p>Плюсов у адаптивного скалолазания очень много, и мы про них ещё не раз расскажем и покажем. А пока слова благодарности! Собрать и установить скалодром в центре помогли родители наших воспитанников. Спасибо большое мужчинам, отцам, тем, кто в свой выходной день не пожалел сил и времени для обустройства зала! И, конечно, мы благодарим Благотворительный фонд Владимира Потанина, ведь занятия на скалодроме стали возможны после поддержки фондом нашего проекта \"Спорт без преград\".</p>\r\n<p>В рамках проекта наши инструкторы АФК недавно прошли практическую стажировку на профессиональном скалодроме. Изучили правила страховки и приёмы работы с детьми с аутизмом в тренировочных условиях на скале. </p>\r\n<p>После установки скалодрома девиз \"Выше, сильнее, быстрее\" на занятиях адаптивной физкультурой в \"РАСсвете\" приобрёл дополнительный смысл. Даже те ребята, кто поначалу опасался подниматься по вертикальной поверхности, со временем полюбили новый вид тренировки.</p>\r\n<p>Освоить \"скалу\" детям с аутизмом помогают инструкторы АФК, использующие Прикладной анализ поведения на занятиях. Дети учатся следовать инструкциям тренера, концентрировать свое внимание на сложных движениях, вырабатывая множество новых нейронных связей и паттернов движений. \r\n</p>\r\n<p>Инструктор АФК Дарья Бабкина рассказала об успехах своей ученицы на занятиях на скалодроме. </p>\r\n<p>- Когда я впервые встретилась с Соней, она многого не умела: прыгать, держать ровно спинку,  удерживать тяжелые гантели самостоятельно. Девочка очень боялась высоты и всегда просила о помощи при выполнении упражнений. Сейчас Соня самостоятельно прыгает, работает с хорошими весами, с удовольствием занимается на скалодроме.</p>\r\n<p>Еще один участник проекта \"Спорт без преград\" - Никита. В программе мальчика стоит физическая и адаптационная подготовка. Для развития мышечной выносливости как нельзя лучше подходит скалодром. Никита занимается на нем всего месяц, но уже делает успехи, несмотря на то, что ему тяжело даются движения в динамике. Преодолевающий режим восхождения по скале тренирует мышцы тела. За счёт динамики тренировки укрепление мышечного корсета происходит быстрее.</p>\r\n<p>Чтобы замотивировать ребёнка поработать на скалодроме, инструктор лепит яркие наклейки на липучках прямо на \"скалу\", на разную высоту. Наклейки разноцветные в виде геометрические форм: треугольник, квадрат, круг.\r\nЗадача ребёнка - подняться (самостоятельно или с поддержкой тренера), снять наклейку и либо отдать её инструктору, либо положить на пол к такой же по форме или цвету наклейке.\r\nДля тех ребят, кто любит сортировку, это упражнение - комбо удовольствия и развития.</p>\r\n<p></p>\r\n<p></p>\r\n<p>Проект \"Спорт без преград\" осуществляется при поддержке Благотворительный фонд Владимира Потанина.</p>\r\n\r\n\r\n\r\n', '/images/AFK/2021/12.jpg', '2021-11-23', '/images/AFK/logo.jpg;\r\n/images/AFK/2021/10.jpg;\r\n/images/AFK/2021/1.webp;\r\n/images/AFK/2021/2.webp;\r\n/images/AFK/2021/11.jpg;\r\n/images/AFK/2021/12.jpg', '', '', '', '', 0, '', '0'),
(479, '19', 'Развитие социальных навыков', 'В этом году в Иркутске впервые за историю образования города открыли ресурсный класс в школе 33 микрорайона Лесной. В нем занимается 8 учеников. Психолог ресурсного класса 33 школы Анастасия Шваркунова отметила, какие социальные навыки нужно развивать ученикам с РАС.', '<p>В этом году в Иркутске впервые за историю образования города открыли ресурсный класс в школе 33 микрорайона Лесной. В нем занимается 8 учеников. Летом перед учебным годом ребята усиленно занимались подготовкой к школе на базе \"РАСсвета\": отрабатывали учебное поведение, минимальные знания и умения, которые нужны первоклашкам, учились находиться в группе.</p>\r\n<p>Психолог ресурсного класса 33 школы Анастасия Шваркунова отметила, какие социальные навыки нужно развивать ученикам с РАС: навык просьбы, навыки приветствия/прощания, навык ждать своей очереди, навык слушать учителя, навык совместной деятельности со сверстниками.</p>\r\n<p>Чтобы помочь детям с РАС овладеть этими навыками, в ресурсном классе созданы специальные образовательные условия и трудятся квалифицированные кадры, владеющие методами Прикладного анализа поведения. Кроме учителя с первоклашками находятся тьюторы, их работу курирует специалист, который, в свою очередь, получает поддержку супервизора.</p>\r\n<p>В ресурсном классе активно используются визуальные подсказки: расписания, календари, карточки.</p>\r\n<p>- В классе есть ритуал приветствия и прощания. Во время групповых занятий с психологом и фронтальных уроков дети учатся взаимодействию, ожиданию, навыку слушаться педагога. С учениками Андреем и Димой мы отрабатываем навык ожидания используя карточку \"Жди\". Со всеми детьми оттачиваем навык просьбы, как во время уроков, так и во время перемен, - поделилась опытом Анастасия Шваркунова, психолог ресурсного класса.\r\n</p>\r\n<p>Благодарим организаторов и партнёров проекта \"Ресурсный класс - Регионы. Расширение практики доступного инклюзивного образования детей с РАС и другими ментальными нарушениями\": Фонд \"Выход\", Ассоциация \"Аутизм - Регионы\" и Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n\r\n\r\n\r\n', '/images/RK/21.11.30/1.jpg', '2021-11-30', '/images/RK/21.11.30/1.jpg;\r\n/images/RK/21.11.30/2.jpg;\r\n/images/RK/21.11.30/3.jpg;\r\n/images/RK/21.11.30/4.jpg;\r\n/images/RK/21.11.30/5.jpg;\r\n/images/RK/21.11.30/6.jpg;\r\n/images/RK/21.11.30/7.jpg', '', '', '', '', 0, '', '0'),
(481, '19', 'Социальные навыки детей с РАС и другими нарушениями развития', 'Тема о развитии социальных навыков у первоклашек ресурсного класса Иркутской школы №33 вызвала интерес у наших читателей. Поэтому мы решили продолжить её освещение, но уже основываясь на опыте педагогов и ребят ресурсного класса школы пос. Молодежный.', '<p>Тема о развитии социальных навыков у первоклашек ресурсного класса Иркутской школы №33 вызвала интерес у наших читателей. Поэтому мы решили продолжить её освещение, но уже основываясь на опыте педагогов и ребят ресурсного класса школы пос. Молодежный.</p>\r\n<p>Социальные навыки формируются в ходе всех режимных моментов: на групповых и индивидуальных занятиях, переменах, во время походов в столовую, раздевалку и т.д.</p>\r\n<p>Групповые уроки могут проходить в форме игры, активных подвижных упражнений, совместной творческой деятельности, развлечений. На этих занятиях формируются такие навыки, как:</p>\r\n<p> наблюдение за деятельностью других детей и подражание им,<br>\r\n инициирование контакта и адекватное привлечение внимания,<br>\r\n включение во взаимодействие, инициированное другим ребенком,<br>\r\n демонстрация другим людям различных предметов и результатов своей деятельности,<br>\r\n навыки просьбы/выполнения просьб других,<br>\r\n выполнение действий по очереди.</p>\r\n<p>В ресурсном классе детей учат здороваться и отвечать на приветствие, адекватно вести себя в присутствии одноклассников и нейротипичных сверстников, социально приемлемым образом привлекать к себе внимание и вежливо отказываться от нежелательной деятельности, делиться игрушками.</p>\r\n<p>Например, ученицу ресурсного класса Соню учат в ответ на то, что кто-то берет ее игрушку, не кричать, а спокойно сказать: \"Верни, это моё\". И не брать без спроса чужие игрушки - это правило проходят со всеми ребятами. </p>\r\n<p>А Стёпу, который не любит никому показывать свои поделки из пластилина, учат спокойно это делать, ведь это отличный повод вступить в коммуникацию со сверстниками из регулярных классов и подружиться. </p>\r\n<p>Перемена на обед - тоже время обучения первоклассников ресурсного класса, ребята развивают полезные навыки: ходить парами по коридору, обедать в столовой, убирать за собой посуду, самостоятельно покупать еду.</p>\r\n<p>Проект \"Центр развития инклюзивного образования\" реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n\r\n\r\n\r\n\r\n\r\n', '/images/RK/21.12.01/3.jpg', '2021-12-01', '/images/RK/21.12.01/1.jpg;\r\n/images/RK/21.12.01/2.jpg;\r\n/images/RK/21.12.01/3.jpg;\r\n/images/RK/21.12.01/4.jpg;\r\n/images/RK/21.12.01/5.jpg;\r\n/images/RK/21.12.01/6.jpg;\r\n/images/RK/21.12.01/7.jpg;\r\n/images/RK/21.12.01/8.jpg;\r\n/images/RK/21.12.01/9.png;\r\n/images/RK/21.12.01/10.png;\r\n/images/RK/21.12.01/11.png\r\n', '', '', '', '', 0, '', '10'),
(483, '20', 'Карточки \"СНАЧАЛА-ПОТОМ\"', 'Зачастую у детей с аутизмом есть проблемы с пониманием времени. Фразы: \"подожди 2 минуты\", \"осталось немного до конца занятия\" и т.п. дети не понимают, а значит, испытывают тревогу ещё по одному поводу. Сделать абстрактное более реальным поможет визуальное представление промежутка времени. Например, планшетка с карточками \"сначала-потом\" - визуальная последовательность из 2 изображений.', '<p>Зачастую у детей с аутизмом есть проблемы с пониманием времени. Фразы: \"подожди 2 минуты\", \"осталось немного до конца занятия\" и т.п. дети не понимают, а значит, испытывают тревогу ещё по одному поводу.</p> <p> Сделать абстрактное более реальным поможет визуальное представление промежутка времени. Например, планшетка с карточками \"сначала-потом\" - визуальная последовательность из 2 изображений. На первой картинке, как правило, стоит какая-то задача, выполнить которую ребёнку может быть сложно или неприятно: посещение врача, учебное задание, поездка на транспорте... На второй - то, что ребёнок рад будет получить или сделать: любимые угощения или активность...</p>\r\n<p>Планшетка \"сначала-потом\" помогает замотивировать ребёнка при обучении выполнению инструкций, новых навыков. Если вы работаете над навыком надевания одежды, и ребёнок уже умеет одеваться, можно показать ему планшетку с карточками и дать короткую устную инструкцию: \"сначала одеваешься, потом гуляем\".</p>\r\n<p>Если ребёнок отвлекается, пока выполняет инструкцию, ему можно напомнить об условиях задачи, показывать планшетку и добавлять, что до того, как он пойдёт на прогулку, ему нужно надеть конкретные вещи.\r\n</p>\r\n<p>Чтобы ребёнок понял и принял идею карточек \"сначала-потом\" он должен получить поощрение (в данном примере - прогулка) сразу после выполнения инструкции.\r\n</p>\r\n<p>Для первых проб с карточками \"сначала-потом\" подойдут такие занятия, выполнять которые ребёнку будет просто и легко.</p>\r\n<p>Планшетка \"сначала-потом\" может послужить первым этапом для освоения более сложных визуальных поддержек, таких как визуальное расписание. Но об этом в другой раз.</p>\r\n<p>Проект \"Социальные навыки и альтернативная коммуникация как основа инклюзивного образования для детей с РАС\" реализуется при поддержке Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n<p>При написании использовались материалы фонда \"Выход\".</p>\r\n\r\n\r\n\r\n', '/images/Absolut/5/2.jpg', '2021-12-09', '/images/Absolut/logo.jpg;\r\n/images/Absolut/5/3.jpg', '', '', 'Новости', '/news.php', 0, '', '9'),
(487, '20', 'Визуальное расписание', 'Зрительная поддержка помогает детям с РАС концентрироваться и удерживать внимание, облегчает понимание времени, последовательности, причинно-следственных связей. Сегодня поговорим о визуальном расписании - системе, которая наглядно показывает, что и когда будет происходить.', '<p>При обучении детей с аутизмом стоит опираться на правило: \"говорите меньше, больше используйте зрительную поддержку\".\r\nЗрительная поддержка помогает детям с РАС концентрироваться и удерживать внимание, облегчает понимание времени, последовательности, причинно-следственных связей. Сегодня поговорим о визуальном расписании - системе, которая наглядно показывает, что и когда будет происходить.</p>\r\n<p>Визуальное расписание не походит на устоявшийся список дел, оно должно всё время меняться, чтобы развить у ребёнка самостоятельность, предупредить его об изменениях, снизить тревогу, упростить переход от одной деятельности к другой и помочь обобщить навыки.</p>\r\n<p>Расписание может быть групповым (предупреждает ребят о предстоящих активностях и распределяет зоны ответственности) и индивидуальным.</p>\r\n<p>Самый простой способ сделать визуальное расписание - разместить на стене на уровне глаз ребёнка самоклеющуюся ленту-липучку (продаются в текстильных магазинах), подготовить набор карточек с нарисованными или активностями (фото пластилина для занятия лепкой, книга - для чтения, горки - для обозначения катания на санках и т.п.). Карточки ламинируются и с обратной стороны к ним также прикрепляется лента-липучка. Каждый день набор карточек на ленте должен меняться в соответствии с расписанием ребёнка.</p>\r\n<p>Более мобильная версия расписания - те же карточки на ленте-липучке на пластиковой или ламинированной картонной планшетке.</p>\r\n<p>Если ребёнок умеет читать, вместо картинок на карточках могут быть записаны названия активностей: отдельные слова или даже фразы, предложения (в зависимости от уровня навыков ребёнка).</p>\r\n<p>Расписание можно составлять на целый день или только на его часть. Важно, чтобы оно находилось в доступном для ребёнка месте и он всегда мог свериться с расписанием.</p>\r\n<p>Проект осуществляется при поддержке Благотворительный фонд \"Абсолют-Помощь\".\r\n</p>\r\n<p>При написании использовались материалы из книги \"Дети с РАС в детском саду и школе\", авторы С. Довбня, Т. Морозова, А. Залогина, И. Монова.</p>\r\n\r\n\r\n\r\n', '/images/Absolut/6/1.jpg', '2021-12-18', '/images/Absolut/logo.jpg;\r\n/images/Absolut/6/4.jpg;\r\n/images/Absolut/6/2.jpg;\r\n/images/Absolut/6/3.jpg', '', '', 'Новости', '/news.php', 0, '', '0'),
(488, '19', 'Уроки в ресурсном классе ', 'Ученики ресурсного класса школы посёлка Молодежный совсем скоро окончат вторую четверть. Порадуемся их успехам вместе с учителем ресурсного класса поведенческим аналитиком и олигофренопедагогом Еленой Михеевой. Елена расскажет о занятиях детей с аутизмом. ', '<p>В конце декабря совсем не хочется думать о работе или учебе, повсюду витает праздничное настроение, все ждут длинных выходных и каникул. Ученики ресурсного класса школы посёлка Молодежный совсем скоро окончат вторую четверть. Пока порадуемся их успехам вместе с учителем ресурсного класса поведенческим аналитиком и олигофренопедагогом Еленой Михеевой. Елена расскажет о занятиях детей с аутизмом. \r\n</p>\r\n<p>- Наш класс создан с учетом всех указаний методического пособия \"Ресурсный класс\", рекомендованного Министерством образования РФ в 2017 году.  И, конечно же, в  классе используются научно доказанные методики работы с детьми с РАС.</p>\r\n<p>Ученики ресурсного класса учатся в школе посёлка Молодежный второй год, период адаптации позади, ученики начинают выходить в инклюзию в регулярные классы.</p>\r\n<p>Все дети обучаются по адаптированным образовательным программам, согласно их образовательного маршрута. Занятия проходят в групповом и индивидуальном формате, весь материал адаптируется под учеников класса. Для поддержки мы активно используем визуальное расписание, правила поведения в классе, социальные истории.</p>\r\n<p>Ученики ресурсного класса Степа и Влад учатся решать задачи. Влад учится решать примеры на сложение через десяток, изначально для этого мы использовали нумикон, а сейчас мальчик начал решать их в уме.</p>\r\n<p>Ваня и Ника учатся печатать на компьютере на слух, но одновременно с этим мы работаем и над развитием их графомоторных навыков, чтобы дети в последующем могли освоить письменную речь.</p>\r\n<p>Соня осваивает решение примеров на вычитание с использованием нумикона, который очень помогает ребенку.</p>\r\n<p>Сейчас занятия ведутся в дистанционном режиме, ребята выходят на уроки онлайн и очень скучают по школе. </p>\r\n<p>Проект \"Центр развития инклюзивного образования для детей с РАС по модели\" Ресурсный класс\" реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n\r\n\r\n\r\n\r\n\r\n', '/images/RK/21.12.21/1.jpg', '2021-12-21', '/images/RK/21.12.21/1.jpg;\r\n/images/RK/21.12.21/2.jpg;\r\n/images/RK/21.12.21/3.jpg;\r\n/images/RK/21.12.21/4.jpg;\r\n/images/RK/21.12.21/5.jpg;\r\n/images/RK/21.12.21/6.jpg;\r\n/images/RK/21.12.21/7.jpg;\r\n/images/RK/21.12.21/8.jpg;\r\n/images/RK/21.12.21/9.jpg;\r\n/images/RK/21.12.21/10.jpg\r\n', '', '', '', '', 0, '', '10'),
(489, '16', 'Перенимаем опыт', 'Продолжаем повышать профессиональные компетенции наших педагогов. Сотрудники  \"РАСсвета\" прошли очную стажировку в Москве в единственном на всю Россию инклюзивном сертифицированном детском саду АВА-центра \"Моя Планета\" .', '<p>Продолжаем повышать профессиональные компетенции наших педагогов. Сотрудники  \"РАСсвета\" прошли очную стажировку в Москве в единственном на всю Россию инклюзивном сертифицированном детском саду АВА-центра \"Моя Планета\" .</p>\r\n<p>Поведенческий аналитик Елена Михеева и АВА-инструктор Фарида Сабирова перенимали опыт московских коллег на протяжении двух дней, изучали работу педагогов и тьюторов детсада на индивидуальных и групповых занятиях с детьми, в том числе, работу с системой альтернативной коммуникации PECS.</p>\r\n<p>Полученный опыт специалисты будут применять на занятиях в \"РАСсвете\": с начала осени в нашем центре дети с #аутизмом обучаются не только в индивидуальном формате, но и в групповом. Есть группа для дошколят, для детей, общающихся при помощи карточек PECS, и ребят постарше - школьного возраста. \r\n</p>\r\n<p>Также АВА-инструктор \"РАСсвета\" Дарья Евдокимова прошла дистанционное обучение в центре \"Моя Планета\" по программе \"Система альтернативной коммуникации PECS. Модуль 1\". Кроме теории, на курсе была практика - в мини-группах инструкторы тренировали друг на друге процедуру коррекции ошибки - один из самых сложных аспектов PECS. </p>\r\n<p>Проект \"Социальные навыки и альтернативная коммуникация как основа инклюзивного образования для детей с РАС\" реализуется при поддержке Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n\r\n\r\n\r\n\r\n', '/images/Absolut/7/1.jpg', '2021-12-22', '/images/Absolut/logo.jpg;\r\n/images/Absolut/7/1.jpg;\r\n/images/Absolut/7/2.jpg', '', '', 'Новости', '/news.php', 0, '', '9'),
(492, '20', 'Подготовка к активным играм', '\"Дети с аутизмом не любят и не умеют играть\" - один из распространённых мифов о РАС. Но не умеют, не значит не могут. Если нейротипичный ребёнок мимолетно улавливает суть игры, то для детей с аутизмом требуется большая подготовительная работа.', '<p>\"Дети с аутизмом не любят и не умеют играть\" - один из распространённых мифов о РАС. Но не умеют, не значит не могут. Если нейротипичный ребёнок мимолетно улавливает суть игры, то для детей с аутизмом, из-за особенностей восприятия информации, требуется большая подготовительная работа, чтобы \r\nсюжетно-ролевые и активные спортивные игры стали им доступны и желанны.</p>\r\n<p>Можно научить ребёнка с аутизмом бросать и ловить мяч (одному или в команде), кататься на самокате, роликах, велосипеде, действовать в эстафете, выполняя серии заданий и спокойно ждать своей очереди. Предварительно необходимо \"прокачать\" определённые навыки.</p>\r\n<p>- Слабая мотивация к получению новых знаний и сотрудничеству - это ахиллесова пята детей с РАС. Поэтому первая цель - установление сотрудничества или руководящего контроля. То есть, когда мы просим ребёнка что-то сделать и он делает это.</p>\r\n<p>- Без понимания инструкций ребёнку очень сложно научиться чему-либо. Поэтому важно развивать навык слушателя, обучать различать на слух слова и инструкции, касающиеся конкретно игровых заданий: \"беги\", \"бросай\", \"наклонись\" и т.п.</p>\r\n<p>- Навык имитации: начиная с простого \"сделай так\" до повтора нескольких действий подряд.</p>\r\n<p>- Развитие визуального восприятия, умения использовать визуальную опору. Визуальная поддержка (карточки с изображением активностей, планшетку \"сначала-потом\", социальные истории и т.п.)решает несколько задач: служит подсказкой ребёнку при выполнении инструкций, может предотвратить проблемное поведение.</p>\r\n<p>- Для многих активных игр ребёнка важно подготовить находиться в группе с другими людьми, так как некоторые дети с РАС испытывают сложности такого рода. </p>\r\n<p>- Развивать навыки социального взаимодействия нужно постепенно: поощрять нахождение одного ребёнка с другим в течение одной, несколько минут без проявления проблемного поведения. Постепенно время увеличивается,  ребёнка учат обращаться к сверстнику с просьбой (при помощи альтернативной коммуникации или речи). Также можно использовать поощрения, которые нравятся группе детей (прыжки на батуте, сухой бассейн с шариками и т.п.).</p>\r\n<p>Проект \"Социальные навыки и альтернативная коммуникация как основа инклюзивного образования для детей с РАС\" реализуется при поддержке Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n\r\n\r\n\r\n\r\n', '/images/Absolut/8/1.jpg', '2021-12-23', '/images/Absolut/logo.jpg;\r\n/images/Absolut/8/1.jpg;\r\n/images/Absolut/8/2.jpg;\r\n/images/Absolut/8/3.jpg;\r\n/images/Absolut/8/4.jpg', '', '', 'Новости', '/news.php', 0, '', '9');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(494, '18', 'Творчество - это свобода', 'Мастерская \"Дом синего слона\" — это особое место, где дети с аутизмом находят свободу самовыражения и общения. Здесь каждый ребёнок может раскрыться, проявляя свои таланты и интересы, ведь творчество — это именно та сфера, где ошибки невозможны. ', '<p>Мастерская \"Дом синего слона\" — это особое место, где дети с аутизмом находят свободу самовыражения и общения. Здесь каждый ребёнок может раскрыться, проявляя свои таланты и интересы. Мы понимаем, насколько важно давать детям возможность творить без ограничений, ведь творчество — это именно та сфера, где ошибки невозможны. </p>\r\n<p>Мы внимательно подбираем темы занятий, ориентируясь на современные тренды и потребности детей. Например, стильные бетонные кашпо стали настоящим хитом среди наших подопечных. Однако мы всегда учитываем возраст и индивидуальные особенности ребят. Если семилетний малыш пока не готов работать с иглой и ниткой, мы предлагаем альтернативные виды деятельности, чтобы поддерживать интерес и развивать навыки.</p>\r\n<p>Регулярность занятий играет ключевую роль. Даже небольшие перерывы могут замедлить прогресс, поэтому мы стараемся поддерживать постоянство и структуру обучения. Это помогает детям чувствовать себя уверенно и комфортно в процессе творчества.</p>\r\n<p>Одной из последних новинок в нашей мастерской стали свечи с деревянным фитилём. Они не только украшают пространство, но и создают особую атмосферу благодаря потрескиванию древесины при горении. А новое направление — работа с стабилизированным мхом — позволяет ребятам почувствовать себя настоящими декораторами. Мягкий и пушистый мох, которым мы оформляем кашпо и вазы, добавляет природной красоты в любой интерьер.</p>\r\n<p>Наши педагоги — это не просто профессионалы своего дела. Многие из них — родители детей с аутизмом, которые понимают важность поддержки и развития ребёнка. По выходным они с радостью приходят в мастерскую, несмотря на усталость после рабочей недели. Особенно приятно видеть, как семьи объединяются вокруг общего дела, создавая уникальные изделия вместе.</p>\r\n<p>Впереди у нас много планов и идей. Мы верим, что каждое занятие в \"Доме синего слона\" станет маленьким шагом к большой цели — раскрытию потенциала каждого ребёнка.</p>\r\n<p>Фото: Анна Попова</p>\r\n<p>АНО \"АПЦ Рассвет\" - участник проекта \"Создание сети творческих досуговых центров для молодых людей с РАС \"Культурный код - возможность для каждого\"\". Благодарим организаторов и партнеров проекта: Ассоциация \"Аутизм - Регионы\", Фонд \"Выход\". Проект реализуется с использованием гранта Президента Российской Федерации на реализацию проектов в области культуры, искусства и креативных (творческих) индустрий Президентский фонд культурных инициатив.</p>', '/images/DSS/21.12.26/9.jpg', '2021-12-26', '/images/DSS/21.12.26/1.jpg;\r\n/images/DSS/21.12.26/2.jpg;\r\n/images/DSS/21.12.26/3.jpg;\r\n/images/DSS/21.12.26/4.jpg;\r\n/images/DSS/21.12.26/5.jpg;\r\n/images/DSS/21.12.26/6.jpg;\r\n/images/DSS/21.12.26/7.jpg;\r\n/images/DSS/21.12.26/8.jpg;\r\n/images/DSS/21.12.26/9.jpg', '', '', '', '', 0, '', '19'),
(495, '20', 'Совместные игры', 'Навыки общения и взаимодействия лучше всего дети осваивают в игровой форме, и неважно, будь то ребёнок с особенностями развития или без них - так утверждает психолог \"РАСсвета\" Анна Леонтьева. Мы подготовили интервью с Анной на тему совместных игр нейротипичных и нейроотличных детей.', '<p>Навыки общения и взаимодействия лучше всего дети осваивают в игровой форме, и неважно, будь то ребёнок с особенностями развития или без них - так утверждает психолог \"РАСсвета\" Анна Леонтьева. Мы подготовили интервью с Анной на тему совместных игр нейротипичных и нейроотличных детей.</p>\r\n<p>- Сегодня у родителей детей с РАС, как правило, нет возможности выбирать компанию для совместного времяпровождения их детей с нейротипичными сверстниками. Где им искать товарищей по играм для ребёнка?</p>\r\n<p>- Родителям нужно проявлять инициативу, искать компанию для ребёнка на детских площадках, в игровых комнатах, в спортивных секциях и творческих мастерских. Стараться сблизиться с родителями нейротипичных детей и устраивать семейные званные обеды, ужины, общие прогулки, отмечать вместе дни рождения детей.</p>\r\n<p>Однако есть важный момент: перед тем, как заняться поиском компании для ребёнка с РАС, нужно снизить частоту проявлений его нежелательного поведения, поработать над руководящим контролем, сенсорным развитием, коммуникативными навыками (ребёнка минимум следует научить сообщать свои желания и понимать простые инструкции). Без предварительной большой подготовки дети с РАС не смогут наладить общение с другими детьми.</p>\r\n<p>- Какие этапы подготовки дети с РАС проходят на занятиях психолога перед совместными играми с нейротипичными сверстниками?</p>\r\n<p>- Сначала надо определить, какие игровые навыки пригодятся ребёнку и отработать их на индивидуальных занятиях.</p>\r\n<p>Когда навыки отработаны (бросание и ловля мяча, к примеру, или собирание пазлов, конструктора), мы переходим к совместному времяпровождению двух детей: нейротипичного и нейроотличного.</p>\r\n<p>Дети должны привыкнуть друг к другу, получать удовольствие от этих встреч. Для этого педагог предлагает занятия, которые помогут им сблизиться и будут одинаково интересны для обоих: катание на парных качелях, игрушечная железная дорога, прыжки на батуте и т.п.</p>\r\n<p>Нужно поощрять и стимулировать общение детей не только во время занятия, но и до, после и в перерывах.</p>\r\n<p>- Время совместного пребывания детей всегда фиксированно в рамках одного занятия или постепенно увеличивается по минутам?</p>\r\n<p>- Постепенно увеличивается. Главное не давить, детям должно быть комфортно находится рядом друг с другом. Когда дети уже долгое время спокойно находятся вместе, мы переходим к обучению игре.</p>\r\n<p>- Обучение происходит в рамках 3 блоков, не более 3 минут каждый. Причём, дети не должны даже догадываться, что их чему-то учат. Все происходит в игровой форме. Каждый блок посвящён разным видам деятельности, выбирать надо интерактивные занятия, интересные обоим детям и только те, которые уже знакомы ребёнку с РАС из предыдущих занятий.</p>\r\n<p>- В какие игры можно сыграть детям вместе?</p>\r\n<p>- Покатать машинки, построить для них из кубиков гараж, собрать пазлы, слепить что-то по образцу из пластилина, приготовить \"еду\" куклам, сыграть в съедобное - несъедобное, прятки, \"море волнуется раз\", \"кошки-мышки\", искать клад, играть в магазин, в настольные игры и т.д. Выбор большой.</p>\r\n<p>- На что нужно обращать внимание во время обучения игре?</p>\r\n<p>- Не разрешайте нейротипичному ребёнку выполнять задания за ребёнка с РАС. Если есть какой-то раздаточный материал для занятия, нужно привлекать детей, чтобы они передавали его друг другу. Всегда подкрепляйте детей за сотрудничество с педагогом и друг с другом.</p>\r\n<p>Стоит также поощрять спонтанное поведение в игре над прописанным в сценарии. Если оно положительное, конечно. Чтобы дать место спонтанности, педагог не должен торопиться вмешиваться в игру с подсказками. При этом надо всегда иметь план Б, если игра вдруг остановилась или дело принимает опасный поворот.</p>\r\n<p>Если ребёнок с РАС отбирает игрушки у сверстника, помогите ему вернуть их назад.</p>\r\n<p>Сначала нужно отдавать предпочтение парным играм, позднее организовывать совместные игры с 3 - 4 и более детьми одновременно. Чтобы ребёнок с РАС мог узнать стиль игры разных детей.</p>\r\n<p>Проект \"Социальные навыки и альтернативная коммуникация как основа инклюзивного образования для детей с РАС\" реализуется при поддержке Благотворительного фонда \"Абсолют-Помощь\".</p>\r\n\r\n\r\n\r\n\r\n\r\n', '/images/Absolut/9/1.jpg', '2021-12-27', '/images/Absolut/logo.jpg;\r\n/images/Absolut/9/1.jpg;\r\n/images/Absolut/9/2.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', '9'),
(496, '22', 'Новогодние подарки от наших друзей', 'В \"РАСсвете\" проходят праздничные программы для наших ребят: Дед Мороз и Снегурочка водят хороводы, поют песни и устраивают весёлые конкурсы. За подарки для наших ребят большое спасибо нашим давним друзьям и спонсорам торговой компании \"Элит Трейд\". \r\n', '<p>До Нового года осталось совсем немного. В \"РАСсвете\" проходят праздничные программы для наших ребят: Дед Мороз и Снегурочка водят хороводы, поют песни и устраивают весёлые конкурсы.</p>\r\n<p>Специально не собираем всех детей в одном зале в одно время - соблюдаем меры безопасности в пандемию и помним, что для ребёнка с РАС шумный праздник с большим количеством гостей - стресс, а не развлечение. Поэтому создаём новогоднюю атмосферу в малых группах.\r\n</p>\r\n<p>А чтобы праздник удался - нужны подарки. За подарки и сказку, подаренную детям, большое спасибо нашим давним друзьям и спонсорам торговой компании \"Элит Трейд\". </p>\r\n<p>Делимся с вами праздничным настроением и нашими фото!</p>', '/images/news/TD2021/5.jpg', '2021-12-30', '/images/news/TD2021/1.jpg;\r\n/images/news/TD2021/2.jpg;\r\n/images/news/TD2021/3.jpg;\r\n/images/news/TD2021/4.jpg;\r\n/images/news/TD2021/5.jpg;\r\n/images/news/TD2021/6.jpg;\r\n/images/news/TD2021/7.jpg;\r\n/images/news/TD2021/8.jpg', '', '', 'Новости', 'news.php', 0, '', '0'),
(497, '20', 'Источники по системам альтернативной коммуникации', 'При формировании коммуникативных навыков у детей с аутизмом АВА-тераписты ориентируются на сильные стороны ребенка: если визуальное восприятие по результатам тестирования, развито сильнее, чем навыки имитации, как правило, выбирают альтернативную систему коммуникации PECS, если обратная ситуация - жестовый язык.', '<p>Ребёнок не говорит или говорит мало и трудноразличимо, но общаться с ним нужно. Как - большой вопрос. На помощь приходит альтернативная коммуникация. Её наиболее распространённые виды:</p>\r\n<p>- Система PECS</p>\r\n<p>- Электронные пособия с голосовыми синхронизаторами</p>\r\n<p>- Блисс – метод</p>\r\n<p>- Леб – система</p>\r\n<p>- Метод пиктограмм</p>\r\n<p>- Макатон</p>\r\n<p>- Жестовый язык</p>\r\n<p>В России описаны опыт применения и рекомендации к применению системы PECS , Леб, Блисс, Макатон.</p>\r\n<p>При формировании коммуникативных навыков у детей с аутизмом АВА-тераписты ориентируются на сильные стороны ребенка: если визуальное восприятие по результатам тестирования, развито сильнее, чем навыки имитации, как правило, выбирают альтернативную систему коммуникации PECS, если обратная ситуация - жестовый язык.</p>\r\n<p>Как показывает практика, у 90% детей с РАС сильная сторона - визуальное восприятие.\r\nСпециалисты \"РАСсвета\" отдают предпочтение PECS, т.к. эта система учит ребенка вступать в коммуникацию с партнёром, каждый её этап ввода прописан и можно отслеживать динамику её освоения с помощью чек-листов, что очень важно при обучении детей для своевременной корректировки программы.</p>\r\n<p>Если специалисты выбирают для ребенка как средство альтернативной коммуникации жестовый язык, то жесты будут адаптированы для ребенка с РАС.</p>\r\n<p>Мы рекомендуем к ознакомлению основные источники, к которым наши педагоги постоянно обращаются:</p>\r\n<p>1. \"Система альтернативной коммуникации с помощью карточек (PECS)\", авторы Л. Фрост и Э. Бонди. Эта книга – руководство для использования карточек и визуальных расписаний в общении и обучении ребёнка с РАС.</p>\r\n<p>2. \"Введение в альтернативную и дополнительную коммуникацию: жесты и графические символы для людей с двигательными и интеллектуальными нарушениями, а также с расстройствами аутистического спектра\". Авторы С. Течнер и Х. Мартинсен рассказывают про формы и средства альтернативной и дополнительной коммуникации, о том, как в разных случаях подбирать техники общения в обучении.</p>\r\n<p>3. Сайт <a href=\"http://www.surdoserver.ru/\">\"Сурдосервер 2.0\"</a></p>\r\n<p>4. Сообщество по обучению русского жестового языка <a href=\"https://jestov.net/\">\"Город жестов: перезагрузка\"</a></p>\r\n<p>Проект \"Социальные навыки и альтернативная коммуникация как основа инклюзивного образования для детей с РАС\" реализуется при поддержке Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n\r\n\r\n', '/images/RK/20.11.24/22.jpg', '2021-12-30', '/images/Absolut/logo.jpg;\r\n/images/Absolut/10/1.jpg;\r\n/images/Absolut/10/2.jpg', '', '', 'Новости', '/news.php', 0, '', '9'),
(503, '22', 'Новые подарки', 'Что может быть лучше новогодних подарков? Подарки, которые нам вручают после Нового года! Ребят после каникул ждут презенты от наших друзей - компании \"Иркутскэнергосбыт\". Спасибо за продолжение праздника!\r\n', '<p>Что может быть лучше новогодних подарков? Подарки, которые нам вручают после Нового года! Ребят после каникул ждут презенты от наших друзей - компании \"Иркутскэнергосбыт\". Спасибо за продолжение праздника!\r\n</p>', '/images/news/48.jpg', '2022-01-07', '', '', '', 'Новости', 'news.php', 0, '', '0'),
(505, '20', 'Оценка навыков', 'В АВА-терапии оценка навыков детей проходит в нескольких функциональных областях развития, всегда конкретно показывает, что именно ребенок умеет и на каком уровне. Тестирования предназначены для разработки комплексных индивидуальных программ обучения.', '<p>В АВА-терапии оценка навыков детей проходит в нескольких функциональных областях развития, всегда конкретно показывает, что именно ребенок умеет и на каком уровне. Тестирования предназначены для разработки комплексных индивидуальных программ обучения.</p>\r\n<p>Специалисты \"РАСсвета\" используют 3 основных инструмента для оценки навыков:</p>\r\n<p>1. VB-MAPP «Программы оценки навыков речи и социального взаимодействия для детей с аутизмом и другими нарушениями развития». </p>\r\n<p>170 навыков, которые оцениваются VB-MAPP, соответствуют основным этапам развития типично развивающихся детей в возрасте до 4 лет включительно. В программе тестирования - диагностический протокол и руководство по составлению индивидуальной программы формирования новых навыков. </p>\r\n<p>2. ABLLS-R  - \"Оценка базовых речевых и учебных навыков\" - содержит информацию об умениях, необходимых для успешного общения и обучения в естественной среде. В общей сложности тест охватывает  544  навыка, которые соответствуют основным этапам развития типично развивающихся детей от 0 до 8 лет. </p>\r\n<p>Подробнее о VB-MAPP и  ABLLS-R мы писали ранее в наших публикациях.</p>\r\n<p>3. AFLS \"Оценка функциональных и жизненно важных навыков: базовые навыки, быт, поведение в обществе\". Этот инструмент помогает определять уровень развития навыков самостоятельности и самопомощи у детей. В \"РАСсвете\" протоколы AFLS появились в конце прошлого года, и по ним сразу начали вести работу. </p>\r\n<p>AFLS состоит из 3 разделов:</p>\r\n<p>- оценка жизненно необходимых навыков: безопасного поведения, гигиенических, коммуникационных, самостоятельного одевания и засыпания и т.п.</p>\r\n<p>- оценка навыков выполнения домашних обязанностей (уборка, приготовление еды, стирка и т.п.) и навыков досуга.</p>\r\n<p>- оценка навыков поведения в обществе (умение самостоятельно передвигаться по улице, обращаться за помощью, совершать покупки,пользоваться телефоном и т.д.).</p>\r\n<p>Проект \"Центр развития инклюзивного образования для детей с РАС по модели\" Ресурсный класс\" реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n\r\n\r\n', '/images/RK/2022/1/1.jpg', '2022-01-11', '/images/RK/2022/1/1.jpg;\r\n/images/RK/2022/1/2.jpg', '', '', 'Новости', '/news.php', 0, '', '10'),
(509, '4', 'Коммуникативные группы', 'В сентябре стартовал новый проект при поддержке Благотворительного Фонда «Абсолют-Помощь», в рамках которого заработали групповые занятия, направленные на развитие социальных навыков и альтернативной коммуникации у детей с аутизмом.', '<p>В сентябре стартовал новый проект при поддержке Благотворительного Фонда «Абсолют-Помощь», в рамках которого заработали групповые занятия, направленные на развитие социальных навыков и альтернативной коммуникации у детей с аутизмом.</p>\r\n<p>Родители 15 детей-участников проекта прошли анкетирование на выявление нарушений коммуникативных навыков, уровня визуального восприятия и МАНД-реакций (умения просить). Для каждого ребёнка определены стратегии по введению карточек PECS, составлены чек-листы для сбора результатов освоения навыков коммуникации.</p>\r\n<p>Подводим маленькие, но позитивные итоги работы наших групповых занятий. С начала осени в центре открылось 3 направления: подготовка к школе, обучение альтернативным методам коммуникации, дошкольная группа.\r\n</p>\r\n<p>Невербальных дошкольников объединили в коммуникативную группу с использованием альтернативной системы коммуникации PECS. Главные задачи, которые стоят перед специалистами центра – уменьшить частоту возникновения нежелательного поведения у детей, научить их взаимодействовать друг с другом, а также выполнять инструкции учителя, находясь в составе группы.</p>\r\n<p>В первый месяц занятий в группах проходило тестирование навыков детей, на основании тестирования составлена программа развития коммуникативных навыков и индивидуальные рекомендации. Инструкторы центра расширяли мотивационную сферу у детей, обучали различению карточек PECS. Четырехчасовое занятие дробилось на упражнения в парах и в полном составе группы. Это делалось для того, чтобы дети привыкли к выполнению требований параллельно с другими детьми.</p>\r\n<p>Во всех трёх группах дети с РАС развивают свои коммуникативные, социальные, бытовые и академические навыки с использованием принципов прикладного анализа поведения. АВА-инструкторы, психолог и логопед сопровождают ребят на занятиях, куратор контролирует правильность выполнения разработанных индивидуальных программ, корректирует их по необходимости.</p>\r\n<p>Дети постепенно включались в работу группы - сначала их объединяли по парам для выполнения заданий, затем подключали ещё одного ребёнка... Теперь все участники группы могут выполнять общее задание, отвечать по очереди, участвовать вместе в игре.</p>\r\n<p>Чтобы дети адаптировались к групповому формату, с ними велась работа над развитием навыков совместного внимания, просьб, умения ждать очереди, понимать фронтальную инструкцию и т.п.\r\n</p>\r\n<p>Во всех 3 группах дети показывают успехи. У дошкольников один из ребят пошёл в детский сад, где находится пока неполный день, но занимается вместе с нейротипичными сверстниками, не проявляя нежелательного поведения.\r\nДевочки и мальчики из группы подготовки к школе освоили учебное поведение, научились взаимодействовать с педагогами и друг с другом.\r\nРебята из группы обучения альтернативной коммуникации значительно улучшили свои коммуникативные навыки, начали осваивать систему PECS.</p>\r\n<p>После зимних каникул коммуникативные группы в центре объединяются: дети адаптировались к индивидуальным занятиям и к работе в группе по 2-3 человека, теперь им надо научиться заниматься в группе из 5-6 человек.</p>\r\n<p>Дошкольники в обновлённой группе вместе выполняют рутины (моют руки, завтракают, убирают за собой посуду, вытирают столы и пр.), спокойно сидят за столом во время выполнения задания, ждут своей очереди в игре, знают основные правила в активных играх и с удовольствием проводят время вместе.</p>\r\n<p>В группе PECS, где дети учатся общаться с помощью альтернативной коммуникации и осваивают бытовые и академические навыки, ребята стали быстрее осваивать новый материал. Процент ошибок в выполненных заданиях значительно уменьшился, что позволяет в ближайшее время перейти на следующий этап обучения PECS.</p>\r\n<p>Дети из группы подготовки к школе проходят счёт в пределах 20, учатся глобальному чтению, работе с ножницами и бумагой, пишут прописи и осваивают учебное поведение.\r\n</p>\r\n<p>Проект \"Социальные навыки и альтернативная коммуникация как основа инклюзивного образования для детей с РАС\" реализуется при поддержке Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n\r\n\r\n', '/images/Absolut/13/1.webp', '2022-01-23', '/images/Absolut/logo.jpg;\r\n/images/Absolut/13/1.webp;\r\n/images/Absolut/13/2.webp;\r\n/images/Absolut/13/3.webp;\r\n/images/Absolut/13/4.webp;\r\n/images/Absolut/13/5.webp', '', '', 'Новости', '/news.php', 0, '', '9'),
(512, '20', 'Этапы обучения с помощью карточек PECS', 'В \"РАСсвете\" группа дошкольников с прошлого года обучается по системе альтернативной коммуникации PECS (с английского - \"коммуникационная система обмена изображениями\"). Есть 6 этапов введения карточек PECS.', '<p>В \"РАСсвете\" группа дошкольников с прошлого года обучается по системе альтернативной коммуникации PECS (с английского - \"коммуникационная система обмена изображениями\").</p>\r\n<p>Есть 6 этапов введения карточек PECS.</p>\r\n<p>На 1 этапе ребёнка учат передавать педагогу карточку с изображением предмета, который ребёнок желает. Например, если ему нравится прыгать на батуте, на карточке должно быть фото батута.\r\n</p>\r\n<p>Обучение проводят с полной физической подсказкой. Желательно, чтобы в процессе участвовало 2 взрослых: педагог и его помощник. Когда ребёнок тянется к батуту, помощник вкладывает в его руку карточку и передаёт её педагогу, который затем отводит ребёнка на батут.\r\n</p>\r\n<p>Важно на первом этапе на планшетке прикреплять всего одну карточку PECS, чтобы не путать ребёнка.</p>\r\n<p>Когда ребёнок самостоятельно даёт карточку педагогу, пора переходить на второй этап обучения. </p>\r\n<p>2 этап называется \"Расстояние и настойчивость\": сначала ребёнка обучают приносить карточку педагогу, когда тот находится на расстоянии от него, а планшетка при этом рядом. Потом наоборот - педагог рядом с ребёнком, но за планшеткой с карточкой нужно пройтись. Затем ребёнок, педагог и планшетка с карточкой должны находится на равном удалении друг от друга.</p>\r\n<p>3 Этап делится на 2 шага:\r\n</p>\r\n<p>1. Ребёнок учится различать карточку с изображением того предмета, который он хочет получить и карточку с изображением предмета, который ему не интересен.</p>\r\n<p>2. Ребёнок учится различать карточки с изображениями предметов, которые ему интересны.</p>\r\n<p>На 4 этапе к уже имеющимся карточкам PECS на планшетку добавляется шаблон для предложений - карточка \"Я хочу\". Ребенок учится построению предложения со свойствами предметов (\"Я хочу синий мяч\"). И  в дальнейшем учится читать по шаблону. </p>\r\n<p>5 этап - обучение  ребёнка отвечать на вопрос \"Что ты хочешь? \", просьба - как ответ на вопрос.</p>\r\n<p>6 этап - это комментирование. Ребёнка учат отвечать на вопросы: \"Что ты хочешь? Что ты видишь? Что у тебя есть? Что ты слышишь? Что это?\", в итоге ребёнок спонтанно (инициативно) просит и комментирует происходящее.</p>\r\n<p>Проект \"Социальные навыки и альтернативная коммуникация как основа инклюзивного образования для детей с РАС\" реализуется при поддержке Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n\r\n\r\n', '/images/Absolut/14/1.jpg', '2022-01-27', '/images/Absolut/logo.jpg;\r\n/images/Absolut/14/1.jpg;\r\n/images/Absolut/14/2.jpg;\r\n/images/Absolut/14/3.jpg', '', '', 'Новости', '/news.php', 0, '', '9'),
(513, '20', 'Как сделать альбом для PECS ', 'У детей, обучающихся системе альтернативной коммуникации PECS, при себе всегда должен быть альбом с карточками, чтобы общаться с другими людьми. Такие альбомы приходится либо заказывать в интернет-магазинах, либо делать самим.\r\n\r\n', '<p>У детей, обучающихся системе альтернативной коммуникации PECS, при себе всегда должен быть альбом с карточками, чтобы общаться с другими людьми. Такие альбомы не купишь в канцелярских магазинах города, их приходится либо заказывать в интернет-магазинах, либо делать самим.\r\n</p>\r\n<p>Чтобы смастерить альбом PECS понадобятся:</p>\r\n<p>- пластиковая папка на кольцах, </p>\r\n<p>- непрозрачные пластиковые папки для бумаг (или цветные листы картона формата А4 заламинированные/обклеенные с 2 сторон скотчем),</p>\r\n<p>- дырокол,</p>\r\n<p>- самоклеющаяся лента-липучка,</p>\r\n<p>- ножницы.</p>\r\n<p>Папка с кольцами - обложка альбома, листами в ней послужат разрезанные пластиковые папки. Их нужно уменьшить под размер обложки, минус полсантиметра, чтобы не торчали уголки (один внешний уголок всё же можно оставить для удобного перелистывания).\r\n</p>\r\n<p>Листы прокалываются дыроколом, на расстоянии колец обложки альбома. Затем на каждую страницу с 2 сторон клеится липучая лента в 2 или 3 горизонтальные, либо вертикальные линии. Расстояние между ними должно быть достаточным, чтобы на них легко помещались и не смешивались карточки PECS.</p>\r\n<p>Карточки PECS можно приобрести в интернет-магазине или смастерить самостоятельно. Изображения предметов можно фотографировать, искать картинки в интернете или скачать на бесплатных ресурсах в сети.\r\n</p>\r\n<p>После того, как изображения собраны, нужно либо поработать в Photoshop, либо сделать в Word с помощью инструмента «таблица» шаблон карточек (задать размеры). Затем вставить в шаблон картинку и снизу подписать название того, что изображено, чтобы педагоги и члены семьи называли этот предмет ребёнку одинаково.</p>\r\n<p>Далее распечатать карточки на цветном принтере, разрезать их и вставить в плёнку для ламинирования( либо обклеить с 2 сторон широким скотчем).</p>\r\n<p>Важно разделить ножницами карточки до ламинирования, т.к. тогда плёнка лучше склеится и карточки дольше послужат.</p>\r\n<p>Затем ламинировать карточки специальной машинкой (в полевых условиях подойдёт обычный утюг), обрезать и закруглить уголки, иначе они будут острыми, а это небезопасно.\r\n</p>\r\n<p>В конце наклеить липучую ленту на изнанку карточки. Помните, что у липучей ленты 2 стороны, она крепится пушистой частью к части с крючками. Важно не перепутать их и не наклеить одинаковые части и к карточке и к странице альбома. </p>\r\n<p>Проект \"Социальные навыки и альтернативная коммуникация как основа инклюзивного образования для детей с РАС\" реализуется при поддержке Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n\r\n\r\n', '/images/Absolut/15/1.webp', '2022-01-28', '/images/Absolut/logo.jpg;\r\n/images/Absolut/15/1.webp;\r\n/images/Absolut/15/2.jpg', '', '', 'Новости', '/news.php', 0, '', '9'),
(514, '20', 'Бумажный и электронный варианты карточек PECS', 'В начале освоения системы альтернативной коммуникации PECS дети пользуются бумажным вариантом карточек и пластиковой папкой - альбомом. Электронный планшет с карточками вводится на 5 этапе обучения, когда ребёнок активно просит разнообразные предметы.', '<p>В начале освоения системы альтернативной коммуникации PECS дети пользуются бумажным вариантом карточек и пластиковой папкой - альбомом.</p>\r\n<p>Сразу начинать с электронного варианта альбома PECS специалисты не рекомендуют, т.к.  ребёнку нужно обусловить связь между конкретной карточкой и предметом или действием, обозначающим её, научить обменивать карточку на предмет или действие с коммуникативным партнером.\r\n</p>\r\n<p>Электронный планшет с карточками вводится на 5 этапе обучения, когда ребёнок, имея большой запас просьб и узнавания предметов, отвечает на вопрос \"Что ты хочешь?\" и активно просит разнообразные предметы.</p>\r\n<p>Карточки для электронного планшета можно загрузить из специальных приложений (платных и бесплатных). В основном родители приобретают для детей планшет Apple IPad и платное приложение к нему PECS 4+. Эта версия на английском языке, её ещё приходится переводить на русский.</p>\r\n<p>Преимущества электронного альбома PECS очевидны: в него легко закачать большое количество карточек и рассортировать их по категориям, сам альбом от этого ни тяжелее, ни объемнее не станет. Это также избавит родителей и педагогов от дополнительной работы по изготовлению и ламинированию бумажных карточек. </p>\r\n<p>Мобильный и компактный электронный планшет соответствует современным реалиям: у детей со школы (а то и раньше) есть свои гаджеты, с помощью которых они играют, учатся и общаются. Поэтому такой планшет у ребёнка с РАС может привлечь интерес его сверстников и стать отправной точкой для общения.</p>\r\n<p>Из минусов у электронных планшетов PECS: высокая цена и то, что в сравнении со своими аналогами из пластиковых папок, их нужно заряжать.\r\n</p>\r\n<p>Проект \"Социальные навыки и альтернативная коммуникация как основа инклюзивного образования для детей с РАС\" реализуется при поддержке Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n\r\n\r\n', '/images/Absolut/16/3.webp', '2022-02-03', '/images/Absolut/logo.jpg;\r\n/images/Absolut/16/1.webp;\r\n/images/Absolut/16/2.webp;\r\n/images/Absolut/16/3.webp\r\n', '', '', 'Новости', '/news.php', 0, '', '9'),
(522, '19', 'Инклюзия в школе', 'На фото - классный час, посвящённый Дню защитника Отечества в школе посёлка Луговое Иркутского района. В кадре обычный праздник, а за ним - серьёзная подготовительная работа, ведь класс не обычный, а ресурсный, для детей с аутизмом.', '<p>На фото - классный час, посвящённый Дню защитника Отечества в школе посёлка Луговой Иркутского района. Стандартный праздник: первоклассники, учителя, конкурсы, поделки... Но это в кадре, а за ним - серьёзная подготовительная работа, ведь класс не обычный, а ресурсный, для детей с аутизмом.<p>\r\n<p>Ресурсный класс в школе Лугового впервые открыли в этом учебном году. Для 7 первоклассников создали целую команду педагогов: учитель, тьюторы, деффектолог, психолог, логопед, специалисты по прикладному анализу поведения - супервизор и куратор.<p>\r\n<p>Классный руководитель, тьюторы и дети познакомились ещё летом, во время учёбы в \"АПЦ РАСсвет\", где взрослые проходили обучение и стажировку, а ребята готовились к школе на специальных групповых занятиях.<p>\r\n<p>Учебное поведение и рутины, фронтальные инструкции, коммуникативные навыки - всё, что с детьми проходили в процессе групповых занятий, пригодилось во время адаптации в школе.\r\n<p>\r\n<p>Куратор класса Вероника Сергеевна Артамонова рассказала, что помогает детям включиться в школьную жизнь.\r\n<p>\r\n<p>- В 8 утра звенит школьный звонок и у учеников ресурсного класса начинаются уроки, также, как и у их сверстников из регулярных классов. Занятия продолжаются до полудня, но во время уроков дети могут делать короткие перерывы для разгрузки в сенсорной зоне.<p>\r\n<p>- До ноября в школе были общие перемены, тьюторы организовывали для детей ресурсного и регулярных классов совместные игры, помогали им взаимодействовать. Сейчас из-за эпидемии ОРВИ у нас объявлен кабинетный режим, общение между классами сведено к минимуму.<p>\r\n<p>- Дети из ресурсного и регулярного класса проводят время вместе в столовой на обеденной перерыве. Это тоже важная часть инклюзивного процесса. В столовую наши ребята ходят в сопровождении учителя и 1 тьютора, строем по парам, это помогает им дойти до нужного места не разбегаясь. То же и с переходом по школьным коридорам на занятия физкультуры.\r\n<p>\r\n<p>Кстати, ученики ресурсного класса (умеющие плавать) уже посещали уроки физры в бассейне вместе с учениками регулярных классов.<p>\r\n<p>- Чтобы дети с РАС активно включались в школьную жизнь, педагоги работают над их корекцией поведения с помощью развития мотивационной сферы, постоянного поиска поощрений, предоставляют визуальную поддержку (групповые и индивидуальные расписания, карточки \"сначала-потом\" и т.д.).<p>\r\n<p>-У первоклассника Димы есть проблемы с вокализацией, он повторяет одну и ту же фразу \"Мама придёт\" для привлечения внимания, избегания выполнения заданий и т.д. (у него может быть несколько функций поведения). Дима не только вокализирует, но и требует от педагогов повторять за ним эту фразу. Чтобы такое поведение не мешало мальчику и его одноклассникам учиться, для него сделали визуальное расписание, мальчик видит, какой объём работы и в какой последовательности нужно выполнить до того, как за ним придёт мама. Важное условие: пока задания не сделаны, мама не может забрать сына с уроков. Дима стал меньше тревожится и выполняет свои задания без прекословий.<p>\r\n<p>- Мы развиваем коммуникативные навыки детей. Ребята учатся проявлять инициативу в просьбах: сходить в туалет, сенсорную зону, получить помощь или поощрение. Пока ученики тренируются запросы с тьюторами, но потом этот навык пригодится им в общении со сверстниками. Так, Юра любит игрушку-сортёр с вкладышами в виде облака. Чтобы спровоцировать мальчика на запрос, тьютор достаёт игрушку и держит её в поле зрения ребёнка. Юра замечает сортёр и просит: \"играть\", \"облако\".<p>\r\n<p>- У нас запланированы на 3 и 4 четверть уроки добра - классные часы для учеников регулярных классов, на которых им будут рассказывать об аутизме и о том, как общаться с детьми из ресурсного класса. Сейчас мы ищем среди девятиклассников ребят, желающих стать волонтёрами, кто будет продвигать тему инклюзии. Им мы и хотим доверить выступление на уроках добра. Похожие собрания мы проведём для сотрудников школы и родителей нейротипичных учеников.<p>\r\n<p><p>\r\n<p><p>\r\n<p><p>\r\n<p><p>\r\n<p>Благодарим организаторов и партнёров проекта \"Эстафета компетенции НКО\" : @Фонд\"Выход, @Ассоциация\"Аутизм-Регионы. Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.<p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '/images/RK/2022/5/1.jpg', '2022-02-23', '/images/RK/2022/5/1.jpg;\r\n/images/RK/2022/5/2.jpg;\r\n/images/RK/2022/5/3.jpg;\r\n/images/RK/2022/5/4.jpg', '', '', '', '', 0, '', '0'),
(525, '18', 'Мастерские: взгляд в будущее', 'Недавно о наших мастерских \"Дом синего слона\" сняли видеоролик: обычный день в творческом пространстве центра. Но для того, чтобы занятия в мастерских стали обыденностью, многие люди вложили в них свои силы и частичку себя.', '<p>Мастерские \"Дом синего слона\" — это не просто место для творчества, это пространство, где рождаются мечты и открываются горизонты для детей и подростков с аутизмом. Недавно о наших мастерских \"Дом синего слона\" сняли видеоролик: обычный день в творческом пространстве центра. Но для того, чтобы занятия в мастерских стали обыденностью, многие люди вложили в них свои силы и частичку себя.</p>\r\n<p>Главная задача \"Дома синего слона\" — пробудить интерес к творчеству и прикладным навыкам, дать возможность ребятам с аутизмом развить свой потенциал и заложить основу для будущей профессии. В этом году мы планируем активно развивать швейное направление, оснащая мастерскую качественным оборудованием. Уже сейчас ребята знакомятся с текстилем и создают удивительные изделия, такие как облачные подушки, каждая из которых отражает характер и настроение её создателя.</p>\r\n<p>Одним из любимых материалов в мастерской является архитектурный бетон. Эта прочная и универсальная смесь используется не только в строительстве, но и в декоративных целях. Изделия из бетона, такие как кашпо для растений, становятся стильным дополнением интерьера и радуют глаз своей простотой и лаконичностью.</p>\r\n<p>Особенностью наших занятий является участие родителей, которые активно вовлекаются в творческий процесс вместе с детьми. Это не только укрепляет семейные связи, но и помогает родителям увидеть, каким талантливым и способным может быть их ребенок. Мама Сони делится впечатлениями: \"Мне нравится, что занятия разнообразны и включают различные виды творчества. Это помогает моему ребенку открыть новые интересы и развить важные жизненные навыки.\"\r\n</p>\r\n<p>Мастерские \"Дома синего слона\" — это не просто место для занятий, это мост в будущее, где каждый ребенок может найти свой путь к самореализации и счастью.</p>\r\n<p>АНО \"АПЦ Рассвет\" - участник проекта \"Создание сети творческих досуговых центров для молодых людей с РАС \"Культурный код - возможность для каждого\"\". Благодарим организаторов и партнеров проекта: Ассоциация \"Аутизм - Регионы\", Фонд \"Выход\". Проект реализуется с использованием гранта Президента Российской Федерации на реализацию проектов в области культуры, искусства и креативных (творческих) индустрий Президентский фонд культурных инициатив.</p>', '/images/DSS/2022/6/1.jpg', '2022-03-07', '/images/DSS/2022/6/4.jpg;\r\n/images/DSS/2022/6/2.jpg;\r\n/images/DSS/2022/6/3.jpg;\r\n/images/DSS/2022/6/5.jpg', '', '', '', '', 0, '', '19'),
(527, '2', '\"Рассвету\" 4 года!', 'На этой неделе мы отметили день рождения центра праздничной программой для наших любимых учеников. А также провели розыгрыш подарков для подписчиков в нашей группе Вконтакте.\r\n', '<p>4 года назад \"АПЦ РАСсвет\" появился на свет. За это время мы успели реализовать крутые и нужные проекты, наши сотрудники обучались сами (и продолжают развивать свои профессиональные компетенции), обучали детей с аутизмом, их родителей и специалистов, работающих с детьми с РАС... Мы уверены, что впереди нас ждёт ещё много интересной, трудной и важной работы. И это замечательно!</p>\r\n<p>На этой неделе мы отметили день рождения центра праздничной программой для наших любимых учеников. А также провели <a href=\"https://vk.com/public165016766?w=wall-165016766_1256\" target=\"_blank\">розыгрыш подарков</a> для подписчиков в нашей группе Вконтакте.</p>\r\n<p>Спасибо всем, кто с нами рядом или далеко, но на одной волне, кто нам помогает и кому можем помочь мы. С днем рождения нас!</p>', '/images/news/2022/1/6.JPG', '2022-03-05', '/images/news/2022/1/2.JPG;\r\n/images/news/2022/1/3.JPG;\r\n/images/news/2022/1/4.JPG;\r\n/images/news/2022/1/5.JPG;\r\n/images/news/2022/1/1.JPG;\r\n/images/news/2022/1/7.JPG;\r\n/images/news/2022/1/8.JPG', '', '', 'Новости', 'news.php', 0, '', '0'),
(530, '10', 'Проект \"Социальные навыки и альтернативная коммуникация как основа инклюзивного образования для детей с РАС\"', 'Статус проекта: завершен<br>\r\nСроки реализации: 01.09.2021 - 30.08.2022<br>\r\nИсточник софинансирования: Благотворительный фонд \"Абсолют-Помощь\"\r\n', '<p><b>Статус проекта:</b> завершен<br>\r\n<b>Сроки реализации:</b> 01.09.2021 - 30.08.2022<br>\r\n<b>Источник софинансирования:</b> Благотворительный фонд \"Абсолют-Помощь\"</p>\r\n<p>Проект направлен на подготовку 15 детей с РАС школьного и дошкольного возраста с использованием поведенческих технологий к вновь создаваемым ресурсным классам в г. Иркутске и Иркутском районе.</p>\r\n<p><b>Цель проекта:</b></p>\r\n<p>Подготовить условия для распространения модели инклюзивного образования в городе Иркутск и Иркутском районе путём внедрения поведенческих технологий в обучение социальному взаимодействию и коммуникации со сверстниками детей с РАС и передачи опыта педагогам, родителям детей с РАС и другим участникам образовательного процесса.</p>\r\n<p><b>Задачи:</b></p>\r\n<p>1. Создать учебную среду для проведения индивидуальных и групповых занятий с детьми с аутизмом с помощью расширения материально-технической базы и дополнительного обучения педагогов АНО \"АПЦ РАСсвет\".</p>\r\n<p>2. Обучить детей с РАС с использованием поведенческих подходов социальным навыкам: взаимодействию, коммуникации, поведению в группе, разрешению естественных конфликтных ситуаций, совместным играм и проведению досуга, - путем проведения групповых занятий с детьми с поэтапным увеличением количества учеников до пяти человек.</p>\r\n<p>3. Повысить компетенции специалистов в государственной системе образования в области Прикладного анализа поведения и альтернативной системы коммуникации.</p>\r\n<p>4. Развить инклюзивную культуру среди педагогического и родительского сообщества посредством информирования о проблеме аутизма, методах обучения социальным навыкам и альтернативных видах коммуникации в СМИ и на семинарах.</p>\r\n<p>5. По результатам оценки базовых учебных навыков и определения уровня нарушения коммуникации внедрить альтернативную систему коммуникации детям с РАС в индивидуальном формате с последующим обобщением в группе сверстников.</p>\r\n<p><a href=\"/school-preparation.php\" class=\"button special\">Публикации о проекте</a></p>', '/images/grants/absolut.jpg', '2021-09-13', '', '', '', 'Гранты', '/pages/grants.php', 0, '', '0'),
(531, '10', 'Проект \"Ты не один в этом мире\"', 'Статус проекта: завершен<br>\r\nСроки реализации: 01.02.2022 - 31.01.2023<br>\r\nИсточник софинансирования: Фонд президентских грантов\r\n', '<p><b>Статус проекта:</b> завершен<br>\r\n<b>Сроки реализации:</b> 01.02.2022 - 31.01.2023<br>\r\n<b>Источник софинансирования:</b> Фонд президентских грантов</p>\r\n<p>Проект направлен на обучение не менее 20 детей и подростков с расстройствами аутистического спектра (РАС) в возрасте 7-16 лет начальным творческим навыкам в индивидуальном формате с последующим переносом в коммуникативные группы с допрофессиональным творческо-прикладным функционалом работ. В группах обобщатся навыки, изучаемые параллельно в индивидуальной форме с педагогом, реализуются процессы приучения детей к взаимодействию и саморегуляции нежелательного поведения. На групповых занятиях дети освоят работу с деревянными заготовками, кожей, с природными материалами, текстильными и вязанными изделиями, картинами из жидкого акрила, корзинами из джута, отливкой форм из бетона. Структура занятий построена по принципам Прикладного анализа поведения (ПАП), индивидуализирующих подход к обучению и коррекции поведения ребенка. В течение всей реализации проекта на мастерских пройдет подготовка к итоговому благотворительному мероприятию «Ты не один в этом мире». Такое событие привлечет людей, ранее не сталкивавшихся с проблемой аутизма, и покажет родителям детей с РАС, что их дети не одиноки и способны быть полноправными членами общества.</p>\r\n<p><b>Цель проекта:</b></p>\r\n<p>Формирование допрофессиональных и коммуникативных навыков у детей и подростков с РАС 7-16 лет для их личностной самореализации и включения в социальную жизнь общества в г. Иркутске путем создания творческих мастерских.</p>\r\n<p><b>Задачи:</b></p>\r\n<p>1. Оказать индивидуальную психолого-педагогическую поддержку детям с РАС с использованием технологий коррекции нежелательных форм поведения на этапе адаптации в группе сверстников</p>\r\n<p>2. Разработать и реализовать индивидуальное обучение детей с аутизмом начальным творческим навыкам с последующим переносом в коммуникативные обучающие группы</p>\r\n<p>3. Создать коммуникативные обучающие группы детей с РАС 7-16 лет с отработкой навыков декоративно-прикладного творчества посредством использования методов прикладного анализа поведения </p>\r\n<p>4. Создать методическую базу обучения детей с РАС творческо-прикладным технологиям и взаимодействию друг с другом путём повышения компетенций педагогов и волонтеров</p>\r\n<p>5. Создание положительного образа человека с аутизмом в обществе путем проведения благотворительного мероприятия «Ты не один в этом мире» с реализацией готовых изделий мастерских</p>\r\n<p><a href=\"/news.php?project=7\" class=\"button\">Публикации о проекте</a></p>', '/images/grants/dss.jpg', '2022-02-15', '', '', '', 'Гранты', '/pages/grants.php', 0, '', ''),
(532, '3', 'Обучение по сенсорной интеграции', 'Из-за трудностей в обработке данных, поступающих от органов чувств, мозг ребёнка с РАС получает информацию в искаженном виде. Над нормализацией сенсорной чувствительности работает метод сенсорной интеграции. Подробнее о нём узнали участники практического семинара, прошедшего 12-13 марта на базе \"АПЦ РАСсвет\". ', '<p>У детей с аутизмом существует взаимосвязь между развитием моторных, речевых навыков, социальным взаимодействием и академической успеваемостью. Из-за трудностей в обработке данных, поступающих от органов чувств (зрения, слуха, обоняния, вкуса и осязания), а также вестибулярного аппарата и сферы проприоцепции (ощущения положения тела в пространстве), мозг ребёнка получает информацию в искаженном виде.</p>\r\n<p>Над нормализацией сенсорной чувствительности работает метод сенсорной интеграции. Подробнее о самом методе и о том, как его применять на занятиях с детьми с аутизмом, узнали участники практического семинара, прошедшего 12-13 марта на базе \"АПЦ РАСсвет\". \r\n</p>\r\n<p>Обучение проводила нейропсихолог, специалист по сенсорной интеграции, руководитель центра раннего развития детей с аутизмом Наталия Великовская.</p>\r\n<p>За 2 дня 16 участников семинара узнали о физиологии сенсорных систем и сенсорном профиле, этапах формирования когнитивных функций, современной классификации нарушений сенсорной обработки информации и их диагностики, видах терапевтических вмешательств, теории сенсорной интеграции А.Д Айрес, протоколе Вильбаргера и других важных аспектах. </p>\r\n<p>Полученные знания закрепили на практике, а также разобрали реальные случаи из работы с детьми с ОВЗ, Наталия дала подробные рекомендации по каждой проблемной ситуации.</p>\r\n<p>Приобретенная недавно в рамках проекта \"Шаг в инклюзию\" офисная новинка - доска флип-чарт, сделала подачу материала на семинаре более доступной и визуально удобной. Благодарим за это Департамент здравоохранения и социальной помощи населения комитета по социальной политики и культуры администрации г. Иркутска.</p>\r\n<p>С новыми знаниями к новому опыту работы сотрудники \"РАСсвета\" готовы!\r\n</p>\r\n\r\n', '/images/seminar/2022/2022.03.14/3.jpg', '2022-03-14', '/images/seminar/2022/2022.03.14/1.jpg;\r\n/images/seminar/2022/2022.03.14/2.jpg;\r\n/images/seminar/2022/2022.03.14/3.jpg;\r\n/images/seminar/2022/2022.03.14/4.jpg;\r\n/images/seminar/2022/2022.03.14/5.jpg;\r\n/images/seminar/2022/2022.03.14/6.jpg;\r\n/images/seminar/2022/2022.03.14/7.jpg;\r\n/images/seminar/2022/2022.03.14/8.jpg;\r\n/images/seminar/2022/2022.03.14/9.jpg', '', '', '', '', 0, '', '8');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(533, '19', 'Как открыть ресурсный класс', 'Всех родителей детей с аутизмом волнует вопрос получения образования их ребенком. Многие из родителей слышали об инклюзивной образовательной модели «Ресурсный класс» и хотели бы, чтобы их ребенок мог учиться в таком классе. О том, что такое ресурсный класс и как его создать - читайте в этой статье.', '<p>Всех родителей детей с аутизмом волнует вопрос получения образования их ребенком. Многие из родителей слышали об инклюзивной образовательной модели «Ресурсный класс» и хотели бы, чтобы их ребенок мог учиться в таком классе. О том, что такое ресурсный класс и как его создать - читайте в этой статье.</p>\r\n<p>Ресурсный класс (РК) для детей с аутизмом – это специально отведенное место в учебном заведении (класс, кабинет), который оборудован специальным образом и обеспечен квалифицированными специалистами.</p>\r\n<p>Ресурсный класс – это:</p>\r\n<p>- место подготовки ученика к обучению в обычном классе;</p>\r\n<p>- место обучения по специальной программе с учётом индивидуальных\r\nпотребностей каждого ребёнка;</p>\r\n<p>- зона, в которой есть необходимые «ресурсы», специально адаптированная среда;</p>\r\n<p>- команда специалистов, владеющих специальными технологиями и методами обучения, основанными на принципах прикладного анализа поведения и\r\nдоказавшими свою эффективность для детей с аутизмом.</p>\r\n<p>В команду специалистов РК входят:</p>\r\n<p>- Учитель «Ресурсного класса» (проводит групповые и часть индивидуальных занятий);</p>\r\n<p>- Педагоги-тьюторы (проводят индивидуальные занятия, сопровождают ребенка на групповых уроках и в инклюзии, оказывают помощь на всех этапах);</p>\r\n<p>- Логопед, психолог (дополнительные индивидуальные занятия);</p>\r\n<p>- Учителя «общих классов», а также музыки, ИЗО, физкультуры и т.д;</p>\r\n<p>- Завуч по инклюзии (либо ответственный за инклюзию в школе);</p>\r\n<p>А также специалисты по прикладному анализу поведения (не входят в штат сотрудников школы):</p>\r\n<p>- Куратор ресурсного класса (составляет индивидуальные программы детей, обучает и консультирует тьюторов по методологии обучения и работе с нежелательным поведением);</p>\r\n<p>- Супервизор (поведенческий аналитик международного уровня, научный руководитель класса, консультирует куратора и других педагогов по сложным случаям).</p>\r\n<p>Более подробно о модели РК и функциях специалистов можно узнать из пособия\r\n<a href=\"http://outfund.ru/wp-content/uploads/2016/10/4_RK_preview.pdf\" target=\"_blank\">\"Ресурсный класс\"</a> (ссылка на скачивание с сайта Фонд «Выход». Аутизм в России) </p>\r\n<p>Открытие РК на базе общеобразовательной школы – это довольно длительный процесс, требующий активного участия родителей и поддержки НКО, занимающейся проблемами детей с РАС в регионе.</p>\r\n<p>Начинать работу по созданию РК нужно задолго до начала учебного года, т.к. она\r\nвключает в себя много предварительный работы.</p>\r\n<p>Что необходимо сделать родителям для создания РК в своем городе:\r\n</p>\r\n<p>1. Найти единомышленников - родителей детей, готовых вместе с вами включиться в\r\nполноценную работу по созданию класса. Если будет много желающих, то можно\r\nобъединяться по территориальному признаку.</p>\r\n<p>2. Прочитать книгу <a href=\"http://outfund.ru/wp-content/uploads/2016/10/4_RK_preview.pdf\" target=\"_blank\">\"Ресурсный класс\"</a> , чтобы иметь представление об этой модели и уметь рассказать о ней администрации школ и другим людям.</p>\r\n<p>3. Найти школу, готовую открыть РК. Поговорить с директором, рассказать об\r\nобразовательной модели РК, выяснить готова ли школа к открытию такого класса и\r\nесть ли в школе возможность выделить помещение для РК, которое должно\r\nвключать в себя не только зону для проведения занятий, но и зону сенсорной\r\nразгрузки.\r\n</p>\r\n<p>4. После договоренности с директором школы следующий шаг - департамент\r\nобразования. Окончательное решение об открытии РК принимает он, также\r\nдепартамент решает вопрос о выделении ставок тьюторов на класс.</p>\r\n<p>5. Продумать вопрос о привлечении средств на реализацию проекта. Иначе все затраты лягут на плечи родителей. Оборудование сенсорной зоны, покупка и\r\nизготовление обучающих материалов и поощрений для детей, оплата работы\r\nспециалистов по прикладному анализу поведения – требуют больших вложений.\r\nНаиболее частый путь – это создание родителями некоммерческой организации\r\n(либо присоединение к уже имеющейся в вашем регионе НКО) и участие в\r\nконкурсах на получение грантов на реализацию своего проекта.\r\n</p>\r\n<p>6. Найти специалистов для работы в классе: учителя с дефектологическим\r\nобразованием, тьюторов с высшим педагогическим или психологическим\r\nобразованием, специалистов по прикладному анализу поведения (куратора и супервизора).</p>\r\n<p>7. Организовать обучение тьюторов базовым принципам и методам прикладного анализа поведения. В настоящее время существует множество возможностей для дистанционного обучения - например, <a href=\"https://aba-kurs.com/\" target=\"_blank\">курсы Юлии Эрц.</a></p>\r\n<p>Итак, мы перечислили основные шаги, которые необходимо предпринять родителям для открытия ресурсного класса. Больше информации о том, как открыть ресурсный класс, правовых основах, компетенциях специалистов, источниках финансирования можно получить из <a href=\"https://www.youtube.com/watch?v=xK4pOnE1LKc\" target=\"_blank\">серии вебинаров</a> Ассоциации \"Аутизм Регионы\".</p>\r\n<p>АНО «АПЦ РАСсвет» уже помогла родителям открыть три ресурсных класса в Иркутске и Иркутском районе, а это значит – нет ничего невозможного. Мы и дальше готовы поддерживать родителей в этом вопросе: делиться опытом, оказывать консультативную помощь, помощь с подготовкой тьюторов и привлечением финансирования, при условии вашей полной включенности и помощи с написанием и реализацией проектов.\r\n</p>\r\n<p>Проект \"Центр развития инклюзивного образования для детей с расстройствами аутистического спектра по модели \"Ресурсный класс\" реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '/images/RK/2022/6/1.jpg', '2022-03-17', '/images/RK/2022/6/1.jpg;\r\n/images/RK/2022/6/2.jpg;\r\n/images/RK/2022/6/3.jpg;\r\n/images/RK/2022/6/4.jpg;\r\n/images/RK/2022/6/5.jpg', '', '', '', '', 0, '', '10'),
(534, '20', 'Социальные истории', 'Эффективно и наглядно объяснить ребёнку с РАС правила поведения, сделать шаги в освоении нового навыка помогут социальные истории - краткий рассказ с подробными иллюстрациями.', '<p>Эффективно и наглядно объяснить ребёнку с РАС правила поведения, сделать шаги в освоении нового навыка помогут социальные истории - краткий рассказ с подробными иллюстрациями.</p>\r\n<p>Брошюры с пошаговым описанием действий, последовательные схематические рисунки, фотографии ребёнка, его близкого окружения и предметов его быта, мест, где он бывает - визуальная поддержка такого рода позволяет ребёнку усваивать информацию в удобном для него режиме, сохраняется надолго и к ней всегда можно вернуться для повторения. \r\n</p>\r\n<p>Социальные истории можно найти в специальной обучающей литературе, в Сети, создать самим (педагогам, родителям) или сделать совместно с ребёнком, если его уровень развития навыков позволяет это.\r\n</p>\r\n<p>Мы уже рассказывали и показывали созданную специалистами  \"РАСсвета\" социальную историю о <a href=\"https://rassvet-apc.ru/ambulance/detail.php?id=520&back=news\" target=\"_blank\">посещении поликлиники</a>. Сегодня поделимся фотографиями наших новых социальных историй из раздела \"игры на детской площадке\", а также, в дополнение, материалами из книг Макото Шибутани \"100 историй о правилах общения и безопасного поведения для детей с аутизмом\" и \"Социальные истории\" Кэрол Грей на темы совместных игр и правил поведения в школе. </p>\r\n<p>Читайте также <a href=\"https://rassvet-apc.ru/social.php\" target=\"_blank\">\"Что такое социальные истории\"</a></p>\r\n<p>Проект \"Социальные навыки и альтернативная коммуникация как основа инклюзивного образования для детей с РАС\" реализуется при поддержке Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n\r\n\r\n\r\n\r\n', '/images/Absolut/17/0.jpg', '2022-03-21', '/images/Absolut/logo.jpg;\r\n/images/Absolut/17/1.jpg;\r\n/images/Absolut/17/2.jpg;\r\n/images/Absolut/17/3.jpg;\r\n/images/Absolut/17/4.jpg;\r\n/images/Absolut/17/5.jpg;\r\n/images/Absolut/17/6.jpg;\r\n/images/Absolut/17/7.jpg;\r\n/images/Absolut/17/8.jpg', '', '', 'Новости', '/news.php', 0, '', '9'),
(535, '20', 'Тестирование мотивационной сферы детей с РАС', 'В структуре каждого занятия в \"РАСсвете\" есть тестирование мотивационных стимулов детей. \r\nИнтересы детей меняются, любимая игрушка или активность может надоесть и уже не будет подходить для поощрения. Поэтому так важно проводить тестирование регулярно. ', '<p>В структуре каждого занятия в \"РАСсвете\" есть тестирование мотивационных стимулов детей. </p>\r\n<p>Мотивационный стимул (поощрение) - это положительное подкрепление поведения. \r\n</p>\r\n<p>В основе прикладного анализа поведения задействована мотивационная сфера: чтобы ребёнок сотрудничал и выполнял инструкции, должно быть что-то, чем мы будем подкреплять его поведение. Например, сегодня после выполнения задания на занятиях у дошкольников, Лиде дают пластилин, так как девочка очень любит лепку, а Агата получает набор \"Лего\", из которого она строит башни и сортирует детали по цветам. Поощрение, выданное после выполнения инструкции, закрепляет поведение ребёнка выполнять правильно задания и взаимодействовать с инструктором. </p>\r\n<p> Интересы детей меняются, любимая игрушка или активность может надоесть и уже не будет подходить для поощрения. Поэтому так важно проводить тестирование регулярно. </p>\r\n<p>Есть 2 типа тестирований на мотивационные стимулы: формальное и неформальное. Ко второму относится метод наблюдения, который часто используют педагоги нашего центра: в течение 20 минут во время занятия внимательно смотрят, какие предметы и активности выбирают дети в игровой зоне, отмечают их в чек-листах и собирают в индивидуальные боксы, чтобы затем выдавать их лишь в качестве поощрения или же оставляют в игровой зоне, где дети отдыхают в перерывах во время занятий.</p>\r\n<p>К формальным методам относится заполнение специального протокола, с помощью которого можно проанализировать, какое поощрение для ребёнка будет самым желанным и составить иерархию поощрений. </p>\r\n<p>Иерархия мотивационных стимулов нужна, чтобы ребёнок получал самое желанное поощрение после задания, которое ему было трудно исполнить, а после небольшого или легкого задания (например, для развития мелкой моторики, собрать палочки в коробку) поощрение будет любимым, но не основным. </p>\r\n<p>Так, для Саввы главное поощрение - игра в догонялки с инструктором и гонки на машинках с сестрой. А дополнительные мотивационные стимулы - слайм и музыкальные игрушки. </p>\r\n<p>АВА-инструкторы находятся в постоянном поиске новых поощрений для детей, расширяют сферу интересов ребят. </p>\r\n<p>Проект \"Социальные навыки и альтернативная коммуникация как основа инклюзивного образования для детей с РАС\" реализуется при поддержке Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n\r\n\r\n\r\n\r\n', '/images/Absolut/18/1.jpg', '2022-03-24', '/images/Absolut/logo.jpg;\r\n/images/Absolut/18/1.jpg;\r\n/images/Absolut/18/2.jpg;\r\n/images/Absolut/18/3.jpg;\r\n/images/Absolut/18/4.jpg;\r\n/images/Absolut/18/5.jpg', '', '', 'Новости', '/news.php', 0, '', '9'),
(538, '18', 'Мастер-класс состоялся', '5 марта в рамках городского мероприятия \"Неделя неформального образования\" в мастерских \"Дом синего слона\" прошел мастер-класс по изготовлению джутовой корзинки. Джут - это натуральное текстильное волокно, изготавливаемое из растений одноимённого рода, поэтому изделие из него можно назвать экологичным.', '<p>5 марта в рамках городского мероприятия \"Неделя неформального образования\" в мастерских \"Дом синего слона\" прошел мастер-класс по изготовлению джутовой корзинки. Джут - это натуральное текстильное волокно, изготавливаемое из растений одноимённого рода, поэтому изделие из него можно назвать экологичным.</p>\r\n<p>Плетение из джута это очень увлекательный процесс. Освоив простое плетение, и взрослые и дети с удовольствием втянулись в работу, так что невозможно было оторваться от нее. Шаг за шагом, под руководством наших педагогов, корзинки вырастали на глазах одна больше другой.</p>\r\n<p>Корзинки получились прекрасные, участники довольны результатом. Получившиеся корзиночки выглядят очень стильно и отлично впишутся в любой интерьер. Это наш первый опыт и очень удачный, планируем проводить такие мастер классы и дальше.</p>\r\n<p>В мероприятии участвовало 20 человек, среди которых дети с расстройствами аутистического спектра и их родители.</p>\r\n<p>Проект \"Ты не один в этом мире\" реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/DSS/2022/8/1.jpg', '2022-03-10', '/images/grants/pgrants_logo.jpg;\r\n/images/DSS/2022/8/1.jpg;\r\n/images/DSS/2022/8/2.jpg;\r\n/images/DSS/2022/8/3.jpg;\r\n/images/DSS/2022/8/4.jpg', '', '', '', '', 0, '', '7'),
(539, '18', 'Совы в \"Доме синего слона\" ', 'В наших мастерских дети и подростки с РАС продолжают осваивать швейное дело! На прошлом занятии были забавные совушки. Ребята в восторге от нового направления на занятиях!', '<p>В наших мастерских дети и подростки с РАС продолжают осваивать швейное дело! На прошлом занятии были забавные совушки.</p>\r\n<p>Работа всегда разделена на этапы - обвести выкройку на ткани, вырезать, а дальше самое интересное - работа с швейной машинкой (тут максимальная включённость наших педагогов - поэтому процесс безопасен!) - ребята в восторге от нового направления на занятиях. \r\n</p>\r\n<p>Сова – символ мудрости и проницательности. Её светящиеся глаза способны видеть в темноте то, что сокрыто от остальных. Прозорливость, ясность ума, решение важных вопросов и ловкость подарит птица своему обладателю. Классно же? В каждом доме должна быть своя сова.</p>\r\n<p>Проект \"Ты не один в этом мире\" реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/DSS/2022/9/1.jpg', '2022-03-24', '/images/grants/pgrants_logo.jpg;\r\n/images/DSS/2022/9/1.jpg;\r\n/images/DSS/2022/9/2.jpg;\r\n/images/DSS/2022/9/3.jpg;\r\n/images/DSS/2022/9/4.jpg;\r\n/images/DSS/2022/9/5.jpg', '', '', '', '', 0, '', '7'),
(540, '12', 'Особый подход', 'В \"РАСсвете\" к спорту подход особый, инструкторы АФК так подбирают программу тренировок и поощрения, чтобы каждый ребёнок захотел возвращаться в зал снова и снова. Тут важно ещё и не перенапрягать маленьких спортсменов.', '<p>Чем занимаются на адаптивной физкультуре дети с аутизмом в \"РАСсвете\"? Такой вопрос нам часто задают родители, перед тем, как записать ребёнка на АФК.</p>\r\n<p>1. Сначала ребёнка тестируют, чтобы выявить в каких направлениях его нужно развивать: работать над мелкой моторикой, сенсорными проблемами, со страхами и т.д. Инструктор выясняет, какие поощрения подходят ребёнку.</p>\r\n<p>2. На основе тестирования куратор составляет индивидуальную программу тренировок, в которой прописываются упражнения, количество подходов, инвентарь. Программа пишется в течение нескольких дней.</p>\r\n<p>3. Затем начинаются занятия адаптивной физкультурой. Рекомендуется посещать их не менее 2 раз в неделю. Тренер - инструктор АФК внимательно наблюдает за состоянием ребёнка во время занятия, программу могут корректировать, исходя из его замечаний.</p>\r\n<p>В \"РАСсвете\" к спорту подход особый, инструкторы АФК так подбирают программу тренировок и поощрения, чтобы каждый ребёнок захотел возвращаться в зал снова и снова. Тут важно ещё и не перенапрягать маленьких спортсменов.</p>\r\n<p>На тренировках в \"РАСсвете\" дети выполняют упражнения, соответствующие их уровню подготовки. Очень важно правильно оценивать уровень сложности. Например, подтягивание на турнике легче выполнить с поддержкой тренера, чем без него. Упражнения на растяжку проще даются, если тренер поможет зафиксировать положение, чем если бы ребёнок пытался удержать это положение сам. В этом случае инструктор АФК помогаем детям приспособиться к адекватным, индивидуально подобранным нагрузкам, которые в будущем позволяют быстрее добиваться прогресса.</p>\r\n<p>При работе с детьми с аутизмом инструктору приходится учитывать множество специфических трудностей и особенностей. Например, ребенок может не откликаться на своё имя, игнорировать обращённую речь или отказываться следовать инструкциям. Для преодоления этих барьеров эффективно использовать игровое взаимодействие, базирующееся на принципах прикладного анализа поведения (ABA). Этот подход помогает установить контакт с ребёнком и постепенно вводить новые формы взаимодействия.\r\n</p>\r\n<p>Кроме того, важную роль играют средства визуальной поддержки, такие как визуальные расписания, таймеры и карточки \"сначала-потом\". Они помогают снизить уровень тревожности у ребенка, обеспечивая ясность и предсказуемость процесса занятий. Ребенок видит, какие задания ему предстоит выполнить и когда он сможет получить заслуженное поощрение. Это создаёт ощущение контроля над ситуацией и облегчает адаптацию к новой деятельности.</p>\r\n<p>Таким образом, комбинация игровых методов и визуальных инструментов значительно улучшает процесс обучения и социализации детей с аутизмом, делая их взаимодействие с окружающим миром более комфортным и продуктивным.</p>\r\n<p>В феврале на базе школы посёлка Молодёжный Иркутского района состоялся межрегиональный этап международного форума социально-педагогических инноваций. Одним из ключевых выступлений стала презентация Викторией Кузнецовой, учредителем АНО \"АПЦ РАСсвет\" и куратором программ адаптивной физкультуры (АФК), вебинара на тему эффективного использования средств визуальной поддержки на занятиях по АФК с детьми с аутизмом.</p>\r\n<p>Проект \"Спорт без преград\" осуществляется при поддержке Благотворительного фонда Владимира Потанина</p>\r\n\r\n\r\n\r\n\r\n', '/images/AFK/2022/3/1.jpg', '2022-03-29', '/images/AFK/logo.jpg;\r\n/images/AFK/2022/3/1.jpg;\r\n/images/AFK/2022/3/2.jpg;\r\n/images/AFK/2022/3/3.jpg;\r\n/images/AFK/2022/3/5.jpg;\r\n/images/AFK/2022/3/4.jpg', '', '', '', '', 0, '', '0'),
(544, '5', 'Информируем в апреле', 'Для апрельской акции мы подготовили информационные листки, они будут полезны родителям детей с РАС. В них  обозначины \"красные флажки\" аутизма: поведение ребёнка, на которое взрослым следует обратить внимание, чтобы затем проконсультироваться у врача. ', '<p>Традиционно в апреле общественные организации совместно с партнёрами из бизнеса, культуры или госструктур, проводят информационные акции, чтобы рассказать обществу об аутизме, обратить внимание на проблемы людей с диагнозом РАС и способствовать их решению в дальнейшем.\r\n</p>\r\n<p>Для апрельской акции мы подготовили информационные листки, они будут полезны родителям детей с РАС. В них  обозначины \"красные флажки\" аутизма: поведение ребёнка, на которое взрослым следует обратить внимание, чтобы затем проконсультироваться у врача. </p>\r\n<p>Для волонтеров, педагогов и всех, кто хочет получить новую профессию в сфере работы с детьми с ОВЗ (тьютор, АВА-инструктор, учитель ресурсного класса), мы выпустили листовку с информацией о прикладном анализе поведения. Листовки можно посмотреть под этим постом. \r\n</p>\r\n\r\n<p>На Rutube канале нашего центра вышел ролик с интервью с руководителем \"РАСсвета\" Екатериной Родыгиной о существующих на региональном уровне проблемах аутизма.</p>\r\n<section class=\"video-detail\">\r\n      <iframe src=\"https://rutube.ru/play/embed/d381b28e844b12c1b99b1b40d8d97ef1\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen class=\"social-video\"></iframe>\r\n         </section>\r\n<p>8 апреля соучредитель \"РАСсвета\", поведенческий аналитик (3 модуля программы обучения специалистов по ПАП) Мария Максимова проведёт обучающий семинар на базе ФКУ Главное бюро медико-социальной экспертизы по Иркутской области на тему коррекции нежелательного поведения при помощи научно доказанных методов прикладного анализа поведения. Семинар будет транслироваться для сотрудников МСЭ по всей области. </p>\r\n<p>Соучредитель \"АПЦ РАСсвет\", олигофренопедагог, поведенческий аналитик (4 модуля сертифицированной программы для специалистов по ПАП) Елена Михеева дала интервью для программы \"Антистресс\" на радиостанции \"Маяк\". Она рассказала об этапах создания ресурсного класса, его финансировании и роли родителей и НКО в создании инклюзивного образования. </p>\r\n<p>Благодарим организаторов и партнёров проекта \"Ресурсный класс - Регионы. Расширение практики доступного инклюзивного образования детей с РАС и другими ментальными нарушениями\": Фонд \"Выход\", Ассоциацию \"Аутизм-Регионы\" и Благотворительный фонд \"Абсолют-Помощь\".\r\n</p>\r\n<div class=\"image\"><img src=\"/images/news/2022/2/1.jpg\" alt=\"фото\"/></div>\r\n<div class=\"image\"><img src=\"/images/news/2022/2/2.jpg\" alt=\"фото\"/></div>\r\n\r\n				\r\n', '/images/news/2022/2.04.jpg\r\n', '2022-04-04', '', '', '', 'Новости', 'news.php', 0, '', '0'),
(547, '18', 'Яркие акценты', 'На наших занятиях ребята начали работать над сумками, а если точнее, то готовили «яркие акценты» - рисовали на ткани, сначала абстракцию, а потом с помощью трафарета превращали её в пейзаж. Получилось так здорово, что мы даже провели серию мастер-классов по рисованию на ткани.', '<p>Мы уже говорили, что в этом году развиваем швейное дело. На наших занятиях ребята начали работать над сумками, а если точнее, то готовили «яркие акценты» - рисовали на ткани, сначала абстракцию, а потом с помощью трафарета превращали её в пейзаж. Смешивали палитры цветов прямо на ткани - очень заинтересовал ребят этот процесс , учились делать пейзажную основу, в основе которой летний цветущий луг и небо с пушистым облаками. Так классно получилось! </p>\r\n<p>Нарисованные пейзажи будут закреплены на ткани особым способ и станут элементами будущих экосумок или шопперов. Шопперы это многоразовые сумки для покупок, они представляют собой сумки с одним отделением и ручками. Изначально изделие было призвано заменить собой полиэтиленовые пакеты при покупке продуктов в магазине. </p>\r\n<p>Выбрали акриловые краски потому, что они отличаются яркостью цветов, гибкостью красочного покрытия и высокой устойчивостью к агрессивным условиям. В связи с этим при правильном нанесении рисунка на ткань можно за него не беспокоиться. Акрил не отслоится, не сморщится и не раскрошится. Эта краска легко переносит перепады температур, механические воздействия и маловосприимчива к влаге.</p>\r\n<p>Роспись ткани акриловыми красками это очень увлекательный процесс, настолько, что мы не остановились на достигнутом, а провели серию мастер- классов для волонтеров, родителей и педагогов проекта по работе с текстилем (рисунок на ткани) с участием детей и подростков с РАС.</p>\r\n<p>Проект \"Ты не один в этом мире\" реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/DSS/2022/11/4.jpg', '2022-04-21', '/images/grants/pgrants_logo.jpg;\r\n/images/DSS/2022/11/1.jpg;\r\n/images/DSS/2022/11/2.jpg;\r\n/images/DSS/2022/11/3.jpg;\r\n/images/DSS/2022/11/4.jpg;\r\n/images/DSS/2022/11/5.jpg', '', '', '', '', 0, '', '7'),
(551, '13,5', 'Интервью об инклюзивном образовании ', 'В рамках апрельской информационной кампании о проблемах аутизма на радио «МАЯК. Иркутск»  в программе «Антистресс» вышло интервью с Еленой Михеевой - соучредителем АНО «АПЦ РАСсвет\", об открытии и работе ресурсных классов Иркутска и Иркутского района. ', '<p>В рамках апрельской информационной кампании о проблемах аутизма на радио «МАЯК. Иркутск» в программе «Антистресс» вышел сюжет об открытии и работе ресурсных классов Иркутска и Иркутского района. </p>\r\n<p>Интервью с Еленой Михеевой, соучредителем АНО «АПЦ РАСсвет\", олигофренопедагогом, учителем ресурсного класса в школе посёлка Молодёжный показало, что создать условия для инклюзивного образования сложно, но возможно. </p>\r\n<p>Цитата из интервью:\r\n</p>\r\n<p>«На открытие первого ресурсного класса в городе Иркутске ушло пять лет работы. Ещё три года назад мне сказали, что никогда такого не будет. Но, оказывается, всё можно, всё решаемо. Дорогу осилит идущий».</p>\r\n<p>Интервью вы можете прослушать в закреплённой ниже записи. </p>\r\n<p><audio controls=\"controls\" src=\"/assets/audio/rk.mp3\"></audio></p>\r\n<p>Прослушать выпуск передачи \"Антистресс\" можно <a href=\"https://vk.com/wall-211913602_21\" target=\"_blank\">по ссылке</a>. </p>\r\n\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/RK/21.09.01/1.jpg', '2022-04-10', '/images/grants/pgrants_logo.jpg;\r\n/images/RK/21.09.01/1.jpg;\r\n/images/RK/21.09.01/3.jpg', '', '', 'Новости', 'news.php', 0, '', '10'),
(556, '20', 'В чём твоя сила? ', 'Сильные стороны ребенка с аутизмом находятся также, как и у его нейротипичных сверстников: путем наблюдения и тестирования интересов. Тестируя навыки ребенка мы можем выяснить, что, например, его сильной стороной является визуальное восприятие. ', '<p>Есть такие футболки с ярким принтом: \"Аутизм - моя суперсила!\", они помогают в позитивной форме заявить об особенностях человека, выразить часть его личности или подготовить окружающих к возможному необычному поведению хозяина одежды.</p>\r\n<p>Но этот пост посвящен не каким-то фантастическим суперсилам, а настоящим сильным сторонам ребёнка с РАС. Найти их и развить - мечта и цель многих родителей и специалистов. </p>\r\n<p>Сильные стороны ребенка с аутизмом находятся также, как и у его нейротипичных сверстников: путем наблюдения и тестирования интересов. Тестируя навыки ребенка мы можем выяснить, что, например, его сильной стороной является визуальное восприятие. После этого стоит предлагать ребенку игры, больше направленые на визуализацию: мемори, пазлы, сортировка, загадки об окружающих предметах (“Я вижу что-то круглое, объёмное, твёрдое, гладкое, синее. Что это? \") и т.п. Так ребёнок будет развивать не только визуальное восприятие, но и другие области - память, логику, сенсорику... \r\n</p>\r\n<p>Зная сильные стороны ребенка, можно использовать их для развития его дефицитов: на занятии при различение предметов например, молотка и ложки, у ребёнка с развитой визуальным восприятием, можно использовать визуальную подсказу (картинку с изображением молотка).\r\n</p>\r\n<p>Если к сильной стороне не говорящего ребёнка относится имитация, можно использовать её для развития навыков коммуникации: развивать жестовую коммуникацию, или визуальную (PECS).</p>\r\n<p>Сильная сторона ребенка может стать поощрением. Наши групповые занятия посещает Семен. У мальчика хорошо развито визуальное восприятие, он любит раскладывать предметы и картинки по цветам. Педагоги используют задание на сортировку, как поощрение во время занятий Семёна.</p>\r\n<p>Проект \"Социальные навыки и альтернативная коммуникация как основа инклюзивного образования для детей с РАС\" реализуется при поддержке Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n\r\n\r\n\r\n\r\n', '/images/Absolut/22/1.jpg', '2022-04-21', '/images/Absolut/logo.jpg;\r\n/images/Absolut/22/1.jpg;\r\n/images/Absolut/22/2.jpg;\r\n/images/Absolut/22/3.jpg', '', '', 'Новости', '/news.php', 0, '', '9'),
(559, '19', 'Лекции в начальных классах', 'В апреле в МОУ СОШ посёлка Молодежный проходят лекции об аутизме для учеников начальных классов. Оказывается, многие ребята даже не подозревали, что в одной школе с ними занимаются дети с РАС - ученики ресурсного класса. ', '<p>В апреле в МОУ СОШ посёлка Молодежный проходят лекции об аутизме для учеников начальных классов. Оказывается, многие ребята даже не подозревали, что в одной школе с ними занимаются дети с РАС - ученики ресурсного класса. </p>\r\n<p>Лекции \"Уроки доброты\" проводят педагоги ресурсного класса, среди них и Любовь Хацкевич - психолог:</p>\r\n<p>- Школьники реагируют на рассказ о том, что такое аутизм с интересом. Мы говорим о том, чем ребёнок с РАС отличается от них, какие проблемы испытывает... Затем подводим к мысли, что дети с аутизмом такие же как и обычные дети - любят то же самое, огорчаются по тем же причинам. Обязательно включаем мультфильм, который в доступной для детского восприятия форме рассказывает об аутизме.</p>\r\n<p>- В конце лекции мы спрашиваем, чем ребята могут помочь ребенку с аутизмом. Радуют их ответы: подойти познакомиться, поиграть,  помочь общаться, помочь с уроками... После школьники приходят на экскурсию в ресурсный класс, садятся за парты, играют в сенсорной зоне. Всем очень нравится такое окончание лекции. </p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n', '/images/RK/2022/11/4.png', '2022-04-22', '/images/grants/pgrants_logo.jpg;\r\n/images/RK/2022/11/2.jpg;\r\n/images/RK/2022/11/3.jpg;\r\n/images/RK/2022/11/4.png;\r\n/images/RK/2022/11/1.jpg', '', '', '', '', 0, '', '10'),
(561, '18', 'Эфир на радио', '20 апреля на радио \"Шансон\" прошёл эфир на тему проблем аутизма и развития инклюзивного общества. Спикерами выступили руководитель АПЦ \"РАСсвет\" Екатерина Родыгина и куратор центра Ева Айвасс.', '<p>20 апреля на радио \"Шансон\" прошёл эфир на тему проблем аутизма и развития инклюзивного общества. Спикерами выступили руководитель АПЦ \"РАСсвет\" Екатерина Родыгина и куратор центра Ева Айвасс.</p>\r\n<p>За полчаса эфира успели обсудить:\r\n</p>\r\n<p>трудности диагностирования аутизма в Иркутской области, и что важно как можно раньше начать реабилитацию ребёнка; </p>\r\n<p>что проявления РАС могут быть разными у разных людей, именно поэтому в формулировке диагноза есть слово \"спектр\";</p>\r\n<p>клинические рекомендации Минздрава и одобренные в них методы прикладного анализа поведения (ПАП), сколько часов терапии нужно проводить для развития ребёнка с аутизмом;</p>\r\n<p>проекты \"РАСсвета\" в сфере социализации детей с аутизмом;</p>\r\n<p> важность командной работы специалистов в области прикладного анализа поведения, педагогов, врачей и родителей детей с РАС;</p>\r\n<p>мастерские для детей и подростков с РАС \"Дом синего слона\", где воспитанники \"РАСсвета\" занимаются индивидуально и в группах, создают на продажу уникальные вещи для декора интерьера. Анонсировали выставку-продажу в мае на \"Иркутском Арбате\".\r\n</p>\r\n<p>АНО \"АПЦ Рассвет\" - участник проекта \"Создание сети творческих досуговых центров для молодых людей с РАС \"Культурный код - возможность для каждого\"\". Благодарим организаторов и партнеров проекта: Ассоциация \"Аутизм-Регионы\" , Фонд \"Выход\". Аутизм в России. </p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на реализацию проектов в области культуры, искусства и креативных (творческих) индустрий Президентский фонд культурных инициатив. </p>', '/images/DSS/2022/16/1.jpg', '2022-04-26', '/images/DSS/2022/16/1.jpg;\r\n/images/DSS/2022/16/2.jpg;\r\n/images/DSS/2022/16/3.jpg', '', '', '', '', 0, '', '19'),
(562, '4', 'Найти общие интересы', 'Одна из основных проблем у детей с аутизмом – трудности в общении с другими людьми. Чтобы увлечь наших учеников в коммуникативных группах общим делом, мы находим им такое занятие, которое понравится и заинтересует всех. ', '<p>Одна из основных проблем у детей с аутизмом – трудности в общении с другими людьми. Чтобы увлечь наших учеников в коммуникативных группах общим делом, мы находим им такое занятие, которое понравится и заинтересует всех. Этот же принцип работает, если вы решите создать комфортную и располагающую обстановку для общения ребёнка с РАС и нейротипичных детей.</p>\r\n<p>Общее занятие для дошкольников в наших группах – сюжетные, подвижные и настольные игры, занятия творчеством. С поддержкой педагогов дети узнают правила игры и начинают им следовать: определять, кто водит после считалочки, разбегаться, догонять, прятаться и искать друг друга или выбирать очередность для сложения пазлов, вкладышей.</p>\r\n<p>Задания по декоративно-прикладному искусству также могут быть совместным творчеством. У нас есть ребята, интересующиеся смешением красок, пластичными материалами, им нравится разбрызгивать кисточкой краску на листок или готовить акварельную бумагу к рисунку, проводя по ней мокрой губкой. Педагог может объединить усилия детей для работы над общим рисунком или поделками.\r\n</p>\r\n<p>Сюжетные игры с любимыми игрушками - очень полезное занятие для развития образности мышления, особенно в инклюзивной группе. Как правило, у детей с РАС игра узконаправленная, но когда в неё включаются нейротипичные сверстники, её видение расширяется, она становится разнообразной, обрастает подробностями. Через прохождение игровой ситуации ребенок с аутизмом может узнать и проработать такие сложные моменты в общении, как умалчивание информации для того, чтобы не ранить чувство другого человека, научиться деликатности.</p>\r\n<p>Для ребят постарше и тех, кто освоил начальные навыки коммуникации, общим интересом с нейротипичными детьми могут стать спец.интересы: определенная тематика (поезда, техника), компьютерные игры, фильмы и литература (комиксы, книги). Через обсуждение или ролевую игру дети не только освоят правила общения, но и смогут развить эмоциональный интеллект.</p>\r\n<p>Очень часто людей с РАС волнует не внешность собеседника, а только то, как он отвечает на его вопросы. Например, фанату «Звездных войн» будут важны в разговоре факты о фильмах, героях, а не одежда и прическа собеседника. Сейчас можно находить друзей по интересам на разных форумах в Сети. Для многих детей с аутизмом это идеальный вариант дружеских отношений, так как он сосредоточен только на информации, а не на физическом присутствии другого человека.</p>\r\n<p>Перед тем, как начать общение, важно не только подготовить ребёнка с аутизмом к взаимодействию с нейротипичными детьми, но и обговорить особенности его поведения с будущими собеседниками, дать им понять, что этого не стоит бояться или смущаться.\r\n</p>\r\n<p>Проект \"Социальные навыки и альтернативная коммуникация как основа инклюзивного образования для детей с РАС\" реализуется при поддержке Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n\r\n\r\n\r\n\r\n', '/images/Absolut/23/1.jpg', '2022-04-27', '/images/Absolut/logo.jpg;\r\n/images/Absolut/23/3.jpg;\r\n/images/Absolut/23/2.jpg', '/images/Absolut/23/3.jpg;0gRUzp_0Ux8?', '', 'Новости', '/news.php', 0, '', '9'),
(564, '19', 'Уроки понимания', 'Чтобы нейротипичные дети могли принять учеников ресурсного класса в свой круг, им нужно научиться их понимать. Поэтому в апреле в школе посёлка Луговое Иркутского района прошли классные часы, посвящённые теме аутизма и инклюзивного образования для младших школьников.', '<p>В школе посёлка Луговое Иркутского района в этом учебном году впервые открылся ресурсный класс для 7 учеников с расстройством аутистического спектра. Классный руководитель, тьюторы и дети познакомились и сработались ещё летом, во время учёбы в \"АПЦ РАСсвет\", где взрослые проходили обучение основам прикладного анализа поведения и стажировку, а ребята готовились к школе на специальных групповых занятиях.</p>\r\n<p>Даже с учётом подготовки к школьной жизни, процесс адаптации у первоклассников был не простым - в учебном году объявляли несколько карантинов из-за эпидемии ОРВИ и пандемии, ученики либо переводились на дистанционный формат обучения, либо не могли покидать классы во время уроков и перемен. Возможностей встретиться и наладить контакт со сверстниками из параллелей у ребят из ресурсного класса было не так много, как хотелось бы. </p>\r\n<p>И всё же дети пересекались время от времени в столовой, на переменах и общих уроках физкультуры в бассейне. Педагоги использовали эти встречи для того, чтобы наладить коммуникацию между \r\nучениками ресурсного класса и их нейротипичными сверстниками.</p>\r\n<p>- Чтобы нейротипичные дети могли принять учеников ресурсного класса в свой круг, им нужно научиться их понимать. Поэтому в апреле мы провели классные часы, посвящённые теме аутизма и инклюзивного образования для младших школьников, - рассказала учитель ресурсного класса Елена Валерьевна Синенкова. </p>\r\n<p>На \"уроках понимания\" ученикам начальных классов показывали презентацию об особенностях развития детей с аутизмом, объясняли, что мир разнообразен, все люди отличаются друг от друга, но могут найти общий язык. </p>\r\n<p>- У каждого из нас есть особенности, предпочтения в еде, одежде, фильмах и т.д. Но также есть и что-то общее: интересы, хобби, любимые предметы в школе... Если относиться спокойно к необычном поведению ребёнка с аутизмом, познакомиться с ним поближе, можно найти нового друга. Вот основная мысль наших \"уроков понимания\", - поделилась Елена Валерьевна.</p>\r\n<p>После классных часов учеников регулярных классов приглашали посетить ресурсный класс. Ребятам очень понравились сенсорные игрушки и не привычная обстановка. Тьюторы провели для гостей и учеников ресурсного класса совместные игры и договорились встречаться почаще на переменах, чтобы продолжить общение.</p>\r\n<p>Благодарим организаторов и партнёров проекта \"Эстафета компетенции НКО\" : Фонд «Выход».  Аутизм в России, Ассоциация \"Аутизм Регионы\". Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фонд президентских грантов.</p>\r\n', '/images/RK/2022/12/1.jpg', '2022-04-28', '/images/grants/pgrants_logo.jpg;\r\n/images/RK/2022/12/2.jpg;\r\n/images/RK/2022/12/3.jpg;\r\n/images/RK/2022/12/1.jpg', '', '', '', '', 0, '', '0'),
(565, '3', 'Инклюзивное образование: повышение квалификации специалистов', 'Повышение квалификации специалистов системы образования проходило по специально разработанной программе \"Развитие инклюзивного образования для детей с РАС по модели \"ресурсный класс\" в рамках проекта \"Центр инклюзивного образования\" при поддержке Фонда Президентских Грантов. ', '<p>В июле 2021 года  \"АПЦ Рассвет\" начал реализацию проекта \"Центр развития инклюзивного образования для детей с расстройствами аутистического спектра по модели \"Ресурсный класс\"\".  Проект направлен на решение проблемы дефицита педагогических кадров, владеющих эффективными методами обучения таких детей и имеющими опыт практической работы в команде профессионалов, реализующих образовательные программы для детей с РАС. </p>\r\n<p>В этом учебном году в Иркутске открылись новые ресурсные классы в школах микрорайона Лесной и поселка Луговое. Также продолжит свою работу ресурсный класс школы пос. Молодежный. Поэтому вопрос подготовки специалистов стоит остро, как никогда.</p>\r\n<p>В августе в «РАСсвете» и в школе поселка Молодёжный прошли тренинги для специалистов центра, тьюторов и стажеров по теме  «АВА-тьютор в ресурсном классе». «Новобранцы» и специалисты с опытом развивали свои знания в области методологии ресурсного класса, этических обязанностей тьютора и навыков, которыми он должен владеть.</p>\r\n<p>В сентябре сотрудники \"РАСсвета\" улучшили свои знания в сфере Прикладного анализа поведения под руководством супервизора центра и ресурсных классов Наталии Александровны Мальтинской, кандидата медицинских наук, сертифицированного поведенческого аналитика ВСВА, руководителя центра \"Диада+\". Программа оказалась очень насыщенной: очные супервизии во всех ресурсных классах и два 5-часовых тренинга для специалистов центра и сотрудников школ.</p>\r\n<p>Тренинги были направленны, в первую очередь, на молодых специалистов ресурсных классов:психологов, учителей и тьюторов. На одной из учебных сессий подробно разбирались тема обучения академическим навыкам детей с РАС в школе. Участники тренинга побывали как в роли инструктора, так и в роли обучающегося. Кураторы центра тоже без дела не сидели. Велись интересные дискуссии, разбирались особые случаи в обучении детей с использованием методов дискретных проб.</p>\r\n<p>В октябре в библиотеке им. Молчанова-Сибирского прошел семинар \"Проблемы инклюзивного образования для детей с РАС в городе Иркутск. Методы внедрения модели \"Ресурсный класс\" в образовательный процесс\" прошедшего на прошлых выходных.\r\nСпикер - олигофренопедагог, поведенческий аналитик Елена Михеева рассказала об инклюзии, а также о плюсах инклюзивного образования как для детей с РАС, так и для их нейротипичных сверстников. Аудитория (на семинаре собралось более 50 слушателей, в основном педагоги школ и родители детей с аутизмом) узнала о структуре модели \"Ресурсного класса\", какие специалисты должны в нем работать и каковы их функции.\r\nЕлена поделилась своим опытом работы в первом ресурсном классе Иркутской области, предупредила родителей о том, с какими проблемами им придётся столкнуться при организации новых ресурсных классов.</p>\r\n<p>20 ноября специалисты \"РАСсвета\" в партнёрстве с ассоциацией \"Аутизм-Регионы\" организовали вебинар \"Опыт организации ресурсных классов для детей с РАС в общеобразовательных школах\". Спикером выступила гостья из Казани - Анна Устиновская, сертифицированный поведенческий аналитик CBA-S, супервизор ресурсных классов, педагог-психолог и руководитель общественной родительской организации \"Просто другие\". В Казани ресурсные классы (РК) появились 5 лет назад в начальном звене школы. В этом году пара из них открылась в среднем звене - 5 класс. В вебинаре приняли участие 60 человек.</p>\r\n<p>Важным аспектов инклюзивного образования является информирование о проблемах аутизма всех участников учебного процесса в школе - педагогического и технического персонала, учеников и их родителей. Для этого педагоги ресурсных классов участвуют в педагогических советах, родительских собраниях, проводит \"уроки добра\" в регулярных классах. В апреле для педагогов МОУ СОШ посёлка Молодежный прошла лекция, посвящённая теме расстройства аутистического спектра и созданию инклюзивных условий в образовательных учреждениях. Спикерами выступили учитель ресурсного класса, олигофренопедагог и поведенческий специалист Елена Михеева и куратор АНО \"АПЦ РАСсвет\", поведенческий специалист  Елена Шапилова.</p>\r\n<p>В рамках проекта в апреле состоялся семинар для работников ФКУ «ГБ МСЭ по Иркутской области» Минтруда России  по теме \"Методы коррекции нежелательного поведения и методы обучения детей с аутизмом посещению медицинских учреждений. Правила общения с детьми с аутизмом\". Более 60 специалистов со всей области смогли поучаствовать в семинаре очно и дистанционно. Обучение провела Максимова Мария Андреевна –  специалистов по Прикладному анализу, куратор ресурсного класса МОУ СОШ посёлка Молодежный, соучредитель АНО \"АПЦ РАСсвет\".</p>\r\n<p>Повышение компетенции специалистов в государственной системе образования проходило также  по специально разработанной программе \"Развитие инклюзивного образования для детей с расстройствами аутистического спектра по модели ресурсный класс\". В январе в Рассвете стартовал бесплатный обучающий курс для специалистов образовательных учреждений Иркутска и области, родителей детей с РАС и всех желающих. </p>\r\n<p>Первый модуль курса проходил в дистанционном формате и длился 4 недели. В конце наших студентов ждало тестирование, по итогу которого лучшие из них получили доступ ко второму модулю обучения, на котором было много практики, встреч в офлайне и ещё больше информации о создании инклюзивных условий в системе образования.  </p>\r\n<p>В рамках второго модуля обучающей программы были проведены практические тренинги по методам обучения в АВА, альтернативной коммуникации и другие. Он завершилася в марте. Двадцать студентов прошли итоговое тестирование, на котором разбирались вопросы по основным темам курса. В актуализации полученных знаний студентам помогли рабочие тетради, разработанные специально для нашего курса.\r\n</p>\r\n<p>На третьем модуле обучающей программы, который проходил в апреле, двадцать человек проходили стажировку на базе ресурсного класса школы посёлка Молодежный и в центре \"РАСсвет\". Под руководством АВА-инструкторов, тьюторов, педагогов и кураторов студенты курса попробовали на практике применять полученные теоретические навыки из двух предыдущих модулей обучения. \r\n</p>\r\n<p>Среди студентов курса были педагоги коррекционных и общеобразовательных школ, сотрудники частных образовательных организаций и родители детей с аутизмом.\r\nНекоторые участники приехали даже из города Чита, где планируют открыть собственный ресурсный класс. Опыт и знания, приобретенные в ходе программы, стали ценным вкладом в их будущие проекты.</p>\r\n<p>Обучающая программа реализована с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n\r\n', '/images/RK/2022/13/1.jpg', '2022-04-29', '/images/RK/2022/13/2.jpg;\r\n/images/RK/2022/13/3.jpg;\r\n/images/RK/2022/13/4.jpg;\r\n/images/RK/2022/13/5.jpg;\r\n/images/RK/2022/13/6.jpg;\r\n/images/RK/2022/13/7.jpg;\r\n/images/RK/2022/13/8.jpg;\r\n/images/RK/2022/13/9.jpg', '', '', '', '', 0, '', '10'),
(567, '18', 'Стажировка по направлению развития помощи молодым людям с РАС', 'В скором времени наши ребята вырастут, поэтому важно уже сейчас планировать и создавать системы помощи взрослым людям с аутизмом. В этом нам может помочь опыт других общественных организаций.', '<p>Те, кто участвует или наблюдает за жизнью \"РАСсвета\" знает, что мы работаем, в основном, с детьми дошкольного и школьного возраста. Но мы понимаем, в скором времени наши ребята вырастут, поэтому важно уже сейчас планировать и создавать системы помощи взрослым людям с аутизмом. В этом нам может помочь опыт других общественных организаций.</p>\r\n<p>В начале апреля сотрудники \"РАСсвета\" отправились на стажировку в город Волжск, в организацию с говорящим названием \"Ребёнок с будущим\", чтобы узнать о местной комплексной системе помощи подросткам и молодым людям с РАС.  Виктория Кузнецова, руководитель отдела мастерских и один из создателей \"Дом синего слона\" рассказала о командировке, поделилась впечатлениями:</p>\r\n<p>— В Волжске на полные три дня, с утра до вечера, мы с головой погрузились в работу школ, мастерских, театральной и музыкальной студий, типографии. Познакомились с пока ещё новым для нас направлением - волонтёрским объединением. Обедали в тренировочной квартире - для нас приготовили сами ребята, это было здорово.</p>\r\n<p>— Рассказать обо всем увиденном и услышанном не представляется возможным в рамках одной лишь публикации. Скажу только, что мы увидели какой мощной двигающей силой обладают родители детей с особенностями развития. Это именно те люди, которые постоянно, и часто без сна, готовы трудиться над тем, чтобы их дети были счастливы и могли как все - жить самостоятельно, работать, участвовать в деятельности творческих коллективов, ездить компанией на весёлый отдых. И это очень похоже на нашу работу здесь, в \"РАСсвете\" - неутомимая энергия, мотивация, цель, результат. </p>\r\n<p>— Было очень приятно наблюдать насколько деликатно включён каждый юноша или девушка с РАС в общий процесс деятельности. Там нет лишних или \"неудобных\", каждому находится дело, с которым ребята успешно справляются, неважно - типография это или небольшая роль в спектакле.</p>\r\n<p>— При ресурсных классах существуют факультативы для детей и их родителей. Это выглядит, как формат обычного урока в школе, с переменами и играми, но родители выступают в роли тьютора для своего ребёнка и полностью работают с ним. Подобные факультативы открывают перед родителями уникальные возможности обучения здесь и сейчас, укрепляют связь с ребёнком и формируют доверительные паттерны с педагогами класса. \r\n</p>\r\n<p>— В Волжске мы, педагоги и родители школьников и подростков с РАС, своими глазами увидели, что жизнь у людей с аутизмом после 18 есть: интересные, насыщенные будни юношей и девушек, которые они проводят в работе, развитии и организованном веселом отдыхе. Это придаёт нам сил работать дальше, развивать деятельность центра, привлекать других родителей с активной жизненной позицией, открывать новые направления для будущего наших детей. \r\n</p>\r\n<p>Скоро в \"РАСсвете\" пройдёт стратсессия по итогам стажировки, на ней мы обсудим какие изменения ещё необходимо внести, чтобы мы могли помогать ещё большему количеству  людей с РАС и их семьям.</p>\r\n<p>АНО \"АПЦ Рассвет\" - участник проекта \"Создание сети творческих досуговых центров для молодых людей с РАС \"Культурный код - возможность для каждого\"\". Благодарим организаторов и партнеров проекта: Ассоциация \"Аутизм Регионы\" , Фонд «Выход».  Аутизм в России . Проект реализуется с использованием гранта Президента Российской Федерации на реализацию проектов в области культуры, искусства и креативных (творческих) индустрий Президентский фонд культурных инициатив. </p>', '/images/DSS/2022/17/1.jpg', '2022-04-30', '/images/DSS/2022/17/1.jpg;\r\n/images/DSS/2022/17/2.jpg;\r\n/images/DSS/2022/17/3.jpg;\r\n/images/DSS/2022/17/4.jpg;\r\n/images/DSS/2022/17/6.jpg;\r\n/images/DSS/2022/17/5.jpg', '/images/DSS/2022/17/1.jpg;SSXWbWe5oIY;\r\n/images/DSS/2022/17/1.jpg;6KI1pLk9l8U', '', '', '', 0, '', '19');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(569, '4', 'Супервизирование \"РАСсвета\"', 'Мы уже писали ранее, что такое супервизии в АВА-терапии и чем они важны. Педагоги нашего центра проходят еженедельные дистанционные супервизии и дважды в год - очные.', '<p>Мы уже писали ранее, что такое супервизии в АВА-терапии и чем они важны. Педагоги нашего центра проходят еженедельные дистанционные супервизии и дважды в год - очные.</p>\r\n<p>Очные супервизии имеют свои преимущества: супервизор на практике с детьми может показать методы работы и проследить за правильностью их выполнения, на месте внести корректировки в работу педагогов. \r\n</p>\r\n<p>На прошлой неделе \"РАСсвет\" посетила наш супервизор Наталия Александровна Мальтинская (сертифицированный поведенческий аналитик ВСВА, специальный психолог, кандидат медицинских наук). На протяжении 3 дней (по 5 часов в день) она наблюдала за действиями педагогов центра во время индивидуальных занятий и обучения в коммуникативных группах, консультировала и координировала их работу. \r\n</p>\r\n<p>Каждый ученик получил предварительную оценку имеющихся навыков, были определены стратегии по учебному процессу на год. </p>\r\n<p>Вместе с супервизором мы разобрали вопросы по нежелательному поведению детей и внесли корректировки в программы обучения. Также подробно проработали вопросы с  навыками учебного поведения и речевыми навыками. \r\n</p>\r\n<p>Одному нашему 5-летнему ученику был предложен интересный способ развития навыков поведения слушателя. Сам метод имеет название \"Смешанные блоки\". Наталия Александровна показала, как можно использовать различные игровые материалы с ребенком для того, чтобы он был сосредоточен на обучении. Стало интересно? Тогда читайте краткое описание кейса!</p>\r\n<p>Итак, цель - добиться от ребенка того, чтобы он был сосредоточен на задании и выполнял инструкции педагога. </p>\r\n<p>В работу берутся материалы, интересующие мальчика (вкладыши, лизуны, поп ит и т.п.) Темп занятия должен быть очень быстрым - не больше 1 минуты. Перед учеником кладут предмет и дают задание, которое он может легко и быстро выполнить. Например, ребёнку дали знакомые вкладыши, он их быстро собрал. Затем положили перед ним палочки, чтобы он их опустил по одной в прорезь коробки. Далее задание на соотнесение предмета с картинкой. И уже после его успешного выполнения, педагог дает целевую инструкцию, например, \"Дай лизун\", и ребенок отдает лизун.</p>\r\n<p>Кроме рекомендаций и практических советов, супервизор провела для наших сотрудников тренинг по написанию обучающих программ и протоколов коррекции поведения. </p>\r\n<p>Следующая очная супервизия планируются в сентябре 2022 года.</p>\r\n<p>Проект \"Социальные навыки и альтернативная коммуникация как основа инклюзивного образования для детей с РАС\" реализуется при поддержке Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n\r\n\r\n\r\n\r\n', '/images/Absolut/24/1.jpg', '2022-05-07', '/images/Absolut/logo.jpg;\r\n/images/Absolut/24/3.jpg;\r\n/images/Absolut/24/4.jpg;\r\n/images/Absolut/24/5.jpg;\r\n/images/Absolut/24/6.jpg;\r\n/images/Absolut/24/7.jpg;\r\n/images/Absolut/24/2.jpg', '', '', 'Новости', '/news.php', 0, '', '9'),
(573, '19', 'Оценки для педагогов', 'В  школе посёлка Молодежный прошли очные консультации с супервизором Наталией Александровной Мальтинской (сертифицированный поведенческий аналитик ВСВА, специальный психолог, кандидат медицинских наук).', '<p>В системе ресурсного класса есть своя иерархия: учеников обучает учитель и частично тьюторы, их работу контролирует куратор, он же проводит тренинги для педагогов класса.\r\nЕщё один необходимый для модели \"Ресурсный класс\" специалист - супервизор - поведенческий аналитик международного уровня, который консультирует куратора и педагогов, разбирает сложные случаи из практики, следит за реализацией программ поведения и обучения, за динамикой продвижения и за соблюдением профессиональных и этических стандартов.</p>\r\n<p>Специалисты \"АПЦ РАСсвет\", работающие с ресурсными классами Иркутска и Иркутского района, проходят супервизии у Наталии Александровны Мальтинской (сертифицированный поведенческий аналитик ВСВА (эксперт АВА), специальный психолог, кандидат медицинских наук) дистанционно 2 раза в неделю. Это помогает им вносить необходимые корректировки и дополнения в программы учеников, исправлять ошибки, которые неизбежно происходят в процессе обучения.</p>\r\n<p>Дважды в год в школах проходят очные консультации с супервизором Наталией Александровной Мальтинской (сертифицированный поведенческий аналитик ВСВА, специальный психолог, кандидат медицинских наук). Команда ресурсного класса школы пос. Молодёжный получила высокую оценку по результатам проделанной за учебный год работы. </p>\r\n<p>В ходе очной супервизии педагоги обсудили с Наталией Александровной вопросы, касающиеся реализации индивидуальных программ обучения и протоколов коррекции поведения для каждого ребёнка, разобрали сложные кейсы.</p>\r\n<p>Для примера, у одного из учеников наблюдается высокочастотное самостимулятивное поведение. Основным методом коррекции такого поведения является сенсорное насыщение ребёнка в перерывах между занятиями. Во время супервизии педагоги искали подходящую замену, чем именно можно заменить ощущения от самостимуляции, чтобы снизить мотивацию к ней. В итоге наметили новую стратегию для работы, которую уже начали внедрять.</p>\r\n<p>Также педагоги обсудили с супервизором инклюзивные маршруты детей: кто из ребят успешно адаптировался к занятиям в большой группе детей и готов к увеличению времени пребывания в общеобразовательном классе, а кому необходимо продолжить освоение программы в индивидуальном формате и в малых группах, а общение со сверстниками из регулярных классов продолжить во время перемен и внеурочных мероприятий.</p>\r\n<p>На данный момент четверо ребят из ресурсного класса посещают уроки физкультуры, музыки и ИЗО с общеобразовательными классами, а двое из них выходят также на уроки русского языка, математики и окружающего мира.</p>\r\n<p>Очную супервизию также прошли ресурсные классы школы №33 микрорайона Лесной и школы посёлка Луговой Иркутского района. </p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/RK/2022/16/1.jpg', '2022-05-11', '/images/grants/pgrants_logo.jpg;\r\n/images/RK/2022/16/1.jpg;\r\n/images/RK/2022/16/2.jpg;\r\n/images/RK/2022/16/3.jpg;\r\n/images/RK/2022/16/4.jpg;\r\n/images/RK/2022/8/2.jpg;\r\n/images/RK/2022/8/3.jpg\r\n', '', '', 'Новости', 'news.php', 0, '', '10'),
(578, '18', 'Подготовительные занятия', 'В мастерских для детей и подростков с РАС \"Дом синего слона\" наши новенькие мастеровые сначала проходят тестирование на дефициты в развитии, для них пишутся программы корректировки поведения, с ними индивидуально занимаются педагоги.\r\n', '<p>Сбор данных  - первый этап в работе по коррекции поведения и одна из первых задач нашего нового проекта \"Ты не один в этом мире\". О чём этот проект? О том, что люди могут самореализовываться, находить дело по душе и по способностям, общаться без предубеждений независимо от того, нейротипичных они или нейроотличные.</p>\r\n<p>На базе нашей мастерской \"Дом синего слона\" 20 детей и подростков учатся прикладному творчеству, взаимодействию в коллективе, саморегуляции. С помощью прикладного анализа поведения, встроенного в структуру индивидуальных и групповых занятий, педагоги помогут участникам проекта скорректировать поведение, затрудняющее обучение. </p>\r\n<p>В мастерских для детей и подростков с РАС \"Дом синего слона\" наши новенькие мастеровые сначала проходят тестирование на дефициты в развитии, для них пишутся программы корректировки поведения, с ними индивидуально занимаются педагоги: учат безопасно обращаться с инструментами и материалами, работают с мотивацией ребёнка, его сенсорными особенностями. Затем, после наблюдения и согласия куратора, ребёнок приступает к групповым занятиям в мастерской.\r\n</p>\r\n<p>Среди подготовительной работы в \"Доме синего слона\" - индивидуальные уроки по ИЗО и декоративно-прикладному творчеству. Дети тренируются катать колбаски и кругляшки из пластилина, вырезать стеками, чтобы затем попробовать создать из глины прекрасные интерьерные вещи. Они учатся держать кисточки в руках и рисовать различными по текстуре красками (и не есть эти краски при этом, не размазывать их по всем поверхностям). Педагог помогает детям освоить ручное шитье, а затем и швейную машину  - на индивидуальных занятиях в этом году ребята шили забавные игольницы-кактусы, а на групповых - подушки и мягкие игрушки.</p>\r\n<p>На индивидуальные занятия ходят не только дети дошкольного возраста, но и ребята постарше, которые пока не готовы к работе в группе и индивидуальный формат занятий для них комфортнее.</p>\r\n<p>Рассказывает Евгения, мама Влада:</p>\r\n<p>-Торнадо, пуля, вечный двигатель, торпеда - как только Влада ни называют окружающие. Неугомонный, вечно в движении! Присесть или перевести дыхание, можно только когда он спит. А ещё в мастерской - ведь там он занят, а если ещё тема занятия совпадает с интересами Влада, и выражение его лица демонстрирует неподдельный интерес, то счастью родителям нет предела.</p>\r\n<p>У Влада практически отсутствует самозанятость, поэтому для него и для нас так важны мастерские - ведь благодаря разнообразным темам, которые выбирают педагоги, ребёнок может показать что именно ему нравится делать и донести до нас с помощью действий и эмоций свои истинные желания. </p>\r\n<p>А ещё мастерские - это разнообразие в нашей жизни. Очень скучно ходить в одни и те же места и делать всегда одно и то же. В мастерских этого нет, это, если хотите, сорочинская ярмарка. Влад хоть и настороженно относится ко всему новому, но, я уверена, после каждого занятия убеждается, что мастерские - это интересно и не стоит всегда бояться нового. В Иркутске подобных мест нет, и мы бы с радостью взрослели вместе с нашими мастерскими, ведь мы ещё много чего не делали!</p>\r\n<p>Проект \"Ты не один в этом мире\" реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/DSS/2022/19/1.jpg', '2022-05-13', '/images/grants/pgrants_logo.jpg;\r\n/images/DSS/2022/19/1.jpg;\r\n/images/DSS/2022/19/2.jpg;\r\n/images/DSS/2022/19/3.jpg;\r\n/images/DSS/2022/19/4.jpg;\r\n/images/DSS/2022/19/5.jpg;\r\n/images/DSS/2022/19/6.jpg;\r\n/images/DSS/2022/19/7.jpg;\r\n/images/DSS/2022/19/8.jpg;\r\n/images/DSS/2022/19/10.jpg;\r\n/images/DSS/2022/19/11.jpg;\r\n/images/DSS/2022/19/12.jpg;\r\n/images/DSS/2022/19/9.jpg', '', '', 'Новости', '', 0, '', '7'),
(579, '20', 'Специнтересы детей с РАС', 'Чем обычно увлекаются маленькие дети? Это могут быть игрушечные машинки, куклы, истории о динозаврах, раскраски, карусели... Если мы говорим о ребенке с аутизмом, то его круг интересов может оказаться очень специфичным...', '<p>Чем обычно увлекаются маленькие дети? Это могут быть игрушечные машинки, куклы, истории о динозаврах, раскраски, карусели...</p>\r\n<p>Если мы говорим о ребенке с аутизмом, то его круг интересов может оказаться очень специфичным: коллекционирование зубных щёток и хозяйственных губок, алфавит (иногда только буквы изображённые в определенной цветовой гамме - синие или красные), цифры или что-то очень крутое, вроде математических задач из университетской поограммы, или же классическая тема для РАС - поезда. Выбор предметов и проявления интереса зависят от уровня нывыков ребёнка и его уровня развития.</p>\r\n<p>Конечно, бывает, что дети с аутизмом интересуются чем-то обычным, как и их нейротипичные сверстники (поездки на машине, космос, рисование), но для них эта тема становится навязчивой. </p>\r\n<p>Специальный интерес - это то, чем ребёнок с аутизмом увлечён больше всего, тема, с которой его почти невозможно переключить. Иногда специнтерес отражается в стереотипных разговорах и действиях, когда любые изменения рутины вызывают стресс и проблемное поведение.\r\n</p>\r\n<p>АВА-инструкторы \"РАСсвета\" используют специнтересы учеников для создания мотивационных условий в обучении и в качестве поощрения. В группе дошкольников у Саввы специнтерес - лифты и эскалатор. Педагоги находят и распечатывают мальчику раскраски с изображением любимой техники, создают лото с карточками-изображениями разных лифтов, вовлекают его в общую игру таким образом. </p>\r\n<p>Одногруппник Саввы Фёдор любит рисовать, поэтому ему дают бумагу, кисточки и краски после успешно выполненного задания.</p>\r\n<p>Сёма из группы PECS любит игрушки на верёвочке, качать крутить предметы. Усложняя его игру, педагоги развивают мелкую моторику мальчика, добавляют новые задания, разбавляя стереотипные движения.</p>\r\n<p>Проект \"Социальные навыки и альтернативная коммуникация как основа инклюзивного образования для детей с РАС\" реализуется при поддержке Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n\r\n\r\n\r\n\r\n', '/images/Absolut/27/1.jpg', '2022-05-16', '/images/Absolut/logo.jpg;\r\n/images/Absolut/27/3.jpg;\r\n/images/Absolut/27/1.jpg;\r\n/images/Absolut/27/2.jpg', '', '', 'Новости', '/news.php', 0, '', '9'),
(585, '20', 'Развиваем коммуникацию и эмоциональный интеллект', 'Коммуникативные группы в \"РАСсвете\" посещают дети от 3 и до 12 лет. С нашими учениками занимается психолог Анна Олеговна Леонтьева. Сегодня делимся её советами по работе над навыком коммуникации у детей с аутизмом и другими особенностями развития. ', '<p>Вы знаете, что в некоторых иркутских детских садах есть разновозрастные группы? Это полезный опыт как для малышей, так и для ребят постарше. Ребенок, общаясь с детьми разного возраста, развивает свой эмоциональный интеллект, обращает внимание не только на свой внутренний мир, но учится понимать других людей. </p>\r\n<p>Коммуникативные группы в \"РАСсвете\" посещают дети от 3 и до 12 лет. Кто-то из них общается преимущественно с помощью карточек PECS, кто-то болтает без умолку, но и тем и другим нужна помощь в освоении навыка коммуникации. С нашими учениками занимается индивидуально и в группе психолог Анна Олеговна Леонтьева. Сегодня делимся её советами по работе над навыком коммуникации у детей с аутизмом и другими особенностями развития. </p>\r\n<p>Чтобы научить ребёнка взаимодействовать с другими людьми, надо сначала узнать, где и с кем ему приходится общаться обычно или подготовить к конкретным условиям взаимодействия в будущем. Итак, мы определяем место: детский сад, игровая площадка, дополнительные занятия, поликлиника, магазин, общественный транспорт, посещение гостей и т.п.</p>\r\n<p>Затем смотрим на категории отношений, с кем ребёнок общается: родители, сестры/братья, другие родственники, воспитатели, одногруппники/одноклассники, врачи, продавцы...</p>\r\n<p>Следующий шаг: проигрывание различных ситуаций через социальные истории, картинки, сюжетные игры с игрушками, на которых мы проговариваем возможные диалоги. Если у ребёнка есть проблемы в общении, то с помощью диалогов работаем над ними.\r\n</p>\r\n<p>Например, один наш 6-летний ученик начинает кричать и падать на землю или пол, когда кто-то чужой обращается к нему с замечанием по поводу его поведения (громких разговоров, бега по коридорам). На занятии мы берём с ним игрушки, и показываем мальчику через их диалог, как себя вести правильно. Даём подсказки, проговариваем фразы, просим повторить, в другой раз задаём наводящий вопрос, говорим: \"Теперь твоя очередь спрашивать\". Важно подбирать разные фразы, чтобы не закреплять шаблонную речь. </p>\r\n<p>На занятиях с детьми с нарушениями речи, интеллекта, больше работаем с действиями, даём физические подсказки. Если ребёнок бьёт других детей для привлечения внимания, надо показать ему, как приглашать в игру с помощью жестов, карточек. Создавать игровые ситуации и увлекать детей в них. \r\n</p>\r\n<p> Также важно научить ребёнка различать свои и чужие эмоции. Сперва учим, какие эмоции существуют по картинкам, тренируется с зеркалом, показываем видео, гифки, фото людей, усложняет задачу постепенно.\r\n</p>\r\n<p>Проект \"Социальные навыки и альтернативная коммуникация как основа инклюзивного образования для детей с РАС\" реализуется при поддержке Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n\r\n\r\n\r\n\r\n', '/images/Absolut/28/2.jpg', '2022-05-20', '/images/Absolut/logo.jpg;\r\n/images/Absolut/28/3.jpg;\r\n/images/Absolut/28/1.jpg;\r\n/images/Absolut/28/4.jpg;\r\n/images/Absolut/28/5.jpg;\r\n/images/Absolut/28/2.jpg', '', '', 'Новости', '/news.php', 0, '', '9'),
(586, '13,19', 'СМИ о ресурсных классах ', '\"23 ребенка с расстройством аутистического спектра завершили учебный год в ресурсных классах в Иркутской области.\" В областных СМИ вышли сюжеты и публикации об успехах учеников ресурсных классов для детей с аутизмом Иркутска и Иркутского райна.', '<p>В областных СМИ вышли сюжеты и публикации об успехах учеников ресурсных классов для детей с аутизмом Иркутска и Иркутского райна.</p>\r\n<p><b>23 ребенка с расстройством аутистического спектра завершили учебный год в ресурсных классах в Иркутской области</b></p>\r\n<p>19 мая вышел сюжет в выпуске новостей на канале \"Россия 24\". Съёмочная группа \"Вести-Иркутск\" побывала на уроке математики в школе посёлка Молодежный, где ученик ресурсного класса Стёпа занимался наравне с нейротипичными сверстниками. </p>\r\n<p>Посмотреть сюжет можно на сайте программы \"Вести-Иркутск\" <a href=\"https://vestiirk.ru/news/23-rebenka-zavershili-uchebnyi-god-v-resursnykh-klassakh-irkutskoi-oblasti/\" target=\"_blank\">по ССЫЛКЕ</a>.</p>\r\n<p><b>Школа с доступным образованием для всех</b></p>\r\n<p>Продолжаем знакомить разные категории обществености с проблемами аутизма и путями их решения. В газете \"Мои года\" вышла статья, где мы рассказываем об инклюзивном образовании в Приангарье.</p>\r\n<p>\"Мировая статистика говорит, что у 1 % населения есть аутизм. Исходя из этих данных, 6000 детей в Иркутской области имеют аутизм, но на учете в поликлиниках Прибайкалья состоят всего лишь 102 ребенка, так как у нас имеются серьезные проблемы с диагностированием. И, к сожалению, не только с ним.</p>\r\n<p>Коррекционные школы и классы не обладают необходимыми кадрами и образовательными программами для обучения детей с аутизмом. Ведь их нужно учить не только читать, писать и решать примеры, но и тому, как вести себя в обществе, работать в коллективе, справляться с особенностями расстройства (повышенной тревожностью, непереносимостью определенных звуков, прикосновений или других ощущений), корректировать поведение\".\r\n</p>\r\n<p></p>\r\n<p>Полностью статью читайте <a href=\"https://moi-goda.ru/irkutsk-slovo-chitatelya/shkola-s-dostupnim-obrazovaniem-dlya-vsech\" target=\"_blank\">по ССЫЛКЕ</a> на сайте газеты.</p>\r\n<p><b>Особенное лето — особенным детям</b></p>\r\n<p>\"Отчаяние, огромная усталость, отсутствие сил — наиболее частые жалобы, которые можно прочитать на форумах, где общаются родители детей с диагнозом «расстройство аутистического спектра» (РАС), или по-простому аутизм. Они не могут расслабиться ни на минуту, а уж мечтать о том, чтобы отправить своего ребёнка отдыхать, например, в лагерь, им не приходится. Руководство средней общеобразовательной школы посёлка Молодёжный решили сломать этот стереотип и организовали для особенных детей летнюю площадку на базе учебного заведения\".\r\n</p>\r\n<p>Полностью статью газеты \"Ангарске огни\" о занятиях и каникулах учеников ресурсного класса школы посёлка Молодежный можно прочитать <a href=\"https://angarogni.ru/?p=18446\" target=\"_blank\">ПО ССЫЛКЕ</a>.</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/RK/2022/18/1.jpg', '2022-06-18', '/images/grants/pgrants_logo.jpg;\r\n/images/RK/2022/18/2.jpg;\r\n/images/RK/2022/18/3.jpg', '', '', 'Новости', 'news.php', 0, '', '10'),
(588, '19', 'Прогресс учеников ресурсного класса ', 'Первый учебный год в ресурсном классе школы посёлка Луговой Иркутского района окончен. Чему научились ребята за этот год, рассказали куратор Вероника Сергеевна Артамонова и классный руководитель Елена Валерьевна Синенкова.\r\n', '<p>Первый учебный год в ресурсном классе школы посёлка Луговой Иркутского района окончен. Для 7 первоклассников он оказался не менее насыщенным, чем для их сверстников из регулярных классов, тем более, что в общешкольных мероприятиях они участвовали наравне со всеми.</p>\r\n<p>Чему научились ребята за этот год, рассказали куратор Вероника Сергеевна Артамонова и классный руководитель Елена Валерьевна Синенкова.</p>\r\n<p>- Мы хорошо поработали над развитием у наших первоклашек самостоятельности в рамках уроков по социально-бытовой ориентации: дети сами снимают и надевают верхнюю одежду, освоили застежки с молниями и пуговицами, складывают аккуратно физкультурную форму, перед началом урока выкладывают без напоминаний из рюкзака нужные школьные принадлежности, а перед тем, как уходить домой складывают все назад. По школьным коридорам ребята ходят организовано, по парам и в сопровождении лишь одного учителя, хотя в начале им требовалась поддержка тьюторов, - делится Елена Валерьевна.</p>\r\n<p>- У детей огромный прогресс в коммуникативных навыках. У всех учеников были проблемы с просьбами: они не просили помощь, когда она им требовалась, не говорили какое поощрение хотят получить, кто-то не мог попроситься в туалет или показать, что ему нужен перерыв. Сейчас с этим проблем нет. И, например, если в столовой Еве забыли положить ложку, она об этом скажет, спокойно попросит, - говорит Вероника Сергеевна.\r\n</p>\r\n<p>Чтобы создать комфортные условия для общения учеников с аутизмом и их сверстников из параллели, педагоги проводили \"уроки доброты\", на которых рассказывали об особенностях людей с РАС, а также организовывали общие игры на переменах в коридорах и в сенсорной зоне ресурсного класса. Любимыми командными играми первоклассников стали ручеек, догонялки и хороводные игры. У детей из ресурсного класса появились приятели в школе, с которыми можно обменяться игрушками или книжками. </p>\r\n<p>- Наш класс участвовал во всех внутришкольных конкурсах и мероприятиях (линейках, праздниках). Для выставок на День космонавтики мы сделали макет ракеты, к Дню Победы - \"Катюшу\" из картона. Кстати на празднование 9 мая мы выступили на школьной сцене с танцем. Дети отлично справились. У себя в классе делали огород на подоконнике - год у нас прошёл увлекательно, и это с учётом нескольких карантинов из-за короновируса и ОРВИ, когда мы не могли общаться с кем-то вне класса, - рассказывает Елена Валерьевна.</p>\r\n<p>Если говорить об академических навыках, то здесь тоже есть пока небольшие успехи, у каждого свои: Ева работает с цифрами в пределах 10, учится читать, а Тимофей решает примеры до 10 на прибавление. Все дети обучаются по общеобразовательной адиптированной программе 8.3. Ориентироваться в ходе уроков им помогают индивидуальные и групповые визуальные расписания.</p>\r\n<p>- Дети научились самостоятельно набирать на планшетке расписание перед началом занятий, следовать ему. Сейчас они могут заниматься все 40 минут урока, а начинали с 10-15 минут. Некоторые ученики освоили учебное поведение, другим ещё требуется поддержка тьютора, чтобы вовремя поднять руку, выйти к доске или ответить на вопрос учителя. Будем работать над этим в следующем году, - резюмировала Вероника Сергеевна.\r\n</p>\r\n<p>На летних каникулах педагоги ресурсного класса посоветовали родителям своих учеников не забывать о небольших занятиях с детьми: читать, писать, решать примеры, чтобы не растерять навыки, но при этом  хорошенько отдохнуть, набраться сил и впечатлений перед 1 сентября.</p>\r\n<p>Благодарим организаторов и партнёров проекта \"Эстафета компетенции НКО\" : Фонд «Выход».  Аутизм в России, Ассоциация \"Аутизм Регионы\". Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фонд президентских грантов.</p>\r\n', '/images/RK/2022/20/1.jpg', '2022-05-24', '/images/grants/pgrants_logo.jpg;\r\n/images/RK/2022/20/2.jpg;\r\n/images/RK/2022/20/3.jpg;\r\n/images/RK/2022/20/4.jpg;\r\n/images/RK/2022/20/1.jpg', '', '', '', '', 0, '', '0'),
(589, '16', 'Перенять опыт профессионалов', 'В мае 6 сотрудников \"РАСсвета\" прошли стажировку в АВА-центре \"Раскраски\" в Пятигорске. Тема обучения для наших специалистов не новая - тестирование по VB-MAPP и ABLLS-R. ', '<p>Именитый педагог и писатель Антон Макаренко говорил: \"Учитель может учить других до тех пор пока учится сам\". И мы с ним в этом солидарны. В мае 6 сотрудников \"РАСсвета\" прошли стажировку в АВА-центре \"Раскраски\" в Пятигорске. Тема обучения для наших специалистов не новая - тестирование по VB-MAPP и ABLLS-R. В \"РАСсвете\" мы используем эти протоколы для определения дефицитов детей, отслеживаем динамику развития навыков в процессе обучения.</p>\r\n<p>На стажировке у специалистов \"РАСсвета\" появилась возможность обменяться опытом с коллегами из других центров, узнать иные, эффективные подходы и получить профессиональную поддержку супервизора Оксаны Строгановой, поведенческого аналитика ВСВА. </p>\r\n<p>- В рамках стажировки проходил двухдневный тренинг, на котором мы практиковались в тестировании навыков учащихся \"Раскрасок\". Супервизор поделилась наработками материалов для тестирования и  шаблонами, показала, как быстро и удобно можно заполнять протоколы в электронном варианте. Также нам предложили более действенные шаги по развитию навыка имитации, - рассказала об обучении Ева Айвасс, куратор \"РАСсвета\".\r\n</p>\r\n<p>В то же время стажировку в московском центре \"Мой солнечный мир\" под руководством Натальи Малиновой, сертифицированного поведенческого аналитика и супервизора CBA-S, прошла Дарья Евдокимова, АВА-инструктор \"РАСсвета\".</p>\r\n<p>АНО \"АПЦ Рассвет\" - участник проекта \"Создание сети творческих досуговых центров для молодых людей с РАС \"Культурный код - возможность для каждого\"\". Благодарим организаторов и партнеров проекта: Ассоциация \"Аутизм Регионы\" , Фонд «Выход».  Аутизм в России . Проект реализуется с использованием гранта Президента Российской Федерации на реализацию проектов в области культуры, искусства и креативных (творческих) индустрий Президентский фонд культурных инициатив. </p>', '/images/training/2022/28.05/1.jpg', '2022-05-28', '/images/training/2022/28.05/1.jpg;\r\n/images/training/2022/28.05/2.jpg;\r\n/images/training/2022/28.05/3.jpg;\r\n/images/training/2022/28.05/4.jpg;\r\n/images/training/2022/28.05/5.jpg;\r\n/images/training/2022/28.05/6.jpg;\r\n/images/training/2022/28.05/7.jpg', '', '', '', '', 0, '', '19'),
(590, '20', 'Тесты для обучения ', 'Дважды в год наши ученики, посещающие занятия в центре на регулярной основе, проходят тестирование. Оценка различных навыков помогает отслеживать динамику развития ребёнка, понять, где и как скорректировать его программу обучения.', '<p>Дважды в год наши ученики, посещающие занятия в центре на регулярной основе, проходят тестирование. Оценка различных навыков помогает отслеживать динамику развития ребёнка, понять, где и как скорректировать его программу обучения.</p>\r\n<p>Тесты, которыми пользуются наши АВА-специалисты: VB-MAPP, ABLLS-R, AFLS.</p>\r\n<p>Мы подробно рассказывали, чем они друг от друга отличаются в <a href=\"https://rassvet-apc.ru/rc/detail.php?id=505&back=rc\" target=\"_blank\">ЭТОЙ СТАТЬЕ</a>.</p>\r\n<p>Все эти виды тестирований предназначены не только для обучения детей с аутизмом. По ним можно работать и с нейротипичными детьми, выявляя их сильные стороны и то, над чем нужно поработать педагогу (воспитателю/учителю/психологу/логопеду).</p>\r\n<p>В медицине есть общепринятое понимание возрастной нормы, критерии, по которым оценивают психическое и физическое развитие детей. Тестирования АВА-терапии позволяет более детально изучить вехи развития ребёнка.</p>\r\n<p>Например, тестируя ученика по VB-MAPP, мы можем проверить, как обстоят у него дела с коммуникацией, умеет ли он общаться с другими детьми, играть в совместные игры, насколько они разнообразны. У многих ребят сейчас из-за увлечения компьютерным играми на телефонах и планшетах, есть проблемы с играми в реальной жизни. Но игровая деятельность очень важна, со временем она переходит в учебную деятельность. Так что пренебрегать этим навыком не стоит.</p>\r\n<p>Тестирование по ABLLS-R у дошкольника покажет, в каких направлениях (речевых, коммуникативных, учебных) нужно дополнительно позаниматься перед первым классом.\r\n</p>\r\n<p>Оценивая поведение по AFLS, педагог и родители проверяют, насколько самостоятельным может быть ребёнок, с какими домашними делами справится и чему его нужно научить в первую очередь.</p>\r\n<p>Проект \"Социальные навыки и альтернативная коммуникация как основа инклюзивного образования для детей с РАС\" реализуется при поддержке Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n\r\n\r\n\r\n\r\n', '/images/Absolut/29/0.jpg', '2022-05-31', '/images/Absolut/logo.jpg;\r\n/images/Absolut/29/3.jpg;\r\n/images/Absolut/29/1.jpg;\r\n/images/Absolut/29/2.jpg', '', '', 'Новости', '/news.php', 0, '', '9'),
(591, '20', 'В продолжении темы тестирования...', 'Расскажем о тесте для оценки динамики и выявления проблем у детей с аутизмом - ATEK. Он достаточно простой, его могут проводить родители, не обладающие специальными знаниями в педагогике и психологии.', '<p>Расскажем о тесте для оценки динамики и выявления проблем у детей с аутизмом - ATEK. Он достаточно простой, его могут проводить родители, не обладающие специальными знаниями в педагогике и психологии.</p>\r\n<p>ATEК состоит из 4 частей:</p>\r\n<p>I. Речь / языковая коммуникация;</p>\r\n<p>II. Общительность;</p>\r\n<p>III. Сенсорная / познавательная способность;</p>\r\n<p>IV. Здоровье / Физическое состояние / Поведение. </p>\r\n<p>Этот тест не является диагностическим, он не определяет, есть ли у человека аутизм. АТЕК используют, чтобы следить за успеваемостью ребёнка, по нему удобно сравнивать изменения состояния до, во время и после обучения по новой методике или лечения/курсов реабилитации. Если тест родители проводят вдвоём, им рекомендуется заполнять опросник по отдельности, затем расчитывать средний результат, для уменьшения человеческого фактора.</p>\r\n<p>Пройти тест онлайн можно <a href=\"http://atektest.ru/\" target=\"_blank\">ЗДЕСЬ</a>.</p>\r\n<p>Скачать приложение с тестом -  <a href=\"https://play.google.com/store/apps/details?id=ru.atec\" target=\"_blank\">ЗДЕСЬ</a>.</p>\r\n<p>Проект \"Социальные навыки и альтернативная коммуникация как основа инклюзивного образования для детей с РАС\" реализуется при поддержке Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n\r\n\r\n\r\n\r\n', '/images/Absolut/29/4.jpg', '2022-06-01', '/images/Absolut/logo.jpg', '', '', 'Новости', '/news.php', 0, '', '9'),
(592, '22', 'Праздник детства', 'Праздник детства вчера удался на славу!  Спасибо за помощь в проведении программы классных аниматоров из \"Мango kids\", и наших друзей из компании \"Иркутскэнергосбыт\" за подарки!\r\n', '<p>Праздник детства вчера удался на славу! Мы разделили детей на 2 группы и провели мероприятие дважды за день, чтобы каждый мог насладиться веселой атмосферой и зарядиться хорошим настроением без суматохи и лишнего шума. Спасибо за помощь в проведении программы классных аниматоров из \"Мango kids\", и наших друзей из компании \"Иркутскэнергосбыт\" за подарки! С нас -  фото с эмоциями наших любимых учеников❤️\r\n\r\n</p>', '/images/news/2022/3/3.jpg', '2022-06-02', '/images/news/2022/3/1.jpg;\r\n/images/news/2022/3/2.jpg;\r\n/images/news/2022/3/3.jpg;\r\n/images/news/2022/3/4.jpg;\r\n/images/news/2022/3/5.jpg;\r\n/images/news/2022/3/6.jpg;\r\n/images/news/2022/3/7.jpg;\r\n/images/news/2022/3/8.jpg;\r\n/images/news/2022/3/9.jpg', '', '', 'Новости', 'news.php', 0, '', '0'),
(595, '4', 'Готовимся к летнему лагерю', 'Весной в центре \"РАСсвет\" стартовал уникальный проект под названием \"Есть контакт!\" Участники проекта - семьи, воспитывающие детей с аутизмом, этим летом отправятся в летний лагерь на Байкале. Сейчас специалисты \"РАСсвета\" проводят подготовительную работу. ', '<p>Весной в центре \"РАСсвет\" стартовал уникальный проект под названием \"Есть контакт!\", направленный на поддержку семей, воспитывающих детей и подростков с расстройствами аутистического спектра (РАС). Проект включает комплекс мероприятий, направленных на развитие физических способностей, навыков общения, взаимодействия и творчества участников.</p>\r\n<p>Основные направления проекта:</p>\r\n<p>- Анкетирование и составление индивидуальных программ обучения для каждого ребёнка;</p>\r\n<p> - Индивидуальные и групповые занятия, направленные на улучшение физической активности, коммуникативных навыков, социального взаимодействия, игровых и бытовых умений. Занятия проводятся с применением методов Прикладного анализа поведения (ABA).</p>\r\n<p>- Недельная смена в инклюзивном коммуникативном лагере с активными мероприятиями: походы, спортивные игры, работа с психологом и творческими педагогами.</p>\r\n<p>- Групповые мастер-классы для семей детей с РАС по созданию декоративных изделий.</p>\r\n<p> - Семинары для специалистов коррекционной сферы и родителей, где будут представлены методики и опыт, накопленные в рамках проекта.</p>\r\n<p>- Заключительная оценка: тестирование для определения динамики развития навыков у детей с РАС, полученных в процессе участия в проекте.</p>\r\n<p>Специалисты \"РАСсвета\" активно готовят участников к предстоящему летнему лагерю. Детям была проведена диагностика по шкале ABLLS-R, оценены их социальные навыки, готовность к участию в групповых занятиях и способность адаптироваться к изменениям. На основе результатов были разработаны индивидуальные образовательные программы, которые включают протоколы по развитию социальных навыков и игровой деятельности.</p>\r\n<p>Занятия проходят в форме активных игр: мяч, лото, догонялки, соревнования. Это позволяет детям учиться взаимодействию со сверстниками, улучшать навыки ожидания, делиться игрушками и соблюдать правила игр.</p>\r\n<p>Психолог центра Анна Леонтьева проводит опросы и консультации, дает рекомендации, как адаптировать ребенка с РАС к изменениям в окружающей среде, уменьшить его тревожность в новой обстановке с незнакомыми людьми. Мы собрали несколько советов психолога и делимся ими с вами.</p>\r\n<p>Перед тем, как куда-то отправиться, используйте проактивные методы визуальной поддержки – визуальное расписание, планшетка с карточками, нарисованная от руки или составленная с фотографиями мест карта. Глядя на них ребенок узнает маршрут, будет понимать, кто и что его ждет.</p>\r\n<p>Снять тревожность ребенку в дороге помогут любимые игрушки, книжки, вещи, музыка. Вы можете переключать его внимание на уже знакомую игру, чтобы не было времени зацикливаться на чем-то негативном.</p>\r\n<p>Важно подкреплять спокойное поведение ребенка, не забывайте хвалить своего путешественника за то, что он хорошо себя ведет. При этом нужно говорить коротко и четко, это также поможет снизить тревожность.</p>\r\n<p>На новом месте создайте благоприятную атмосферу. Также уделяйте ребенку внимание, когда он спокоен, усиливайте его приемлемое поведение с помощью поощрений: обнимашек, игрушек, активностей и т.п. - у каждого мотивационный стимул будет свой.</p>\r\n<p>Не стоит надеяться, что на новом месте дети с РАС смогут сами завести друзей и найти общий язык с незнакомыми людьми. Помогите своему ребенку наладить коммуникацию: будьте рядом с ним, когда он взаимодействует с другими детьми, создавайте ситуации, располагающие к совместным играм, играйте вместе, объясните новым знакомым простыми словами особенности поведения ребенка.</p>\r\n<p>Проект \"Есть контакт\" реализуется при поддержке Благотворительного фонда «Вклад в будущее».</p>\r\n\r\n\r\n\r\n\r\n', '/images/Kontakt/1/3.jpg', '2022-06-08', '/images/grants/VK_VB.jpg;\r\n/images/Kontakt/1/1.jpg;\r\n/images/Kontakt/1/2.jpg;\r\n/images/Kontakt/1/3.jpg;\r\n/images/Kontakt/1/4.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', '6'),
(601, '19', 'Прорыв года', 'Двое учеников ресурсного класса иркутской школы №33 в  микрорайоне Лесной по итогам их первого учебного года переведены в регулярные классы. С сентября Андрей и Алиса будут учиться уже во втором классе с новыми одноклассниками и со своими тьюторами.', '<p>Двое учеников ресурсного класса иркутской школы №33 в  микрорайоне Лесной по итогам их первого учебного года переведены в регулярные классы. С сентября Андрей и Алиса будут учиться уже во втором классе с новыми одноклассниками и со своими тьюторами.</p>\r\n<p>Вернёмся немного назад, чтобы это достижение стало понятно всем, кто ещё не в теме инклюзивного образования. В этом году в школе №33 впервые открылся ресурсный класс. Для 8 первоклашек собрали целую команду педагогов: учитель, логопед, психолог, дефектолог, тьюторы (у каждого ребёнка свой), специалисты по прикладному анализу поведения - куратор и супервизор. Дети занимались в специально оборудованном классе с партами с высокими бортиками, визуальной поддержкой и пр., у них была отдельная комната для сенсорной разрядки, где ребята могли отдохнуть от занятий и снять напряжение. Перед обучением всех детей протестировали, исходя из дефицитов развития, составили индивидуальные программы по освоению не только академических навыков, но и коммуникационных, социальных, бытовых. Иными словами, в ресурсном классе учили не только читать, писать и считать, но и тому, как и с кем нужно здороваться, отвечать на вопросы, просить о помощи, поддерживать разговор, самостоятельно одеваться, собирать вещи в рюкзак, отвечать у доски и многим другим полезным вещам, которые так легко даются нейротипичным детям, и так сложно - детям с аутизмом.</p>\r\n<p>- С сентября учеников ресурсного класса учили работать в группе, быть внимательными и собранными, слушать фронтальную инструкцию (то, что говорит педагог для всего класса), на переменах тьюторы и учитель организовывали для них совместные игры с ребятами из других классов. Так мы адаптировали наших первоклассников к школе, к обычной среде. Позднее мы ввели на уроках те же задания, учебники и рабочие тетради, по которым занимаются их сверстники в регулярных классах. Когда основные проблемы с поведением у детей были решены (научились просить, ждать, уменьшились вокализации и т.д.), они стали посещать вместе с тьюторами уроки в других первых классах. Два урока в день, по любым предметам, - рассказала Вероника Артамонова, куратор ресурсного класса, специалист \"АПЦ РАСсвет\" .</p>\r\n<p>Как правило, дети справлялись отлично с уроками в других классах, если начинали волноваться и перегружались за 40 минут, тьюторы возвращали их в ресурсный класс или давали отдохнуть в сенсорной зоне.</p>\r\n<p>Учителя, подготовленные куратором к возможным трудностями, связанным с поведением и физическими особенностями детей с РАС, помогали первоклассникам адаптироваться на уроках. \r\nНапример, Алиса писала вместе с учениками регулярного класса диктант, но из-за проблем с моторикой, не могла делать это быстро, поэтому ей дали тот же текст, что и всем, но уже напечатаный, с пропущенными буквами, которые девочка и вписывала.</p>\r\n<p>Кстати, именно у Алисы первой из своего класса, появились школьные друзья и общие с ними интересы.</p>\r\n<p>В мае дети прошли итоговое тестирование, по его результатам школьная администрация решила  перевести Алису и Андрея во вторые регулярные классы. Ещё двое ребят - Дима и Рома, также стали второклассниками, но остались в своём ресурсном классе. Остальные ученики продолжат обучение по пролонгированной программе первого класса, они будут также посещать уроки со сверстниками, играть и общаться с ними на переменах.\r\n</p>\r\n<p>Остаётся лишь открытым вопрос финансирования ресурсного класса. Он был открыт по инициативе и при активном участии родителей детей с аутизмом, специалистов \"АПЦ РАСсвет\" и наших друзей и партнёров по проекту \"Ресурсный класс - Регионы\". Сейчас проект подошёл к концу, а это значит, что пока полное обеспечение ресурсных классов не является заботой государства и Министерства образования, родителям учеников с РАС придётся искать пути решения этого вопроса. </p>\r\n<p>Благодарим организаторов и партнёров проекта \"Ресурсный класс - Регионы. Расширение практики доступного инклюзивного образования детей с РАС и другими ментальными нарушениями\":  Фонд \"Выход\", Ассоциация \"Аутизм-Регионы\" и Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n', '/images/RK/2022/22/1.jpg', '2022-06-11', '/images/RK/2022/22/2.jpg;\r\n/images/RK/2022/22/3.jpg;\r\n/images/RK/2022/22/4.jpg;\r\n/images/RK/2022/22/5.jpg;\r\n/images/RK/2022/22/6.jpg;\r\n/images/RK/2022/22/7.jpg;\r\n/images/RK/2022/22/1.jpg', '', '', '', '', 0, '', '0'),
(605, '18', 'Субботняя ярмарка', 'Первая выставка-ярмарка мастерских для детей и подростков с аутизмом \"Дом синего слона\" в Иркутске в этом году состоялась! В рамках ежегодного фестиваля \"Иркутский Арбат\" 11 июня мы показали свои лучшие работы hand made.', '<p>Первая выставка-ярмарка мастерских для детей и подростков с аутизмом \"Дом синего слона\" в Иркутске в этом году состоялась! В рамках ежегодного фестиваля \"Иркутский Арбат\" 11 июня мы показали свои лучшие работы hand made: кашпо, подсвечники, вазочки из бетона, хлопковые шопперы с рисунками наших ребят, мягкие игрушки и подушки, цветные восковые свечи, ароматное мыло, картины и другие изделия.</p>\r\n<p>День выдался жарким, но только на погоду. Продавать в условиях большой конкуренции (в фестивале участвует более 120 мастеров со всей области) было сложно. И хотя несколько классных вещиц из \"Дома синего слона\" нашли новых хозяев, мы решили вновь перейти на формат онлайн-ярмарок. Совсем скоро на странице мастерской Вконтакте загрузим фотоальбомы и ряды товаров с ценами. Там вы сможете наблюдать за созданием новых изделий и приобретать их для своего интерьера или даже гардероба.</p>\r\n<p>А в конце года у нас запланированно  массовое мероприятие вне стен центра. На нем также будет ярмарка мастерской. </p>\r\n<p>Оставайтесь с нами и будьте в курс наших новостей.</p>\r\n<p>АНО \"АПЦ Рассвет\" - участник проекта \"Создание сети творческих досуговых центров для молодых людей с РАС \"Культурный код - возможность для каждого\"\". Благодарим организаторов и партнеров проекта: Ассоциация \"Аутизм Регионы\" , Фонд «Выход».  Аутизм в России . Проект реализуется с использованием гранта Президента Российской Федерации на реализацию проектов в области культуры, искусства и креативных (творческих) индустрий Президентский фонд культурных инициатив. </p>', '/images/DSS/2022/23/5.jpg', '2022-06-11', '/images/DSS/2022/23/1.jpg;\r\n/images/DSS/2022/23/2.jpg;\r\n/images/DSS/2022/23/3.jpg;\r\n/images/DSS/2022/23/4.jpg;\r\n/images/DSS/2022/23/6.jpg;\r\n/images/DSS/2022/23/5.jpg', '', '', '', '', 0, '', '19'),
(610, '3', 'Обучающая программа в рамках проекта \"Социальные навыки и альтернативная коммуникация\"', 'В «РАСсвете» закончилась летняя стажировка для родителей детей с аутизмом и педагогов, завершающая наш обучающий курс. Мы поделились опытом обучения ребят с особенностями развития разных возрастов, познакомили наших студентов с основами АВА и альтернативной коммуникации PECS.', '<p>В «РАСсвете» закончилась летняя стажировка для родителей детей с аутизмом и педагогов, завершающая наш обучающий курс в рамках проекта \"Социальные навыки и альтернативная коммуникация\", стартовавшего в сентябре прошлого года. Мы поделились опытом обучения ребят с особенностями развития разных возрастов: от 3-летних малышей до подростков, познакомили наших студентов с основами прикладного анализа поведения и альтернативной коммуникации PECS,  теоретическую базу дополнили практическим материалом на тренингах и посещением занятий центра. \r\n</p>\r\n<p>Одной из задач нашего проекта является развитие инклюзивной культуры среди педагогов и родителей, путем информирования о методах обучения социальным навыкам и альтернативных видах коммуникации в СМИ и на семинарах. К решению этой задачи мы и приступили с самого начала реализации проекта.</p>\r\n<p>В \"РАСсвете\" систему PECS применяют с первых дней открытия центра. Начальные этапы введения системы альтернативной коммуникации, методику и практику применения представила на семинаре специалист \"РАСсвета\", учитель первого ресурсного класса Иркутской области Елена Михеева. Она прочитала доклад в рамках конференции \"Мир с добрыми глазами\", организованной ИОООРДОВ \"Радуга\" и ИРО МОО \"Равные возможности\". Елена поделилась опытом применения карточек PECS у учеников ресурсного класса и получила много дополнительных вопросов после своего выступления - интерес к теме у аудитории оказался очень большим.  </p>\r\n<p>В октябре в Иркутской областной библиотеке им. И.И. Молчанова-Сибирского прошёл семинар \"Проблема инклюзивного образования для детей с РАС в городе Иркутск. Методы внедрения модели\" Ресурсный класс\" в образовательный процесс\".\r\nСпикер - куратор АНО \"АПЦ РАСсвет\" Елена Шапилова поделилась опытом работы ресурсного класса в школе пос. Молодежный, показала видео-нарезку уроков, рассказала о специальных условиях обучения: индивидуальных адаптированных программах и адаптации учебного материала, тьюторском сопровождении, программах работы с поведением, специальных технологиях и методах обучения.</p>\r\n<p>В феврале 2022 года стартовала бесплатная программа обучения \"Развитие инклюзивного образования для детей с РАС по модели\" Ресурсный класс\" для родителей и педагогов, работающих с детьми с аутизмом.</p>\r\n<p>На первом модуле программы слушатели узнали о базовых принципах прикладного анализа поведения, познакомились с системой альтернативной коммуникации PECS и со структурой образовательного процесса детей с аутизмом в инклюзивных условиях, узнали о методах проведения предварительной диагностики навыков детей и оценивания динамики обучающего процесса. Обучение проходило в дистанционном формате.</p>\r\n<p>Уже в марте в \"РАСсвете\" прошёл тренинг по основам системы альтернативной коммуникации PECS. Спикер - олигофренопедагог, поведенческий аналитик Елена Михеева (4 модуля сертифицированной программы для специалистов по ПАП) провела обучение для участников курса.</p>\r\n<p>На тренинге повторяли лекционный материал первого модуля, отрабатывали на практике все этапы освоения системы PECS, разбирали нюансы работы с альтернативной коммуникацией. </p>\r\n<p>Полученные на тренинге знания, 20 участников курса, успешно прошедших итоговое тестирование по 2 модулю, закрепляли на стажировке в \"РАСсвете\" в рамках 3 модуля программы, который стартовал 1 апреля. </p>\r\n<p>В рамках проекта 2 апреля в День распространения информации о проблеме аутизма в Иркутской областной государственной универсальной научной библиотеке им. И. И. Молчанова-Сибирского прошёл семинар на тему коррекции нежелательного поведения детей с РАС - \"Сотрудничество с Великим Нехочухой\".</p>\r\n<p>Какие есть виды нежелательного поведения, почему важно знать причину детских истерик, что помогает её найти и какие стратегии в коррекции поведения выбирает АВА-специалист рассказала Мария Максимова - поведенческий аналитик, 3 модуля сертифицированной программы для специалистов по ПАП.\r\nСеминар посетили более 80 человек: родители и родственники детей с РАС, специалисты дошкольных и школьных образовательных учреждений Иркутска и Иркутского района.</p>\r\n<p>Для участников семинара были подготовлены информационные листки о симптоматике аутизма, деятельности \"АПЦ РАСсвет\", прикладном анализе поведения и рабочих местах, в этой сфере.</p>\r\n<p>И, наконец, в апреле и мае в \"РАСсвете\" состоялась стажировка для 20 педагогов и родителей детей с аутизмом. Студенты курса посещали индивидуальные и групповые занятия, мастерские центра, наблюдали за работой АВА-инструкторов, психолога и волонтеров-тьюторов, включались в процесс обучения детей, разбирали теоретические вопросы из первых модулей программы. Делимся моментами стажировки и других обучающих мероприятий в нашей фото-галерее.</p>\r\n<p>Проект реализуется при поддержке Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n\r\n\r\n\r\n\r\n', '/images/Absolut/30/5.jpg', '2022-06-18', '/images/Absolut/logo.jpg;\r\n/images/Absolut/30/3.jpg;\r\n/images/Absolut/30/4.jpg;\r\n/images/Absolut/30/2.jpg;\r\n/images/Absolut/30/1.jpg;\r\n/images/Absolut/30/7.jpg;\r\n/images/Absolut/30/8.jpg;\r\n/images/Absolut/30/9.jpg;\r\n/images/Absolut/30/10.jpg;\r\n/images/Absolut/30/6.jpg', '', '', 'Новости', '/news.php', 0, '', '0'),
(611, '2,22', 'Навестили зверей', 'Поездки в \"Иркутский Зоосад\" для \"РАСсвета\" - добрая традиция. Администрация зоосада приглашает нас в гости каждый год, сотрудники отвечают на все вопросы детей, дают потрогать и погладить животных. Все это бесплатно, за что отдельное спасибо!', '<p>Сегодня мы собрали наших учеников, педагогов, друзей и поехали в Иркутский зоосад. Было здорово собраться всем вместе, наблюдать за повадками зверей, кормить их, бегать по дорожкам в тени деревьев, качаться на качелях и чувствовать лето.</p> \r\n<p>Такие поездки для \"РАСсвета\" - добрая традиция. Администрация зоосада приглашает нас в гости каждый год, сотрудники отвечают на все вопросы детей, дают потрогать и погладить животных. Все это бесплатно, за что отдельное спасибо!</p>\r\n<p>На этот раз к нам присоединились ребята из Иркутской областной общественной организации боевых искусств \"Федерация Айкидо\". Было приятно познакомиться, надеемся, что ещё будем встречаться с вами.</p>\r\n\r\n\r\n', '/images/dosug/2022/1/11.jpg', '2022-06-17', '/images/dosug/2022/1/1.jpg;\r\n/images/dosug/2022/1/2.jpg;\r\n/images/dosug/2022/1/3.jpg;\r\n/images/dosug/2022/1/4.jpg;\r\n/images/dosug/2022/1/5.jpg;\r\n/images/dosug/2022/1/6.jpg;\r\n/images/dosug/2022/1/7.jpg;\r\n/images/dosug/2022/1/8.jpg;\r\n/images/dosug/2022/1/9.jpg;\r\n/images/dosug/2022/1/10.jpg', '', '', '', '', 0, '', '0');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(612, '18', 'Два в одном: абстракция и медитация', 'В мастерских для детей и подростков с РАС \"Дом синего слона\" любят пробовать новые и желательно не самые трудные направления в творчестве. Идеально всем ребятам подошла техника \"флюид арт\", в переводе с английского - жидкое искусство.', '<p>В мастерских для детей и подростков с РАС \"Дом синего слона\" любят пробовать новые и желательно не самые трудные (помните, что у наших мастеровых есть особенности в поведении) направления в творчестве. Идеально всем ребятам подошла техника \"флюид арт\", в переводе с английского - жидкое искусство.</p>\r\n<p>Этот вид абстрактной живописи основан на рисовании акриловой краской, которой придают жидкую текучую консистенцию. Жидкий акрил разных цветов поочередно или одновременно выливают на холст, где цвета перемешиваются и формируют рисунок. \r\n</p>\r\n<p>В мастерской мы рисовали используя наборы с уже подобранным сочетанием красок и холстами. Процесс был увлекательным и успокаивающим, не зря флюид арт сравнивают с медитацией. </p>\r\n<p>Результаты работы ребят смотрите на фотографиях. Кстати,  эти картины уже есть в продаже на странице <a href=\"https://vk.com/dom.sinego.slona\" target=\"_blank\">ДОМ СИНЕГО СЛОНА</a></p>\r\n<p>Проект \"Ты не один в этом мире\" реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/DSS/2022/24/2.jpg', '2022-06-17', '/images/grants/pgrants_logo.jpg;\r\n/images/DSS/2022/24/1.jpg;\r\n/images/DSS/2022/24/2.jpg;\r\n/images/DSS/2022/24/3.jpg;\r\n/images/DSS/2022/24/4.jpg', '', '', 'Новости', '', 0, '', '7'),
(614, '19', 'О ресурсных классах в Иркутске', 'В «РАСсвет» обратились с вопросом: будет ли наш центр организовывать открытие новых ресурсных в Иркутске в ближайшее время? В следующем учебном году «РАСсвет» сосредоточится на работе уже открытых ресурсных классов в Иркутском районе: в школах поселка Молодежный и Луговой. ', '<p>В «РАСсвет» обратились с вопросом: будет ли наш центр организовывать открытие новых ресурсных в Иркутске в ближайшее время? К сожалению, нет, для этого у нас не хватает средств – кадров, материальной поддержки, времени. В следующем учебном году «РАСсвет» сосредоточится на работе уже открытых ресурсных классов в Иркутском районе: в школах поселка Молодежный и Луговой. Также специалисты центра будут оказывать консультационную и экспертную поддержку ресурсному классу в городе Чита. </p>\r\n<p>Во всех ресурсных классах Приангарья за прошедший учебный год есть успехи. Ученики адаптировались к школьным условиям, научились работать в группах внутри своего коллектива и в регулярных классах с нейротипичными сверстниками, выполнять задания учителя за партой и у доски, просить помощь, когда она требуется, посещать столовую, туалет, взаимодействовать на переменах с другими ребятами. Родители отмечают, что их дети стали более самостоятельными и в школу ходят с удовольствием.</p>\r\n<p>Екатерина Родыгина, председатель «АПЦ РАСсвет», рассказала о том, какие вопросы пришлось решать для открытия ресурсных классов над какими проблемами, связанными с ними еще предстоит поработать.\r\n</p>\r\n<p>- На то, чтобы создать первый ресурсный класс в поселке Молодежный у нас ушло 3 года. Открыть следующие 2 класса оказалось не намного легче. Во-первых, у Департамента образования Иркутска и у Министерства образования Иркутской области не было четкого понимания, что представляет собой модель «Ресурсный класс». Во-вторых, чиновники нам все время говорили, что общеобразовательные школы переполнены и нет мест для создания специальных условий для обучения детей с аутизмом. В-третьих, у нас не хватало профессиональных кадров: тьюторов, учителей, подготовленных для работы с детьми с РАС, специалистов прикладного анализа поведения (ПАП) (кураторов, супервизоров). И, конечно, остро стоял финансовый вопрос: оплата тех же специалистов ПАП, обеспечение классов и сенсорных зон мебелью, пособиями, развивающими материалами и прочими необходимыми предметами.\r\n</p>\r\n<p>- Учебный год окончен и можно с уверенностью сказать, что работа была проделана колоссальная. Но проблемы с кадрами и с финансированием так и остаются не решеными. В прошлом году мы запустили бесплатные обучающие курсы для педагогов и родителей детей с аутизмом. Дали азы ПАП, наметили ориентиры для создания ресурсного класса, поделились опытом и дали практические советы. Но это лишь первый шаг, чтобы педагоги могли оказывать квалифицированную помощь, им нужно постоянно повышать свои компетенции. На это уйдет не один год и немало денег. Также, как и на курирование и супервизии поведенческих аналитиков. Сейчас мы полностью зависим от фондовой поддержки, плюс внутренние ресурсы родителей учеников. Нужно, чтобы модель ресурсного класса полностью перешла на государственное обеспечение. Для этого надо продолжать диалог с представителями гос.системы, добиваться, чтобы в школах вводили ставки для куратора и супервизора, чтобы в ВУЗах появилось обучение ПАП. </p>\r\n<p>Благодарим организаторов и партнёров проекта \"Эстафета компетенции НКО\" : Фонд «Выход».  Аутизм в России, Ассоциация \"Аутизм Регионы\". Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фонд президентских грантов.</p>\r\n', '/images/RK/2022/23/3.jpg', '2022-06-21', '/images/grants/pgrants_logo.jpg;\r\n/images/RK/2022/23/2.jpg;\r\n/images/RK/2022/23/3.jpg;\r\n/images/RK/2022/23/4.jpg;\r\n/images/RK/2022/23/1.jpg', '', '', '', '', 0, '', '0'),
(616, '18', 'У \"РАСсвета\" появился свой мерч...', 'Звучит круто. Да так оно и есть. Чтобы привлечь внимание общественности к теме аутизма, развеять мифы связанные с РАС, и честно рассказывать о таких непростые вещах, мы решили развиваться в новом направлении.\r\n', '<p>Звучит круто. Да так оно и есть. Чтобы привлечь внимание общественности к теме аутизма, развеять мифы связанные с РАС, и честно рассказывать о таких непростые вещах, мы решили развиваться в новом направлении.\r\n</p>\r\n<p>Теперь мы готовим одежду и аксессуары с рисунками, цитатами наших учеников и с фразами, идеально подходящими им. </p>\r\n<p>\"Быть обычным - скучно\", \"Мне просто нечего сказать\", \"Я не знаню\", \"Кусаюсь!\", \"Тише будь\", \"Лужи - это упавшее небо\". И, конечно, наш слоган: \"Ты не один в этом мире\".\r\n</p>\r\n<p>Надевая футболку с необычной надписью, прикалывая к рюкзаку значок с чьей-то цитатой, мы показываем свою уникальность. И в то же время то, что у всех нас есть что-то общее: желание быть услышанным, понятым, принятым в свой круг.\r\n</p>\r\n<p>Проект \"Ты не один в этом мире\" реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/DSS/2022/26/0.jpg', '2022-06-24', '/images/grants/pgrants_logo.jpg;\r\n/images/DSS/2022/26/1.jpg;\r\n/images/DSS/2022/26/2.jpg;\r\n/images/DSS/2022/26/3.jpg;\r\n/images/DSS/2022/26/5.jpg;\r\n/images/DSS/2022/26/6.jpg;\r\n/images/DSS/2022/26/7.jpg;\r\n/images/DSS/2022/26/4.jpg', '', '', 'Новости', '', 0, '', '7'),
(619, '12', 'История успеха проекта \"Спорт без преград\"', 'Наш проект \"Спорт без преград\" завершился, оставив яркий след в жизни детей, принимавших участие в регулярных тренировках по адаптивной физкультуре. Занятия проводились как в спортивном зале центра, так и за его пределами, что позволило ребятам адаптироваться к различным условиям и улучшить физическую форму.', '<p>Наш проект \"Спорт без преград\" успешно завершился, оставив яркий след в жизни двадцати детей, принимавших активное участие в регулярных тренировках по адаптивной физкультуре. Занятия проводились как в спортивном зале центра, так и за его пределами, что позволило ребятам адаптироваться к различным условиям и улучшить физическую форму.</p>\r\n<p>Индивидуальные программы физической реабилитации были разработаны специально для каждого участника, учитывая особенности их развития и потребности. Коррекционные мероприятия помогли детям лучше ориентироваться в пространстве, правильно воспринимать команды инструктора и уверенно двигаться вперёд.</p>\r\n<p>На протяжении проекта наши юные спортсмены показали значительные успехи: улучшилась моторика и мышечная координация, укрепилось физическое здоровье и эмоциональная устойчивость. Особенно приятно отметить, что многие упражнения, казавшиеся сложными вначале, теперь легко выполняются ребятами.</p>\r\n<p>Отдельно стоит упомянуть Тасю и Тимофея, которые благодаря своим успехам на адаптивных тренировках начали активно заниматься плаванием. Подростки освоили разные стили плавания и с радостью участвуют в водных упражнениях, демонстрируя уверенность и готовность взаимодействовать с тренерами.</p>\r\n<p>Весной в рамках проекта состоялась увлекательная инклюзивная эстафета на скалодроме наших партнеров — федерации \"Лига Мечты\". Соревнования объединили двадцать юных спортсменов, среди которых были дети с расстройствами аутистического спектра и их здоровые сверстники. Поддержку участникам оказывали три тренера центра и три волонтера.</p>\r\n<p>Эстафета включала разнообразные испытания: прохождение полосы препятствий, восхождение на учебную скалу и перетягивание каната. Каждый участник проявил смелость и упорство, делая этот день незабываемым.\r\n\r\nФинальным аккордом стало награждение всех участников приятными призами — светящимися мыльными пузырями, символизирующими радость от достигнутых успехов.</p>\r\n<p>Проект \"Спорт без преград\" был реализован при поддержке Благотворительного фонда Владимира Потанина, который помогает воплощать мечты детей в реальность.</p>\r\n\r\n\r\n\r\n\r\n', '/images/AFK/2022/5/7.jpg', '2022-06-28', '/images/AFK/logo.jpg;\r\n/images/AFK/2022/5/1.jpg;\r\n/images/AFK/2022/5/2.jpg;\r\n/images/AFK/2022/5/3.jpg;\r\n/images/AFK/2022/5/4.jpg;\r\n/images/AFK/2022/5/5.jpg;\r\n/images/AFK/2022/5/6.jpg;\r\n/images/AFK/2022/5/8.jpg\r\n', '', '', '', '', 0, '', '0'),
(620, '18,13', 'В Иркутске работает творческая мастерская для детей с аутизмом', 'Автор: Яна Шутова <br>\r\nИсточник: \"Верблюд в огне\" <a class=\"a-blue\" href=\"https://verbludvogne.ru/articles/blue-elephant/\" target=\"_blank\">(verbludvogne.ru)</a><br>\r\nДва года назад в Иркутске открылась мастерская для детей с РАС «Дом синего слона». Юные мастера «Дома синего слона» создают прекрасные вещи, которые продаются по всей России.', 'Автор: Яна Шутова <br>\r\nИсточник: \"Верблюд в огне\" <a class=\"a-blue\" href=\"https://verbludvogne.ru/articles/blue-elephant/\" target=\"_blank\">(verbludvogne.ru)</a><br>\r\nДва года назад в Иркутске открылась мастерская для детей с РАС «Дом синего слона». После большой подготовительной работы юные мастера «Дома синего слона» создают прекрасные вещи, которые продаются по всей России.<br>\r\nЧитайте далее по <a class=\"a-blue\" href=\"https://verbludvogne.ru/articles/blue-elephant/\" target=\"_blank\">ССЫЛКЕ</a>.', '/images/DSS/2022/29.06.jpg', '2022-06-29', '', '', 'https://verbludvogne.ru/articles/blue-elephant/', '', '', 1, '', '0'),
(624, '16', 'Обучение в экспериментальной школе \"НКО. Трансформация\"', 'Специалисты \"РАСсвета\" на этой неделе прошли обучение в экспериментальной школе Фонда президентских грантов \"НКО. Трансформация\". За 3 дня интенсивной работы было много интересных и полезных встреч, знакомств, дискуссий и инсайтов. ', '<p>Специалисты \"РАСсвета\" на этой неделе прошли обучение в экспериментальной школе Фонда президентских грантов \"НКО. Трансформация\". За 3 дня интенсивной работы (в очном формате начинали учёбу в 10 утра и продолжали до 19-20 часов вечера) было много интересных и полезных встреч, знакомств, дискуссий и инсайтов. \r\n</p>\r\n<p>Спасибо экспертам школы и специалистам Фонда президентских грантов за то, что помогли по-новому взглянуть на цели и возможности НКО, показали, какие ресурсы можно найти для развития организации.</p>\r\n<p>Сейчас в ближайших планах провести внутри центра совещание с обсуждением основных моментов обучения, чтобы выстроить работу \"РАСсвета\" более эффективно.</p>\r\n\r\n\r\n\r\n\r\n', '/images/news/2022/5/4.jpg', '2022-07-07', '/images/news/2022/5/1.jpg;\r\n/images/news/2022/5/2.jpg;\r\n/images/news/2022/5/3.jpg;\r\n/images/news/2022/5/4.jpg;\r\n/images/news/2022/5/5.jpg;\r\n/images/news/2022/5/6.jpg\r\n', '', '', 'Новости', 'news.php', 0, '', '0'),
(625, '18', 'Занятия по гончарке', 'Как и все творческие люди, наши педагоги из мастерской \"Дом синего слона\" любят узнавать и пробовать новые направления работы. Ещё недавно Светлана и Александра проходили обучение в гончарной школе \"Бруклин\", а теперь успешно передают знания ребятам.', '<p>Как и все творческие люди, наши педагоги из мастерской \"Дом синего слона\" любят узнавать и пробовать новые направления работы. Ещё недавно Светлана и Александра проходили обучение в гончарной школе \"Бруклин\", а теперь успешно передают знания ребятам. Конечно, им ещё многое предстоит узнать о гончарном деле. Кстати, на прошлой неделе педагогам прислали сертификаты о прохождении курса в \"Бруклине\". На фото можно увидеть документы и итоговые работы наших мастеров.\r\n</p>\r\n<p>Печь для обжига изделий предоставила партнёр проекта \"Ты не один в этом мире\" - Лидия Лось - автор выставки \"Аутизм. Семейный альбом\". Очень символично, вы не находите? Название проекта и то, как он реализуется, показывает, что люди с РАС и их родственники могут найти поддержку в самых разных организациях, у самых разных людей.</p>\r\n<p>Глина - один из самых любимых материалов наших ребят из мастерской \"Дом синего слона\": пластичная, в меру вязкая, прохладная... Работа с ней - это не только творческий процесс, но и сенсорное удовольствие.</p>\r\n<p>Посмотрите, какие изделия готовят к выпуску в мастерской для детей и подростков с РАС \"Дом синего слона\". Каждая из этих подвесок выполнена вручную с помощью простых инструментов, плюс, немного творческой магии - когда природный материал превращается в искусство, универсальное украшение, которое подойдёт каждому, не зависимо от пола, возраста, национальности и т.п.</p>\r\n<p>Будущие кулоны, браслеты и подвески уже сейчас выглядят необыкновенно. Но их ещё ждёт доработка. После того, как глина высохнет, оттиск заполнят цветными ангобами. После обжига рисунок станет более выраженным, а сама поверхность изделия приобретёт глянец.</p>\r\n<p>А еще ребята начали делать посуду из глины, работая в технике лепка пластом, наносили роспись ангобом (специальное покрытие) с помощью трафаретов. \r\nСледующий этап - обжиг в печи. Чтобы глиняные тарелочки стали прочными, необходима температура 800-1000°C. С нетерпением ждём результатов нашего \r\n гончарного опыта!</p>\r\n<p>Проект \"Ты не один в этом мире\" реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/DSS/2022/28/1.jpg', '2022-07-07', '/images/grants/pgrants_logo.jpg;\r\n/images/DSS/2022/28/1.jpg;\r\n/images/DSS/2022/28/2.jpg;\r\n/images/DSS/2022/28/3.jpg;\r\n/images/DSS/2022/28/5.jpg;\r\n/images/DSS/2022/28/6.jpg;\r\n/images/DSS/2022/28/7.jpg;\r\n/images/DSS/2022/28/8.jpg;\r\n/images/DSS/2022/28/9.jpg;\r\n/images/DSS/2022/28/4.jpg', '', '', 'Новости', '', 0, '', '7'),
(626, '18', 'Работа с особенностями ', 'Сложно представить себе, что человек с нарушениями чувствительности сможет работать в мастерских. И всё же это возможно, доказано двухлетним опытом мастерских для детей и подростков с РАС \"Дом синего слона\". \r\n', '<p>Боится шума, не распознаёт запахи или остро реагирует на них, испытывает дискомфорт от прикосновений, моторно неловок, страдает от пикацизма (пробует и ест несъедобное: бумагу, мыло, пластилин, нитки и т.д.) - около 90% людей с РАС имеют эти и другие нетипичные сенсорные ощущения.</p>\r\n<p>Сложно представить себе, что человек с такими нарушениями чувствительности сможет работать в мастерских, где нужно иметь дело со швейной и вязальной машинами, красками, лаком, глиной, бетоном и прочими материалами.</p>\r\n<p>И всё же это возможно, доказано двухлетним опытом мастерских для детей и подростков с РАС \"Дом синего слона\". </p>\r\n<p>Чтобы наши ученики могли спокойно и эффективно работать им оказывают психолого-педагогическую поддержку на занятиях с дефектологом, АВА-инструктором и психологом, под курацией поведенческих аналитиков.\r\n</p>\r\n<p>Залог успеха в регулярности занятий и работ в мастерских. Каждую неделю ребята учатся самоконролю, развивают коммуникативные способности и навыки учебного поведения (слушать и спокойно выполнять задания, находиться за столом, просить о помощи). Педагоги помогают ребятам снизить тревожность к триггерам благодаря визуальной поддержке, шумопоглощающим наушниками и другим средствам.</p>\r\n<p>Занятия юных мастеров проходят в рамках проекта \"Ты не один в этом мире\".</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/DSS/2022/29/6.jpg', '2022-07-11', '/images/grants/pgrants_logo.jpg;\r\n/images/DSS/2022/29/1.jpg;\r\n/images/DSS/2022/29/2.jpg;\r\n/images/DSS/2022/29/3.jpg;\r\n/images/DSS/2022/29/5.jpg;\r\n/images/DSS/2022/29/4.jpg', '', '', 'Новости', '', 0, '', '7'),
(627, '10', 'Проект \"Есть контакт!\"', 'Статус проекта: завершен<br>\r\nСроки реализации: 01.04.2022 - 31.12.2022<br>\r\nИсточник софинансирования: Благотворительный фонд Сбербанка \"Вклад в будущее\"\r\n', '<p><b>Статус проекта:</b> завершен<br>\r\n<b>Сроки реализации:</b> 01.04.2022 - 31.12.2022<br>\r\n<b>Источник софинансирования:</b> Благотворительный фонд Сбербанка \"Вклад в будущее\"</p>\r\n<p>Проект направлен на реабилитацию и социализацию 20 семей, воспитывающих детей и подростков 5-12 лет с расстройством аутистического спектра, путем регулярных индивидуальных и групповых занятий на базе АНО \"АПЦ РАСсвет\" и проведения недельной смены в инклюзивном коммуникативном лагере.</p>\r\n<p>Программа проекта в подготовительном периоде включает серию мероприятий по социальной адаптации детей и подростков с РАС, а именно занятия по развитию физической активности, навыков коммуникации, взаимодействия, игры, творческих и бытовых навыков, реализуемых через принципы Прикладного анализа поведения, индивидуализирующих подход к обучению и коррекции поведения каждого ребенка. По результатам предварительного анкетирования 20 детей по критериям ABBLS-R будут составлены программы обучения, по подготовке детей к усвоению социальных навыков, генерализованных в последствии в натуральной среде в период недельной загородной летней смены посредством проведения индивидуальных и групповых мероприятий активной социальной направленности с привлечением родителей, сиблингов (братьев, сестер) и волонтеров.</p>\r\n<p>Проект также предусматривает участие нейротипичных детей в возрасте 5-15 лет. В ходе смены в лагере пройдут индивидуальные и групповые занятия психологической и разгрузочной подготовки детей к новым условиям. Физкультурно-оздоровительные мероприятия, включающие пеший поход и обучение активным играм (футбол, пионербол и др.) приобщат семьи к спортивному ориентированию и совместному досугу. Групповые коммуникативные игры с участием нейротипичных и нейроотличных детей сформируют позитивное отношение к людям с особыми потребностями, обучат детей владеть своими чувствами, сопереживать партнерам по общению, конструктивно взаимодействовать и сотрудничать со сверстниками и взрослыми.</p>\r\n<p>Работа коммуникативных групп, творческих и оздоровительных занятий после окончания летней смены закрепят внедренную систему обучения взаимодействию. После лагеря продолжатся индивидуальные творческие занятия с использованием поведенческих вмешательств, которые научат детей работе с различными материалами и инструментами. Групповой мастер-класс для семей детей и подростков с РАС по созданию панно из природных материалов, собранных на берегу озера Байкал во время занятий в коммуникативном лагере, разовьет творческие способности и креативное мышление у ребят с РАС. Также после лагеря продолжатся занятия по закреплению сформированных навыков коммуникации, самообслуживания, физической активности с использованием поведенческих вмешательств. В конце реализации проекта заключительное тестирование оценит динамику развития освоенных в ходе проекта навыков у детей с РАС. Полученный опыт будет распространен командой проекта через проведение семинара для специалистов коррекционных и смежных видов деятельности, а также родителей детей с РАС. Таким образом, в проекте примут участие не менее 130 человек из числа детей с РАС, нейротипичных детей, родителей (опекунов), волонтеров, педагогов проекта и участников семинара.</p>\r\n<p><a href=\"/news.php?project=6\" class=\"button\">Публикации о проекте</a></p>', '/images/grants/kontakt.jpg', '2022-04-23', '', '', '', 'Гранты', '/pages/grants.php', 0, '', '0'),
(628, '4', 'Формирование учебного поведения', 'Важным навыком для наших учеников, особенно будущих школьников, является навык сидеть спокойно за столом. У многих наших подопечных с этим возникают проблемы: кто-то стремиться убежать, другие «растекаются» по стулу и столу.\r\n', '<p>Важным навыком для наших учеников, особенно будущих школьников, является навык сидеть спокойно за столом. У многих наших подопечных с этим возникают проблемы: кто-то стремиться убежать, другие «растекаются» по стулу и столу, третьи занимаются самостимулированием (притопывают, машут пальцами перед глазами и т.п.) и вокализируют. </p>\r\n<p>На занятиях мы используем визуальное расписание и жетоны. С их помощью ученик понимает, когда и чем закончится урок, тренирует навык ожидания и снижает уровень своей тревоги.</p>\r\n<p>АВА-инструктор вовремя оказывает подсказки и сосредотачивает внимание ребенка на выполнении задания. Подсказки постепенно удаляются, чтобы ребенок мог самостоятельно контролировать свое поведение.</p>\r\n<p>Для отработки навыка спокойного нахождения ученика за столом берутся знакомые для него задания. Сначала мы учим сидеть за столом и выполнять одно задание, затем два, три и т.д., постепенно увеличиваем время выполнения задания и их количество. </p>\r\n<p>На первых занятиях мы тестируем, сколько ребенок может сидеть за столом. Определяется граница, после которой он начинает демонстрировать нежелательное поведение. С этого момента мы постепенно увеличиваем время пребывания за столом. </p>\r\n<p>В начале посещения групповых занятий наш 12-летний ученик Андрей мог сидеть за столом и выполнять знакомые для него задания на протяжении всего 2 минут. После завершения задания ребенок сразу же убегал. Мы работали над тем, чтобы научить мальчика дольше сидеть за столом и уходить по инструкции учителя. Для этого использовали мотивационные задания и таймер. По звонку таймера Андрей мог встать и выйти из-за стола или же продолжить заниматься мотивационной для него деятельностью - рисовать красками, играть с кинетическим песком. Как правило, он выбирал любимое занятие. Затем мы чередовали мотивационные задания с немотивационными, постепенно увеличивали время пребывания мальчика за столом.\r\n</p>\r\n<p>Ребенка можно учить сидеть за столом с 3 лет, но нужно понимать, что в этом возрасте он должен сидеть за столом не продолжительное время - не более 2 минут. Основные занятия должны проходить в игровой форме вне стола.</p>\r\n<p>Проект реализуется при поддержке Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n\r\n\r\n\r\n\r\n', '/images/Absolut/33/1.jpg', '2022-07-13', '/images/Absolut/logo.jpg;\r\n/images/Absolut/33/3.jpg;\r\n/images/Absolut/33/2.jpg;\r\n/images/Absolut/33/1.jpg', '', '', 'Новости', '/news.php', 0, '', '9'),
(629, '2', 'Будни инклюзивного лагеря\r\n', 'Идея создания инклюзивного летнего лагеря в \"РАСсвете\" витала уже давно. Это наш первый опыт в организации летнего отдыха такого масштаба, поэтому сейчас пробуем и выбираем, что нужно включать в программу, а без чего можно обойтись.', '<p>Идея создания инклюзивного летнего лагеря в \"РАСсвете\" витала уже давно. С местом определились быстро - до Байкала можно доехать на машине за несколько часов. После опроса родителей наших подопечных выяснили, что близ Сармы есть мелководные бухты, пригодные для купания детей, и подходящая инфраструктура в самой деревне. Там и решили устроить базу для лагеря.</p>\r\n<p>Компания собралась разновозрастная и весёлая: папы, мамы, дети с аутизмом, их братья и сестры, и, конечно, педагоги \"РАСсвета\".</p>\r\n<p>Это наш первый опыт в организации летнего отдыха такого масштаба, поэтому сейчас пробуем и выбираем, что нужно включать в программу, а без чего можно обойтись.</p>\r\n<p>Например, спортивные игры - перетягивание каната, футбол, пионербол, небольшие эстафеты - у ребят зашли на \"ура\". Даже обычные активности на основе догонялок на байкальских просторах вызывают у детей восторг. Неудивительно, что АФК-тренер Валерий Клоков стал самой популярной личностью этой смены (правда, до официального награждения дело ещё не дошло). \r\n</p>\r\n<p>Любители ходить оценили вылазки за пределы деревни: пешие походы к Сарминскому ущелью и прогулки вдоль берега. Здесь отличились мама Наталья и её сын Юра - бывалые туристы, они уже во второй день после заезда отправились покорять местные тропы и вершины. </p>\r\n<p>Совместные игры, походы и обеды помогли родителям и детям приспособиться к новым условиям и найти общие интересы. </p>\r\n<p>В нашем инклюзивном АВА-лагере время летит незаметно. Дни наполнены активным отдыхом и занятиями. Педагоги обучают ребят, используя игры и необычные задания. Для занятий по творчеству педагоги приготовили визуальные подсказки, на них пошагово показано, как сделать ту или иную поделку. А вот от стандартной жетонной системы отказались – мотивации у детей и так хватает.</p>\r\n<p>Активный отдых, ежедневные групповые и индивидуальные занятия, игры, купание в Байкале - это лето нашим подопечным и их родителям и родным запомнится надолго. Мы рады, что первый опыт организации лагеря прошёл успешно.</p>\r\n<p>Проект \"Есть контакт\" реализуется при поддержке Благотворительного фонда «Вклад в будущее».</p>\r\n\r\n\r\n\r\n\r\n', '/images/Kontakt/3/1.jpg', '2022-07-25', '/images/grants/VK_VB.jpg;\r\n/images/Kontakt/3/1.jpg;\r\n/images/Kontakt/3/2.jpg;\r\n/images/Kontakt/3/4.jpg;\r\n/images/Kontakt/3/5.jpg;\r\n/images/Kontakt/3/6.jpg;\r\n/images/Kontakt/3/7.jpg;\r\n/images/Kontakt/3/8.jpg;\r\n/images/Kontakt/3/9.jpg;\r\n/images/Kontakt/3/3.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', '6'),
(631, '20', 'Как рассказать об аутизме?', 'Как рассказывать детям в детском саду, школе, на игровых площадках и других общественных местах об аутизме ребенка? С этим вопросом часто сталкиваются родные детей с РАС.\r\n', '<p>Как рассказывать детям в детском саду, школе, на игровых площадках и других общественных местах об аутизме ребенка? С этим вопросом часто сталкиваются родные детей с РАС.</p>\r\n<p>В начальных школах, где есть ресурсные классы для детей с РАС, педагоги «РАСсвета» регулярно проводят «уроки добра» - классные часы, на которых рассказывают ученикам обычных регулярных классов об аутизме. Мы собрали советы наших педагогов и публикуем их здесь.\r\n</p>\r\n<p>✏️ Начните рассказ с того, что все люди разные, у всех есть индивидуальные запросы, особенности, предпочтения в еде, одежде, музыке и т.д. Говорите простыми словами, подбирайте понятные примеры: «Про кого ты любишь смотреть мультфильмы? А есть дети, которым нравятся мультфильмы про животных, роботов, фей…Кто-то любит мороженное с шоколадом и орехами, кто-то фисташковое, кто-то ванильное… У каждого есть свои предпочтения, и это нормально».</p>\r\n<p>✏️ Объясните, что важно находить общие интересы, уметь общаться с разными людьми.\r\n</p>\r\n<p>✏️ Постройте диалог, пусть дети задают вопросы о необычном поведении ребенка с аутизмом, а вы на них ответите. Расскажите, что у людей с РАС мозг работает иначе, чем у большинства других. Аутичный ребенок может привлекать внимание криками, взмахами руками, тем, что носит наушники, не слушая музыку. Такое необычное поведение вызвано особенной чувствительностью.\r\n</p>\r\n<p>✏️ Чтобы подготовиться к разговору, почитайте брошюры <a href=\"/pages/friendly_school.php\" target=\"_blank\">\"Дружелюбная школа\"</a> с ответами на вопросы учеников. Материалы разработали Ассоциация \"Аутизм Регионы\"  и Фонд «Выход».  Аутизм в России.\r\n</p>\r\n<p>✏️ Покажите на телефоне или планшете мультфильм или социальный ролик на тему аутизма. Подборка <a href=\"https://clck.ru/eSavd\" target=\"_blank\">ЗДЕСЬ</a>.</p>\r\n<p>✏️ Организуйте совместную игру, покажите, как дети могут помочь освоиться в компании вашему ребенку.</p>\r\n<p>Проект реализуется при поддержке Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n\r\n\r\n\r\n\r\n', '/images/Absolut/34/1.jpg', '2022-07-23', '/images/Absolut/logo.jpg;\r\n/images/Absolut/34/2.jpg;\r\n/images/Absolut/34/1.jpg', '', '', 'Новости', '/news.php', 0, '', '9'),
(632, '9', 'Диалог с родителями ', 'Непрошенные советы, неуместные комментарии, назойливое любопытство или напротив, нарочитое игнорирование... \r\nСвоим опытом выстраивания диалога с нейротипичными детьми и их родителями поделилась Елена Михеева, мама ребенка с РАС и соучредитель «РАСсвета». \r\n', '<p>«Он ударился головой в младенчестве?», «Его нужно лучше воспитывать», «Я слышал про аутизм, это модный диагноз», «Заставьте его замолчать», «Оставили бы ее дома, она тут всех распугает» — это лишь немногие фразы, которые слышат родители детей с аутизмом в общественных местах и в домашнем кругу. Непрошенные советы, неуместные комментарии, назойливое любопытство или напротив, нарочитое игнорирование – реакция может быть разной. Научиться спокойно отвечать на подобные высказывания трудно. Здесь помогает психологическая поддержка, время и регулярная практика. </p>\r\n<p>Своим опытом выстраивания диалога с нейротипичными детьми и их родителями поделилась Елена Михеева, мама ребенка с РАС и соучредитель «РАСсвета». </p>\r\n<p>- Моему сыну Ване 10 лет, он не избегает общения с другими людьми, но не может говорить – пользуется системой альтернативной коммуникации PECS. У него есть особенности в поведении, их трудно не заметить. Когда мы приходим на спортивную площадку, где дети играют в футбол, Ване тоже хочется погонять мяч со всеми. Поскольку он не может сказать об этом и не понимает всех правил игры, я ему помогаю: знакомлю ребят с сыном, коротко рассказываю о его диагнозе и особенностях, прошу включить его в игру на определенных условиях. Например, Ваня может поиграть с детьми во время их перерыва между матчами. Такой расклад устраивает всех. Нельзя настаивать, на том, чтобы нейротипичные дети играли с ребенком с РАС, но надо создать такую ситуацию, чтобы им было весело вместе. Дети со временем привыкают к ребенку с особенностями развития. </p>\r\n<p>- Если вы пришли на игровую площадку и у вашего ребенка началось проблемное поведение, нужно объяснить находящимся рядом родителям и детям, что у ребенка аутизм, и его поведение связано с конкретной причиной. Например, я говорю: «Ваня машет руками, когда волнуется, сейчас он успокоится и перестанет, мы с ним продолжим игру». </p>\r\n<p>- Реакция родителей бывает разная, некоторые быстро собирают своих детей и уводят их с площадки. На моей практике было пара таких ситуаций. Но в основном понимающе относятся. Важно в первую очередь выстроить диалог со знакомыми родителями, с которыми вы постоянно видитесь на площадках, в детском саду или школе. Сделать это проще, чем с совершенно незнакомыми людьми. \r\n </p>\r\n<p>- Когда родители нейротипичных детей видят, что ребенок с РАС находится под постоянным присмотром и контролем своих родителей, их уровень тревожности за своих детей снижается. Они понимают, что ничего страшного не произойдёт. </p>\r\n<p>- Родителям детей с особенностями развития нужно учиться рассказывать об аутизме простым языком, постепенно подключать своего ребенка к жизни в обществе: в школе, детском саду, на игровой площадке, в поликлинике, кафе и других местах, где дети с РАС имеют возможность взаимодействовать с другими людьми, которые ничего не знают об этом диагнозе. </p>\r\n<p>Проект реализуется при поддержке Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n\r\n\r\n\r\n\r\n', '/images/Absolut/4.jpg', '2022-07-27', '/images/Absolut/logo.jpg', '', '', 'Новости', '/news.php', 0, '', '0'),
(635, '12', 'Состязания в \"РАСсвете\" ', 'В «Рассвете» этим летом благодаря проекту \"Шаг в инклюзию\" появился свой аэрохоккей. Динамичная игра с простыми правилами сразу смогла увлечь наших учеников, как дошкольников, так и подростков.  ', '<p>В «Рассвете» этим летом благодаря проекту \"Шаг в инклюзию\" появился свой аэрохоккей. Динамичная игра с простыми правилами сразу смогла увлечь наших учеников, как дошкольников, так и подростков. Для многих она уже стала поощрением за выполнение заданий - так сильно желают ребята в неё поиграть. </p>\r\n<p>Как отметил тренер по адаптивной физкультуре центра Валерий Клоков, аэрохоккей не только развивает ловкость и скорость реакции у детей, но и коммуникацию, умение ждать очереди и спокойно относится к пройгрышу. \r\n</p>\r\n<p>Турниры по аэрохоккею даже вошли в серию инклюзивных эстафет, которые мы проводили в центре на протяжении 2 недель. В спортивных состязаниях участвовали наши подопечные и их нейротипичные сверстники: лазали по скалодрому, метали мяч в кольцо, прыгали в длину, бегали и преодолевали небольшие препятствия. Ребятам помогали тьюторы - направляли, поддерживали и активно \"болели\" за всех спортсменов.</p>\r\n<p>Эстафеты прошли в рамках мероприятия для семей, воспитывающих детей с аутизмом, \"Здоровое общество - инклюзивное общество\". Впереди ещё целый месяц лета и несколько ярких инклюзивных событий. </p>\r\n<p>Проект \"Шаг в инклюзию\" проходит при поддержке Департамента здравоохранения и социальной помощи населения комитета по социальной политики и культуры администрации г. Иркутска.</p>\r\n\r\n\r\n\r\n\r\n', '/images/AFK/2022/10/1.jpg', '2022-07-28', '/images/AFK/2022/10/1.jpg;\r\n/images/AFK/2022/10/2.jpg;\r\n/images/AFK/2022/10/3.jpg;\r\n/images/AFK/2022/10/4.jpg;\r\n/images/AFK/2022/10/5.jpg;\r\n/images/AFK/2022/10/6.jpg;\r\n/images/AFK/2022/10/7.jpg\r\n', '', '', '', '', 0, '', '8'),
(636, '18', 'Поймать тот самый момент', '\"Меня зовут Аня и с февраля этого года я работаю в мастерских \"Дом синего слона\", но на фото в этом аккаунте вы меня не увидите, потому что я фотограф и всегда нахожусь \"за кулисами\".', '<p>\"Меня зовут Аня и с февраля этого года я работаю в мастерских \"Дом синего слона\", но на фото в этом аккаунте вы меня не увидите, потому что я фотограф и всегда нахожусь \"за кулисами\".</p>\r\n<p>Чтобы поймать тот самый момент, мне приходится много наблюдать за процессом и первое, что я вижу, это то, как дети идут на занятия: кто-то бежит, кто-то идёт вприпрыжку, а кто-то нехотя крадётся. Как бы то ни было, дети знают куда и зачем идут.</p>\r\n<p>Спустя примерно 5 минут, как все зашли и началось занятие, захожу я. В небольшом помещении за общим столом собираются ребята и уже что-то мастерят. Обычно группа состоит из 3-5 человек. У каждого ребёнка есть тьютор, который оказывает подсказки и своевременно реагирует, если что-то пошло не по плану. Быть тьютором не просто, нужно знать особенности каждого ребёнка и уметь с ними работать. На занятии всегда присутствует мастер, он регулирует весь процесс и дает советы по технологии изготовления. Когда детей 5, а тьюторов всего 3, то мастер-это еще и тьютор. Так же, детям помогают и родители.</p>\r\n<p>За несколько месяцев своей работы мне удалось увидеть много разных операций с глиной, мылом, бетоном и другими материалами, но в каждом из этих сложных процессов специалисты выстраивают работу таким образом, чтобы дети максимально были вовлечены в процесс, даже когда операция совсем \"не детская\". Например, раскрой ткани, шитье на швейной машине или ручной иглой. Пока что не все творческие задачи детям по плечу, но они всегда участвуют в процессе создания изделий и по мере взросления станут всё больше и больше проявлять самостоятельность.</p>\r\n<p>Очень люблю мастерские, в них всегда царит атмосфера волшебства и чувствуется душа, которую вкладывают мастера в процесс создания изделий.\"\r\n</p>\r\n<p>Проект Ассоциация \"Аутизм Регионы\" \"Создание сети творческих досуговых центров для молодых людей с РАС \"Культурный код - возможность для каждого\"\" реализуется при поддержке Фонд «Выход». Аутизм в России с использованием гранта Президента Российской Федерации на реализацию проектов в области культуры, искусства и творческих индустрий Президентский фонд культурных инициатив.</p>', '/images/DSS/2022/30/2.jpg', '2022-07-28', '/images/grants/FKI.png;\r\n/images/DSS/2022/30/1.jpg;\r\n/images/DSS/2022/30/2.jpg;\r\n/images/DSS/2022/30/3.jpg', '', '', 'Новости', '', 0, '', '19'),
(639, '18', 'Два лайфхака от поведенческого специалиста', 'Легко структурированное занятие может стать целым поощрением для ребёнка, если в дело вступает лепка из глины. Глиняное ремесло позволяет насладиться природным материалом и насытить сенсорные анализаторы ребёнка.', '<p>Легко структурированное занятие может стать целым поощрением для ребёнка, если в дело вступает лепка из глины. Глиняное ремесло позволяет насладиться природным материалом и насытить сенсорные анализаторы ребёнка, а в заключении получить стильный предмет интерьера.</p>\r\n<p>Бывает так, что ребёнок отказывается от контакта с липкой массой, тогда в дело вступает наш старый добрый метод десенсибилизации (метод поведенческой терапии, основанный на систематическом постепенном уменьшении чувствительности человека к предметам, событиям или людям, вызывающим тревожность и фобии). Мы помогаем ребёнку безболезненно познавать этот мир, показывая, что лепка — это безопасно и совсем не страшно.</p>\r\n<p>Когда мы добиваемся того, что ребёнок спокойно манипулирует массой для лепки, подключаем «пэринг» (в переводе с английского – спаривание, присоединение). Метод, который сочетает в паре любимое времяпровождение (например, прослушивание музыки) и целевой стимул (в нашем случае-лепку). У ребёнка выстраиваются новые нейронные связи: любимая музыка + глина и, вуаля, в «Мастерской синего слона» хочется быть снова и снова.</p>\r\n<p>Примечание: В первую очередь мы себя пытаемся «спарить» с приятным занятием. Это не просто так специалисты прыгают вместе с ребенком на батуте и качают рядышком гамак, так мы показываем ребенку, что с нами не страшно, а весело. Это и есть «пэринг». Вы можете заметить на первых занятиях, что тьюторы много играют с ребенком и совсем чуть-чуть занимаются, постепенно наращивая требования. Ребенку важно понять, что здесь, в наших мастерских, можно не только творчески развиваться, но и весело проводить время.\r\n</p>\r\n<p>Благодаря использованию поведенческих подходов нам удается установить контакт с ребенком и изготовить такие прекрасные изделия. Только посмотрите!</p>\r\n<p>Проект Ассоциации \"Аутизм Регионы\" \"Создание сети творческих досуговых центров для молодых людей с РАС \"Культурный код - возможность для каждого\"\" реализуется при поддержке Фонда «Выход» с использованием гранта Президента Российской Федерации на реализацию проектов в области культуры, искусства и творческих индустрий Президентский фонд культурных инициатив\r\n</p>\r\n<p></p>', '/images/DSS/2022/31/2.png', '2022-08-10', '/images/grants/FKI.png;\r\n/images/DSS/2022/31/1.png;\r\n/images/DSS/2022/31/2.png', '', '', 'Новости', '', 0, '', '19'),
(640, '18', 'Когда ребёнок плачет «молча» - это самое страшное', 'Поговорили с Екатериной Никулиной учредителем «Рассвета», тьютором ресурсного класса в поселке Молодежном и мамой Таси – девочки подростка с аутизмом о трудностях подросткового возраста, мастерских и планах на будущее.\r\n', '<p>Поговорили с Екатериной Никулиной учредителем «Рассвета», тьютором ресурсного класса в поселке Молодежном и мамой Таси – девочки подростка с аутизмом о трудностях подросткового возраста, мастерских и планах на будущее.</p>\r\n<p><b>Какие изменения произошли у Таси с наступлением подросткового возраста?</b></p>\r\n<p>Из поведенческих проблем: она стала очень обидчивая. Примерно с 10 лет она стала обижаться на любой пустяк. Что бы ей не сказали, она обижалась, уходила в другую комнату, ложилась на кровать, накрывалась одеялом и «молча» плакала. Когда ребёнок плачет «молча» - это самое страшное. Ты сделаешь всё, лишь бы она больше так не делала. Это был долгий период – примерно три месяца.</p>\r\n<p>Так же она стала более твёрдо отстаивать своё мнение. Когда она была маленькой, ещё как-то можно было ее переубедить, надавить, и она делала то, что, возможно, делать не хотела, но сейчас она может очень четко и ясно сказать «нет, не хочу, не буду». Чтобы добиться от Таси сделать то, что она не хочет, приходится ее уговаривать и долго объяснять, что да как.</p>\r\n<p>В физиологическом плане - она очень выросла, начали проявляться первые признаки полового созревания. Пожалуй, это самые «подростковые» изменения Таси.</p>\r\n<p><b>Что сложнее всего?</b></p>\r\n<p>Сложнее всего дались обиды. Мы, конечно, как и все родители ждали подросткового возраста, но такого мы не ожидали. Сейчас у неё период, когда на все она говорит «нет». Даже когда предлагаешь то, что ей нравится, она говорит «не хочу, не буду». То есть она изначально уходит в отрицание, но даже в такие моменты мы стараемся с ней договориться. Тася человек довольно гибкий, несмотря на то что у неё аутизм.</p>\r\n<p><b>У Таси появились какие-нибудь новые увлечения с наступлением подросткового возраста?</b></p>\r\n<p>Телефон, как и у всех подростков. На каникулах я учила Тасю играть в игры на гаджетах. Хоть сама я в них играть не умею, но мне удалось научить ее. Она теперь играет. Мне нравится больше наблюдать. Мы вообще с Тасей созерцатели.\r\nЕще ей нравятся книги по мотивам ее любимых мультиков.</p>\r\n<p><b>Как тебе удается «уговаривать» Тасю?</b></p>\r\n<p>Изучаем различные социальные истории, используем визуальную поддержку. Тася не очень хорошо понимает речь, откровенно говоря, плохо понимает. Какие-либо новые слова для неё – космос, но тем не менее, ей очень помогает визуальное расписание. Например, помощь по дому происходит без проблем, благодаря визуальному расписанию. Тася умеет делать все: начиная от «заправить постель» до «помыть что угодно». В хозяйстве мне остается только готовка, потому что к плите мы ее без присмотра не пускаем. Во-первых, боимся, что она обожжется, во-вторых, она может забыть выключить печь.</p>\r\n<p>Если мы куда-то идём, а она не хочет выходить из дома, мы используем планшетку «сначала-потом» - «сначала пойдём погуляем, а потом мы придём домой и ты будешь делать то, что хочешь».</p>\r\n<p><b>Есть ли у Таси интерес к противоположному полу?</b></p>\r\n<p>Интерес есть. Эта тема вызывает у меня страх. Я боюсь, что Тася влюбится, поэтому мы стараемся выбирать преподавателей женщин. Это не из-за недоверия к мужчинам, просто если она влюбится, что нам потом с этой любовью делать? Как известно, наши дети склонны к депрессии. Любовь может повлечь множество проблем. Тем не менее, Тася все равно проявляет интерес к противоположному полу, заигрывает с парнями и улыбается им. Я стараюсь отслеживать ее сердечные поползновения, стараюсь «подложить соломку», но, конечно, я понимаю, что везде «соломку» я не подложу. \r\nЕсли у Таси появится друг, я не против. Другой вопрос, во что эта дружба может вылиться? Короче говоря, я за этим делом \"бдю\". </p>\r\n<p><b>Как вы проводите время вместе?</b></p>\r\n<p>Ох, сейчас с Тасей вообще хорошо проводить время. С ней можно сходить куда угодно. Это, конечно, далось нам не просто так. Мы много тренировались, социализировались. Например, каждую пятницу у нас кафе. Она мне, конечно, может сказать «Пятница – кафе» не только в пятницу, но и в субботу, и в понедельник. Мы посещаем разные заведения общепита, она всегда сама выбирает то, чем хочет полакомиться. Тася любит вкусняшки: тортики, пасту, пиццу. Так же мы ходим в батутные центры. Очень много гуляем, особенно летом, пока каникулы. Прогулки длятся 3-4 часа. Последнее время дома мы проводим семейные вечера, играем в настольные игры. Они не всегда соответствуют возрасту Таси (13 лет), но главное – мы играем в них вместе: «Домино», «Лото», «Дубль», «Слеп-Джек», «Мемори». В подростковом возрасте дети дистанцируются от взрослых, не говоря уже о детях с РАС, которые обычно не предпочитают проводить время в компании. Кстати, в детстве Тася была более ласковая, чем сейчас. Раньше она была очень тактильная, всегда лезла обниматься и все такое. Сейчас я такого не наблюдаю, обычно обнимаю или целую ее первая.</p>\r\n<p><b>С какого возраста вы начали заниматься творчеством? Что это было? Легко ли было?</b></p>\r\n<p>У Таси очень хорошо развито визуальное восприятие. Когда Тасе было полтора года и у нее еще не было официального диагноза, хотя я уже подозревала, что есть нарушения развития, мы начали пробовать вырезать аппликации. Она самостоятельно собирала модели по образцу, клеила заплатки и наклейки. Это давалось ей не сложно, потому что моторика была не плохая, а визуальное восприятие - ее ведущая часть. Чем старше становилась Тася, тем сложнее становилось заниматься творчеством, в виду поведенческих особенностей. Надо сказать, что нравится ей не все: шить она не любит, хотя если надо, она это сделает. Сейчас аппликации мы уже не делаем, это не по ее возрасту, но вот алмазные мозаики, различные творческие наборы идут в ход. Всегда ищу для неё что-то новенькое, смотрю, каким творчеством занимаются другие подростки, беру на заметку. Мы не можем просто взять и отправить ее в какой-нибудь Дом пионеров на рисование, хотя мы и пробовали, ничего хорошего из этого не вышло. Сейчас мы активно используем YouTube в своём творчестве, рисуем по поэтапным урокам. У неё хорошо получается. Все рисунки мы всегда раздариваем на праздники. Так же мы активно принимаем участие на мастер-классах «Синего слона».</p>\r\n<p><b>Что вам дают мастерские?</b></p>\r\n<p>Мастерские-это отдушина. Во-первых, наш ребёнок занят. Она не просто слоняется, она занята делом. Ей нравится приходить в центр именно на мастерские. Ей нравится, что здесь тусят другие ребятишки, что взрослые здесь ее спокойно воспринимают. Она же все равно понимает, кто как на неё смотрит. Например, когда едем в автобусе, люди начинают рассматривать Тасю. Бывает, она в общественных местах где-то застимит, где-то язык покажет, люди на это обращают внимание и откровенно начинают «глазеть». Тася в ответ может попросить человека отвернуться. Недавно мы ехали и она сказала «отвернись» молодому человеку, который активно ее разглядывал. Он, видимо, ее не понял. Она повторила: «Отвернись!». Он ее дальше разглядывает. Тут мне хотелось уже самой стукнуть его тяжелым предметом, но я не дотягивалась. Тася уже развернулась и глядя ему прямо в глаза: «Отвернись, пожалуйста!». До парня наконец-то дошло и он отвернулся.</p>\r\n<p>Мне нравится, что в «Доме синего слона» подбирают материалы, придумывают творческие задания, которые соответствуют возможностям ребят. Здесь разнообразие деятельности: сегодня-шерстю занимаются, завтра-вязание на машинке, послезавтра-лепка, не заскучаешь! И Тасе здесь очень нравится. Ей очень важен результат ее деятельности: когда она что-то делает, в конце обязательно предмет рассмотрит, потрогает, подарит кому-нибудь. Если мы делаем какие-либо поделки здесь, и я вижу, что ей зашло, стараюсь найти похожий материал, чтобы она могла поработать с ним дома. Для меня важно, чтоб ей нравилось. </p>\r\n<p>Творчество - это развитие, это самозанятость. Самозанятость в подростковом возрасте - это самое важное. Я рада, что она проводит время не в телефоне, а занимается делом.</p>\r\n<p><b>Видишь ли ты будущее для своего ребенка, связанное с мастерскими?</b></p>\r\n<p>Я понимаю, что скоро закончится школа и очень сократится время, которое она будет занята вне дома. Нам нужно будет продолжать ходить на занятия и мастерские - это одно из этих мест. Я понимаю, что ни в каких техникумах и колледжах нас не ждут, поэтому будем обучаться здесь и продолжать развивать наш творческий потенциал. Тем более, когда мы видим, что на мастерских она изготавливает не просто какую-то ерунду, а красивые и, главное, функциональные вещи. Это меня радует.</p>\r\n<p><b>Дай пару советов родителям, чьи дети проходят подростковый возраст.</b></p>\r\n<p>Во-первых, не наседайте на детей. Помните, что даже если у ребят есть какие-либо ментальные или интеллектуальные особенности - они такие же люди, как и мы. Да, они не такие, как все, но возрастные особенности никто не отменял. Наши дети так же растут, меняются, проходят возрастные трудности, сталкиваются со сложностями восприятия этого мира и, конечно же, надо учитывать все это и помнить, что чем взрослее они становятся, тем больше самостоятельности решений им требуется. Ну и во-вторых, самозанятость - это наше все!</p>\r\n\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фонд президентских грантов.</p>\r\n<p><b></b></p>\r\n<p></p>', '/images/DSS/2022/32/1.jpg', '2022-08-16', '/images/grants/pgrants_logo.jpg;\r\n/images/DSS/2022/32/1.jpg;\r\n/images/DSS/2022/32/2.jpg;\r\n/images/DSS/2022/32/3.jpg;\r\n/images/DSS/2022/32/5.jpg;\r\n/images/DSS/2022/32/4.jpg', '', '', 'Новости', '', 0, '', '7');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(641, '19', 'Готовимся к школе', 'Дважды в неделю в \"РАСсвете\" проходят занятия в коммуникативной группе \"Подготовка к школе\": 8 уроков по 20 минут каждый. Русский язык, чтение, математика, окружающий мир, музыка, физкультура и игры для развития коммуникации - все то, что обязательно пригодится ученикам в школе.', '<p>Дважды в неделю в \"РАСсвете\" проходят занятия в коммуникативной группе \"Подготовка к школе\": 8 уроков по 20 минут каждый. Русский язык, чтение, математика, окружающий мир, музыка, физкультура и игры для развития коммуникации - все то, что обязательно пригодится ученикам в школе.</p>\r\n<p>Вместе с детьми учатся и взрослые - на групповых занятиях проходят стажировку будущие тьюторы ресурсных классов.</p>\r\n<p>Подготовка педагогов, понимающих, как работать с нестандартным поведением детей - одна из проблем инклюзивного образования в Иркутске. В системе образования не хватает квалифицированных специалистов-поведенщиков.</p>\r\n<p>Учитель ресурсного класса школы посёлка Молодежный Елена Сергеевна Михеева рассказала, в чем сложность открытия ресурсного класса:\r\n</p>\r\n<p>- Создать инициативу группу (как правило, в неё входят родители детей с аутизмом) и через запрос в Департамент образования открыть ресурсный класс - мало. Правильно организовать дальнейшую работу ресурсного класса очень сложно. \r\n</p>\r\n<p>- Необходимо найти специалистов (учителя, тьюторов) и обучить их основам прикладного анализа поведения(ПАП), найти куратора класса (это опытный специалист с высокой квалификацией в области ПАП), закупить оборудование и материалы (мы обычно используем поддержку грантов и субсидий, но для этого нужно писать проекты и выигрывать конкурсы).</p>\r\n<p>- Начинать подготовку к открытию ресурсного класса нужно минимум за год до поступления ребёнка в школу. И в первую очередь, родителям и педагогам важно подробно изучить пособие <a href=\"http://outfund.ru/wp-content/uploads/2016/10/4_RK_preview.pdf\" target=\"_blank\">\"Ресурсный класс\"</a> и сопутствующие материалы к нему (на сайте разработчиков пособия - Ассоциации <a href=\"https://autismregions.ru/\" target=\"_blank\">\"Аутизм-Регионы\"</a>). Понимание сути инклюзивного образования, организация этого процесса очень важна. Если вам не просто нужно, чтобы ребёнок находился в школе и как-то там с ним занимались, а важно, чтобы он полноценно вливался в школьную жизнь и получал качественное образование, согласно его потребностям и возможностям.  </p>\r\n<p>Ранее мы уже писали подробную статью об организации ресурсных классов, читайте её по ссылке: <a href=\"https://rassvet-apc.ru/rc/detail.php?id=533&back=rc\" target=\"_blank\">\"Как открыть ресурсный класс\"</a></p>\r\n<p>Проект реализуется при поддержке Благотворительный фонд \"Абсолют-Помощь\".</p>\r\n\r\n\r\n\r\n\r\n', '/images/Absolut/35/5.jpg', '2022-08-23', '/images/Absolut/logo.jpg;\r\n/images/Absolut/35/5.jpg;\r\n/images/Absolut/35/6.jpg;\r\n/images/Absolut/35/1.jpg;\r\n/images/Absolut/35/2.jpg;\r\n/images/Absolut/35/3.jpg;\r\n/images/Absolut/35/4.jpg', '', '', 'Новости', '/news.php', 0, '', '9'),
(642, '2', 'Путешествие по Ангаре ', 'Последние тёплые денечки на исходе, мы решили оставить приятные воспоминания о лете 2022 года - отправились в путешествие одного дня.\r\n', '<p>Последние тёплые денечки на исходе, мы решили оставить приятные воспоминания о лете 2022 года - отправились в путешествие одного дня.\r\n</p>\r\n<p>Прогулка  на теплоходе по Ангаре, экскурсия в архитектурно-этнографическом музее под открытым небом \"Тальцы\", новые знакомства и общения с друзьями и единомышленниками - то, что было нужно и нашим ученикам и их родителям, и педагогам центра, чтобы взбодриться и отдохнуть.</p>\r\n<p>Смотрите фото с нашей инклюзивной поездки. Отзывы довольных участников тоже прикладываем. Очень приятно получать обратную связь! </p>\r\n<p>Поездка прошла в рамках проекта \"Шаг в инклюзию\" при поддержке Департамента здравоохранения и социальной помощи населения комитета по социальной политики и культуры администрации г. Иркутска.</p>\r\n\r\n\r\n\r\n\r\n', '/images/dosug/2022/2/1.jpg', '2022-08-24', '/images/dosug/2022/2/1.jpg;\r\n/images/dosug/2022/2/2.jpg;\r\n/images/dosug/2022/2/3.jpg;\r\n/images/dosug/2022/2/4.jpg;\r\n/images/dosug/2022/2/5.jpg;\r\n/images/dosug/2022/2/6.jpg;\r\n/images/dosug/2022/2/7.jpg;\r\n/images/dosug/2022/2/8.jpg;\r\n/images/dosug/2022/2/9.jpg;\r\n/images/dosug/2022/2/10.jpg;\r\n/images/dosug/2022/2/11.jpg;\r\n/images/dosug/2022/2/12.jpg;\r\n/images/dosug/2022/2/13.jpg;\r\n/images/dosug/2022/2/14.jpg;\r\n/images/dosug/2022/2/15.jpg;\r\n/images/dosug/2022/2/16.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', '8'),
(644, '4', 'Подводим итоги…', 'Подводим итоги и прощаемся с проектом «Социальные навыки и альтернативная коммуникация как основа инклюзивного образования для детей с РАС». Кратко о самом проекте для тех, кто недавно с нами и пропустил публикации на эту тему.', '<p>Подводим итоги и прощаемся с проектом «Социальные навыки и альтернативная коммуникация как основа инклюзивного образования для детей с РАС». Кратко о самом проекте для тех, кто недавно с нами и пропустил публикации на эту тему.</p>\r\n<p>☀ проект продолжался 1 год </p>\r\n<p>☀ участники: 15 учеников от 3 до 12 лет</p>\r\n<p>☀ + 10 педагогов, куратор и супервизор</p>\r\n<p>☀ групповые занятия по 3 направлениям: «Дошкольники», «PECS» и «Подготовка к школе»</p>\r\n<p>☀ занятия дважды в неделю по 3-4 часа в день в группе из 2-3-6 детей (увеличение учеников в группе велось поэтапно)</p>\r\n<p>☀ детей учили общаться друг с другом и взрослыми с помощью речи и/или альтернативных методов коммуникации, справляться с конфликтами, вместе играть, поведению слушателя, фронтальным инструкциям, развивали их графомоторные навыки и других умения, необходимые для учебы в школе.</p>\r\n<p>АВА-инструкторы отмечают успехи детей: дошкольники Лида и Захар перешли на 4 этап освоения PECS. Они научились составлять предложения из карточек PECS: находят в своём альбоме карточку с символом \"Я хочу\", крепят её на планшетку с липучей лентой, затем находят изображение предмета, который они хотят получить, крепят его следом и отдают планшетку АВА-инструктору.</p>\r\n<p>В группе по подготовке к школе дети начали самостоятельно поднимать руку, спокойно сидеть за столом, выходить к доске и возвращаться самостоятельно за парту без проявления проблемного поведения. У них получается с каждым днем всё лучше, что мотивирует их заниматься с большей охотой.</p>\r\n<p>ИСТОРИИ УЧЕНИЦ</p>\r\n<p>— Агате 4 года, ее родители обратились в «РАСсвет» этой зимой с запросом на коррекцию нежелательного поведения и развития речи ребенка: девочка не говорила слова, вокализировала. На групповых занятиях Агату научили пользоваться альтернативной системой коммуникации PECS, а затем уже с помощью карточек, логоритмики и методов смешанных блоков запустили речь. Сейчас ребенок использует коммуникативный планшет только в случае, если не знает, как что-то называется. После того, как Агата научилась доносить до окружающих свои желания и получать быструю обратную связь, ее поведение улучшилось. Благодарим родителей девочки, они активно включились в работу и уже дома обобщали навыки, полученные в центре.</p>\r\n<p>— Сверстница Агаты Лида сейчас находится на 4 этапе PECS – составляет предложения из карточек. Раньше девочка не терпела ожидания, не играла со сверстниками, занять ее чем-то полезным и развивающим было очень трудно. На занятиях Лида научилась ждать до 30 секунд (а начиналось все с пары секунд!), стала более внимательной (развитие шло за счет игр на внимание - слэп джек), может собирать по образцу, занимается по расписанию с подсказкой, стала спокойнее относится к сверстникам и научилась совместным подвижным играм.\r\n</p>\r\n<p>— 8-летняя Ира этой осенью пойдет в 1 класс. Но девочке сложно находится в группе. Из-за волнения и сенсорной перегрузки она громко вокализировала, размахивала руками, не могла сосредоточиться на задании педагога. На занятиях по подготовке к школе с помощью тьютера Ира повысила свою концентрацию внимания – за счет вовремя полученной подсказок (когда приступить к заданию, попросить о помощи, перемене). Также Ире помогли сориентироваться карточки с визуальным расписанием и изображениями правил поведения в классе. Первое время поощрением Иры за правильно выполненные задания было выйти из класса и побыть только в компании тьютера. Сейчас девочка научилась находиться в группе на протяжении всего занятия, ее поощрение – рисовать в альбоме.\r\n</p>\r\n<p>Проект подошел к концу, но групповые занятия в центре продолжаются. Чтобы узнать о них подробнее и записаться на консультацию к специалисту «РАСсвета» (поведенческому специалисту, дефектологу, психологу), звоните по телефону 89526236675.</p>\r\n<p>Проект реализовался при поддержке Благотворительный фонд «Абсолют-Помощь» .</p>\r\n\r\n\r\n\r\n\r\n', '/images/Absolut/36/2.jpg', '2022-08-31', '/images/Absolut/logo.jpg;\r\n/images/Absolut/36/5.jpg;\r\n/images/Absolut/36/6.jpg;\r\n/images/Absolut/36/1.jpg;\r\n/images/Absolut/36/2.jpg;\r\n/images/Absolut/36/3.jpg;\r\n/images/Absolut/36/4.jpg', '', '', 'Новости', '/news.php', 0, '', '9'),
(646, '18', '«Как вы заранее готовитесь»', '«Как вы заранее готовитесь» - слышим от партнеров акции <a href=\"https://vk.com/al_feed.php?section=search&q=%23тынеодинвэтоммире\" target=\"_blank\">#тынеодинвэтоммире</a>.\r\nПланируем мероприятие, как можно раньше, чтобы учесть все нюансы.\r\n', '<p>«Как вы заранее готовитесь» - слышим от партнеров акции <a href=\"https://vk.com/al_feed.php?section=search&q=%23тынеодинвэтоммире\" target=\"_blank\">#тынеодинвэтоммире</a>.\r\nПланируем мероприятие, как можно раньше, чтобы учесть все нюансы.\r\n</p>\r\n<p>Пока идут обсуждения с партнерами, силы мастерской направлены на подготовку изделий к декабрьской ярмарке.\r\nЗадумок много и так хочется их реализовать.</p>\r\n<p>Дошла очередь до изготовления браслетов и брелоков из кожи с символикой акции.\r\nНесколько движений на прессе и пробойнике и вуаля - стильный аксессуар из кожи готов.\r\nСтепа доволен результатом. А вам нравится?</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/DSS/2022/33/1.jpg', '2022-09-08', '/images/grants/pgrants_logo.jpg;\r\n/images/DSS/2022/33/1.jpg;\r\n/images/DSS/2022/33/2.jpg;\r\n/images/DSS/2022/33/3.jpg', '', '', 'Новости', '', 0, '', '7'),
(648, '2', 'Провожаем лето 2022\r\n', 'Сегодня в Иркутске по-настоящему хмурый осенний день. Чтобы впустить в него немного тепла – вспоминаем лето и вторую смену нашего инклюзивного АВА-лагеря!', '<p>Сегодня в Иркутске по-настоящему хмурый осенний день. Чтобы впустить в него немного тепла – вспоминаем лето и вторую смену нашего инклюзивного АВА-лагеря!</p>\r\n<p>В августе в лагере отдыхали 15 детей с аутизмом, вместе со своими близкими: родителями, братьями и сестрами. За неделю у нас было столько приятных событий, что мы зарядились энергией на весь год! Мы загорали, танцевали, играли в настолки, гоняли мяч и играли в волейбол, участвовали в эстафетах, ходили в маленькие походы, делали мандалы на песке, рисовали, делали поделки из камней, устраивали битву с водяными пистолетами, занимались игротерапией с психологом, собирались вместе за столом и делились вкусняшками, взрослые обменивались опытом воспитания. \r\n</p>\r\n<p>Смены в АВА-лагере окончились, но теперь мы точно знаем, что следующее лето должно быть таким же классным. Находимся в предвкушении и строим планы!</p>\r\n<p>Проект \"Есть контакт\" реализуется при поддержке Благотворительного фонда «Вклад в будущее».</p>\r\n\r\n\r\n\r\n\r\n', '/images/Kontakt/6/4.jpg', '2022-09-14', '/images/grants/VK_VB.jpg;\r\n/images/Kontakt/6/3.jpg;\r\n/images/Kontakt/6/2.jpg;\r\n/images/Kontakt/6/1.jpg;\r\n/images/Kontakt/6/5.jpg;\r\n/images/Kontakt/6/7.jpg;\r\n/images/Kontakt/6/6.jpg', '', '', 'Новости', '/news.php', 0, '', '6'),
(650, '18', 'Занятия по арт-терапии', 'В мастерских продолжаются занятия по арт-терапии, на которых наши малыши и дошколята готовятся к групповым занятиям в \"Слоне\". Результатом занятия не обязательно должна быть готовая поделка, наиболее важной составляющей терапии является сам процесс.', '<p>В мастерских продолжаются занятия по арт-терапии, на которых наши малыши и дошколята готовятся к групповым занятиям в \"Слоне\".</p>\r\n<p>Арт-терапия с элементами ИЗО и ДПИ это направление психотерапии, метод, который позволяет:</p>\r\n<p>Развить познавательно-речевую активность, коммуникацию и память,</p>\r\n<p>Корректировать поведение, в том числе, убрать стимминг (повторяющиеся движения, \"залипания\" на одном предмете),\r\n</p>\r\n<p>Проработать сенсорные проблемы,</p>\r\n<p>Избавиться от страхов, стресса,</p>\r\n<p>Сформировать позитивное самовосприятие.</p>\r\n<p>Результатом занятия не обязательно должна быть готовая поделка, наиболее важной составляющей терапии является сам процесс, в ходе которого преодолеваются преграды для обучения и происходит сенсорное насыщение.\r\n</p>\r\n<p>Мы подбираем для каждого ученика программу и материалы, с которыми ему интересно будет взаимодействовать не только в рамках занятия, но и в жизни, даже если сейчас ребёнок не может с ними справиться самостоятельно(держать карандаш в руке, вырезать ножницами, рисовать кистью и т.п.).</p>\r\n<p>А также используем поведенческий подход в обучении - прикладной анализ поведения или АВА-терапию.</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/DSS/2022/34/1.png', '2022-09-29', '/images/grants/pgrants_logo.jpg;\r\n/images/DSS/2022/34/1.png;\r\n/images/DSS/2022/34/2.png;\r\n/images/DSS/2022/34/4.png;\r\n/images/DSS/2022/34/5.png;\r\n/images/DSS/2022/34/3.png', '', '', 'Новости', '', 0, '', '7'),
(651, '19', 'Сентябрь в ресурсных классах', 'С чего начинается учебный год в школе? С повторения пройденного в прошлом году. Ресурсные классы не исключение: в сентябре тьюторы проверяют, все ли навыки учеников, освоенные ранее, обобщены и закреплены, над чем нужно поработать, что нового добавить в индивидуальные программы обучения.\r\n', '<p>\"Право на образование\" - название нашего проекта по работе с ресурсными классами в Иркутском районе и Забайкальском крае. В этом учебном году благодаря поддержке наших друзей - Благотворительного фонда «Абсолют-Помощь», 20 школьников с аутизмом получили возможность обучаться в инклюзивной среде - наравне со своими сверстниками из обычных классов.</p>\r\n<p>Что такое инклюзивная среда в школе - это адаптированные образовательные программы, протоколы коррекции поведения, помощь тьюторов, специальное оборудование и материалы для учебы в классе – парты с высокими бортиками, сенсорная зона разгрузки, визуальная поддержка и пр.\r\nПедагоги 3 ресурсных классов будут проходить обучение и консультации у специалистов «РАСсвета», а также работать под супервизией опытного поведенческого аналитика.</p>\r\n<p>С чего начинается учебный год в школе? С повторения пройденного в прошлом году. Ресурсные классы в Иркутском районе, в которых обучаются дети с аутизмом, не исключение: в сентябре тьюторы проводят тестирования ABLLS-R, проверяют, все ли навыки учеников, освоенные ранее, обобщены и закреплены, над чем нужно поработать, что нового добавить в индивидуальные программы обучения.\r\n</p>\r\n<p>Ребята из ресурсного класса школы посёлка Луговой будут в этом учебном году развивать и укреплять умение сотрудничать, работать в команде.  Выполнять задачу начали буквально с первых дней занятий: на групповой экскурсии на природе дети собирали листья и шишки, а затем все вместе готовили пано для традиционного осеннего школьного конкурса. </p>\r\n<p>Недавнее тестирование наших второклассников из школы посёлка Молодежный показало, что у многих ребят по-прежнему есть сложности с мелкой моторикой. Пока они совершенствуют графомоторные навыки с помощью прописей, педагоги также учат их печатать текст на компьютере. Чтобы замотивировать ребят на учёбу, тьюторы подбирают для них презентации с картинками, другим же предлагают набирать текст в поисковике и смотреть появляющиеся картинки - кому что больше нравится.</p>\r\n<p>И ещё немного примеров, над чем будут работать ребята в ближайшее время. Второклашки Стёпа и Влад будут продолжать учёбу, посещая занятия не только в ресурсном классе, но и в общеобразовательных классах. Плюс, индивидуальные занятия с тьютором, логопедом, психологом и дефектологом.</p>\r\n<p>Стёпе сложно читать и еще сложнее отвечать на вопросы по прочитанному тексту. Поэтому с ним педагоги отдельно работают над техникой чтения, и отдельно над пониманием прочитанного и ответами на вопросы по небольшим предложениям на карточках с картинками. Картинки, конечно же мотивационные - дают визуальную поддержку и помогают Стёпе ответить на вопрос.</p>\r\n<p>У Влада сложности с математикой. В этом году он будет решать примеры и задачи во втором десятке, а также осваивать умножение. В этом ему поможет многофункциональное пособие \"Нумикон\". \r\n</p>\r\n<p>Для оценки успешности освоения индивидуальных программ обучения в конце года, ученики ресурсных классов снова проходят специальные тестирования. О них подробнее в фото, прикреплённых ниже.</p>\r\n<p>В наших ресурсных классах, как и в общеобразовательных, есть 5-бальная система оценок (начиная со 2 класса начальной школы и за исключением образовательного маршрута 8.4).</p>\r\n<p></p>\r\n<p><a href=\"/news/detail.php?id=652&back=news\" target=\"_blank\">Проект \r\n\"Право на образование\"</a> осуществляется при поддержке Благотворительного фонда \"Абсолют-Помощь\".</p>\r\n\r\n\r\n\r\n\r\n\r\n', '/images/RK/2022/25/1.jpg', '2022-09-30', '/images/RK/2022/25/1.jpg;\r\n/images/RK/2022/25/2.jpg;\r\n/images/RK/2022/25/3.jpg;\r\n/images/RK/2022/25/5.jpg;\r\n/images/RK/2022/24/5.jpg;\r\n/images/RK/2022/24/1.jpg;\r\n/images/RK/2022/24/4.jpg;\r\n/images/RK/2022/24/3.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', '5'),
(652, '10', 'Проект \"Право на образование\"', 'Статус проекта: завершен<br>\r\nСроки реализации: 01.09.2022 - 30.06.2023<br>\r\nИсточник софинансирования: Благотворительный фонд \"Абсолют-Помощь\"\r\n', '<p><b>Статус проекта:</b> завершен<br>\r\n<b>Сроки реализации:</b> 01.09.2022 - 30.06.2023<br>\r\n<b>Источник софинансирования:</b> Благотворительный фонд \"Абсолют-Помощь\"</p>\r\n<p>Проект предполагает создание обучающих условий 20 ученикам в 2 ресурсных классах (РК) Иркутского района и 1 РК в г. Чита, для чего помимо образовательных программ мы проведем оценку по ABLLS-R и внедрим каждому ученику протоколы коррекции поведения, поддерживая образовательный процесс консультациями Поведенческого аналитика ВСВА. Оказываемая кураторами ежедневная консультационная работа для тьюторов РК передаст им опыт ведения занятий по модели РК.  </p>\r\n<p>Чтобы помочь государству встроить инклюзивную модель \"Ресурсный класс\" в систему образования мы поделимся опытом использования поведенческих технологий в РК со специальными педагогами на тренингах, семинарах и конференциях. На уроках доброты и собраниях ученикам регулярных классов, их родителям и учителям расскажем об особенностях детей с РАС. На мероприятиях все участники получат брошюры с социальными историями о детях с РАС. А также привлечём к проблеме аутизма внимание широкой аудитории путём публикаций статей на интернет ресурсах.\r\n</p>\r\n<p><b>Цель проекта:</b></p>\r\n<p>Масштабирование инклюзивного образования для детей с РАС школьного возраста путем информирования специальных педагогов и родителей детей с РАС о поведенческих подходах с доказанной эффективностью и внедрения модели «Ресурсный класс» в образовательные учреждения Забайкальского края и Иркутского района. </p>\r\n<p><b>Задачи:</b></p>\r\n<p>1. Повысить компетенции специальных педагогов общеобразовательных учреждений и родителей детей с аутизмом с помощью реализации обучающих тренингов, семинаров и супервизий.  </p>\r\n<p>2. Создать учебную среду на занятиях с детьми с РАС в общеобразовательных учреждениях Иркутского района и Забайкальского края в соответствии с моделью “ресурсный класс”.  </p>\r\n<p>3.Развить инклюзивную культуру участников образовательного процесса путем информирования о проблеме аутизма.</p>\r\n<p><a href=\"https://rassvet-apc.ru/news.php?project=5\" class=\"button\">Публикации о реализации проекта</a></p>', '/images/grants/absolut_RK.jpg', '2022-09-05', '', '', '', 'Гранты', '/pages/grants.php', 0, '', '0'),
(653, '18', 'Столярная мастерская в \"Доме синего слона\"', 'Готовим полки для ярмарки акции \"Ты не один в этом мире\". Сперва тщательно шлифуем, работа эта не из легких. Понять на ощупь достаточно ли гладкая доска ребятам сложно, на помощь приходит мастер с карандашом.', '<p>- Что это там шыркает? - Да это же в мастерских сегодня столярка!</p>\r\n<p>Готовим полки для ярмарки акции \"Ты не один в этом мире\". Сперва тщательно шлифуем, работа эта не из легких. Понять на ощупь достаточно ли гладкая доска ребятам сложно, на помощь приходит мастер с карандашом. Закрашиваем шершавое место, это будет знаком для подмастерья, что здесь надо шлифовать до тех пор, пока не исчезнет след карандаша.\r\n</p>\r\n<p>Когда доска гладкая можно приступать к покраске. В этом процессе ребята уже настоящие асы, еще бы, сама работа располагает к самостоятельности. Здесь и что делать понятно, и как это делать понятно и результат сразу виден. Отличная работа!</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/DSS/2022/35/1.jpg', '2022-10-06', '/images/grants/pgrants_logo.jpg;\r\n/images/DSS/2022/35/1.jpg;\r\n/images/DSS/2022/35/2.jpg;\r\n/images/DSS/2022/35/4.jpg;\r\n/images/DSS/2022/35/3.jpg', '', '', 'Новости', '', 0, '', '7'),
(655, '4', 'АВА-инструктор, куратор, супервизор - кто они?', 'В настоящей ABA-терапии в «борьбу» за навыки ребенка всегда вступает целая команда специалистов! Инструктора, куратор и супервизор. Каждый член команды супер-важен! В этой статье мы объясняем, в чём состоят функции каждого из них.', '<p>В настоящей ABA-терапии в «борьбу» за навыки ребенка всегда вступает целая команда специалистов! Инструктора, куратор и супервизор. Каждый член команды супер-важен! В этой статье мы объясняем, в чём состоят функции каждого из них.</p>\r\n<p>Инструктор - это руки, которые работают только с ребенком. Каждый день терапист встречает вас в центре и проводит занятия. Не удивляйтесь, если тераписты меняются. В ABA есть понятие «генерализация»-это когда ребенок закрепляет навык в разных условиях с разными людьми, чтобы мы точно могли сказать, - «освоено».</p>\r\n<p> Куратор - это голова. Вся основная аналитическая работа и консультирование родителей-его задача.\r\n\r\nКуратор анализирует вехи развития вербального поведения ребенка, ставит цели на период обучения, пишет программу и протоколы, следит за реализацией учебной программы и ведет работу с родителями.</p>\r\n<p>Супервизор - это шея. Опытный наставник, который много лет занимается прикладным анализом поведения и помогает куратору не пропустить ни одной важной детали в поведении наших ребят.</p>\r\n<p>Хоть в прикладном анализе поведения и существует достаточно жесткая иерархия подчинения педагогического отдела, каждый участник учебного процесса вносит свой существенный вклад в развитие наших юных учеников.</p>\r\n<p>Кстати, в ресурсных классах также есть свои куратор и супервизор, а вместо инструктора - тьютор. Дистанционные супервизии педагоги наших ресурсных классов проходят раз в 2 недели, а очные - дважды в год. На следующей неделе ждём приезда супервизора в школу Читы.</p>\r\n<p><a href=\"/news/detail.php?id=652&back=news\" target=\"_blank\">Проект \r\n\"Право на образование\"</a> осуществляется при поддержке Благотворительного фонда \"Абсолют-Помощь\".</p>\r\n\r\n\r\n\r\n\r\n\r\n', '/images/RK/2022/28/3.jpg', '2022-10-17', '/images/Absolut/logo.jpg;\r\n/images/RK/2022/28/1.jpg;\r\n/images/RK/2022/28/2.jpg;\r\n/images/RK/2022/28/3.jpg', '', '', 'Новости', '/news.php', 0, '', '5'),
(656, '1,26', 'Проект \"Передышка для мамы\"', 'Статус проекта: завершен<br>\r\nСроки реализации: 01.10.2022 - 30.09.2023<br>\r\nИсточник софинансирования: Фонд президентских грантов\r\n', '<p><b>Статус проекта:</b> завершен<br>\r\n<b>Сроки реализации:</b> 01.10.2022 - 30.09.2023<br>\r\n<b>Источник софинансирования:</b> Фонд президентских грантов</p>\r\n<p>Проект направлен на интенсивную абилитацию детей с расстройствами аутистического спектра (РАС) 3-12 лет в семьях, экономически и психологически пострадавших из-за последствий влияния на граждан РФ недружественных стран. Из числа семей, выявленных при проблемных интервью, 60 детей с РАС с участием родителей пройдут консультации поведенческого аналитика, где определятся сильные и слабые стороны ребенка. На консультациях педагог даст рекомендации по методам освоения важных навыков ребенка. Также на консультациях определятся 20 семей, чьи дети нуждаются в немедленной абилитации, а родители в психологической поддержке. 20 детей с РАС получат регулярное индивидуальное обучение жизненно важным навыкам, согласно выявленным на тестировании преградам и пробелам в вехах развития ребенка. Особенности проекта заключаются в оказании психологической поддержки и передаче опыта использования научно-обоснованных методов коррекции поведения ребенка с РАС родителям, чтобы у семей появилась возможность организовать занятия с детьми вне зависимости от их благосостояния.</p>\r\n<p>20 родителей пройдут 3 цикла еженедельной групповой психологической поддержки. В ходе индивидуального регулярного обучения детей и групповых встреч с родителями выявятся 10 семей, которым необходима ежедневная интенсивная помощь. Для 10 детей пройдут интенсивные индивидуальные занятия по 4 часа в день в течение месяца, после которых дети продолжат занятия на регулярных занятиях. Во время 4х часовых занятий с детьми родители на первых 2 неделях пройдут индивидуальные консультации с психологом и теоретическое обучение основам прикладного анализа поведения по разработанному курсу для родителей. На последующих 2х неделях родители пройдут стажировку по работе с навыками своих детей для дальнейшей самостоятельной работы в натуральной среде. По окончанию интенсивных занятий поведенческие специалисты составят рекомендации для родителей по дальнейшему обучению ребенка в естественных условиях. По завершению регулярных занятий, пройдет повторная диагностика навыков 20 детей с помощью VB-MAPP, AFLS или ABLLS-R (выбор инструмента зависит от возраста и навыков конкретного ребенка), а родители получат рекомендации для дальнейшей адаптации ребенка дома. По результатам тестирования у каждого ребенка улучшится учебное поведение, навыки коммуникации, сформируются навыки самообслуживания, социальные навыки: умение взаимодействовать, имитировать действия сверстников, умение общаться, в том числе с использованием альтернативных способов коммуникации, что в дальнейшем позволит ребенку социализироваться в обществе.</p>\r\n<p>500 семей, которые только столкнулись с проблемой аутизма и не знакомы с методами помощи детям с РАС, получат информационные брошюры, из которых узнают о медицинских аспектах расстройства, эффективных научно обоснованных методах диагностики и абилитации. А также узнают, что такое Прикладной анализ поведения и как он помогает в обучении и воспитании детей с РАС.</p>\r\n<p><b>Цель проекта:</b></p>\r\n<p>Оказать комплексную психолого-педагогическую поддержку с использованием методов с научно доказанной эффективностью семьям с детьми с РАС, оказавшихся в трудной жизненной ситуации в связи с экономическими последствиями в Российской Федерации, с последующей передачей опыта родителям для продолжения абилитации ребенка с РАС в домашних условиях.</p>\r\n<p><b>Задачи:</b></p>\r\n<p>1. Сформировать жизненно важные навыки и социально приемлемое поведение у детей с РАС путем проведения интенсивных занятий по разработанным индивидуальным программам обучения с использованием поведенческих технологий.</p>\r\n<p>2. Снизить уровень эмоционального выгорания и истощения родителей детей с РАС путем консультационной и психологической поддержки в групповом и индивидуальном форматах. </p>\r\n<p>3. Развить у родителей детей с РАС навыки особого воспитания их детей путем индивидуального практического и теоретического обучения методам Прикладного анализа поведения.</p>\r\n<p><a href=\"https://rassvet-apc.ru/news.php?project=4\" class=\"button\">Публикации о реализации проекта</a></p>', '/images/grants/respite_for_mom.jpg', '2022-10-05', '', '', '', 'Гранты', '/pages/grants.php', 0, '', '0'),
(658, '12', 'Физкультура в групповом формате\r\n', 'На занятиях адаптивной физкультурой (АФК) в центре \"РАСсвет\" дети с особыми потребностями учатся не только ловкости, скорости и выносливости. Особенно полезны занятия в группе, где ребята учатся взаимодействовать друг с другом.', '<p>На занятиях адаптивной физкультурой (АФК) в центре \"РАСсвет\" дети с особыми потребностями учатся не только ловкости, скорости и выносливости. Благодаря методикам АВА-терапии тренер помогает ребятам концентрироваться на заданиях, развивать внимание и последовательность действий, спокойно принимать неудачи и искренне радоваться каждому успеху. Это способствует повышению самооценки и укреплению мотивации к обучению через игру.</p>\r\n<p>Чтобы поддерживать интерес и стимулировать прогресс, тренеры используют систему поощрений. Например, Соня обожает музыку — после упражнений она получает возможность послушать любимую песню или поиграть на музыкальном инструменте. Влад же предпочитает активные игры: вместе с тренером они устраивают увлекательные догонялки, превращая отдых в дополнительную тренировку.</p>\r\n<p>Особенно полезны занятия в группе, где ребята учатся взаимодействовать друг с другом. Они осваивают навыки работы в команде, терпеливо ждут своей очереди, помогают товарищам и участвуют в дружеских соревнованиях. Даже поражение воспринимается ими спокойнее благодаря поддержке наставников.</p>\r\n<p>Обучение игровой деятельности происходит постепенно. Перед тем, как проводить групповые тренировки, сначала на индивидуальных занятиях дети учатся базовым движениям: бросать и ловить мяч, забрасывать его в корзину, бегать на разные дистанции, прыгать в длину и высоту. Эти навыки впоследствии помогают им успешно участвовать в коллективных играх и даже показывать результаты на соревнованиях.</p>\r\n<p>Собрали для вас фото с индивидуальных (подготовка к командной игре начинается с них) и групповых занятий по адаптивной физкультуре. \r\n</p>\r\n<p>Проект \"Есть контакт\" реализуется при поддержке Благотворительного фонда «Вклад в будущее».</p>\r\n\r\n\r\n\r\n\r\n', '/images/Kontakt/7/2.jpg', '2022-10-26', '/images/grants/VK_VB.jpg;\r\n/images/Kontakt/7/3.jpg;\r\n/images/Kontakt/7/4.jpg;\r\n/images/Kontakt/7/1.jpg;\r\n/images/Kontakt/7/5.jpg;\r\n/images/Kontakt/7/7.jpg;\r\n/images/Kontakt/7/6.jpg', '', '', 'Новости', '/news.php', 0, '', '6'),
(661, '9', 'Родители в стрессе', 'Что испытывают родители, впервые узнавшие об аутизме своего ребенка? Целую гамму эмоций и чувств, которые можно объединить одним словом – стресс. Психолог \"АПЦ РАСсвет\" Анна Леонтьева  рассказала, как стресс родителей влияет на состояние детей и что с этим делать.', '<p>Что испытывают родители, впервые узнавшие об аутизме своего ребенка? Целую гамму эмоций и чувств, которые можно объединить одним словом – стресс. Это состояние проходит у всех по-разному, кто-то начинает искать «волшебные таблетки», чтобы исправить ситуацию и вернуть все в привычное русло, кто-то отказывается верить словам врача, выбирая тактику «само со временем рассосется», есть и другие способы справится с новой реальностью.</p>\r\n<p>Психолог \"АПЦ РАСсвет\" Анна Леонтьева  рассказала, как стресс родителей влияет на состояние детей и что с этим делать.</p>\r\n<p>♥ Родителям детей с РАС жизненно необходима постоянная психологическая консультация, начиная с первых дней постановки диагноза их детям, и в течение всей жизни. В период острого стресса человек проходит все стадии принятия ситуации: отрицание, гнев, торг, депрессия и только потом осмысление – принятие неизбежного. Задача психолога на начальном этапе - помогать и поддерживать, активно развивать тему возможных ресурсов, а не конфронтировать или спорить.\r\n</p>\r\n<p>♥ Психолог учит родителей принимать своего ребенка с тем небольшим запасом возможностей, который он имеет от природы, а также использовать их максимально эффективно для жизни. </p>\r\n<p>♥ Грамотная поддержка специалиста помогает родителям иначе взглянуть на жизнь, не сравнивать своего ребенка с другими детьми: родственниками, знакомыми, одноклассниками. Радоваться малым успехам, которые кажутся обычным людям незначительными, но являются грандиозными достижениями для ребенка с РАС: уметь донести свои желания с помощью речи или системы альтернативной коммуникации, самостоятельно одеться, поесть, занять себя полезным делом и т.п.\r\n</p>\r\n<p>♥ Даже помощи команды квалифицированных педагогов недостаточно для стойкой положительной динамики развития ребенка с аутизмом, если родители не принимают его таким, какой он есть, вместе с его особенностями. </p>\r\n<p>♥ В процессе воспитания детей с РАС родители часто сами подвергаются стрессам, эмоциональным срывам, что приводит к нервному истощению, агрессии, негативным выплескам. Это становится причиной конфликтов между членами семьи – между родителями, родителями и детьми. Выбранные в момент стресса методы разрешения конфликтных ситуаций, как правило, приводят еще к более серьезным последствиям. Поэтому так важно получать помощь со стороны специалиста, работать над собой и отношениями.\r\n</p>\r\n<p>Проект <a href=\"/news/detail.php?id=656&back=news\" target=\"_blank\">«Передышка для мамы»</a> реализован с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/respite/1.jpg', '2022-10-31', '/images/grants/pgrants_logo.jpg', '', '', 'Новости', '/news.php', 0, '', '4'),
(662, '18', '«Неделя неформального образования»', '«Неделя неформального образования» - это ежегодное городское мероприятие, в котором участвуют сотни иркутян: для людей разных возрастов, социального положения, физических возможностей, проводят обучающие и познавательные лекции, мастер-классы, презентации и т.п.', '<p>Ух, и насыщенная была неделя, да не простая, а «Неделя неформального образования»! Это ежегодное городское мероприятие, в котором участвуют сотни иркутян: для людей разных возрастов, социального положения, физических возможностей, проводят обучающие и познавательные лекции, мастер-классы, презентации и т.п.</p>\r\n<p>Мы, конечно, не могли такое пропустить! С удовольствием подключились к проекту и\r\nпровели инклюзивный мастер-класс «Знакомство с глиной». Желающих изготовить тарелочку из глины было так много, что мастерским пришлось временно переместиться в кабинет побольше и провести обучение в двух группах.\r\n</p>\r\n<p>Участники мастер-класса постарались на славу, первый опыт работы с глиной прошёл успешно. Особую гордость испытываем за нашим воспитанников, которые проявили небывалую самостоятельность и спокойно находились в группе. Мы очень рады новым знакомствам, приходите к нам еще!</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/DSS/2022/36/5.jpg', '2022-11-07', '/images/grants/pgrants_logo.jpg;\r\n/images/DSS/2022/36/1.jpg;\r\n/images/DSS/2022/36/2.jpg;\r\n/images/DSS/2022/36/4.jpg;\r\n/images/DSS/2022/36/3.jpg', '', '', 'Новости', '', 0, '', '7'),
(666, '4', 'Бесплатные консультации для родителей', 'Благодаря поддержке Фонда президентских грантов, мы смогли оказать консультативную помощь семьям, воспитывающим детей с аутизмом, бесплатно. Основные запросы родителей: есть у ребенка признаки РАС, как развить тот или иной навык, как работать с проблемным поведением.  ', '<p>Какой у вас выдался ноябрь?  Для сотрудников нашего центра этот ноябрь – динамичный: ведём  занятия для детей, для специалистов, выступаем на конференциях, успеваем готовиться к предновогодней акции «Ты не один в этом мире» и проводим консультации для родителей – участников нового проекта «Передышка для мамы» при поддержке Фонда Президентских Грантов, который стартовал в октябре этого года. \r\n</p>\r\n<p>В наших социальных сетях мы попросили родителей заполнить анкеты. Те, кто это сделал, получили бесплатные консультации АВА-специалистов «РАСсвета».  Подобная услуга в нашем центре стоит 2000 рублей, но благодаря поддержке Фонда президентских грантов, мы смогли оказать консультативную помощь семьям, воспитывающим детей с аутизмом, бесплатно. \r\n</p>\r\n<p>Как проходят консультации? Родители приходят к нам в центр вместе с ребенком, и обращаются с конкретным запросом к специалисту, рассказывают, что на данный момент их беспокоит в вопросах воспитания и обучения.</p>\r\n<p>Основные запросы: есть у ребенка признаки РАС, как развить тот или иной навык, как работать с проблемным поведением.  </p>\r\n<p>Во время консультации АВА-специалист собирает анамнез ребенка, узнает, развиты ли у него навыки игры, туалета, приема пищи, какие есть проблемы с поведением, принимает ли он лекарства и если да, то какие, у каких врачей наблюдается, посещает ли дополнительные занятия и т.п. \r\n</p>\r\n<p>Затем педагог проводит первичное тестирование, чтобы посмотреть соответствуют ли навыки ребенка его возрасту. Для этого АВА-специалист вступает с ним во взаимодействие: предлагает в игровой форме задания, оценивает навыки коммуникации и игры, поведение слушателя,  умение сотрудничать, учебные навыки, визуальное восприятие. \r\n</p>\r\n<p>В конце встречи специалист дает рекомендации родителям, объясняет, какие дефициты есть у ребенка, что необходимо развивать в первую очередь, какие занятия посещать, что может предложить наш центр для развития ребенка.</p>\r\n<p>В проекте принимают участие не только иркутяне, но и жители других городов и регионов, поэтому некоторые консультации мы проводим в дистанционно, хотя, конечно, очный формат дает больше результатов.</p>\r\n<p>Консультация по АФК</p>\r\n<p>На бесплатную консультацию в рамках нового проекта \"Передышка для мамы\" к нам пришла мама 9-летнего Дениса. До 7 лет у ребенка не было речи. Он начал говорить благодаря интенсивной работе с мамой, использующей методы АВА-терапии и уже принимавшей участие в наших проектах. Несмотря на успехи в чтении и счете, мальчик испытывает трудности с пониманием текста и взаимодействием с окружающими из-за редких посещений школы. У Дениса также сильная реакция на запахи, что усложняет его обучение вне дома. Родители сосредоточены на домашних занятиях, включая физическую подготовку, чтобы улучшить здоровье сына.</p>\r\n<p>Мама Дениса обратилась к специалистам нашего центра с конкретным запросом – как ей проводить адаптивные занятия по физкультуре. Кроме рекомендаций по структуре занятий и возрастных особенностей в выборе упражнений, АВА- консультант поделилась наработками коллег – методическим пособием по адаптивной физкультуре для детей и подростков с РАС.\r\n</p>\r\n<p>У ребенка с аутизмом нет официального диагноза, в какую школу ему идти?</p>\r\n<p>Такой вопрос задала на консультации с АВА-специалистом «РАСсвета» мама 6-летнего Ромы. Мальчик посещает общеобразовательный детский сад, но в групповых занятиях и утренниках участие не принимает – есть трудности с общением и обучением. Следующей осенью Рома пойдет в школу, в обычном классе с 30 учениками и 1 учителем ребенку будет слишком сложно учиться.\r\n\r\nВыбирать ли домашнее обучение или семейную форму образования, добиваться ли официального диагноза и тьютора для ребенка – у родителей Ромы сейчас много сомнений.</p>\r\n<p>На консультации поведенческий аналитик и руководитель экспертного отдела «РАСсвета» Елена Михеева рассказала маме Ромы, как сформировать учебное поведение сына, развить его коммуникативные навыки – то, что входит в стандартную подготовку к школе наших подопечных. И рекомендовала пройти психолого-медико-педагогическую комиссию и получить официальный диагноз у врача-психиатра.</p>\r\n<p>Прежде чем проходить ПМПК и консультации врачей, советуем разобраться в вопросе – что такое расстройство аутистического спектра и чем этот диагноз отличается от умственной отсталости, задержки речевого развития и т.п., чтобы аргументированно общаться со специалистами и отстаивать права своего ребенка. </p>\r\n<p>\r\n<p>Проект <a href=\"/news/detail.php?id=656&back=news\" target=\"_blank\">«Передышка для мамы»</a> реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/respite/1/1.jpg', '2022-11-30', '/images/grants/pgrants_logo.jpg;\r\n/images/respite/1/2.jpg;\r\n/images/respite/1/3.jpg;\r\n/images/respite/1/4.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', '4'),
(667, '16', 'Проект \"Рассвета\" в Топ-100 проектов победителей конкурсов на предоставление президентских грантов', 'Наш проект 2021 года \"Скорая помощь для детей с РАС их семей\" вошёл в Топ-100 проектов победителей конкурсов на предоставление президентских грантов! Спасибо Фонду президентских грантов за высокую оценку и доверие!\r\n', '<p>Наш проект 2021 года <a href=\"/ambulance.php\" target=\"_blank\">\"Скорая помощь для детей с РАС их семей\"</a> вошёл в Топ-100 проектов победителей конкурсов на предоставление президентских грантов!</p>\r\n<p>Это значит, что мы - специалисты АПЦ РАСсвет, Аутизм в Иркутске, наши подопечные и их родители отлично поработали и справились с задачами проекта: в непростое время (пандемия, самоизоляция) научили детей с аутизмом важным гигиеническим и бытовым навыкам, спокойному посещению поликлиник, помогли им освоить дистанционный формат обучения и восстановить утраченные навыки общения, самообслуживания.</p>\r\n<p>Спасибо Фонду президентских грантов за высокую оценку и доверие - мы получили огромную мотивацию для работы. Победа повысила репутацию центра и поможет нам в дальнейших конкурсах.</p>\r\n<p>В рамках проекта мы сняли ролик о том, как наши ребята после десенсибилизации на занятиях, ходили на осмотр к врачу в клинику Стоматология в Иркутске Лечение зубов и кариеса.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '/images/news/2022/sp.jpg', '2022-11-07', '', '/images/news/2022/sp.jpg;yI6Y-nk3d2Y', '', 'Новости', 'news.php', 0, '', '13'),
(668, '4', 'Насыщение &#129185; обучение\r\n', 'У наших подопечных есть необычные увлечения, которые мешают им заниматься обычными делами. Поэтому наши специалисты решили насытить сенсорный голод ребят на индивидуальных занятиях по ИЗО.', '<p>У наших подопечных есть необычные увлечения, которые мешают им заниматься обычными делами. </p>\r\n<p>Например, Илья на занятиях творчеством не мог сделать даже самые простые аппликации: размазывал клей по рукам и ближайшим поверхностям, склеивал и разлеплял свои пальчики – стимился и получал удовольствие от этих ощущений. Переключить мальчика на следующие этапы аппликации не получалось. \r\n</p>\r\n<p>У Игоря была проблема с мытьем рук: он знал и умел выполнять весь алгоритм гигиенической процедуры, но в какой-то момент зациклился на мыльной пене, не смывал ее с рук, а добавлял все больше и больше. Мог долго стоять возле раковины, пока его в итоге не уводили взрослые. </p>\r\n<p>Взывать к совести или логике, в таких случаях, бесполезно (это и с нейротипичными детьми не всегда работает), поэтому наши специалисты пошли другими путем: решили насытить сенсорный голод ребят на индивидуальных занятиях по ИЗО.</p>\r\n<p>Илье на протяжении 4 уроков выдавали специальную основу – плотный картон обклеенный двусторонним скотчем. Мальчик водил по ней ладонями, прилеплял пальчики, затем выкладывал на основу цветы и листики, сыпал в формочки крупу - получилось панно. </p>\r\n<p>За пару недель у Ильи пропала потребность в ощущении липкости, к клею он стал относиться спокойно, и использует его по назначению.</p>\r\n<p>Игорю для насыщения сенсорной зоны подобрали технику мокрого валяния шерсти. Мальчик играл за столом с пеной, прежде чем раскатывать по мыльному коврику шерсть. </p>\r\n<p>&#128073; АВА-совет: удовлетворяйте сенсорные потребности детей! Чтобы в рамках обычной жизни ребенок не отвлекался на свои ощущения, а спокойно выполнял бытовые дела или учебные задания.\r\n</p>\r\n<p>Проект \"Есть контакт\" реализуется при поддержке Благотворительного фонда «Вклад в будущее».</p>\r\n\r\n\r\n\r\n\r\n', '/images/Kontakt/8/5.jpg', '2022-11-11', '/images/grants/VK_VB.jpg;\r\n/images/Kontakt/8/3.jpg;\r\n/images/Kontakt/8/4.jpg;\r\n/images/Kontakt/8/1.jpg;\r\n/images/Kontakt/8/2.jpg;\r\n/images/Kontakt/8/6.jpg', '', '', 'Новости', '/news.php', 0, '', '6'),
(679, '4', 'Подводим итоги проекта \"Есть контакт\"\r\n', 'Восемь месяцев назад мы запустили проект \"Есть контакт\" по развитию коммуникации и социализации у детей с РАС, и теперь, после заключительного тестирования детей, подводим его итоги. ', '<p>Восемь месяцев назад мы запустили проект \"Есть контакт\" по развитию коммуникации и социализации у детей с РАС, и теперь, после заключительного тестирования детей, подводим его итоги. </p>\r\n<p>Наши подопечные - дети с аутизмом дошкольного и школьного возраста успешно прошли обучение:</p>\r\n<p>&#128153; научились включаться в работу группы, выполнять задание сообща;</p>\r\n<p>&#128153; прошли обучение в разных направлениях - академические и бытовые навыки, адаптивная физкультура, </p>\r\n<p>&#128153; улучшили свои коммуникативные навыки и способность адаптироваться к изменениям в среде (социальная адаптация);</p>\r\n<p>&#128153; вместе с братьями, сёстрами и родителями отдохнули в летнем инклюзивном лагере на Байкале. </p>\r\n<p>Благодаря совместной работе педагогов центра (инструкторов, психологов, куратора), супервизора и родителей ребят, дети получили успешный старт для дальнейшего развития своих социальных навыков. Верим, что у них всё будет хорошо! И продолжаем занятия теперь уже в других проектах!</p>\r\n<p></p>\r\n<p>Проект \"Есть контакт\" был реализован при поддержке Благотворительного фонда «Вклад в будущее».</p>', '/images/Kontakt/10/0.jpg', '2022-12-22', '/images/grants/VK_VB.jpg;\r\n/images/Kontakt/10/2.jpg;\r\n/images/Kontakt/10/3.jpg;\r\n/images/Kontakt/10/1.jpg;\r\n/images/Kontakt/10/5.jpg;\r\n/images/Kontakt/10/4.jpg', '', '', 'Новости', '/news.php', 0, '', '6'),
(680, '19', 'Супервизии в ресурсных классах', 'Один основных принципов в АВА-терапии: регулярное консультирование специалистов с более опытными коллегами. В ресурсных классах АВА-система работает по тому же принципу. Лишь немного меняется состав (но не иерархия) специалистов.', '<p>Один основных принципов в АВА-терапии: регулярное консультирование специалистов с более опытными коллегами: инструкторы получают поддержку кураторов, кураторы - супервизоров, супервизоры находят наставников среди друг друга.</p>\r\n<p>В ресурсных классах АВА-система работает по тому же принципу. Лишь немного меняется состав (но не иерархия) специалистов.</p>\r\n<p>В ресурсном классе школы посёлка Молодежный дистанционные супервизии в этом учебном году проходят дважды в месяц. В них участвует, в первую очередь, куратор класса, он заранее готовит материал: видео с занятий, программы детей, чек-листы, таблицы сбора данных по поведению. Также на консультации с супервизором всегда присутствуют учитель класса или дефектолог, приглашаются по необходимости тьюторы.</p>\r\n<p>На супервизии обсуждаются самые разные вопросы: планы и протоколы по коррекции поведения, стратегии по формированию навыков, успехи детей и сложные моменты, в которых педагогам класса требуется совет. Происходит проверка индивидуальных программ детей, релевантность целей этих программ, их соответствие уровню навыков ребёнка, функциональность и практическая значимость.</p>\r\n<p>Изучая видео с занятий, супервизор оценивает технику работы учителей и тьюторов, правильное применение АВА-методов и приёмов обучения, подсказок и поощрений. И даёт советы куратору на что обратить внимание во время будущих обучающих тренингов/консультаций для педагогов класса.</p>\r\n<p>В декабре наш супервизор - Наталия Александровна Мальтинская (сертифицированный поведенческий аналитик ВСВА, специальный психолог, кандидат медицинских наук) посетила ресурсные классы Иркутского района с очным визитом. </p>\r\n<p>Наталия Александровна ведёт супервизии в ресурсных классах Иркутского района с первого дня их работы, многих учеников знает ещё до того, как они стали школьниками. </p>\r\n<p>В нынешний приезд она провела обучение для педагогов и помогла скорректировать поведенческие планы для ребят. </p>\r\n<p>Поведенческий план в АВА-терапии - это программа вмешательства, для изменения поведения ребёнка: коррекция проблемного поведения (убегания, внезапные крики, отказ от еды, туалета и т.п.) и обучение ребёнка замещающему (социально приемлемому поведению) и новым необходимым навыкам. </p>\r\n<p></p>\r\n<p><a href=\"/news/detail.php?id=652&back=news\" target=\"_blank\">Проект \r\n\"Право на образование\"</a> осуществляется при поддержке Благотворительного фонда \"Абсолют-Помощь\".</p>\r\n\r\n\r\n\r\n\r\n\r\n', '/images/RK/2022/32/1.jpg', '2022-12-26', '/images/Absolut/logo.jpg;\r\n/images/RK/2022/32/2.jpg;\r\n/images/RK/2022/32/4.jpg;\r\n/images/RK/2022/32/5.jpg;\r\n/images/RK/2022/32/3.jpg', '', '', 'Новости', '/news.php', 0, '', '5');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(681, '5', 'Информационные листовки для родителей', 'Что делать, если вы подозревает, что у ребёнка аутизм? Мы сделали информационную листовку с инструкцией для ответа на этот вопрос. Скачивайте и делитесь информацией со знакомыми родителями. ', '<p>Есть распространенное заблуждение, что расстройства аутистического спектра - большая редкость. Давайте посмотрим на числа. </p>\r\n<p>По данным Минздрава РФ у 1% детей в нашей стране есть аутизм. По статистике прошлого года в России проживают более 30 миллионов детей, это значит, что у 300 тысяч из них есть РАС.</p>\r\n<p>Что делать, если вы подозревает, что у ребёнка аутизм? Мы сделали информационную листовку с инструкцией для ответа на этот вопрос. Скачивайте <a href=\"/images/respite/5/1.jpg\" target=\"_blank\">по ссылке</a> и делитесь информацией со знакомыми родителями. </p>\r\n<p>Листовки опубликуются на сайте Главного бюро медико-социальной экспертизы по Иркутской области и на ресурсах Ассоциация \"Аутизм Регионы\". </p>\r\n<p>Проект <a href=\"/news/detail.php?id=656&back=news\" target=\"_blank\">«Передышка для мамы»</a> реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n', '/images/respite/5/2.jpg', '2022-12-22', '/images/grants/pgrants_logo.jpg', '', '', 'Новости', '/news.php', 0, '', '4'),
(682, '2', 'Новый год в батутном центре', '\"Кто хорошо работает, тот хорошо отдыхает\" - подумали мы и организовали на выходных новогодний праздник для наших подопечных и их семей в одном из самых больших батутных парках Иркутска! Ребята весело и продуктивно провели время.', '<p>\"Кто хорошо работает, тот хорошо отдыхает\" - подумали мы и организовали на выходных новогодний праздник для наших подопечных и их семей в одном из самых больших батутных парках Иркутска!</p>\r\n<p>Ребята весело и продуктивно провели время. Успели: поиграть с Дедом Морозом, потанцевать, полазать, получить подарки, поваляться в яме из мягких кубиков, покататься с горки, покидать мячики в сухом бассейне, сфотографироваться с ёлкой и конечно же попрыгать от души.</p>\r\n<p>Родители времени зря не теряли, участвовали во всех детских активностях и даже придумали новые сенсорные поощрения!</p>\r\n<p>Мы были очень рады встретить старых и новых друзей. Спасибо батутному центру \"SkyPark\" за интересную программу и доброе отношение к детям с особенностями в развитии.\r\n</p>\r\n<p>Мероприятие для родителей было абсолютно бесплатным, такая поддержка для детей и родителей стала возможной благодаря проекту Ассоциации \"Аутизм Регионы\"  «Культурный код - возможность для каждого 2.0: развитие сети творческих досуговых центров для подростков и молодых людей с РАС» реализуется при поддержке Фонда «Выход». Аутизм в России  с использованием гранта Президента Российской Федерации на реализацию проектов в области культуры, искусства и творческих индустрий \"Президентский фонд культурных инициатив\" </p>\r\n\r\n\r\n\r\n\r\n\r\n', '/images/dosug/2022/3/1.jpg', '2022-12-24', '/images/grants/FKI.png;\r\n/images/dosug/2022/3/1.jpg;\r\n/images/dosug/2022/3/2.jpg;\r\n/images/dosug/2022/3/3.jpg;\r\n/images/dosug/2022/3/4.jpg;\r\n/images/dosug/2022/3/5.jpg;\r\n/images/dosug/2022/3/6.jpg;\r\n/images/dosug/2022/3/7.jpg;\r\n/images/dosug/2022/3/8.jpg', '', '', 'Новости', '/news.php', 0, '', '19'),
(683, '9', 'Группы психологической поддержки родителей', 'В декабре прошлого года в рамках проекта \"Передышка для мамы\" в центре Рассвет стартовали группы поддержки для родителей, участвующих в программе. Эти встречи охватывали важные темы. Специалисты центра помогали родителям разобраться со сложными вопросами.', '<p>В декабре прошлого года в рамках проекта \"Передышка для мамы\" при поддержке Фонда Президентских Грантов в центре Рассвет стартовали группы поддержки для родителей, участвующих в программе. </p>\r\n<p>Эти встречи охватывали важные темы, такие как получение диагноза расстройства аутистического спектра (РАС), прохождение психолого-медико-педагогической комиссии (ПМПК), подготовка детей с ментальными особенностями к школьному обучению, работа с эмоциями и многое другое. Специалисты центра помогали родителям разобраться с такими сложными вопросами, как раздражение, усталость, детская ревность и разногласия с родственниками по вопросам воспитания.</p>\r\n<p>Родители детей с РАС нуждаются в постоянной психологической помощи с момента получения диагноза и на протяжении всей жизни. Специалисты проекта организовали ряд тренингов, направленных на обучение родителей методам снятия эмоционального напряжения и управления собственными эмоциями. Одним из ключевых мероприятий стал тренинг \"Снятие эмоционального напряжения\", который провел психолог Анна Леонтьева. Родители узнали о важности осознания собственных чувств и научились практическим техникам саморегуляции.</p>\r\n<p>Еще одной важной темой стало создание ресурсных классов для детей с РАС. Елена Михеева, директор экспертного отдела \"РАСсвета\", поделилась ценными рекомендациями по организации таких классов в школах. Она подчеркнула необходимость активного участия родителей в процессе, включая поиск подходящей школы и написание грантовых заявок.</p>\r\n<p>Также был проведен тренинг \"Мир детский — мир взрослый\", который помог родителям лучше понимать динамику смены состояний между ролями \"родителя\" и \"взрослого\". В ходе занятия участники освоили технику создания картин из шерсти, что позволило им испытать радость творчества и применить полученные навыки в воспитании детей.</p>\r\n<p>Отдельное внимание было уделено вопросу диагностики РАС. Екатерина Родыгина отметила важность официального диагноза для улучшения системы поддержки людей с РАС и их семей на государственном уровне. </p>\r\n<p>Проект <a href=\"/news/detail.php?id=656&back=news\" target=\"_blank\">«Передышка для мамы»</a> реализован с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n', '/images/respite/4/4.jpg', '2023-04-26', '/images/grants/pgrants_logo.jpg;\r\n/images/respite/4/1.jpg;\r\n/images/respite/4/2.jpg;\r\n/images/respite/4/3.jpg;\r\n/images/respite/4/5.jpg;\r\n/images/respite/4/6.jpg', '', '', 'Новости', '/news.php', 0, '', '4'),
(684, '5', 'Итоги нашей акции \"Ты не один в этом мире\"', '10 декабря прошла наша благотворительная акция «Ты не один в этом мире» в поддержку людей с аутизмом! Мы были очень рады видеть всех участников и гостей мероприятия: 247 человек присоединились к акции. ', '<p>10 декабря в СКДЦ \"Художественный\" прошла наша благотворительная акция «Ты не один в этом мире» в поддержку людей с аутизмом! Мероприятия длились весь день,было из чего выбрать: образование, культура, творчество, шопинг, общение, конкурсы для детей и взрослых.</p>\r\n<p>Мы были очень рады видеть всех участников и гостей мероприятия: 247 человек присоединились к акции. Ещё были те, кто не смог приехать, но поддержал нас материально - переводами через благотворительный сбор (все средства пошли на развитие инклюзивных мастерских для людей с РАС \"Дом синего слона\"). \r\n</p>\r\n<p>Вы участвовали в наших мастер-классах, круглых столах, ярмарке, приходили на спектакль и на многие другие активности. Мы говорим «Спасибо!» всем вам и будем рады видеть снова на наших мероприятиях.\r\n</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/DSS/2022/38/7.jpg', '2022-12-12', '/images/grants/pgrants_logo.jpg;\r\n/images/DSS/2022/38/1.jpg;\r\n/images/DSS/2022/38/2.jpg;\r\n/images/DSS/2022/38/3.jpg;\r\n/images/DSS/2022/38/4.jpg;\r\n/images/DSS/2022/38/5.jpg;\r\n/images/DSS/2022/38/6.jpg;\r\n/images/DSS/2022/38/7.jpg;\r\n/images/DSS/2022/38/8.jpg;\r\n/images/DSS/2022/38/9.jpg;\r\n/images/DSS/2022/38/10.jpg', '', '', 'Новости', '', 0, '', '7'),
(685, '22,5', 'Благодарим партнеров акции в поддержку людей с аутизмом', 'Наша акция «Ты не один в этом мире» уже завершилась, но приятные воспоминания останутся с нами надолго! И мы хотим поблагодарить всех наших партнеров, которые решили принять участие в акции и провести свои мастер-классы, круглые столы и даже спектакль! ', '<p>Наша акция «Ты не один в этом мире» уже завершилась, но приятные воспоминания останутся с нами надолго! И мы хотим поблагодарить всех наших партнеров, которые решили принять участие в акции и провести свои мастер-классы, круглые столы и даже спектакль! А особую благодарность выражаем Фонду президентских грантов за поддержку нашего проекта!</p>\r\n<p>Выражаем благодарность:</p>\r\n<p>✨ Кинотеатру \"Художественный\" и лично Папоновой Дарье Андреевне за гостеприимство и помощь в организации акции</p>\r\n<p>✨ Открытый драматический театр Alter Homo Иркутск за прекрасный спектакль и душевное завершение акции</p>\r\n<p>✨ Развлекательно-досуговому центру Город Детства. Детские праздники в Ангарске</p>\r\n\r\n<p>✨ Дизайн-салону «Городские цветы» и \"Цветочный базар\" за организацию и проведение мастер-класса по изготовлению хвойной композиции</p>\r\n\r\n<p>✨ Творческой мастерской \"Сладкие истории\" за проведение пряничного мастер-класса</p>\r\n\r\n<p>✨ Образовательному центру Easy School  за проведение интерактивной зоны</p>\r\n\r\n<p>✨ Гульфине Ремовой за проведение мастер-класса по изготовлению новогодней игрушки из ваты</p>\r\n\r\n<p>✨ Екатерине Тицкой за проведение мастер-класса в правополушарной технике рисования</p>\r\n\r\n<p>✨ Виктории Дятловой за то, что вкусно накормила наших участников</p>\r\n\r\n<p>✨ Кофейне \"Доза кофе\" за то, что согрели ароматным кофе и чаем</p>\r\n\r\n<p>✨ Магазину \"Место простых чудес\",  Ксении Насоновой \"Style_Himawari\", Людмиле Деменской за участие в ярмарке</p> \r\n<p>✨ «MYSTIK LAKE» за предоставление подарков для партнеров</p>\r\n\r\n<p>✨ \"Mini_fabrika_\" за изготовление монеток \"слоников\"</p>\r\n<p>Выражаем благодарность за приготовление сладостей для наших партнеров:</p>\r\n<p>✨ Юлии Игоревне Арфаниди, Неверовской Евгении Александровне \"Goldencake.art\", Васильевой Галине Николаевне \"Sladkoe_charstvo38\", Юлии Александровна Шпак, Чернышевой Елене Александровне, Дорофеева Дарье </p>\r\n<p>За изготовление букетов:</p>\r\n<p>✨ Полтавской Юлии</p>\r\n<p>✨ И отдельная благодарность информационным партнёрам, СМИ, журналистам и блоггерам: Дина Оккерт , «МАЯК. Иркутск» , IRK.ru Live , Яна Варшавская, Ксения Глубокова, Дарья Ли-Романова и всех тех, кто откликнулся, сделал репост и позвал друзей и знакомых на акцию!</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/DSS/2022/39/1.jpg', '2022-12-13', '/images/grants/pgrants_logo.jpg;\r\n/images/DSS/2022/39/1.jpg;\r\n/images/DSS/2022/39/2.jpg;\r\n/images/DSS/2022/39/3.jpg;\r\n/images/DSS/2022/39/4.jpg;\r\n/images/DSS/2022/39/5.jpg;\r\n/images/DSS/2022/39/6.jpg;\r\n/images/DSS/2022/39/7.jpg;\r\n/images/DSS/2022/39/8.jpg', '', '', 'Новости', '', 0, '', '7'),
(687, '19', 'Завершаем первое полугодие', 'Первое учебное полугодие позади! 4 месяца ребята усердно учились и в групповом формате, и в индивидуальном, прокачивали свои скиллы в коммуникации, социализации, речи, академических знаниях и даже бытовых навыках. \r\n', '<p>Первое учебное полугодие позади, ученики с РАС из ресурсного класса школы посёлка Молодежный Иркутского района отправляются на заслуженные каникулы. </p>\r\n<p>4 месяца ребята усердно учились и в групповом формате, и в индивидуальном, прокачивали свои скиллы в коммуникации, социализации, речи, академических знаниях и даже бытовых навыках. </p>\r\n<p>Дважды в неделю в классе проходили уроки по социально-бытовому ориентированию. Дети на практике проходили правила готовки, уборки, ухода за одеждой. Аккуратно складывать вещи, чистить обувь, зашивать прорехи, готовить салаты и мыть за собой посуду теперь могут все наши второклашки! </p>\r\n<p>С помощью сюжетно-ролевых игр, наглядных презентаций и бесед ребята изучили правила дорожного движения, культуру поведения в общественных местах. \r\n</p>\r\n<p>Особое внимание педагоги класса уделили медицинским манипуляциям: обыгрывали возможные ситуации в поликлинике. Дети попробовали себя и в роли пациента, и в роли врача. Недавно даже \"брали кровь\" из вены - это самый сильный страх наших подопечных. Такие уроки снижают страх к врачебных манипуляциям, дают возможность детям убедиться, что это не страшно и не больно. \r\n</p>\r\n<p><a href=\"/news/detail.php?id=652&back=news\" target=\"_blank\">Проект \r\n\"Право на образование\"</a> осуществляется при поддержке Благотворительного фонда \"Абсолют-Помощь\".</p>\r\n\r\n\r\n\r\n\r\n\r\n', '/images/RK/2022/33/1.jpg', '2022-12-28', '/images/Absolut/logo.jpg;\r\n/images/RK/2022/33/2.jpg;\r\n/images/RK/2022/33/4.jpg;\r\n/images/RK/2022/33/5.jpg;\r\n/images/RK/2022/33/6.jpg;\r\n/images/RK/2022/33/7.jpg;\r\n/images/RK/2022/33/8.jpg;\r\n/images/RK/2022/33/9.jpg;\r\n/images/RK/2022/33/1.jpg;\r\n/images/RK/2022/33/10.jpg', '', '', 'Новости', '/news.php', 0, '', '5'),
(691, '13,18', 'О мастерской \"Дом синего слона\" в Иркутских СМИ', 'В иркутских СМИ в январе вышли статьи о работе наших инклюзивных мастерских для детей и подростков с РАС \"Дом синего слона\" и о новом проекте \"РАСсвета\" - победителе конкурса Фонда президентских грантов.', '<p>В иркутских СМИ в январе вышли статьи о работе наших инклюзивных мастерских для детей и подростков с РАС \"Дом синего слона\" и о новом проекте \"РАСсвета\" - победителе конкурса Фонда президентских грантов.</p>\r\n<p>Благодаря новому проекту \"Тёплый дом синего слона\" наши подопечные научатся делать полезные для быта вещи: мыло, свечи, текстильную продукцию. Изделия послужат для людей, попавших в трудную жизненную ситуацию. Такой вот круговорот добра получится в Иркутске.</p>\r\n<p>Ссылка на статью на портале <a href=\"https://www.irk.ru/news/20230117/workshop/\" target=\"_blank\"><b>IRK.ru</b></a></p>\r\n<p>Ссылка на статью на сайте <a href=\"https://glagol38.ru/text/17-01-2023/007\" target=\"_blank\"><b>«Глагол. Иркутское обозрение»</b></a></p>\r\n<p>Ссылка на статью на сайте Правового портала <a href=\"https://www.ogirk.ru/2023/01/17/podrostki-s-autizmom-v-irkutske-budut-izgotavlivat-vostrebovannye-obshhestvom-tovary/\" target=\"_blank\"><b>\"Областная газета\"</b></a></p>\r\n<p>Ссылка на статью на сайте сетевого издания <a href=\"https://baikal24.ru/text/17-01-2023/090/\" target=\"_blank\"><b>\"Байкал-24\"</b></a></p>\r\n<p>Проект \"Ты не один в этом мире\" реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', 'https://static.irk.ru/media/img/site/gallery/30611/84bd663a-2b44-461d-8f59-9f6d52bce783_jpg_730x1000_x-False_q85.jpg', '2023-01-19', '/images/grants/pgrants_logo.jpg', '', '', 'Новости', 'news.php', 0, '', '2,7'),
(692, '18', 'Завершение проекта \"Ты не один в этом мире!\"', 'Почти год назад мы начали такой большой и важный проект \"Ты не один в этом мире!\". За 9 месяцев проекта попробовали новые для себя и ребят виды деятельности, обзавелись новыми друзьями и партнерами, обрели колоссальный опыт организации мероприятий.', '<p>Почти год назад мы начали такой большой и важный проект \"Ты не один в этом мире!\". С содроганием в сердце и воодушевленные кучей идей начали подготовку к декабрьской акции уже в начале года.</p>\r\n<p>За 9 месяцев проекта попробовали новые для себя и ребят виды деятельности - научились вязать на вязальной машине, работать с прессом и тиснением на коже, залили десятки свечей и мыла, начали заниматься керамикой, учились всему вместе с ребятами, работали над поведением и мотивацией (спасибо знаниям по прикладному анализу поведения и поведенческим специалистам). Что-то сразу находило отклик и давалось легко, над чем-то пришлось попыхтеть не один день.</p>\r\n<p>Мастерские обзавелись новыми друзьями и партнерами, обрели колоссальный опыт организации мероприятий.\r\nНедавно ребята прошли итоговое тестирование и показали хорошие результаты: у каждого ребёнка есть успехи и свои большие и маленькие победы.</p>\r\n<p>Но самое главное, от каждого занятия мы получали награду — это улыбки наших детей, порой молчаливый и застенчивый, но довольный взгляд. Что ещё надо для счастья педагогу?! Моменты обратной связи от детей это самое ценное, то ради чего стоит продолжать развивать навыки, которые пригодятся ребятам уже во взрослой жизни.</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/DSS/2023/1/11.jpg', '2023-01-28', '/images/grants/pgrants_logo.jpg;\r\n/images/DSS/2023/1/1.jpg;\r\n/images/DSS/2023/1/2.jpg;\r\n/images/DSS/2023/1/3.jpg;\r\n/images/DSS/2023/1/4.jpg;\r\n/images/DSS/2023/1/5.jpg;\r\n/images/DSS/2023/1/6.jpg;\r\n/images/DSS/2023/1/7.jpg;\r\n/images/DSS/2023/1/8.jpg;\r\n/images/DSS/2023/1/9.jpg;\r\n/images/DSS/2023/1/10.jpg', '', '', 'Новости', '', 0, '', '7'),
(693, '10', 'Проект «Шаг в инклюзию»', 'Статус проекта: завершен<br>\r\nСроки реализации: 01.02.2022 - 31.12.2022<br>\r\nИсточник софинансирования: Департамент здравоохранения и социальной помощи населению администрации г. Иркутска\r\n', '<p><b>Статус проекта:</b> завершен<br>\r\n<b>Сроки реализации:</b> 01.02.2022 - 31.12.2022<br>\r\n<b>Источник софинансирования:</b> Департамент здравоохранения и социальной помощи населению администрации г. Иркутска</p>\r\n<p>Проект «Шаг в инклюзию» направлен на создание условий, помогающих решению проблем оказания социальной поддержки семьям, имеющим детей-инвалидов с расстройством аутистического спектра в г.Иркутск. Мероприятия проекта призваны решить задачи, связанные с обучением и социализацией таких детей и успешным включением их в жизнь общества в дальнейшем.  </p>\r\n<p><b>Цель проекта:</b></p>\r\n<p>Социальная поддержка детей-инвалидов, детей из семей инвалидов, лиц с ограниченными возможностями, в том числе детей и подростков с РАС,   путем комплексного обучения и социализации в обществе.  </p>\r\n<p><b>Задачи:</b></p>\r\n<p>1. Создание условий для обучения детей и подростков с РАС социально-бытовым и коммуникативным навыкам в индивидуальной и групповой формах, путем проведения занятий с применением методов и технологий прикладного анализа поведения.</p>\r\n<p>2. Раскрытие творческого потенциала и наработка прикладных навыков с детьми и подростками с РАС в ходе работы в мастерских «Дом синего слона».</p>\r\n<p>3. Обобщение навыков коммуникации и взаимодействия с привлечением волонтеров и родителей через спортивное состязание (эстафету) среди детей с РАС и нормотипичных сверстников.</p>\r\n<p>4. Распространение информации о проблеме аутизма среди населения на организованной выставке изделий, созданных руками детей с РАС на мастерских.</p>\r\n<p>5. Установление доверительных отношений и укрепление кровных семейных связей через совместный досуг семей с детьми с РАС, педагогами и волонтерами путем познавательных экскурсий и генерализацией социальных навыков воспитанников центра.</p>\r\n<p>6. Тиражирование опыта работы с детьми с РАС среди специалистов города Иркутск и Иркутского района, работающих с детьми с ОВЗ в общеобразовательных учреждениях и ДДУ.</p>\r\n<p><b>Результаты проекта:</b></p>\r\n<p>Проведены занятия в мини-группах для детей с ОВЗ. На занятиях дети научились спокойно находиться в группе, взаимодействовать друг с другом социально приемлемым путем, используя карточки или речь, играть в игры с простым сюжетом, осуществлять прием пищи в группе, аккуратно складывать свои вещи. У детей с ОВЗ, посещавших группы по подготовке к школе, улучшились академические знания, навыки учебного поведения, просьб и спокойного нахождения в группе. Снизилось количество эпизодов нежелательного поведения у детей, проработаны страхи. Таким образом, благодаря занятиям в мини-группах, дети в сентябре смогли начать посещать обычный детский сад, некоторые дети поступили в школу.</p>\r\n<p>На занятиях в мастерских «Дом синего слона» подростки с ОВЗ в большинстве перестали бояться звука швейной машины, с помощью мастера научились обрабатывать края изделий, уверенно держать иголку в руке и выполнять действия по инструкции с физической подсказкой тьютора. Швейное дело требует длительного обучения, операции по кройке и ручному шитью детям даются нелегко из-за несформированности навыков мелкой моторики, работу в этом направлении планируем продолжать и после окончания проекта.</p>\r\n<p>Семинар по сенсорной интеграции позволил специалистам лучше понять особенности сенсорной сферы у детей с ОВЗ. Сенсорные дефициты, создающие преграду для обучения, своевременно диагностируются специалистами и восполняются на занятиях. В следствие чего у детей с ОВЗ снизились эпизоды самостимулятивного поведения, дети стали более собраны и внимательны, улучшилось учебное поведение, зрительный контакт стал более устойчивым. Освоение навыков стало проходить быстрее.</p>\r\n<p>Летом было проведено сразу два инклюзивных мероприятия – спортивные эстафеты и водная экскурсия в музей «Тальцы». Благодаря участию в мероприятиях детей с нормотипичным развитием дети с ОВЗ получают положительный опыт социального взаимодействия, проявляют первые самостоятельные попытки для коммуникации, участвуют в совместных играх с помощью тьюторов и становятся частью детского коллектива. Родители, участвуя в инклюзивных мероприятиях, поняли, что при соблюдении некоторых условий (подготовленных сверстников и подсказок тьюторов) их дети могут успешно адаптироваться к обществу. Взрослые стали меньше бояться и стали чаще посещать со своими детьми общественные места. Инклюзивные мероприятия получили большой отклик не только от родителей детей с ОВЗ, но и от детей с нормотипичным развитием. Ребятам очень понравились совместные мероприятия, планируем проводить инклюзивные мероприятия, как можно чаще.</p>\r\n<p><a href=\"/news.php?project=8\" class=\"button\">Публикации о реализации проекта</a></p>', '/images/DSS/2022/29/6.jpg', '2023-01-15', '', '', '', 'Гранты', '/pages/grants.php', 0, '', ''),
(696, '3', 'Отзыв об обучающем курсе для родителей', 'Специалисты нашего центра с прошлого года готовили обучающий видеокурс для родителей детей с аутизмом. Лекции с примерами из личной практики, задания, кейсы, тесты. 20 родителей прошли 11 бесплатных уроков об особенностях обучения детей с аутизмом.', '<p>Специалисты нашего центра с прошлого года готовили обучающий видеокурс для родителей детей с аутизмом, который проходил в рамках проекта \"Передышка для мамы\" при поддержке Фонда Президентских Грантов.</p>\r\n<p>Двадцать родителей, участвовавших в проекте, прошли 11 бесплатных уроков о расстройствах аутистического спектра, особенностях обучения детей с аутизмом, коррекции нежелательного поведения, решении проблем с туалетом и пищевой избирательностью. Всё с примерами из личной практики наших АВА-специалистов, заданиями, кейсами, тестами. Видео с уроками выводились на монитор в комнате для отдыха родителей и, пока дети занимались с педагогами, их мамы и папы тоже обучались!</p>\r\n<p>\r\n\"У меня были сомнения - правильно ли я выстраиваю развивающие занятие с ребёнком дома. АВА-терапией интересовалась, но пройти обучение никак не получалось. Поэтому, когда появилась возможность пройти дистанционный курс по прикладному анализу поведения со специалистами \"РАСсвета\", я с готовностью поучаствовала.</p>\r\n<p>Мне очень помогли в обучении видеопримеры с занятий: все показано наглядно и понятно.\r\n\r\nСразу взяла с курса жетонную систему, ввела её с ребёнком в домашние уроки, и заметила результат. Ребёнку стало интереснее заниматься, ему нравится собирать жетоны за выполненные задания.</p>\r\n<p>Вообще занятия у нас стали более насыщенными. Я узнала, как нужно к ним готовиться заранее, как удобнее выкладывать материалы\".</p>\r\n<p>Это отзыв Анастасии, мамы нашего подопечного Паши. В этом году она прошла обучающий видеокурс для родителей детей с аутизмом, который подготовили поведенческие специалисты \"РАСсвета\".</p>\r\n<p>Проект <a href=\"/news/detail.php?id=656&back=news\" target=\"_blank\">«Передышка для мамы»</a> реализован с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n', '/images/respite/7/3.jpg', '2023-05-23', '/images/grants/pgrants_logo.jpg;\r\n/images/respite/7/1.jpg;\r\n/images/respite/7/2.jpg', '', '', 'Новости', '/news.php', 0, '', '4'),
(698, '19', 'Обратная инклюзия в ресурсном классе ', 'В ресурсном классе школы посёлка Луговой Иркутского района на этой неделе прошло необычное занятие. На уроке труда дети сделали валентинки для своих пап и мам. Творческий процесс происходил в компании ребят из общеобразовательного класса. ', '<p>В ресурсном классе школы посёлка Луговой Иркутского района, где обучаются наши подопечные с РАС, на этой неделе прошло необычное занятие. На уроке труда дети сделали валентинки для своих пап и мам. Причём творческий процесс происходил в компании ребят из общеобразовательного класса. </p>\r\n<p>Дети из разных классов познакомились, пообщались, научились делать открытки и стали ближе друг к другу. Так работает обратная инклюзия. </p>\r\n<p>Ученикам общеобразовательного класса сделали небольшой презент - брошюры с социальной историей о дружбе между двумя мальчиками, у одного из которых аутизм. Брошюры новенькие - их подготовили в нашем центре 3 месяца назад. Мы рады, что они нашли своих читателей</p>\r\n<p>Урок прошёл при поддержке Благотворительный фонд Владимира Потанина.\r\n</p>\r\n\r\n\r\n\r\n\r\n\r\n', '/images/RK/2023/1/1.jpg', '2023-02-14', '/images/RK/2023/1/2.jpg;\r\n/images/RK/2023/1/4.jpg;\r\n/images/RK/2023/1/3.jpg', '', '', 'Новости', '/news.php', 0, '', ''),
(703, '16,13', 'День открытых дверей в \"Рассвете\"', '6 марта в \"Рассвете\" состоялся День открытых дверей, приуроченный к дню рождения организации. Гости мероприятия - Главное бюро МСЭ по Иркутской области - опубликовали материал об этом событии на сайте учреждения.', '<p>6 марта в \"Рассвете\" состоялся День открытых дверей, приуроченный к первому юбилею организации.</p>\r\n<p>Гостями мероприятия стали партнёры организации и коллеги из некоммерческого сектора, педагоги, врачи и специалисты государственных и частных учреждений.</p>\r\n<p>Спасибо всем, кто выкроил в своём загруженном графике время прийти в гости, задать важные вопросы, поделиться своим опытом и искренне пожелать успехов...</p>\r\n<p>Благодарим Главное бюро МСЭ по Иркутской области за высокую оценку и публикацию материала о \"Рассвете\" на сайте учреждения.</p>\r\n<p>Прочитать публикацию можно по <a href=\"https://www.38.gbmse.ru/news/#!pozdravlyaem_adaptacionno_pedagogicheskiy_centr_rassvet_s_pervym_pyatiletnim_yubileem\" target=\"_blank\">ССЫЛКЕ</a></p>\r\n', '/images/smi/29.jpg', '2023-03-10', '/images/team/Rodygina/49.jpg;\r\n/images/smi/06.03.2023/6.jpg;\r\n/images/smi/06.03.2023/5.jpg;\r\n/images/smi/06.03.2023/4.jpg;\r\n/images/smi/06.03.2023/3.jpg;\r\n/images/smi/06.03.2023/2.jpg;\r\n/images/smi/06.03.2023/1.jpg', '', '', 'Новости', 'news.php', 0, '', '0'),
(704, '13', 'Пять лет центру \"Рассвет\"', 'В начале марта наш центр отпраздновал своё 5-летие. О мероприятиях в рамках празднования и о том, как работа специалистов \"РАСсвета\" меняет жизнь людей с РАС написали региональные и федеральные СМИ.', '<p>В начале марта наш центр отпраздновал своё 5-летие. О мероприятиях в рамках празднования и о том, как работа специалистов \"РАСсвета\" меняет жизнь людей с РАС написали региональные и федеральные СМИ.</p>\r\n<p>Читайте публикации по ссылкам:</p>\r\n<p>Статья на сайте издания <a href=\"https://baikal24.ru/text/09-03-2023/027/\" target=\"_blank\">\"Байкал-24\"</a></p>\r\n<p>Статья на сайте <a href=\"https://glagol38.ru/text/09-03-2023/005\" target=\"_blank\">«Глагол. Иркутское обозрение»</a></p>\r\n<p>Статья на сайте <a href=\"https://irkutsk.news/novosti/2023-03-09/387644-v-17-raz-uvelichilos-kolichestvo-detei-s-autizmom-kotorym-pomogaet-rassvet-v-irkut.html\" target=\"_blank\">«Иркутск.NEWS»</a></p>\r\n<p>Статья на сайте <a href=\"https://irkutsk.bezformata.com/listnews/kotorim-pomogaet-rassvet-v-irkutskoy/115017497/\" target=\"_blank\">«BezFormata.Com»</a></p>\r\n<p>Статья на сайте <a href=\"https://www.asi.org.ru/report/2023/03/07/apcz-rassvet-otprazdnoval-pyatiletie-seminarom-dlya-roditelej-detej-s-osobennostyami-razvitiya/\" target=\"_blank\">«Агентство социальной информации»</a></p>', '/images/smi/30.jpg', '2023-03-13', '', '', '', 'Новости', 'news.php', 0, '', '0'),
(705, '3', '«Точка опоры»: обучение специалистов и родителей в Иркутске и Братске', 'В феврале и марте этого года специалисты «РАСсвета» провели серию обучающих мероприятий, посвящённых методам прикладного анализа поведения (ABA) и развитию инклюзивного образования в регионах. Обучение проходило при поддержке благотворительного фонда Владимира Потанина.', '<p>В феврале и марте этого года специалисты «РАСсвета» провели серию обучающих мероприятий, посвящённых методам прикладного анализа поведения (ABA) и развитию инклюзивного образования в регионах. Проведение обучения стало возможным благодаря победе в конкурсе «Точка опоры» благотворительной программы «Эффективная филантропия» Фонда Владимира Потанина».</p>\r\n<p>Обучение в Братске</p>\r\n<p>Поведенческие аналитики Вероника Артамонова и Елена Михеева организовали двухдневный тренинг в Братске, где более 30 участников познакомились с основами ABA и практическими методами введения карточек PECS — системы альтернативной коммуникации. Семинар поддержали Благотворительный фонд Владимира Потанина, АНО «Улыбка в сердце» и Благотворительный фонд «Единство».\r\n</p>\r\n<p>Мероприятия в Иркутске</p>\r\n<p>В марте в детском саду №100 «Берегиня» прошёл тренинг для педагогов, который провели куратор Елена Шапилова и учитель-логопед Екатерина Никулина. Участники узнали, как корректировать проблемное поведение детей, понимать причины их действий и мотивировать на занятия.</p>\r\n<p>Через неделю состоялся семинар «Развитие инклюзивного образования для детей с РАС», собравший около 100 участников. Среди выступавших были:\r\n</p>\r\n<p>- Ксения Афанасьева, исполнительный директор Ассоциации «Аутизм – Регионы»,</p>\r\n<p>- Екатерина Родыгина, учредитель и председатель «АПЦ Рассвет»,</p>\r\n<p> Елена Михеева, учредитель «РАСсвет».</p>\r\n<p>Семинар затронул важные темы, такие как права детей с РАС на инклюзивное образование, опыт реализации инклюзии в России и создание ресурсных классов в регионе.</p>\r\n<p>Эти мероприятия стали важным шагом в повышении квалификации специалистов и поддержке семей, воспитывающих детей с особыми потребностями.</p>', '/images/seminar/2023/3/1.jpg', '2023-03-13', '/images/seminar/2023/3/5.jpg;\r\n/images/seminar/2023/3/2.jpg;\r\n/images/seminar/2023/3/3.jpg;\r\n/images/seminar/2023/3/4.jpg;\r\n/images/seminar/2023/3/6.jpg', '', '', '', '', 0, '', ''),
(706, '16,19', 'Маленькие шаги к большим переменам', 'Специалисты «РАСсвета» постоянно участвуют в различных образовательных мероприятиях на разных площадках региона. Формируют положительное мнение о детях с РАС и их семьях, об АВА-методах обучения, продвигают тему инклюзивного образования и инклюзии в обществе.', '<p>Маленькими шагами к большим результатам – один из принципов АВА-терапии, который подходит к любой сфере жизни. Например, к развитию инклюзивного образования. Основные проблемы, которые перед нами сейчас стоят: нехватка АВА-специалистов и то, что их нет в государственных детских садах и школах, неготовность педагогического состава работать с детьми с ОВЗ, отсутствие в учебных заведениях специальных условий и материалов для детей с аутизмом, стигматизация темы аутизма в обществе и т.п. Как работать с таким огромным объемом задач, где брать ресурсы и с чего начинать? </p>\r\n<p>С самого доступного – информирования. Специалисты «РАСсвета» постоянно участвуют в различных образовательных мероприятиях на разных площадках региона. Формируют положительное мнение о детях с РАС и их семьях, об АВА-методах обучения, продвигают тему инклюзивного образования и инклюзии в обществе. В этом учебном году выступления педагогов проходили в рамках проекта \"Право на образование\" при поддержке Благотворительного фонда \"Абсолют-Помощь\".</p>\r\n<p>Семинар \"Организация образовательного процесса обучающихся с расстройствами аутистического спектра в соответствии с требованиям  федеральных государственных образовательных стандартов\" прошёл 21 октября 2025 года в онлайн-формате. Он был организован ГАУ ДПО Институт образования Иркутской области, чтобы педагоги Приангарья могли поделиться опытом работы с детьми с аутизмом, обсудить эффективные подходы к организации обучения учеников с РАС, проблемы и перспективы инклюзивного образования.</p>\r\n<p>Среди выступавших было 5 педагогов ресурсных классов и специалистов \"АПЦ РАСсвет\". Почему для нас было важно выступить на семинаре: наши педагоги смогли показать отличные результаты работы ресурсных классов, доказать, что инклюзия возможна с АВА-подходом, что дети с РАС обучаемы и успешны, если им предоставить специальные условия и профессиональную команду. </p>\r\n<p>Представители АНО \"АПЦ Рассвет\" были особо отмечены организаторами семинара в статье \"Об итогах проведения семинара для педагогов, работающих с обучающимися с РАС\", ознакомиться с которой можно по <a href=\"https://new.iro38.ru/archives/33332\" target=\"_blank\">ССЫЛКЕ</a></p>\r\n<p>В ноябре в Иркутске прошла в дистанционном формате VII Межрегиональная научно-практическая конференция для педагогов «Создание специальных условий образования для обучающихся с особыми образовательными потребностями в образовательном пространстве: результаты, опыт, перспективы». Вся суть мероприятия раскрывается в его длинном, но понятном названии. </p>\r\n<p>На конференции выступила с докладом о модели инклюзивного образования «Ресурсный класс» Елена Сергеевна Михеева, руководитель экспертного отдела «РАСсвета», педагог ресурсного класса общеобразовательной  школы поселка Молодежный. Поделилась опытом работы в первом в Иркутской области ресурсном классе для детей с аутизмом, который был открыт в 2020 году, рассказала, как ученики с особенностями развития встраиваются в обычную школьную жизнь. Спасибо организатору конференции – Педагогическому институту ИГУ за возможность быть услышанными, за стремление к новым знаниям и возможностям. </p>\r\n<p>В начале третьей учебной четверти педагоги ресурсного класса школы посёлка Молодежный Иркутского района выступили спикерами на заседании муниципальной ассоциации педагогов коррекционно-развивающего обучения образовательных организаций ИРМО. А в марте текущего года приняли участие в двух значимых образовательных мероприятиях: выступили спикерами Международной научно-практической конференции \"Инновации в работе с педагогами индивидуально обучения в современных условиях: результаты, опыт, перспективы\". Не менее значимым событием стала межведомственная региональная научно-практическая конференция \"Доступность получения образования для детей-инвалидов в Иркутской области\". Педагоги ресурсного класса вновь доказали свою готовность делиться знаниями и опытом, способствуя повышению доступности качественного образования для всех детей региона.</p>\r\n<p>Работа по развитию инклюзивного образования требует последовательных действий, начиная с информирования общественности и заканчивая созданием специализированных условий в учебных заведениях. Постепенное продвижение этих идей, вовлечение всех заинтересованных сторон и использование лучших практик позволяют сделать маленькие шаги, которые ведут к большим изменениям в образовательной системе.</p>\r\n<p>Выступления прошли при поддержке Благотворительного фонда \"Абсолют-Помощь\" в рамках проекта <a href=\"/news/detail.php?id=652&back=news\" target=\"_blank\"> \r\n\"Право на образование\"</a></p>', '/images/RK/2023/2/4.jpg', '2023-03-27', '/images/Absolut/logo.jpg;\r\n/images/RK/2023/2/1.jpg;\r\n/images/RK/2023/2/2.jpg;\r\n/images/RK/2023/2/3.jpg;\r\n/images/RK/2023/2/5.jpg;\r\n/images/RK/2023/2/6.jpg', '', '', 'Новости', '/news.php', 0, '', '5'),
(707, '18', 'Тёплый дом синего слона', 'Мы хотим, чтобы в наших инклюзивных мастерских \"Дом синего слона\" всем подопечным, педагогам и волонтёрам было комфортно, спокойно и радостно от встреч с друзьями. Поэтому наш новый проект называется \"Тёплый дом\".', '<p>Мы хотим, чтобы в наших инклюзивных мастерских \"Дом синего слона\" всем подопечным, педагогам и волонтёрам было комфортно, спокойно и радостно от встреч с друзьями. Поэтому наш новый проект называется \"Тёплый дом\".</p>\n<p>Вещи, которые ребята будут готовить в мастерских тоже про тепло: свечи, вязанная одежда, текстиль для дома и другие полезные и необходимые изделия. </p>\n<p>Новый проект откроет двери в \"дом\" для 15 подростков с расстройствами аутистического спектра. Конечно, занятия будут проходить с применением АВА-терапии, тестированием навыков, индивидуальных программ обучения, визуальной поддержкой, привлечением тьюторов... Ведь чтобы научить наших подопечных мастерству, нужно поработать с их поведением, коммуникацией и сенсорными особенностями. \n</p>\n<p>В проекте у нас запланированы различные мероприятия для взрослых и детей. </p>\n<p>Вчера, например, в рамках проекта мы провели экскурсию по мастерским для студентов педагогического института ИГУ. Теперь ждём их в качестве волонтеров на наших занятиях!</p>\n<p>Проект <a href=\"https://rassvet-apc.ru/news/detail.php?id=709&back=news\" target=\"_blank\">«Тёплый дом синего слона»</a> реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/DSS/2023/2/1.jpg', '2023-03-22', '/images/grants/pgrants_logo.jpg;\r\n/images/DSS/2023/2/4.jpg;\r\n/images/DSS/2023/2/2.jpg;\r\n/images/DSS/2023/2/3.jpg\r\n', '', '', 'Новости', '', 0, '', '2'),
(709, '10', 'Проект \"Теплый дом синего слона\"', 'Статус проекта: завершен<br>\r\nСроки реализации: 01.03.2023 – 31.08.2023<br>\r\nИсточник софинансирования: Фонд президентских грантов\r\n', '<p><b>Статус проекта:</b> завершен<br>\r\n<b>Сроки реализации:</b> 01.03.2023 – 31.08.2023<br>\r\n<b>Источник софинансирования:</b> Фонд президентских грантов</p>\r\n<p>Проект направлен на формирование допрофессиональных навыков у 15 подростков с РАС в мастерских путем развития выполнимых и востребованных направлений.</p>\r\n<p>Из наиболее успешных в реализации направлений выделены швейно-вязальная мастерская, изготовление свечей и мыловарение. То есть в мастерских планируется сделать акцент на продукцию, которая создает тепло и уют в доме, относится к товарам первой необходимости и доступна в изготовлении ребятами, имеющими сенсорные, коммуникативные и поведенческие нарушения.</p>\r\n<p> Для привлечения внимания к мастерским и выявления потенциальных мастеров и тьюторов в «Дом синего слона» пройдут вебинар для студентов, педагогов и родителей подростков с РАС и экскурсия по мастерской для студентов педагогического института с целью погружения в проект в качестве волонтеров.</p>\r\n<p> Итогом реализации проекта станет посещение БФ «Оберег», где люди, попавшие в трудную жизненную ситуацию, получат полезные подарки от наших воспитанников. А подростки с РАС внесут свой вклад в развитие общества.</p>\r\n<p><b>Цель проекта:</b></p>\r\n<p>Формирование допрофессиональных навыков у подростков с РАС путем снижения поведенческих преград к обучению и развития востребованных направлений мастерской \"Дом синего слона\" для последующего оказания помощи людям, находящимся в трудной жизненной ситуации.</p>\r\n<p><b>Задачи:</b></p>\r\n<p>1. Формирование допрофессиональных навыков у 15 подростков с РАС путем коррекции их поведения на регулярных занятиях в мастерских \"Дом синего слона\"</p>\r\n<p>2. Повышение качества изготавливаемой продукции в мастерских \"Дом синего слона\" путем развития компетенций педагогов и моторных навыков у 15 подростков с РАС</p>\r\n<p>3. Изготовление функциональных изделий подростками с РАС в мастерских \"Дом синего слона\" с целью оказания помощи людям, находящимся в трудной жизненной ситуации</p>\r\n<p><a href=\"https://rassvet-apc.ru/news.php?project=2\" class=\"button\">Публикации о реализации проекта</a></p>', '/images/DSS/2023/2/1.jpg', '2023-03-05', '', '', '', 'Гранты', '/pages/grants.php', 0, '', '0'),
(712, '10', 'Проект «Расширяя границы»', 'Статус проекта: завершен<br>\r\nСроки реализации: 01.03.2023 – 30.12.2023<br>\r\nИсточник софинансирования: Департамент здравоохранения и социальной помощи населению комитета по социальной политике и культуре администрации города Иркутска\r\n', '<p><b>Статус проекта:</b> завершен<br>\r\n<b>Сроки реализации:</b> 01.03.2023 – 30.12.2023<br>\r\n<b>Источник софинансирования:</b> Департамент здравоохранения и социальной помощи населению комитета по социальной политике и культуре администрации города Иркутска</p>\r\n<p>Проект направлен на адаптацию 15 детей ОВЗ при посещении общественных мест посредством индивидуальных и групповых занятий с использованием методов с научно-доказательной эффективностью. В индивидуальном формате у детей отрабатываются навыки спокойного нахождения в кресле, ожидания своей очереди, формирования адекватной реакции на внешние раздражители (шум, скопление людей, темный зал кинотеатра и театра). В группе у детей обобщатся навыки, изучаемые параллельно в индивидуальном форме с педагогом, разовьется терпимость при нахождении рядом друг с другом без демонстрации социально неприемлемого поведения. Все освоенные навыки будут закрепляться на творческих мастер-классах в мастерских “Дом синего слона” и выездных массовых мероприятиях.</p>\r\n<p><b>Цель проекта:</b></p>\r\n<p>Социальная поддержка проживающих на территории г. Иркутска детей-инвалидов, детей из семей инвалидов, лиц с ограниченными возможностями здоровья (ОВЗ), в том числе детей и подростков с РАС, путем комплексного регулярного обучения, адаптации в окружающем пространстве с учетом их особенностей, формирования социальных навыков посредством обучения посещения общественных мест и мероприятий в г. Иркутске.</p>\r\n<p><b>Задачи:</b></p>\r\n<p>1. Создание оптимальных условий в общественных местах для детей с ОВЗ с учетом их индивидуальных потребностей.</p>\r\n<p>2. Формирование у детей с ОВЗ приемлемого поведения в общественных местах путем предварительной подготовки и индивидуального подхода к каждому ребенку.</p>\r\n<p>3. Включение при планировании посещения любого мероприятия детей с нормальным развитием в качестве обязательных участников мероприятия.</p>\r\n<p><a href=\"https://rassvet-apc.ru/news.php?project=3\" class=\"button\">Публикации о реализации проекта</a></p>', '/images/dosug/2022/2/1.jpg', '2023-03-06', '', '', '', 'Гранты', '/pages/grants.php', 0, '', '0'),
(714, '20', 'Рекомендации по прохождению ПМПК', 'На одной из встреч группы психологической поддержки для родителей детей с РАС начальник экспертного отдела Елена Михеева рассказала родителям, почему так важно пройти ПМПК  перед школой, и какие трудности возникают в Иркутске на ПМПК с детьми с РАС. ', '<p>На одной из встреч группы психологической поддержки для родителей детей с РАС начальник экспертного отдела Елена Михеева рассказала родителям, почему так важно пройти ПМПК  перед школой, и какие трудности возникают в Иркутске на ПМПК с детьми с РАС. </p>\r\n</p>\r\n<p>«Психологомедико-педагогическую комиссию (ПМПК) важно пройти перед школой для того, чтобы прописать особые образовательные условия для ребенка. \r\n</p>\r\n<p>Что это дает:</p>\r\n<p>- адаптированную образовательную программу согласно образовательному маршруту ребенка.\r\n</p>\r\n<p>- сопровождение обучения ребенка узкими специалистами (логопед, дефектолог, психолог) и, если необходимо, то какими-то техническими средствами (альтернативная система коммуникации, коммуникативные планшеты и т.д.)</p>\r\n<p>- адаптацию учебного материала</p>\r\n<p>- тьюторское сопровождение. Тьютор является ключевой единицей в обучении детей с РАС, потому что часть образовательной программы ребенок проходит один на один с тьютором. </p>\r\n<p>Трудность прохождения ПМПК в том, что детям с РАС достаточно сложно вступать в взаимодействие с незнакомыми взрослыми. А это необходимо, чтобы показать специалистам умения и навыки, которые есть у ребенка. Поэтому я всегда рекомендую родителям брать с собой видео, на которых ребенок демонстрирует те или иные навыки, чтобы члены комиссии смогли убедиться, что ребенок действительно это знает и умеет.\"</p>\r\n<p></p>\r\n<p></p>\r\n<p>Проект <a href=\"/news/detail.php?id=656&back=news\" target=\"_blank\">«Передышка для мамы»</a> реализован с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n', '/images/respite/9/1.jpg', '2023-03-28', '', '', '', 'Новости', '/news.php', 0, '', '4'),
(716, '18', '\"Неделя неформального образования\"', 'На днях в мастерской \"Дом Синего Слона\" прошла серия мастер-классов в рамках \"Недели неформального образования\". Для нас участие в  масштабном обучающем городском проекте - добрая традиция.', '<p>На днях в мастерской \"Дом Синего Слона\" прошла серия мастер-классов в рамках \"Недели неформального образования\". Для нас участие в  масштабном обучающем городском проекте (более 600 образовательных занятий в Иркутске) - добрая традиция. </p>\r\n<p>В этом году в наших мастер-классах поучаствовало 17 ребят с ОВЗ. Дети изготовили красочные панно с использованием текстурной пасты. </p>\r\n<p>С этим материалом работать очень интересно и приятно, процесс затягивает, а текстурные рисунки получаются неповторимыми. </p>\r\n<p>Работы получились интересные, не только внешне, но и на ощупь. Все участники мастер-классов остались довольны результатом, а это самое главное.</p>\r\n<p>Мероприятия состоялись в рамках проекта <a href=\"https://rassvet-apc.ru/news/detail.php?id=712&back=news\" target=\"_blank\">«Расширяя границы»</a> при поддержке Департамента здравоохранения и социальной помощи населению комитета по социальной политике и культуре администрации г. Иркутска.</p>', '/images/DSS/2023/3/5.jpg', '2023-03-30', '/images/grants/logo_irkutsk.png;\r\n/images/DSS/2023/3/1.jpg;\r\n/images/DSS/2023/3/2.jpg;\r\n/images/DSS/2023/3/3.jpg;\r\n/images/DSS/2023/3/4.jpg;\r\n/images/DSS/2023/3/6.jpg;\r\n/images/DSS/2023/3/7.jpg;\r\n/images/DSS/2023/3/8.jpg;\r\n/images/DSS/2023/3/9.jpg;\r\n/images/DSS/2023/3/10.jpg;\r\n/images/DSS/2023/3/11.jpg\r\n', '', '', 'Новости', '', 0, '', '3'),
(721, '18', 'Мастер-класс для школьников \r\n', 'Креативно мы подошли к апрельской акции  информирования о проблемах аутизма - устроили инклюзивный мастер-класс в школе посёлка Молодежный Иркутского района. \r\nВместе с учениками ресурсного класса отправились в третий регулярный класс. ', '<p>Креативно мы подошли к апрельской акции  информирования о проблемах аутизма - устроили инклюзивный мастер-класс в школе посёлка Молодежный Иркутского района. </p>\r\n<p>Вместе с учениками ресурсного класса и наших инклюзивных мастерских \"Дом синего слона\" отправились в третий регулярный класс. Искали тему простую в исполнении и при этом интересную для ребят. И не прогадали, когда выбрали необычное мороженое - из воздушного пластилина. Этот материал мы очень любим на занятиях арт-терапией и педагоги сразу же решили, что ребятам должна понравиться такая работа. И ведь действительно, учеников было не оторвать от процесса, подошли к этому занятию с большим интересом. </p>\r\n<p>Ребята из ресурсного класса \"чувствовали себя, как дома\" и хорошо влились в коллектив. Сразу видна проделанная работа тьюторов и педагогов, а ведь несколько лет назад родители многих из наших ребят и подумать не могли, что их дети смогут учиться наравне со сверстниками в обычной школе. </p>\r\n<p>Спасибо за теплый прием, мы обязательно придем к ребятам еще уже с новыми идеями. </p>\r\n<p>Мероприятие прошло в рамках проекта Ассоциация \"Аутизм Регионы\"  «Культурный код - возможность для каждого 2.0: развитие сети творческих досуговых центров для подростков и молодых людей с РАС» реализуется при поддержке фонда \"Выход\" с использованием гранта Президента Российской Федерации на реализацию проектов в области культуры, искусства и творческих индустрий Президентский фонд культурных инициатив  </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '/images/DSS/2023/5/0.jpg', '2023-04-10', '/images/grants/FKI.png;\r\n/images/DSS/2023/5/1.jpg;\r\n/images/DSS/2023/5/2.jpg;\r\n/images/DSS/2023/5/3.jpg;\r\n/images/DSS/2023/5/4.jpg;\r\n/images/DSS/2023/5/5.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', '19');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(722, '4', 'Как Юра начал говорить', 'В марте АВА-интенсив в \"РАСсвете\" проходил Юра. В свои 5 лет мальчик не произнёс ни одного слова. Эхо и запуск речи - с таким запросом обратилась к нам мама Юры Наталья. В итоге он научился просить словами и называть несколько предметов', '<p>В марте АВА-интенсив в \"РАСсвете\" проходил Юра. В свои 5 лет мальчик не произнёс ни одного слова. Эхо и запуск речи - с таким запросом обратилась к нам мама Юры Наталья.</p>\r\n<p>- Развивать речь мы решили через навык моторной имитации. Важно было научить Юру повторять за педагогами движения, в том числе движения губ. Когда он преуспел в этом, мы перешли к повтору звуков - эхо. Юра не только смог повторять за АВА-инструкторами звуки, но и слоги. В итоге он научился просить словами и называть несколько предметов, - рассказала Анастасия Шваркунова, поведенческий специалист, куратор \"РАСсвета\".</p>\r\n<p>\"Машина\", \"пена\", \"собака\" - первым словам Юры радовались все сотрудники центра! Это была настоящая победа, причём коллективная. Мама Юры регулярно и много занималась с сыном по предложенной программе дома: обобщала навыки, повторяла и закрепляла материал без устали. Мы гордимся Натальей не меньше, чем Юрой!</p>\r\n<p>Занятия с Юрой проходят в рамках проекта «Передышка для мамы», проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<section class=\"video-detail\">\r\n								<iframe src=\"https://rutube.ru/play/embed/8605825decfa97d51e071a5ae47c5c9a/\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen class=\"social-video\"></iframe>\r\n							</section>', '/images/respite/3.jpg', '2023-04-10', '/images/grants/pgrants_logo.jpg\r\n\r\n\r\n', '', '', 'Новости', '/news.php', 0, '', '4'),
(723, '13,18', 'Об инклюзивных мастерских на радио \"Маяк. Иркутск\"', 'Слушайте подкаст радио «МАЯК. Иркутск», программа \"Антистресс\", где специалисты \"РАСсвета\" рассказывают об инклюзивных мастерских \"Дом синего слона\".\r\n', '<p>Зачем учить детей с РАС шить на машинке, работать с прессом, варить свечи и заливать бетонные кашпо?</p>\r\n<p>Всё вышеперечисленное кажется на первый взгляд опасным и трудновыполнимым для ребёнка с особенностями развития... \r\n</p>\r\n<p>Но смысл и надежда на лучшее будущее в этом есть. Слушайте подкаст радио «МАЯК. Иркутск», <a href=\"https://vk.com/wall-211913602_370\" target=\"_blank\"><b>программа \"Антистресс\"</b></a>, где специалисты \"РАСсвета\" рассказывают об инклюзивных мастерских \"Дом синего слона\".</p>\r\n<p>Проект \"Тёплый дом синего слона\" реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/smi/32.jpg', '2023-04-12', '/images/grants/pgrants_logo.jpg', '', '', 'Новости', 'news.php', 0, '', '2'),
(725, '16', 'Очный тренинг в рамках сертифицированного курса для специалистов на базе \"Рассвета\"', 'В апреле наш центр впервые стал базой для проведения очных тренингов в рамках сертифицированного курса для поведенческих специалистов. Спасибо команде проекта \"Аутизм: коррекционная работа АВА\" и Оксане Строгановой за интересные и познавательные тренинги. ', '<p>В марте 10 специалистов \"Рассвета\" прошли обучение в сфере прикладного анализа поведения и сдали экзамены - повысили свои компетенции и прокачали скилы. </p>\r\n<p>А уже апреле наш центр впервые стал базой для проведения очных тренингов по прикладному анализу поведения в рамках сертифицированного курса для поведенческих специалистов. </p>\r\n<p>Было очень интересно и познавательно поработать с новыми методами и техниками по применению экспериментальной процедуры функционального анализа, разобрать кейсы с непосредственным участием супервизора Оксаны Строгоновой. \r\n</p>\r\n<p>Первые пару дней тренинг проходил для тех, кто окончил 3 модуль курса сертификационной программы для специалистов по ПАП. Затем началось обучение для тех, кто окончил 1 модуль курса. Мы сразу же применяли наши знания на практике, работая с детьми. </p>\r\n<p>Вот, что говорит о прошедших тренингах Елена Михеева, начальник экспертного отдела \"РАСсвета\":</p>\r\n<p>- Во время тренинга мы на практике проводили интервью с родителями, наблюдали за ребёнком, выбирали поведение и собирали данные о его частоте в разных средах, тем самым определяя функцию этого нежелательного поведения. По окончанию сбора и анализа полученных данных в процессе эксперимента, мы составили график и план коррекции данного нежелательного поведения.\r\n</p>\r\n<p>Также, мы имели возможность работать в команде и узнать друг о друге больше. Это было очень полезно для нашего профессионального и личностного роста и развития навыков работы в коллективе.</p>\r\n<p>Мы очень довольны результатами и уверены, что теперь обладаем новыми и полезными навыками.</p>\r\n<p>Спасибо команде проекта \"Аутизм: коррекционная работа АВА\" и Оксане Строгановой за интересные и познавательные тренинги.</p>\r\n<p>Обучение прошло в рамках проекта «Профессия помогать» при поддержке Благотворительного фонда Владимира Потанина.</p>', '/images/training/2023/1/1.jpg', '2023-04-12', '/images/training/2023/1/3.jpg;\r\n/images/training/2023/1/4.jpg;\r\n/images/training/2023/1/5.jpg;\r\n/images/training/2023/1/2.jpg', '', '', '', '', 0, '', '20'),
(727, '19,5', 'Информационная кампания ', 'В апреле проходит традиционная ежегодная информационная компания, посвящённая теме аутизма. СМИ также обсуждают диагностику РАС, меры медицинской поддержки для людей с аутизмом, инклюзивное образование...', '<p>В апреле проходит традиционная ежегодная информационная компания, посвящённая теме аутизма. У нас есть отличные результаты! </p>\r\n<p>500 школьников из Иркутского области и Забайкалького края стали участниками уроков доброты - занятий по толерантному отношению к людям с аутизмом. </p>\r\n<p>На протяжении этого учебного года в общеобразовательных школах поселков Луговой, Молодежный, Пивовариха и школы №35 г. Читы проходят необычные уроки, на которых рассказываются об особенностях поведения людей с РАС, о том, как общаться с ними, какую помощь можно им предложить. </p>\r\n<p>Уроки проводят педагоги ресурсных классов, где обучаются ученики с РАС. \r\nДля наглядности детям раздают брошюры с социальной историей о дружбе между двумя мальчиками, у одного из которых аутизм. Историю придумали и нарисовали в нашем центре специально для уроков доброты. </p>\r\n<p>Также педагоги приглашают учеников посетить ресурсные классы и пообщаться с их учениками. Такие «мини-экскурсии» проходят регулярно, на них дети с аутизмом и их нейротипичные сверстники вместе играют, заводят друзей. \r\n</p>\r\n<p>- Мы проводим уроки доброты в начальном и среднем звене. Ученики реагируют на информацию положительно, многие задают вопросы, рассказывают о своих знакомых детях, которые тоже ведут себя необычно. Главное послание уроков – мы все разные, и если кто-то отличается от тебя, выделяется своим поведением, не нужно боятся. Каждый может быть полезным обществу, развиваясь в своем темпе, - говорит Елена Михеева, учитель-дефектолог школы поселка Молодежный, начальник экспертного отдела «АПЦ РАСсвет».\r\n</p>\r\n<p>Наши друзья и партнеры из Благотворительного фонда «Абсолют-Помощь» рассказывают об уроках доброты на тему расстройств аутистического спектра, прошедших в Иркутской области и Забайкальском крае в своих социальных сетях <a href=\"https://vk.com/wall-128651156_3240\" target=\"_blank\">ЧИТАТЬ</a></p>\r\n<p>Уроки доброты проходят в рамках проекта \"Право на образование\" при поддержке Благотворительного фонда \"Абсолют-Помощь\"</p>\r\n<p>СМИ также обсуждают диагностику РАС, меры медицинской поддержки для людей с аутизмом, инклюзивное образование. Мы подготовили информацию для региональных СМИ о работе педагогов ресурсных классов и проведении уроков доброты.</p>\r\n<p>Подробнее можно почитать по ссылкам:</p>\r\n<p>Статья на сайте издания <a href=\"https://baikal24.ru/text/29-03-2023/052/\" target=\"_blank\">\"Байкал-24\"</a></p>\r\n<p>Статья на сайте <a href=\"https://glagol38.ru/text/29-03-2023/009\" target=\"_blank\">«Глагол. Иркутское обозрение»</a></p>\r\n<p>Статья на сайте издания <a href=\"https://www.ogirk.ru/2023/03/29/shkolnikov-irkutskoj-oblasti-uchat-ponimat-sverstnikov-s-autizmom/\" target=\"_blank\">«Областная газета»</a></p>\r\n<p>Статья на сайте <a href=\"https://news.myseldon.com/ru/news/index/281048799\" target=\"_blank\">«Seldon.News»</a></p>', '/images/smi/31.jpg', '2023-04-20', '/images/Absolut/logo.jpg;\r\n/images/smi/31.jpg;\r\nhttps://baikal24.ru/public/images/upload/image1680075414441_i1.jpg;\r\n/images/DSS/2023/5/3.jpg;\r\nhttps://sun9-77.userapi.com/impg/b8x_SL2QpVPxzWAOktn8ip2dhNmOwazUM6PLIw/QRsV56PqeLE.jpg?size=1280x853&quality=95&sign=3f650b52363e63e89f11ffe63259e7ff&type=album\r\n', '', '', 'Новости', 'news.php', 0, '', '5'),
(728, '4', 'Тестирование базовых навыков', 'Чтобы составить для ребенка с РАС индивидуальную программу занятий, нужно сначала протестировать его, понять, чему его нужно учить в первую очередь. Ребенку предлагают различные задания, игры и наблюдают, как он с ними справляется – где нужна поддержка, а где учить с нуля. ', '<p>Чтобы составить для ребенка с РАС индивидуальную программу занятий, нужно сначала протестировать его, понять, чему его нужно учить в первую очередь. Ребенку предлагают различные задания, игры и наблюдают, как он с ними справляется – где нужна поддержка, а где учить с нуля. </p>\r\n<p>Перед началом тестирования навыков педагогу необходимо наладить контакт с ребёнком — это важный этап, известный как «руководящий контроль». Для маленьких детей возрастом 1,5–3 года этот процесс может занять от 2 до 8 часов, поскольку малыши часто сильно привязаны к родителям. Чтобы ускорить установление доверия, используются специальные мотивационные игрушки и могут привлекаться родители.</p>\r\n<p>В ходе начальных занятий ребёнку предлагаются лёгкие задания и игры, которые позволяют оценить уровень текущих навыков. Важно наблюдать за тем, как малыш взаимодействует с заданиями, какие действия вызывает затруднения, а какие выполняются легко. Все результаты заносятся в таблицы для дальнейшего анализа.</p>\r\n<p>Особое внимание уделяется способностям ребёнка общаться и просить помощь. Педагог отслеживает, может ли ученик обращаться не только к маме, но и к другим людям, включая взрослых специалистов. </p>\r\n<p>Если ребёнок сталкивается с трудностями выполнения определённых задач, например, не может положить один предмет на другой, несмотря на понимание задания, процесс тестирования временно прекращают. Вместо этого начинают работу над формированием недостающих базовых навыков, таких как умение складывать предметы друг на друга. После освоения этой основы продолжают основное тестирование.</p>\r\n<p>Тестирование в АВА-центре отличаются от стандартных тестов в детских садах или школах. Задания имеют иной формат и структуру, что позволяет глубже оценить способности ребёнка. Поскольку некоторые дети изначально могут не понимать суть вопросов, специалисты показывают и объясняют требуемые действия доступным способом, помогая ребёнку освоиться.</p>\r\n<p>Поговорим более подробно о тестировании навыка имитации.</p>\r\n<p>Если посмотреть таблицу уровней VB-MAPP (инструмент для оценки навыков речи и социального взаимодействия у детей с аутизмом и другими нарушениями), то на ней наглядно видно, что имитация присутствует только на 2 уровнях развития, но ее вклад в развитие ребенка гораздо масштабнее.</p>\r\n<p>Имитация - это фундамент развития вербального поведения ребенка. С помощью нее запускается речь, усложняются фразовые конструкции ребенка, развиваются навыки визуального восприятия и социального взаимодействия, а еще имитация способствует развитию понимания нового вида подсказок (очень нужный навык, если ребенок зависим от физических подсказок и ждет, когда взрослый начнет выполнять задание за него).</p>\r\n<p>&#8265; Какие преграды могут встать на пути развития имитативного поведения ребенка?\r\n</p>\r\n<p>1. Ребенок не сотрудничает. Просьбы взрослого о занятии воспринимаются в штыки или игнорируются, даже если это простая короткая инструкция , на выполнение которой требуется 1 секунда времени.</p>\r\n<p>2. Проблемы с совместным вниманием - ребенок не смотрит на модель имитации. Просим повторить похлопать в ладоши вслед за взрослым, а ребенок отворачивается или смотрит в сторону.</p>\r\n<p>3. У ребенка есть моторные сложности к имитации. Например, ребенку тяжело или невозможно совершать действия руками.</p>\r\n<p>❗ Над каждой из этих проблем можно и нужно работать. Поведенческие аналитики прописывают индивидуальные протоколы и помогают детям справиться с преградами.</p>\r\n<p>Навыки имитации:</p>\r\n<p>&#9461; уровень</p>\r\n<p>- Ребенок имитирует 20 движений моторики любого типа (имитация с предметом, крупная моторика, мелкая моторика)</p>\r\n<p>- Может спонтанно имитировать поведение других людей в 5 различных ситуациях)\r\n</p>\r\n<p>&#9462; уровень</p>\r\n<p>- Может имитировать действия с выбором предметов (ребенок выбирает барабанные палочки из набора, в котором также присутствует колокольчик и молоток, и имитирует игру взрослого на барабане)</p>\r\n<p>- Имитирует 20 различных действий мелкой моторики (класс, фигушка, коза)</p>\r\n<p>- Имитирует 10 различных трехкомпонентных последовательностей действий после подсказки \"Сделай так\" (хлопает в ладоши, потом показывает нос и подпрыгивает; наливает воду из чайника в кружку, мешает ложечкой \"чай\" и пьет его)</p>\r\n<p>- Спонтанно имитирует 5 функциональных навыков в естественной среде (кушает ложкой, снимает ботинки, вытирает стол)</p>\r\n<p>- Без предварительного обучения имитирует (или пытается) какое-либо новое моторное движение взрослого</p>\r\n<p>&#9989; Когда мы проводим тестирование навыков ребенка, мы обязательно проверяем имитативные умения. Если они есть - круто, двигаемся дальше! А если где-то есть пробел, обязательно его восполняем, чтобы ребенок не испытывал трудностей в развитии других навыков.</p>\r\n<p>Проект «Передышка для мамы» реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n', '/images/respite/10/1.jpg', '2023-04-21', '/images/grants/pgrants_logo.jpg;\r\n/images/respite/10/1.jpg;\r\n/images/respite/10/2.jpg;\r\n/images/respite/10/3.jpg\r\n\r\n\r\n', '', '', 'Новости', '/news.php', 0, '', '4'),
(730, '18', 'Тестирование участников нового проекта', 'Чтобы понимать, где нашим подмастерьям требуются подсказки, дополнительные меры безопасности (те же противошумные наушники), к каким видам работ у детей лежит душа, мы проводим тестирование и анкетирование для каждого ребёнка.', '<p>Занятия в инклюзивных мастерских \"Дом синего слона\" пользуются успехом у наших подопечных. Иногда ребятам бывает сложно выполнить весь объем работ, иногда не хватает терпения...Тогда мы берём перерыв и возвращаемся к изделиям с новыми силами и вдохновением.</p>\r\n<p>Чтобы понимать, где нашим подмастерьям требуются подсказки, дополнительные меры безопасности (те же противошумные наушники), к каким видам работ у детей лежит душа, мы проводим тестирование и анкетирование для каждого ребёнка.\r\n</p>\r\n<p>Проходят они так: родители рассказывают о ключевых моментах поведения своих детей, педагоги мастерской предлагают ребятам различные материалы на нескольких первых занятиях - выявляют их сенсорные особенности, дефициты развития. Вся эта информация фиксируется, на её основе определяются направления и цели, над которыми нужно поработать на регулярных занятиях в \"Доме синего слона\".</p>\r\n<p>В апреле тестирование прошли участники нашего нового проекта \"Тёплый дом\". Радуемся пополнению подмастерьев и желаем им творческих успехов! </p>\r\n<p></p>\r\n<p>Проект \"Тёплый дом синего слона\" реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/DSS/2023/7/1.jpg', '2023-04-22', '/images/grants/pgrants_logo.jpg;\r\n/images/DSS/2023/7/1.jpg;\r\n/images/DSS/2023/7/2.jpg', '', '', 'Тёплый дом', '/projects/warm_house.php', 0, '', '2'),
(734, '18', 'Индивидуальные занятия в инклюзивных мастерских', 'В индивидуальном формате педагог и ученик могут тщательно проработать весь алгоритм действий по изготовлению изделия, повторяя и закрепляя \"сложные моменты\" - то, с чем ребенку сложно справиться самостоятельно.', '<p>Индивидуальные занятия в инклюзивных мастерских \"Дом синего слона\" - это комфортные и безопасные условия работы для наших подопечных.\r\n</p>\r\n<p>В индивидуальном формате педагог и ученик могут тщательно проработать весь алгоритм действий по изготовлению изделия, повторяя и закрепляя \"сложные моменты\" - то, с чем ребенку сложно справиться самостоятельно.\r\n</p>\r\n<p>Также в таком формате уделяется особое внимание  сенсорным и поведенческим особенностям ученика. С помощью материалов и специальных техник ребёнок насыщает сенсорные зоны, например, месит глину, смешивает краски. А педагог работает над коррекцией некоторых проблем в поведении ученика.</p>\r\n<p>На фото наши девочки: Марина и Саша на индивидуальных занятиях готовят мыло. Результатами поделимся чуть позже. </p>\r\n<p>Занятия проходят в рамках проекта \"Тёплый дом синего слона\". Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/DSS/2023/9/1.jpg', '2023-04-27', '/images/grants/pgrants_logo.jpg;\r\n/images/DSS/2023/9/1.jpg;\r\n/images/DSS/2023/9/2.jpg', '', '', 'Тёплый дом', '/projects/warm_house.php', 0, '', '2'),
(736, '10', 'Проект \"Профессия помогать\"', 'Статус проекта: завершен<br>\r\nСроки реализации: 09.01.2023​ - 09.06.2023<br>\r\nИсточник софинансирования: Благотворительный фонд Владимира Потанина\r\n', '<p><b>Статус проекта:</b> завершен<br>\r\n<b>Сроки реализации:</b> 09.01.2023​ - 09.06.2023<br>\r\n<b>Источник софинансирования:</b> Благотворительный фонд Владимира Потанина</p>\r\n<p><b>Цель проекта:</b></p>\r\n<p>Создать эффективную абилитацию детей с РАС в СФО для поддержания их здоровья, успешной социальной адаптации, самореализации и интеграции в общество путем повышения 16 педагогам АНО «АПЦ Рассвет» профессиональных компетенций в научно-доказанных методах обучения на основе международных стандартов профессиональной практики.</p>\r\n<p><b>Задачи:</b></p>\r\n<p>1. Обучение специалистов на специализированных курсах и семинарах по ПАП для повышения теоретических знаний о научно-обоснованных методах обучения. </p>\r\n<p>2. Обучение специалистов на практических тренингах и встречах для апробации на практике изученных на специализированных курсах и семинарах научно-обоснованных методов обучения.</p>\r\n<p>3. Применение научно-обоснованных методов обучения на обучающих занятиях с детьми с РАС под супервизией сертифицированного поведенческого аналитика.</p>\r\n<p><b>Целевая аудитория проекта:</b></p>\r\n<p>Психологи, логопеды, олигофренопедагоги, АВА-инструкторы педагогического отдела АНО «АПЦ Рассвет» в возрасте от 21 до 45 лет, имеющие стаж работы не менее 6 месяцев в организации, с высшим образованием, желающие повышать компетенции для работы с поведением и навыками детей с РАС с использованием эффективных научно-обоснованных коррекционно-педагогических методов и оказывающие широкий спектр педагогической помощи: инклюзивное образование, ранняя поддержка, социализация, психологическая поддержка и информирование об аутизме.  Развитие компетенций специалистов обеспечивает поддержание здоровья, успешную социальную адаптацию, самореализацию и интеграцию в общество основной целевой аудитории - детей с РАС, а также обучает родителей и специальных педагогов навыкам абилитации детей с РАС.</p>\r\n\r\n<p>Педагоги АНО \"АПЦ РАСсвет\" пройдут онлайн обучение на дистанционных курсах и семинарах по прикладному анализу поведения (ПАП). Каждый педагог проходит обучение в соответствии с его профессиональным уровнем в области ПАП: 5 человек на 1 модуле, 2 человека на 2 модуле, 1 - на 3 модуле, 2 - на 4 модуле, 2 специалиста повышают квалификацию на курсах по методам обучения детей в школе. В последующем специалисты проходят 3 практических тренинга для апробации на практике методов, изученных на курсах в теории: 1 тренинг дистанционно и 2 тренинга в очном формате. В очных тренингах участвуют дети с РАС, получающие помощь в АНО, по 1 ребенку на 4-5 педагогов. На тренинге специалисты скорректируют ошибки и получат обратную связь по работе с детьми, с которыми им предстоит заниматься в дальнейшем.</p>\r\n<p>Параллельно с обучением специалисты изучат профессиональную литературу и раз в 2 недели собираются для анализа прочитанного и разбора возникающих в ходе занятий с детьми с РАС вопросов на встречах «Читаем Купера». 5 кураторов центра пройдут 2 практикума супервизий для углубления знаний в прикладном анализе поведения и в последующем идут на четвертый практический тренинг дистанционно, где собирают данные по работе с младшим педагогическим персоналом АНО и под руководством эксперта научатся проводить визуальный анализ, определять обязанности персонала, разрабатывать алгоритмы задач по протоколам, проводить оценку профессиональных навыков, обучать персонал и собирать обратную связь. Курсы повышения квалификации обучат практическим приемам необходимым для формирования у детей с РАС учебного поведения, обучению письму, чтению, работе с текстом, математическим навыкам, работе в групповом формате в 1-4 классах. Семинары углубят навыки в развитии социально-бытовых навыков у детей с РАС, а встречи «Читаем Купера» позволят сразу же применять знания на практике. И в Иркутской области появятся первые кураторы MBP, которые могут работать без привлечения внешних экспертов.</p>\r\n<p><a href=\"https://rassvet-apc.ru/news.php?project=20\" class=\"button\">Публикации о реализации проекта</a></p>', '/images/training/2023/1/1.jpg', '2023-01-25', '', '', '', 'Гранты', '/pages/grants.php', 0, '', '0'),
(737, '19', 'Поддержка ресурсного класса в Забайкалье', 'В этом учебном году при поддержке \"РАСсвета\" был создан первый в Забайкалье ресурсный класс для 5 первоклашек с аутизмом. Благодаря поддержке Благотворительного фонда «Абсолют-Помощь» \"Рассвет\" имеет возможность делиться своими знаниями с коллегами из Читы.', '<p>Недавно мы вспоминали, как непросто было открыть первый ресурсный класс в Приангарье. Никто не знал точно, как его организовать, где найти квалифицированных педагогов, сколько это будет стоить... \r\nРуководитель \"РАСсвета\" Екатерина Родыгина ездила в Новосибирск, чтобы перенять опыт организации РК у наших коллег из Ассоциации \"Аутизм Регионы\".</p>\r\n<p>Теперь, после открытия трех ресурсных классов в Иркутске и Иркутском районе, мы перешли в разряд экспертов по инклюзивному образованию. В сентябре при поддержке \"РАСсвета\" был создан первый в Забайкалье ресурсный класс для 5 первоклашек с аутизмом. Мы рады делиться своими знаниями с активными, целеустремленными родителями и педагогами.</p>\r\n<p>Первая командировка специалистов \"Рассвета\" в Читу состоялась в октябре прошлого года. Елена Михеева и Елена Шапилова помогли коллегам из Читы разработать индивидуальные программы развития учеников по итогам первого тестирования, провели тренинг по методам обучения и системе альтернативной коммуникации PECS, дали рекомендации как работать в групповом формате с учениками, у которых отличаются уровни освоения навыков. Все рекомендации были подкреплены практическими примерами.</p>\r\n<p>Вторая командировка кураторов \"РАСсвета\" Вероники Артамоновой и Елены Михеевой состоялась в конце апреля. </p>\r\n<p>За пару дней поездки удалось многое успеть: обсудили вопросы инклюзивного образования, протоколы обучения и сбор данных в чек-листах, вновь провели практические тренинги. </p>\r\n<p>Елена Михеева рассказала о поездке в Читу:</p>\r\n<p>- У всех обучающихся в классе улучшились навыки коммуникации, поведения в группе и социальное взаимодействие, что подтверждает результаты тестирования. Это отличный результат, который в очередной раз подтверждает, что инклюзивная модель обучения ресурсный класс действительно улучшает коммуникацию у детей с  РАС и поведенческие специалисты играют не маловажную роль в этом. \r\n</p>\r\n<p>Педагоги ресурсного класса читинской школы №6, в свою очередь, активно делятся опытом обучения детей с РАС по методикам прикладного анализа поведения. Задать вопрос, посмотреть урок, пообщаться с учениками класса можно буквально каждый будний день! Причем не только коллегам из школы, но и студентам педагогического направления, частным педагогам, воспитателям, родителям детей с аутизмом любого возраста.</p>\r\n<p>За 8 месяцев существования ресурсного класса в Чите, его посетили более 150 человек!\r\nНедавно в гости приезжали специалисты Центра для коррекции заболеваний и обучения социальным навыкам детей с аутизмом из поселка Агинское. Возможно, в скором будущем там откроется свой ресурсный класс!</p>\r\n<p>О важности таких встреч рассказала Елена Федосеева, куратор ресурсного класса, директор АНО помощи людям с РАС «Мой новый мир»: \"Администрация школы посещает уроки в ресурсном классе и отмечает динамику развития нашего общего проекта, вместе с нами радуются позитивному опыту педагогов и успехам детей. А благодаря педагогам регулярных классов, сегодня к нашим РАСпрекрасным детям сверстники относятся намного проще и в то же время заинтересованнее. Школьники приходят к нам в класс, чтобы поиграть, позаниматься и пообщаться. Это пример обратной инклюзии.\"</p>\r\n<p></p>\r\n<p>Проект \"Право на образование\" осуществляется при поддержке Благотворительного фонда \"Абсолют-Помощь\".</p>\r\n\r\n\r\n\r\n\r\n\r\n', '/images/RK/2023/6/1.jpg', '2023-05-02', '/images/Absolut/logo.jpg;\r\n/images/RK/2023/6/1.jpg;\r\n/images/RK/2023/6/2.jpg;\r\n/images/RK/2023/6/4.jpg;\r\n/images/RK/2022/27/10.jpg;\r\n/images/RK/2022/27/6.jpg', '', '', 'Новости', '/news.php', 0, '', '5'),
(739, '3,18', '\"Как научить подростков с РАС изготавливать качественную продукцию\"', '28 апреля мы провели вебинар \"Как научить подростков с РАС изготавливать качественную продукцию\". Поделились своим опытом и узнали интересную информацию от коллег из Новосибирска и Липецка - педагогов инклюзивных мастерских. \r\n', '<p>28 апреля мы провели вебинар \"Как научить подростков с РАС изготавливать качественную продукцию\". Поделились своим опытом и узнали интересную информацию от коллег из Новосибирска и Липецка - педагогов инклюзивных мастерских. </p>\r\n<p>Если вы хотите, чтобы ребёнок занимался полезной и творческой деятельностью, начните с простых, но увлекательных заданий. Ребенок любит ароматы и смешивать цвета? Варите вместе с ним мыло! Наборы по мыловарению продаются в интернет-магазинах. Из плюсов: формирование бытовых навыков, которые пригодится в жизни при готовке на кухне (нарезание ножом, работа с тёркой, микроволновкой или плитой).</p>\r\n<p>Всем участникам вебинара выслали на почту электронные сертификаты. А все, кто не смог присоединиться к вебинару, могут посмотреть его запись &#128071;.</p>\r\n<p>Вебинар прошёл в рамках проекта \"Тёплый дом синего слона\". Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фонд президентских грантов. </p>', '/images/seminar/2023/3.jpg', '2023-05-03', '', '/images/seminar/2023/3.jpg;6OMsRJ5SxHs', '', '', '', 0, '', '2'),
(740, '13,19', 'Статья о ресурсных классах в \"Российской газете\"', 'В \"Российской газете\" вышла большая статья о наших ребятах из ресурсных классов, их родителях и учителях. В этом учебном году ресурсные классы школ пос. Молодежный, Луговой и Читы поддерживает Благотворительный фонд \"Абсолют-Помощь\". ', '<p>В \"Российской газете\" вышла большая статья о наших ребятах из ресурсных классов, их родителях и учителях.</p>\r\n<p>В этом учебном году ресурсные классы школ пос. Молодежный, Луговой и Читы поддерживает Благотворительный фонд \"Абсолют-Помощь\". </p>\r\n<p>За информационную поддержку спасибо журналисту Елене Вторушиной. У неё получился качественный и открытый материал. </p>\r\n<p>Прочитать статью можно в печатном номере \"Российской газеты\" от 3 мая 2023 года. </p>\r\n<p><a href=\"/images/smi/33.jpg\" target=\"_blank\" class=\"button\">Посмотреть статью</a></p>', '/images/smi/33-1.jpg', '2023-05-05', '', '', '', 'Новости', 'news.php', 0, '', '5'),
(743, '9', 'Советы психолога', 'В нашем центре родители детей с расстройствами аутистического спектра могут получить индивидуальные консультации у опытного психолога Дмитрия Александровича Басова. Дмитрий Александрович рассказал, почему важно заботиться о своем ментальном здоровье и как родители могут справляться со стрессом самостоятельно.', '', 'https://sun9-78.userapi.com/impg/_40Y0tix7kReQk9fwBVlVl7z0seyuwBd-jSJYA/yddZwaSTdpw.jpg?size=807x807&quality=95&sign=bc2ac68de445b16764bb52a0ec5f0949&type=album', '2023-03-07', '', '', 'https://vk.com/@rassvetapc-psihologicheskaya-podderzhka-roditelei-detei-s-osobennostyam', 'Новости', '/news.php', 1, '', '4'),
(745, '19', 'Обучение специалистов РК', 'Важная поддержка для педагогов ресурсных классов - сопровождение опытными поведенческими специалистами. Благодаря тренингам и супервизиям повышается общий уровень компетентности педагогов, а также поддерживается точность в применении методов обучения.', '<p>Подходит к концу учебный год в школах, наши подопечные в ресурсных классах готовятся к контрольным и проверочным, а вместе с ними усиленно работают и педагоги: учителя, тьюторы, психологи... </p>\r\n<p>Важная поддержка для педагогов ресурсных классов - еженедельные тренинги с кураторами - опытными поведенческими специалистами. На них повышается общий уровень компетентности педагогов, а также поддерживается точность в применении методов обучения, реализации протоколов по формированию навыков и коррекции поведения.</p>\r\n<p>Елена Шапилова, куратор \"РАСсвета\", рассказала о том, как проходили тренинги в школе посёлка Молодежный Иркутского района:</p>\r\n<p>- Тренинги проводятся в двух основных форматах. \r\nФормат \"обучение в классе\" включает в себя лекции, просмотр видео, выполнение заданий, тестов, обыгрывание различных ситуаций. На них мы обсуждаем вопросы теории, разбираем сложные кейсы, примеры из практики. Ведущими таких тренингов выступают не только куратор или учитель, но и тьюторы. Такой подход, когда тьюторы проводят лекции по вопросам теории для своих коллег, позволяет им лучше разобраться и усвоить материал. \r\n</p>\r\n<p>Второй формат - \"интенсивное обучение навыкам\" направлен на отработку точности выполнения протоколов по формированию какого-то навыка или применению какого-либо метода. Он включает: предоставление инструкций по выполнению протокола, моделирование выполнения куратором или другим опытным специалистом класса, тренировку выполнения тьюторами в паре с куратором или другими тьюторами, предоставление обратной связи и повторение тренировки до достижения критерия точности. </p>\r\n<p>Не только тьюторам ресурсных классов важно постоянно обучаться и повышать свою квалификацию. В этом учебном году Елена Шапилова окончила 4 курс обучения в сфере прикладного анализа поведения у сертифицированного поведенческого аналитика Юлии Эрц, наряду с другими специалистами \"Рассвета\". </p>\r\n<p>\"Одна из ключевых тем 4 курса - тема о курации поведенческих программ и поддержке терапевтического персонала, то есть инструкторов и тьюторов, работающих с детьми. Мне, как практикующему куратору, было очень полезно узнать как наиболее эффективно провести тренинг, обеспечить структурированный подход к формированию нужных компетенций у инструкторов и тьюторов, оценить эффективность процесса и оказывать профессиональную поддержку специалистам.\r\nПосле пройденного обучения я стараюсь сделать процесс тренинга тьюторов более индивидуализированным, с опорой на принципы прикладного анализа поведения и базовые навыки каждого специалиста. Ведь процесс формирования профессиональных навыков схож с процессом формирования навыков у детей\", - говорит Елена.</p>\r\n<p>Супервизии в ресурсных классах - консультации, на которых разбираются актуальные запросы специалистов по работе с учениками ресурсного класса,  также способствуют повышению квалификации всех педагогов класса. </p>\r\n<p>Супервизор наших ресурсных классов - Наталия Александровна Мальтинская, сертифицированный поведенческий аналитик, специальный психолог, кандидат медицинских наук.</p>\r\n<p>В начале мая в ресурсных классах Иркутского района прошли очные супервизии. Традиционно они проходят два раза в год. Дистанционные супервизии - дважды в месяц. Супервизия в конце учебного года помогла нашим педагогам определиться со стратегиями по учебному процессу, подвести итоги и внести дополнения в индивидуальные программы обучения каждого ученика.</p>\r\n<p>Проект \"Право на образование\" осуществляется при поддержке Благотворительного фонда \"Абсолют-Помощь\".</p>', '/images/RK/2023/9/1.jpg', '2023-05-16', '/images/Absolut/logo.jpg;\r\n/images/RK/2023/9/2.jpg;\r\n/images/RK/2023/9/3.jpg;\r\n/images/RK/2023/9/4.jpg;\r\n/images/RK/2023/9/5.jpg;\r\n/images/RK/2023/9/6.jpg;\r\n/images/RK/2023/9/7.jpg;\r\n/images/RK/2023/9/8.jpg', '', '', 'Новости', '/news.php', 0, '', '5'),
(746, '4', 'Зачем нужны групповые занятия', 'Взаимодействовать в группе, даже малой, из 2-3 человек, детям с аутизмом всегда непросто. У всех наших подопечных есть свои сенсорные особенности: кто-то не выносит шума, кто-то прикосновений, и даже прямого взгляда бывает достаточно, чтобы ребёнок начал кричать и убегать прочь. \r\n', '<p>Взаимодействовать в группе, даже малой, из 2-3 человек, детям с аутизмом всегда непросто. Тут полно скрытых камней. У всех наших подопечных есть свои сенсорные особенности: кто-то не выносит шума, кто-то прикосновений, и даже прямого взгляда бывает достаточно, чтобы ребёнок начал кричать и убегать прочь. \r\n</p>\r\n<p>Не забудьте и о сложностях с коммуникацией - рассказать о том, что его беспокоит, интересует, чего хочется, не каждый ребёнок с РАС может...</p>\r\n<p>Перечислять трудности такого рода можно долго, но всё же преодолевать их, учить ребёнка с РАС находиться в компании из нескольких людей нужно.</p>\r\n<p>Поэтому в \"РАСсвете\" регулярно проходят групповые занятия для детей с аутизмом и другими особенностями развития. Для снижения сенсорной чувствительности и развития коммуникативных и социально-бытовых умений, используются разнообразные игрушки и материалы. </p>\r\n<p>АВА-специалисты учат детей просить (с помощью карточек PECS или слов), сотрудничать и делиться с одногруппниками.  </p>\r\n<p>Чему ещё можно научить ребёнка с аутизмом на групповых занятиях:</p>\r\n<p>&#9989; оставаться в нужном месте во время урока,</p>\r\n<p>&#9989; выполнять действия одновременно с другими,</p>\r\n<p>&#9989; смотреть на говорящего,</p>\r\n<p>&#9989; отвечать на вопросы, обращенные ко всем участникам занятия,</p>\r\n<p>&#9989; следовать групповым инструкциям и т.п.</p>\r\n<p>Групповые занятия в центре проходят при поддержке Департамента здравоохранения и социальной помощи населения комитета по социальной политики и культуры администрации г. Иркутска.\r\n</p>', '/images/news/2023/2.jpg', '2023-05-19', '/images/grants/logo_irkutsk.png', '', '', 'Новости', '', 0, '', '3'),
(747, '4', 'Верить в своего ребенка', '\"Главное, я в себя больше верить стала, и в своего ребёнка. Раньше я не была уверена, что мой сын сможет что-то делать, что ему вообще что-то дано. А теперь я знаю, что может...\" Это отзыв Светланы - мамы Германа, нашего подопечного, прошедшего недавно свой первый АВА-интенсив.', '<p>\"Главное, я в себя больше верить стала, и в своего ребёнка. Раньше я не была уверена, что мой сын сможет что-то делать, что ему вообще что-то дано. А теперь я знаю, что может...\"</p>\r\n<p>Это отзыв Светланы - мамы Германа, нашего подопечного, прошедшего недавно свой первый АВА-интенсив.</p>\r\n<p>У мамы мальчика было желание научить ребенка слушать, контактировать с людьми, просить то, что ему хочется понятным способом, приобрести некоторые навыки самостоятельности.</p>\r\n<p>Над этими целями в течение месяца РАБОТАЛИ АВА-специалисты \"РАСсвета\". И вот что Светлана говорит об изменениях в поведении Германа:</p>\r\n<p>- Раньше у Германа было рассеянное внимание, он не слушал никого, задачи не выполнял, на вопросы не отвечал и вообще редко общался.</p>\r\n<p>Сейчас же, после интенсива, я вижу, что мой ребенок выполняет просьбы: \"подойди\", \"открой\", \"возьми\". На занятиях он начал выполнять задания, хотя ещё месяц назад он не понимал, что от него хотят.</p>\r\n<p>Дома я не могла обучить Германа тому, что его не интересовало. А в центре эта черта изменилась. Теперь его можно направить, что-то с него спросить, похвалить и он это понимает.</p>\r\n<p>Мы даже перешли к заданию посложнее - научить сына кушать самостоятельно. И тут я также увидела ощутимые результаты!</p>\r\n<p>Еще во время занятий мы выяснили, что мой сын читать умеет. Мы его этому не учили дома, а просто знакомили с алфавитом. И на занятии, когда показывали карточки с картинкой и текстом, он смог прочитать текст. Проверили мы это очень просто - показали ему картинку с шапкой, а подпись у неё была \"варежка\". И сын сказал \"варежка\"!</p>\r\n<p>Этот навык мы, конечно же, будем и дальше развивать. К тому же он очень любит буквы, книжки.</p>\r\n<p>Интенсив Германа прошёл в рамках проекта «Передышка для мамы». Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n', '/images/respite/7.jpg', '2023-05-20', '/images/grants/pgrants_logo.jpg\r\n\r\n\r\n', '/images/respite/7.jpg;fhJSh1Ss48o', '', 'Новости', '/news.php', 0, '', '4'),
(748, '13', '«Не два плюс два, а надень штаны». Как в Иркутске адаптируют детей с аутизмом', 'Недавно в \"РАСсвет\" приходила журналист \"ИрСити\" Алёне Карпашова. В итоге получился отличный репортаж о запуске нового проекта по развитию бытовых навыков при поддержке Благотворительного фонда «Вклад в будущее». ', '<p>Недавно в \"РАСсвет\" приходила журналист \"ИрСити\" Алёне Карпашова. В итоге получился отличный репортаж о запуске нового проекта по развитию бытовых навыков при поддержке Благотворительного фонда «Вклад в будущее». </p>\r\n<p>Проект \"Сам себе помощник\" реализуется при поддержке Благотворительного фонда «Вклад в будущее».</p>', 'https://cdn.iportal.ru/preview/news/articles/b7126a1aa3646330ba036eceaf0e3cc330b3bdcb1_727.jpg.webp-portal', '2023-05-26', '/images/grants/VK_VB.jpg', '', 'https://ircity.ru/text/society/2023/05/26/72329576/?utm_source=vk&utm_medium=social&utm_campaign=ircity', 'Новости', '/news.php', 1, '', '1'),
(749, '2', 'Семейный выходной в театре', 'Семейный выходной в театре - в конце мая мы организовали для наших подопечных и их родителей совместное посещение спектакля в ТЮЗе. Смотрели историю о Белоснежке, слушали красивые песни и дружно хлопали в конце, когда добро победило.', '<p>Семейный выходной в театре - в конце мая мы организовали для наших подопечных и их родителей совместное посещение спектакля в ТЮЗе. Смотрели историю о Белоснежке, слушали красивые песни и дружно хлопали в конце, когда добро победило.</p>\r\n<p>Как поведёт себя ребёнок, когда погасят свет в зале? Как он отреагирует на громкие звуки со сцены? Будет ли вокализировать и мешать другим зрителям? Эти и другие страхи родителей детей с особенностями развития мы проработали на практике. В итоге никому не мешали и получили много положительных эмоций, поняли над чем ещё можно поработать на занятиях с ребятами.</p>\r\n<p>Конечно, без таких культурных выходов подготовить детей к посещению общественных мест невозможно, как бы упорно с ними не тренировались педагоги. Поэтому совсем скоро попробуем новую развлекательную площадку и поделимся с вами результатами.</p>\r\n<p></p>\r\n<p>Мероприятие состоялось в рамках проекта <a href=\"https://rassvet-apc.ru/news/detail.php?id=712&back=news\" target=\"_blank\">«Расширяя границы»</a> при поддержке Департамента здравоохранения и социальной помощи населению комитета по социальной политике и культуре администрации г. Иркутска.</p>', '/images/dosug/2023/2/1.jpg', '2023-05-30', '/images/grants/logo_irkutsk.png;\r\n/images/dosug/2023/2/2.jpg;\r\n/images/dosug/2023/2/3.jpg;\r\n/images/dosug/2023/2/4.jpg;\r\n/images/dosug/2023/2/5.jpg', '', '', 'Новости', '', 0, '', '3'),
(753, '16', 'Участие в практическом тренинге по работе с персоналом', 'Пять специалистов «РАСсвета» проходят четвертый практический тренинг в рамках сертификационной программы подготовки специалистов по прикладному анализу поведения (Школа Ю.Эрц), посвященный тренингу персонала и обеспечению процессуальной точности вмешательства. ', '<p>Пять специалистов «РАСсвета» проходят четвертый практический тренинг в рамках сертификационной программы подготовки специалистов по прикладному анализу поведения (Школа Ю.Эрц, сертифицированного поведенческого аналитика и супервизора CBA-S), посвященный тренингу персонала и обеспечению процессуальной точности вмешательства.</p>\r\n<p>Когда к нам в центр обращаются за педагогической помощью, мы всегда действуем в рамках профессиональной этики поведенческих специалистов: предоставляем учебную программу, составленную куратором и одобренную супервизором.  \r\n</p>\r\n<p>Куратор обязан обеспечить эффективность поведенческого вмешательства и провести тренинг инструкторам таким образом, чтобы все участники процесса могли применять программу или план по коррекции поведения со 100% точностью. </p>\r\n<p>Основная задача тренинга 4 модуля - проверить и отточить навыки поведенческих специалистов в проведении тренинга персонала и предоставлении позитивной и корректирующей обратной связи.</p>\r\n<p>Обучение прошло в рамках проекта «Профессия помогать» при поддержке Благотворительного фонда Владимира Потанина.</p>', '/images/training/2023/2/1.jpg', '2023-06-09', '/images/training/2023/2/2.jpg', '', '', '', '', 0, '', '20'),
(754, '18', 'Пробуем новые направления', 'Недавно в наши мастерские поступили новые материалы для занятий, среди которых была петельная пряжа. Пока мы только учим ребят вытягивать петельки, но ряд за рядом изделие уже приобретает форму.', '<p>Недавно в наши мастерские поступили новые материалы для занятий, среди которых была петельная пряжа. В планах связать из пряжи пледы, подушки, шарфики, из такого материала они получатся особенно приятными и подарят тепло людям, которые нуждаются в заботе и внимании. \r\n</p>\r\n<p>Пока мы только учим ребят вытягивать петельки, но ряд за рядом изделие уже приобретает форму. Тьюторы вовремя оказывают подсказки и корректируют объем работы в зависимости от настроя ребенка, при необходимости делают перерыв. Процесс вязания, который приносит удовольствие, мягкая, приятная на ощупь пряжа, ориентиры того, что процесс не бесконечный и успех обеспечен.</p>\r\n<p>Занятия проходят в рамках проекта \"Тёплый дом синего слона\". Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/DSS/2023/11/1.jpg', '2023-05-10', '/images/grants/pgrants_logo.jpg;\r\n/images/DSS/2023/11/1.jpg;\r\n/images/DSS/2023/11/3.jpg;\r\n/images/DSS/2023/11/4.jpg;\r\n/images/DSS/2023/11/2.jpg', '', '', '', '', 0, '', '2'),
(755, '18', 'Свечи разные нужны', 'В мастерских вместе с ребятами мы изготавливаем свечи из вощины и соевого воска. Стараемся разнообразить процесс заливки не только красителями, ароматизаторами, но и разными формами.', '<p>В мастерских вместе с ребятами мы изготавливаем свечи из вощины и соевого воска. Стараемся разнообразить рабочий процесс, чтобы ребята всегда получали удовольствие от занятий.</p>\r\n<p>Заливаем свечи в силиконовые формы, стеклянные баночки, бетонное кашпо и даже в кокосовые половинки, а ещё используем сухоцветы для декора. При этом важно показать ребёнку готовый результат, ведь по силиконовой форме не понятно, что получится в итоге.</p>\r\n<p>Используем различные ароматизаторы и цвета, ученики ощущают запах и могут выбрать понравившийся цвет.</p>\r\n<p>И вишенка на торте - это возможность зажечь уже готовую свечу. У нас в мастерской есть парочка экземпляров для такого опыта, чтобы ребятам могли увидеть и конечный результат.</p>\r\n<p>Свечи из нашей инклюзивной мастерской не только красивые и ароматные, они ещё и долго и ярко горят, не коптят, готовятся из натуральных материалов.</p>\r\n<p>Вот такая красота отправится в\r\nБлаготворительный фонд «Оберег» - Иркутск уже в конце лета и подарит тепло и заботу людям, которые в этом нуждаются</p>\r\n<p>Изделия изготовлены на занятиях в мастерских в рамках проекта \"Тёплый дом синего слона\". Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/DSS/2023/12/8.jpg', '2023-06-14', '/images/grants/pgrants_logo.jpg;\r\n/images/DSS/2023/12/9.jpg;\r\n/images/DSS/2023/12/10.jpg;\r\n/images/DSS/2023/12/11.jpg;\r\n/images/DSS/2023/12/1.jpg;\r\n/images/DSS/2023/12/2.jpg;\r\n/images/DSS/2023/12/3.jpg;\r\n/images/DSS/2023/12/4.jpg;\r\n/images/DSS/2023/12/6.jpg;\r\n/images/DSS/2023/12/7.jpg;\r\n/images/DSS/2023/12/5.jpg', '', '', '', '', 0, '', '2'),
(756, '18', 'Визуальное расписание в мастерских', 'Волонтеры и педагоги нашей инклюзивной мастерской подготовили визуальные инструкции для наших подопечных, где подробно показаны алгоритмы работы над разными изделиями. ', '<p>В АВА-терапии визуальное расписание - способ воздействовать на поведение ребёнка с особенностями развития. Обычная планшетка с картинками меняет к лучшему ход занятия, сокращает истерики, убегания, отказ от сотрудничества со стороны ученика.</p>\r\n<p>Понимая, что нужно сделать на занятии, сколько осталось заданий до конца учебного процесса или до перерыва, ребёнок меньше подвергается стрессу, окружающий мир для него становится более предсказуемым. Предупреждён, значит спокоен.</p>\r\n<p>Волонтеры и педагоги нашей инклюзивной мастерской подготовили визуальные инструкции для наших подопечных, где подробно показаны алгоритмы работы над разными изделиями. По ним удобно работать не только детям с РАС, но и их нейротипичным сверстникам, и даже взрослым.</p>\r\n<p>Прикрепляем полезный наглядный материал, который вы можете распечатать и использовать дома:</p>\r\n<p><a href=\"https://vk.com/doc16315605_661704628?hash=CRgGx7aYZZXlyHu37mxaVUYiBI6D3jzofzqFKjD8k1k&dl=1TzTJGYXD91IO6OmEG9HfUb5msV3sC1zZFyf0k0egB0\" target=\"_blank\">инструкция по изготовлению мягкой игрушки</a></p>\r\n<p><a href=\"https://vk.com/doc16315605_661704629?hash=l8TkFaqpqqjbzZQIGymuZXZZOvoSBDS6MOGvFdnDTD0&dl=MiEgxuHffHKy5dVqf8QSttvF9I9WH3fm0cbU7OIsxjP\" target=\"_blank\">инструкция по мыловарению</a>.</p>\r\n<p>Работа с визуальным расписанием проводится в рамках проекта \"Тёплый дом синего слона\". Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/DSS/2023/1.jpg', '2023-05-24', '/images/grants/pgrants_logo.jpg', '', '', '', '', 0, '', '2'),
(758, '18', 'Наши планы на лето', 'У нас грандиозные планы: нужно изготовить свечи, мыло, подушки, навязать пледов, шапок и шарфов и всего этого чем больше, тем лучше, ведь уже в конце августа изделия, изготовленные с душой и заботой, отправятся дарить тепло нуждающимся.', '<p>Лето это солнце, море, отпуск, деревня и занятия в мастерских \"Дом синего слона\". У нас грандиозные планы: нужно изготовить свечи, мыло, подушки, навязать пледов, шапок и шарфов и всего этого чем больше, тем лучше, ведь уже в конце августа изделия, изготовленные с душой и заботой, отправятся дарить тепло нуждающимся.</p>\r\n<p>На фото фрагменты с групповых занятий, на которых ребята учатся \"дружить\" со швейной машинкой, наносить разметочную линию, скалывать ткань булавками, работать с электрической плитой, измельчать воск и мыльную основу, вязать петельной пряжей и выполнять многие другие маленькие операции.</p>\r\n<p>Занятия проходят в рамках проекта \"Тёплый дом синего слона\". Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/DSS/2023/13/4.jpg', '2023-06-20', '/images/grants/pgrants_logo.jpg;\r\n/images/DSS/2023/13/9.jpg;\r\n/images/DSS/2023/13/8.jpg;\r\n/images/DSS/2023/13/1.jpg;\r\n/images/DSS/2023/13/2.jpg;\r\n/images/DSS/2023/13/3.jpg;\r\n/images/DSS/2023/13/4.jpg;\r\n/images/DSS/2023/13/6.jpg;\r\n/images/DSS/2023/13/7.jpg;\r\n/images/DSS/2023/13/5.jpg', '', '', '', '', 0, '', '2');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(759, '18', 'Завершение проекта \"Культурный код: возможность для каждого 2.0\"\r\n', 'Завершается проект Ассоциации \"Аутизм Регионы\" \"Культурный код: возможность для каждого 2.0\". В нем приняли участие 8 регионов! В том числе и наши мастерские \"Дом синего слона\". Благодарим Ассоциацию \"Аутизм Регионы\" и Президентского фонда культурных инициатив!', '<p>Завершается проект Ассоциации \"Аутизм Регионы\" \"Культурный код: возможность для каждого 2.0\". В нем приняли участие 8 регионов! В том числе и наши мастерские \"Дом синего слона\".</p>\r\n<p>Радуемся за успехи подопечных творческих мастерских: более 100 ребят с аутизмом реализуют свой креативный потенциал, приобретают навыки полезной творческой занятости, а некоторые из них уже осваивают предпрофессиональные знания и опыт, которые пригодятся им во взрослой жизни </p>\r\n<p>В рамках проекта педагоги нашей мастерской имели возможность обменяться опытом со своими коллегами из других регионов.</p>\r\n<p>К нам в гости в мастерские «Дом синего слона» приезжала Марина Бакулина, руководитель направления по работе с подростками центра творчества и социальной адаптации подростков с аутизмом «Цветок Папоротника» г.Липецк.\r\nМарина прошла стажировку на индивидуальных и групповых занятиях в мастерской, поучаствовала в мастер-классах по работе с кожей и текстурной пастой и обменивалась опытом со специалистами. </p>\r\n<p>Очень интересно не только рассказывать о своей деятельности, но и узнавать что-то новое от коллег, поэтому наши специалисты отправились стажироваться в Уфу, где изучили опыт трёх организаций - членов ассоциации \"Аутизм-Регионы\", в которых занимаются дети и взрослые с РАС и другими особенностями развития.</p>\r\n<p>Педагоги инклюзивной мастерской Александра Смелая и Аракс Месропян, а также специалист отдела развития Марина Андреева прошли стажировку в благотворительном образовательном фонде \"Мархамат\", региональной общественной организации родителей детей с инвалидностью \"СоДействие\", и у тезки нашего центра автономной неккомерческой организации \"Рассвет\".</p>\r\n<p>Кабинеты сенсорной интеграции, группы кратковременного пребывания для подготовки малышей к детскому саду, летний инклюзивный лагерь, АВА-занятия, обучение в мастерских подростков и взрослых (30+) и тренировочные квартиры для них же... Направлений работы у наших уфимский коллег много, взяли на заметку и решили кое-что попробовать у нас.</p>\r\n<p>Пришло время слов благодарности в адрес Ассоциации \"Аутизм Регионы\" и Президентского фонда культурных инициатив за поддержку наших мастерских!\r\n</p>\r\n<p>Проект Ассоциации \"Аутизм Регионы\"  «Культурный код - возможность для каждого 2.0: развитие сети творческих досуговых центров для подростков и молодых людей с РАС» реализуется при поддержке фонда \"Выход\" с использованием гранта Президента Российской Федерации на реализацию проектов в области культуры, искусства и творческих индустрий Президентский фонд культурных инициатив  </p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-213504262&id=456239031&hash=b700ba1a9cd4ba38\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>\r\n', '/images/DSS/2023/10/2.jpg', '2023-06-21', '/images/grants/FKI.png;\r\n/images/DSS/2023/6/1.jpg;\r\n/images/DSS/2023/6/4.jpg;\r\n/images/DSS/2023/10/3.jpg', '', '', 'Новости', '/news.php', 0, '', '19'),
(760, '18', 'Мастер-класс по изготовлению подушки\r\n', 'Если Вы любите рисовать по ткани также сильно как и мы, а Ваши навыки шитья ещё далеки до совершенства, то изготовление подушки это то, что нужно. Поделимся небольшим алгоритмом изготовления.', '<p>Если Вы любите рисовать по ткани также сильно как и мы, а Ваши навыки шитья ещё далеки до совершенства, то изготовление подушки это то, что нужно. Поделимся небольшим алгоритмом изготовления.\r\n</p>\r\n<p>Вам понадобятся: акриловые краски, кисть-спонж, кисть номер 10 или больше, два квадрата однотонной ткани размером 45*45см, нитки, маркер по ткани, линейка, портновские булавки, швейная машинка, утюг, отрезок плотного картона 10*20см.</p>\r\n<p>Начинаем процесс с рисования картины на ткани. Берём 1 квадрат однотонной ткани. Закрашиваем фон синей или бирюзовой краской. Наносим толстую полоску краски белого цвета на картон и размазываем вазу прямоугольной формы. С помощью спонжа и красной краски рисуем цветы, с помощью кисти и зеленой краски - листочки и стебель. Готовый рисунок оставляем рисунок до полного высыхания, для закрепления проглаживаем рисунок утюгом (используйте сетку для глажки во избежание прилипания).</p>\r\n<p>Приступаем к шитью. Складываем получившийся рисунок и второй квадрат ткани лицевой стороной внутрь, скалываем булавками.</p>\r\n<p>На расстоянии 1 см от края ткани с помощью маркера и линейки рисуем линии, по которым будет идти строчка на швейной машинке. Оставляем небольшое отверстие для набивания, через которое будем проталкивать пух.</p>\r\n<p>Прошиваем по начерченной линии, убираем булавки. Будьте осторожны и внимательны, оставляйте булавки в игольнице или другом приспособленном для них месте.</p>\r\n<p>Выворачиваем подушку, отглаживаем и набиваем её пухом. Подушка должна быть приятной на ощупь.</p>\r\n<p>Зашиваем отверстие  потайным швом или любым другим швом с помощью нитки и иголки.</p>\r\n<p>Всё! Подушка готова к использованию!</p>\r\n<p>Проект Ассоциации \"Аутизм Регионы\" «Культурный код - возможность для каждого 2.0: развитие сети творческих досуговых центров для подростков и молодых людей с РАС». Проект реализуется при поддержке Фонда «Выход» - Аутизм в России, с использованием гранта Президента Российской Федерации на реализацию проектов в области культуры, искусства и творческих индустрий Президентский фонд культурных инициатив </p>', '/images/DSS/2023/15/1.jpg', '2023-03-30', '/images/grants/FKI.png;\r\n/images/DSS/2023/15/1.jpg;\r\n/images/DSS/2023/15/2.jpg;\r\n/images/DSS/2023/15/3.jpg;\r\n/images/DSS/2023/15/4.jpg;\r\n/images/DSS/2023/15/5.jpg;\r\n/images/DSS/2023/15/6.jpg;\r\n/images/DSS/2023/15/7.jpg;\r\n/images/DSS/2023/15/8.jpg;\r\n/images/DSS/2023/15/9.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', '19'),
(763, '2', 'Поездка в \"Город профессий\"', '2 июня мы организовали поездку в детский город профессий «Любогород». Это был незабываемый опыт для всех участников. Они имели возможность попробовать новые игры и профессии, а также провести время вместе со своими сверстниками. ', '<p>2 июня мы организовали поездку в детский город профессий «Любогород». Это был незабываемый опыт для всех участников.</p>\r\n<p>Когда мы прибыли в игровую комнату, наши дети были в восторге. Они попробовали множество разных игр и профессий, которые были доступны в игровом пространстве. Дети изучали такие профессии, как спасатель МЧС, врач, строитель, управляли эскалатором, лечили зубы.\r\n</p>\r\n<p>Однако, что было особенно важно, так это то, что наши дети чувствовали себя безопасно и комфортно.</p>\r\n<p>Эта поездка в город профессий была важным событием для наших детей. Они имели возможность попробовать новые игры и профессии, а также провести время вместе со своими сверстниками. Наша команда работает над тем, чтобы такие поездки стали регулярными и наши дети смогли получать еще больше положительных эмоций и впечатлений.\r\n</p>\r\n<p></p>\r\n<p></p>\r\n<p>Мероприятие состоялось, благодаря проекту Ассоциации \"Аутизм Регионы\"  «Культурный код - возможность для каждого 2.0: развитие сети творческих досуговых центров для подростков и молодых людей с РАС», который реализуется при поддержке Фонда «Выход» - Аутизм в России  с использованием гранта Президента Российской Федерации на реализацию проектов в области культуры, искусства и творческих индустрий \"Президентский фонд культурных инициатив\" </p>\r\n\r\n\r\n\r\n\r\n\r\n', '/images/dosug/2023/3/2.jpg', '2023-06-06', '/images/grants/FKI.png;\r\n/images/dosug/2023/3/1.jpg;\r\n/images/dosug/2023/3/2.jpg;\r\n/images/dosug/2023/3/3.jpg;\r\n/images/dosug/2023/3/4.jpg;\r\n/images/dosug/2023/3/5.jpg;\r\n/images/dosug/2023/3/6.jpg;\r\n/images/dosug/2023/3/7.jpg', '', '', 'Новости', '/news.php', 0, '', '19'),
(765, '13', 'Обучение детей с аутизмом в иркутском адаптационно-педагогическом центре', 'Почисть зубы, застегнуть пуговицы, помыть посуду и руки. Этим и другим навыкам обучают детей с аутизмом в адаптационно-педагогическом центре \"РАСсвет\". Почему это важнее, чем, например, чтение, узнала корреспондент телекомпании АИСТ Юлия Бархатова, побывав в гостях у \"Рассвета\". ', '<p>Почисть зубы, застегнуть пуговицы, помыть посуду и руки. Этим и другим навыкам обучают детей с аутизмом в адаптационно-педагогическом центре \"РАСсвет\". Почему это важнее, чем, например, чтение, узнала корреспондент телекомпании АИСТ Юлия Бархатова, побывав в гостях у \"Рассвета\". </p>\r\n<p><a class=\"button\" href=\"https://aisttv.ru/page/obuchenie-detei-s-autizmom-v-irkutskom-adaptatsionno-pedagogicheskom-tsentre/\">Читать</a></p>\r\n<p>Проект \"Сам себе помощник\" реализуется при поддержке Благотворительного фонда «Вклад в будущее».</p>', '/images/smi/34.jpg', '2023-06-08', '/images/grants/VK_VB.jpg', '', 'https://aisttv.ru/page/obuchenie-detei-s-autizmom-v-irkutskom-adaptatsionno-pedagogicheskom-tsentre/', 'Новости', '/news.php', 1, '', '1'),
(766, '19', 'Летний отдых в кругу друзей', 'Подходит к концу наш проект \"Право на образование\", направленный на поддержку ресурсных классов Иркутского района и г. Читы. Пока взрослые подводят итоги, дети уже вовсю наслаждаются каникулами и летними деньками. ', '<p>Что может быть лучше летнего отдыха, проведенного в кругу друзей и с пользой?</p>\r\n<p>Подходит к концу наш проект \"Право на образование\", направленный на поддержку ресурсных классов Иркутского района и г. Читы.</p>\r\n<p>Позади итоговое тестирование ABLLS-R для оценки освоения индивидуальных программ, выставление оценок по школьным предметам и награждение грамотами за успешное окончание второго класса.</p>\r\n<p>Пока взрослые подводят итоги, дети уже вовсю наслаждаются каникулами и летними деньками. Рассказываем о том, как проводят каникулы ученики ресурсного класса п. Молодежный.</p>\r\n<p>Ребятам так не хотелось расставаться со своими одноклассниками и любимыми педагогами, поэтому они вместе со своими тьюторами весело проводят время на школьной летней площадке.</p>\r\n<p>Это прекрасная возможность развивать коммуникативные и социальные навыки в неформальной обстановке. Наши ребята участвуют не только в общих лагерных мероприятиях и занятиях вместе с другими ребятами.</p>\r\n<p>Педагоги класса подготовили для них свою интересную программу занятий. Дни выдаются очень насыщенными. Ребята успевают и погулять, и поиграть в настольные и коммуникативные игры, рисуют, лепят, готовят, учат стихи и даже участвуют в театральной постановке.</p>\r\n<p>Отведено время и для индивидуальных занятий, ведь для ребят очень важно поддерживать навыки, приобретенные в течение учебного года.</p>\r\n<p>В программе нашего лагеря есть и экскурсии. Ребята уже посетили театр кукол \"Аистенок\", покатались на колесе обозрения и катамаранах на острове Конный. Впереди еще много интересных экскурсий и занятий.</p>\r\n<p>Учеников наших ресурсных классов поддерживает Благотворительный фонд \"Абсолют-Помощь\" в рамках проекта \"Право на образование\".</p>\r\n\r\n\r\n\r\n\r\n\r\n', '/images/RK/2023/11/2.jpg', '2023-06-16', '/images/Absolut/logo.jpg;\r\n/images/RK/2023/11/1.jpg;\r\n/images/RK/2023/11/3.jpg;\r\n/images/RK/2023/11/4.jpg;\r\n/images/RK/2023/11/5.jpg;\r\n/images/RK/2023/11/6.jpg;\r\n/images/RK/2023/11/7.jpg;\r\n/images/RK/2023/11/9.jpg;\r\n/images/RK/2023/11/10.jpg;\r\n/images/RK/2023/11/11.jpg;\r\n/images/RK/2023/11/12.jpg;\r\n/images/RK/2023/11/14.jpg;\r\n/images/RK/2023/11/13.jpg;\r\n/images/RK/2023/11/15.jpg;\r\n/images/RK/2023/11/8.jpg', '', '', 'Новости', '/news.php', 0, '', '5'),
(767, '3', 'Летняя стажировка в \"Рассвете\"', 'В июне на базе \"АПЦ Рассвет\" прошла двухнедельная практическая стажировка по работе с детьми с аутизмом и другими особенностями развития (ЗПР, ЗПРР, СДВГ) с использованием методов прикладного анализа поведения (АВА-терапия). ', '<p>В июне на базе \"АПЦ Рассвет\" прошла двухнедельная практическая стажировка по работе с детьми с аутизмом и другими особенностями развития (ЗПР, ЗПРР, СДВГ) с использованием методов прикладного анализа поведения (АВА-терапия). </p>\r\n<p>Обучение проводили опытные поведенческие специалисты адаптационно-педагогического центра \"РАСсвет\" (г. Иркутск), педагоги ресурсных классов для детей с расстройствами аутистического спектра.</p>\r\n<p>В стажировке приняли участие родители детей с аутизмом и специалисты смежных профессий (дефектологи, логопеды, воспитатели).\r\n</p>\r\n<p>За две недели обучения были разобраны множество важных тем: установление сотрудничества с ребенком, методы обучения и коррекции поведения, система альтернативной коммуникации  и многое другое.  Все полученные знания сразу же отрабатывались на практике между собой и на занятиях с детьми, под руководством наставников. </p>\r\n<p>«Мы рады, что прикладным анализом поведения начали интересоваться педагоги,\r\nкоторые работают с детьми с особенностями развития. Еще пару лет назад\r\nАВА-специалистов в нашем городе было не найти, сейчас ситуация меняется в\r\nлучшую сторону. Люди интересуются, учатся, это здорово!» - рассказывает куратор \"Рассвета\" и педагог ресурсного класса - Никулина Екатерина Сергеевна,  одна из ведущих тренинга.</p>\r\n<p>Отзывы участников стажировки можно увидеть в прикрепленном ролике.</p>\r\n<section class=\"video-detail\"><iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240045&hash=9307d03ffa45ae5e\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe></section>', '/images/seminar/2023/5/3.jpg', '2023-06-22', '/images/seminar/2023/5/1.jpg;\r\n/images/seminar/2023/5/2.jpg;\r\n/images/seminar/2023/5/3.jpg;\r\n/images/seminar/2023/5/4.jpg;\r\n/images/seminar/2023/5/5.jpg;\r\n/images/seminar/2023/5/6.jpg;\r\n/images/seminar/2023/5/7.jpg;\r\n/images/seminar/2023/5/8.jpg;\r\n/images/seminar/2023/5/9.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', ''),
(770, '3,18', 'МАСТЕР-КЛАСС ОТ МАСТЕРСКИХ \"ДОМ СИНЕГО СЛОНА\"', '29 июня в прямом эфире состоялся мастер-класс по свечеварению с использованием методик на основе прикладного анализа поведения от мастерской «Дом синего слона», где занимаются дети и подростки с расстройствами аутистического спектра (РАС).', '<p>29 июня в прямом эфире состоялся мастер-класс по свечеварению с использованием методик на основе прикладного анализа поведения от мастерской «Дом синего слона», где занимаются дети и подростки с расстройствами аутистического спектра (РАС).</p>\r\n<p>Занятие провела Александра Смелая, педагог мастерской, арт-терапевт и клинический психолог, а помогал ей Степан, подросток с РАС, ученик ресурсного класса. Александра и Степан научили зрителей делать аромо-свечи из соевого воска, поделились лайфхаками по свечеварению, показали, как сделать занятие интересным и понятным для ребенка с РАС.</p>\r\n<p>К прямому эфиру подключилось 82 участника. Отзывы зрителей говорят о том, что такой формат занятия очень востребован и интересен родителям и педагогам, работающим с детьми с РАС. </p>\r\n<p>Посмотреть запись мастер-класса можно на странице <a href=\"https://vk.com/wall-174296278_4000\" target=\"_blank\">Ассоциации \"Аутизм-регионы\"</a> в VK, а также на странице мастерских <a href=\"https://vk.com/dom.sinego.slona\" target=\"_blank\">\"Дом синего слона\"</a>. </p>\r\n<p>АПЦ \"РАСсвет\" - участник проекта Ассоциации \"Аутизм-Регионы\" \"Культурный код - возможность для каждого 2.0: развитие сети творческих досуговых центров для подростков и молодых людей с РАС\". Проект реализуется при поддержке фонда \"Выход\" с использованием гранта Президента Российской Федерации на реализацию проектов в области культуры, искусства и креативных индустрий Президентский фонд культурных инициатив.</p>\r\n<section class=\"video-detail\">\r\n                                        <iframe src=\"https://vk.com/video_ext.php?oid=-174296278&id=456239206&hash=fe95a59a1bca9d2d\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen class=\"social-video\"></iframe>\r\n                                    </section>\r\n', '/images/seminar/2023/5.jpg', '2023-07-01', '/images/grants/FKI.png', '', '', 'Новости', '/news.php', 0, '', '19'),
(771, '1', 'Присоединяйтесь к нашей команде!', 'Адаптационно - педагогический центр \"Рассвет\" в городе Иркутске ищет в свою команду активных, трудолюбивых и добрых людей для работы с детьми с аутизмом, ЗПРР,  УО   и  другими интеллектуальными нарушениями на должность АВА-инструктор.', '<p>Адаптационно - педагогический центр \"Рассвет\" в городе Иркутске ищет в свою команду активных, трудолюбивых и добрых людей для работы с детьми с аутизмом, ЗПРР,  УО   и  другими интеллектуальными нарушениями на должность АВА-инструктор.</p>\r\n<p>АВА-инструктор  - это педагог, который занимается  с детьми с РАС (аутизм) и   другими ментальными расстройствами, с использованием научно-доказанных методов абилитации, утвержденных Министерством Здравоохранения РФ: АВА-терапия или  прикладной анализ поведения.</p>\r\n<p>Работа инструктора заключается в проведении занятий по индивидуальным протоколам кураторов; в подготовке материалов для занятий; в участии в тренингах и индивидуальных консультациях с кураторами центра; в обучении родителей практическому применению научных подходов при работе с детьми; в тестировании навыков ребёнка.</p>\r\n<p>Подробнее о методе работы и целях АВА-инструктора Вы узнаете, если придете к нам. Мы всему научим!</p>\r\n<p><b>Требования:</b> </p>\r\n<p>&#10071; обязательно наличие законченного высшего или средне-специального  образования по педагогике, специальной педагогике, логопедии, клинической психологии или психологии;  </p>\r\n<p>&#10071; опыт работы с детьми с особенностями развития приветствуется, но не обязателен; </p>\r\n<p>&#10071; готовность обучаться, работать в команде, позитивный настрой</p>\r\n<p><b>Условия: </b></p>\r\n<p>&#9989;  обучение прикладному анализу поведения   на базе адаптационно-педагогического центра “Рассвет”;   </p>\r\n<p>&#9989; работа под супервизией  специалистов по прикладному анализу поведения;</p>\r\n<p>&#9989; официальное трудоустройство с полным социальным пакетом и полной занятостью 36 часов в неделю;</p>\r\n<p>&#9989; заработная плата от 42 000 рублей;</p>\r\n<p>&#9989; возможность быстрого (в течение года) карьерного роста.</p>\r\n<p>Если в вакансии Вы узнали себя,  отправьте резюме по адресу <a href=\"mailto:autism@rassvet-apc.ru\"><b>autism@rassvet-apc.ru</b></a> или откликнитесь на <a href=\"https://irkutsk.hh.ru/vacancy/106053531\"><b>ВАКАНСИЮ</b></a> на сайте hh.ru.</p>\r\n<p>Наш адрес: <a href=\"https://irkutsk.hh.ru/search/vacancy/map?enable_snippets=true&items_on_page=100&label=with_address&vacancy_id=79996439&text=&bottom_left_lat=52.24832732572254&bottom_left_lng=104.30597661889644&top_right_lat=52.25766418047959&top_right_lng=104.3461453811035&width=1872&height=708.984\" target=\"_blank\">город Иркутск, Байкальская улица, 234В/2.</a> Ждем Вас!</p>', '/images/ABA/2024/12.jpg', '2023-07-06', '', '', '', 'Вакансии', '/pages/job_openings.php', 0, '', '0'),
(772, '1', 'Ищем тьюторов для детей с аутизмом', 'Открыты вакансии тьюторов для работы в ресурсных классах общеобразовательных школ Иркутска и Иркутского района. Нам нужны специалисты с педагогическим или психологическим образованием и опытом работы по специальности от одного года. ', '<p>Открыты вакансии тьюторов для работы в ресурсных классах общеобразовательных школ города Иркутска (школы № 17 и № 8) и Иркутского района (школы п. Молодежный и п. Луговое).</p>\r\n\r\n<p>&#10071; <b>Нам нужны: </b> \r\n<p>Специалисты с педагогическим или психологическим образованием (опыт работы не обязателен) или студенты заочных отделений, готовые обучаться современным методам работы с детьми с аутизмом. </p> \r\n<p>\r\n<div class=\"autism-quote\">\r\n                <b>&#128204; Ресурсный класс</b> — это модель инклюзивного образования, которая позволяет детям с аутизмом и другими нарушениями развития учиться в обычной школе вместе со сверстниками, развивающимися типично. Ученики ресурсных классов обучаются по индивидуальным адаптированным образовательным программам с помощью команды специалистов, владеющих специальными технологиями и методами обучения, основанными на принципах прикладного анализа поведения. </div></p>\r\n\r\n<p><b>Мы предлагаем: </b></p>\r\n<p>&#9989;  Обучение и стажировку на базе центра АНО \"АПЦ Рассвет\"</p>\r\n<p>&#9989;  Работу под супервизией специалистов по прикладному анализу поведения;</p>\r\n<p>&#9989; Официальное трудоустройство в школе</p>\r\n<p>&#9989; Занятость - первая половина дня</p>\r\n<p>&#9989; Заработная плата от 28 тыс.</p>\r\n<section class=\"video-detail\">\r\n      <iframe src=\"https://rutube.ru/play/embed/518f191b01f18b12bfd5ea2339552f7e/\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen class=\"social-video\"></iframe>\r\n         </section>\r\n<p><b>Обязанности тьютора: </b></p>\r\n<p>&#128313;  Проведение индивидуальных занятий с ребенком с использованием методов прикладного анализа поведения</p>\r\n<p>&#128313;  Сбор данных о поведении и продвижении по программе и их регистрация  в электронном виде​</p>\r\n<p>&#128313;  Работа над формированием коммуникативных навыков и навыков социально приемлемого поведения​</p>\r\n<p>&#128313;  Адаптация учебного материала</p>\r\n<p>&#128313;  Сопровождение ученика на групповых занятиях в ресурсном и в общеобразовательных классах и во всех режимных моментах (урок, перемена, столовая, гардероб и т.д.)</p>\r\n<p>&#128313;  Выполнение рекомендаций учителя и куратора класса</p>\r\n<p>&#128313;  Участие в семинарах, тренингах и других совместных мероприятиях персонала.\r\n</p>\r\n<p>\r\n<div class=\"autism-quote\"> &#128242;\r\nЕсли вакансия Вас заинтересовала или появились вопросы - <b>звоните по телефону <a href=\"tel:+79086566428\"> 89086566428</a></b> (Елена Сергеевна). Или заполните форму ниже и мы перезвоним Вам!</div></p>\r\n<p>Заполняя форму, вы соглашаетесь с <a href=\"https://rassvet-apc.ru/privacy_policy\">Политикой обработки персональных данных</a></p>\r\n<br>\r\n<script src=\"https://yastatic.net/s3/frontend/forms/_/embed.js\"></script><iframe src=\"https://forms.yandex.ru/u/64ac31aa73cee79a75052818/?iframe=1\" frameborder=\"0\" name=\"ya-form-64ac31aa73cee79a75052818\" width=\"100%\" style=\"max-width: 650px\"></iframe>\r\n', '/images/RK/2023/12/1.jpg', '2023-07-10', '', '', '', 'Вакансии', '/pages/job_openings.php', 0, '', '0'),
(776, '4', 'Проект \"Сам себе помощник\": обучение самостоятельности', 'В апреле, когда весь мир объединяет усилия для повышения осведомлённости об аутизме, стартовал наш новый проект \"Сам себе помощник\". Благодаря применению методов АВА-терапии, мы помогаем двадцати нашим подопечным приобрести жизненно важные социальные и бытовые навыки.', '<p>В апреле, когда весь мир объединяет усилия для повышения осведомлённости о расстройствах аутистического спектра, стартовал наш новый проект \"Сам себе помощник\". Благодаря применению методов АВА-терапии, мы помогаем двадцати нашим подопечным приобрести жизненно важные социальные и бытовые навыки. Это включает в себя обучение приёму пищи, использованию посуды, навыкам одевания и раздевания, складыванию одежды, а также общению, игре и участию в занятиях.\r\n</p>\r\n<p>История Димы: путь к самостоятельности</p>\r\n<p>На протяжении целого месяца Дима посещал бесплатные занятия в центре \"РАСсвет\". Ежедневно по четыре часа мальчик учился быть самостоятельным, развивался интеллектуально и социализировался с окружающими. Программа обучения началась с диагностики, позволяющей выявить слабые места и поставить конкретные цели.</p>\r\n<p>В первую очередь работали над навыком просьбы. Дима научился просить желаемое с помощью указательного жеста.</p>\r\n<p>Также уделяли много внимания навыкам гигиены и самообслуживания. Научили Диму сидеть на горшке и самостоятельно одеваться, предварительно проработав необходимые преднавыки.</p>\r\n<p>Дима не умел сидеть на месте и заниматься. Когда мы поработали над его учебным поведение - улучшился навык сотрудничества со взрослыми, а также навыки визуального восприятия и поведения слушателя. А с помощью навыка имитации Дима научился повторять звуки.</p>\r\n<p>К занятиям подключилась и мама Димы. Мы научили её сотрудничать со своим ребенком и показали, как можно учить Диму новым навыкам.</p>\r\n<p>Как научить застёгивать пуговицы: опыт Саввы</p>\r\n<p>Чтобы освоить этот сложный навык, важно проверить развитие мелкой моторики и визуально восприятия: видит ли ребенок мелкие предметы и может ли он ими манипулировать.Если ребенок испытывает трудности в этих заданиях, уделите время тренировке мелкой моторики. </p>\r\n<p>При обучении застегиванию пуговиц не усложняйте ребенку жизнь, учитесь застегивать на горизонтальной поверхности. Например, на столе. Начинайте с крупных пуговиц.Оказывайте максимальные подсказки и хвалите ребенка!\r\nСнижайте уровень ваших подсказок: от полной физической «рука в руке» до минимальной, насколько это возможно! Постепенно размер пуговиц можно уменьшать.\r\nКак только ребенок начнет без проблем застегивать пуговицы на столе, можно надеть одежду и практиковаться в вертикальном положении.</p>\r\n<p>Игорь: самообслуживание после еды</p>\r\n<p>Наш Игорь отлично орудует ложкой и вилкой, может вытереть рот салфеткой - это все плоды кропотливой работы его родителей и педагогов. Но, к сожалению, моторика мальчика не позволяет ему овладеть навыками приема пищи в идеале.\r\nВ данном случае мы делаем упор на самообслуживание. Учим Игоря после плотного обеда самостоятельно убирать свое место трапезы. </p>\r\n\r\n<p>✅ убрать грязную посуду в раковину<br>\r\n✅ воспользоваться губкой и средством для мытья посуды<br>\r\n✅ помыть посуду<br>\r\n✅ разложить ее на сушку<br>\r\n✅ протереть стол<br>\r\n✅ прополоскать тряпочку для стола<br>\r\n✅ подмести пол\r\n</p>\r\n<p>Проект \"Сам себе помощник\" реализуется при поддержке Благотворительного фонда «Вклад в будущее».</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240085&hash=bc0104bc5af9637d\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/ind/2023/2.jpg', '2023-07-18', '/images/grants/VK_VB.jpg', '', '', 'Новости', '/news.php', 0, '', '1'),
(777, '18,22', 'Благодарим за ремонт в наших мастерских!', 'Спасибо Тамаре Гайсиной и всей ее команде за ремонт в мастерских «Дом синего слона» для наших юных мастеров! ', '<p>Спасибо Тамаре Гайсиной и всей ее команде за ремонт в мастерских «Дом синего слона» для наших юных мастеров! </p>\r\n<p>Приходить в мастерские - это удовольствие для наших ребят, а приходить в красивые мастерские - это двойное удовольствие!</p>\r\n<p>Радуемся и посылаем лучи добра!</p>\r\n<p><a href=\"https://vk.com/clip-211689065_456239096\" target=\"_blank\">Видео</a></p>', '/images/DSS/2023/2.jpg', '2023-07-19', '', '', '', 'Новости', '', 0, '', ''),
(778, '4', 'Дистанционная супервизия', 'Супервизия — это наблюдение более опытного коллеги за работой или равного консультанта и обсуждение сильных и слабых сторон, разбор и исправление возникших ошибок, поиск решений и других механизмов работы.\r\n', '<p>\"Мы не устанем повторять, что даже самому крутому поведенческому аналитику необходима супервизия.</p>\r\n<p>Супервизия — это наблюдение более опытного коллеги за работой или равного консультанта и обсуждение сильных и слабых сторон, разбор и исправление возникших ошибок, поиск решений и других механизмов работы.\r\n</p>\r\n<p>А вы знали, что хорошего супервизора найти не так уж и просто. А если вы его нашли, то вероятность, что он живет в вашем городе, очень мала.</p>\r\n<p>Наши супервизоры находятся далеко от нас, но стабильно радуют нас приездами и видео-созвонами.</p>\r\n<p>Организовать <a href=\"/news/detail.php?id=569&back=news\" target=\"_blank\">очную супервизию</a> весьма затратно. Оплата перелетов, проживания, часов супервизии - все ложится на наши плечи, поэтому чаще выбор формата консультаций падает на онлайн.</p>\r\n<p>Мы расскажем вам цепочку действий онлайн-супервизии специалистами нашего центра.</p>\r\n<p>&#128313; У нашего куратора есть вопрос по поводу того или иного поведения ребенка, на который бы хотелось получить ответ от более опытного и насмотренного коллеги</p>\r\n<p>&#128313; Куратор готовит список вопросов супервизору и материалы ребенка (видео с занятий, сбор данных поведения в разнообразных таблицах и графиках, цели обучения, протоколы и т.д.)</p>\r\n<p>&#128313; Созвон. Супервизор дает обратную связь по поставленным вопросам, дает свои рекомендации.</p>\r\n<p>&#128313; После созвона куратор в ближайшее время вносит изменения в поведенческую программу ребенка и следит за точностью ее реализации остальными участниками учебного процесса.</p>\r\n\r\n<p>Проект «Передышка для мамы» реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n', '/images/respite/8.jpg', '2023-07-21', '/images/grants/pgrants_logo.jpg\r\n\r\n\r\n', '', '', 'АВА-центр', '/about.php', 0, '', '4'),
(779, '18', 'Работа на оверлоке', 'Наши ребята с удовольствием проводят время, обметывая края пледа на оверлоке. Это такой процесс, который придает изделиям профессиональный вид и прочность. Будь то плед, подушка или одежда, оверлок делает их идеальными!', '<p>Наши ребята с удовольствием проводят время, обметывая края пледа на оверлоке. Это такой процесс, который придает изделиям профессиональный вид и прочность. Будь то плед, подушка или одежда, оверлок делает их идеальными!</p>\r\n<p>Работа на оверлоке требует немного практики и терпения, но ребята находят в этом удовольствие. Этот процесс не только придает изделию красивый и аккуратный вид, но и предотвращает его распускание. Ведь кто хочет, чтобы его любимое изделие порвалось?</p>\r\n<p>Присоединяйтесь к нам и станьте настоящими мастерами оверлока!</p>\r\n<p>Занятия проходят в рамках проекта \"Тёплый дом синего слона\". Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239103&hash=13482d792fa4c048\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>\r\n', '/images/DSS/2023/3.jpg', '2023-07-24', '/images/grants/pgrants_logo.jpg', '', '', '', '', 0, '', '2'),
(781, '18', 'Подушка для Димы', 'Сегодня в нашей мастерской на занятии с Димой мы сделали что-то особенное! Шили подушку! Взяли красивую ткань и сшили ее на нашей швейной машинке. Дима с таким интересом и усердием выполнял каждый шов, что мы просто восхищаемся его талантами.', '<p>Сегодня в нашей мастерской на занятии с Димой мы сделали что-то особенное! Шили подушку! Взяли красивую ткань и сшили ее на нашей швейной машинке. Дима с таким интересом и усердием выполнял каждый шов, что мы просто восхищаемся его талантами.</p>\r\n<p>А потом самое веселое - мы наполнили подушку синтепоном! Дима с руками в синтепоне, щекочущим его легким пушистым прикосновением, радостно смеялся и радовался каждой маленькой пушинке.</p>\r\n<p>Такие занятия помогают нашим деткам развивать мелкую моторику, координацию движений и сосредоточенность.\r\n</p>\r\n<p>Мы счастливы видеть, как каждый из них проявляет свою индивидуальность и творческий потенциал. Мы верим, что каждый ребенок способен на большее и заслуживает любви и поддержки.</p>\r\n<p>Занятия проходят в рамках проекта \"Тёплый дом синего слона\". Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/DSS/2023/16/1.jpg', '2023-07-26', '/images/grants/pgrants_logo.jpg;\r\n/images/DSS/2023/16/2.jpg;\r\n/images/DSS/2023/16/3.jpg;\r\n/images/DSS/2023/16/4.jpg', '', '', '', '', 0, '', '2'),
(782, '9', 'Чрезмерная опека родителей', 'Мы составили для вас подборку советов от психолога для родителей. Часто, желая избавить ребенка от трудностей, родители чрезмерно опекают его, оберегают от всего, что может огорчить, не дают ничего делать самостоятельно. ', '<p>Мы составили для вас подборку советов от психолога для родителей.</p>\r\n<p>Часто, желая избавить ребенка от трудностей, родители чрезмерно опекают его, оберегают от всего, что может огорчить, не дают ничего делать самостоятельно. </p>\r\n<p>&#9989; Развивайте умения и навыки по самообслуживанию.</p>\r\n<p>&#9989; Предоставляйте ребёнку самостоятельность в действиях и принятии решений. </p>\r\n<p>⚠️ Лайфхак: </p>\r\n<p>Не бойтесь отказать ребёнку в чём-либо, если считаете его требования чрезмерными. \r\nОднако проанализируйте количество запретов, с которыми сталкивается ваш ребенок. Продумайте, все ли они обоснованы, нет ли возможности сократить ограничения. </p>\r\n<p>&#128308; Разделите требования на красные, которые ни в коем случае нельзя нарушать: убегать на улице, есть песок, толкать других людей. </p>\r\n<p>&#128992; Желтые: которые можно сформулировать не в приказном тоне, а в качестве вопроса. «Сынок, давай выключим телефон?». У ребенка будет возможность отказаться приемлемым способом и выключить его чуть-чуть позже. </p>\r\n<p>&#128994; И зеленые правила: «Доченька, не тряси руками». От вашего комментария ничего не изменится, ведь дочка постоянно трясет руками. Пока вы не научили ребенка замещающему поведению, например, держать руки в карманах, не стоит постоянно делать замечания ребенку, т.к. он все равно не будет к вам прислушиваться. И получается, что просьбы взрослого выполнять не обязательно. Отсюда могут возникнуть проблемы с красными правилами.</p>\r\n<p>Проект «Передышка для мамы» реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n', '/images/respite/9.jpg', '2023-07-28', '/images/grants/pgrants_logo.jpg\r\n\r\n\r\n', '', '', 'Новости', '/news.php', 0, '', '4'),
(785, '2', 'Путешествие с «РАСсветом»', 'В конце июля наша дружная банда путешествовала по бесплатному маршруту «Иркутск-Тальцы-Иркутск».\r\nДля наших ребят такие поездки отличная возможность потренировать как социальные навыки, так и устроить себе сенсорный релакс!', '<p>В конце июля наша дружная банда путешествовала по бесплатному маршруту «Иркутск-Тальцы-Иркутск».</p>\r\n<p>Для наших ребят такие поездки отличная возможность потренировать как социальные навыки, так и устроить себе сенсорный релакс!</p>\r\n<p>Ветерок на теплоходе, возможность побродить по берегу Ангары и насладиться такими интересными текстурами деревянного зодчества - все это в веселой компании друзей!</p>\r\n<p>Мероприятие состоялось в рамках проекта <a href=\"https://rassvet-apc.ru/news/detail.php?id=712&back=news\" target=\"_blank\">«Расширяя границы»</a> при поддержке Департамента здравоохранения и социальной помощи населению комитета по социальной политике и культуре администрации г. Иркутска.</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240163&hash=e6a048d147574e56\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n<section>', '/images/dosug/2023/4/3.jpg', '2023-07-31', '/images/grants/logo_irkutsk.png;\r\n/images/dosug/2023/4/2.jpg;\r\n/images/dosug/2023/4/1.jpg;\r\n/images/dosug/2023/4/4.jpg;\r\n/images/dosug/2023/4/6.jpg;\r\n/images/dosug/2023/4/7.jpg;\r\n/images/dosug/2023/4/8.jpg;\r\n/images/dosug/2023/4/9.jpg;\r\n/images/dosug/2023/4/11.jpg;\r\n/images/dosug/2023/4/10.jpg;\r\n/images/dosug/2023/4/5.jpg', '', '', 'Новости', '', 0, '', '3'),
(786, '13', 'Дети дождя', 'Откровенное интервью родителей детей с аутизмом, интересное про «Дом синего слона» и про проблему постановки диагноза РАС читайте на страницах \"Областной газеты\"', '', 'https://www.ogirk.ru/wp-content/uploads/post/2023/08/img_1954-scaled.jpg', '2023-08-02', '', '', 'https://www.ogirk.ru/2023/08/02/deti-dozhdja/', 'Новости', '/news.php', 1, '', ''),
(787, '18', 'Как мы делали изделия в \"Оберег\"', 'Несколько недель назад мы начали работать над изготовлением особенных изделий для благотворительного фонда \"Оберег\". \r\nВ нашей мастерской мы собрались вместе с детками и с любовью создали полотенца, пледы, мыло, свечи, подушки и многое другое.', '<p>Несколько недель назад мы начали работать над изготовлением особенных изделий для благотворительного фонда \"Оберег\".</p>\r\n<p>В нашей мастерской \"Дом синего слона\" мы собрались вместе с детками и с любовью создали полотенца, пледы, мыло, свечи, подушки и многое другое. Это была настоящая командная работа, где каждый наш участник внес свой вклад и проявил свои таланты.</p>\r\n<p>Завтра мы отправляемся в благотворительный фонд \"Оберег\", чтобы передать все наши изделия. Мы уверены, что они принесут тепло и радость тем, кто нуждается в этой поддержке.</p>\r\n<p>Следите за нашими новостями, чтобы узнать, как помочь или просто поддержать нас в этом важном деле. Ведь вместе мы можем сделать невероятные вещи!</p>\r\n<p>Спасибо всем, кто принимал участие в этом замечательном проекте. Ваша поддержка и внимание очень важны для нас и для детей. Вместе мы создаем лучший мир для всех!</p>\r\n<p>Проект \"Тёплый дом синего слона\" реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239113&hash=844997a01b8d8b38\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>\r\n', '/images/DSS/2023/4.jpg', '2023-08-03', '/images/grants/pgrants_logo.jpg', '', '', '', '', 0, '', '2'),
(789, '18', 'Подарки для \"Оберега\"', 'Прошлая пятница оказалась особенной для нашей мастерской \"Дом Синего Слона\"! Мы с гордостью передали изделия, которые наши талантливые ребята изготовили с огромной любовью и заботой.', '<p>Прошлая пятница оказалась особенной для нашей мастерской \"Дом Синего Слона\"! Мы с гордостью передали изделия, которые наши талантливые ребята изготовили с огромной любовью и заботой.</p>\r\n<p>Эти уникальные изделия были переданы в\r\nБлаготворительный фонд «Оберег» - Иркутск , который помогает нуждающимся детям и семьям. Мы надеемся, что наши работы смогут принести радость и поддержку тем, кто в них нуждается.</p>\r\n<p>Каждый предмет, созданный в нашей мастерской, является уникальным и несет в себе особый смысл. Они не только являются прекрасным украшением для вашего дома, но и станут настоящим оберегом для вас и ваших близких.\r\n</p>\r\n<p>Мы благодарим всех наших талантливых участников, которые вложили в эти изделия свою душу и энергию. Благодаря вам, мы смогли создать нечто особенное и помочь тем, кто в нас нуждается.</p>\r\n<p>Проект \"Тёплый дом синего слона\" реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239116&hash=b247a435dc8baae7\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>\r\n', '/images/DSS/2023/5.jpg', '2023-08-07', '/images/grants/pgrants_logo.jpg', '', '', '', '', 0, '', '2'),
(790, '18', 'Чего мы добились?', 'Начинаем подводить итоги проекта \"Теплый дом синего слона\" и заполняем опросник FISH (наш адаптированный инструмент для выявления динамики в формировании профессиональных навыков). Интерпретация результатов наших ребят.', '<p>Начинаем подводить итоги проекта \"Теплый дом синего слона\" и заполняем опросник FISH (наш адаптированный инструмент для выявления динамики в формировании профессиональных навыков).</p>\r\n<p>Интерпретация результатов наших ребят:</p>\r\n<p>&#128153; <b>Игорь</b></p>\r\n<p>«Прогресс Игореши колоссальный! В начале проекта ребенок отказывался заходить в мастерские, а если уж и зашел, то проворачивал свою стереотипную цепочку: обойти кабинет по кругу, заглянуть в шкаф, урвать оттуда банку с гуашью и на выход.\r\nСейчас он занимается в группе ребят 30 минут без перерыва!</p>\r\n<p>Начинали мы с индивидуальных занятий: тренировались следовать визуальному расписанию, использовали планшетку «сначала-потом», увеличивали время нахождения в мастерской и, конечно, учились ремесленным навыкам.\r\nОдно из направлений – швейное дело. Игорь боялся звука работающего оверлока: закрывал уши, не соглашался даже просто сесть рядом с машинкой. Сейчас наш герой уверено сидит за оверлоком: одной рукой держит изделие, а другой – жмет педаль машинки. Все это происходит в компании ребят!</p>\r\n<p>Упаковать изделие пока что для Игоря самая сложная задача – там нужны аккуратность и креатив, а он на этом этапе торопится и не понимает такие абстрактные понятия как «сложи красиво», «давай аккуратнее»» - Александра Смелая, арт-терапевт, педагог \"Дома синего слона\".</p>\r\n<p>&#128153; <b>Стёпа</b></p>\r\n<p>Степан взял все направления работы. Научился выполнять все алгоритмы ремесленных навыков, которые мы ему предлагали, с минимальными подсказками. Ребенок сам может изготовить свечку или мыло с нуля! Где-то еще ему нужно подсказать закрутить крышку ароматизатора или помыть чашу, в которой плавили воск, у ребенка получается такой творческий беспорядок в момент творения. Наш Степа всегда приходит в мастерские в хорошем настроении, ему нравится находиться в компании друзей и заниматься\r\nтворчеством. Он чувствует себя уверенно, так как знает, что нужно делать. Для ребят всегда есть план занятия, их всегда ставят в известность, чем они будут заниматься. Благодаря такой комфортной обстановке дети не испытывают лишнего стресса. Гордимся Степой и всегда находимся с ним на связи, потому что изделия у него получаются отменные!» - Александра Смелая, арт-терапевт, педагог \"Дома синего слона\".</p>\r\n<p>&#128153; <b>Саша</b></p>\r\n<p>«Хочу похвалить Сашеньку! Она, как и другие ребята, с индивидуальных занятий перешла в группу. У Саши была проблема зависимости от физической подсказки во время нарезки мыльной основы или соевого воска, размешивания составов или заливки материала по формам. Она держала в руках нож, но делала это безучастно: смотрела в сторону и не надавливала на рукоятку. Сидела и ждала, пока ей помогут. Сейчас она справляется самостоятельно на 40% и мы ей гордимся! Работа продолжается и мы нацелены на лучшие результаты!» - Александра Смелая, арт-терапевт, педагог \"Дома синего слона\".</p>\r\n<p>Проект \"Тёплый дом синего слона\" реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/DSS/2023/6.jpg', '2023-08-07', '/images/grants/pgrants_logo.jpg', '', '', '', '', 0, '', '2'),
(791, '2', 'Инклюзивный лагерь на Байкале', 'Незаметно пролетела неделя для наших ребят в инклюзивном лагере в деревне Сарма. Дети с особенностями развития и их семьи провели долгожданный отпуск в море солнца, улыбок и позитива.', '<p>Незаметно пролетела неделя для наших ребят в инклюзивном лагере в деревне Сарма. Дети с особенностями развития и их семьи провели долгожданный отпуск в море солнца, улыбок и позитива.</p>\r\n<p>&#9989; Поход в Сарминское ущелье – загадочное и величественное</p>\r\n<p>&#9989; Посетили мыс Уюга – место силы священного озера Байкал</p>\r\n<p>&#9989; Заплывы по Байкалу на надувном матрасе</p>\r\n<p>&#9989; Местные жители приглашали ребят в гости</p>\r\n<p>&#9989; Игры, веселье и отдых в большой компании друзей</p>\r\n<p>«Мы уже дома. Всю дорогу нас сопровождали необыкновенные облака. Рассматривали их с детьми, видели в них зверей и птиц, очень увлекательная игра у нас вышла. Смотрите чаще в небо!»\r\n- поделилась Елена Михеева, мама Вани, мальчика с РАС.</p>\r\n<p>Проект реализуется победителем конкурса «Точка опоры»\r\nблаготворительной программы «Эффективная филантропия»\r\nБлаготворительного фонда Владимира Потанина»</p>\r\n', '/images/dosug/2023/5/5.jpg', '2023-08-07', '/images/dosug/2023/5/2.jpg;\r\n/images/dosug/2023/5/1.jpg;\r\n/images/dosug/2023/5/4.jpg;\r\n/images/dosug/2023/5/6.jpg;\r\n/images/dosug/2023/5/7.jpg;\r\n/images/dosug/2023/5/8.jpg;\r\n/images/dosug/2023/5/9.jpg;\r\n/images/dosug/2023/5/11.jpg;\r\n/images/dosug/2023/5/10.jpg;\r\n/images/dosug/2023/5/3.jpg', '', '', 'Новости', '', 0, '', ''),
(793, '12', 'Тренировки на тренажерах', 'Тренировка, проводимая на тренажерах, способствует развитию силовых качеств, координации движений, выносливости, умению владеть собственным телом. ', '<p>Знаете ли вы, что лучшее время для занятий физкультурой это ранний вечер 17-18 часов?</p>\r\n<p>В это время организм ещё не устал, обменные процессы запущены, температура тела достигает максимума, что ускоряет обмен веществ и делает мышцы более эластичными, в результате снижается вероятность получить травму во время тренировки.</p>\r\n<p>Занятия адаптивной физкультурой в \"РАСсвете\" безопасны в любое время - тренер Валерий Клоков внимательно следит за выполнением упражнений, помогает ребятам там, где нужна поддержка, корректирует программу тренировок.</p>\r\n<p>Тренировка, проводимая на тренажерах, способствует развитию силовых качеств, координации движений, выносливости, умению владеть собственным телом. </p>\r\n<p>Тренажеры позволяют зафиксировать правильную позу, необходимую для выполнения упражнения. Тренировка улучшает общее физическое состояние детей с аутизмом и повышает специальную и общую выносливость организма. </p>\r\n<p>Дети с аутизмом легко и быстро адаптируются к тренировкам на тренажерах, проявляют к некоторым из них особый интерес. </p>\r\n<p>Занятия АФК проходят при поддержке Благотворительного фонда Благотворительный фонд Владимира Потанина. Проект реализуется победителем конкурса \"Точка опоры\" благотворительной программы \"Эффективная филантропия\".</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456239900&hash=23f0a486b69f3ae4\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen class=\"social-video\"></iframe>\r\n<section>', '/images/AFK/2023/1/0.jpg', '2023-04-08', '/images/AFK/2023/1/1.jpg;\r\n/images/AFK/2023/1/2.jpg;\r\n/images/AFK/2023/1/3.jpg;\r\n/images/AFK/2023/1/4.jpg;\r\n/images/AFK/2023/1/6.jpg;\r\n/images/AFK/2023/1/5.jpg\r\n\r\n\r\n', '', '', 'Новости', '/news.php', 0, '', '');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(794, '16', 'Сертифицированные АВА-кураторы в Иркутске', 'Реестр поведенческих специалистов школы Юлии Эрц обновился! Теперь в разделе \"кураторы MBP\" можно найти специалистов \"РАСсвета\"! Специалисты нашего центра обучаются в режиме нон-стоп: прохождение курсов, участие в вебинарах и семинарах, бесчисленные тренинги...', '<p><a href=\"https://aba-kurs.com/reestr-specialistov/\">Реестр поведенческих специалистов</a> школы Юлии Эрц обновился! Теперь в разделе \"кураторы MBP\" можно найти специалистов \"РАСсвета\"!</p>\r\n<p>Практические тренинги пройдены, компетенции подтверждены, но и это еще не предел. Специалисты нашего центра обучаются в режиме нон-стоп: обучение в университетах, прохождение курсов, участие в вебинарах и семинарах, бесчисленные тренинги...</p>\r\n<p>\"Наш проект один из немногих на территории России и близлежащих стран, выпускники которого получают сертификаты, подтверждающие их компетенцию на международном уровне Behavior Analyst Certification Board, Inc.®\" - Юлия Эрц, CBA-S.<br></p>\r\n<br>\r\n</section>\r\n<br>\r\n<div class=\"help-main-list\">\r\n<a href=\"https://aba-kurs.com/reestr-specialistov/kuratory/mikheeva-elena/#startpage\" class=\"help-main\">\r\n <img class=\"help-main-image\" src=\"/images/news/2023/3/2.jpg\" style=\"border: 1px solid #000000\" alt=\"\" />\r\n </a>  \r\n <a href=\"https://aba-kurs.com/reestr-specialistov/kuratory/shapilova-elena/#startpage\" class=\"help-main\">\r\n<img class=\"help-main-image\" class=\"help-main-image\" src=\"/images/news/2023/3/3.jpg\" style=\"border: 1px solid #000000\" alt=\"\" />\r\n</a>\r\n </div>\r\n\r\n<section class=\"news detail\">\r\n<div class=\"content\">\r\n<p></p>\r\n<p><b><a href=\"https://aba-kurs.com/reestr-specialistov/kuratory/mikheeva-elena/#startpage\">Елена Михеева</a></b></p>\r\n<p>                                                               <b>Образование:</b><br>\r\nВысшее, ФГБОУ ВПО ИГУ, отделение педагогического социального и специального образования, кафедра теории и практики специального обучения и воспитания, год окончания – 2022. <br>\r\nВ настоящее время магистрант 2 курса ФГБОУ ВПО ИГУ по направлению «Психолого-педагогическое сопровождение инклюзивного образования» <br>\r\n«Специальное (дефектологическое) образование»</p>\r\n \r\n<p> <b>Дополнительное образование в сфере АВА:</b><br>\r\n Успешно окончила 4 модуля сертификационной программы.</p>\r\n<p> <b>\r\nПройденные тренинги:</b><br>\r\n «Применение базовых АВА-стратегий обучения в работе с детьми с РАС»<br>\r\n «Применение оценки VB-MAPP и выбор целей для поведенческой программы»<br>\r\n «Применение экспериментального анализа для определения функции поведения»<br>\r\n «Тренинг персонала и обеспечение процессуальной точности вмешательства»<br></p>\r\n \r\n<p><b>Количество накопленных часов практики под супервизией:</b> 1160<br>\r\n \r\n <b>\r\nКоличество часов супервизии:</b> 58<br></p>\r\n\r\n<p> <b>Дополнительная информация:</b><br>\r\n	 - Тренинг «Денверская модель раннего вмешательства», ноябрь 2019 <br>\r\n	- Тренинг Система альтернативной коммуникации PЕCS, 36 часов, март 2020, level 1; <br>\r\n	- Тренинг Система альтернативной коммуникации PECS, 20 часов, сентябрь 2020, level 2; <br>\r\n	- Сертификат об успешном завершении и сдаче международного экзамена PECS, март 2020, level 1; <br>\r\n	- Прохождение стажировки в Инклюзивном центре «Моя планета» по курсу «Практические навыки индивидуальных занятий АВА и АВА сада, с применением системы альтернативной коммуникации PECS», 6 часов, ноябрь 2021 </p>\r\n<hr class=\"low\">\r\n<p><b><a href=\"https://aba-kurs.com/reestr-specialistov/kuratory/shapilova-elena/#startpage\">Елена Шапилова</a></b></p>\r\n<p>                                                                    <b>Образование:</b><br>\r\n Высшее (Иркутский государственный педагогический институт, учитель математики и информатики)</p>\r\n <p><b>\r\nДополнительное образование в сфере АВА:</b><br>\r\n Успешно окончила 4 модуля сертификационной программы.<br></p>\r\n<p>\r\n <b>\r\nПройденные тренинги:</b><br>\r\n «Применение базовых АВА-стратегий обучения в работе с детьми с РАС»<br>\r\n «Применение оценки VB-MAPP и выбор целей для поведенческой программы»<br>\r\n «Применение экспериментального анализа для определения функции поведения»<br>\r\n «Тренинг персонала и обеспечение процессуальной точности вмешательства»</p>\r\n <p> <b>Количество накопленных часов практики под супервизией:</b> 1240<br>\r\n  <b>\r\nКоличество часов супервизии:</b> 62\r\n </p>\r\n<hr class=\"low\">\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного «Фондом президентских грантов».</p>', '/images/news/2023/3/1.jpg', '2023-08-14', '', '', '', '', '', 0, '', '4'),
(795, '12', 'Тренируем ум и тело', 'Адаптивная физкультура – это не просто зарядка. АФК – это эффективный инструмент для интеграции человека с особенностями развития в общество посредством развития физического здоровья.', '<p>Адаптивная физкультура – это не просто зарядка. Это эффективный инструмент развития ребенка.</p>\r\n<p>Над чем идет работа, когда вы приводите своего ребенка на наши занятия?\r\n</p>\r\n<p>&#129513; Ребенок учится координировать работу правого и левого полушарий с помощью нейро-упражнений, тем самым развивая взаимодействие тела и интеллекта</p>\r\n<p>&#127880; Элементы сенсорной интеграции помогают ребенку научиться ощущать свое тело (в том числе и лицо, что позволяет развивать речь)</p>\r\n<p>&#128267; Усиливается концентрация на занятии (если ребенок гиперчувствительный и постоянно отвлекается на внешние раздражители: лампочки, взмахи, раскачивания, грызет одежду)</p>\r\n<p>&#128227; Ребенок учится слушать и выполнять просьбы (развивается пассивный словарный запас ребенка, он учится следовать инструкциям педагога («сядь», «встань», «ложись», «иди сюда») или более сложным вербальным конструкциям («когда услышишь слово «гора», залезай на скалодром»)\r\n</p>\r\n<p>&#9200; Изучается понятие времени (мы используем различные визуальные подсказки, чтобы ребенок понимал, сколько нужно делать то или иное упражнение, чтобы он понимал, когда закончится занятие и можно отправится домой)</p>\r\n<p>⏱ Умение ждать (между походами упражнений есть тайм-аут, когда ребенок учится ждать следующую задачу)</p>\r\n<p>⚽ Ну и, конечно, фитнес! Индивидуальная программа адаптированных упражнений для ребенка, исходя из всех особенностей развития наших гостей! Учитываются особенности здоровья, особенности ментального развития и запрос родителей!</p>\r\n<p>АФК – это эффективный инструмент для интеграции человека с особенностями развития в общество посредством развития физического здоровья.</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240190&hash=030ca6dc7c5b72d7\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n<section>', '/images/AFK/2023/2/1.jpg', '2023-08-15', '/images/AFK/2023/2/1.jpg;\r\n/images/AFK/2023/2/2.jpg;\r\n/images/AFK/2023/2/3.jpg\r\n\r\n', '', '', 'Новости', '/news.php', 0, '', ''),
(797, '4', 'Как занятия в Рассвете помогают снизить проблемное поведение', 'Индивидуальные программы наших учеников направлены на улучшение учебного поведения, сотрудничества со взрослыми и сверстниками, развитие речи и навыков самообслуживания. Специалисты центра работают над решением актуальных проблем, включая коррекцию проблемного поведения.\r\n', '<p>Регулярные занятия в центре \"РАСсвет\" играют важную роль в развитии детей с расстройствами аутистического спектра (РАС). Индивидуальные программы направлены на улучшение учебного поведения, сотрудничества со взрослыми и сверстниками, развитие речи и навыков самообслуживания. Специалисты центра работают над решением актуальных проблем, включая коррекцию проблемного поведения.</p>\r\n<p>&#128994; Андрей: снижение самоагрессии и истерик</p>\r\n<p>Некоторые дети испытывают стресс из-за неопределенности и отсутствия четкого алгоритма действий. Андрею, как и многим детям с РАС, помогла визуальная поддержка: <br>\r\n- Планшетка \"сначала-потом\" с изображением, что будет происходить сейчас, а что потом<br>\r\n- Система альтернативной коммуникации в виде карточек с изображением предметов или действий для просьб<br>\r\n- Жетоны для определения конца учебной сессии и доступа к поощрениям.</p>\r\n<p>Сейчас Андрей спокойно и без визуальной поддержки перемещается по центру и выполняет различные задания. Жетоны все еще помогают ему чувствовать себя более уверенно, но мы постепенно от них уходим. Хотя его речь пока ограничена, он активно пытается озвучивать названия карточек, что конечно-же поощряется специалистами.</p>\r\n<p>&#128992; Лёша: преодоление страха перед чисткой зубов</p>\r\n<p>Метод систематической десенсибилизации позволил научить Лёшу спокойно реагировать на процедуру чистки зубов, когда его родители обратились к нам с этой проблемой. </p>\r\n<p>Мы всегда предупреждаем родителей: если ребенок сопротивляется при выполнении каких-либо гигиенических процедур, нельзя удерживать ребенка против его воли - таким образом вы усиливаете его негативизм и наносите ребенку эмоциональные травмы. Кроме того, ребенок растет и скоро вы физически не сможете с ним справиться.\r\nСейчас Лёша находится на промежуточном этапе обучения. Мы постепенно увеличиваем интенсивность и продолжительность процедуры. Здесь очень важно не торопиться и не прыгать с этапа на этап. Ребенок должен быть спокоен и за свои старания получать классное поощрение.</p>\r\n<p>&#128993; Аюр: следование правилам на уроках</p>\r\n<p>Родители Аюра обратились в \"РАСсвет\" с запросом объяснить мальчику, что во время занятия ему нужно следовать правилам учителя и не спорить с ним, не заваливать кучей вопросов не по теме, ведь он учится в ресурсном классе и его поведение отвлекает как его самого, так и одноклассников.</p>\r\n<p>Для Аюра был разработан поведенческий договор, благодаря которому он научился следовать правилам учителя на уроке и не отвлекаться вопросами, не относящимися к теме. Во время перемены он может свободно обсуждать интересующие его темы и заниматься любимыми делами. Его увлечением является техника, и он удивляет своими знаниями и умениями в этой области. Аюр может по памяти перечислить все модели телефонов и года их выпуска, а при знакомстве узнать вашего сотового оператора и в деталях нарисовать его логотип.</p>\r\n<p>Специалисты центра \"РАСсвет\" индивидуально подходят к каждому ребенку, учитывая его уникальные особенности и потребности. Структурные занятия и методы коррекции поведения помогают детям успешно адаптироваться и развиваться, улучшая качество их жизни.</p>\r\n<p>Проект «Передышка для мамы» реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240201&hash=0f8b32b257da13f9\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n<section>', '/images/ABA/2023/5.jpg', '2023-08-31', '/images/grants/pgrants_logo.jpg;\r\n/images/ind/2023/4.jpg;\r\n/images/ABA/2023/3.jpg;\r\n/images/ABA/2023/4.jpg\r\n\r\n\r\n', '', '', 'Новости', '/news.php', 0, '', '4'),
(798, '20', 'Роль семьи в АВА-терапии', 'Этот пост призван показан важность участия родителей в терапии. Если у вас нет ресурсов подключиться к реабилитации ребенка, не вините себя! Обратитесь за помощью к психологу, наберитесь сил и у вас всё получится.', '<p>Этот пост призван показан важность участия родителей в терапии. Если у вас нет ресурсов подключиться к реабилитации ребенка, не вините себя! Обратитесь за помощью к психологу, наберитесь сил и у вас всё получится.</p>\r\n<p>&#128214; Отрывок из книги Р. Лифа и Дж.Макэкена «Идет работа»:</p>\r\n<p>«Участие семьи в терапевтическом процессе является необходимым условием его успеха. Никто не знает вашего ребёнка лучше вас; никого, кроме вас, так не заботит его состояние; ни на ком, кроме вас, так не отражаются его проблемы. Вы проводите со своим ребёнком много времени и можете использовать это время для обобщения учебных навыков в повседневных ситуациях.</p>\r\n<p>Нередко родители сами выступают АВА-терапевтами для своего ребёнка. Однако, как слишком хорошо известно всем родителям, ребёнок с аутизмом отнимает много эмоциональной энергии, а координация работы терапевтической команды - это нелёгкий труд. Поэтому целесообразно, по возможности, использовать услуги наёмных АВА-инструкторов для проведения интенсивных занятий. Это даст родителям возможность немного отдохнуть, а то время, которое они проведут со своим ребёнком, станет более приятным и продуктивным. Родители могут использовать время, не связанное с интенсивным программным обучением, для работы над игровыми, социальными навыками и навыками самообслуживания своего ребёнка.</p>\r\n<p>Прогулка в парке, поход в продуктовый магазин рядом с домом, отправка письма или время, проведённое в гостях у родственника, дают возможность обобщать навыки и изменять поведение к лучшему.</p>\r\n<p>Точно так же приём ванны, обед, переодевание или кормление домашнего питомца - лишь некоторые из ежедневных событий, которые можно использовать для обучения ребёнка.</p>\r\n<p>Таким образом, весь день ребёнка становится частью терапевтического процесса, а родители - активными членами терапевтической команды. Постоянное вовлечение ребёнка в ежедневные заботы важно ещё и по-тому, что это препятствует его дальнейшей изоляции» &#128214;</p>\r\n<p>Рассказываем в карточках, почему еще важно подключать к терапии семью &#11015;</p>\r\n\r\n<p>Проект «Передышка для мамы» реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n', '/images/family_album/Tasya/3-2.jpg', '2023-08-22', '/images/ABA/2023/1/1.jpg;\r\n/images/ABA/2023/1/2.jpg;\r\n/images/ABA/2023/1/3.jpg\r\n\r\n\r\n', '', '', 'Новости', '/news.php', 0, '', '4'),
(801, '18', 'Процесс создания свечей в \"Доме синего слона\"', 'Наши ребята научились делать невероятно красивые свечи! И это благодаря поддержке Фонда Президентских Грантов. Свет свечей всегда создает особую атмосферу, наполняет дом теплом и уютом. ', '<p>Наши ребята научились делать невероятно красивые свечи! И это благодаря поддержке Фонда Президентских Грантов.</p>\r\n<p>Свет свечей всегда создает особую атмосферу, наполняет дом теплом и уютом. А теперь представьте, какими прекрасными и уникальными стали свечи, созданные нашими талантливыми ребятами!</p>\r\n<p>Каждая свеча - это настоящий шедевр. Причем, свечи наших ребят не только красивы, но и экологически чисты. При создании свечей используются натуральные материалы и воск, лишенный вредных добавок. Таким образом, вы получаете не только визуальное удовольствие, но и заботу о своем здоровье и окружающей среде.</p>\r\n<p>Благодаря проекту «Теплый дом синего слона» наши ребята смогли развить свой творческий потенциал и научились создавать уникальные свечи, которые уже завоевывают сердца многих людей.</p>\r\n<p>Проект \"Тёплый дом синего слона\" реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239138&hash=19d0d7506daa7620\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>\r\n', '/images/DSS/2023/8.jpg', '2023-08-23', '/images/grants/pgrants_logo.jpg', '', '', '', '', 0, '', '2'),
(804, '19', 'Подготовка ребят с РАС к школе', 'Закончились подготовительные занятия к школе, будем скучать по нашим звездочкам!\r\nВпереди их ждет длинная дорога школьной жизни с переменами, домашками, столовой и главное - с друзьями!', '<p>Закончились подготовительные занятия к школе, будем скучать по нашим звездочкам!\r\nВпереди их ждет длинная дорога школьной жизни с переменами, домашками, столовой и главное - с друзьями!</p>\r\n<p>В первый день занятий мы глубоко вдохнули - получится ли создать в классе комфортную обстановку для обучения?</p>\r\n<p>&#129513; Дело в том, что дети с расстройством аутистического спектра часто нуждаются в особенных образовательных потребностях: ресурсная зона (где ребенок может отдохнуть и расслабиться; индивидуальное сопровождение тьютора; адаптированный учебный материал (не все детки могут понимать обращенную речь).</p>\r\n<p>&#127919; Нашей целью на протяжении месяца была адаптация ребят к новым условиям жизни: новому помещению, новым людям вокруг, шуму, требованиям, правилам...</p>\r\n<p>Сегодня мы можем сказать, что адаптация проходит успешно.</p>\r\n<p>Ребятам предстоит перенести приобретенные навыки в школу.\r\nМы видим успехи наших учеников и радуемся за каждого.\r\nМы видим слабые стороны наших учеников и стараемся их скомпенсировать.\r\nОдно только нас огорчает: нехватка тьюторов для детей с РАС.</p>\r\n<p>&#128205; Тьютор - это взрослый друг, который всегда будет рядом, готовый помочь ребенку в любой момент. Это наставник, который решит любой вопрос.\r\nТьютор - это любовь, уважение и понимание.</p>\r\n<p>&#128279; Готовы ли вы <a href=\"https://rassvet-apc.ru/news/detail.php?id=772&back=news\" target=\"_blank\">стать тьютором</a> ребенка с аутизмом?</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240224&hash=15a3627bc563a3ef\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>\r\n\r\n\r\n\r\n', '/images/RK/2023/2.jpg', '2023-08-25', '', '', '', 'Новости', '/news.php', 0, '', ''),
(805, '18', 'Работа с глиной', 'На этой неделе в нашей мастерской \"Дом синего слона\" наши талантливые ребята познакомились с удивительным искусством работы с глиной. Это был первый опыт для некоторых, и они справились на ура! \r\n', '<p>На этой неделе в нашей мастерской \"Дом синего слона\" наши талантливые ребята познакомились с удивительным искусством работы с глиной. </p>\r\n<p>Это был первый опыт для некоторых, и они справились на ура! А другие уже выглядели как настоящие профессионалы, создавая потрясающие произведения искусства.\r\n</p>\r\n<p>Глядя на работы ребят, мы восхищаемся их творческим потенциалом и умением преобразовывать простые материалы в настоящие шедевры.\r\n</p>\r\n<p></p>\r\n\r\n<p>Проект \"Тёплый дом синего слона\" реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239151&hash=0298597db65d5a50\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>\r\n', '/images/DSS/2023/9.jpg', '2023-08-25', '/images/grants/pgrants_logo.jpg', '', '', '', '', 0, '', '2'),
(806, '16', '«РАСвет» учится у лучших!', 'Прошедший в новосибирском АВА-центре «Мозаика» практический тренинг ведущих экспертов в области прикладного анализа поведения Autism Partnership, зарядил наших сотрудников новыми идеями для реализации поведенческих программ.', '<p>Прошедший в новосибирском АВА-центре «Мозаика» практический тренинг ведущих экспертов в области прикладного анализа поведения Autism Partnership от программного директора Autism Partnership Hong Kong - Кан Вонг (22 года опыта работы с детьми с особенностями развития) и поведенческого консультанта Джейн Лай, зарядил наших сотрудников новыми идеями для реализации поведенческих программ.</p>\r\n<p>Однозначно, такие образовательные поездки развивают потенциал нашего центра: перенимать опыт у лучших поведенческих аналитиков в мире - отличная привычка, которая держит \"РАСсвет\" достойным представителем АВА-сообщества.</p>\r\n<p>А вот и принципы работы Autism Partnership, которые нам близки:\r\n</p>\r\n<p>&#9989;  В любой ситуации терапист должен суметь принять верное клиническое решение, если протокол задания не предусматривает ту или иную ситуацию, которая может произойти с ребенком.<br>\r\nНапример, ребенок может устать, но до конца занятия еще целых 2 часа! Мастерство тераписта в том, чтобы учесть потребности ребенка, но при это не снизить эффективность занятия!<br>\r\nА что делать при сенсорной перегрузке? Преподаватель должен применить эффективные методики и помочь ребенку справиться с этим состоянием.</p>\r\n<p>&#9989; Умение адаптировать подсказки (не нужно \"тыкать\" ребенку пальцем на верный ответ, если он не понимает указательный жест).</p>\r\n<p>&#9989; Если АВА-терапевт образованный, имеющий большой опыт работы с поведением, компетентен в вышеперечисленных пунктах, то начинающие тераписты только учатся принимать верные клинические решения. И задача куратора помочь быстрее освоить такие нюансы, чтобы процесс терапии оставался на высоком уровне!</p>\r\n<p>&#9757; На АВА-центрах России лежит миссия воспитать достойных поведенческих аналитиков, потому что получить профильное образование специалиста по прикладному анализу в ВУЗе - такой функции в нашей стране пока что нет.</p>\r\n\r\n<p>Обучение наших специалистов проходило в рамках проекта \"Передышка для мамы\". Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного «Фондом президентских грантов».</p>', '/images/training/2023/3/2.jpg', '2023-08-29', '/images/grants/pgrants_logo.jpg;\r\n/images/training/2023/3/1.jpg;\r\n/images/training/2023/3/3.jpg;\r\n/images/training/2023/3/4.jpg;\r\n/images/training/2023/3/5.jpg;\r\n/images/training/2023/3/6.jpg;\r\n/images/training/2023/3/7.jpg;\r\n/images/training/2023/3/8.jpg;\r\n/images/training/2023/3/9.jpg', '', '', '', '', 0, '', '4'),
(808, '4', 'Жетонная система поощрений: подсказки для педагогов', 'Жетонная система поощрений является эффективной методикой вмешательства, которую сравнительно легко настроить и реализовать, при условии, что все сделано правильно.', '', '/images/ABA/2023/6.jpg', '2023-09-04', '\r\n\r\n\r\n', '', 'https://vk.com/@rassvetapc-zhetonnaya-sistema-pooschrenii-podskazki-dlya-pedagogov', 'Новости', '/news.php', 1, '', ''),
(809, '19', 'Лучшая инклюзивная школа региона', 'МОУ ИРМО \"СОШ п. Молодёжный\" стала победителем регионального этапа конкурса \"Лучшая инклюзивная школа России\" и готовится к выходу на федеральный этап конкурса!', '<p>МОУ ИРМО \"СОШ п. Молодёжный\" стала победителем регионального этапа конкурса \"Лучшая инклюзивная школа России\" и готовится к выходу на федеральный этап конкурса!</p>\r\n<p>Мы бесконечно рады, что один из наших ресурсных классов для детей с аутизмом уже 4-ый год растет и развивается на базе школы п. Молодежный.</p>\r\n<p>Первая школа в Иркутской области, которая приняла модель ресурсного класса и всячески поддерживает наших ребят и по сей день!</p>\r\n<p>Инклюзии быть!</p>\r\n\r\n\r\n\r\n', '/images/RK/2023/3.jpg', '2023-09-04', '', '/images/RK/2023/3.jpg;0rP5Hu2NRbg?si=XW51gknm1OPK3uk3', '', 'Новости', '/news.php', 0, '', ''),
(811, '18,22', 'Благодарим за помощь \"Дому синего слона!\"', 'Мастерские «Дом Синего Слона» продолжают бесплатные занятия! Неравнодушные люди помогли закрыть благотворительный сбор в поддержку наших ребят! Мы бесконечно благодарны, что в мире есть люди, готовые помогать и поддерживать людей с аутизмом!', '<p>Мастерские \"Дом Синего Слона\" создали родители ребят с расстройством аутистического спектра в 2019 году, чтобы у детей была возможность развивать свой творческий потенциал в кругу друзей, получать ремесленные навыки, которые могли бы стать их профессией в будущем, общаться и дружить - и все это под курацией опытных мастеров и наставников, которые помогают ребятам справляться с трудностями, которые бывают у людей с ментальными особенностями развития. </p>\r\n<p>Помещение, материалы для творчества, педагоги и их обучение (в Иркутске не было АВА-специалистов) - все финансовые траты легли на плечи семей детей с РАС.\r\n</p>\r\n<p>За время существования наших мастерских, мы научились находить средства на помощь нашим детям:</p>\r\n<p>&#128153; благотворительные мероприятия</p>\r\n<p>&#128153; проектная деятельность</p>\r\n<p>&#128153; грантовая поддержка</p>\r\n<p>Мы учились вместе с нашими детьми находить выход из любой ситуации, учились у организаций Ассоциации \"Аутизм-Регионы\" из других городов настраивать работу нашего творческого пространства!</p>\r\n<p>Мы первые в Иркутской области организовали творческие мастерские для ребят с аутизмом, применяющие методики прикладного анализа поведения для обучения подростков ремесленным навыкам, которые проводят свои занятия абсолютно бесплатно!</p>\r\n<p>Жизнь \"Дома Синего Слона\" - череда взлетов и падений..</p>\r\n<p>Осень 2023 года предстояла быть тяжелой, финансирования на предстоящий сезон мы не нашли.\r\nОгромное количество НКО нуждается в ресурсах и обосновать, почему помощь нужна именно нам, все сложнее и сложнее.</p>\r\n<p>Но «Дом Синего Слона» продолжает бесплатные занятия!</p>\r\n<p>Неравнодушные люди помогли закрыть благотворительный сбор в поддержку наших ребят! Выход есть! Спасибо!</p>\r\n<p>Мы бесконечно благодарны, что в мире есть люди, готовые помогать и поддерживать людей с аутизмом!</p>\r\n<p>Все собранные средства будут направлены на оплату труда педагогов мастерской. Отчет о расходовании средств сбора будет добавлен в раздел отчетов.</p>\r\n', '/images/help/DSS/3.jpg', '2023-09-08', '', '', '', 'Новости', '', 0, '', ''),
(812, '16', 'Участие в Восточном экономическом форуме', 'Председатель \"АПЦ Рассвет\" Екатерина Родыгина приняла участие в работе Восточного экономического форума, который состоялся в сентябре во Владивостоке. ', '<p>Председатель \"АПЦ Рассвет\" Екатерина Родыгина приняла участие в работе Восточного экономического форума, который состоялся в сентябре во Владивостоке. </p>\r\n<p>Около 7 000 участников из разных стран  собрались на территории кампуса Дальневосточного федерального университета (ДВФУ). Четырехдневная программа форума была очень насыщенной. Много сессий по креативным индустриям, большое количество обсуждений проектов культуры и образования, а также не обошлось без дискуссий про культурный код.</p>\r\n<p>Представители 50 ведущих НКО смогли принять участие в таком масштабном мероприятии благодаря Фонду президентских грантов. Это уникальная возможность рассказать о своих проектах и найти новые знакомства.</p>', '/images/news/2023/4/2.jpg', '2023-09-15', '/images/news/2023/4/1.jpg;\r\n/images/news/2023/4/3.jpg;\r\n/images/news/2023/4/4.jpg', '', '', '', '', 0, '', ''),
(813, '20', 'Ошибки в реализации туалетного тренинга', 'Почему ребенок не может научиться ходить на горшок? В этой статье мы собрали самые распространенные ошибки родителей при обучении ребенка навыкам туалета.', '<p>Почему ребенок не может научиться ходить на горшок? В этой статье мы собрали самые распространенные ошибки родителей при обучении ребенка навыкам туалета.</p>\r\n<p>&#10060; Отсутствие подкрепления</p>\r\n<p>Часто для ребенка с аутизмом похвала и громкие овации не являются достаточно мотивационным условием, чтобы начать ходить на горшок и проявлять инициативу к походу в туалет, поэтому туалетный тренинг не двигается с места.\r\nНаша задача инсценировать такую ситуацию, где ребенок за свои туалетные старания будет получать супер-подкрепление, которое будет воодушевлять его обращать внимания на свои физиологические потребности.</p>\r\n<p>&#10060; Подгузник</p>\r\n<p>Если вы взялись за туалетный тренинг, назад пути нет. Подгузник вы больше не используете. Без исключений.\r\nВ ситуациях, где ребенок ходит в подгузниках и его периодически усаживают на горшок меньшая вероятность закрепить нужное поведение, нежели в ситуациях, где ребенок может ощутить последствия в виде «мокрых штанишек».\r\nЗапаситесь сменной одеждой и заварите себе ромашковый чай, наш вам совет.</p>\r\n<p>&#10060; Наказание</p>\r\n<p>Наставления, нравоучения, недовольства, крик и физические наказания не работают! Нельзя настоятельно требовать от ребенка того, чему вы его еще не научили.</p>\r\n<p>&#10060; Силой усаживать ребенка на горшок</p>\r\n<p>Такой опыт для ребенка будет стрессовым, вы лишь усилите избегание туалетных дел. Ребенок будет зажиматься, терпеть. В общем, он перестанет вам доверять! Это плохо!</p>\r\n<p>&#10060; Бросать тренинг на полпути</p>\r\n<p>Приучение к туалету - это процесс. Результаты у всех детей разные: быстро\\медленно. Если вы тренируетесь несколько дней, но удачного похода на горшок еще не было – это нормально. Не отступайте. Чем дольше ребенок ходит в подгузнике, тем сложнее приучить его к туалету. Дорогу осилит идущий!</p>\r\n<p>Проект \"Сам себе помощник\" реализуется при поддержке Благотворительного фонда «Вклад в будущее».</p>\r\n\r\n', 'https://sun4-18.userapi.com/impg/Clna6dDOh-fpZgWmtzLS_4MC9GlXjfmkpH5f9Q/Kf3aU27Sv1Y.jpg?size=807x807&quality=95&sign=be0b961cf7e99eb62a7420309192249f&c_uniq_tag=o2cC4-XigciYNVTD1a9ft7bgx09TfGalF4-RVUAS3p4&type=album', '2023-09-12', '/images/grants/VK_VB.jpg', '', '', 'Новости', '/news.php', 0, '', '1'),
(814, '3', 'Семинары по самообслуживанию', '16 сентября мы провели важную встречу в библиотеке имени Молчанова-Сибирского, где снова обсуждали навыки самообслуживания. Мы хотим, чтобы наши дети с особенностями развития были самостоятельными в будущем, и это является одной из ключевых задач.', '<p>Одной из задач нашего проекта \"Сам себе помощник\" является информирование специальных педагогов и родителей детей с РАС о социально-бытовых навыках, позволяющих ребенку с РАС независимо и самостоятельно функционировать, а также о методах обучения и диагностирования этих навыков, с учётом возрастных и ментальных особенностей детей с аутизмом.</p>\r\n<p>29 мая поведенческие специалисты \"РАСсвета\" Мария Максимова и Екатерина Никулина провели бесплатный вебинар \"Методы обучения детей с РАС бытовым навыкам и соблюдению санитарно-гигиенических правил\".\r\nНа вебинаре слушатели получили много полезных прикладных знаний - то, что сразу можно взять в работу со своим ребенком или учеником. Разобрали:\r\nособенности формирования бытовых навыков,\r\nинструменты построения программ для навыков самообслуживания,\r\nвозрастные особенности формирования навыков. </p>\r\n<p>Порадовала заинтересованная аудитория и множество вопросов: \"Как научить сморкаться? Как приучить ребенка с РАС ходить в туалет? Как научить завязывать шнурки? Как приучить навыку опрятности?\" и даже затронули вопросы по серьезнее про половое созревание и гигиену в этот период...</p>\r\n<p>16 сентября состоялась важная встреча в библиотеке имени Молчанова-Сибирского, где снова обсуждали навыки самообслуживания. Мы хотим, чтобы наши дети с особенностями развития были самостоятельными в будущем, и это является одной из ключевых задач.</p>\r\n<p>Большое спасибо всем, кто пришел и проявил интерес к данной теме. Ваше участие и вопросы помогли нам понять, что эта тема затрагивает многих родителей и близких, которые хотят поддержать своих детей в их развитии.\r\n</p> \r\n<p>На встрече мы обсудили много важных аспектов самообслуживания, таких как умение одеваться, кушать, общаться и многое другое. Мы делились опытом, обменивались советами и находили новые решения вместе.\r\n</p>\r\n<p>Было замечательно видеть, как все присутствующие были заинтересованы и готовы делиться своими историями. Вместе мы создаем поддерживающую и вдохновляющую среду, где каждый может найти нужную информацию и поддержку.</p>\r\n<p>Наша встреча в библиотеке была только началом. Мы понимаем, что это долгий и постоянный процесс, поэтому мы будем продолжать проводить подобные мероприятия и обмен опытом.</p>\r\n<p>Встреча прошла при поддержке Благотворительного фонда «Вклад в будущее».</p>', '/images/seminar/2023/6/1.png', '2023-09-18', '/images/grants/VK_VB.jpg;\r\n/images/seminar/2023/4/2.jpg;\r\n/images/seminar/2023/6/2.png;\r\n/images/seminar/2023/6/3.png;\r\n/images/seminar/2023/6/4.png;\r\n/images/seminar/2023/6/5.png;\r\n/images/seminar/2023/6/6.png\r\n', '', '', 'Новости', '/news.php', 0, '', '1'),
(815, '19', 'Диагностика навыков социального взаимодействия в ресурсном классе', 'В ресурсном классе МОУ ИРМО «СОШ поселка Молодежный» работа над развитием социального взаимодействия детей ведется постоянно! Но для успешного развития навыков необходимо сначала провести диагностику и определить базовый уровень, чтобы понимать на что именно сделать упор при обучении.', '<p>Новый учебный год уже в разгаре. Мальчишки и девчонки из ресурсного класса школы пос. Молодежный тоже приступили к занятиям. В этом году класс стал участником проекта «Ресурсный класс. Доступная среда», организованного Ассоциацией \"Аутизм-Регионы\" при поддержке Фонда президентских грантов. А это значит, что ребята снова будут поддерживаться поведенческими специалистами на протяжении всего учебного года.</p>\r\n<p>Сегодня мы хотим рассказать о важнейших для наших ребят навыках - навыках социального взаимодействия.</p>\r\n<p>Что такое социальное взаимодействие?\r\nЕсли говорить простыми словами, это такие действия, которые являются реакцией на поведение других людей и направлены на то, чтобы вызвать ответную реакцию с их стороны.</p>\r\n<p>Люди с аутизмом испытывают проблемы с тем, чтобы начать и поддерживать социальное взаимодействие, понимать невербальную коммуникацию и социальные ситуации.\r\n</p>\r\n<p>Социальные отношения – это важная часть жизни любого человека и оставлять ребят с РАС одних, не учить их общаться и не помогать налаживать отношения с другими людьми – это большая ошибка, которая не оставит аутичным людям шанса быть равноправной частью общества.</p>\r\n<p>В ресурсном классе МОУ ИРМО «СОШ поселка Молодежный» работа над развитием социального взаимодействия детей ведется постоянно! Учителя, тьюторы, куратор, супервизор, администрация школы, ученики регулярных классов – все подключаются к социализации наших ребят! Но для успешного развития навыков необходимо сначала провести диагностику и определить базовый уровень, чтобы понимать на что именно сделать упор при обучении.</p>\r\n<p>Диагностика навыков детей в ресурсном классе производится 2 раза в год: на начало учебного года и на конец учебного года с помощью диагностического инструмента ABBLS-R. По результатам тестирования строится индивидуальный план обучения и отслеживается прогресс в навыках.</p>\r\n<p>Доктор Джеймс Партингтон в «Руководстве ABBLS-R» пишет: «Развивать навыки социального взаимодействия нужно всегда, вне зависимости от уровня развития речевых навыков. Если речевые навыки пока не очень хорошо развиты, то необходимо обучать ребенка приветствовать других людей, ждать своей очереди, приглашать других принять участие в совместной деятельности и возвращать игрушки.</p>\r\n<p>Если речевые навыки ребенка сформированы достаточно хорошо, то подходящими целями работы будут обучение ребенка просить предметы у сверстников или задавать им вопросы, делиться, первым приветствовать сверстника (не только отвечать не приветствия), сотрудничать со сверстниками, разговаривать на интересующие их темы.</p>\r\n<p>Очень важно учить ребенка обращать внимание на действия других людей и понимать их интересы.</p>\r\n<p>Множество изменений в нашем поведении в качестве обратной связи помогут ребенку понять, правильно ли он сейчас себя ведет, так же, как помогает в этом появление других последствий, приятных и не совсем.</p>\r\n<p>Чтобы уметь общаться с другими людьми, важно уметь обращать внимание на изменение темы разговора, изменение интонации, темпа речи, выражения лица, другие изменения поведения собеседника»</p>\r\n<p><b>Навыки социального взаимодействия Вани</b></p>\r\n<p>На момент тестирования навыков социального взаимодействия в мае 2020 года Иван мог спокойно находится в кругу сверстников, но взаимодействовал с ними только с помощью тьютора (например, поделиться игрушкой, поиграть в подвижные игры, сделать совместное задание).</p>\r\n<p>Уже в мае 2021 года Ваня мог самостоятельно присоединиться к деятельности сверстников, если они делают что-то интересное, следовать простым инструкциям других ребят (например, «Побежали!»), попросить интересующую его вещь с помощью альтернативной коммуникации!</p>\r\n<p>К маю 2023 года Ваня научился приветствовать и прощаться с одноклассниками, смотреть им в лицо при общении, просить у них нужные ему вещи.</p>\r\n<p>Сентябрьская диагностика показывает, что уровень навыков социального взаимодействия Вани остался на том же уровне, что и в мае. А это значит, что он за лето не утратил никакие из них! Но, конечно, еще есть над чем работать и в новом учебном году.</p>\r\n<p>«Безусловно, навыки социального взаимодействия детей улучшаются с каждым годом. Дети взаимодействуют друг с другом и с другими детьми из регулярных классов во время перемен. Некоторым детям все еще требуется помощь тьютора. Во время такого взаимодействия педагоги и диагностируют уровень социальных навыков, наблюдая за детьми и фиксирую данные в мониторинговой таблице.\r\nНаш Ваня отлично подключается к подвижным играм со сверстниками.\r\nК нему приходила девочка из регулярного класса и предлагала побегать с ней. Что мы увидели? Иван без проблем принял ее приглашение в игру и взаимодействовал с ней: они играли в «Догоняшки».</p>\r\n<p>Другая девочка приходила к Владу. Они вместе занимались любимым занятием - рисованием: девочка сидела рядом с ним и просила поделиться листком и другими материалами для творчества. Влад делился и общался со своей новой соседкой по парте!</p>\r\n<p>Безусловно, у всех детей ресурсного класса есть прирост навыков социального взаимодействия – мы это видим!» - Елена Михеева, учитель-дефектолог ресурсного класса.</p>\r\n<p>Наблюдая за успехами ребят с РАС, обучающихся в ресурсном классе, можно сделать вывод, что грамотно организованное инклюзивное пространство и профессиональное сопровождение поведенческими специалистами способствует развитию социальных навыков детей с аутизмом.</p>\r\n<p>АНО \"АПЦ Рассвет\" - участник проекта «Ресурсный класс. Доступная среда». Благодарим организатора проекта Ассоциацию \"Аутизм-Регионы\". Проект реализуется при поддержке Фонда президентских грантов.</p>\r\n\r\n\r\n', '/images/RK/2023/13/1.jpg', '2023-09-18', '/images/RK/2023/13/1.jpg;\r\n/images/RK/2023/13/2.jpg;\r\n/images/RK/2023/13/3.jpg;\r\n/images/RK/2023/13/4.jpg;\r\n/images/RK/2023/13/5.jpg;\r\n/images/RK/2023/13/6.jpg', '', '', 'Новости', '/news.php', 0, '', ''),
(816, '18', 'Арт-терапия для детей с особенностями развития', 'Что такое арт-терапия? Это метод, включающий использование различных художественных материалов и техник для достижения психологического и эмоционального благополучия.', '<p>Открыта запись на занятия арт-терапией для детей с особенностями развития! </p>\r\n<p>Уникальная возможность помочь вашему ребенку раскрыть свой творческий потенциал и развить навыки самовыражения!\r\n</p>\r\n<p>&#128310; Что такое арт-терапия? Это метод, включающий использование различных художественных материалов и техник для достижения психологического и эмоционального благополучия.\r\n</p>\r\n<p>&#128310; Для кого эти занятия? Для детей с особенностями развития, такими как аутизм, СДВГ, задержка речи и другие.</p> \r\n<p>&#128310; Арт-терапия позволяет ребятам выразить свои эмоции, улучшить моторику, развить творческое мышление и самооценку. Способствует развитию\r\nкоммуникативных навыков и формирует особое пространство, в котором ребенок с РАС может воспроизвести предшествующий опыт построения контакта с окружающей средой.</p>\r\n<p>&#128310; Занятия проводит опытный арт-терапевт. Все занятия проходят в индивидуальном формате, учитывая потребности каждого ребенка.</p>\r\n<p>&#128310; Когда и где проходят занятия? Занятия проводятся по предварительной записи в нашей мастерской на базе АПЦ «РАСсвет» по адресу: Байкальская 234в/2\r\n</p>\r\n<p>&#128310; Как записаться? Просто позвоните по телефону +7 (952) 623-66-75</p>\r\n<p>Не упустите возможность помочь вашему ребенку раскрыть свой потенциал и найти новые способы самовыражения!\r\n</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239173&hash=ebf235bbfef36a56\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n&nbsp; &nbsp; &nbsp;\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239186&hash=22e6dc4caad8b921\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2023/10.jpg', '2023-09-23', '', '', '', '', '', 0, '', ''),
(817, '20', '9 важнейших коммуникативных навыков', 'Функциональные коммуникативные навыки имеют большое значение для всех учеников, поэтому эти навыки необходимо развивать во всех средах, в том числе дома. Некоторые из этих навыков включают в себя навыки говорящего, а другие - навыки слушателя.', '<p>Аннотация к статье Promoting Functional Communication within the Home Andy Bondy, Catherine Horton, and Lori Frost Pyramid Educational Consultants, Inc.</p>\r\n<p>Функциональные коммуникативные навыки имеют большое значение для всех учеников, поэтому эти навыки необходимо развивать во всех средах, в том числе дома. В то время, когда перемещения вне дома ограничены, многим семьям нужно искать для своих детей возможности применения уже имеющихся функциональных навыков коммуникации или даже освоения новых навыков. В данной статье приведено описание девяти важнейших коммуникативных навыков и множество примеров того, как семьи могут повлиять на развитие этих важных навыков у детей. Некоторые из этих навыков включают в себя навыки говорящего, а другие - навыки слушателя.</p>\r\n<p>9 важнейших коммуникативных навыков, которым надо учить детей с расстройствами аутистического спектра:</p>\r\n<p>&#9989; Просьба о желаемом</p>\r\n<p>&#9989; Просьба о помощи</p>\r\n<p>&#9989; Просьба о перерыве</p>\r\n<p>&#9989; Согласие \"ДА\"</p>\r\n<p>&#9989; Отказ \"НЕТ\"</p>\r\n<p>&#9989; Реакция на просьбу \"Подожди\" (принятие отказа)</p>\r\n<p>&#9989; Выполнение инструкций</p>\r\n<p>&#9989; Следование расписанию</p>\r\n<p>&#9989; Спокойные переходы</p>\r\n<p>Подробнее о каждом навыке читайте в наших карточках &#129179;</p>\r\n<br>\r\n<p></p>\r\n<div class=\"help-achievements-list\">\r\n<div class=\"help-achievement\">\r\n<img class=\"help-achievement-image\" src=\"/images/ABA/2023/2/2.jpg\" alt=\"\" />\r\n</div>\r\n<div class=\"help-achievement\">\r\n<img class=\"help-achievement-image\" src=\"/images/ABA/2023/2/3.jpg\" alt=\"\" />\r\n</div>\r\n<div class=\"help-achievement\">\r\n<img class=\"help-achievement-image\" src=\"/images/ABA/2023/2/4.jpg\" alt=\"\" />\r\n</div>\r\n<div class=\"help-achievement\">\r\n<img class=\"help-achievement-image\" src=\"/images/ABA/2023/2/5.jpg\" alt=\"\" />\r\n</div>\r\n<div class=\"help-achievement\">\r\n<img class=\"help-achievement-image\" src=\"/images/ABA/2023/2/6.jpg\" alt=\"\" />\r\n</div>\r\n<div class=\"help-achievement\">\r\n<img class=\"help-achievement-image\" src=\"/images/ABA/2023/2/7.jpg\" alt=\"\" />\r\n</div>\r\n<div class=\"help-achievement\">\r\n<img class=\"help-achievement-image\" src=\"/images/ABA/2023/2/8.jpg\" alt=\"\" />\r\n</div>\r\n<div class=\"help-achievement\">\r\n<img class=\"help-achievement-image\" src=\"/images/ABA/2023/2/9.jpg\" alt=\"\" />\r\n</div>\r\n<div class=\"help-achievement\">\r\n<img class=\"help-achievement-image\" src=\"/images/ABA/2023/2/10.jpg\" alt=\"\" />\r\n</div>					\r\n</div>\r\n<p>Проект «Передышка для мамы» реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', '/images/ABA/2023/2/1.jpg', '2023-09-22', '/images/grants/pgrants_logo.jpg', '', '', 'Новости', '/news.php', 0, '', '4'),
(819, '13,19', 'Класс для детей с РАС открыли в школе № 17', 'Класс для детей с расстройствами аутического спектра открыли в школе № 17 Иркутска. Об этом событии рассказали \"Вести - Иркутск\" в выпуске от 25 сентября 2023 года.', '<p>Класс для детей с расстройствами аутического спектра открыли в школе № 17 Иркутска. Об этом событии рассказали \"Вести - Иркутск\" в выпуске от 25 сентября 2023 года.</p>\r\n<p>В классе реализуется адаптированная программа, которая позволяет ученикам с ограниченными возможностями здоровья усваивать знания наравне со сверстниками. Сейчас занятия посещают 7 ребят.</p>\r\n<p>– Абсолютно все имеют право на получение образования. Цель проекта – создать в обычной школе такие условия для особенных ребятишек, чтобы они могли социализироваться, общаться с ровесниками. Родители наших учеников, понимая это, поддержали идею формирования такого класса, – прокомментировала директор школы № 17 Ирина Курсакова.</p>\r\n<p>Уроки проводят квалифицированные педагоги. Каждого ребенка сопровождает наставник. В обучении применяют индивидуальный подход. На занятиях присутствуют родители.\r\n</p>\r\n<p>– К сожалению, детям с аутизмом уделяют не так много внимания. И открытие такого класса в нашем городе – это большой шаг в развитии инклюзивного образования. Здесь нужна специальная система обучения с использованием прикладного анализа поведения. Все организовано на высоком уровне. Я очень рада, что мой сын может ходить в школу, как все, – поделилась иркутянка Ариадна Чиркова.     </p>\r\n<p>Ресурсный класс создали по поручению мэра города Руслана Болотова. Во время встречи с гражданами в региональной общественной приемной «Единой России» с такой инициативой к нему обратилась руководитель адаптационно-педагогического центра «Рассвет» Екатерина Родыгина. В дальнейшем специалисты организации работали над формированием программы вместе с департаментом образования администрации Иркутска.</p>\r\n<p>Публикация на сайте <a href=\"https://vestiirk.ru/news/klass-dlia-osobennykh-detei-otkryli-v-shkole-no-17-v-irkutske/\" target=\"_blank\">\"Вести-Иркутск\"</a></p>\r\n<p>Публикация на странице <a href=\"https://vk.com/vesti_irk?w=wall-35929536_144958\" target=\"_blank\">«Вести - Иркутск» в ВК</a></p>\r\n<section class=\"video-detail\">\r\n                                        <iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240311&hash=191768e7c959645a\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen \r\nclass=\"social-video\"></iframe>                    </section>\r\n<p>В печатном номере  газеты “Аргументы и факты” № 40 (2237) от 4-10 октября 2023 года также вышла статья об открывшемся  ресурсном классе в школе № 17. </p>\r\n<p>Журналист Анна Садовникова провела день с учениками 1 “г” и рассказала о том как был открыт класс для детей с аутизмом и как  проходит процесс их обучения.</p>\r\n<p>В 1 “г”  СОШ № 17 г. Иркутска учатся 7 детей. Помимо учителя осваивать образовательную программу детям помогают тьюторы, а также куратор АНО “АПЦ Рассвет”. Обучение проходит с использование методов прикладного анализа поведения.</p>\r\n\r\n<p><a href=\"/images/RK/2023/14/2.jpg\" target=\"_blank\" class=\"button\">Посмотреть статью</a></p>', '/images/RK/2023/4.jpg', '2023-10-17', '', '', '', 'Новости', 'news.php', 0, '', ''),
(820, '1', 'Опрос для родителей, воспитывающих детей с РАС', 'У ребёнка болят зубы? Без паники! Идём к стоматологу. Заполни форму и поделись проблемой, мешающей ребёнку лечить зубы, а мы сделаем всё возможное, чтобы помочь.\r\n', '<p>У ребёнка болят зубы? Без паники! Идём к стоматологу. Заполни форму по ссылке ниже и поделись проблемой, мешающей ребёнку лечить зубы, а мы сделаем всё возможное, чтобы помочь.</p>\r\n<p>Мы проводим опрос, направленный на выявление трудностей, с которыми сталкиваются родители и дети с расстройствами аутистического спектра при посещении стоматолога. Мы работаем над созданием более комфортных и безопасных для наших детей условий при посещении стоматологии.</p>\r\n<p>Опросник займет у вас около 15 минут. Вопросы касаются аспектов посещения стоматолога, начиная от первого контакта и до проведения процедур. Просим постараться описать ситуацию , с которой сталкиваетесь именно вы, как можно детальнее, чтобы нам лучше понять ситуацию.</p>\r\n<p>Просим откликнуться на нашу просьбу и поделиться опросником с другими родителями, нуждающимися в решении данной проблемы.</p>\r\n<p>Давайте вместе сделаем поход к стоматологу приятным и безопасным для всех детей!</p>\r\n<p><a class=\"button\" href=\"https://forms.yandex.ru/u/6511b5ba50569045b9220ac7/\">Заполнить форму</a></p>\r\n<p>Спасибо за поддержку! ❤️</p>\r\n', '/images/news/50.jpg', '2023-09-30', '', '', '', 'Новости', '', 0, '', '');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(821, '4', 'Как заставить молчуна говорить?', 'Речь развивается исключительно благодаря мотивации! Ищите то, от чего ребенок в восторге и с позитивным настроем работайте над развитием спонтанных вокализаций и имитации.', '<p>Запуск и развитие речи, конечно, самая запрашиваемая тема для обсуждения.</p>\r\n<p>Рекомендации поведенческого аналитика:</p>\r\n<p>Речь развивается исключительно благодаря мотивации! Ищите то, от чего ребенок в восторге (игрушки, щекотка, вкусняшки или какие-то совершенно неожиданные вещи) и исключительно с позитивным настроем работайте над развитием спонтанных вокализаций и имитации.</p>\r\n<p>Не стоит обесценивать прогресс ребенка! Если есть задержка речи, мы не требуем от ребенка, чтоб он сразу произносил слова. Сначала мы подкрепляем абсолютно любые звуки, которые издает ребенок и радуемся, если он даже просто сказал нам что-то непонятное, потом подкрепляем все более и более сложные реакции, которые доступны ребенку: конкретные звуки, слоги, потом уже слова из 2-х слогов, 3-х, и где-то там дальше предложения.</p>\r\n<p>Не нужно зацикливаться лишь на том, чтобы ребенок уже наконец-то нам что-то сказал.\r\nПараллельно развивайте сотрудничество и коммуникацию (вводите пекс или жесты: любой ребенок, даже неговорящий, должен уметь выражать свои потребности: учите делать это социально приемлемым образам, иначе будет нежелательное поведение, тяжелое нежелательное поведение: истерики, агрессия, самоагрессия). Никто не может дать вам прогноз, сколько именно будет развиваться умение говорить, а ребенок растет, его потребности растут, он должен уметь доносить их до других людей!</p>\r\n<p>Маршрут, по которому двигался наш ученик Илья, прежде, чем научился повторять слова:</p>\r\n<p>1. Научился спокойно сидеть напротив педагога и внимательно смотреть на него</p>\r\n<p>2. Научился повторять действия с предметами за взрослым (если учитель стучит палочкой по барабану, Илья находит палочку на столе и повторяет действие среди барабана, маракаса и гармошки)</p>\r\n<p>3. Научился повторять действия большой моторики (если учитель хлопает, Илья повторяет действие)</p>\r\n<p>4. Научился повторять действия мелкой моторики (если учитель соединяет указательные пальцы, Илья повторяет)</p>\r\n<p>5. Научился повторять действия артикуляции (если учитель показывает язык, Илья повторяет)</p>\r\n<p>6. К артикуляции подключились звуки (Илья повторяет за учителем легкие звуки из одного слога)</p>\r\n<p>7. Повторение слов из 2-3 слогов</p>\r\n<p>Из-за того, что путь к вербальному поведению был разбит на этапы, Илья учился с удовольствием и без стресса. У него хорошо получалось, его хвалили и за его старания он получал любимые поощрения.</p>\r\n<p>Что сейчас важно? Сохранять мотивацию ребенка повторять, постепенно усложнять реакции, вводить навык наименования и учить ребенка просить то, что он хочет.</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240333&hash=81d8738d67cdcd14\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n&nbsp; &nbsp; &nbsp;\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240361&hash=927b2ceb97136ad0\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n<section>', '/images/ABA/2023/7.jpg', '2023-10-14', '', '', '', 'Новости', '/news.php', 0, '', ''),
(822, '18', 'Фестиваль «Счастье для всех»', 'У вас ребенок с особенностями развития и вы до сих пор думаете, что с такими детьми лучше не появляться в обществе? Это не так! С такими детками нужно чаще выходить в общество.', '<p>У вас ребенок с особенностями развития и вы до сих пор думаете, что с такими детьми лучше не появляться в обществе? Это не так! С такими детками нужно чаще выходить в общество.</p>\r\n<p>Например, стоит посещать инклюзивные фестивали. У нас в Иркутске как раз прошел инклюзивный фестиваль «счастье для всех» и наш центр вместе с мастерскими проводили мастер-класс и читали лекцию по навыкам самообслуживания у детей с особенностями развития.\r\n</p>\r\n<p>А самое интересное это то, что все происходило на территории зоосада! \r\n</p>\r\n<p>На фестивале дети с особенностями развития встречались с обычными детьми, делали творческие задания вместе и учились новым навыкам. Ведь это не только возможность для особенных детей участвовать в мероприятиях, но и шанс для обычных детей узнать больше о разных способах жизни и дружить с разными людьми.</p> \r\n<p>Инклюзивные фестивали - это прекрасная возможность показать всем, что каждый ребёнок имеет право на счастье\r\n</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239200&hash=9fd4b53f79e63586\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2023/11.jpg', '2023-10-05', '', '', '', '', '', 0, '', ''),
(824, '18', 'Чем полезна глина?', 'Глина - это отличный способ для сенсорного развития. Наши дети с особенностями развития часто испытывают особую потребность в тактильном восприятии. Они любят осязать разные текстуры и ощущать прикосновение к различным материалам.', '<p>Почему глина является таким полезным материалом для наших ребят?</p>\r\n<p>Во-первых, глина - это отличный способ для сенсорного развития. Наши дети с особенностями развития часто испытывают особую потребность в тактильном восприятии. Они любят осязать разные текстуры и ощущать прикосновение к различным материалам. Глина предоставляет им возможность насытить эту потребность, так как она мягкая, пластичная и приятная на ощупь. Дети могут лепить, сжимать, размазывать глину, получая удовольствие от тактильных ощущений.</p>\r\n<p>Во-вторых, работа с глиной развивает моторику рук и координацию движений. Для наших детей с особенностями развития это очень важно, так как они иногда испытывают трудности с мелкой моторикой и координацией. Лепка из глины требует точности и умения контролировать движения рук. Постепенно, через практику и повторение, дети улучшают свои навыки и развивают моторику.\r\n</p>\r\n<p>Кроме того, работа с глиной способствует развитию воображения и творческого мышления. Наши дети могут свободно выражать свои идеи и фантазии через лепку. Они могут создавать различные формы, фигурки, абстрактные композиции. Глина дает возможность развить творческий потенциал и самовыражение.</p>\r\n<p>Не менее важно, что работа с глиной способствует развитию концентрации и внимания. Наши дети учатся сосредотачиваться на задаче, следить за деталями и доводить свои работы до конца. Это очень важные навыки, которые помогут им в будущем.</p>\r\n<p>Итак, глина - это замечательный материал для наших детей с особенностями развития. Она развивает сенсорику, моторику, воображение и концентрацию.</p>\r\n<p>Проект реализуется при поддержке «Губернского собрания общественности Иркутской области»</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239213&hash=8693843b17dbbd3c\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2023/12.jpg', '2023-10-13', '/images/grants/GS.png', '', '', '', '', 0, '', '14'),
(825, '10', 'Проект «Давай дружить»', 'Статус проекта: завершен<br>\r\nСроки реализации: 18.09.2023 – 18.09.2024<br>\r\nИсточник софинансирования: Губернское собрание общественности Иркутской области\r\n', '<p><b>Статус проекта:</b> завершен<br>\r\n<b>Сроки реализации:</b> 18.09.2023 – 18.09.2024<br>\r\n<b>Источник софинансирования:</b> Губернское собрание общественности Иркутской области</p>\r\n<p></p>\r\n<p><b>Цель проекта:</b></p>\r\n<p>Сформировать навыки социального взаимодействия у 20 детей с РАС с 5 до 12 лет г. Иркутска и Иркутского района посредством их обучения на групповых занятиях в мастерских с последующим вовлечением в массовые мероприятия со сверстниками</p>\r\n<p><b>Задачи:</b></p>\r\n<p>1. Показать 20 родителям детей с РАС, как справляться с нежелательным поведением и способствовать взаимодействию своего ребенка со сверстниками, путем участия в мастер-классах и группах поддержки.</p>\r\n<p>2. Поспособствовать взаимодействию 20 детей с РАС и 10 нейротипичных сверстников посредством участия в совместных мероприятиях.</p>\r\n<p>3. Научить 20 детей с РАС общаться со сверстниками на групповых занятиях в мастерских и инклюзивных мастер-классах.</p>\r\n<p>4. Научить 20 детей с РАС общаться со сверстниками на групповых занятиях в мастерских и инклюзивных мастер-классах.</p>\r\n<p>5. Рассказать 90 детям с нейротипичным развитием об особенностях детей с РАС на уроках добра.</p>\r\n<p><a href=\"/news.php?project=14\" class=\"button\">Публикации о реализации проекта</a></p>', '/images/grants/GS.png', '2023-09-28', '', '', '', 'Гранты', '/pages/grants.php', 0, '', ''),
(826, '19', 'Как работа ресурсного класса помогает включать ребенка с аутизмом в общеобразовательную школу', 'Чтобы процесс инклюзии ребенка с аутизмом проходил мягко и с пользой для всех участников процесса, сначала ребята учатся индивидуально и по мере готовности включаются в групповые формы работы в ресурсном классе.', '<p>\"Ресурсный класс - это не отдельный класс для обучающихся с ОВЗ в общеобразовательной школе. Это специальная образовательная модель, позволяющая организовать для обучающегося, в зависимости от его потребностей и возможностей, инклюзивное образование и индивидуальное обучение. При этом ученик выходит в общеобразовательный класс, а ресурсный класс - это место, где ему оказывается поддержка специалистов.\" - выдержка из Практического пособия \"Ресурсный класс. Опыт организации обучения и внеурочной деятельности детей с аутизмом в общеобразовательной школе\"</p>\r\n<p>Другими словами, ресурсный класс - это место подготовки ученика с аутизмом к обучению в общеобразовательном классе. И одним из важнейших факторов в этом процессе является команда специалистов ресурсного класса, владеющих методами прикладного анализа поведения.</p>\r\n<p>Чтобы процесс инклюзии ребенка с аутизмом проходил мягко и с пользой для всех участников процесса, сначала ребята учатся индивидуально и по мере готовности включаются в групповые формы работы в ресурсном классе. Учителя и тьюторы, под руководством консультантов по прикладному анализу поведения (куратора и супервизора) обучают ребят не только академическим навыкам, но в первую очередь - навыкам социально приемлемого поведения, навыкам коммуникации с другими детьми и взрослыми, навыкам самостоятельности и многому другому. Индивидуальная программа ребят очень многогранна!</p>\r\n<p>На групповых уроках в ресурсном классе ребята приобретают навыки, необходимые для обучения в общеобразовательных классах: соблюдать правила поведения, выполнять фронтальные инструкции, поднимать руку, выходить к доске, самостоятельно работать над заданием.</p>\r\n<p>Когда у ребенка с аутизмом формируется учебное поведение и навыки групповой работы, он может начинать постепенно выходить на уроки в общеобразовательный класс в сопровождении тьютора.</p>\r\n<p>Все дети в силу своих возможностей включаются в инклюзивный процесс. Ребята, которые не посещает уроки в регулярных классах, взаимодействуют со сверстниками на общих переменах, мероприятиях обратной инклюзии, досуговых мероприятиях.</p>\r\n<p>О процессе включения ребят из ресурсного класса школы п. Молодежный на уроки в регулярные классы нам рассказали их педагоги.</p>\r\n<p>\"Сначала ребятам было сложно тихо и смирно сидеть целый урок, поэтому включение в общеобразовательный процесс происходило постепенно: сначала ребята начали выходить на уроки на очень короткое время и это были уроки ИЗО, физкультуры и музыки. И только после того как были освоены навыки красиво и спокойно сидеть за партой, слушать учителя, писать в тетради, решать примеры, ребята стали выходить на уроки русского языка и математики в регулярный класс.</p>\r\n<p>Если ребёнок затрудняется выполнить какое-то задание, то на помощь приходит тьютор, который своевременно даёт подсказки, тем самым делая процесс обучения безошибочным. Успешность ребёнка в этом случае создаёт дальнейшую мотивацию к процессу обучения в школе.\"</p>\r\n<p>Опыт ресурсного класса поселка Молодежный показывает, что процесс включения ребят с аутизмом в общеобразовательные классы не только не понижает качество обучения других учеников, а, напротив, может способствовать его повышению, ведь учитель регулярного класса имеет возможность приобрести навыки, полезные для всех учеников. Научившись искать подход к ребенку с аутизмом, учитель всегда сможет найти подход и к любому другому ребенку.</p>\r\n<p>АНО \"АПЦ Рассвет\" - участник проекта «Ресурсный класс. Доступная среда». Благодарим организатора проекта Ассоциацию \"Аутизм-Регионы\". Проект реализуется при поддержке Фонда президентских грантов и благотворительного фонда \"Абсолют - Помощь\".</p>\r\n\r\n\r\n', '/images/RK/2023/15/2.jpg', '2023-10-27', '/images/RK/2023/15/1.jpg;\r\n/images/RK/2023/15/2.jpg;\r\n/images/RK/2023/15/3.jpg;\r\n/images/RK/2023/15/4.jpg;\r\n/images/RK/2023/15/5.jpg;\r\n/images/RK/2023/15/6.jpg', '', '', 'Новости', '/news.php', 0, '', ''),
(827, '18', 'Инклюзивный урок добра', '24 октября в школе пос. Молодежный состоялся урок добра, где ребята 1 класса совместно с учениками ресурсного класса делали поделку из воздушного пластилина.', '<p>24 октября в школе пос. Молодежный состоялся урок добра, где ребята 1 класса совместно с учениками ресурсного класса делали поделку из воздушного пластилина.</p>\r\n<p>Урок добра - это урок, в рамках которого нейротипичным сверстникам рассказывается о проблемах и способах взаимодействия с ребятами с расстройствами аутистического спектра. Варианты проведения уроков доброты могут быть разные.\r\nВажно учитывать возраст обучающихся и чередовать деятельность: информативный и практический блоки.</p>\r\n<p>«Ребята внимательно и с удовольствием слушали Елену Сергеевну, отвечали на ее вопросы, делились друг с другом историями принятия особенностей других людей, к чему мягко подвела их учитель. Сначала разговор был о том, что все люди разные: цвет кожи, цвет волос, черты лица, характер, возраст, а потом речь пошла об отличиях в поведении. Елена Сергеевна спросила, замечали ли ребята что-то необычное в поведении других людей. Ребята делились своими впечатлениями, описывали забавные и неловкие ситуации и все вместе находили ответы, на возникающие вопросы» - поделилась с нами арт-терапевт мастерских «Дом Синего Слона», Александра Смелая.\r\n</p>\r\n<p>На второй части урока ребята знакомились с учениками ресурсного класса. Дети представились и расселись на свободные места. Первоклашки встретили их тепло и с заботой!</p>\r\n<p>Сюрпризом для всех стал мастер-класс от Александры Смелой, арт-терапевта, клинического психолога мастерских «Дома Синего Слона» - изготовление очаровательной овечки из воздушного пластилина.</p>\r\n<p>Все дети лепили поделку по образцу, соблюдая пошаговую инструкцию, которую разработала Александра Алексеевна специально для ребят с РАС.\r\nОсобенность восприятия информации людей с аутизмом не всегда позволяет им копировать действия за другим человеком, не отставая от общего темпа, но если им эффективно подсказать - добавить визуальную опору в виде инструкции - они справятся с задачей.</p>\r\n<p>Обратная связь от детей общеобразовательного класса говорит сам за себя: после урока дети побежали обниматься и делиться впечатлениями к гостям, приглашать их на уроки и просили провести подобный урок еще раз!</p>\r\n<p>Теперь ребята знают, что тот самый мальчик, который ходит по школе в наушниках - вовсе не слушает музыку, а пытается укрыться от шума на общей перемене и это нормально. Теперь малыши знают, что делать, если они встретят неговорящего ребенка в коридоре, который изо всех сил будет пытаться поиграть с ними в мяч, но не сможет им сказать это словами.</p>\r\n<p>Проект реализуется при поддержке «Губернского собрания общественности Иркутской области»</p>\r\n', '/images/RK/2023/15/7.jpg', '2023-10-27', '/images/grants/GS.png;\r\n/images/RK/2023/15/2.jpg;\r\n/images/RK/2023/15/8.jpg;\r\n/images/RK/2023/15/6.jpg;\r\n/images/RK/2023/15/9.jpg;\r\n/images/RK/2023/15/10.jpg;\r\n/images/RK/2023/15/11.jpg;\r\n/images/RK/2023/15/12.jpg', '', '', '', '', 0, '', '14'),
(828, '13,18', 'О нас в журнале \"В хорошем вкусе\"', 'В октябрьском номере первого светского журнала Иркутска \"В хорошем вкусе\" вышла статья об одном из направлений деятельности \"Рассвета\" - мастерских \"Дом синего слона\". Прочитать статью можно в онлайн-версии издания на странице 36.', '', '/images/smi/35.jpg', '2023-10-31', '', '', 'https://debonton.ru/archive/читать-онлайн-журнал-в-хорошем-вкусе-9', 'Новости', '/news.php', 1, '', ''),
(829, '18', 'Инклюзивный мастер-класс', 'Инклюзивные мастер-классы по рисунку в технике штампования для детей с РАС с участием нейротипичных сверстников прошли на базе АНО \"АПЦ Рассвет\" и мастерских \"Дом синего слона\". ', '<p>Инклюзивные мастер-классы по рисунку в технике штампования для детей с РАС с участием нейротипичных сверстников прошли на базе АНО \"АПЦ Рассвет\" и мастерских \"Дом синего слона\".</p>\r\n<p>Штампинг - интересная техника рисования, доступная малышам и очень интересная для детишек постарше. Она дает простор фантазии и стимулирует воображение. </p>\r\n<p>Шедевры живописи вышли из-под спонжиков и штампов ребят, а сколько эмоций было на лицах наших художников!</p>\r\n<p>Мероприятие прошло в дружной компании, где каждый мог чувствовать себя хо-ро-шо &#128153;</p>\r\n<p></p>\r\n<p></p>\r\n<p>Проект \"Давай дружить\" реализуется при поддержке «Губернского собрания общественности Иркутской области»</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239232&hash=c27fcdbb26af85ee\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2023/13.jpg', '2023-11-03', '/images/grants/GS.png', '', '', '', '', 0, '', '14'),
(830, '13,19', '«Это не мой крест — это мое счастье»', 'Светлана - куратор ресурсного класса МБОУ г.Иркутска СОШ №17 г.Иркутска стала героем статьи Алены Кашпаровой, на портале ircity.ru!\r\nИскренне, по-доброму, честно - рекомендуем к прочтению &#128153;', '', 'https://cdn.iportal.ru/preview/news/articles/102fdfdd0e10b733084406a0286a614e30f6611ba_666_444_c.jpg.webp-portal', '2023-11-08', '', '', 'https://ircity.ru/text/health/2023/11/08/72876989/', 'Новости', '/news.php', 1, '', ''),
(831, '3', 'Практическая АВА-стажировка', 'С 30 октября по 10 ноября в \"Рассвете\" прошла двухнедельная практическая АВА-стажировка. Наши эксперты поделились с участниками ценными знаниями и навыками, необходимыми для работы с детьми с аутизмом и другими нарушениями развития.', '<p>С 30 октября по 10 ноября в \"Рассвете\" прошла двухнедельная практическая АВА-стажировка. Наши эксперты поделились с участниками ценными знаниями и навыками, необходимыми для работы с детьми с аутизмом и другими нарушениями развития.</p> \r\n<p>Программа стажировки включала в себя множество интересных и практических занятий. Мы разобрали на практике основные принципы и понятия прикладного анализа поведения, методы эффективные для обучения детей с РАС навыкам функциональной коммуникации, вербальным навыкам, навыкам гигиены и самообслуживания, в том числе навыку ходить на горшок. Кроме того, наши стажеры узнали, как преодолеть фобии ребенка с аутизмом и научить его заходить в новые места, посещать врача и многое другое.\r\n</p>\r\n<p>Обучение проводили опытные поведенческие специалисты адаптационно-педагогического центра \"РАСсвет\", педагоги ресурсных классов для детей с расстройствами аутистического спектра.</p>\r\n<p>Мы хотели бы поблагодарить всех, кто принял участие в стажировке, за доверие, которое вы нам оказали. Без вашей поддержки и участия наша стажировка не смогла бы быть такой успешной. Ваши вопросы, комментарии и активное участие помогли нам улучшить наше обучение и сделать его максимально полезным для всех участников.</p>\r\n<p>Мы верим, что полученные вами навыки и знания найдут свое применение в вашей повседневной жизни и работе. Мы надеемся, что вы сможете использовать их для достижения новых высот и преодоления любых вызовов, с которыми вы столкнетесь.</p>\r\n<p>Мы действительно вложили в это обучение все наши силы и энергию, чтобы сделать его интересным и полезным для вас. И ваши отзывы – это настоящий бальзам на душу! Спасибо, что выбрали нас и доверили нам ваше обучение.</p>\r\n<section class=\"video-detail\"><iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240458&hash=a246fe738b421495\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe></section>', '/images/seminar/2023/7/1.jpg', '2023-11-10', '/images/seminar/2023/7/10.jpg;\r\n/images/seminar/2023/7/2.jpg;\r\n/images/seminar/2023/7/3.jpg;\r\n/images/seminar/2023/7/4.jpg;\r\n/images/seminar/2023/7/5.jpg;\r\n/images/seminar/2023/7/6.jpg;\r\n/images/seminar/2023/7/7.jpg;\r\n/images/seminar/2023/7/8.jpg;\r\n/images/seminar/2023/7/9.jpg;\r\n/images/seminar/2023/7/11.jpg;\r\n/images/seminar/2023/7/12.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', ''),
(832, '18', 'Какие навыки развивает шитье?', 'Шитье на швейной машинке – это замечательное занятие, которое помогает развивать множество навыков у детей с особенностями развития. Они улучшают мелкую моторику рук, развивают творческое мышление, тренируют терпение и сосредоточенность.', '<p>Сегодня мы расскажем вам о том, какие навыки можно развить, если научить ребенка с особенностями развития шить на швейной машинке.</p>\r\n<p>Первое, что шитье на швейной машинке развивает – это мелкую моторику рук. Дети учатся контролировать движения пальцев и рук, чтобы справиться с тканью и педалью машинки. Это особенно важно для детей с трудностями в двигательной сфере, так как они могут улучшить свои навыки мелкой моторики и координации движений.</p>\r\n<p>Кроме того, шитье на швейной машинке развивает творческое мышление.\r\n</p>\r\n<p>Шитье на швейной машинке также способствует развитию терпения и сосредоточенности. Дети учатся сосредотачиваться на мелких деталях и выполнять задачи до конца. Это особенно важно для детей с особенностями развития, так как им может быть сложно сосредоточиться на одном деле в течение длительного времени.</p>\r\n<p>Наконец, шитье на швейной машинке помогает детям развивать уверенность в себе и самооценку. Когда они видят, что могут создавать что-то своими руками, это повышает их чувство достижения и уверенности в себе.</p>\r\n<p>В заключение, шитье на швейной машинке – это замечательное занятие, которое помогает развивать множество навыков у детей с особенностями развития.</p>\r\n<p>Проект \"Давай дружить\" реализуется при поддержке «Губернского собрания общественности Иркутской области»</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239233&hash=e681726035704ac2\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2023/14.jpg', '2023-11-09', '/images/grants/GS.png', '', '', '', '', 0, '', '14'),
(833, '16', 'Конференция \"Инклюзивное образование - инклюзивное общество\"', 'С 8 по 10 ноября в Университете \"Сириус\" проходила II междисциплинарная научно-практическая конференция \"Инклюзивное образование - инклюзивное общество\". В ней приняла участие соучредитель \"РАссвета\" и учитель ресурсного класса Мария Максимова.', '<p>С 8 по 10 ноября в Университете \"Сириус\" проходила II междисциплинарная научно-практическая конференция \"Инклюзивное образование - инклюзивное общество\".\r\nКонференция проводилась Центром когнитивных исследований Университета «Сириус» в партнерстве с Благотворительным фондом «Вклад в будущее» (генеральный партнер) и Ассоциацией \"Аутизм-Регионы\" (социальный партнер).</p>\r\n<p>В конференции приняла участие соучредитель \"РАссвета\" и учитель ресурсного класса Мария Максимова.</p>\r\n<p>Мария поделилась своим отзывом о конференции: \"Меня впечатлили масштабы мероприятия. И то, сколько энергии во всех этих людях, которым небезразлично будущее особенных людей. Огромная благодарность Ассоциации \"Аутизм-Регионы\" за профессиональную программу, дружескую поддержку, новые контакты, возможность в кратчайшие сроки познакомиться с коллегами и изучить их опыт по интересным и содержательным выступлениям.\"</p>\r\n<p>На конференции освещались такие важные темы, как:</p>\r\n<p>- Позитивные изменения и нерешенные задачи жизненного маршрута человека с аутизмом в России</p>\r\n<p>- Доказательные подходы к обучению детей с РАС и сопутствующими нарушениями: наука и практика</p>\r\n<p>- Какой может быть взрослая жизнь человека с РАС?</p>\r\n<p>- Доказательная медицина в помощь специалистам и родителям</p>\r\n<p>- Грантовая поддержка проектов и инклюзивных практик: приоритеты и критерии грантодателей</p>\r\n<p>- Прекурсоры поведенческих расстройств при РАС и основные стратегии вмешательства</p>\r\n<p>Смотрите записи трансляций II междисциплинарной научно-практической конференции \"Инклюзивное образование - инклюзивное общество\" в <a href=\"https://vk.com/video/playlist/-174296278_10\" target=\"_blank\">ПЛЕЙЛИСТЕ</a> на странице Ассоциации \"Аутизм-Регионы\". \r\n</p>\r\n<p>Чтобы было легче ориентироваться в материалах, рекомендуем воспользоваться <a href=\"https://inclusive.sirius.ru/\" target=\"_blank\">ПРОГРАММОЙ</a> конференции.\r\n</p>\r\n<p>АНО \"АПЦ Рассвет\" - участник проекта \"Ресурсный класс. Доступная среда\" - благодарит Ассоциацию \"Аутизм-Регионы\", Фонд президентских грантов и Благотворительный фонд «Абсолют-Помощь» за многолетнюю поддержку инклюзивного образования в России.</p>\r\n', '/images/news/2023/5/0.jpg', '2023-11-16', '/images/news/2023/5/2.jpg;\r\n/images/news/2023/5/3.jpg;\r\n/images/news/2023/5/5.jpg;\r\n/images/news/2023/5/6.jpg;\r\n/images/news/2023/5/7.jpg;\r\n/images/news/2023/5/8.jpg;\r\n/images/news/2023/5/9.jpg;\r\n/images/news/2023/5/4.jpg', '', '', '', '', 0, '', ''),
(834, '18', 'Готовим новогоднее мыло', 'Никто не останется без подарков на Новый год! Наши ребята настоящие волшебники-готовят подарочное мыло! А чем полезны занятия по изготовлению мыла для детей с аутизмом?', '<p>Никто не останется без подарков на Новый год &#127876; Наши ребята настоящие волшебники-готовят подарочное мыло!</p>\r\n<p>Пробовали когда-нибудь создать собственное мыло?\r\nСоставляли ли вы сами цвет и аромат мыла? Выбирали ли вы форму, в которой будет ваше душистое произведение?\r\nА долгожданная распаковка, что же там в итоге получилось?</p>\r\n<p>Для наших ребят все эти этапы приготовления мыла не несут в себе таинство - это скорее механическая работа по алгоритму, где нет возможности посенсориться (опасно!горячо!).</p>\r\n<p>Начиная от нарезки мыльной основы-заканчивая уборкой рабочего места - все это работа.</p>\r\n<p>Тогда что дают мастерские ребятам с аутизмом?</p>\r\n<p>&#9989; социализация</p>\r\n<p>&#9989; умение работать в группе</p>\r\n<p>&#9989; приобретение ремесленных навыков</p>\r\n<p>&#9989; ребенок учится слушать мастера и выполнять алгоритмы работы</p>\r\n<p>&#9989; развитие моторики и внимания</p>\r\n<p></p>\r\n<p>Проект \"Давай дружить\" реализуется при поддержке «Губернского собрания общественности Иркутской области»</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239239&hash=2cb037af58724c4b\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2023/17/1.jpg', '2023-11-15', '/images/grants/GS.png;\r\n/images/DSS/2023/17/1.jpg;\r\n/images/DSS/2023/17/2.jpg;\r\n/images/DSS/2023/17/3.jpg;\r\n/images/DSS/2023/17/4.jpg;\r\n/images/DSS/2023/17/5.jpg;\r\n/images/DSS/2023/17/6.jpg', '', '', '', '', 0, '', '14'),
(835, '20', 'Ребенок не умеет ходить в туалет', 'Бывает, что дети в дошкольном и подростковом возрасте испытывают трудности с походом в туалет. Отсутствие навыка похода в туалет – это отсутствие данного поведения в репертуаре ребенка. Как сформировать нужное поведение - читайте в этой статье.', '<p>Бывает, что дети в дошкольном и подростковом возрасте испытывают трудности с походом в туалет. Отсутствие навыка похода в туалет – это отсутствие данного поведения в репертуаре ребенка.  Как и при коррекции любой другой поведенческой проблемы, следует воспринимать ее не как симптом какого-либо диагноза, а как проблематичное поведение. Отсюда, первым шагом в решении туалетной проблемы будет сбор и анализ данных.</p>\r\n<p>На начальном этапе следует записывать данные в течение как минимум двух недель, на ежедневной основе. Следует записывать:</p>\r\n<p>1. Дату и точное время всех испражнений<br>\r\n2. Время всех приемов пищи и жидкости<br>\r\n3. Место, в котором ребенок справил нужду<br>\r\n4. Во что ребенок был одет<br>\r\n5. Пописал или покакал?</p>\r\n<p>Эти данные помогут нам понять:</p>\r\n<p>&#10067; Может поход по нужде происходит примерно в одно и тоже время<br>\r\n&#10067; Может неудачные инциденты происходят, когда ребенок надевает неудобные лосины, которые сложно снять в ответственный момент<br>\r\n&#10067; Может быть стоит обратить внимание на место \"аварии\", например, если ребенок часто писается у окна, у нас будет сигнал быть готовыми!</p>\r\n<p>Далее определяем возможную причину неудачи в туалетных делах:</p>\r\n<p>⚠Медицинские причины<br>\r\n⚠Отказ от сотрудничества (ребенок отказывается садиться на горшок\\унитаз, кричит, дерется, кусается, терпит, «зажимается»)<br>\r\n⚠Наличие ритуалов и стереотипного поведения (справляет нужду, только когда полностью голый или только когда находится один в помещении)<br>\r\n⚠Отсутствие навыка (смотрим данные: если мочеиспускание не зависит от стереотипий и других специфичных ситуаций)</p>\r\n<p>Что нам нужно на пути к успешному походу в туалет?</p>\r\n<p>1. Выбор поощрения. Оно должно быть супер-желанным для ребенка, от чего он по-настоящему в восторге! Зарезервируйте это поощрение только за туалетные дела и не давайте его в других ситуациях! За неделю до тренинга изолируйте ребенка от выбранного поощрения. Если это мультики, значит, неделя без мультиков.</p>\r\n<p>2. На начало туалетного тренинга избавьтесь от памперсов и запаситесь чистой сменной одеждой. Если ребенок никогда не носил трусы, уделите время тому, чтобы он привык к новому виду одежды.</p>\r\n<p>3. Рекомендуем начинать туалетный тренинг с положения «сидя». Даже если это мальчик.</p>\r\n<p>4. Поставьте цель и разбейте ее на этапы. Придерживайтесь плана, не гоните веред паровоза, соблюдайте шаги.</p>\r\n<p>«Ребенок отказывается садиться на горшок: упирается руками и ногами, кричит и плачет. Даже если горшок просто стоит в комнате, он может вообще отказываться в нее войти»</p>\r\n<p>В подобных ситуациях (боязнь горшка, унитаза, туалетной бумаги) мы проводим процедуру систематической десенсибилизации. Мы постепенно помогаем ребенку снижать чувствительность к его фобии. В описанной выше ситуации мы разбили работу на этапы – целью было спокойно писать в горшок, соблюдая график походов в туалет.</p>\r\n<p>\r\n&#9461; Замеряем, на каком расстоянии  Замеряем, на каком расстоянии у ребенка начинается проблемное поведение.</p>\r\n<p>&#9462; Начинаем подкреплять спокойное поведение ребенка, не более чем через 1 секунду, при приближении к горшку.</p>\r\n<p>&#9463; Если ребенок спокоен, хвалим и даем ему его супер-приз.</p>\r\n<p>&#9464; Если ребенок отказывается сотрудничать, подкрепление не даем, уходим на предыдущий этап, где ребенок спокоен. В этом случае нам нужно делать шаги еще меньше, чтоб они были еле заметны для ребенка.</p>\r\n<p>&#9465; Если все идет хорошо, подкрепляем каждый шаг на встречу к горшку.</p>\r\n<p>&#9466; Когда мы подошли к горшку, можно предложить его сначала потрогать, потом сесть рядом, потом присесть на горшок на 1 секунду, потом на 5 и так далее.</p>\r\n<p>&#9467; Когда ребенок будет спокойно сидеть на горшке минуту, мы начинаем давать поощрение и похвалу только за то, что ребенок пописал.</p>\r\n<p>&#9468; Тут поможет наш график походов в туалет, который мы собирали 2 недели. В какое время, вероятнее всего, ребенок может пописать? Садим ребенка в эти промежутки и готовимся к тому, что сейчас ребенок пописает в туалет и мы подарим ему весь мир, все конфеты и все мультики. И так каждый раз, пока ребенок не начнет ходить на горшок стабильно.</p>\r\n<p>Если ребенок не пописал или описался в трусы, не фокусируемся на этом. Тем более, не ругаем ребенка!</p>\r\n<p>Наладить отношения с горшком можно за один день, но может и понадобиться время! Главное, придерживаться плана и быть готовым в любой момент предоставить самое лучшее поощрение!</p>\r\n<p>Проект \"Сам себе помощник\" реализуется при поддержке Благотворительного фонда «Вклад в будущее».</p>\r\n\r\n', '/images/ABA/2023/3/1.jpg', '2023-07-01', '/images/grants/VK_VB.jpg', '', '', 'Новости', '/news.php', 0, '', '1'),
(836, '20', 'И снова о туалетном тренинге', 'Продолжаем серию статей о туалетном тренинге. В этой статье мы расскажем о том как научить ребенка вытирать попу. Если есть проблема брезгливого отношения ребенка к данной процедуре, прорабатываем сначала эту проблему.', '<p>Продолжаем серию статей о туалетном тренинге. Предыдущие статьи на эту важную тему можно прочитать <a href=\"https://rassvet-apc.ru/events/detail.php?id=835&back=news\" target=\"_blank\">Здесь</a> и <a href=\"https://rassvet-apc.ru/events/detail.php?id=813&back=news\" target=\"_blank\">Здесь</a></p>\r\n<p>В этой статье мы расскажем о том как научить ребенка вытирать попу. Если есть проблема брезгливого отношения ребенка к данной процедуре, прорабатываем сначала эту проблему.</p>\r\n<p>&#9989; Постепенно снижаем тревожность ребенка к тому, что он может испачкаться. В дело вступают всеразличные пенки, краски, еда и.т.д. - сенсорная интеграция.</p>\r\n<p>&#9989; Учим ребенка ухаживать за собой.<br>\r\n- наглядно изучаем понятия чисто\\грязно<br>\r\n- знакомим ребенка с гигиеническими принадлежностями (туалетная бумага, салфетки)<br>\r\n- учим ребенка вытирать грязные места (лучше начинать с рук, ног, постепенно передвигаясь к попе).</p>\r\n<p>&#9989;  Встраиваем вытирание попы в цепочку!<br>\r\n1. Зайти в туалет<br>\r\n2. Открыть крышку унитаза<br>\r\n3. Снять штаны<br>\r\n4. Сесть<br>\r\n5. Пописать\\покакать<br>\r\n6. Встать<br>\r\n7. Оторвать туалетную бумагу (не весь рулон, а нужное количество)<br>\r\n8. Вытереть попу<br>\r\n9. Выбросить бумагу в унитаз<br>\r\n10. Надеть штаны<br>\r\n11. Смыть испражнения<br>\r\n12. (возможно, тут можно встроить уборку ёршиком)<br>\r\n13. Помыть руки</p>\r\n<p>Каждый из этих пунктов - это тоже цепочка и чтобы ничего не забыть, возможно, ребенку понадобится визуальная напоминалка прямо в туалете.</p>\r\n<p>&#9989; Используем поощрение за улучшенные реакции ребенка.</p>\r\n<p>Конечно, мы не ожидаем, что ребенок с первого раза блестяще справится с такой гигиенической процедурой. Наша задача шейпить (шейпинг - это процесс постепенного увеличения желаемого поведения, разбивая его на более мелкие шаги и усиливая то поведение, которое приближает к целевому) поведение ребенка для достижения все лучших и лучших результатов.</p>\r\n<p>Проект \"Сам себе помощник\" реализуется при поддержке Благотворительного фонда «Вклад в будущее».</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240459&hash=db5c28d75321ca5e\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/ABA/2023/3/2.jpg', '2023-11-13', '/images/grants/VK_VB.jpg', '', '', 'Новости', '/news.php', 0, '', '1'),
(837, '19', 'Очные супервизии в ресурсных классах', 'На прошлой неделе в ресурсные классы МОУ СОШ №17 и МОУ ИРМО «СОШ п. Молодежный» и на очную супервизию приехала наш супервизор из Санкт-Петербурга - Соломатина Тамара Викторовна. Это были четыре дня плотной и плодотворной  работы!  ', '<p>На прошлой неделе в ресурсные классы МОУ СОШ №17 и МОУ ИРМО «СОШ п. Молодежный» и на очную супервизию приехала наш супервизор из Санкт-Петербурга - Соломатина Тамара Викторовна. \r\nЭто были четыре дня плотной и плодотворной  работы с ребятами, тьюторами, куратором и учителями!  </p>\r\n<p>По итогам были даны общие рекомендации для классов:</p>\r\n<p>&#9989; Снижение тьюторской поддержки на фронтальных уроках.</p>\r\n<p>&#9989; Создавать ситуаций во время перемен для развития навыков коммуникации со сверстниками ( подвижные игры, игра по очереди, настольные игры).</p>\r\n<p>&#9989; Даны рекомендации по каждому ребенку по формированию навыков индивидуальной программы обучения.</p>\r\n<p>Также Тамара Викторовна провела собрание с родителями и подробно ответила на интересующие вопросы по дальнейшей работе классов.</p>\r\n<p>Будем снова ждать Тамару Викторовну  в конце учебного года!</p>\r\n<p>АНО \"АПЦ Рассвет\" - участник проекта «Ресурсный класс. Доступная среда». Благодарим организатора проекта Ассоциацию \"Аутизм-Регионы\". Проект реализуется при поддержке Фонда президентских грантов и благотворительного фонда \"Абсолют - Помощь\".</p>\r\n\r\n\r\n', '/images/RK/2023/16/1.jpg', '2023-11-21', '/images/RK/2023/16/7.jpg;\r\n/images/RK/2023/16/2.jpg;\r\n/images/RK/2023/16/3.jpg;\r\n/images/RK/2023/16/4.jpg;\r\n/images/RK/2023/16/5.jpg;\r\n/images/RK/2023/16/6.jpg', '', '', 'Новости', '/news.php', 0, '', ''),
(838, '19', 'Опыт коррекции проблемного поведения в ресурсном классе', 'Многие дети с аутизмом имеют поведенческие особенности, которые не только исключают их из социальной среды, но и могут  быть опасными для жизни и здоровья самого ребенка. Рассказали как поведенческие специалисты помогли решить опасную для здоровья проблему одного из учеников ресурсного класса школы пос. Молодежный.', '<p>Многие дети с аутизмом имеют поведенческие особенности, которые не только мешают их независимому функционированию (обучению в школе, социализации,  досугу), исключают из социальной среды, но и могут  быть опасными для жизни и здоровья самого ребенка. Читайте в этой статье как поведенческие специалисты помогли решить опасную для здоровья проблему одного из учеников ресурсного класса школы пос. Молодежный.</p>\r\n<p><b>Какая возникла проблема?</b></p>\r\n<p>&#129094; Ученик: 12 лет, образовательный маршрут 8.4</p>\r\n<p>&#129094; Имеет нарушение речи, сопутствующие нарушения – сенсорно-моторная алалия \r\n</p>\r\n<p>&#129094; Проблемное поведение: накручивает и дергает волосы у себя на голове до образования визуально-видимых залысин. После этого может съесть вырванные волосы.</p>\r\n<p>Данное поведение прежде всего опасно для здоровья самого ученика. В желудке образуется безоар, который можно удалить только хирургическим путем.\r\n</p>\r\n<p>Безоар (желудочный) – инородное тело, образующееся в результате скопления в полости желудка различных по своему происхождению частиц (грубых волокон, волос), не способных перевариться.\r\n</p>\r\n<p>&#128073; Цель поведенческого вмешательства: \r\nЧерез 2 месяца количество эпизодов проблемного поведения- “дергать волосы” снизится как минимум на 80% во время нахождения на уроках в школе. \r\n</p>\r\n<p><b>Алгоритм работы с проблемным поведением:</b></p>\r\n<p>1. Выбор поведения для коррекции и его точное описание.<br>\r\n2. Сбор данных и определение функции поведения.<br>\r\n3. Выбор стратегий и методов коррекции поведения.<br>\r\n4. Применение протокола и мониторинг процесса.<br>\r\n5. Изменение программы и обобщение приобретенных навыков.</p>\r\n<p>Проведенный сбор данных об эпизодах проблемного поведения помог предположить его функцию - сенсорная аутостимуляция. Исходя из функции был разработан план по коррекции этого поведения.</p>\r\n<p>Были использованы проактивные процедуры (направленные на предотвращение и снижение мотивации к проявлению проблемного поведения):</p>\r\n<p>&#9989; Социальная история в картинках.</p>\r\n<p>&#9989;Сенсорное насыщение в школе (удовлетворение сенсорных потребностей безопасным способом, оральное и тактильное насыщение  предметами - массажеры, сенсорные игрушки, массажер для головы, кукла с волосами, бусы, зубная нить.) На парте всегда лежало что-то, чем можно занять руки. \r\n</p>\r\n<p>&#9989; Также в план была включена процедура DRO (Differential Reinforcement of Other Behavior -дифференциальное подкрепление отсутствия поведения)- процедура, которая предполагает поощрение учащегося, если он НЕ проявляет проблемного поведения в течение определенного промежутка времени. То есть, производится дифференциальное подкрепление (поощрение при определенных условиях) отсутствия поведения в заданном периоде времени. \r\n </p>\r\n<p>С помощью применения методов прикладного анализа поведения и поддержки поведенческих специалистов мы добились существенного снижения частоты проблемного поведения.\r\n</p>\r\n<p><img class=\"news-image\" src=\"/images/RK/2023/17/2.jpg\" alt=\"Картинка\"></p>\r\n<p><b>Что способствовало положительной динамике по коррекции проблемного поведения ученика:</b> </p>\r\n<p>1. Точный сбор данных<br>\r\n2. Комбинация нескольких процедур прикладного анализа поведения<br>\r\n3. Сильное подкрепление<br>\r\n4. Условия коррекции поведения- ресурсный класс<br>\r\n4. Ресурсы - обучение происходило один на один с тьютором<br>\r\n5. Генерализация с другими сотрудниками команды ресурсного класса<br>\r\n6. Реализация и поддержание протокола родителями ученика  в домашних условиях\r\n</p>\r\n<p></p>\r\n<p></p>\r\n<p></p>\r\n<p></p>\r\n<p></p>\r\n\r\n<p>АНО \"АПЦ РАСсвет\" - участник проекта \"Ресурсный класс. Доступная среда\". Благодарим организатора проекта  Ассоциацию \"Аутизм-Регионы\".\r\nПроект реализуется при поддержке Фонд президентских грантов и Благотворительный фонд «Абсолют-Помощь»</p>\r\n\r\n\r\n', '/images/RK/2023/17/0.jpg', '2023-11-28', '', '', '', 'Новости', '/news.php', 0, '', ''),
(840, '16', 'О прикладном анализе поведения на курсах в ИРО', 'Три дня очных лекций прошли  в Институте развития образования Иркутской области на курсах повышения квалификации «Организация, планирование и методы обучения в школе детей с РАС и другими нарушениями в развитии». Один из дней был посвящен прикладному анализу поведения.', '<p>Три дня очных лекций прошли  в Институте развития образования Иркутской области на курсах повышения квалификации «Организация, планирование и методам обучения в школе детей с РАС и другими нарушениями в развитии». </p>\r\n<p>На лекции собралось более 30 учителей из общеобразовательных и специальных (коррекционных) школ города Иркутска и Иркутской области. Это была уникальная возможность для педагогов поделиться опытом, обменяться идеями и узнать о научно-доказанных методах обучения.</p>\r\n<p>Один из дней обучения был посвящен прикладному анализу поведения.  Спикером лекций о ПАП стала Михеева Елена Сергеевна -  куратор поведенческих программ (МВР), начальник экспертного отдела нашего центра. Она рассказала о том как методы прикладного анализа поведения помогают организовать учебный процесс в общеобразовательной школе в ресурсном классе для детей с РАС.</p>\r\n<p>Мы стремимся к тому, чтобы как можно больше специалистов могли применять в своей практике работы с детьми только научно-доказанные методы, ведь с ними процесс обучения становится не только эффективнее для развития ребенка, но и интереснее для самого специалиста.</p>\r\n<p>Спасибо слушателям за интерес и вовлеченность в процесс работы с детьми с аутизмом. Вместе с вами ещё больше детей смогут получать эффективную помощь.</p>', '/images/news/2023/6/1.jpg', '2023-11-29', '/images/news/2023/6/1.jpg;\r\n/images/news/2023/6/2.jpg;\r\n/images/news/2023/6/3.jpg', '', '', '', '', 0, '', ''),
(842, '18', 'Подарки ручной работы', 'Как вам идея в этом году искать подарки среди ручной работы? Не покупать очередные носки, наборы для душа, маски, косметику, конфеты и т д… А выбрать что-то сделанное вручную, в единственном экземпляре. Передать через подарок эмоции, память, а не только «нужность и полезность»!', '<p>Как вам идея в этом году искать подарки среди ручной работы? Не покупать очередные носки, наборы для душа, маски, косметику, конфеты и т д… А выбрать что-то сделанное вручную, в единственном экземпляре. Передать через подарок эмоции, память, а не только «нужность и полезность»!</p>\r\n<p>Многие из нас не задумываются о том, как создаются такие прекрасные предметы ручной работы. Но для наших ребят свечи стали не только хобби, но и способом самовыражения и терапии.</p>\r\n<p>Каждая свеча создается с любовью и вниманием. Ребята выбирают ароматы, цвета и формы свечей, чтобы передать свои эмоции и чувства. Их работы - это настоящие произведения искусства, которые привлекают взгляды и восхищение.\r\n</p>\r\n<p>Кроме того, эти свечи имеют глубокий смысл. Они напоминают нам о том, что каждый человек уникален и способен на великие дела, даже если у него есть особенности. Это важное напоминание о том, что нужно быть терпимыми и открытыми к разным людям и их талантам.</p>\r\n<p></p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239265&hash=daf0c3fbb55c598d\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2023/15.jpg', '2023-11-30', '', '', '', '', '', 0, '', ''),
(845, '22,19', 'Благодарим за помощь ресурсному классу!', 'Хорошие новости, друзья! Мы закрыли <a href=\"https://dobro.mail.ru/projects/pomozhem-seve-i-druim-detyam-s-ras-pojti-v-perevyij-klass/\" target=\"_blank\">сбор средств</a> для работы ресурсного класса в школе №17. Спасибо, что откликнулись и поддержали нашего первоклассника Севу и других ребят. Спасибо вам за огромные сердца и пожертвования!', '<p>Хорошие новости, друзья! Мы закрыли <a href=\"https://dobro.mail.ru/projects/pomozhem-seve-i-druim-detyam-s-ras-pojti-v-perevyij-klass/\" target=\"_blank\">сбор средств</a> для работы ресурсного класса в школе №17. Спасибо, что откликнулись и поддержали нашего первоклассника Севу и других ребят. Спасибо вам за огромные сердца и пожертвования!</p>\r\n<p>Сева посещает ресурсный класс вместе с другими шестью ребятами с расстройствами аутистического спектра. Важно, чтобы ребята получали индивидуальную поддержку и обучение с учетом их потребностей, ведь это помогает им успешно развиваться и набираться уверенности.</p>\r\n<p>Благодаря вашей щедрости и поддержке, мы смогли оплатить работу куратора и супервизора. Протестировать каждого ребёнка, построить и реализовать индивидуальные программы с каждым ребёнком под чутким руководством поведенческих аналитиков.</p>\r\n\r\n', '/images/help/school_world/5-1.jpg', '2023-12-06', '', '', '', 'Новости', '', 0, '', ''),
(846, '19', 'Инклюзия в Иркутске', 'Вчера в МОУ ИРМО \"СОШ п. Молодёжный\" прошел \"Урок добра\" в 1 «З» классе.\r\nПедагоги ресурсного класса рассказали о детях с РАС и их особенностях первоклассникам. ', '<p>Вчера в МОУ ИРМО \"СОШ п. Молодёжный\" прошел \"Урок добра\" в 1 «З» классе.\r\nПедагоги ресурсного класса рассказали о детях с РАС и их особенностях первоклассникам. Игровая форма \"Урока добра\" позволила детям из ресурсного класса и их сверстникам лучше узнать друг друга. Они поняли, что, несмотря на различия в предпочтениях, они все равно могут находить общий язык и наслаждаться обществом друг друга.</p>\r\n<p>Участие студенток 2 курса Педагогического института Иркутского государственного университета, проходящих тьюторскую практику, в проведении мастер-класса по лепке из пластилина позволило детям активно взаимодействовать и развивать свои творческие способности. Они работали в команде, делились материалами и идеями, а также смогли выразить свои чувства и мысли через создание собственных произведений.\r\n</p>\r\n<p>Такой подход к обучению помогает детям с РАС развивать свои навыки социализации, общения и творчества. Он также способствует формированию у сверстников более позитивного отношения к детям с РАС и пониманию их особенностей.\r\n</p>\r\n<p>Такие мероприятия являются важной частью работы ресурсного класса и помогают детям поддерживать социальные связи и чувствовать себя комфортно в обществе. Они также способствуют формированию толерантного отношения к различиям в воспитании, терпимости и сопереживанию\r\nу всех детей.</p>\r\n<p>Проект реализуется при поддержке «Губернского собрания общественности Иркутской области»</p>\r\n', '/images/RK/2023/18/2.jpg', '2023-12-06', '/images/grants/GS.png;\r\n/images/RK/2023/18/2.jpg;\r\n/images/RK/2023/18/3.jpg;\r\n/images/RK/2023/18/4.jpg', '', '', '', '', 0, '', '14');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(848, '16', 'Участие в конкурсе \"Доброе сердце\"', '\"АПЦ Рассвет\" принял участие во втором областном конкурсе «Доброе сердце» в номинации \"Жизнь без ограничений\", с целью привлечение внимания органов государственной власти к проблемам образования детей с РАС и распространения положительного опыта по созданию и масштабированию  инклюзивного образования по модели «Ресурсный класс» на территории Иркутской области.\r\n', '<p>8 декабря в Иркутске состоялась церемония награждения лауреатов и участников второго областного конкурса «Доброе сердце», который проводился Правительством Иркутской области в целях стимулирования и поддержки общественных инициатив, направленных на развитие и укрепление гражданского общества в Иркутской области. </p>\r\n<p>В Конкурсе принимали участие социально ориентированные некоммерческие организации, организации территориального общественного самоуправления и граждане, осуществляющие реализацию на территории Иркутской области общественно значимых проектов.</p>\r\n<p>\"АПЦ Рассвет\" принял участие в номинации \"Жизнь без ограничений\", представив свой проект \"Право на развитие и образование\", с целью привлечение внимания органов государственной власти к проблемам образования детей с расстройствами аутистического спектра и распространения положительного опыта по созданию и масштабированию  инклюзивного образования по модели «Ресурсный класс» на территории Иркутской области.</p>\r\n<p>За огромный вклад в развитие инклюзивного образования \"Рассвет\" в лице председателя Екатерины Родыгиной был награжден дипломом участника конкурса \"Доброе сердце\".</p>\r\n<div class=\"image\"><img src=\"/images/news/2023/7/2.jpg\" alt=\"\"></div>\r\n<div class=\"image\"><img src=\"/images/news/2023/7/3.jpg\" alt=\"\"></div>\r\n\r\n\r\n\r\n\r\n\r\n', '/images/news/2023/7/1.jpg', '2023-12-10', '', '', '', 'Новости', 'news.php', 0, '', ''),
(849, '20', 'Навыки, которые позволят ученику выйти в новую для него среду', 'Социально-бытовые навыки помогают людям справляться с ежедневной рутиной и быть независимыми настолько, насколько это возможно. Для детей с расстройствами аутистического спектра навыки самообслуживания могут быть вызовом.', '<p>Социально-бытовые навыки помогают людям справляться с ежедневной рутиной и быть независимыми настолько, насколько это возможно.</p>\r\n<p>Для детей с расстройствами аутистического спектра навыки самообслуживания могут быть вызовом, поскольку они часто имеют трудности с коммуникацией, социальным взаимодействием и общей координацией.</p>\r\n<p>Не стоит полагать, что если ребенок в 3 года начал осваивать азбуку и цифры, то он так же ловко начнет чистить зубы или переодеваться. Развитие должно быть направлено на все сферы жизни, в том числе, и на развитие функциональных навыков:</p>\r\n<p>&#9989; навыки, которые нужны каждый день</p>\r\n<p>&#9989; навыки, которые ведут к самостоятельности</p>\r\n<p>&#9989; навыки, которые полезны</p>\r\n<p>&#9989; навыки, которые позволят ученику выйти в новую для него среду</p>\r\n<p>Как узнать, насколько самостоятелен мой ребенок?</p>\r\n<p>Существуют различные тестирования, направленные на выявление уровня развития в социально-бытовом ориентировании. Все они построены по принципу от простых задач к сложным, поэтому оценить навыки самостоятельности ребенка может абсолютно любой человек.</p>\r\n<p><b>VB-MAPP</b> - программа оценки навыков речи и социального взаимодействия для детей с аутизмом и другими нарушениями.</p>\r\n<p>Мы прикрепили <a href=\"https://vk.com/doc785351097_676682228?hash=4CxIE52kQeyQPZfIHkXUQLkuqZ8nSJux55EeU2lBSH8&dl=4zZNLieZESzuhBsYcK9mQuugYSTy1K8AN5yOzSGVtfX\">таблицу</a>, по которой вы можете самостоятельно определить, какой уровень развития самостоятельности у вашего ребенка.</p>\r\n<p>Навыки самообслуживания перечислены в удобной и понятной таблице и разбиты по возрастным нормам (до 4-х лет).</p>\r\n<p>Несмотря на то, что навыки самообслуживания перечислены для ребят до 4-х лет, очень часто даже взрослые ребята, которые приходят к нам на занятия, имеют серьезные пробелы в тестировании. Поэтому мы восполняем базовые навыки, а уже потом переходим к более сложным задачам.</p>\r\n<p>Так же существуют и другие инструменты оценки навыков ребенка.</p>\r\n<p><b>ABLLS-R</b> - позволяет сделать оценку начальных навыков ребенка, построить индивидуальный план обучения и отслеживать прогресс в обучении.\r\nВ тестировании нет сравнения с возрастными нормами или типичным психическим развитием, поэтому тест можно проводить в любом возрасте, но чаще всего его используют для детей школьного возраста.</p>\r\n<p>Блок социально-бытовых навыков включает в себя:</p>\r\n<p>&#9989; 15 навыков одевания</p>\r\n<p>&#9989; 10 навыков приема пищи (базовые навыки самообслуживания при приеме и приготовлении пищи)</p>\r\n<p>&#9989; 7 базовых навыков ухода за внешностью и личной гигиены</p>\r\n<p>&#9989; 10 навыков использования туалета (опрятность и использование туалета)</p>\r\n<p><b>AFLS</b> - оценка жизненно-функциональных навыков (система оценки может быть применима для постановки целей обучения детей, начиная с 5-6 лет, а особенно актуальна для подростков и взрослых с РАС).</p>\r\n<p>Русскоязычный пакет протоколов представлен семью книгами:</p>\r\n<p>1. AFLS: Руководство по проведению оценки</p>\r\n<p>2. AFLS: Основы самообслуживания</p>\r\n<p>3. AFLS: Быт</p>\r\n<p>4. AFLS: Школа</p>\r\n<p>5. AFLS: Участие в жизни общества</p>\r\n<p>6. AFLS: Самостоятельная жизнь</p>\r\n<p>7. AFLS: Трудоустройство и работа</p>\r\n<p>Зачем проводить тестирование?</p>\r\n<p>&#9989; Чтобы оценить навыки ребенка</p>\r\n<p>&#9989; Чтобы определить приоритетные направления развития на данном этапе</p>\r\n<p>&#9989; Чтобы отследить прогресс в развитии</p>\r\n<p>Проект \"Сам себе помощник\" реализуется при поддержке Благотворительного фонда «Вклад в будущее».</p>\r\n', '/images/ABA/2023/10.jpg', '2023-12-15', '/images/grants/VK_VB.jpg', '', '', 'Новости', '/news.php', 0, '', '1'),
(850, '4', 'Ребенок не ест в общественных местах?', 'Если ребенок отказывается есть в общественных местах вне дома, возможно, есть несколько причин такого поведения. \r\nВ этом случае пищевой тренинг может быть полезным для помощи ребенку в преодолении своих страхов.', '<p>Если ребенок отказывается есть в общественных местах вне дома, возможно, есть несколько причин такого поведения. Одна из них может быть вызвана тревожностью или фобиями по отношению к новым продуктам, местам или людям.\r\nВ этом случае пищевой тренинг может быть полезным для помощи ребенку в преодолении своих страхов.</p>\r\n<p>В основе пищевого тренинга лежит метод систематической десенсибилизации, используемый для помощи людям, испытывающим тревожность или фобию по отношению, например, к пище.\r\n\r\n</p>\r\n<p>Этот тренинг предполагает постепенное и контролируемое введение проблемных продуктов питания или ситуаций, связанных с едой, чтобы помочь человеку преодолеть свои страхи и негативные реакции.\r\nПищевой тренинг фокусируется на систематическом анализе и изменении поведения с использованием различных стратегий и техник.\r\n</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240532&hash=5d093daf5e9cc22e\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n&nbsp; &nbsp; &nbsp;\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240548&hash=c13706fbbd882276\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/ABA/2023/11.jpg', '2023-12-13', '', '', '', '', '', 0, '', ''),
(851, '18', 'Новогодние поделки', 'На этой неделе мы с ребятами занимались интересными новогодними поделками.  Работали с различными материалами и клеевым пистолетом. Кто-то делал снеговика, кто-то \r\nёлку и новогодние шары, а кто-то даже смог преодолеть свой страх!', '<p>На этой неделе мы с ребятами занимались интересными новогодними поделками.  Работали с различными материалами и клеевым пистолетом. Кто-то делал снеговика, кто-то \r\nёлку и новогодние шары, а кто-то даже смог преодолеть свой страх!</p>\r\n<p>Тимофей очень боится горячего. Как же он сделал свою поделку без использования горячего клеевого пистолета?</p>\r\n<p>Наша мастерица Александра придумала простое и безопасное решение! Для того, чтобы прижимать клей, мы применили деревянную палочку.</p>\r\n<p>Тимофей был очень рад этому открытию и с удовольствием приступил к созданию своей новогодней поделки. Он наносил клей и щедро его приклеивал к своему изделию, не опасаясь обжечься.</p>\r\n<p>Наш опыт показал, что иногда необходимо найти альтернативные способы, чтобы преодолеть свои страхи. Теперь Тимофей точно знает, что деревянная палочка - настоящий спаситель, когда дело касается горячего клея.</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239268&hash=ca2258af4d3e9a10\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n&nbsp; &nbsp; &nbsp;\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239267&hash=414c9c4404bb7c22\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2023/16.jpg', '2023-12-08', '', '', '', '', '', 0, '', ''),
(853, '2', 'Праздник чистоты \"Мойдодыр\"', 'Сегодня в нашем центре прошел невероятный праздник чистоты! Наши замечательные ребята приняли участие в увлекательной игре, главная цель которой - победить злого микроба! Каждый ребенок с радостью справлялся с заданиями, проявляя настоящий героизм и мастерство.', '<p>Сегодня в нашем центре прошел невероятный праздник чистоты! Наши замечательные ребята приняли участие в увлекательной игре, главная цель которой - победить злого микроба! Каждый ребенок с радостью справлялся с заданиями, проявляя настоящий героизм и мастерство.</p>\r\n<p>Волшебной атмосферой самого праздника были эмоции, это по-настоящему растопило наши сердца. Увидеть на лицах детей такую счастливую улыбку - это лучшая награда для нас, взрослых. Все они были настоящими волшебниками, способными победить любые преграды.</p>\r\n<p>И конечно же, нельзя забыть о самом главном - подарках! В конце праздника каждый ребенок получил маленькую, но такую долгожданную награду. Призы были подобраны с любовью и заботой, чтобы каждый ребенок почувствовал, как он важен и особенен для нас.\r\n</p>\r\n<p>Этот праздник чистоты стал не только веселым и увлекательным, но и уникальным опытом для наших детей. Они не только преодолели свои страхи, но и почувствовали себя частью большой и дружной команды.</p>\r\n<p>Мы гордимся каждым ребенком, который принял участие и проявил свои лучшие качества!</p>\r\n<p>Благодарим всех, кто принял участие в организации этого праздника! Благодаря вашей поддержке мы смогли создать такое волшебное событие для наших детей.</p>\r\n<p>Проект \"Сам себе помощник\" реализуется при поддержке Благотворительного фонда «Вклад в будущее».</p>\r\n<section class=\"video-detail\">\r\n<iframe width=\"333\" height=\"680\" src=\"https://www.youtube.com/embed/OhljE82yPts\" title=\"Праздник чистоты для наших ребят\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share\" allowfullscreen></iframe>\r\n</section>', '/images/dosug/2023/1.jpg', '2023-12-20', '/images/grants/VK_VB.jpg', '', '', 'Новости', '/news.php', 0, '', '1'),
(855, '19', 'Зачем школе нужна поддержка поведенческих специалистов?', 'Поведенческий специалист в школе - это педагог, владеющий методиками прикладного анализа поведения (ПАП) и анализирующий поведение каждого конкретного ученика, для выявления взаимосвязи интересующего поведения и изменений в окружающей среде.\r\n', '<p>Поведенческий специалист в школе - это педагог, владеющий методиками прикладного анализа поведения (ПАП) и анализирующий поведение каждого конкретного ученика, для выявления взаимосвязи интересующего поведения и изменений в окружающей среде.</p>\r\n<p>Над чем может работать поведенческий специалист?</p>\r\n<p>- Развитие академических навыков</p>\r\n<p>- Развитие самостоятельности ученика</p>\r\n<p>- Работа с проблемным поведением</p>\r\n<p>- Социализация ребят и многое другое</p>\r\n<p>К сожалению, в Иркутской области не осуществляется ВУЗовская подготовка специалистов по прикладному анализу поведения, поэтому обучение поведенческих аналитиков может быть организовано только на базе других образовательных учреждений или в рамках профессиональных курсов и тренингов за счет учителя, который заинтересован в своем развитии.</p>\r\n<p>Если специалисты прикладного анализа поведения в Иркутске уже есть, бюджетных вакансий по данной специализации еще нет, поэтому все экономические издержки ложатся на плечи родителей. Снизить финансовую нагрузку с семей помогают различные благотворительные проекты и грантовая поддержка различных ведомств.\r\nТак, например, проект «Ресурсный класс. Доступная среда» от Ассоциации \"Аутизм-Регионы\" позволяет оплачивать работу кураторов и супервизора в школе пос. Молодежный и в школе пос. Луговое.</p>\r\n<p>Прикладной анализ поведения – это научная дисциплина, основанная на объемной теоретической части и имеющая прикладной характер, поэтому очевидно, что уровень профессионализма поведенческих аналитиков различается. Для анализа и составления поведенческих протоколов нужен обученный куратор, который будет брать супервизии у более опытного специалиста. Куратор так же обучает тьюторов и учителей работать по поведенческой программе.</p>\r\n<p>Зачем нужен поведенческий аналитик?</p>\r\n<p>- Сбор данных и анализ поведения</p>\r\n<p>- Консультирование родителей и педагогов</p>\r\n<p>- Составление индивидуального плана поведенческого вмешательства</p>\r\n<p>- Обучение тьюторов и педагогов работать по протоколам</p>\r\n<p>- Реализация учебной программы</p>\r\n<p>Проблема</p>\r\n<p>Ребенок выкрикивает на уроке неприличные слова.\r\nКаждый раз, когда Данил выкрикивал матершинные слова, одноклассники начинали смеяться, а учитель ругался «Ты срываешь мне урок! Как тебе не стыдно?!».</p>\r\n<p>Поведенческий аналитик посоветовал учителю не обращать внимание на выкрики Данила, так как реакция одноклассников и учителя подкрепляет поведение мальчика выкрикивать неприличные фразы - ему нравится находиться в центре внимания, а самый легкий способ получить интерес окружающих, сорвать урок.</p>\r\n<p>Теперь каждый раз, когда Данил начинал выкрикивать неприличные слова во время урока, класс не обращал на это никакого внимания. Параллельно с стратегией гашения неприемлемого поведения, учитель ввел совместные игры на переменах, где Данил мог получить внимание одноклассников, а так же Данил получал внимание учителя, когда вел себя хорошо!</p>\r\n<p>Поддержка поведенческого специалиста в данном случае была необходима для анализа поведения ребенка и предложения эффективных стратегий работы с ним. Поведенческий специалист помог учителю внести изменения в подход к ребенку и создать благоприятную обучающую среду для него. Этот процесс позволил Данилу получать необходимое внимание и поддержку со стороны своих одноклассников, а также мотивировал его к активному участию в уроках. Результатом поддержки поведенческого специалиста стало полное прекращение неприемлемого поведения ребенка.</p>\r\n<p>Как работать с обучающимся с РАС?</p>\r\n<p>Многие учителя задают этот вопрос, ведь в институтах об этом не рассказывали.\r\nКак установить сотрудничество, замотивировать ребенка учиться, как организовать обучающее пространство для ребенка, который отвлекается на все подряд или наоборот, которому ничего не интересно? Как понятно объяснить ребенку различные темы по программе?\r\nИменно на эти и многие другие вопросы нам могут ответить поведенческие специалисты.</p>\r\n<p>Прикладной анализ поведения работает только с детьми с РАС?</p>\r\n<p>В основном, применение ПАП более всего известно как метод терапии для детей с особенностями развития, однако этот метод имеет широкий спектр. Например, в образовании (повышение школьной успеваемости), поддержании здорового образа жизни и физической культуре, спорте, изучении языков, воспитании детей и многих других сферах.</p>\r\n<p>АНО \"АПЦ Рассвет\" - участник проекта «Ресурсный класс. Доступная среда». Благодарим организатора проекта Ассоциацию \"Аутизм-Регионы\". Проект реализуется при поддержке Фонда президентских грантов и благотворительного фонда \"Абсолют - Помощь\".</p>\r\n\r\n\r\n', '/images/RK/2023/19/1.jpg', '2023-12-22', '/images/RK/2023/19/2.jpg;\r\n/images/RK/2023/19/3.jpg;\r\n/images/RK/2023/19/4.jpg;\r\n/images/RK/2023/19/5.jpg', '', '', 'Новости', '/news.php', 0, '', ''),
(856, '18', 'Новогодние свечи от наших ребят', 'Весь декабрь наши невероятно талантливые ребята трудились над созданием уникальных новогодних свечей. Они не только участвовали в каждом этапе процесса, но и проявили творческую самостоятельность, воплощая свои идеи в реальность.', '<p>Весь декабрь наши невероятно талантливые ребята трудились над созданием уникальных новогодних свечей. Они не только участвовали в каждом этапе процесса, но и проявили творческую самостоятельность, воплощая свои идеи в реальность.</p>\r\n<p>Наши звездочки самостоятельно топили воск, выбирали ароматы и цвета, а затем с удовольствием заливали силиконовые формы.</p>\r\n<p>Когда свечки окончательно остывали, наши ребята с нетерпением распаковывали их от силиконовых форм. Их лица светились радостью и гордостью за свой труд. И, действительно, результаты их творчества поражают: каждая свечка – это отражение индивидуальности каждого ребенка.\r\n</p>\r\n<p>Мы с гордостью делимся этими красивыми свечами с вами, потому что они не только являются символом нашего творчества, но и доказывают, что каждый ребенок способен на большие достижения, когда ему предоставляются возможности и поддержка.</p>\r\n<p>Проект \"Давай дружить\" реализуется при поддержке «Губернского собрания общественности Иркутской области»</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239274&hash=92e6cbabf5c358b9\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2023/17.jpg', '2023-12-24', '/images/grants/GS.png', '', '', '', '', 0, '', '14'),
(857, '2', 'Батутный центр - наша новогодняя традиция!', '26 декабря мы устроили новогодний праздник для наших ребят! Собрали большую банду и отправились в батутный центр. Как малыши, так и взрослые ребята, стали частичкой праздника: счастливые визги и смех разливались по всему зданию. ', '<p>26 декабря мы устроили новогодний праздник для наших ребят!\r\nСобрали большую банду и отправились в батутный центр.\r\nКак малыши, так и взрослые ребята, стали частичкой праздника: счастливые визги и смех разливались по всему зданию. Улыбки сверкали, как вспышки: тут и там!</p>\r\n<p>Удивительно наблюдать, как дети, очень чуткие к шуму и большим компаниям, сумели хотя бы на чуть-чуть расслабиться и попрыгать в свое удовольствие.\r\nПраздник посетили Дед Мороз со Снегурочкой и их плюшевый друг-Зеленый Мишка.</p>\r\n<p>Чтобы получить новогодний подарок, детям нужно было подойти к Деду Морозу и рассказать стихотворение.\r\nИ тут наши маленькие герои справлялись с личными трудностями!\r\nПрограмма праздника включала в себя танцы и интерактив со сказочными представителями, и все это на огромных батутах, где можно по-настоящему веселиться: бегать и прыгать чуть ли не до самого потолка!\r\n</p>\r\n<p>«Ты не один в этом мире» - наш девиз. Когда ты находишься в кругу друзей, когда ближние понимают и принимают твои особенности, легче дать волю эмоциям и быть самим собой.</p>\r\n<p>Спасибо всем, кто посетил праздник и был с нами❤\r\nЖелаем вам много радости, счастья и успехов в наступающем году!</p>\r\n<p>Проект \"Давай дружить\" реализуется при поддержке «Губернского собрания общественности Иркутской области»</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240594&hash=3e02775557cd1a55\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/dosug/2023/2.jpg', '2023-12-27', '/images/grants/GS.png', '', '', '', '', 0, '', '14'),
(858, '12', 'Принципы АФК для детей с РАС', 'Физическая активность благотворно влияет на все сферы жизни, главное - не отступать от намеченной цели! А как привить ребенку физическое воспитание, если он не следует инструкциям взрослых и не может сосредоточить внимание на упражнениях?', '<p>Физическая активность благотворно влияет на все сферы жизни, главное - не отступать от намеченной цели! А как привить ребенку физическое воспитание, если он не следует инструкциям взрослых и не может сосредоточить внимание на упражнениях?\r\n</p>\r\n<p>На занятиях АФК в \"РАСсвете\" мы используем простые принципы, которые могут быть полезными при проведении физкультуры для ребят с расстройствами аутистического спектра, и супер-тренеров, которые грамотно составляют индивидуальные программы занятий для учеников. </p>\r\n<p>&#9989; Предоставьте структурированную среду.<br>\r\nРебенку с аутизмом может быть сложно разбираться в новых ситуациях, поэтому важно предоставить им структурированный и предсказуемый режим занятий. Установите четкие правила и ожидания и придерживайтесь их во время тренировок.</p>\r\n<p>&#9989; Четкие требования.<br> Дайте ребенку понимание о том, что ожидается от него во время физической активности. Четко объясните правила и цели каждой задачи и предоставьте стратегии для достижения этих целей.</p>\r\n<p>&#9989; Используйте визуальные подсказки.<br>\r\nВизуальные подсказки могут быть полезными для детей с аутизмом, так как они могут помочь им понимать последовательность действий и планировать свои действия. Рассмотрите возможность использования графических горизонтальных раскладок или других визуальных средств для облегчения понимания.</p>\r\n<p>&#9989; Дайте перерыв при необходимости.<br>\r\nМногие дети с аутизмом испытывают сенсорную перегрузку, поэтому могут понадобиться перерывы для снятия накопившегося стресса. Предоставьте возможность для кратких перерывов или включите в программу физической активности такие активности, как глубокое дыхание или расслабление.</p>\r\n<p>&#9989; Используйте интересы ребенка для поддержания мотивации.<br>\r\nУчитывайте интересы каждого ребенка и используйте их в качестве мотивации для участия в физической активности. Например, если ребенок заинтересован в горных велосипедах, можно предложить ему возможность посетить велодорожки или участвовать в специальной группе по велосипедному спорту.</p>\r\n<p>&#9989; Сотрудничество с педагогами. <br>\r\nСовместная работа с специалистами прикладного анализа поведения может помочь адаптировать физическую активность для каждого ребенка. Вместе вы можете разработать индивидуальные планы и стратегии, которые соответствуют потребностям и возможностям каждого ребенка.</p>\r\n<p>&#9989; Предоставьте место для самовыражения. <br> Предоставьте ребенку возможность самовыражения через физическую активность. Это может быть танец, хореография, йога или другие виды активности, которые позволяют ребенку выразить себя и свои эмоции.\r\n</p>\r\n<p>Важно помнить, что каждый ребенок уникален, поэтому необходимо индивидуальное консультирование. Сотрудничество с специалистами прикладного анализа поведения позволит создать наиболее эффективную и подходящую среду для физической активности для детей с расстройствами аутистического спектра.</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240614&hash=79f9a033b193b95b\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n<section>', '/images/AFK/2024/1.jpg', '2024-01-08', '', '', '', 'Новости', '/news.php', 0, '', ''),
(859, '22', 'Подарки от \"Иркутского Зоосада\"', 'В первые рабочие дни января в наш центр ворвалась команда \"Иркутского зоосада\". Ребята пришли в новогодних костюмах и с подарками для наших подопечных! ', '<p>В первые рабочие дни января в наш центр ворвалась команда \"Иркутского зоосада\". Ребята пришли в новогодних костюмах и с подарками для наших подопечных! Это были и сладости, и целых две коробки с игрушечными дракончиками ручной работы!\r\n</p>\r\n<p>В ноябре \"Иркутский зоосад\" объявил конкурс мягкой игрушки «Создай своего дракона!» Все игрушки, принявшие участие в конкурсе, были созданы специально для ребят с аутизмом из \"Адаптационно-педагогического центра Рассвет\"! </p>\r\n<p>Благодарим \"Иркутский зоосад\" и всех, кто принял участие в конкурсе за эти прекрасные подарки! Дракон - символ этого года - станет настоящим оберегом и будет приносить удачу на протяжении всего года нашим ребятам!</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240622&hash=eb844448e264b0ec\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/gratitude/foto/2.jpg', '2024-01-12', '', '', '', 'Новости', '/news.php', 0, '', ''),
(860, '4', 'Как помочь ребенку с аутизмом начать общаться со сверстниками?', 'Общение - это двухсторонний процесс, и может потребоваться время, чтобы ваш ребенок и его потенциальные друзья поняли и нашли общий язык друг с другом. Будьте терпеливыми и поддерживайте своего ребенка в этом процессе.', '<p>Общение - это двухсторонний процесс, и может потребоваться время, чтобы ваш ребенок и его потенциальные друзья поняли и нашли общий язык друг с другом. Будьте терпеливыми и поддерживайте своего ребенка в этом процессе.</p>\r\n<p>&#9989; Обратитесь в организации и сообщества, которые занимаются поддержкой семей детей с аутизмом. Там вы сможете познакомиться с другими семьями, столкнувшимися с такими же проблемами, и создать возможности для детей встречаться и играть вместе.</p>\r\n<p>&#9989; Разработайте индивидуальный план социального развития для вашего ребенка с педагогом или специалистом в области прикладного анализа поведения. Это поможет развивать навыки социального взаимодействия\r\nи коммуникации, необходимые для общения.</p>\r\n<p>&#9989; Рассмотрите возможность включения вашего ребенка в коммуникативную группу, которая ориентирована на детей с РАС. Программы групп обычно предлагают различные активности для формирования и развития социально-коммуникативных навыков и участия в общественной жизни.</p>\r\n<p>&#9989; Поощряйте хобби или занятия своего ребенка, где он может встретить единомышленников. Это может быть клуб или группа, которая сосредоточена на общих интересах, что поможет создать основу для общения.</p>\r\n<p>&#9989; Взаимодействуйте с родителями других детей, чтобы они понимали особые потребности вашего ребенка. Объясните им, как можно облегчить совместную игру или взаимодействие между детьми.</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240637&hash=22ec2059ac07778b\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>\r\n', '/images/ABA/2024/1.jpg', '2024-01-11', '', '', '', 'Новости', '/news.php', 0, '', ''),
(861, '18', 'Ваш ребенок заслуживает только самое лучшее!', 'В Иркутске существует удивительное место, где дети разных возрастов и способностей собираются вместе, чтобы учиться, играть и расти вместе. Это инклюзивная мастерская Дом Синего Слона!', '<p>Знаете ли вы, что проводя все время с ребенком только дома, вы лишаете его возможности полноценного развития? Особенно это актуально для детей с особенностями развития. Но не отчаивайтесь! В Иркутске существует удивительное место, где дети разных возрастов и способностей собираются вместе, чтобы учиться, играть и расти вместе. Это инклюзивная мастерская Дом Синего Слона!</p>\r\n<p>Что такое инклюзивная мастерская? Это место, где дети с особенностями развития и дети с нормальным развитием могут встретиться, подружиться и совместно заниматься различными видами творчества. Это не только творческий процесс, но и пространство для развития социальных навыков. Дети учатся общаться и строить дружеские отношения. Это идеальное место для того, чтобы ваш ребенок научился быть самостоятельным и уверенным в себе!</p>\r\n<p>Все занятия проводятся профессиональными педагогами, которые учитывают индивидуальные потребности каждого ребенка.</p>\r\n<p>Инклюзия - это ключевое слово в современном мире. Она помогает создать равные возможности для всех детей, независимо от их способностей.</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239285&hash=5b934953becd3857\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2024/1.jpg', '2024-01-08', '', '', '', '', '', 0, '', ''),
(862, '18', 'Эффективный способ развития детей с РАС', 'Дети с РАС, как правило, испытывают трудности в организации своих мыслей и действий. Варение мыла помогает им структурировать процесс, разбивая его на шаги и последовательности. Таким образом, они учатся планировать и контролировать свои действия.', '<p>Оказывается, что варить мыло это не только интересное и творческое занятие, но и эффективный способ развития детей с РАС.\r\n</p>\r\n<p>Как это работает? Во-первых, варить мыло требует определенных навыков и последовательности действий. Дети с РАС, как правило, испытывают трудности в организации своих мыслей и действий. Варение мыла помогает им структурировать процесс, разбивая его на шаги и последовательности. Таким образом, они учатся планировать и контролировать свои действия.</p>\r\n<p>Кроме того, варить мыло - это творческое занятие, которое помогает развивать воображение и фантазию. Дети могут выбирать ароматы, цвета и формы мыла, что способствует развитию их творческого мышления. Это также помогает им выражать свои чувства и эмоции через создание уникального продукта.</p>\r\n<p>Но самое главное - варение мыла создает возможности для социального взаимодействия. Дети могут варить мыло вместе со своими родителями, братьями, сестрами или друзьями. Это позволяет им учиться работать в команде, сотрудничать, общаться и развивать навыки социализации.</p>\r\n\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239287&hash=9c1a1bc8d362400f\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2024/2.jpg', '2024-01-09', '', '', '', '', '', 0, '', ''),
(864, '16', 'Благодарность губернатора Иркутской области', 'В декабре 2023 года учредителю АНО \"АПЦ Рассвет\" и куратору ресурсного класса Шапиловой Елене Александровне была объявлена Благодарность Губернатора Иркутской области за достижения в общественной сфере и безупречную работу. ', '<p>В декабре 2023 года учредителю АНО \"АПЦ Рассвет\" и куратору ресурсного класса Шапиловой Елене Александровне была объявлена Благодарность Губернатора Иркутской области за достижения в общественной сфере и безупречную работу. </p>\r\n<p><a href=\"https://www.ogirk.ru/pravo/archives/law/351989\" target=\"_blank\">УКАЗ ГУБЕРНАТОРА ИРКУТСКОЙ ОБЛАСТИ от 12.12.2023 года</a></p>\r\n<p>Елена Александровна начала свою трудовую деятельность в 1995 году в должности учителя математики и информатики. Являясь супругой военнослужащего Военно-воздушных сил МО РФ, следовала за ним в новые места пребывания, где успешно продолжала работать в сфере образования, приобретая опыт и навыки в образовательном процессе. Общий трудовой стаж составляет более 25 лет.    </p>\r\n<p>Елена Александровна грамотный, высококвалифицированный, ответственный работник. За короткое время она изучила все тонкости большого и сложного процесса обучения детей с расстройствами аутистического спектра на основе принципов прикладного анализа поведения. Имеет сертификат поведенческого аналитика (куратора поведенческих программ), подтверждающий её квалификацию в сфере прикладного анализа поведения. В настоящее время постоянно повышает свой профессиональный уровень. Полученные знания эффективно применяет на практике, что позволяет ей обеспечивать качественную работу с детьми с расстройствами аутистического спектра.  </p>\r\n<p>Коллектив АНО \"АПЦ Рассвет\" сердечно поздравляет Елену Александровну и желает ей дальнейших успехов!</p>', '/images/news/2023/7.jpg', '2023-12-25', '', '', '', '', '', 0, '', ''),
(865, '10', 'Проект «Без паники! Идем к стоматологу»', 'Статус проекта: действующий<br>\r\nСроки реализации: 01.02.2024 – 30.04.2025<br>\r\nИсточник софинансирования: Фонд президентских грантов\r\n', '<p><b>Статус проекта:</b> действующий<br>\r\n<b>Сроки реализации:</b> 01.02.2024 – 30.04.2025<br>\r\n<b>Источник софинансирования:</b> Фонд президентских грантов</p>\r\n\r\n<p><b>Цель проекта:</b></p>\r\n<p>Обеспечить беспрепятственное оказание квалифицированной стоматологической помощи для не менее 30 детей с РАС 2-18 лет для поддержания их соматического здоровья посредством внедрения поведенческих вмешательств и научно обоснованных практик в детских стоматологических поликлиниках г. Иркутска.</p>\r\n<p><b>Задачи:</b></p>\r\n<p>1. Подготовить базу для внедрения квалифицированной помощи по профилактике и лечению стоматологических заболеваний у детей с РАС посредством материально-технического оснащения проекта и повышения компетенций поведенческих специалистов АНО «АПЦ Рассвет».</p>\r\n<p>2. Популяризировать среди не менее 200 врачей и родителей детей с РАС Иркутской области модель оказания стоматологической помощи детям с РАС и профилактики стоматологических заболеваний с использованием научно обоснованных принципов коррекции поведения.</p>\r\n<p>3.Научить не менее 40 родителей детей с РАС использовать поведенческие подходы в обучении ребенка профилактике полости рта и лечении стоматологических заболеваний.</p>\r\n<p>4.Сформировать целевое поведение и провести стоматологические манипуляции не менее 30 детям с РАС путем использования научно доказанных методов прикладного анализа поведения на индивидуальных занятиях в АНО «АПЦ Рассвет».</p>\r\n<p>5.Научить не менее 10 детских стоматологов г. Иркутска использовать научно обоснованные принципы коррекции поведения при проведении стоматологических осмотров и лечебных манипуляций у детей с РАС с их последующим внедрением в ежедневную врачебную практику.</p>\r\n<p><b>Краткое описание:</b></p>\r\n<p>Здоровое функционирование организма без боли и дискомфорта — это базовая потребность любого человека. Научить ребенка новому невозможно, если его беспокоит зубная боль, а стоматологические процедуры у детей с РАС без анестезии затруднены из-за особенностей поведения и фобий. Из-за сложностей в обобщении, спокойное посещение стоматолога ребенком с РАС возможно только при командной работе. Поэтому проектная деятельность направлена не только на подготовку к медицинским процедурам детей с РАС, но и на обучение стоматологов и родителей подходам работы с детьми с РАС.</p>\r\n<p>Для привлечения родителей и стоматологов мы проведем информационную кампанию, где расскажем о научно-доказанных методах коррекции поведения ребенка с РАС при стоматологических манипуляциях и гигиене полости рта. На семинарах 150 родителям расскажем о поведенческих подходах, методах систематической десенсибилизации, визуальной поддержки для подготовки ребят к стоматологическим процедурам.</p>\r\n<p>Проведенный опрос по FAST позволит из 70 семей выбрать детей с высокой необходимостью поведенческого вмешательства в обучение навыкам гигиены и посещения стоматолога, которые будут приглашены на индивидуальную консультацию. Вместе с поведенческими специалистами 40 родителей со своим ребенком проведут начальную оценку уровня преград, препятствующих проведению медицинских манипуляций, и проработают навыки, необходимые ребенку для спокойного посещения стоматолога. По результатам оценки 20 детей с высоким уровнем преград под руководством поведенческих специалистов пройдут 4000 часов индивидуального обучения стоматологическим процедурам.</p>\r\n<p>После предварительной подготовки 30 детей с РАС пройдут без анестезии и удерживания ребенка осмотр и лечение в детской стоматологии, что станет возможным благодаря предварительному обучению врачей-стоматологов г. Иркутска на семинарах, дистанционном курсе, стажировках и тренингах по использованию методов прикладного анализа поведения на приеме у стоматолога. В течение всего процесса обучения эксперты БФ «Импульс развития» поделятся опытом внедрения модели «Дружелюбная стоматология» в работу стоматологов: проведут онлайн-консультации, вебинары и очные тренинги для врачей стоматологов Иркутской государственной детской стоматологической поликлиники.</p>\r\n<p>По итогам проекта будет смонтирован короткометражный документальный фильм, где отразится весь путь обучения детей с РАС гигиене полости рта и подготовки к стоматологическим процедурам. Фильм будет размещен на интернет-ресурсах организации и распространен с помощью партнеров проекта для распространения информации о появившейся возможности стоматологического осмотра и лечения по ОМС в детских стоматологических клиниках г. Иркутска. На семинаре в Главном бюро МСЭ не менее 50 врачам мы расскажем об успехах ребят с РАС при использовании поведенческого подхода в медицине с демонстрацией короткометражного фильма, для возможности дальнейшего распространения рабочих инструментов при лечении и диагностике детей с РАС.</p>\r\n\r\n<p><a href=\"/news.php?project=11\" class=\"button\">Публикации о реализации проекта</a></p>', '/images/SP/9/4.jpg', '2024-01-16', '', '', '', 'Гранты', '/pages/grants.php', 0, '', '0'),
(866, '4', 'Ребенок плачет, когда указываете на его ошибки', 'Что делать, если ребенок плачет, когда вы указываете на его ошибки. Попробуйте сформулировать обратную связь в более дружелюбной и поддерживающей форме.\r\nЕсли реакция ребенка на слова \"нет\" и \"неправильно\" вызывает только негативные эмоции, попробуйте говорить по-другому.', '<p>Что делать, если ребенок плачет, когда вы указываете на его ошибки. Попробуйте сформулировать обратную связь в более дружелюбной и поддерживающей форме.\r\nЕсли реакция ребенка на слова \"нет\" и \"неправильно\" вызывает только негативные эмоции, попробуйте говорить по-другому.</p>\r\n<p>&#9989; Давай попробуем еще раз</p>\r\n<p>&#9989; Мимо, пробуем еще раз</p>\r\n<p>&#9989; Давай попробуем вместе</p>\r\n<p>&#9989; Ммм, повторим еще</p>\r\n<p>&#9989; Я тебе помогу</p>\r\n<p>Следите за интонацией обратной связи! Если вы задаете ребенку вопрос \"Давай еще раз?\", будьте готовы, что он ответит нет, а вы подкрепите его отказ, ведь вы сами спросили его и предоставили ребенку выбор.</p>\r\n<p>Не спрашивайте, если не готовы услышать отказ.\r\nНе начинайте оспаривать: \"Ну как это нет? Надо уроки делать, давай-ка не придумывай\". Если вы подбодрите ребенка в утвердительной форме \"Пробуем еще раз вместе\", вы не спровоцируете ребенка на отказ.</p>\r\n<p>Реабилитируйте слова \"нет\" и \"неправильно\" в жизни ребенка, ведь ему придется сталкиваться с ними в жизни ни один раз. Дайте понять ребенку, что в них нет ничего плохого и что если он слышит их, это не значит, что \"я плохой и я не успешный\".</p>\r\n\r\n\r\n', '/images/ABA/2024/2.jpg', '2024-01-15', '', '', '', 'Новости', '/news.php', 0, '', ''),
(867, '16', 'Новая победа в конкурсе Фонда президентских грантов', '«РАСсвет» выиграл в конкурсе Фонда президентских грантов и уже в феврале мы начинаем воплощать супер-проект, направленный на преодоление поведенческих проблем у детей с аутизмом при посещении врача-стоматолога!', '<p>«РАСсвет» выиграл в конкурсе Фонда президентских грантов и уже в феврале мы начинаем воплощать супер-проект, направленный на преодоление поведенческих проблем у детей с аутизмом при посещении врача-стоматолога!</p>\r\n<p>Плановый осмотр может стать доступен каждому ребенку и мы знаем, что нужно делать!</p>\r\n<p>&#129463; Проведем ряд семинаров для родителей, чтобы поделиться нашим опытом - ходить к врачу на осмотр - реально!</p>\r\n<p>&#129463; Обучим стоматологов Иркутска, как расположить к себе ребенка, который совсем не настроен на контакт!</p>\r\n<p>&#129463; Будем делиться успехами и рассказывать рабочие лайфхаки в наших социальных сетях!</p>\r\n<p>&#129463; И, конечно, будем проводить занятия с ребятками, которые нуждаются в помощи поведенческих аналитиков, целью которых будет поход к стоматологу без тяжелого нежелательного поведения!</p>\r\n<p>Следите за анонсами, будет полезно и интересно!</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>', '/images/SP/9/4.jpg', '2024-01-16', '', '', '', '', '', 0, '', '11'),
(868, '18', 'Знакомство с тьютором', 'Продолжаем знакомить вас с нашими талантливыми сотрудниками! Сегодня речь пойдет о нашей замечательной Анастасии, которая является тьютором в нашей мастерской. Анастасия учится на 2 курсе в ПИ ИГУ по профилю логопедия и сопровождение детей с ОВЗ.', '<p>Продолжаем знакомить вас с нашими талантливыми сотрудниками! Сегодня речь пойдет о нашей замечательной Анастасии, которая является тьютором в нашей мастерской. Анастасия учится на 2 курсе в ПИ ИГУ по профилю логопедия и сопровождение детей с ОВЗ.</p>\r\n\r\n<p>Мы очень рады, что Анастасия присоединилась к нашей команде. За короткое время она уже добилась больших успехов. Настя научилась налаживать сотрудничество с детьми, что является неотъемлемой частью ее работы. Она проявляет терпение и понимание, помогая ребятам преодолевать трудности и достигать новых высот.</p>\r\n<p>Но это еще не все! Анастасия также развивает свои творческие навыки. Она научилась шить на машинке, создавать выкройки и даже делать свечи и мыло своими руками.</p>\r\n<p>Но самое главное - Анастасия полюбилась нашим ребятам. Они с удовольствием работают с ней и получают удовольствие от каждой встречи. Анастасия не только помогает им развиваться и преодолевать трудности, но и создает дружественную и поддерживающую атмосферу.</p>\r\n<p>Мы гордимся Анастасией и благодарны ей за ее труд и вклад в нашу мастерскую!</p>\r\n', '/images/DSS/2024/2/1.jpg', '2024-01-16', '/images/DSS/2024/2/2.jpg;\r\n/images/DSS/2024/2/3.jpg;\r\n/images/DSS/2024/2/4.jpg', '', '', '', '', 0, '', ''),
(870, '19', 'Как ресурсный класс помогает ребенку учиться в школе', 'В этой статье мы расскажем, как специальные условия обучения в ресурсном классе помогают детям с аутизмом учиться в обычных школах и осваивать социально важные навыки. Истории от мамы ребенка с РАС и педагога ресурсного класса.', '<p>В этой статье мы расскажем, как специальные условия обучения в ресурсном классе помогают детям с аутизмом учиться в обычных школах и осваивать социально важные навыки.</p>\r\n<p>Своей историей поделилась с нами мама одной из учениц ресурсного класса г. Иркутск Оксана Разгуляева.</p>\r\n<p>\"Что дает обучение в ресурсном классе обычной маме с ребенком, у которого диагностирован аутизм? </p>\r\n<p><b>Возможности.</b></p>\r\n<p>Возможность посещать общеобразовательную школу: каждый день видеть огромное количество людей и учиться справляться со своими эмоциями от встреч.</p>\r\n<p>Моя дочь наконец-то видит, что мир не ограничивается только родственниками и небольшим мирком коррекционных школ, у неё есть возможность наблюдать, что все люди разные, со своими чувствами и переживаниями.</p>\r\n<p>Возможность учиться взаимодействию с окружающими.</p>\r\n<p>Возможность приобретать навыки как в школьных науках, так и в общении со сверстниками.</p>\r\n<p><b>Спокойствие.</b></p>\r\n<p>Я могу быть спокойной, что с моей дочерью в школе не случится ничего плохого.\r\nЕсли возникнут какие-либо трудности, педагоги ей помогут, а меня научат, как справляться с подобными ситуациями в дальнейшем.</p>\r\n<p><b>Понимание.</b></p>\r\n<p>Понимание особых нужд моего ребенка: педагоги понимают, что быстрая утомляемость ребенка – это не увертки, не причуды, а увы, данность, за которую её не накажут, а помогут быстро восстановиться.</p>\r\n<p><b>Помощь.</b></p>\r\n<p>Помощь как дочери, так и мне, в адаптировании учебных материалов.\r\nРебенка учат школьной программе доступным ему языком: если она не понимает длинных предложений и метафор, ей объяснят четко и кратко, применят визуальные подсказки, так, чтобы она смогла понять, что от неё требуется.</p>\r\n<p>Также преподаватели справедливо оценивают навыки детей: если ребенок не понял материал, ему не поставят 2 и не станут отчитывать перед всем классом. Ему объяснят еще раз, применив более подходящие подсказки.</p>\r\n<p><b>Счастье.</b></p>\r\n<p>Можно бесконечно перечислять, что дает мне и моей дочери учеба в ресурсном классе, но главное - она приносит счастье.\r\nДочка счастлива от того, что она становится успешна во многом.\r\nЯ счастлива от того, что мой ребенок становится ближе ко мне и окружающим каждый день.\r\nЭто счастье, когда ребенок бежит в школу на уроки.\r\nСчастье, что ребенок переживает и скучает по классу, когда нет занятий.</p>\r\n<p>И все это было нельзя получить во время учебы в коррекционной школе.\r\nЕсли где-то обучение ребенка с аутизмом в коррекционной школе возможно, то в нашем случае, максимум на что мы могли там рассчитывать, так только на домашнее обучение и, если повезет, несколько раз в неделю посещение индивидуальных занятий с учителем, а этого, увы, мало.\"</p>\r\n<p>Что говорит тьютор ресурсного класса? Своим видением с нами поделилась педагог ресурсного класса школы пос. Молодежный Светлана Зайченко.</p>\r\n<p>\"Ресурсный класс - это уникальная тренировочная площадка, которая позволяет подготовить учеников с РАС к общеобразовательной среде.\r\nУ каждого ребёнка ресурсного класса есть индивидуальный план развития не только академических, но и социально-бытовых, коммуникативных навыков, благодаря которым ученики постепенно тренируют свои умения и накапливают знания с помощью специалистов ресурсного класса, которые помогут им максимально независимо существовать в обществе.</p>\r\n<p>Ресурсный класс - это зона специальной помощи, которая закрывает особые образовательные потребности ребят с аутизмом. </p>\r\n<p>Наши ученики посещают уроки в обычных общеобразовательных классах, где они применяют навыки, которые предварительно отрабатывали в ресурсном классе с помощью тьютора (например поднять руку, когда хочешь ответить, применять визуальные опоры, которые нужны для решения примеров и задач по математике, или обратиться за помощью, если она необходима для выполнения задания).</p>\r\n<p>Даже такие простые на первый взгляд вещи, как игры на перемене, предварительно отрабатываются в ресурсном классе, т.к. дети с РАС не всегда сразу могут понять правила игры.</p>\r\n<p>Недавно наши ученики Соня и Влад посещали урок ИЗО в регулярном классе. Задача была для наших учеников непростая - написать зимний пейзажУ детей с расстройствами аутистического спектра зачастую отсутствует воображение и ребятам сложно выполнить рисунок, просто представив зимнюю природу, не имея визуальной опоры.</p>\r\n<p>Но ребята справились, т.к. находились в знакомом им формате урока: сначала учитель рассказывал про зиму, потом приводил примеры разных цветовых решений выполнения пейзажа, ребята внимательно следили за ходом урока.</p>\r\n<p>Дети спокойно принимали помощь учителя, делали палитру, использовали разные кисти, выполняли инструкции и советы.</p>\r\n<p>Закончив свои рисунки, Соня и Влад навели порядок на партах.\r\nВсё получилось! У каждого по-своему!\r\nУрок ребятам очень понравился и они вернулись в ресурсный класс в хорошем настроении.</p>\r\n<p>Для нас важно привести каждого ученика ресурсного класса к его личному успеху, чтобы процесс обучения был для него приятным и приносил пользу ему и его семье в дальнейшей жизни.\r\nПоэтому мы радуемся вместе любым, даже самым малым, достижениям! Участвуем в школьных конкурсах, даже занимаем призовые места.\r\nРисуем, поём и играем с учениками из других классов.\r\nВсегда находимся в поиске решений для того, чтобы научить ребят чему-то новому.\"</p>\r\n<p>АНО \"АПЦ Рассвет\" - участник проекта «Ресурсный класс. Доступная среда». Благодарим организатора проекта Ассоциацию \"Аутизм-Регионы\".\r\nПроект реализуется при поддержке Фонда президентских грантов и благотворительного фонда \"Абсолют - Помощь\".</p>\r\n\r\n\r\n', '/images/RK/2024/1/1.jpg', '2024-01-26', '/images/RK/2024/1/2.jpg;\r\n/images/RK/2024/1/3.jpg;\r\n/images/RK/2024/1/4.jpg;\r\n/images/RK/2024/1/5.jpg;\r\n/images/RK/2024/1/6.jpg;\r\n/images/RK/2024/1/7.jpg;\r\n/images/RK/2024/1/8.jpg;\r\n/images/RK/2024/1/9.jpg;\r\n/images/RK/2024/1/10.jpg', '', '', 'Новости', '/news.php', 0, '', '');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(871, '18', 'А вот и наш директор', 'Сегодня мы хотим представить вам нашего замечательного начальника - Дарью Николаевну! Она является директором инклюзивной мастерской «Дом синего слона» и нашей надежной опорой.', '<p>Сегодня мы хотим представить вам нашего замечательного начальника - Дарью Николаевну! Она является директором инклюзивной мастерской «Дом синего слона» и нашей надежной опорой.</p>\r\n<p>Дарья Николаевна - это не только профессионал своего дела, но и человек с невероятно добрым и чутким сердцем. Она всегда готова выслушать и помочь не только нашим ученикам, но и их родителям. Ее рассудительность и понимание позволяют ей находить индивидуальный подход к каждому человеку, создавая комфортную и поддерживающую атмосферу в мастерской.</p>\r\n<p>Дарья Николаевна - настоящий вдохновитель для нас всех. Ее энергия и любовь к своей работе передаются каждому, кто ступает на порог нашей мастерской. Она считает, что каждый человек имеет право на равные возможности и стремится создать условия, в которых каждый может раскрыть свой потенциал.\r\n</p>\r\n<p>Мы гордимся тем, что Дарья Николаевна возглавляет нашу инклюзивную мастерскую. Ее профессионализм и преданность делу делают нашу работу еще более значимой и важной.</p>', '/images/DSS/2024/4/1.jpg', '2024-01-23', '/images/DSS/2024/4/2.jpg;\r\n/images/DSS/2024/4/3.jpg;\r\n/images/DSS/2024/4/5.jpg;\r\n/images/DSS/2024/4/4.jpg', '', '', '', '', 0, '', ''),
(872, '18', 'Что делать, если ребенок отказывается выполнять задание', 'Мы часто сталкиваемся с ситуациями, когда наши ребята отказываются выполнять задания или не проявляют интерес к участию в занятиях. Что мы делаем в таких случаях?', '<p>Мы часто сталкиваемся с ситуациями, когда наши ребята отказываются выполнять задания или не проявляют интерес к участию в занятиях. Что мы делаем в таких случаях?</p>\r\n<p>Во-первых, важно понимать, что каждый ребенок уникален, и то, что работает для одного, может не сработать для другого. Поэтому необходимо индивидуально подходить к каждому ребенку, учитывая его потребности и особенности. Поговорите с родителями и специалистами, чтобы получить больше информации о ребенке и его способностях.\r\n</p>\r\n<p>Во-вторых, создайте безопасную и поддерживающую атмосферу в мастерской. Дети с расстройствами аутистического спектра могут испытывать тревогу в новой среде или ситуации. Постарайтесь создать предсказуемый режим, предоставьте им пространство, где они могут ощущать себя комфортно и безопасно.</p>\r\n<p>В-третьих, используйте визуальные инструменты и поддержку - визуальное расписание. Это поможет детям лучше понимать, что от них ожидается, и структурировать их задания. Также не забывайте о возможности использования альтернативных способов коммуникации - карточки Pecs.</p>\r\n<p>В-четвертых, играйте на интересах ребенка. Узнайте, что ему нравится и попробуйте включить эти интересы в задания. Если ребенок увлечен конкретной темой или предметом, он будет более мотивирован участвовать и выполнять задания.\r\n</p>\r\n<p>И, наконец, не забывайте о терпении и понимании. Работа с детьми с расстройствами аутистического спектра может быть сложной, но они также могут принести невероятную радость и удивительные результаты.</p>\r\n', '/images/DSS/2024/5/1.jpg', '2024-01-24', '/images/DSS/2024/5/6.jpg;\r\n/images/DSS/2024/5/3.jpg;\r\n/images/DSS/2024/5/5.jpg;\r\n/images/DSS/2024/5/4.jpg', '', '', '', '', 0, '', ''),
(874, '18', 'Почему стоит проводить занятия с глиной с детьми с РАС', 'Читайте наши ТОП-6 советов, почему стоит проводить занятия с глиной с детьми с расстройством аутистического спектра.', '<p>Читайте наши ТОП-6 советов, почему стоит проводить занятия с глиной с детьми с расстройством аутистического спектра.</p>\r\n<p>1️⃣ Тактильная стимуляция. Дети с расстройствами аутистического спектра часто имеют проблемы с тактильной чувствительностью. Работа с глиной позволяет им познакомиться с новыми ощущениями, развивать тактильную чувствительность и улучшать моторику рук.\r\n</p>\r\n<p>2️⃣ Эмоциональная выгрузка. Занятия с глиной помогают детям выразить свои эмоции и чувства. Они могут создавать различные формы, фигурки, отображая свои эмоции и переживания. Это способствует развитию самовыражения и общению.</p>\r\n<p>3️⃣ Концентрация и фокус. Наши ребята часто испытывают трудности с концентрацией. Занятия с глиной требуют усиленного внимания и фокуса на задаче. Это помогает детям развивать навыки концентрации и улучшать способность к обучению.</p>\r\n<p>4️⃣ Сенсорная интеграция. Работа с глиной стимулирует сенсорные системы детей - зрение, слух, осязание. Это помогает им лучше воспринимать окружающий мир и адаптироваться к новым ситуациям.</p>\r\n<p>5️⃣ Творческое самовыражение. Занятия с глиной дают нашим ребятам возможность развивать свою творческую натуру. Они могут свободно экспериментировать, создавать уникальные произведения и развивать свои творческие способности.\r\n</p>\r\n<p>6️⃣ Социализация. Занятия с глиной проводятся в группе, что способствует развитию социальных навыков детей. Они учатся работать в коллективе, сотрудничать, делиться материалами или идеями.</p>\r\n<p>Проект \"Давай дружить\" реализуется при поддержке «Губернского собрания общественности Иркутской области»</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239308&hash=674e05dc08a79694\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2024/5/2.jpg', '2024-01-25', '/images/grants/GS.png', '', '', '', '', 0, '', '14'),
(875, '1', 'Счастливое будущее для особенного школьника Ярика', 'Из-за расстройства аутистического спектра Ярику всегда было сложно понимать других и проявлять себя. АНО \"АПЦ Рассвет\" открыл сбор, чтобы ресурсный класс, в котором учится Ярик, продолжал работать. Подарим ребятам с аутизмом счастливое детство!', '<p>Улыбчивый мальчишка, который любит побаловаться, — таким всегда был Ярик. А в какой-то момент родителям стало ясно, что сын отличается от большинства сверстников.</p>\r\n<p>Родители пробовали водить Ярослава на развивающие занятия и в самые разные кружки, но ничего не получалось: он не слушал педагогов, не выполнял заданий и отказывался сидеть на месте.</p>\r\n<p>Однажды дефектолог посоветовала маме обратиться в «Рассвет» — адаптационно-педагогический центр в Иркутске, специалисты которого работают с детьми с помощью методов АВА (прикладного анализа поведения). </p>\r\n<p>Три года постоянных занятий сильно изменили Ярика: он стал самостоятельнее, научился контролировать себя, у него появились интересы и увлечения.</p>\r\n<p>Когда пришло время думать о школе, родители Ярика решили, что оптимальным вариантом для него будет ресурсный класс - образовательная модель, позволяющая детям с РАС учиться в обычных школах наравне со сверстниками. Такие классы уже четвертый год открываются и работают в Иркутске и Иркутском районе, благодаря поддержке \"Рассвета\". </p>\r\n<p>Первоклассник Ярик уже может похвастаться первыми успехами: в ресурсном классе он научился играть и общаться с другими детьми, слушать взрослых и сотрудничать с ними.</p>\r\n<p>Главное в ресурсных классах - это индивидуальный подход к каждому ребенку, обучение в нем происходит с применением методов прикладного анализа поведения. \"Рассвет\" выделяет ресурсному классу супервизора и куратора, без которых эффективная работа была бы просто невозможна. Куратор составляет учебную программу и программу работы с поведением и в случае необходимости корректирует их, получает от тьюторов данные о детях и анализирует их. Супервизор — более опытный поведенческий специалист, который помогает куратору и направляет его. </p>\r\n<p>АНО «АПЦ Рассвет» открыл благотворительный сбор на платформе \"VK Добро\", чтобы ресурсный класс, в котором учится Ярик, продолжал работу до конца учебного года. Благодаря собранным средствам куратор и супервизор класса в течение четырёх месяцев смогут получать зарплату.</p>\r\n<p><a class=\"button red-button\" href=\"https://dobro.mail.ru/projects/pomozhem-yariku-i-ego-druzyam-prodolzhit-uchitsya-v-shkole/\">Поддержать сбор</a></p>\r\n<p><b>Давайте поможем Ярику и его одноклассникам узнавать новое. Пусть особенные ребята растут, учатся и находят друзей!</b></p>\r\n<p></p>\r\n<div class=\"help-donate-team\">\r\n<br>\r\n<img src=\"/images/RK/2023/4.jpg\" alt=\"\">\r\n<div class=\"help-donate-team-desc\">\r\n<span class=\"help-donate-team-heading\">&#128149; Спасибо за вашу поддержку! &#128149;</span>\r\n</div></div>\r\n<blockquote><h5 align=\"left\">Об организации</h5>\r\n<p></p>										<p>АНО \"АПЦ Рассвет\" - некоммерческая организация родителей детей с расстройствами аутистического спектра (РАС) в городе Иркутске. Мы входим в реестр социально-ориентированных НКО Министерства юстиции РФ, рекомендованных для сотрудничества, и являемся членом Ассоциации некоммерческих организаций \"АУТИЗМ-РЕГИОНЫ\". Уже 5 лет успешно реализуем программы по четырем направлениям, которые охватывают разный возрастной диапазон. <a href=\"/company.php\">Подробнее об организации</a></p><br>								<p></p>	\r\n<p></p>	\r\n<div class=\"help-pride-items\">\r\n <a href=\"/projects/early_help.php\" class=\"help-pride-item\"><img class=\"help-pride-item-image\" src=\"/images/icons/1.png\" alt=\"\">\r\n<span class=\"help-pride-item-bold\">Ранняя помощь</span></a>\r\n<a href=\"/projects/education.php\" class=\"help-pride-item\"><img class=\"help-pride-item-image\" src=\"/images/icons/7.png\" alt=\"\"><span class=\"help-pride-item-bold\">Образование</span></a>\r\n<a href=\"/projects/socialization.php\" class=\"help-pride-item\">\r\n<img class=\"help-pride-item-image\" src=\"/images/icons/6.png\" alt=\"\">\r\n<span class=\"help-pride-item-bold\">Социализация и самореализация</span></a>\r\n <a href=\"/projects/information.php\" class=\"help-pride-item\"><img class=\"help-pride-item-image\" src=\"/images/icons/8.png\" alt=\"\">\r\n<span class=\"help-pride-item-bold\">Информирование</span></a>\r\n</div>\r\n</blockquote>\r\n<br>\r\n<div class=\"help-documents\">\r\n<a class=\"help-document\" href=\"/images/documents/s1.jpg\">\r\n<div class=\"help-document-title\">Свидетельство</div>\r\n<div class=\"help-document-desc\">о&nbsp;государственной регистрации</div></a>\r\n<a class=\"help-document\" href=\"/images/documents/s2.jpg\">\r\n<div class=\"help-document-title\">Свидетельство</div>\r\n<div class=\"help-document-desc\">о&nbsp;постановке на учет в&nbsp;налоговом органе</div>\r\n</a><a class=\"help-document\" href=\"https://drive.google.com/file/d/1w9mz5yCVCR2SfNuBdVa0lL_jBHyuSHjw/view?usp=sharing\">\r\n<div class=\"help-document-title\">Устав</div>\r\n<div class=\"help-document-desc\">АНО \"АПЦ Рассвет\"</div></a>\r\n<a class=\"help-document\" href=\"/project_reporting/2022.php\">\r\n<div class=\"help-document-title\">Публичный отчет</div>\r\n<div class=\"help-document-desc\">организации за 2022 год </div>\r\n</a>\r\n<a class=\"help-document\" href=\"https://drive.google.com/drive/folders/1KjEEUlxFZIS0Hph6nc8AGwX4l0ZtKcH0?usp=sharing\">\r\n<div class=\"help-document-title\">Отчеты организации</div>\r\n<div class=\"help-document-desc\">в Минюст за 5 лет</div>\r\n</a>\r\n<a class=\"help-document\" href=\"https://drive.google.com/file/d/1DUbxlhiUsU6A1thepnhE-IwtMOz-0H3-/view?usp=sharing\">\r\n<div class=\"help-document-title\">Договор - оферта</div>\r\n<div class=\"help-document-desc\">о добровольном пожертвовании</div></a>\r\n</div> \r\n<div class=\"help-questions\">\r\n<div class=\"accordion\">\r\n<ul>\r\n<li>\r\n<input type=\"checkbox\" checked>\r\n<i></i>\r\n<p class=\"help-question\">Реквизиты организации</p>\r\n<div class=\"msg\">\r\nАВТОНОМНАЯ НЕКОММЕРЧЕСКАЯ ОРГАНИЗАЦИЯ \"АДАПТАЦИОННО-ПЕДАГОГИЧЕСКИЙ ЦЕНТР РАССВЕТ\"<br>\r\nИНН: 3849068491  <br>\r\nКПП:384901001 <br>\r\nР/С: 40703810018350000827  <br>\r\nК/С: 30101810900000000607<br>\r\nБИК: 042520607<br>\r\nНАЗНАЧЕНИЕ ПЛАТЕЖА: пожертвование на ведение уставной деятельности\r\n</div>\r\n</li>\r\n</ul></div>\r\n</div>\r\n<p></p>	\r\n<a class=\"button red-button\" href=\"https://dobro.mail.ru/projects/pomozhem-yariku-i-ego-druzyam-prodolzhit-uchitsya-v-shkole/\">Поддержать сбор</a>', '/images/help/school_world/8.jpg', '2024-01-31', '', '', '', 'Новости', '', 0, '', ''),
(876, '4', 'Без паники! Идём к стоматологу!', 'Поход к стоматологу вызывают тревогу даже у взрослых, а у детей с высокой сенсорной чувствительностью и множеством страхов и стереотипов даже обычный осмотр полости рта может вызвать реакцию мелтдауна.', '<p>Поход к стоматологу вызывают тревогу даже у взрослых, а у детей с высокой сенсорной чувствительностью и множеством страхов и стереотипов даже обычный осмотр полости рта может вызвать реакцию мелтдауна.</p>\r\n<p>Неприятный опыт, близкое взаимодействие с врачом, процедуры в области рта, боль, яркий свет, шум и незнакомая обстановка делают поход к стоматологу кажущимся почти невозможным.\r\n</p>\r\n<p>Однако, игнорировать профилактические осмотры и острые ситуации нельзя, когда речь идет о здоровье.</p>\r\n<p>Умение спокойно (без истерик и агрессивного поведения) посещать стоматолога является важным социальным навыком. Ребенку пригодится эта умение не только для поддержания здоровья зубов, но и в других областях жизни, где ему может потребоваться взаимодействовать с медицинскими или учебными учреждениями.</p>\r\n<p>Проект «Без паники! Идем к стоматологу!» нацелен на детей с особенностями в развитии Иркутска.\r\nС помощью научно-обоснованных методик прикладного анализа поведения, зарекомендованных в работе над формированием важнейших социальных навыков, мы будем обучать детей доверять врачам и спокойно переносить медицинские процедуры.</p>\r\n<p>Впереди огромное количество занятий и мероприятий, следите за новостями!</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240723&hash=4ad5add39213e024\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>\r\n', '/images/ABA/2024/3.jpg', '2024-02-01', '', '', '', '', '', 0, '', '11'),
(879, '4', 'Как \"случайно\" научить ребенка просить?', 'Метод спонтанного обучения в ABA-терапии основывается на  использовании естественного интереса ребенка к окружающему миру и часто используется в комбинации с другими структурированными методами обучения.', '<p>Метод спонтанного обучения в ABA-терапии основывается на использовании естественного интереса ребенка к окружающему миру и часто используется в комбинации с другими структурированными методами обучения.</p>\r\n<p>Рассмотрим пример применения этого метода на практике.</p>\r\n<p>Ребенок стоит у холодильника и ждет, когда вы достанете ему любимую \"Агушу\". Вы каждый день угощаете его, поэтому вы прекрасно знаете, что если он встал на кухне, то пришло время перекуса. \r\nВы, как обычно, открываете дверку холодильника, достаете бутылочку, но вместо того, чтобы просто отдать ее ребенку, предлагаете ему попросить доступным ему способом: указательный жест, посмотреть на вас, попросить словом \"а\"\\\"агу\"\\\"агуша\"\\\"дай агушу\" или с помощью карточки.</p>\r\n<p>Что же делать, если ребенок еще не умеет просить и впадает в истерику, если ему не дать желаемое?</p>\r\n<p>В первую очередь нужно выбрать коммуникативную реакцию, доступную ребенку, которую вы будете формировать.  Начните с такой коммуникативной реакции, которую ребенку будет сделать быстро и не сложно. </p>\r\n<p>Если ребенок не владеет указательным жестом, то стоит начать именно с него. Сформировать у ребенка просьбу указательным жестом можно с помощью полной физической подсказки. Например, спросить «что ты хочешь?» и пальчиком ребенка указать на желаемый предмет. После этого четко озвучить название предмета и быстро отдать его ребенку. </p>\r\n<p>Когда указательный жест будет освоен (ребенок может попросить то, что хочет, просто показав пальчиком, без вырывания и истерик), можно начинать обучение конкретным просьбам - вокальным или <a href=\"https://rassvet-apc.ru/school-preparation/detail.php?id=512\" target=\"_blank\">с помощью карточек</a>.</p>\r\n<p>Если ребенок уже умеет повторять за вами слова или звуки и вы хотите обучить его вокальным просьбам - начинайте с элементарного для ребенка. А чтобы ребенок без напряга и с удовольствием повторял за вами, нужно работать над имитацией. О том как от имитации перейти к повторению слов читайте  <a href=\"/events/detail.php?id=821&back=news\" target=\"_blank\">здесь</a> </p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240361&hash=927b2ceb97136ad0\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe></iframe>\r\n</section>\r\n<p>Что еще можно посоветовать родителям при обучении просьбам?</p>\r\n<p>&#9989; Следовать естественным интересам ребенка</p>\r\n<p>&#9989; Поддерживать активность и самостоятельность</p>\r\n<p>&#9989; Предоставлять адекватные и не завышенные требования ребенку</p>\r\n<p>&#9989; Использовать бытовые ситуации для коммуникативных реакций: игра, прогулка, прием пищи, просмотр мультиков, переодевание, туалет и т.д.</p>\r\n<p>&#9989; Не настаивать, если у ребенка пропал интерес.</p>\r\n<p>При обучении просьбам в естественной среде также очень важно вовремя убрать подсказку, ведь нам нужны самостоятельные реакции! Как можно это сделать рассказали в видео-ролике про Пашу. </p>\r\n<p>Исходные данные: Паша умеет повторять трехсложные слова и обожает мыльные пузыри. Тем не менее, на первых занятиях мальчик предпочитал молчать и совершенно не демонстрировал просьбы у других людей.</p>\r\n<p>Наладив контакт с Пашей, нам удалось \"разговорить\" его и уже спустя 4 минуты усиленного тренинга ребенок просил вокально поиграть с ним в мыльные пузыри.\r\nЭтот же метод мы опробовали и на батуте, где удалось \"вытащить\" самостоятельные просьбы \"прыгать\", \"полетели\" и \"шарик\".</p>\r\n<p>Желаем Паше удачи, ведь интенсив только начался, предстоит много работы!</p>', '/images/ABA/2024/4.jpg', '2024-02-07', '', '/images/ABA/2024/4.jpg;U43YrJnC7Sc', '', 'Новости', '/news.php', 0, '', ''),
(880, '18', 'Возможно ли ребенка с аутизмом научить шить? ', 'Это один из вопросов, который часто задают родители детей с аутизмом. Ответ на него может быть неоднозначным, но определенно стоит попробовать! Шитье – это не только полезное и практичное умение, но и отличный способ развивать моторику рук, координацию движений и концентрацию.', '<p>Это один из вопросов, который часто задают родители детей с аутизмом. Ответ на него может быть неоднозначным, но определенно стоит попробовать!</p>\r\n<p>Шитье – это не только полезное и практичное умение, но и отличный способ развивать моторику рук, координацию движений и концентрацию. Для ребенка с аутизмом, которому может быть сложно сосредоточиться на задаче, шитье может стать настоящей помощью.</p>\r\n<p>Конечно, каждый ребенок уникален, поэтому важно учитывать его индивидуальные особенности и интересы. Начните с простых упражнений, таких как нанизывание бусин на нитку или шитье на бумаге. Постепенно усложняйте задания, добавляйте новые навыки и материалы.</p>\r\n<p>Важно понимать, что процесс обучения может занять время и потребует терпения. Ребенок с аутизмом может испытывать трудности с коммуникацией и восприятием инструкций. Постарайтесь установить доверительные отношения, обращайте внимание на его прогресс и поощряйте каждое достижение.</p>\r\n<p>Помимо шитья, важно также развивать другие навыки, которые могут пригодиться в будущем. Например, учите ребенка распознавать цвета и формы, использовать различные инструменты, понимать инструкции и следовать последовательности действий.</p>\r\n<p>Не забывайте, что каждый ребенок способен на большее, чем мы можем представить. Важно поддерживать его интересы и стимулировать его развитие. Шитье может стать не только средством для развития навыков, но и источником удовлетворения и самовыражения.</p>\r\n<p>Так что ответ на вопрос \"Возможно ли ребенка с аутизмом научить шить?\" – да, возможно! И, возможно, это откроет новые горизонты и возможности для вашего ребенка. Дайте ему шанс попробовать и раскрыть свой потенциал! </p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239339&hash=0f4d51ebb6e255b5\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2024/5.jpg', '2024-02-12', '', '', '', '', '', 0, '', ''),
(882, '1', '\"Они меня убили\"', 'Истории мам детей с расстройствами аутистического спектра про поход к стоматологу. \"Без паники! Идем к стоматологу!\" - наш стоматологический проект, в котором \"Рассвет\" будет учить ребят с РАС посещать зубного врача спокойно и без страха!', '<p>У каждой мамы ребенка с аутизмом есть своя история про поход к стоматологу. Некоторые из них можно почитать в прикрепленных фото. За каждой такой историей много боли и страха.</p>\r\n<p>\"Без паники! Идем к стоматологу!\" - наш стоматологический проект, в котором \"Рассвет\" будет учить ребят с РАС посещать зубного врача спокойно и без страха!\r\n</p>\r\n<p>Да, это возможно! Справиться с горьким опытом похода к врачу, когда ребенка приходилось силой удерживать в кресле. Вновь завоевать доверие и показать, что ухаживать за улыбкой и садиться в медицинское кресло можно без тяжелого нежелательного поведения!</p>\r\n<p>Уже в марте мы приглашаем родителей на вступительные семинары, на которых обсудим проблемы и определим дальнейший план действий! Следите за нашими анонсами .</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>\r\n\r\n', '/images/ABA/2024/Don\'t_panic/1.jpg', '2024-02-13', '/images/ABA/2024/Don\'t_panic/2.jpg;\r\n/images/ABA/2024/Don\'t_panic/3.jpg;\r\n/images/ABA/2024/Don\'t_panic/4.jpg;\r\n/images/ABA/2024/Don\'t_panic/5.jpg;\r\n/images/ABA/2024/Don\'t_panic/6.jpg;\r\n/images/ABA/2024/Don\'t_panic/7.jpg;\r\n/images/ABA/2024/Don\'t_panic/8.jpg;\r\n/images/ABA/2024/Don\'t_panic/9.jpg', '', '', 'Новости', '/news.php', 0, '', '11'),
(883, '4', 'Спокойная стрижка', 'Умение быть спокойным во время стрижки - это одна из целей в интенсивной программе нашего ученика Мирослава. Детям с расстройствами аутистического спектра и другими особенностями развития особенно сложно находиться в пугающих и нервирующих обстоятельствах.', '<p>Умение быть спокойным во время стрижки - это одна из целей в интенсивной программе нашего ученика Мирослава.\r\n</p>\r\n<p>Все мы знаем, как порой тяжело перебороть страх.\r\nНо мы не можем представить, насколько сложно детям с расстройствами аутистического спектра и другими особенностями развития находиться в пугающих и нервирующих обстоятельствах.</p>\r\n<p>Даже обычные повседневные ситуации, как мытье головы, поход к врачу, стрижка волос или ногтей, могут превратиться в настоящую проблему из-за сильной эмоциональной реакции ребенка.</p>\r\n<p>Существуют разные приемы работы со страхом.\r\nОдин из них называется систематической десенсибилизацией - метод поведенческой психотерапии Джозефа Вольпе, основанный на систематическом постепенном уменьшении чувствительности человека к предметам, событиям или людям, вызывающим тревожность и фобии. Метод систематической десенсибилизации причисляют к наиболее часто используемым методам поведенческой психотерапии.</p>\r\n\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240774&hash=77200b7847237bb0\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>\r\n<p>Подборка наших прошлых материалов про стрижку:</p>\r\n<p>Еще маленький Игорь и  <a href=\"https://rassvet-apc.ru/intensive/2.php\" target=\"_blank\">программа \"Стрижка\"</a> под курацией Дарьи Ли-Романовой (Дарья Гонконг).</p>\r\n<p>А <a href=\"https://rassvet-apc.ru/ambulance/detail.php?id=309&back=ambulance\" target=\"_blank\">здесь</a> рассказывали про метод систематической десенсибилизации и его широкое применении не только при стрижке, но и на массаже, при посещении врача. </p>', '/images/ABA/2024/5.jpg', '2024-02-21', '', '', '', 'Новости', '/news.php', 0, '', ''),
(885, '19', 'Как ресурсный класс повысил качество жизни Степы и его семьи', 'В ресурсном классе работает целая команда опытных специалистов: учитель ресурсного класса, тьюторы, логопед и дефектолог, учителя регулярных классов, а также куратор и супервизор, которые консультируют специалистов класса и родителей по вопросам обучения и работе с поведением.', '<p>«Мы познакомились со Степой, когда он готовился к поступлению в 1 класс коррекционной школы. Мальчик пришел к нам на занятия в «РАСсвет», чтобы подготовиться к новому этапу жизни, к поступлению в 1 класс коррекционной школы. Мы учили Степана заниматься в структуре и развивали академические навыки» - вспоминает куратор ресурсного класса, Елена Шапилова.</p>\r\n<p><b>История мамы Елены:</b></p>\r\n<p>«У Степы РАС, которое ярко проявляется в стереотипиях. Любые, даже малозначительные изменения, приводят его в состояние аффекта: смена привычной одежды, смена маршрута прогулки.</p>\r\n<p>Что уж говорить, когда осенью ребенок пошел в коррекционную школу, в совершенно новое для него место, это вызвало тяжелый стресс для него.\r\nУчителя долгое время не понимали, что делать с его беспокойствами, как ему помочь. Будучи спокойным и тихим мальчиком, в связи с большими переменами в жизни, к которым он был не готов, у Степы появилось неприемлемое поведение. Он отказывался делать что-либо в группе сверстников, учителя снижали требования и позволяли сидеть отдельно и рисовать, в то время, когда другие ребята учились и общались.</p>\r\n<p>Я была обеспокоена тем, что еще летом ребенок успешно сотрудничал и развивался, а придя в коррекционную школу, он начал отказываться что-либо делать.\r\n\r\nБыло принято решение поступать в первый ресурсный класс пос. Молодежный, так как прикладной анализ поведения зарекомендовал себя с самой лучшей стороны.\r\nГлавный критерий перехода – это будет лучше для Степы.</p>\r\n<p>На базе АНО «АПЦ РАСвет» подготовка к школе началась задолго до 1 сентября ресурсного класса. Школьники посещали подготовительные занятия, знакомились с учителем, с тьюторами. Для дошкольников были организованы ознакомительные поездки в их будущую школу, чтобы придя на праздник День знаний, для ребят не было стрессом оказаться в незнакомой обстановке с незнакомыми людьми.\r\nПредусмотрев, как минимум, вышеперечисленные ознакомительные мероприятия, ребенок уже будет легче адаптироваться к новому этапу в жизни.</p>\r\n<p>Что касается Степы, индивидуальный подход в ресурсном классе включает работу с стереотипами: спокойно относиться к новой одежде, к новым игрушкам, не переживать, если что-то пойдет не по плану. И, конечно, работа над навыками коммуникации. В школе его учат тому, что если он устал или ему очень сложно, то не нужно проявлять нежелательное поведение, достаточно просто сказать об этом и его услышат. Степа учится доверять.</p>\r\n<p>В ресурсном классе работает целая команда опытных специалистов: учитель ресурсного класса, тьюторы, логопед и дефектолог, учителя регулярных классов, а также куратор и супервизор, которые консультируют специалистов класса и родителей по вопросам обучения и работе с поведением.</p>\r\n<p>Существенной поддержкой для родителей является возможность получать обратную связь от специалистов класса и консультации от куратора и супервизора. Если нет возможностей для личной встречи, то консультации проводятся дистанционно.</p>\r\n<p>Поведенческие специалисты отлеживают динамику в обучении и поведении ребёнка и дают рекомендации родителям для поддержания коммуникативных и учебных навыков дома.</p>\r\n<p>Сейчас школа для Степана стала частью его жизни, он уже учится в третьем классе, постепенно осваивает учебную программу, выходит на уроки к сверстникам в регулярные классы, учится общаться и творит шедевры в творческих мастерских «Дом Синего Слона».</p>\r\n<p>Степан может самостоятельно изготовить формовую свечу без подсказок взрослого! Это невероятный прогресс! Этапы работы от расплавки воска при нужной температуре до уборки рабочего места – все это Степа освоил на групповых занятиях. Мы можем быть спокойны, ребенок успешно приобретает ремесленные навыки, которые в будущем смогут принести ему оплачиваемую работу!</p>\r\n<p>АНО \"АПЦ Рассвет\" - участник проекта «Ресурсный класс. Доступная среда». Благодарим организатора проекта Ассоциацию \"Аутизм-Регионы\".\r\nПроект реализуется при поддержке Фонда президентских грантов и благотворительного фонда \"Абсолют - Помощь\".</p>\r\n\r\n\r\n', '/images/RK/2024/2/1.jpg', '2024-02-22', '/images/RK/2024/2/2.jpg;\r\n/images/RK/2024/2/3.jpg;\r\n/images/RK/2024/2/4.jpg;\r\n/images/RK/2024/2/5.jpg;\r\n/images/RK/2024/2/6.jpg', '', '', 'Новости', '/news.php', 0, '', ''),
(886, '4', 'Как сделать ЭКГ без проблемного поведения', 'Игорь пришел с мамой в поликлинику на электрокардиографию. Если бы мальчик не был подготовлен к этой процедуре заранее, то итог мог бы быть печальным. Что помогло? АВА-терапия!', '<p>Игорю 11 лет. У него аутизм и снижено понимание речи. Он пришел с мамой в поликлинику на электрокардиографию. Если бы Игорь не был подготовлен к этой процедуре заранее, то итог мог бы быть печальным. </p>\r\n<p>К сожалению, с подобными ситуациями сталкиваются многие родители детей с аутизмом.</p>\r\n<p>Еще совсем недавно Игорь не мог даже просто зайти в кабинет к доктору, были истерики и агрессивное поведение по отношению к себе, родителям и врачам. Однажды, Игорь разбил головой зеркало в кабинете.</p>\r\n<p>Мы долго готовились к этой процедуре, чтобы Игоря не охватывала паника при виде врача, кушетки и этих страшных холодных присосок, от которых замирает дух.</p>\r\n<p>Что помогло?</p>\r\n<p>✅ АВА-терапия</p>\r\n<p>С помощью методик прикладного анализа поведения и регулярных занятий ребенку удалось показать, что именно хочет сделать доктор, что в этой процедуре нет ничего страшного и ее можно пережить абсолютно спокойно.</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240782&hash=9fb999101979f0ef\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>\r\n', '/images/ABA/2024/6.jpg', '2024-02-26', '', '', '', '', '', 0, '', '11'),
(887, '16', '\"День НКО\" на ВДНХ', '24 февраля на выставке «Россия» на ВДНХ (г. Москва) прошел \"День некоммерческих организаций\". Руководитель АНО \"АПЦ Рассвет\" Екатерина Родыгина приняла участие в этом мероприятии и выступила в роли эксперта в ток-шоу «НКО. Правда или миф?»', '<p>24 февраля на выставке «Россия» на ВДНХ (г. Москва) прошел \"День некоммерческих организаций\". Лидеры НКО со всей страны приехали, чтобы рассказать гостям и СМИ о своей деятельности, найти новых единомышленников, оглянуться назад и подвести итоги семилетнего сотрудничества с Фондом президентских грантов.</p>\r\n\r\n<p>Что было в программе: </p>\r\n<p>&#8226; аналитическая секция о динамике развития некоммерческого сектора с 2000 года; </p>\r\n<p>&#8226; встреча с первым заместителем Руководителя Администрации Президента Российской Федерации Сергеем Владиленовичем Кириенко, где каждый мог задать интересующие вопросы и получать развёрнутые ответы по дальнейшим стратегиям развития некоммерческого сектора в России;</p>\r\n<p>&#8226; тематические прикладные дискуссии с авторами социальных проектов; </p>\r\n<p>&#8226; ток-шоу «НКО. Правда или миф?» про стереотипы, связанные с некоммерческим сектором, в котором приняли участие руководители организаций и посетители выставки;</p>\r\n<p>&#8226; а также мастер-классы, спектакли и викторины, подготовленные самими некоммерческими организациями для гостей выставки.</p>\r\n<p>Руководитель АНО \"АПЦ Рассвет\" Екатерина Родыгина приняла участие в этом мероприятии и выступила в роли эксперта в ток-шоу «НКО. Правда или миф?», где развеивала мифы об аутизме. </p>\r\n<p>Екатерина поделилась впечатлениями об этом дне на своей странице в социальной сети \"В контакте\":</p>\r\n<p>\"День был невероятно продуктивным, и в первую очередь я благодарна за возможность поделиться на огромную аудиторию самым важным для меня - о проблемах аутизма.</p>\r\n<p>Мы поговорили о том, что в фильмах и медиа часто создается впечатление, что дети с аутизмом гениальны. Однако, это не всегда соответствует действительности.</p>\r\n<p>Важно помнить, что у нас на слуху больше исключение из правил, потому что это яркие и запоминающиеся феномены личности. А другая сторона аутизма зачастую нахо́дится в четырёх стенах, причём происходит аутизация всей семьи, ведь мама не в состоянии отойти от такого ребёнка ни на минуту. У детей с аутизмом из-за поведенческих преград, сенсорных нарушений и трудностей коммуникации ещё не так давно стояло клеймо \"необучаемый\". Но при правильном подходе к ним мы можем подобрать ключик, чтобы развивать их навыки и интересы. По опыту со своими детьми я вижу, что именно работа с поведением снижает преграды к обучению и помогает им развивать свои сильные стороны и преодолевать трудности...\"</p>\r\n<p>Главные итоги семилетней работы Фонда президентских грантов, дискуссии деловой программы, запись подкастов и прямых эфиров СМИ – все это можно найти в <a href=\"https://vk.com/video-143830192_456240468\" target=\"_blank\">итоговом видео</a> о «Дне некоммерческих организаций\" на выставке «Россия» на странице Фонда президентских грантов. </p>', '/images/news/2024/1/3.jpg', '2024-02-27', '/images/news/2024/1/1.jpg;\r\n/images/news/2024/1/2.jpg;\r\n/images/news/2024/1/4.jpg;\r\n/images/news/2024/1/5.jpg;\r\n/images/news/2024/1/6.jpg;\r\n/images/news/2024/1/7.jpg', '', '', '', '', 0, '', ''),
(888, '4', 'Подготовка к УЗИ', 'Метод систематической десенсибилизации является одним из способов подготовки детей к процедуре УЗИ.\r\nОн основан на постепенном привыкании ребенка к стимулам, которые вызывают у него беспокойство или стресс.', '<p>Метод систематической десенсибилизации является одним из способов подготовки детей к процедуре УЗИ.\r\nОн основан на постепенном привыкании ребенка к стимулам, которые вызывают у него беспокойство или стресс.</p>\r\n<p>1️⃣ Начните с постепенного знакомства ребенка с процедурой УЗИ. Объясните процедуру заранее, в зависимости от уровня понимания речи ребенка. Используйте простые и понятные слова, визуальные подсказки (картинки, видео).</p>\r\n<p>2️⃣ Постепенно увеличивайте время, которое ребенок проводит в окружении медицинских инструментов и оборудования, связанных с УЗИ. Начните с коротких сессий, постепенно увеличивая их продолжительность.</p>\r\n<p>3️⃣ Позитивно подкрепляйте поведение ребенка во время подготовки и проведения процедуры.\r\nИспользуйте мотивационные поощрения, чтобы помочь ребенку чувствовать себя уверенно и заинтересованно.</p>\r\n<p>4️⃣ Если вы видите, что ребенок выражает беспокойство на этапе подготовки, не бойтесь делать шаг назад и прорабатывать страх более тщательно.\r\nНи в коем случае не настаивайте и не удерживайте ребенка!</p>\r\n<p>5️⃣ Важно помнить, что каждый ребенок уникален, поэтому метод систематической десенсибилизации требует индивидуального подхода и тщательного анализа потребностей и способностей конкретного ребенка. Советуем обратиться к специалисту, который владеет данным методом.</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240796&hash=62f40ebfc6e01341\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>\r\n', '/images/ABA/2024/7.jpg', '2024-02-28', '', '', '', '', '', 0, '', '11'),
(889, '1', 'Семинары «Без паники! Идем к стоматологу!»', 'Приглашаем родителей и педагогов посетить семинары «Без паники! Идем к стоматологу! Курс выживания» (в двух частях) в Иркутске 23 и 30 марта в 11:00 в библиотеке им. Молчанова-Сибирского АБСОЛЮТНО БЕСПЛАТНО!', '<p>Приглашаем родителей и педагогов Иркутска посетить семинары <b>«Без паники! Идем к стоматологу! Курс выживания»</b> (в двух частях) АБСОЛЮТНО БЕСПЛАТНО!</p>\r\n<p>&#128197; 23 и 30 марта</p>\r\n<p>&#128344; начало в 11 часов</p>\r\n        			<p>&#128205; г.Иркутск, ул. Лермонтова, 253 (Библиотека им. Молчанова-Сибирского)</p>\r\n<p><b>Спикеры</b>: Родыгина Екатерина Андреевна - председатель АНО \"АПЦ Рассвет\", поведенческий аналитик, врач, а также специалисты АНО «АПЦ РАСсвет»</p>\r\n<p><b>23 марта</b> на первой части семинара вы узнаете:</p>\r\n<p>- о заболеваниях полости рта и их профилактике у детей с особенностями развития</p>\r\n<p>- о научно-доказательных методах работы со страхами, которые помогают не только детям, но и взрослым, на примерах из реальной жизни учеников нашего центра</p>\r\n<p>- о проекте «Дружелюбная стоматология» по полису ОМС. Позитивный опыт других городов</p>\r\n<p>- о том, как привить гигиенические привычки детям в любом возрасте</p>\r\n<p>Также Вас ждет практическая тренировка и ответы на вопросы.</p>\r\n<p><b>30 марта</b> во второй части семинара мы\r\nпродолжим обсуждать вопросы, касаемые лечения и профилактики зубных заболеваний у детей с расстройствами аутистического спектра и другими особенностями развития с ведущими экспертами АНО «АПЦ РАСсвет».</p>\r\n<p>- Как обучить своего ребенка?</p>\r\n<p>- Может ли обычный педагог научить ребенка жизненно-необходимым навыкам «спокойного» похода к врачу?</p>\r\n<p>- Методики прикладного анализа поведения на практике.</p>\r\n<p><b>ВАЖНАЯ ИНФОРМАЦИЯ</b>: Для дальнейшего участия в проекте «Без паники! Идем к стоматологу!» вам необходимо посетить оба семинара и заполнить тестовые бланки, которые будут выдаваться на семинарах 23.03 и 30.03.</p>\r\n<p>Исходя из результатов тестирований «РАСсвет» будет набирать группы ребят для дальнейшего обучения навыкам «спокойного» похода к врачу на базе АПЦ «РАСсвет» с участием настоящих стоматологов.</p>\r\n<p><a href=\" https://forms.yandex.ru/u/65c1f28ec09c020469968dd6/\" class=\"button red-button\">Зарегистрироваться</a></p>\r\n<p>До встречи на семинарах! ❤️</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>', '/images/seminar/2024/2.jpg', '2024-03-06', '', '', '', 'Новости', '/news.php', 0, '', '11'),
(890, '3', 'Прямой эфир \"Опытные родители: как выжить у стоматолога\"', '2 марта на странице \"АПЦ Рассвет\" в VK прошел прямой эфир, на котором опытные родители делились результатами одного из прошлый проектов \"Рассвета\" по подготовке ребят к врачебному осмотру, в том числе осмотру у стоматолога.', '<p>2 марта на странице \"АПЦ Рассвет\" в VK прошел прямой эфир, на котором опытные родители делились результатами одного из прошлый проектов \"Рассвета\" по подготовке ребят к врачебному осмотру, в том числе осмотру у стоматолога.</p>\r\n<p>Дети с опасным проблемным поведением, у которых снижено понимание обращенной речи, учились посещать врачей и позволять проводить медицинские осмотры и процедуры.</p>\r\n<p>Эфир провела Михеева Елена Сергеевна, поведенческий аналитик и мама Вани, ребенка с аутизмом, который также принимал участие в проекте.</p>\r\n<p>Гостем эфира стала Татьяна Лимановская, мама Влада, который также принимал участие в проекте и в результате смог посетить стоматолога.</p>\r\n<p>Во время эфира речь шла о том, какие подготовительные процедуры и мероприятия проводились для подготовки ребят к осмотру стоматолога, что помогло договориться с ребенком, какие сложности были на пути и как обстоят дела с посещением врачей сейчас, спустя время.\r\n</p>\r\n<p>Также во время эфира Елена Михеева рассказала, как попасть в новый проект Рассвета \"Без паники! Идем в стоматологу!\", в котором на базе центра будет проходить подготовка ребят с РАС к походу к стоматологу АБСОЛЮТНО БЕСПЛАТНО!</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240807&hash=a644ec684dfcc769\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen class=\"social-video\"></iframe>                        </section>\r\n', '/images/seminar/2024/3.jpg', '2024-03-03', '', '', '', 'Новости', '/news.php', 0, '', '11'),
(891, '1', 'ИНДИВИДУАЛЬНАЯ ПРАКТИЧЕСКАЯ СТАЖИРОВКА В \"РАССВЕТЕ\"', '30-ти часовая индивидуальная практическая АВА-стажировка для педагогов и тьюторов, на которой мы расскажем про эффективные методики работы с социально-важным поведением детей и подростков с особенностями развития и покажем, как их грамотно применять в педагогической деятельности, чтобы видеть результат от занятий!', '<p>30-ти часовая индивидуальная практическая АВА-стажировка для педагогов и тьюторов, на которой мы расскажем про эффективные методики работы с социально-важным поведением детей и подростков с особенностями развития и покажем, как их грамотно применять в педагогической деятельности, чтобы видеть результат от занятий!</p>\r\n<p>&#128197; В удобные для Вас даты</p>\r\n					<p>&#128344; 6 часов в день </p>\r\n           			<p>&#128205; Иркутск, Байкальская, 234в/2</p>\r\n           			<p>&#128091; 1 неделя — 8.000 руб.</p>\r\n					<p>Стажировка рассчитана на одну неделю (25 часов практической работы с терапистом и 5 часов теоретической работы с куратором).</p>\r\n					<p>Запись на стажировку по телефону: 89526236675</p>\r\n<p><span class=\"help-donate-team-heading\"><a href=\"tel:+79526236675\" class=\"button red-button\">&#9742; Записаться</a></span></p>', '/images/seminar/2024/1.jpg', '2024-01-25', '', '', '', 'Новости', '/news.php', 0, '', ''),
(892, '18', 'Создаём кашпо из бетона', 'На этой неделе мы с ребятами создавали модные кашпо из бетона. В этой статье делимся пошаговым рецептом изготовления. Запоминайте или записывайте!', '<p>На этой неделе мы с ребятами создавали модные кашпо из бетона. В этой статье делимся пошаговым рецептом изготовления. Запоминайте или записывайте!</p>\r\n<p>1️⃣ Первое, что мы делаем - добавляем бетон в воду (или воду в бетон) и хорошенько всё промешиваем, чтобы не было комочков.\r\n</p>\r\n<p>2️⃣ Далее мы добавляем вермикулит и тоже хорошо перемешиваем.</p>\r\n<p>3️⃣ Затем добавляем краситель. На этот раз сильно не размешиваем, чтобы создать эффект мрамора. Но если вы хотите добиться однотонности, то можно размешать до однородной окраски.</p>\r\n<p>4️⃣ Готовую смесь добавляем в заранее подготовленные формы. </p>\r\n<p>5️⃣ Наполненные формы аккуратно постукиваем, чтобы позволить пузырькам воздуха выйти наружу.\r\n</p>\r\n<p>Посмотрите, какие классные изделия можно получить таким простым способом!</p>\r\n<p>Проект \"Давай дружить\" реализуется при поддержке «Губернского собрания общественности Иркутской области»</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239364&hash=c8ce993570d2c8e4\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2024/6.jpg', '2024-03-04', '/images/grants/GS.png', '', '', '', '', 0, '', '14'),
(893, '10', 'Проект «Дом синего слона»', 'Статус проекта: завершен<br>\r\nСроки реализации: 01.03.2024 - 31.12.2024<br>\r\nИсточник софинансирования: Департамент здравоохранения и социальной помощи населению администрации г. Иркутска\r\n', '<p><b>Статус проекта:</b> завершен<br>\r\n<b>Сроки реализации:</b> 01.03.2024 - 31.12.2024<br>\r\n<b>Источник софинансирования:</b> Департамент здравоохранения и социальной помощи населению администрации г. Иркутска</p>\r\n<p></p>\r\n<p><b>Цель проекта:</b></p>\r\n<p>Социальная поддержка проживающих на территории г. Иркутска детей-инвалидов, детей из семей инвалидов, лиц с ограниченными возможностями здоровья (ОВЗ), в том числе детей и подростков с РАС, путем коррекции поведения,  развития социальных навыков, формирования допрофессиональных навыков в мастерских «Дом синего слона» и создания комфортной среды в окружающем детей и подростков с ОВЗ пространстве.   </p>\r\n<p><b>Задачи:</b></p>\r\n<p>1. Создать условия для трудовой занятости в мастерских «Дом синего слона» для детей с ОВЗ с учетом их индивидуальных потребностей.  </p>\r\n<p>2. Сформировать у детей с ОВЗ приемлемое учебное поведение для занятий в мастерских путем предварительной подготовки на индивидуальных занятиях с дефектологами и психологами.</p>\r\n<p>3. Сформировать допрофессиональные  навыки у детей с ОВЗ на регулярных занятиях в мастерских «Дом синего слона». </p>\r\n<p>4. Обобщить навыки коммуникации и социализации детей и подростков с ОВЗ, полученных на индивидуальных и групповых занятиях, посредством обучения посещения общественных мест и мероприятий в г. Иркутске.</p>\r\n<p>5. Улучшить коммуникацию детей с ОВЗ со сверстниками путем включения детей с нормальным развитием в совместные мероприятия.  </p>\r\n<p><b>Краткое описание:</b></p>\r\n<p>Проект направлен на формирование предпрофессиональных и социальных навыков у 50 детей с ОВЗ посредством индивидуальных и групповых занятий с психологом, дефектологом и мастерских с использованием методов Прикладного анализа поведения, имеющих научно доказанную эффективность, посещения мастер-классов, общественных мест, культурных мероприятий.</p>\r\n<p>На регулярных занятиях в мастерских «Дом синего слона» 20 детей с ОВЗ (из 50 детей с ОВЗ) получат регулярную социальную поддержку. Остальные 30 детей, которые еще не готовы к формату мастерских из-за поведенческих преград, пройдут индивидуальное обучение с психологом и дефектологом, где научатся учебному поведению и жизненно-важным навыкам, которые в дальнейшем позволят встроиться в занятия мастерских.</p>\r\n<p>На занятиях в мастерских дети и подростки с ОВЗ, помимо обучения технике изготовления продукции, в зависимости от их поведенческих особенностей проработают учебное поведение и коммуникацию с педагогами и сверстниками. В группах обобщатся навыки, изучаемые в индивидуальном формате с педагогом, реализуются процессы приучения подростков с ОВЗ к взаимодействию друг с другом и с педагогами и регуляции нежелательного поведения. Подготовка к занятиям включает создание визуального расписания этапов работы, что облегчает понимание этапов изготовления продукции и уменьшает у ребят проявление нежелательных форм поведения на занятиях благодаря структурированию учебных процессов.</p>\r\n<p><a href=\"\" class=\"button\">Публикации о реализации проекта</a></p>', '/images/grants/logo_irkutsk.png', '2025-03-01', '', '', '', 'Гранты', '/pages/grants.php', 0, '', '');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(894, '10', 'Проект «Я могу: обучение жизненно-важным навыкам детей с РАС»', 'Статус проекта: действующий<br>\r\nСроки реализации: 01.03.2024 - \r\n 28.02.2025<br>\r\nИсточник софинансирования: Благотворительный фонд \"Абсолют-помощь\"\r\n', '<p><b>Статус проекта:</b> действующий<br>\r\n<b>Сроки реализации:</b> 01.03.2024 -  28.02.2025<br>\r\n<b>Источник софинансирования:</b> Благотворительный фонд \"Абсолют-помощь\" </p>\r\n<p></p>\r\n<p><b>Цель проекта:</b></p>\r\n<p>Обеспечить детям с расстройствами аутистического спектра 3-12 лет, проживающим на территории Иркутской области и Забайкальского края, доступ к услугам, способствующим формированию жизненно-важных навыков: умение общаться, правила гигиены, пользование туалетом, прием пищи, самообслуживание и речь.</p>\r\n<p><b>Задачи:</b></p>\r\n<p>1. Познакомить не менее 300 родителей, подозревающих у своего ребенка расстройство аутистического спектра, с симптоматикой РАС, методами диагностики и научно-обоснованными методами обучения детей с РАС.</p>\r\n<p>2. Провести для выбранной группы детей с РАС (25 чел) программу обучения и развития жизненно-важных навыков, состоящую из социального взаимодействия, посещения туалета, приема пищи и самообслуживания.</p>\r\n<p>3. Интегрировать у 25 детей с РАС жизненно-важные навыки, освоенные в ходе программы обучения, в ситуации, смоделированные на стажировке с родителями, групповых занятиях и инклюзивном празднике.</p>\r\n\r\n<p><b>Краткое описание:</b></p>\r\n<p>Приобретение жизненно важных навыков - основа развития любого человека. Детей с расстройствами аутистического спектра (РАС) из-за особенностей поведения считают необучаемыми, им сложно даются действия, которые для нас просты и повседневны. В формировании навыков у детей с РАС мы используем принципы прикладного анализа поведения (ПАП), нацеленные на снижение интенсивности нежелательного поведения и преодоление преград к развитию. </p>\r\n<p>Мы обеспечим для 25 детей с РАС в возрасте 3–12 лет Иркутской области и Забайкальского края доступ к услугам, способствующим формированию жизненно-важных навыков (умение общаться, правила гигиены, пользование туалетом, прием пищи, самообслуживание и речь). 300 семей, которые подозревают у своего ребенка РАС, с помощью заполнения анкеты, размещенной в социальных сетях «АПЦ Рассвет» и информационных партнеров проекта, таргетированной рекламы в ВК и Яндекс.Директе, получат на электронную почту информационные брошюры, из которых узнают о красных флагах аутизма, медицинских аспектах расстройства, эффективных научно обоснованных методах диагностики и абилитации, а также получат ссылку приглашение на вебинар, на котором поведенческий аналитик расскажет о симптоматике РАС и ответит на вопросы слушателей. Это способствует тому, что родители своевременно начнут диагностику ребенка, смогут использовать в коррекции РАС только научно-обоснованные методы, избегая траты времени и средств на методики, не приносящие эффекта.</p>\r\n<p>На выездных семинарах в г. Братск, г. Чита и г. Иркутск для 60 родителей детей с РАС (по 20 родителей на каждом мероприятии) мы обсудим основы ПАП, методы по формированию жизненно-важных навыков у детей с РАС, проведем очные консультации для детей, проживающих в г.Братске и г.Чите. Из числа семей, принявших участие в вебинаре, выездных семинарах и заполнивших анкетирование, 60 детей с РАС с участием родителей пройдут консультации поведенческого аналитика, где специалист выявит зоны развития, даст рекомендации по дальнейшей работе с ребенком и формированию у него жизненно-важных навыков.  На консультациях определятся 25 семей из г. Иркутска, Братска, Читы, чьи дети нуждаются в немедленной абилитации. 15 детей с РАС получат регулярное индивидуальное обучение жизненно важным навыкам, согласно выявленным на тестировании преградам и пробелам в вехах развития ребенка. Для 10 детей, проживающих в Иркутской области и Забайкальском крае, пройдут интенсивные индивидуальные занятия по 4 часа в день в течение месяца, после которых дети продолжат занятия на регулярных занятиях. На стажировке с родителями дети с РАС обобщат полученные на занятиях навыки, родители пройдут практическое и теоретическое обучение на занятиях со своими детьми по работе с навыками для дальнейшей самостоятельной работы в натуральной среде. На групповые занятия дети с РАС будут поочередно привлекаться на 2 часа в неделю по 2-3 ребенка для обобщения освоенных навыков на индивидуальных занятиях и обучения социальному взаимодействию со сверстниками (спокойное нахождение в группе, понимание фронтальных инструкций, просьбы социально приемлемым способом, выстраивание диалога, взаимодействие в простой игре). На новогоднем празднике дети с РАС отработают навыки ожидания своей очереди, десенсибилизации к громким звукам и взаимодействия в игре (хождение в хороводе, кидание снежков), социально приемлемых просьб и спонтанной имитации.  По завершению регулярных занятий, пройдет повторная диагностика навыков 25 детей с помощью VB-MAPP, AFLS или ABLLS-R (выбор инструмента зависит от возраста и навыков конкретного ребенка), а родители получат рекомендации для дальнейшей адаптации ребенка дома. Результаты тестирования продемонстрируют положительную динамику после занятий у каждого ребенка в среднем на 5 баллов по шкале выбранного тестирования в следующих областях: учебное поведение, навыки коммуникации, самообслуживания, социальные навыки: умение взаимодействовать, имитировать действия сверстников, умение общаться, в том числе с использованием альтернативных способов коммуникации, что в дальнейшем позволит ребенку социализироваться в обществе. </p>\r\n<p><a href=\"https://rassvet-apc.ru/news.php?project=12\" class=\"button\">Публикации о реализации проекта</a></p>', '/images/partners/absolut.jpg', '2024-03-01', '', '', '', 'Гранты', '/pages/grants.php', 0, '', ''),
(895, '10', 'Проект «Расскажи о спорте»', 'Статус проекта: действующий<br>\r\nСроки реализации: 01.03.2024 - 31.12.2025<br>\r\nИсточник софинансирования: Благотворительный фонд Владимира Потанина', '<p><b>Статус проекта:</b> действующий<br>\r\n<b>Сроки реализации:</b> 01.03.2024 - 31.12.2025<br>\r\n<b>Источник софинансирования:</b> Благотворительный фонд Владимира Потанина</p>\r\n<p></p>\r\n<p><b>Цель проекта:</b></p>\r\n<p>Включение детей с РАС 5-12 лет в инклюзивную спортивную жизнь посредством внедрения методов ПАП в занятия и дальнейшим тиражированием опыта среди специалистов, работающих с детьми с РАС и их родителей.</p>\r\n<p><b>Задачи:</b></p>\r\n<p>1. Подготовить не менее 35 детей с РАС 5-12 лет к инклюзивным занятиям спорту путем коррекции нежелательного поведения при использовании принципов ПАП на групповых и индивидуальных занятиях АФК.</p>\r\n<p>2. Научить педагогов физкультуры использовать методы ПАП на занятиях с детьми с РАС 8-12 лет путем прохождения стажировки в «АПЦ Рассвет» и сопровождения занятий физкультуры на базе школ поведенческими специалистами.</p>\r\n<p>3. Тиражировать опыт занятий физкультурой с детьми с РАС по принципам ПАП среди специалистов и родителей, решающих проблемы детей с РАС с использованием научных подходов.</p>\r\n<p><b>Краткое описание:</b></p>\r\n<p>В рамках проекта предполагается подготовить детей с РАС 5-7 лет на индивидуальных занятиях АФК с применением методов прикладного анализа поведения (ПАП) для коррекции сложных форм нежелательного поведения, мешающих детям участвовать в групповых занятиях спортом, для возможности включения ребят в ресурсные классы общеобразовательных школ. Учащиеся в ресурсных классах дети с РАС 8-12 лет обучатся в мини-группах учебному поведению и навыкам взаимодействия для последующего включения в инклюзивные занятия по физической культуре на базе общеобразовательных школ (МОУ СОШ №17, ИРМО СОШ п. Молодежный, Марковская СОШ №2). Дети с РАС дошкольного возраста обобщат навыки взаимодействия на эстафетах, а ученики ресурсных классов поучаствуют в спортивных соревнованиях совместно со сверстниками.</p>\r\n<p>Параллельно 2 специалиста АФК \"АПЦ Рассвет\" углубят знания в ПАП на сертифицированных курсах и обменяются опытом проведения соревнований и использования элементов сенсорной интеграции на занятиях физкультурой с детьми с РАС в ЛРОО «Вырастите мир. Аутизм в Липецке». Полученным опытом специалисты «Рассвета» поделятся с не менее 5 педагогами физкультуры общеобразовательных школ и МООР «Ребенок с будущим» г. Волжска на стажировке, где подарят разработанные методические пособия по применению методов ПАП в адаптивном спорте с детьми с РАС. Специалисты «АПЦ РАСсвет» помогут педагогам общеобразовательных школ внедрить методы ПАП в уроки физкультурой с учениками с РАС с последующей оценкой динамики ребят: оценка по VB-MAPP, по 5 разработанным адаптивным критериям и анкетирование родителей. Кураторы и тренеры АФК «АПЦ Рассвет», МООР \"Ребенок с будущим и ЛРОО «Вырастите мир. Аутизм в Липецке» при поддержке Ассоциации «Аутизм-Регионы» поделятся опытом внедрения занятий АФК для детей с РАС в общеобразовательные учреждения на вебинаре и научно-практической конференции «Инклюзивное образование — инклюзивное общество» и распространят разработанные методические пособия среди участников конференции.</p>\r\n\r\n<p><a href=\"/news.php?project=16\" class=\"button\">Публикации о реализации проекта</a></p>', '/images/AFK/logotip.jpg', '2024-03-01', '', '', '', 'Гранты', '/pages/grants.php', 0, '', ''),
(896, '18', 'Диванные подушки от наших ребят', 'В нашу инклюзивную мастерскую поступил интересный заказ - изготовление 10 диванных подушек! Мы с радостью принялись за работу и принялись раскраивать ткань для наших участников.', '<p>В нашу инклюзивную мастерскую поступил интересный заказ - изготовление 10 диванных подушек! Мы с радостью принялись за работу и принялись раскраивать ткань для наших участников.</p>\r\n<p>А затем в нашу мастерскую пришли Влад, Тася, Степа и Ника - наши неутомимые и талантливые швеи. Они сразу взялись за работу и начали создавать эти прекрасные подушки. Каждый из них добавляет в работу свою изюминку и оригинальность, что делает этот процесс ещё более интересным.</p>\r\n<p>Смотрите, как здорово у них получается! Никто бы не сказал, что вся эта красота создана руками детей с особыми потребностями. Эти подушки будут не только украшением для дивана, но и символом того, что каждый человек, независимо от своих особенностей, может преуспеть в творчестве и делах.</p>\r\n<p>Мы очень гордимся нашими ребятами и их достижениями. Надеемся, что подушки, которые они создали, принесут много уюта и радости тому, кто их приобретёт. А мы продолжим трудиться и помогать нашим участникам развиваться и раскрывать свой потенциал.</p>\r\n<p>Приходите к нам в мастерскую, чтобы увидеть и по достоинству оценить результаты их труда. И не забывайте, что вместе мы можем преодолеть любые трудности и создать красоту вместе!</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239376&hash=15e2a99f10e4527e\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2024/7.jpg', '2024-03-11', '', '', '', '', '', 0, '', ''),
(897, '4', 'Как научить ребенка с аутизмом чистить зубы', 'Эта задача может быть вызовом для многих родителей, но с правильным подходом и терпением она может стать возможной. Во-первых, важно понять, что каждый ребенок уникален и может иметь свои особенности и трудности. ', '<p>Эта задача может быть вызовом для многих родителей, но с правильным подходом и терпением она может стать возможной. Во-первых, важно понять, что каждый ребенок уникален и может иметь свои особенности и трудности. Поэтому, необходимо прислушиваться к его потребностям и находить индивидуальный подход к обучению чистке зубов.</p>\r\n<p>Одним из первых шагов может быть визит к стоматологу. Специалист сможет оценить состояние зубов ребенка и дать рекомендации по уходу за ними. Также, стоматолог может показать родителям, как правильно чистить зубы и дать советы по выбору подходящей зубной щетки и пасты.</p>\r\n<p>Следующим шагом будет создание регулярной рутины чистки зубов. Ребенок с аутизмом часто стремится к стабильности и предсказуемости, поэтому важно создать расписание и придерживаться его. Например, можно установить определенное время для чистки зубов после каждого приема пищи.</p>\r\n<p>Для многих детей с аутизмом может быть сложно справиться с изменениями в рутине. Поэтому, чтобы сделать процесс чистки зубов более предсказуемым, можно использовать визуальные подсказки. Например, можно создать картинку с последовательностью действий: открыть зубную щетку, нанести пасту, чистить зубы и смыть пену. Это поможет ребенку понять, что происходит и что от него ожидается.\r\n</p>\r\n<p>Некоторым детям может быть неприятно ощущение чистки зубов, поэтому необходимо быть терпеливыми и искать альтернативные способы. Например, можно попробовать использовать специальные щетки для массажа десен или пасту с приятным вкусом.</p>\r\n<p>Самое важное - это создать положительную атмосферу и делать процесс чистки зубов интересным и игровым. Используйте игрушки, песни или небольшие награды, чтобы мотивировать ребенка. Помните, что наша главная цель - сохранить здоровье зубов и улыбку нашего ребенка.</p>\r\n<p>Надеемся, эти советы помогут вам научить ребенка чистить зубы. Помните, что каждый шаг вперед – это важная победа! Будьте терпеливыми, поддерживайте своего ребенка и верьте в его способности. С вами все получится!</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240832&hash=416755e2d0eb6ef7\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>\r\n', '/images/ABA/2024/8.jpg', '2024-03-11', '', '', '', '', '', 0, '', '11'),
(899, '13', 'Институт развития образования заключил соглашение о сотрудничестве с АНО «АПЦ РАСсвет»', '20 апреля 2023 года между ГАУ ДПО ИРО и АНО «АПЦ РАСсвет» было заключено соглашение о сотрудничестве в части проведения информационных и научно-практических мероприятий, направленных на развитие профессиональных компетенций административных и педагогических работников, реализующих адаптированные образовательные программы в системе общего, дополнительного и профессионального образования.', '', '/images/partners/IRO.png', '2023-05-15', '', '', 'https://new.iro38.ru/archives/37200', 'Новости', '/news.php', 1, '', ''),
(902, '18', 'Ярмарка от \"Дома синего слона\"', '16 марта в 17-ой школе прошла наша долгожданная ярмарка! Это был невероятно веселый и яркий день, полный радости и улыбок. Мы хотели бы выразить огромную благодарность всем, кто пришел поддержать нас. Ваше присутствие значит для нас так много!', '<p>16 марта в 17-ой школе прошла наша долгожданная ярмарка! Это был невероятно веселый и яркий день, полный радости и улыбок. Мы хотели бы выразить огромную благодарность всем, кто пришел поддержать нас. Ваше присутствие значит для нас так много!</p>\r\n<p>Особая благодарность уходит к одной замечательной девушке - Дарье Арслановой. Она ни разу не пропускает наши ярмарки и всегда поддерживает нас. Дарья, ты - наша настоящая героиня! Спасибо тебе за твою преданность и поддержку. Мы очень ценим твою настойчивость и энтузиазм!</p>\r\n<p>Также хотим выразить благодарность всем, кто проявил интерес к нашей мастерской. Ваши вопросы, комментарии и фотографии сделали наш день ярким и запоминающимся. Спасибо вам за то, что вы делаете нашу работу более значимой и вдохновляете нас на новые творческие идеи!</p>\r\n<p>АНО \"АПЦ РАСсвет\" - участник проекта Ассоциации «Аутизм-Регионы» \"Полезная занятость с применением практик и методов с научно доказанной эффективностью для молодых людей с РАС\" при поддержке Фонда президентских грантов.\r\n</p>\r\n', '/images/DSS/2024/8.jpg', '2024-03-19', '', '', '', '', '', 0, '', ''),
(903, '20', 'Устанавливаем руководящий контроль', 'Что делать, если ребенок не хочет заниматься? Если ребенок не следует инструкциям и просьбам, капризничает, не слушается, ведет себя слишком активно или даже агрессивно. В таком случае есть смысл поработать над установлением руководящего контроля.', '<p>Что делать, если ребенок не хочет заниматься? Если ребенок не следует инструкциям и просьбам, капризничает, не слушается, ведет себя слишком активно или даже агрессивно. Такими способами он как-будто говорит: “Отстаньте! Я буду делать только то, что я хочу!”.\r\n</p>\r\n<p>В таком случае необходимо в первую очередь работать над установлением руководящего контроля и формированием мотивации к сотрудничеству, которые служат базой для дальнейшего развития ребенка.</p>\r\n<p>Если в сотрудничестве наблюдаются проблемы, не тащите ребенка за стол.\r\nСначала подружитесь с ним и приходите за стол вместе!</p>\r\n<p>&#128204; Руководящий контроль -​ это умение педагога управлять поведением ребенка путем осознанного и целенаправленного подкрепления желательного поведения и формирования мотивации​.</p>\r\n<p>Роберт Шрамм (Robert Schramm, МА, ВСВА), один из ведущих специалистов в поведенческом анализе, предлагает пошаговый метод приобретения \"Руководящего контроля\"​. (из книги «Мотивация и подкрепление. Практическое применение методов прикладного анализа поведения и анализа вербального поведения (ABA/VB)»\r\n</p>\r\n<p>1️⃣ Контроль над поощрениями.</p>\r\n<p>Все вещи, которые любит ребенок должны контролироваться взрослым. Ребенок может получить то, что он хочет, выполняя простые инструкции взрослого. Чтобы ограничить доступ ребенка к мотивационным предметам, можно положить их на шкаф в прозрачную коробку, чтобы ребенок мог их видеть, но не мог достать.</p>\r\n<p>2️⃣ Восприятие взрослого как поощрения. </p>\r\n<p>Покажите ребенку, что с вами весело! Сделайте деятельность ребенка еще более интересной, когда вы рядом, чтобы он захотел проводить с вами больше времени. И как только ребенок начнет воспринимать вас как человека, с которым весело и интересно, он будет готов сотрудничать.   </p>\r\n<p>3️⃣ Тщательный выбор инструкций и обращенной речи.  </p>\r\n<p>Каждое слово, с которым вы обращаетесь к ребенку, должно быть тщательно взвешенным. Если вы дали ребенку инструкцию, он должен сначала выполнить ее и только потом получить поощрение.  Но при этом вы можете помогать ребенку, не забывая постепенно убирать подсказки. Используйте четкие и конкретные инструкции, например: «сядь», «дай стакан», вместо «сядь, пожалуйста, на стул» и «можешь подать стакан?».    </p>\r\n<p>4️⃣ Использование мотивационных ситуаций для предъявления требований.  </p>\r\n<p>Предъявляйте ребенку легкие требования часто и подкрепляйте их выполнение. Ваше требование может быть любым относящимся к ситуации или полезным. Не используйте такие выражения как \"Если ..., то ...\". Ждите инициативы ребенка -  и как только он её проявляет - попросите его сделать что-нибудь для вас. </p>\r\n<p>5️⃣ Постепенное снижение частоты поощрений. </p>\r\n<p>На ранних этапах установления руководящего контроля поощряйте каждую верную реакцию ребенка. Ни одна верная реакция не должна остаться незамеченной. Позже постепенно можно переходить к переменному режиму подкрепления, то есть перейти от подкрепления каждой верной реакции к подкреплению выполнения каждой второй, третьей и даже четвертой инструкции.</p>\r\n<p>6️⃣ Оценка предпочтений. </p>\r\n<p>У ребенка должен быть разнообразный список поощрений. Делайте записи о каждом предмете или действии, которое вызывает интерес ребенка. Записывайте, в каких ситуациях он предпочитает тот или иной предмет.   Чередуйте поощрения, чтобы предотвратить потерю интереса у ребенка. Ищите новые поощрения каждый день.    </p>\r\n<p>7️⃣ Сокращение нежелательного поведения. </p>\r\n<p>Давайте ребенку такие инструкции, которым он захочет следовать. Но покажите, что игнорирование вашей инструкции или неприемлемое поведение не приведет к получению подкрепления. Убедитесь, что ребенок не получает НИКАКОЙ выгоды, когда избегает выполнения ваших требований.​</p>\r\n\r\n<p>Проект \"Сам себе помощник\" реализуется при поддержке Благотворительного фонда «Вклад в будущее».</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240033&hash=beff1b7e740eeaff\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/ABA/2024/11.jpg', '2023-06-20', '/images/grants/VK_VB.jpg', '', '', 'Новости', '/news.php', 0, '', '1'),
(904, '4', 'Советы по подготовке ребенка к забору крови', 'Часто ребятам с аутизмом сложно переживать медицинские процедуры с иглами. Даём несколько рекомендаций, которых стоит придерживаться, чтобы адаптировать ребенка к уколам или забору крови.\r\n', '<p>Часто ребятам с аутизмом сложно переживать медицинские процедуры с иглами. Даём несколько рекомендаций, которых стоит придерживаться, чтобы адаптировать ребенка к уколам или забору крови.</p>\r\n<p>&#10060; Не удерживайте ребенка</p>\r\n<p>Когда ребенка начинают скручивать заранее, еще при входе в кабинет, это разрушает доверие и повышает тревожность до начала самой процедуры. Помочь придержать руки в момент ввода иглы, когда ребенок вам доверяет и знает, что вы не схватите его - допустимая форма удерживания, исключая моменты, когда ребенку требуется экстренная госпитализация без возможности подготовиться заранее.</p>\r\n<p>&#9989; Установите <a href=\"/events/detail.php?id=903&back=news\" target=\"_blank\">руководящий контроль</a>, научите ребенка слышать ваши просьбы и выполнять их.</p>\r\n<p>«Руки под попу!», почему бы и нет! Такие отношения строятся заранее, перед походом к врачу.\r\nТренируйтесь выполнять задания, сидя на стульчике. Подбирайте такой материал, который бы имел очевидное окончание. Например, собрать пазл или монетки в копилку. Отлично подойдут жетоны.\r\n<p>&#9989; Проанализируйте проблемное поведение ребенка.</p>\r\n<p>«Отрепетируйте» заранее затягивание жгута на руку, обработку места укола спиртом, пусть ребенок постепенно привыкает к виду иглы и перестанет бояться ее, если укол просто лежит рядом, находится в руке у другого человека и слегка прикасается к коже.</p>\r\n<p>&#9989; Предупредите медицинский персонал о особенностях поведения ребенка. </p>\r\n<p>Дайте им конкретные рекомендации. Например, не пытаться уговорить ребенка нескончаемым потоком речи об «укусе комарика», если этот способ действительно не помогает, а только путает.</p>\r\n<p>&#9989; Продумайте поощрение для ребенка.</p>\r\n<p>Важно подкреплять адаптивное поведение.<br>\r\n- Ребенок спокойно зашел и сел на стульчик<br>\r\n- Спокойно ведет себя при виде иглы<br>\r\n- При вводе иглы все лучше и лучше контролирует себя и не дергается<br>\r\n«Какой ты молодец! Так стараешься, дарю тебе в честь этого робота, которого ты так хотел!»</p>\r\n<p>Важно, чтоб поощрение и похвала были предоставлены ребенку в ту же секунду, как закончится процедура. Со временем, когда навыки ребенка усовершенствуются, сдавая кровь систематически, поощрение можно сокращать.\r\n</p>\r\n\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240844&hash=e6bdeb474456ca05\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>\r\n', '/images/ABA/2024/9.jpg', '2024-03-20', '', '', '', '', '', 0, '', '11'),
(905, '4', 'Как научить ребенка с аутизмом сдавать кровь', 'Как научить ребенка с аутизмом сдавать кровь с помощью методик прикладного анализа поведения обычной маме? Перед тем как начать обучение, определите конкретные цели и задачи поведенческой терапии. ', '<p>Как научить ребенка с аутизмом сдавать кровь с помощью методик прикладного анализа поведения обычной маме?</p>\r\n<p>1️⃣ Перед тем как начать обучение, определите конкретные цели и задачи поведенческой терапии. Например, цель может быть в том, чтобы ребенок смог сдать кровь из вены в больнице, оставаясь спокойным на протяжении всей процедуры. «Спокойно» тоже лучше описать более конкретно: сидит на стуле в течение 3-х минут, правая рука затянута жгутом и вытянута на столе, левая рука лежит на коленке\\под попой.</p>\r\n<p>2️⃣ Разбейте процесс сдачи крови на несколько небольших шагов. Например, научитесь сидеть с вытянутой рукой на столе -> научитесь затягивать жгут -> обрабатывать место укола антисептиком -> оставаться спокойным при виде иглы -> оставаться спокойным, когда игла касается кожи -> надавливайте на предполагаемое место прокола иглой (можно спилить острую часть, чтобы преждевременно не поранить ребенка) и т.д.</p>\r\n<p>3️⃣ Используйте позитивное подкрепление и поощряйте ребенка каждый раз, когда он успешно выполняет какой-то шаг процесса. Например, можно использовать похвалу, любимую игрушку, мультики или вкусняшки. Так же можно использовать отсроченное подкрепление, например, с помощью жетонов.</p>\r\n<p>4️⃣ Не используйте наказания или негативные приемы для того, чтобы заставить ребенка сдать кровь. Это может вызвать только больше стресса и негативных эмоций.</p>\r\n<p>5️⃣ Постепенно увеличивайте сложность: по мере того, как ребенок становится более уверенным, с каждым шагом процесса увеличивайте сложность задачи. Например, начните с прикосновения к иголке, затем переходите к прокалыванию, и т.д.\r\n</p>\r\n<p>6️⃣ Регулярно проводите тренировки и повторения процесса забора крови, чтобы ребенок мог закрепить навыки и чувствовать себя более уверенно.</p>\r\n<p>7️⃣ Сотрудничайте с профессионалами: если у вас возникают трудности или вам нужна помощь, обратитесь за советом и поддержкой к специалистам в области прикладного анализа поведения.\r\n</p>\r\n<p>Проект «Я могу: социализация и обучение жизненно-важным навыкам детей с РАС» реализуется с использованием пожертвования Благотворительного фонда «Абсолют-Помощь».</p>\r\n<p><a href=\"/news/detail.php?id=894\" target=\"_blank\">Подробнее о проекте</a></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240859&hash=296d5157cfa55779\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>\r\n', '/images/ABA/2024/10.jpg', '2024-03-27', '', '', '', '', '', 0, '', '12'),
(906, '13,16', 'Лучший дефектолог Иркутской области', 'Михеева Елена Сергеевна, соучредитель АНО \"АПЦ Рассвет\" и учитель-дефектолог школы п. Молодежный, стала победителем регионального этапа Всероссийского конкурса \"Учитель-дефектолог России\" в 2024 году! Региональный этап конкурса проходил в феврале в заочном формате.', '<p>Михеева Елена Сергеевна, соучредитель АНО \"АПЦ Рассвет\" и учитель-дефектолог школы п. Молодежный, стала победителем регионального этапа Всероссийского конкурса \"Учитель-дефектолог России\" в 2024 году!</p>\r\n<p>Региональный этап конкурса проходил в феврале 2024 г. на территории Иркутской области в заочном формате. В нем приняли участие 23 педагога из 15 муниципальных образований Приангарья. Экспертная комиссия оценивала «Профессиональное портфолио» и видеоролик занятия участника.</p>\r\n<p></p>\r\n<p></p>\r\n<p>Какой путь прошла Елена Михеева, прежде чем стать учредителем \"РАСсвета\", поведенческим специалистом, дефектологом и победителем регионального этапа конкурса «Учитель дефектолог России - 2024» смотрите в сюжете программы <a href=\"https://vestiirk.ru/news/zaneslo-v-professiiu-volei-sluchaia-prepodavatel-iz-irkutska-elena-mikheeva-vyigrala-v-regionalnom-etape-vserossiiskogo-konkursa-uchitel-defektolog-rossii/\" target=\"_blank\">\"Вести-Иркутск\"</a>.</p>\r\n<p>Абилитация детей с применением поведенческих методик, имеющих научно доказанную эффективность, и сопровождение команды ресурсного класса опытным супервизором - все это помогает добиться наилучших результатов в обучении учеников с расстройствами аутистического спектра.</p>\r\n<p>Являясь мамой ребенка с аутизмом и учредителем АНО \"АПЦ Рассвет\", Елена Сергеевна стояла у истоков создания первого ресурсного класса для детей с РАС в Приангарье, стала его первым учителем. Сейчас она работает в ресурсном классе учителем-дефектологом, а также продолжает свою работу в качестве начальника экспертного отдела АНО \"АПЦ Рассвет\", делится опытом и консультирует по вопросам организации образования детей с РАС.</p>\r\n<p>Гордимся, что именно в нашем коллективе есть такой идейный и активный человек, который заражает всех своей энергией. Постоянное обучение, совершенствование своих профессиональных навыков, отзывчивость и доброта помогают Елене Сергеевне развивать инклюзию в Иркутской области.</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-35929536&id=456272965&hash=1eca41137248a1c3\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen class=\"social-video\"></iframe>                        </section>\r\n<p>Результаты конкурса опубликованы на сайте <a href=\"https://new.iro38.ru/archives/44901\" target=\"_blank\">Института развития образования ИО</a></p>\r\n<p>С распоряжением министерства образования Иркутской области можно ознакомиться <a href=\"https://disk.yandex.ru/i/j93FxP_d9n9gKg\" target=\"_blank\">ЗДЕСЬ</a></p>\r\n<p>Публикации о конкурсе в региональных СМИ:</p> \r\n<p><a href=\"https://irk.aif.ru/society/luchshim_defektologom_irkutskoy_oblasti_priznan_uchitel_iz_molodyozhnogo\" target=\"_blank\">\"Аргументы и факты\" Иркутск</a></p>\r\n<p><a href=\"https://angarogni.ru/?p=28933\" target=\"_blank\">\"Ангарские огни\"</a></p>\r\n<p><a href=\"https://irk.today/2024/03/14/uchitel-iz-molodyozhnogo-stala-luchshim-defektologom-irkutskoy-oblasti/\" target=\"_blank\">\"Иркутск сегодня\"</a></p>', '/images/smi/37.jpg', '2024-03-25', '', '', '', '', '', 0, '', ''),
(907, '13', '«РАСсвет» в студии \"Утро России\"', 'Екатерина Родыгина, председатель адаптационно-педагогического центра \"РАСсвет\" и специалист по прикладному анализу поведения Дарья Евдокимова отвечают на вопросы ведущего программы <a href=\"https://vk.com/wall-211987611_2716\" target=\"_blank\">\"Утро России. Иркутск\"</a> Глеба Нечаева.', '<p>❓ Как в Иркутске появился адаптационно-педагогический центр для ребят с аутизмом?</p>\r\n<p>❓ С какими проблемами обращаются к поведенческим аналитикам?</p>\r\n<p>❓ Адаптивные стоматологические приемы для детей с тяжелым проблемным поведением?</p>\r\n<p>Екатерина Родыгина, председатель адаптационно-педагогического центра \"РАСсвет\" и специалист по прикладному анализу поведения Дарья Евдокимова отвечают на вопросы ведущего программы <a href=\"https://vk.com/wall-211987611_2716\" target=\"_blank\">\"Утро России. Иркутск\"</a> Глеба Нечаева.</p>\r\n<p>Проект «Я могу: социализация и обучение жизненно-важным навыкам детей с РАС» реализуется с использованием пожертвования Благотворительного фонда «Абсолют-Помощь»</p>\r\n<p><a href=\"https://rassvet-apc.ru/news/detail.php?id=894\" target=\"_blank\">Подробнее о проекте</a></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211987611&id=456240596&hash=e7854e38ba72ac35\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen class=\"social-video\"></iframe>                        </section>', '/images/smi/38.jpg', '2024-03-27', '/images/Absolut/logo.jpg', '', '', '', '', 0, '', '12'),
(908, '1', 'Онлайн-встречи с экспертами в области развития детей с РАС', 'Вас ждет вторая часть семинара «Без паники! Идем к стоматологу!» - 6 апреля в дистанционном формате. А также встреча в формате «Вопрос-ответ» с Андреем Шевченко и Екатериной Родыгиной.', '<p>Приглашаем родителей детей с РАС и специалистов на онлайн-встречи с экспертами в области развития детей с аутизмом и другими интеллектуальными расстройствами.</p>\r\n<p>Вас ждут:</p>\r\n<p>1️⃣ <b>Вторая часть семинара «Без паники! Идем к стоматологу!»</b>.</p>\r\n<p>&#128197; 6 апреля</p>\r\n<p>&#128344; 12:00 по Иркутскому времени (7:00 по МСК)</p>\r\n        			\r\n<p><b>Спикеры</b>: <br>\r\n✔️ Родыгина Екатерина Андреевна - председатель АНО \"АПЦ Рассвет\", поведенческий аналитик, врач <br>\r\n✔️ Бардамова Татьяна Васильевна – ABA-инструктор «АПЦ Рассвет», психолог</p>\r\n<p>Во второй части семинара мы\r\nпродолжим обсуждать вопросы, касаемые лечения и профилактики зубных заболеваний у детей с расстройствами аутистического спектра и другими особенностями развития.</p>\r\n<p>❓ Как обучить своего ребенка?</p>\r\n<p>❓ Может ли обычный педагог научить ребенка жизненно-необходимым навыкам «спокойного» похода к врачу?</p>\r\n<p>❓ Методики прикладного анализа поведения на практике.</p>\r\n<p></p>\r\n<br>\r\n<p>2️⃣ <b>Встреча в формате «Вопрос-ответ»</b> на волнующие для родителей и специалистов темы.</p>\r\n<p>&#128197; 17 апреля</p>\r\n<p>&#128344; 16:00 по Иркутскому времени (11:00 по МСК)</p>\r\n<p><b>Спикеры</b>: <br>\r\n✔️ Шевченко Андрей Викторович - кандидат психологических наук, дефектолог-логопед г. Иркутска, с опытом работы с детьми более 19 лет.<br>\r\n✔️ Родыгина Екатерина Андреевна - председатель АНО \"АПЦ Рассвет\", поведенческий аналитик, врач</p>\r\n<p>&#128205; Ссылка для подключения к онлайн-встречам будет отправлена на почту зарегистрировавшимся участникам.</p>\r\n<p><a href=\" https://forms.yandex.ru/u/65e96d935d2a0639df21ff72/\" class=\"button red-button\">Зарегистрироваться</a></p>\r\n<p>До встречи на вебинарах! ❤️</p>\r\n', '/images/seminar/2024/5.jpg', '2024-03-30', '', '', '', 'Новости', '/news.php', 0, '', '11'),
(909, '18', 'Мастер-класс по работе с глиной', 'В нашей уютной мастерской состоялся инклюзивный мастер-класс по работе с глиной. В нем приняли участие как нейротипичные ребята, так и ребята с аутизмом. И вместе они смогли создать что-то по-настоящему волшебное! ', '<p>В нашей уютной мастерской состоялся инклюзивный мастер-класс по работе с глиной. В нем приняли участие как нейротипичные ребята, так и ребята с аутизмом. Каждый участник смог познакомиться с интересными техниками работы с глиной. И вместе ребята смогли создать что-то по-настоящему волшебное! </p>\r\n<p>Если кто-то до сих пор думает, что состыковывать детали из глины так же просто, как с пластилином, то вы ошибаетесь. Ведь для соединения деталей из глины нужно использовать специальный \"клей\" – шликер. Рассказать об этом и было главной целью нашего инклюзивного мастер-класса.\r\n</p>\r\n<p>Наши ребята справились с этим заданием на отлично! Они смело и уверенно приступили к работе и уже в конце занятия наши столы заполнились готовыми изделиями. Кто-то делал тарелки в виде рыбок, а кто-то делал тарелки с кошечкой. Каждое изделие было уникальным и отражало индивидуальность каждого ребенка.</p>\r\n<p>Наши талантливые участники мастер-класса не только научились состыковывать детали из глины, но и развили свое воображение и мелкую моторику. Ведь работа с глиной требует точности и тонкой работы рук. И наши ребята справились с этим на отлично!</p>\r\n<p>Сейчас все наши работы подсохнут и пройдут обжиг, чтобы стать еще крепче и прочнее. А потом мы сможем показать вам их в полной красе.</p>\r\n<p>Мы очень рады, что провели этот мастер-класс и дали возможность детям проявить себя в таком увлекательном и интересном искусстве, как работа с глиной. И благодаря инклюзивному подходу, все наши ребята смогли принять участие и насладиться процессом творчества.</p>\r\n<p>Статья о мастер-классе на <a href=\"https://babr24.com/?IDE=258179\" target=\"_blank\">Babr24.com</a></p>\r\n<p>Проект \"Давай дружить\" реализуется при поддержке «Губернского собрания общественности Иркутской области»</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239395&hash=ed3f8a328735123e\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2024/10.jpg', '2024-03-30', '/images/grants/GS.png', '', '', '', '', 0, '', '14'),
(910, '13,18', 'Об инклюзивном мастер-классе в СМИ', 'В рамках недели неформального образования в Иркутске прошли инклюзивные мастер-классы для детей \"Знакомство с глиной\". \r\nОб этом событии вышла статья в интернет издании <a href=\"https://babr24.com/?IDE=258179\" target=\"_blank\">Babr24.com</a>', '', '/images/DSS/2024/9-1.jpg', '2024-03-30', '/images/grants/GS.png', '', 'https://babr24.com/?IDE=258179', '', '', 1, '', '14'),
(912, '18', 'Мастер-класс в день информирования об аутизме', '&#129513; 2 апреля - международный день информирования об аутизме! &#129513; Мы решили начать этот особенный месяц с уроков добра в школах. Наш первый урок прошел в школе поселка Молодежный, и это было незабываемо!', '<p>&#129513; 2 апреля - международный день информирования об аутизме! &#129513; </p>\r\n<p>Мы решили начать этот особенный месяц с уроков добра в школах. Наш первый урок прошел в школе поселка Молодежный, и это было незабываемо!\r\n</p>\r\n<p>Мы рассказали ребятам с обычным развитием о том, что такое аутизм. Мы объяснили, что дети с РАС - это такие же дети, как и все остальные. Они могут быть разными, но никогда не стоит бояться или думать, что они могут сделать что-то плохое.\r\n</p>\r\n<p>А затем мы пригласили ребят из ресурсного класса, где учатся дети с РАС, и вместе с ними мы создали добрых мишек из воздушного пластилина. Это было прекрасное слияние двух миров, где дети, несмотря на свои различия, могли находить общий язык и творить вместе.</p>\r\n<p>Проект \"Давай дружить\" реализуется при поддержке «Губернского собрания общественности Иркутской области»</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239399&hash=3040c0d9b9004db0\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2024/12.jpg', '2024-04-03', '/images/grants/GS.png', '', '', '', '', 0, '', '14'),
(913, '19', 'Групповой формат занятий в ресурсном классе', 'Организация групповых уроков для детей с аутизмом может быть сложной задачей, но с правильной подготовкой и использованием методик прикладного анализа поведения можно создать эффективную и поддерживающую среду для развития навыков учеников. ', '<p>Организация групповых уроков для детей с аутизмом может быть сложной задачей, но с правильной подготовкой и использованием методик прикладного анализа поведения можно создать эффективную и поддерживающую среду для развития навыков учеников. </p>\r\n<p>Учитель ресурсного класса школы пос. Молодежный Мария Максимова поделилась рекомендациями по организации групповых уроков для ребят:\r\n</p>\r\n<p>1️⃣ Разработайте индивидуальные программы обучения.</p>\r\n<p>Учитывайте потребности каждого ребенка с аутизмом и разработайте индивидуальные программы обучения, которые будут соответствовать их уровню развития и способностям. Учтите их личные интересы и мотивацию, чтобы уроки были интересными и стимулирующими.</p>\r\n<p>2️⃣ Структурируйте уроки.</p>\r\n<p>Четкие и понятные правила облегчают учебный процесс для детей: они понимают, что ожидать. Используйте визуальные подсказки, расписание и таймеры, чтобы помочь детям понимать и предсказывать, что будет происходить.</p>\r\n<p>3️⃣ Создайте безопасную и поддерживающую среду.</p>\r\n<p>Обеспечьте тихое и спокойное место для работы ребят, минимизируйте возможные источники стресса (шум, яркий свет и т. д.) и предоставьте индивидуальную поддержку детям, когда им это необходимо.</p>\r\n<p>4️⃣ Используйте визуальные подсказки.</p>\r\n<p>Диаграммы, картинки, иконки и символы, могут быть полезными для детей с аутизмом, чтобы понять и запомнить информацию. Используйте их для объяснения правил, последовательности действий и ожиданий на уроке.</p>\r\n<p>5️⃣ Индивидуализируйте подход.</p>\r\n<p>Каждый ребенок с аутизмом уникален, поэтому важно использовать индивидуальный подход к каждому ребенку даже в условиях группового обучения. Узнайте, какой стиль обучения работает для каждого ребенка, и попробуйте адаптировать уроки в соответствии с их потребностями.\r\nКаждый ребенок нуждается в индивидуально адаптированных подсказках и актуальном подкреплении.</p>\r\n<p>6️⃣ Обеспечьте социальное взаимодействие.</p>\r\n<p>Помогайте ученикам развивать коммуникативные навыки, умение работать совместно и раздельно, создавайте совместные задачи и игры, где дети могут сотрудничать на доступном их уровне (просить доступным способом, делиться, помогать друг другу и т.д.)</p>\r\n<p>7️⃣ Обучайте педагогов и тьюторов.</p>\r\n<p>Это один из самых важных аспектов успешной организации групповых уроков. Обучение должно включать изучение особенностей поведения людей с РАС, методы обучения и стратегии управления поведением.</p>\r\n<p>Важно помнить, что каждый ребенок с аутизмом уникален, поэтому может потребоваться адаптировать подходы и методы обучения в соответствии с их индивидуальными потребностями и анализировать, подходят они или нужно менять подход. Открытость и гибкость педагогов может создать эффективную и поддерживающую среду для развития навыков детей с аутизмом.</p>\r\n<p>АНО \"АПЦ Рассвет\" - участник проекта «Ресурсный класс. Доступная среда». Благодарим организатора проекта Ассоциацию \"Аутизм-Регионы\".\r\nПроект реализуется при поддержке Фонда президентских грантов и благотворительного фонда \"Абсолют - Помощь\".</p>\r\n\r\n\r\n', '/images/RK/2024/3/1.jpg', '2024-03-31', '/images/RK/2024/3/2.jpg;\r\n/images/RK/2024/3/3.jpg;\r\n/images/RK/2024/3/4.jpg;\r\n/images/RK/2024/3/5.jpg', '', '', 'Новости', '/news.php', 0, '', ''),
(915, '19', 'Полезные советы по организации инклюзивных мероприятий', 'Формирование инклюзивного пространства в образовательной организации является одной из основных задач. Как провести инклюзивное мероприятие для детей с РАС и типично развивающихся сверстников, рассказали педагоги ресурсных классов Иркутского района.', '<p>Формирование инклюзивного пространства в образовательной организации является одной из основных задач. К инклюзии должны быть готовы все участники образовательных отношений и это не только дети из регулярных классов, но и их родители, учителя и сотрудники школы.</p>\r\n<p>Родители и учителя должны понимать, что процесс включение детей с РАС в общие классы будет проходить постепенно, учитывая сильные стороны ребенка и при необходимости с тьюторским сопровождением.</p>\r\n<p>Только от взрослых зависит отношение нейротипичных сверстников к детям с РАС.\r\nПоэтому одним из важных предварительных этапов перед включением учеников с РАС в инклюзивный процесс - является проведение собраний с родителями и сотрудниками школы.</p>\r\n<p>Как провести инклюзивное мероприятие для детей с РАС и типично развивающихся сверстников, рассказали педагоги ресурсных классов Иркутского района.</p>\r\n<p>1️⃣ Подготовьте учеников регулярного класса, предварительно проведя \"Уроки добра\". Расскажите о детях с РАС, особенностях и причинах их поведения, объясните, как можно взаимодействовать и общаться на примере простых историй, доступных для понимания детей.</p>\r\n<p>2️⃣ Познакомьте учеников регулярного класса с учениками с РАС: например, организуйте выход учеников ресурсного класса во время перемены в регулярный класс или наоборот, пригласите учеников регулярного класса в ресурсный класс.</p>\r\n\r\n<br>\r\n<p></p>\r\n<div class=\"help-achievements-list\">\r\n<div class=\"help-achievement\">\r\n<img class=\"help-achievement-image\" src=\"/images/RK/2024/4/2.jpg\" alt=\"\" />\r\n</div>\r\n<div class=\"help-achievement\">\r\n<img class=\"help-achievement-image\" src=\"/images/RK/2024/4/4.jpg\" alt=\"\" />\r\n</div>\r\n		\r\n</div>\r\n\r\n<p>3️⃣ Соберите данные какие увлечения и игры нравятся нормотипичным детям и детям с РАС и выявите общие интересы.\r\n</p>\r\n<p>4️⃣ Определите формат инклюзивного мероприятия, например, общий выход в кинотеатр или совместное проведение праздника.</p>\r\n<p>5️⃣ Подготовьте социальную историю для обучающихся с РАС.</p>\r\n<p>6️⃣ Обсудите план мероприятия со всеми педагогами класса, уделив внимание особенностям восприятия конкретных детей.</p>\r\n</p>\r\n<br>\r\n<p></p>\r\n<div class=\"help-achievements-list\">\r\n<div class=\"help-achievement\">\r\n<img class=\"help-achievement-image\" src=\"/images/RK/2024/4/5.jpg\" alt=\"\" />\r\n</div>\r\n<div class=\"help-achievement\">\r\n<img class=\"help-achievement-image\" src=\"/images/RK/2024/4/6.jpg\" alt=\"\" />\r\n</div>\r\n			\r\n</div>\r\n<p>7️⃣ Помните, что необходимо сохранить интерес у детей с РАС к проводимому мероприятию.</p>\r\n<p>8️⃣ При необходимости оказывайте подсказки детям.</p>\r\n<p>9️⃣ Поощряйте детей за самостоятельные реакции, необходимые в данном мероприятии. Также поощряйте сверстников за взаимодействие и помощь ученикам с РАС.</p>\r\n<p>1️⃣0️⃣ Максимально включайте учеников с РАС во взаимодействие со сверстниками.\r\n<p>АНО \"АПЦ РАСсвет\" - участник проекта \"Ресурсный класс. Доступная среда\".\r\nБлагодарим организатора проекта Ассоциацию \"Аутизм-Регионы\".\r\nПроект реализуется при поддержке Фонда президентских грантов и Благотворительного фонда «Абсолют-Помощь».</p>', '/images/RK/2024/4/1.jpg', '2024-04-09', '', '', '', 'Новости', '/news.php', 0, '', ''),
(916, '19,5', 'Инклюзивные события,  посвященные месяцу информирования об аутизме', 'В апреле в школах Иркутска и Иркутского района, где работают наши ресурсные классы для детей с аутизмом, традиционно проходят инклюзивные мероприятия, посвященные месяцу информирования об аутизме. Педагоги ресурсных классов начали апрель с проведения \"Уроков добра\" для начальных классов.\r\n', '<p>В апреле в школах Иркутска и Иркутского района, где работают наши ресурсные классы для детей с аутизмом, традиционно проходят инклюзивные мероприятия, посвященные месяцу информирования об аутизме.</p>\r\n<p>Педагоги ресурсного класса школы поселка Молодежный начали апрель с проведения \"Уроков добра\" для начальных классов. Главным героем уроков стал синий мишка Петруша, знакомый многим по публикациям и информационным роликам Фонда \"Выход\".</p>\r\n<p>На примере Петруши и его друзей, педагоги рассказали ребятам о детях с аутизмом и о том, что все люди - разные, и если кто-то отличается от других, то его не нужно бояться, а с ним можно общаться и дружить. А в классах, уже хорошо знакомых с нашими ребятами, провели викторину \"Что вы знаете об аутизме\", которая очень понравилась ребятам и показала, как много они уже знают.</p>\r\n<p>Инклюзивные уроки ИЗО и технологии также были посвящены синему мишке и его друзьям. Девизом уроков стал слоган \"Мы разные, но мы вместе!\"</p>\r\n<p>Тема \"Урока добра\" школы поселка Луговое: \"Как хорошо, что все мы разные\". Дети изготавливали совместную инклюзивную стенгазету.\r\n</p>\r\n<p>Ну а традиционные коммуникативные перемены помогли ребятам из ресурсных классов и их сверстникам стать еще ближе друг к другу.</p>\r\n<p>АНО \"АПЦ Рассвет\" - участник проекта «Ресурсный класс. Доступная среда». Благодарим организатора проекта Ассоциацию \"Аутизм-Регионы\".\r\nПроект реализуется при поддержке Фонда президентских грантов и благотворительного фонда \"Абсолют - Помощь\".</p>\r\n\r\n\r\n', '/images/RK/2024/5/1.jpg', '2024-04-14', '/images/RK/2024/5/2.jpg;\r\n/images/RK/2024/5/3.jpg;\r\n/images/RK/2024/5/4.jpg;\r\n/images/RK/2024/5/5.jpg;\r\n/images/RK/2024/5/6.jpg;\r\n/images/RK/2024/5/7.jpg;\r\n/images/RK/2024/5/8.jpg;\r\n/images/RK/2024/5/9.jpg;\r\n/images/RK/2024/5/10.jpg;\r\n/images/RK/2024/5/11.jpg;\r\n/images/RK/2024/5/12.jpg;\r\n/images/RK/2024/5/13.jpg', '', '', 'Новости', '/news.php', 0, '', '');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(917, '3', 'Стажировка \"Ресурсный класс под ключ\"', 'Эта неделя была невероятно продуктивной и интересной! У нас в центре проходила стажировка \"Ресурсный класс под ключ\" с гостями из центра \"Ньютошки\" из города Челябинск.', '<p>Эта неделя была невероятно продуктивной и интересной! У нас в центре проходила стажировка \"Ресурсный класс под ключ\" с гостями из центра \"Ньютошки\" из города Челябинск.</p> \r\n<p>За это время мы поделились своими уникальными методиками и подходами к обучению детей с особыми образовательными потребностями.</p>\r\n<p>А так же провели практический тренинг, где нашим ученикам удалось на практике применить новые знания и навыки.</p>\r\n<p>Отзывы участников стажировки:</p>\r\n<p>\"Это, наверное, самая лучшая стажировка. Стажировка на практике не только в центре, но и в ресурсном классе. Спасибо за доверие инструкторам центра и педагогам ресурного класса. Так приятно видеть эмоции детей особенно после ответов у доски, каждый раз как звёздный час очень важный для них. Желаю продуктивного развития в обучении новых специалистов, чтобы каждый ребёнок с РАС имел возможность обучаться в лучших для себя условиях, а слова надомное обучение для детей с РАС быстрее ушло в прошлое.\"</p>\r\n<p>\"Хотим выразить искреннюю благодарность всей команде АПЦ РАСсвет за подготовку и проведение стажировки по работе с детьми с расстройствами аутистического спектра. Самое главное, что мы нашли единомышленников в лице Елены Сергеевны Михеевой и Екатерины Родыгиной и поняли, что мы движемся в правильном направлении, а также заручились их поддержкой . Теперь мы точно знаем как выстраивать организационную систему в нашем эрго-садике и как работать с нежелательным поведением у детей с РАС. Огромное спасибо за ваш опыт и ваши знания. Процветания вашему центру. Надеемся на дальнейшее сотрудничество и с радостью ждём в гости.\" Команда «Ньютошки» г.Челябинск</p>\r\n<p>Большое спасибо гостям из центра \"Ньютошки\" за их открытость и доброту! Нам было очень приятно общаться с профессионалами своего дела и делиться опытом.</p>\r\n<section class=\"video-detail\"><iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240914&hash=ebde8427f6cdbd15\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe></section>', '/images/seminar/2024/2/1.jpg', '2024-04-14', '/images/seminar/2024/2/2.jpg;\r\n/images/seminar/2024/2/3.jpg;\r\n/images/seminar/2024/2/4.jpg;\r\n/images/seminar/2024/2/5.jpg;\r\n/images/seminar/2024/2/6.jpg;\r\n/images/seminar/2024/2/7.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', ''),
(919, '13,5', 'Интервью для радиостанции \"Комсомольская правда\"', 'Как распознать у своего малыша признаки РАС, как с этим справиться, как открыть ресурсные классы и научить своего ребенка ходить к стоматологу, рассказывает Екатерина Родыгина, председатель «Рассвета» в интервью для программы \"Доброволец\" радиостанции \"Комсомольская правда\".', '', '/images/smi/39.webp', '2024-04-22', '', '', 'https://radiokp-ru.turbopages.org/turbo/radiokp.ru/s/podcast/dobrovolec/732927', 'Новости', '/news.php', 1, '', '11'),
(920, '3', 'Семинары «Без паники! Идем к стоматологу!» завершены', 'В рамках проекта \"Без паники! Идем к стоматологу\" в Иркутске прошла серия семинаров от \"АПЦ Рассвет\" о стоматологической помощи детям и подросткам с аутизмом и другими нарушениями развития.', '<p>В рамках проекта \"Без паники! Идем к стоматологу\" в Иркутске прошла серия семинаров от \"АПЦ Рассвет\" о стоматологической помощи детям и подросткам с аутизмом и другими нарушениями развития. </p>\r\n<p>Спикеры семинара - Родыгина Екатерина Андреевна (председатель АНО \"АПЦ Рассвет\", поведенческий аналитик, врач) и Бардамова Татьяна Васильевна (ABA-инструктор «АПЦ Рассвет», психолог) в подробностях познакомили слушателей со всеми тонкостями подготовки детей с аутизмом к приёму врача и медицинским манипуляциям. </p>\r\n<p>В первой очной части семинара, которая прошла 30 марта в библиотеке имени Молчанова-Сибирского, слушатели семинара узнали: \r\nо научно-доказательных методах работы со страхами, которые помогают не только детям, но и взрослым, на примерах из реальной жизни учеников нашего центра; о проекте «Дружелюбная стоматология» по полису ОМС и  позитивном опыте других городов; о том, как привить гигиенические привычки детям в любом возрасте.</p>\r\n        			<p>Во второй части семинара, которая прошла 6 апреля в дистанционном формате, спикеры продолжили обсуждать вопросы, касаемые лечения и профилактики зубных заболеваний у детей с расстройствами аутистического спектра и другими особенностями развития. И ответили на вопросы: </p>\r\n<p>- Как справиться с трудностями, с которыми сталкиваются дети с аутизмом при посещении стоматолога?</p>\r\n<p>- Как бороться со страхами?</p>\r\n<p>- Как научиться ребенка демонстрировать, где болит?</p>\r\n<p>- Как спокойно выдержать всю стоматологическую процедуру? </p>\r\n<p>Все родители, посетившие очный семинар 30 марта, имели возможность заполнить опросник FAST - скрининговый инструмент для проведения функциональной оценки поведения у детей с аутизмом, чтобы наши поведенческие аналитики могли проанализировать описанное поведение детей и пригласить их на бесплатные мероприятия по адаптации детей с РАС к стоматологическим приемам.</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>', '/images/seminar/2024/1/1.jpg', '2024-04-08', '/images/seminar/2024/1/1.jpg;\r\n/images/seminar/2024/1/2.jpg;\r\n/images/seminar/2024/1/3.jpg;\r\n/images/seminar/2024/1/4.jpg;\r\n/images/seminar/2024/1/5.jpg;\r\n/images/seminar/2024/1/6.jpg;\r\n/images/seminar/2024/1/7.jpg;\r\n/images/seminar/2024/1/9.jpg;\r\n/images/seminar/2024/1/10.jpg', '', '', 'Новости', '/news.php', 0, '', '11'),
(921, '2', '\"Летучий корабль\" на сцене \"Музыкального театра\"', 'В воскресенье наши подопечные посетили \"Музыкальный театр\" и насладились спектаклем \"Летучий корабль\". Надо отметить, что ребята проявили настоящую выдержку и просидели весь спектакль! ', '<p>В воскресенье наши подопечные посетили \"Музыкальный театр\" и насладились спектаклем \"Летучий корабль\". Надо отметить, что ребята проявили настоящую выдержку и просидели весь спектакль!</p>\r\n<p>Такие мероприятия не только развивают интерес к искусству, но и помогают детям с аутизмом в адаптации к различным звуковым стимулам. Очень радует, что они смогли насладиться музыкальным спектаклем и получить положительные эмоции.\r\n</p>\r\n<p>Мы уверены, что подобные культурно-массовые выходы имеют огромное значение для нашего общества. Они не только помогают детям с особенностями развития, но и способствуют формированию толерантного отношения к ним со стороны окружающих.</p>\r\n<p>Большое спасибо организаторам мероприятия и артистам за их творчество и понимание! Надеемся, что в будущем у нас будет еще больше подобных возможностей для наших замечательных детей.\r\n</p>\r\n<p>Мероприятие состоялось в рамках проекта <a href=\"https://rassvet-apc.ru/news/detail.php?id=893\" target=\"_blank\">«Дом синего слона»</a> при поддержке Департамента здравоохранения и социальной помощи населению комитета по социальной политике и культуре администрации г. Иркутска.</p>\r\n', '/images/dosug/2024/1/1.jpg', '2024-04-22', '/images/grants/logo_irkutsk.png;\r\n/images/dosug/2024/1/2.jpg;\r\n/images/dosug/2024/1/3.jpg;\r\n/images/dosug/2024/1/4.jpg', '', '', 'Новости', '', 0, '', ''),
(922, '3', 'Делимся мнениями и ищем пути решения', 'Дефектология и прикладной анализ поведения не противоречат друг другу, а значит, объединив знания, можно добиться наилучших результатов в обучении детей с особенностями развития. Встреча экспертов в области развития детей состоялась 17 апреля на странице «РАСсвета» в \"ВКонтакте\".', '<p>Как научить ребёнка делится тем, что произошло в течении дня? Может ли у ребенка с аутизмом не появиться речь? Как избавиться от негативизма у ребенка в отношении выполнения заданий?</p>\r\n<p>На эти и другие актуальные вопросы развития детей с расстройствами аутистического спектра и другими ментальными нарушениями ответили логопед-дефектолог Андрей Шевченко и поведенческий аналитик, председатель «РАСсвета» Екатерина Родыгина.</p>\r\n<p>Встреча состоялась 17 апреля на странице «РАСсвета» в ВКонтакте, запись эфира можно посмотреть <a href=\"https://vk.com/wall-165016766_3535\" target=\"_blank\">здесь</a>.</p>\r\n<p>«Наша главная идея - поделиться мнениями и тем самым найти какие-то пути решения» - отмечает Андрей.</p>\r\n<p>Педагогика - не место боя различных методов обучения и парадигм. В обучении детей должны руководить здравый смысл и научный подход. \r\nДефектология и прикладной анализ поведения не противоречат друг другу, а это значит, что объединив знания, можно добиться наилучших результатов в обучении детей с особенностями развития.\r\n</p>\r\n<p>«Прикладной анализ поведения - это по-сути мостик в дефектологию: как закрепить полученные навыки, как понятно подсказать, как адаптировать материал под конкретного ребенка, что делать с проблемным поведением - это все каналы связи, которые помогает налаживать поведенческий анализ в освоении новых навыков» - рассказала Екатерина.</p>\r\n<p>✅ Если ребенок не проявляет интереса к развитию речи, то важно обратиться к специалистам и начать работу с ними как можно раньше. Раннее вмешательство и научно обоснованные методики помогут развить у ребенка коммуникативные навыки.</p>\r\n<p>✅ Чтобы помочь ребенку изменить негативное отношение к выполнению заданий, важно поощрять его за успешные попытки выполнения заданий. \r\nТакже стоит постепенно увеличивать сложность заданий и разбивать их на более маленькие части, чтобы ребенок мог постепенно достигать целей.</p>\r\n<p>✅ Важно помнить, что каждый ребенок уникален и требует индивидуального подхода. Работа со специалистами и поддержка родителей играют ключевую роль в успешном развитии детей с ментальными нарушениями.</p>\r\n<p>Проект «Я могу: социализация и обучение жизненно-важным навыкам детей с РАС» реализуется с использованием пожертвования Благотворительного фонда «Абсолют-Помощь».</p>\r\n<p><a href=\"https://rassvet-apc.ru/news/detail.php?id=894\" target=\"_blank\">Подробнее о проекте</a></p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240910&hash=778b5a19d64b961a\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen class=\"social-video\"></iframe>                        </section>\r\n', '/images/seminar/2024/9.jpg', '2024-04-23', '/images/Absolut/logo.jpg', '', '', 'Новости', '/news.php', 0, '', '12'),
(923, '18', 'Создаём пасхальные тарелочки', 'Рисование - это не просто забавная активность, это настоящее искусство, которое может иметь огромное значение для развития ребенка с РАС. Вот пять причин, почему стоит включить рисование в ежедневную рутину вашего ребенка. ', '<p>Рисование - это не просто забавная активность, это настоящее искусство, которое может иметь огромное значение для развития ребенка с РАС. Вот пять причин, почему стоит включить рисование в ежедневную рутину вашего ребенка:</p>\r\n<p>1️⃣ Выражение эмоций: Рисование дает возможность детям с РАС выразить свои эмоции и мысли. Через рисунки они могут показать свои радость, грусть или любопытство.</p>\r\n<p>2️⃣ Развитие моторики: Рисование требует тонкой моторики и координации движений. Это помогает развивать общую моторику ребенка, а также улучшает его навыки письма и рукописи.</p>\r\n<p>3️⃣ Улучшение визуального восприятия: Рисование помогает развивать способность ребенка с РАС визуально воспринимать и интерпретировать окружающий мир. Они учатся распознавать формы, цвета и текстуры.</p>\r\n<p>4️⃣ Концентрация внимания: Рисование требует от ребенка сосредоточенности и внимания. Они должны фокусироваться на деталях и следить за своими движениями. Это помогает развивать их способность концентрироваться и улучшает их внимание.</p>\r\n<p>5️⃣ Самовыражение и самооценка: Рисование - это способ для ребенка с РАС выразить свою индивидуальность и увидеть результат своего творчества. Это помогает им развивать самооценку и уверенность в себе.</p>\r\n<p>Рисование - это прекрасный способ для ребенка с РАС развивать свои навыки и потенциал. Поэтому не стесняйтесь давать им возможность погрузиться в мир искусства и развиваться через рисование.</p>\r\n<p>На одном из прошлых занятий наши ребята делали расписные пасхальные тарелочки и кружечки. Процесс создания можно посмотреть в нашем видео.</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239418&hash=6ce6fea3bbdabbc7\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2024/13.jpg', '2024-04-19', '', '', '', '', '', 0, '', ''),
(924, '3', 'Семинары для педагогов и родителей Братска', 'Два дня бесплатных лекций, открытых занятий с детьми и консультаций родителей по вопросам формирования навыков сотрудничества ребят с расстройствами аутистического спектра прошли 19 и 20 апреля в Братске на базе БФ «Единство».', '<p>Два дня бесплатных лекций, открытых занятий с детьми и консультаций родителей по вопросам формирования навыков сотрудничества ребят с расстройствами аутистического спектра прошли 19 и 20 апреля в Братске на базе Благотворительного фонда «Единство».</p>\r\n<p>Елена Михеева (поведенческий аналитик МВР, сертифицированный специалист по альтернативной системе коммуникации PECS, учитель-дефектолог) и Дарья Евдокимова (поведенческий аналитик, сертифицированный специалист по альтернативной системе коммуникации PECS) рассказали слушателям и показали на практике особенности работы с детьми с расстройством аутистического спектра, методы и приемы формирования навыков сотрудничества у ребят. </p>\r\n<p>Спасибо участникам за вовлеченность и проявленный интерес! Восхищаемся педагогами, которые открыто признают проблему выстраивания сотрудничества с детьми с аутизмом, несмотря на свой огромный опыт и багаж знаний в педагогике, и с готовностью перенимают наш опыт! </p>\r\n<p>7-8 мая специалисты «РАСсвета»  проведут бесплатные встречи с родителями и педагогами г.Читы. Зарегистрироваться на встречи можно по <a href=\"https://forms.yandex.ru/u/6618ca87d046882d2f64cb08/\" target=\"_blank\">ссылке</a>.</p>\r\n<p>Проект «Я могу: социализация и обучение жизненно-важным навыкам детей с РАС» реализуется с использованием пожертвования Благотворительного фонда «Абсолют-Помощь».</p>\r\n<p><a href=\"https://rassvet-apc.ru/news/detail.php?id=894\" target=\"_blank\">Подробнее о проекте</a></p>\r\n\r\n', '/images/seminar/2024/3/1.jpg', '2024-04-20', '/images/Absolut/logo.jpg;\r\n/images/seminar/2024/3/2.jpg;\r\n/images/seminar/2024/3/3.jpg;\r\n/images/seminar/2024/3/4.jpg;\r\n/images/seminar/2024/3/5.jpg;\r\n/images/seminar/2024/3/6.jpg;\r\n/images/seminar/2024/3/7.jpg;\r\n/images/seminar/2024/3/8.jpg', '', '', 'Новости', '/news.php', 0, '', '12'),
(925, '4', 'Обучение родителей', 'Чтобы результаты ребенка, приобретенные в стенах центра, сохранились и развивались дальше, важно применять их в реальной жизни: дома, на улице, в саду или школе.\r\nОсновам выстраивания сотрудничества и обучения команда «РАСсвета» учит родителей наших учеников на практике.', '<p>Чтобы результаты ребенка, приобретенные в стенах центра, сохранились и развивались дальше, важно применять их в реальной жизни: дома, на улице, в саду или школе.</p>\r\n<p>✅ Как правильно подсказывать ребенку, как договариваться, как развивать коммуникацию и на что обращать внимание в поведении - всему этому команда «РАСсвета» учит родителей наших учеников на практике.</p>\r\n<p>Последняя неделя интенсива - родительская. Родители приглашаются на занятия, где педагоги центра показывают и рассказывают, как эффективно взаимодействовать с ребенком, используя новые навыки, которые он освоил на занятиях. Родители также получат рекомендации по дальнейшему развитию ребенка и поддержке его успехов.</p>\r\n<p>Важно, чтобы родители были вовлечены в обучение своего ребенка и продолжали работать с ним дома для достижения максимальных результатов.</p>\r\n<p>Обучение родителей является неотъемлемой частью программы центра помощи детям с особенностями развития. Только совместные усилия специалистов и родителей могут обеспечить полноценное и успешное развитие ребенка.</p>\r\n<p>Проект «Я могу: социализация и обучение жизненно-важным навыкам детей с РАС» реализуется с использованием пожертвования Благотворительного фонда «Абсолют-Помощь».</p>\r\n<p><a href=\"https://rassvet-apc.ru/news/detail.php?id=894\" target=\"_blank\">Подробнее о проекте</a></p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240944&hash=41744aa483a4165d\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>                        </section>\r\n', '/images/ABA/2024/12.jpg', '2024-04-25', '/images/Absolut/logo.jpg', '', '', 'Новости', '/news.php', 0, '', '12'),
(926, '18', 'Полезная занятость для подростков с аутизмом в Иркутске вышла на новый уровень', 'Еще совсем недавно на диагнозе «аутизм» стояло клеймо – «необучаемый», но на сегодняшний день мы видим, что дети могут находиться в обществе сверстников и занимать себя полезной деятельностью при грамотной педагогической поддержке.', '<p>Еще совсем недавно на диагнозе «аутизм» стояло клеймо – «необучаемый», но на сегодняшний день мы видим, что дети могут находиться в обществе сверстников и занимать себя полезной деятельностью при грамотной педагогической поддержке.</p>\r\n<p>Творческая мастерская для подростков с аутизмом «Дом Синего Слона» выросла из кружка по интересам в настоящую кузницу интерьерных шедевров.</p>\r\n<p>Дети, имеющие нарушения коммуникации и социального взаимодействия, учатся работать в коллективе и изготавливают качественные свечи, мыло, текстильную продукцию и посуду из глины на творческих занятиях с педагогом-психологом.</p>\r\n<p>«Каждый ребенок с аутизмом уникален и имеет свои собственные особенности и потребности. У кого-то из деток снижено понимание речи, но это не проблема, мы используем для таких ребят визуальные инструкции. \r\nКто-то панически боится запачкать руки какой-нибудь жижей. \r\nКто-то напрочь отказывается подходить к печи, чтобы растопить воск. А кому-то нахождение в обществе людей доставляет такой дискомфорт, что у ребенка случается истерика.</p>\r\n<p>С помощью поведенческой психологии мы прорабатываем подобные страхи и учим ребят сотрудничать и заниматься полезной деятельностью. \r\nВажно понимать, что люди с РАС могут достигать успеха и быть активными членами общества при правильной поддержке, понимании и терпимости» - рассказывает руководитель мастерских - Дарья Бабкина.</p>\r\n<p>«Дом Синего Слона» открыл свои двери еще в 2020 году. Люди с аутизмом стереотипны в своем поведении, а их интересы обычно ограничены, поэтому заинтересовать их новой полезной деятельностью было сложно.</p>\r\n<p>Подопечные «Слона» свой путь начинали с адаптации к новой полезной занятости: \r\nпривыкали к большому количеству народа в стенах мастерской, занимались только любимыми занятиями: лепка, лизуны, игрушки, а кто-то даже просто приходил посмотреть мультики. Постепенно ребят приучали к требованиям, к тому, что надо откладывать любимые дела и приступать к занятиям. Что с материалами нужно работать аккуратно и по определенным правилам. Дети осваивали ремесленные навыки с помощью индивидуальных помощников – тьюторов. </p>\r\n<p>«Все ученики в мастерских разные: кто-то виртуозен в действиях, а кто-то мастерит совсем не спеша. Кто-то вдумчиво пытается выстроить порядок в своих работах, а кто-то не запаривается над композицией. Задача мастерских – подобрать для учеников деятельность, которая бы была им по душе, и в которой они бы были успешны и максимально самостоятельны. Не зависели от помощи другого человека. Мы предлагаем им попробовать себя и с глиной, и с бетоном. Воск, мыльная основа, различные ткани, акриловые картины – занятия разнообразны!» - поделилась тьютор мастерских, Анастасия Андреева.</p>\r\n<p>«Мы вышли на новый уровень, наши ученики могут самостоятельно, без помощи взрослых, создать свечу! Они научились закреплять фитили, плавить воск, украшать и ароматизировать свечи. Ребята научились следовать алгоритму и получать удовольствие от работы. Есть процент ребят, кому еще требуется помощь наставника, но все действия им знакомы, и они не проявляют проблемного поведения, занимаясь полезной деятельностью» - Александра Смелая, психолог мастерской.</p>\r\n<p>Здорово, что в Иркутске есть бесплатные проекты, где дети с расстройствами аутистического спектра могут развиваться и практиковаться в ремесле, изучать навыки, которые в будущем могут помочь им трудоустроиться.</p>\r\n<p>АНО \"АПЦ Рассвет\" - участник проекта Ассоциации \"Аутизм-Регионы\" - \"Полезная занятость с применением практик и методов с научно доказанной эффективностью для молодых людей с РАС\" при поддержке Фонда президентских грантов.</p>', '/images/DSS/2024/6/1.jpg', '2024-04-29', '/images/DSS/2024/6/2.jpg;\r\n/images/DSS/2024/6/3.jpg;\r\n/images/DSS/2024/6/4.jpg;\r\n/images/DSS/2024/6/5.jpg;\r\n/images/DSS/2024/6/6.jpg;\r\n/images/DSS/2024/6/7.jpg;\r\n/images/DSS/2024/6/8.jpg;\r\n/images/DSS/2024/6/9.jpg;\r\n/images/DSS/2024/6/10.jpg;\r\n/images/DSS/2024/6/11.jpg', '', '', '', '', 0, '', ''),
(927, '18', 'Необычные свечи', 'Сегодня хотим рассказать вам историю о том, как у нас в голове зародилась идея создания таких необычных свечек с сухоцветами. И да, они действительно выглядят просто потрясающе!\r\n', '<p>Сегодня хотим рассказать вам историю о том, как у нас в голове зародилась идея создания таких необычных свечек с сухоцветами. И да, они действительно выглядят просто потрясающе!\r\n</p>\r\n<p>Все началось одним прекрасным днем, когда наша мастерская включила свою фантазию на полную мощность. Одним из вечеров наша внимательная Александра наткнулась на видео, где девушка делала эти удивительные свечи. И сразу же сверкнула глазами и подумала: \"Почему бы и нам не попробовать?\"\r\n\r\n</p>\r\n<p>Решение было принято, и мы приступили к мастерству. Ведь у нас в мастерской есть особенность - мы работаем с детьми с РАС, и мы всегда ищем новые способы развивать их творческие навыки. Эта идея сделать свечки с сухоцветами была просто идеальной для нас.</p>\r\n<p>Мы начали собирать самые красивые и нежные сухоцветы, которые можно было найти. Затем мы их красиво укладывали в композицию, после плавили воск и аккуратно заливали его в специальные формы.\r\n</p>\r\n<p>И вот, наконец, наши свечки готовы! Мы восхищены результатом и не можем не поделиться им с вами. Они выглядят так нежно, так мило, что невозможно пройти мимо.</p>\r\n<p>Проект \"Давай дружить\" реализуется при поддержке «Губернского собрания общественности Иркутской области»</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239425&hash=26bd0947353665c5\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2024/14.jpg', '2024-04-30', '/images/grants/GS.png', '', '', '', '', 0, '', '14'),
(928, '4', 'Как научить ребенка не хватать предметы во время занятия', 'Как мы работаем с ребятами, кто отказывается заниматься, хватает учебные материалы или вовсе убегает? Мы собрали видео про замечательного мальчика Мирослава, с которым мы познакомились на занятиях в \"РАСсвете\".', '<p>Как мы работаем с ребятами, кто отказывается заниматься, хватает учебные материалы или вовсе убегает? Мы собрали видео про замечательного мальчика Мирослава, с которым мы познакомились на занятиях в \"РАСсвете\".</p>\r\n<p>⛔ Проблемы, с которыми мы столкнулись: Мирослав отказывался садиться за стол, всячески избегал просьбы взрослых и не следовал правилам игры.</p>\r\n<p>✅ Первое, за что мы взялись - установление сотрудничества. Весело играли с его любимыми игрушками, не указывали ему, что делать, а просто следовали за ним.\r\nРебенок доверился нам и уже не переживал, если мы предлагали ему пересесть с пола за парту.</p>\r\n<p>✅ Учебное поведение. С помощью жетонной системы подкрепления Мирослав научился внимательно слушать педагога и следовать правилам игры.</p>\r\n<p>✅ Адекватные задания.\r\nМы исключили задания, не соответствующие уровню развития навыков ребенка, которые расстраивали его из-за сложности и непонятности, разбили их на более легкие навыки и тренировали по отдельности.</p>\r\n<section class=\"video-detail\">\r\n								<iframe src=\"https://www.youtube.com/embed/p1jnem5B140?si=ZoUDWgMc1MdqoaqY\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen class=\"social-video\"></iframe>\r\n							</section>\r\n<p>Проект «Я могу: социализация и обучение жизненно-важным навыкам детей с РАС» реализуется с использованием пожертвования Благотворительного фонда «Абсолют-Помощь».</p>\r\n<p><a href=\"https://rassvet-apc.ru/news/detail.php?id=894\" target=\"_blank\">Подробнее о проекте</a></p>\r\n', '/images/ABA/2024/13.jpg', '2024-04-30', '/images/Absolut/logo.jpg', '', '', 'Новости', '/news.php', 0, '', '12'),
(929, '13,18', 'О мастерских \"Дом синего слона\" на Babr24.com', 'Здорово, что в Иркутске есть бесплатные проекты, где дети с расстройствами аутистического спектра могут развиваться и практиковаться в ремесле, изучать навыки, которые в будущем могут помочь им трудоустроиться. О мастерских \"Дом синего слона\" вновь вышла статья в интернет издании <a href=\"https://babr24.com/?lDE=259395\" target=\"_blank\">Babr24.com</a>', '', '/images/DSS/2024/6/9.jpg', '2024-04-30', '', '', 'https://babr24.com/?lDE=259395', '', '', 1, '', ''),
(930, '4', 'Бесплатные консультации в Рассвете', 'В \"РАСсвете\" в самом разгаре бесплатные консультации с поведенческими аналитиками в рамках социального проекта \"Без паники! Идем к стоматологу\". Все родители, посетившие стоматологические семинары и заполнившие родительские анкеты, приглашены на индивидуальные консультации с кураторами нашего центра.\r\n', '<p>В \"РАСсвете\" в самом разгаре бесплатные консультации с поведенческими аналитиками в рамках социального проекта \"Без паники! Идем к стоматологу\". Все родители, посетившие стоматологические семинары и заполнившие родительские анкеты, приглашены на индивидуальные консультации с кураторами нашего центра.\r\n</p>\r\n<p>На встречах специалисты оценивают поведение ребенка в условиях медицинского осмотра, дают рекомендации по адаптации и работе с проблемным поведением, а также по результатам консультации \"РАСсвет\" приглашает детей на индивидуальные занятия АВА-терапией по работе со страхами и развитием навыков спокойного похода к стоматологу.</p>\r\n<p>Кто-то из ребят испытывает жуткую тревогу при виде медицинского кресла и с криком отказывается заходить в кабинет, а кто-то достаточно хорошо сотрудничает с врачами, но, например, боится поставить укол в десну или нервничает из-за громкого звука бормашины.</p>\r\n<p>Впереди много работы!\r\nАдаптационные занятия с ребятками, обучение стоматологов Иркутской городской детской стоматологической поликлиники, прием наших старших товарищей из московского фонда \"Импульс развития\", первых организовавших \"Дружелюбную стоматологию\" в России.\r\n</p>\r\n<p>Совместными усилиями мы сможем помочь детям преодолеть страх перед походом к стоматологу и создать у них позитивный опыт посещения зубного врача.\r\nМы знаем, что благодаря научному подходу и систематическим занятиям ребята смогут преодолеть свои страхи и довериться профессионалам заботы о здоровье и улыбке.</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240957&hash=759e4a371900b185\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>\r\n', '/images/ABA/2024/14.jpg', '2024-04-30', '', '', '', '', '', 0, '', '11'),
(932, '16', 'Поздравляем с успешным окончанием первого модуля', 'Поздравляем наших АВА-инструкторов Татьяну и Екатерину с успешным окончанием первого теоретического курса и прохождением практического тренинга в рамках сертифицированной программы подготовки специалистов по прикладному анализу поведения (руководитель программы: Юлия Эрц, МА, BCBA).', '<p>Поздравляем наших АВА-инструкторов Татьяну и Екатерину с успешным окончанием первого теоретического курса и прохождением практического тренинга в рамках сертифицированной программы подготовки специалистов по прикладному анализу поведения (руководитель программы: Юлия Эрц, МА, BCBA).</p>\r\n<p>Девушки поделились своим мнением о важности обучения:</p>\r\n<p>Татьяна: На мой взгляд, обучение очень важно. Я говорю не только про прикладной анализ, а вообще - когда ты постоянно узнаешь что-то новое, ты развиваешься и, соответственно, становишься более компетентным в этой области. </p>\r\n<p>Екатерина: Если ты хочешь работать в АВА, то да, да и ещё раз да! Нужно постоянно обновлять свои знания, подтверждать свои компетенции. Иначе какие мы помощники, если не будем развиваться!</p>\r\n<p>Обучение проходило в рамках проекта \"Без паники! Идём к стоматологу\". Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>', '/images/news/2024/3.jpg', '2024-05-02', '', '', '', '', '', 0, '', '11'),
(933, '19', 'Что такое День Победы?', 'Дорогие друзья! В этом году мы с вами отмечаем 79-ю годовщину Победы в Великой Отечественной войне. Это важное событие для нашей страны, которое навсегда останется в сердцах каждого из нас.', '<p>Дорогие друзья! В этом году мы с вами отмечаем 79-ю годовщину Победы в Великой Отечественной войне. Это важное событие для нашей страны, которое навсегда останется в сердцах каждого из нас.</p>\r\n<p>День Победы – это не только праздник, но и символ нашей солидарности, мужества и единства. Это день, когда мы чтим память тех, кто отдал свою жизнь за нашу свободу и независимость.\r\n</p>\r\n<p>Очень важно донести до подрастающего поколения знания о Великой Отечественной войне и подвиге народа! И дети с аутизмом не исключение. </p>\r\n<p>Педагоги наших ресурсных классов каждый год в преддверии праздника проводят уроки, на которых рассказывают детям о важности и исторической значимости тех тяжелый дней. Ребята участвуют в инклюзивных мероприятиях, посвященных празднику Великой Победы! </p>\r\n<p>В этом году ученики ресурсного класса школы поселка Молодежный выучили стихотворение Андрея Усачева «Что такое День Победы?» и возложили цветы к памятнику \"Всем победившим в Великую Отечественную Войну\".</p>\r\n<p>А ребята из ресурсного класса поселка Луговое приняли участие в общешкольном праздничном концерте и выставке макетов военной техники, а также приготовили открытки для ветеранов Великой Отечественной Войны.</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240971&hash=28738d4ed7a9c259\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen class=\"social-video\"></iframe>                        </section>\r\n\r\n<p>АНО \"АПЦ Рассвет\" - участник проекта «Ресурсный класс. Доступная среда». Благодарим организатора проекта Ассоциацию \"Аутизм-Регионы\".\r\nПроект реализуется при поддержке Фонда президентских грантов и благотворительного фонда \"Абсолют - Помощь\".</p>\r\n\r\n\r\n', '/images/RK/2024/6/1.jpg', '2024-05-09', '/images/RK/2024/6/2.jpg;\r\n/images/RK/2024/6/3.jpg;\r\n/images/RK/2024/6/4.jpg;\r\n/images/RK/2024/6/6.jpg;\r\n/images/RK/2024/6/7.jpg;\r\n/images/RK/2024/6/8.jpg;\r\n/images/RK/2024/6/9.jpg', '', '', 'Новости', '/news.php', 0, '', ''),
(934, '19', '+ 1 ресурсный класс в Иркутске', 'Родительские собрания проведены, фронт работ намечен: на базе МБОУ СОШ №8 в 2024 году открывается пятый по счету ресурсный класс для ребят с РАС под сопровождением команды «РАСсвета»!', '<p>Родительские собрания проведены, фронт работ намечен: на базе МБОУ СОШ №8 в 2024 году открывается пятый по счету ресурсный класс для ребят с РАС под сопровождением команды «РАСсвета»!</p>\r\n<p>Ресурсный класс будет предоставлять специализированную помощь и поддержку детям с особыми образовательными потребностями, чтобы помочь им успешно адаптироваться к учебной среде и раскрыть потенциал каждого конкретного ребенка.\r\n</p>\r\n<p>Адаптированная учебная программа для каждого ученика, научно обоснованный подход в обучении детей, системная поддержка опытных специалистов прикладного анализа поведения и главное - развитие социально-коммуникативных навыков ребятишек.\r\n</p>\r\n<p>Проект реализуется при поддержке «Губернского собрания общественности Иркутской области»</p>\r\n', '/images/RK/2024/7/1.jpg', '2024-05-13', '/images/grants/GS.png;\r\n/images/RK/2024/7/2.jpg;\r\n/images/RK/2024/7/3.jpg;\r\n/images/RK/2024/7/4.jpg', '', '', '', '', 0, '', '14'),
(936, '3', 'Бесплатные интенсивы для семей Братска и Читы', 'В рамках социального проекта \"Я могу: социализация и обучение жизненно-важным навыкам детей с РАС\" команда \"РАСсвета\" посетила города Братск и Чита. Для местных жителей были проведены обучающие мероприятия и, конечно, очные консультации.', '<p>В рамках социального проекта \"Я могу: социализация и обучение жизненно-важным навыкам детей с РАС\" команда \"РАСсвета\" посетила города Братск и Чита.\r\nДля местных жителей были проведены семинары по теме \"Формирование навыков сотрудничества с детьми с РАС\", открытые практические занятия с ребятишками и, конечно, очные консультации с поведенческими аналитиками для семей, воспитывающих ребят с поведенческими особенностями.</p>\r\n<p>В поездке мы познакомились с большим количеством чудесных семей и рады вам сообщить, что \"РАСсвет\" готовиться принять на бесплатные интенсивы несколько детей. </p>\r\n<p>АВА-терапия - это не просто часовые занятия в кабинете, это стиль жизни. Наше существование подчинено законам поведения. При осознанном подходе к изменениям в окружающей среде, можно добиться колоссальных результатов в обучении ребят с проблемами в коммуникации: понимать речь, общаться, играть, быть самостоятельным и быть частью общества.</p>\r\n<p><a href=\"https://rassvet-apc.ru/events/detail.php?id=924&back=seminar\" target=\"_blank\">Здесь</a> можно посмотреть подробнее про поездку в Братск.</p>\r\n<p>Благодарим благотворительные организации «Единство» (Братск) и «МОЙ НОВЫЙ МИР” (Чита) за помощь в организационных вопросах и теплых приемах!</p>\r\n<p>А также сердечно поздравляем Благотворительный фонд «Абсолют-Помощь» с днем рождения!\r\nВаша поддержка позволяет менять ситуацию с аутизмом в Сибири в лучшую сторону: родители начинают понимать и принимать своих детей, педагоги начинают применять прогрессивные и научно обоснованные методики преподавания, а образовательные организации готовятся создавать подходящие условия для учеников с нарушениями коммуникации!</p>\r\n<p>Проект «Я могу: социализация и обучение жизненно-важным навыкам детей с РАС» реализуется с использованием пожертвования Благотворительного фонда «Абсолют-Помощь».</p>\r\n<p><a href=\"https://rassvet-apc.ru/news/detail.php?id=894\" target=\"_blank\">Подробнее о проекте</a></p>\r\n\r\n', '/images/seminar/2024/4/1.jpg', '2024-05-14', '/images/Absolut/logo.jpg;\r\n/images/seminar/2024/4/2.jpg;\r\n/images/seminar/2024/4/3.jpg;\r\n/images/seminar/2024/4/4.jpg;\r\n/images/seminar/2024/4/5.jpg;\r\n/images/seminar/2024/4/6.jpg;\r\n/images/seminar/2024/4/7.jpg;\r\n/images/seminar/2024/4/9.jpg;\r\n/images/seminar/2024/4/8.jpg', '', '', 'Новости', '/news.php', 0, '', '12'),
(937, '4', 'Стоматолог. С чего начать?', 'Замерьте, что именно вызывает проблемное поведение ребенка? Он не понимает правила поведения в больнице или у него был неприятный опыт и он боится врачей? А может он чувствителен к текстурам, запахам, свету и ощущениям?', '<p>Замерьте, что именно вызывает проблемное поведение ребенка? Он не понимает правила поведения в больнице или у него был неприятный опыт и он боится врачей? А может он чувствителен к текстурам, запахам, свету и ощущениям?\r\n</p>\r\n<p>Расскажем про Демьяна</p>\r\n<p>В данном случае мы можем начать с того, что позволим Демьяну самому держать инструмент, который будет использовать врач, чтобы он мог почувствовать его форму, вес и текстуру. Затем постепенно будем приближать инструмент к его роту, но не вводить его в полость рта.\r\nПосле этого предложим Демьяну прикоснуться инструментом к губам, зубам, чтобы он мог привыкнуть к ощущениям.</p>\r\n<p>Важно проводить все эти шаги постепенно и внимательно наблюдать за реакцией Демьяна, чтобы не перегрузить его и не вызвать новый страх. По мере того, как ребенок будет преодолевать тревогу, можно увеличивать время контакта с инструментом и постепенно приводить к процедуре обследования у стоматолога.\r\n</p>\r\n<p>Используя поведенческую методику - систематическую десенсибилизацию, мы можем помочь Демьяну преодолеть свой страх перед посещением стоматолога и успешно пройти процедуру обследования.</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240983&hash=db73d6c41d5c882b\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>\r\n', '/images/ABA/2024/15.jpg', '2024-05-15', '', '', '', '', '', 0, '', '11'),
(938, '10', 'Проект «Коммуникативные группы для детей с расстройствами аутистического спектра»', 'Статус проекта: завершен<br>\r\nСроки реализации: 01.04.2024 – 31.12.2024<br>\r\nИсточник софинансирования: Благотворительный фонд Сбербанка \"Вклад в будущее\"', '<p><b>Статус проекта:</b> завершен<br>\r\n<b>Сроки реализации:</b> 01.04.2024 – 31.12.2024<br>\r\n<b>Источник софинансирования:</b> Благотворительный фонд Сбербанка \"Вклад в будущее\"</p>\r\n<p></p>\r\n<p><b>Цель проекта:</b></p>\r\n<p>Сформировать навыки социально-приемлемого взаимодействия и коммуникации у 20 детей с РАС 5-15 лет путем проведения занятий с применением принципов Прикладного анализа поведения, направленных на снижение преград к обучению с последующим обобщением в общественных местах.</p>\r\n<p><b>Задачи:</b></p>\r\n<p>1. Снизить частоту нежелательного поведения и преград к обучению навыкам коммуникации (в том числе альтернативной коммуникации Pecs) у 20 детей с РАС на индивидуальных занятиях с использованием поведенческих подходов</p>\r\n<p>2. Закрепить приобретенные на индивидуальных занятиях навыки у 20 детей с РАС в групповом формате с целью формирования навыков социального взаимодействия</p>\r\n<p>3. Обобщить освоенные на групповых занятиях навыки социального взаимодействия и коммуникации у 20 детей с РАС путем посещения общественных мест в игровом формате с участием членов семей</p>\r\n<p><b>Краткое описание:</b></p>\r\n<p>У детей с РАС нарушена коммуникация и навыки социального взаимодействия со сверстниками, а сложности в воображении отягощают обобщение и, как следствие, социализацию ребенка и приводит к аутизации семьи. Данную проблему мы решаем с помощью проведения с 20 детьми занятий с применением принципов прикладного анализа поведения.</p>\r\n<p>Проект начинается с консультации с каждым родителем и структурированного наблюдения за ребенком, в ходе которого поведенческий аналитик знакомит родителей с обучающим маршрутом и разрабатывает рекомендации, направленные на развитие коммуникации и социального взаимодействия с использованием поведенческих подходов.</p>\r\n<p>В начале занятий с детьми с РАС устанавливается руководящий контроль для дальнейшего сотрудничества и выявления дефицитов в навыках коммуникации и социального взаимодействия и оценка навыков по VB-MAPP и ABLS-R, на основании тестирований кураторы разработают индивидуальные протоколы для каждого навыка в зависимости от особенностей конкретного ребенка. На индивидуальных занятиях с детьми с РАС, направленных на снижение частоты нежелательного поведения и преград к обучению, дети подготовятся к групповому формату занятий. Оборудованная комната сенсорной разгрузки плавно переведет ребенка из индивидуального формата занятий в групповой и в случаях неприемлемого поведения во время групповых занятий даст возможность вовремя вывести ребенка на индивидуальный формат работы.</p>\r\n<p>На 2х часовых занятиях в группе у детей с РАС сформируются навыки социального взаимодействия: спокойное нахождение в группе за столом и вне стола, ожидание своей очереди, навыки просьбы, ведение простого диалога, развитие игровых навыков. На игровых переменках дети с РАС получат положительный опыт игры друг с другом, а также закрепят навыки имитации в группе, хождения в хороводе, ожидания своей очереди, просьб друг у друга.</p>\r\n<p>Для закрепления сформированные на занятиях навыков в натуральной среде со сверстниками дети с РАС примут участие в двух детских мероприятиях: на свежем воздухе с походом в магазин и на детские площадки и в игровой комнате г. Иркутска.</p>\r\n<p>Для 20 родителей детей с РАС пройдут собрания-консультации по возникающим в ходе обучения ребенка вопросам, по обобщению освоенных умений ребенка с учетом его поведенческих особенностей в естественной среде. Для повышения осведомленности родителей детей с РАС опубликуются вовлекающие статьи и видеоролики об успехах ребят и методах обучения коммуникации и социальному взаимодействию.</p>\r\n<p>По окончании проекта дети с аутизмом при регулярном обучении обретут новые навыки коммуникации и социального взаимодействия, которые позволят им успешно социализироваться в обществе, что подтвердится заключительным тестированием ребят.</p>\r\n\r\n<p><a href=\"/news.php?project=21\" class=\"button\">Публикации о реализации проекта</a></p>', '/images/partners/VK_VB-color.png', '2025-04-06', '', '', '', 'Гранты', '/pages/grants.php', 0, '', ''),
(939, '4', 'Адаптивные занятия в \"Рассвете\"', 'Стоматологические инструменты, дезинфицирующие средства и материалы,необходимые для создания стоматологических условий для детей с расстройствами аутистического спектра закуплены и уже активно применяются в работе с учениками!', '<p>Друзья, мы рады сообщить вам, что стоматологические инструменты, дезинфицирующие средства и материалы,необходимые для создания стоматологических условий для детей с расстройствами аутистического спектра закуплены и уже активно применяются в работе с учениками!\r\n</p>\r\n<p>Цель адаптивных занятий - создать условия, максимально схожие со стоматологическим кабинетом, чтобы облегчить процесс десенсибилизации и проведения стоматологических процедур для детей с проблемным поведением.</p>\r\n<p>На консультативной встрече куратор «РАСсвета» Елена Сергеевна так вошла в роль врача-стоматолога, что ее любимый ученик Тимофей даже чуточку запереживал, не придется ли лечить зубы по-настоящему!\r\n</p>\r\n<p>Несмотря на всю серьезность занятия, он позволил провести осмотр, а проблемные моменты мы взяли на вооружение и в консультативной форме дали родителям рекомендации по адаптации Тимы к стоматологическим процедурам!\r\n</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240992&hash=c2f1478a5fb68411\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>\r\n', '/images/ABA/2024/16.jpg', '2024-05-16', '', '', '', '', '', 0, '', '11'),
(940, '18', 'Занятия бетоном', 'Многие родители наверняка задумывались, чем можно занять своих детей, чтобы развивать их навыки и умения. Занятие бетоном может стать отличным выбором!', '<p>Сегодня хотим рассказать вам о замечательном занятии для детей с РАС - работе с бетоном. Многие родители наверняка задумывались, чем можно занять своих детей, чтобы развивать их навыки и умения. А вот занятие бетоном может стать отличным выбором!</p>\r\n<p>Во-первых, работа с бетоном развивает моторику и координацию движений у детей с РАС. Ведь для создания фигурок или скульптур из бетона требуется точность и аккуратность. Постепенно дети научатся контролировать свои движения, что будет полезно для их общей физической координации.</p>\r\n<p>Во-вторых, занятие бетоном способствует развитию воображения и креативности у детей. Они могут создавать самые разнообразные фигурки, игрушки или декоративные элементы. Это поможет им расширить свои представления о мире и научиться мыслить творчески.</p>\r\n<p>Кроме того, работа с бетоном может стать отличным способом для детей с РАС выразить свои эмоции и чувства. Ведь через создание фигурок они могут передать свои мысли и переживания. Это особенно важно для детей, которые испытывают трудности в общении и выражении своих эмоций.\r\n</p>\r\n<p>Также занятие бетоном может помочь детям с РАС развить усидчивость и терпение. Ведь для создания качественных изделий требуется время и усилия. Дети научатся сосредотачиваться на процессе работы и не бросать начатое. Это важный навык, который пригодится им во многих сферах жизни.</p>\r\n<p>Надеемся, что теперь вы понимаете, насколько полезно занятие бетоном для детей с РАС. Оно развивает моторику, креативность, помогает выражать эмоции и учиться усидчивости. Попробуйте предложить это занятие своему ребенку и увидите, как он будет радоваться созданию собственных произведений и развиваться вместе с ними. Удачи вам и вашим детям!</p>', '/images/DSS/2024/8/1.jpg', '2024-05-16', '/images/DSS/2024/8/2.jpg;\r\n/images/DSS/2024/8/3.jpg;\r\n/images/DSS/2024/8/4.jpg', '', '', '', '', 0, '', ''),
(941, '3', 'Обучение стоматологов работе с детьми с РАС', 'Обучению стоматологов Городской детской стоматологической поликлиники \"РАСсвет\" уделяет большое внимание: ознакомительные вебинары, обучающий курс и конечно же - практика работы с пациентами с РАС под руководством ведущих специалистов проекта \"Дружелюбная стоматология для детей с РАС\"', '<p>Готовность стоматолога к приему пациента с нарушениями коммуникации в большинстве случаев является определяющим фактором - получится провести комфортный осмотр/лечение ребенку или нет.</p>\r\n<p>Именно поэтому обучению стоматологов Городской детской стоматологической поликлиники \"РАСсвет\" уделяет большое внимание: ознакомительные вебинары, обучающий курс и конечно же - практика работы с пациентами с РАС под руководством поведенческих аналитиков и ведущих специалистов проекта \"Дружелюбная стоматология для детей с РАС\" , приехавших специально к нам в гости из Москвы.</p>\r\n<p>На сегодняшний день было проведено уже 4 приема с подопечными детьми нашего центра. Ребятишки большие молодцы! Дети знают правила поведения в больнице и отлично сотрудничают со взрослыми даже в самых тревожных для них ситуациях.\r\nКто-то боится бор-машины, а кто-то переживает из-за яркого света лампы.\r\nТем не менее, дети позволяют осуществлять уход за полостью рта и даже получают удовольствие от своих успехов!</p>\r\n<p>Мальчишки продолжают заниматься и совершенствоваться в поведении \"спокойного\" похода к стоматологу.</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>', '/images/seminar/2024/5/1.jpg', '2024-05-17', '/images/seminar/2024/5/2.jpg;\r\n/images/seminar/2024/5/3.jpg;\r\n/images/seminar/2024/5/4.jpg;\r\n/images/seminar/2024/5/5.jpg;\r\n/images/seminar/2024/5/6.jpg;\r\n/images/seminar/2024/5/7.jpg', '', '', 'Новости', '/news.php', 0, '', '11');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(942, '13', 'Об обучении стоматологов в Иркутских СМИ', 'Читайте в Иркутских СМИ: Сотрудники ИГДСП учатся работать с детьми, имеющими расстройства аутистического спектра. Обучение ведется в рамках проекта \"Без паники! Идем к стоматологу\" АПЦ \"РАСсвет\", который реализуется при поддержке Фонда президентских грантов.', '<p>Сотрудники Иркутской городской детской стоматологической поликлиники учатся работать с детьми, имеющими расстройства аутистического спектра. Обучение ведется в рамках проекта \"Без паники! Идем к стоматологу\" адаптационно-педагогического центра \"РАСсвет\", который реализуется при поддержке Фонда президентских грантов.</p>\r\n<p>Подробнее об обучении читайте в региональных СМИ:</p>\r\n<p><a href=\"https://baikal.mk.ru/social/2024/05/17/v-irkutske-stomatologi-uchatsya-pomogat-detyam-s-autizmom.html?utm_source=yxnews&utm_medium=desktop\" target=\"_blank\">\"Московский комсомолец\"</a></p>\r\n<p><a href=\"https://www.ogirk.ru/2024/05/17/irkutskie-stomatologi-uchatsja-okazaniju-pomoshhi-detjam-s-rasstrojstvami-psihologicheskogo-razvitija/?utm_source=yxnews&utm_medium=desktop\" target=\"_blank\">\"Областная газета\"</a></p>\r\n<p><a href=\"https://baikal24.ru/text/17-05-2024/050/?utm_source=yxnews&utm_medium=desktop\" target=\"_blank\">\"Байкал24\"</a></p>\r\n<p><a href=\"https://vk.com/wall-191732365_4277\" target=\"_blank\">\"Минздрав Иркутской области\"</a></p>', '/images/seminar/2024/5/8.jpg', '2024-05-18', '', '', '', '', '', 0, '', '11'),
(943, '4', 'Эффектное появление Ярика в стоматологии', 'Ярик лоялен к врачам, без проблем зашел, сел в кресло и позволил провести осмотр стоматологическими инструментами. Но серьезной преградой для проведения стоматологических процедур является его страх к громким вибрирующим инструментам, гиперакузия. ', '<p>Ярик лоялен к врачам, без проблем зашел, сел в кресло и позволил провести осмотр стоматологическими инструментами. Но серьезной преградой для проведения стоматологических процедур является его страх к громким вибрирующим инструментам, гиперакузия.</p>\r\n<p>В работе со страхами применяется метод систематической десенсибилизации. Он заключается в том, что пациент постепенно и систематически привыкает к стимулу, который вызывает у него страх или дискомфорт.</p>\r\n<p>Врач постепенно знакомила Ярослава с бормашиной: в выключенном состоянии касалась сначала рук, постепенно подступая ко рту. Потом включила машину на минимальных оборотах, дотрагиваясь сначала до пальчиков, а потом подступила и к настоящей механической чистке зубов.\r\nТоже самое было проделано и с слюноотсосом, мягко и постепенно.\r\nМама Ярика находилась рядом и подкрепляла поведение Яры жетонами.\r\n</p>\r\n<p>Вот что рассказывает Светлана, мама Ярика:\r\n</p>\r\n<p>«Были с сыном в рамках проекта на приеме. Мой сын позволил поместить включенные инструменты в рот! Возможно для кого-то это обычное дело, но для нас-это настоящее достижение! Спасибо огромное ОГАУЗ «ИГДСП», АПЦ РАСсвет, Соболевой Наталье Николаевне и Екатерине Родыгиной за такую возможность! Врач работала, учитывая особенности поведения детей с аутизмом, что позволило добиться таких результатов. По закону все имеют право на оказание медицинской помощи, проект позволяет это право обеспечить в полной мере. Ещё раз спасибо всем причастным!»</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456240998&hash=6af2f124d232fd5f\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>\r\n', '/images/seminar/2024/5/5.jpg', '2024-05-20', '', '', '', '', '', 0, '', '11'),
(944, '19', 'Отзыв тьютора о работе в РК', 'Дети ресурсного класса МОУ ИРМО «Марковской СОШ №2» заканчивают уже 3-ий год обучения. Тьютор Наталья Васильевна Короткова делится, как обучение и поддержка поведенческих аналитиков помогает команде класса еще лучше обучать учеников с РАС.', '<p>Дети ресурсного класса МОУ ИРМО «Марковской СОШ №2» заканчивают уже 3-ий год обучения. Тьютор Наталья Васильевна Короткова делится, как обучение и поддержка поведенческих аналитиков помогает команде класса еще лучше обучать учеников с РАС.</p>\r\n<p>\"Работа тьютора - это тесное взаимодействие с детьми. Мы должны уметь найти подход к каждому ребёнку в классе и создать для него комфортные условия и мотивировать на успешное обучение.\r\nВ ходе работы мы привыкаем к детям, а они к нам, ведь мы вместе каждый учебный день. Какое-то специфичное поведение ребенка становится привычным для всех в классе и мы продолжаем его подкреплять.</p>\r\n<p>Например, вокализации у ребенка. Он не реагирует на замечания взрослых и продолжает мычать на уроках, мешая учебному процессу.\r\nМы не можем объяснить ученику, чтобы он вел себя тише, поэтому начинаем воспринимать вокализации за его особенность и они становятся частью наше рутины.</p>\r\n<p>Поведенческий специалист обращает внимание на такое поведение и предлагает с ним работать, применяя поведенческие стратегии. И вот проведя функциональный анализ, установив причину его мычания, выясняется, что таким образом ребенок просит помощь у взрослых. Начинает мычать, громче и громче, на него обращают персональное внимание, подходят к нему и, чаще всего, в такой момент ученику требуется помощь взрослого.\r\nРебенка нужно учить альтернативному поведению - просьбе \"помоги\".</p>\r\n<p>Бывают такие ситуации, когда ты всё делаешь по протоколу, но что-то не получается, результата нет. Вот тут на помощь приходит куратор. Это человек со «свежим взглядом» со стороны. Педагог, который скорректирует твою работу, даст дельный совет. Мне это очень помогает в работе.\r\nА самое главное, что общение с куратором проходит в позитивном ключе, ведь у нас общая цель - помочь ребенку.\r\n</p>\r\n<p>Кроме того, важно подчеркнуть, что куратор не только проверяет нашу работу, но и поддерживает нас как специалистов. Он помогает нам общаться с родителями, дает рекомендации по работе с детьми с РАС, выходит на супервизии, на которых мы можем обсудить сложные случаи и найти решения все вместе\".\r\n</p>\r\n<p>АНО \"АПЦ Рассвет\" - участник проекта «Ресурсный класс. Доступная среда». Благодарим организатора проекта Ассоциацию \"Аутизм-Регионы\".\r\nПроект реализуется при поддержке Фонда президентских грантов и благотворительного фонда \"Абсолют - Помощь\".</p>\r\n\r\n\r\n', '/images/RK/2024/8/1.jpg', '2024-05-21', '/images/RK/2024/8/2.jpg;\r\n/images/RK/2024/8/3.jpg;\r\n/images/RK/2024/8/4.jpg;\r\n/images/RK/2024/8/5.jpg', '', '', 'Новости', '/news.php', 0, '', ''),
(945, '18', 'Мастер-класс в Иркутском Зоосаде', 'Вчера мы провели творческий мастер-класс на ежегодном инклюзивном фестивале, который был организован \"Иркутским зоосадом\". Темой мастер-класса были «Приёмы арт-терапии в работе с детьми с ограниченными возможностями здоровья». \r\n', '<p>Вчера мы провели творческий мастер-класс на ежегодном инклюзивном фестивале, который был организован \"Иркутским зоосадом\". Темой мастер-класса были «Приёмы арт-терапии в работе с детьми с ограниченными возможностями здоровья». Все вместе мы создавали рисунок «Ёжик».\r\n</p>\r\n<p>Мы были рады видеть не только постоянных учеников «Дома Синего Слона», но и других детей, которые с удовольствием присоединились к нам.</p>\r\n\r\n<p>Ребята — большие молодцы! Каждый старался нарисовать своего лучшего ёжика, и у них это отлично получилось! </p>\r\n<p>Было приятно наблюдать, как ученики с расстройством аутистического спектра активно общались со своими сверстниками. Мастер-класс предоставил им замечательную возможность проявить свой творческий потенциал и почувствовать себя частью коллектива.\r\n</p>\r\n<p>Мы хотим выразить благодарность «Иркутскому зоосаду» за организацию этого замечательного события.</p>\r\n<p>АПЦ РАСсвет/Центр адаптации и АВА-терапии - участник проекта Ассоциации «Аутизм-Регионы» \"Полезная занятость с применением практик и методов с научно доказанной эффективностью для молодых людей с РАС\" при поддержке Фонда президентских грантов.</p>', '/images/DSS/2024/9/1.jpg', '2024-05-21', '/images/DSS/2024/9/2.jpg;\r\n/images/DSS/2024/9/3.jpg;\r\n/images/DSS/2024/9/5.jpg;\r\n/images/DSS/2024/9/6.jpg;\r\n/images/DSS/2024/9/4.jpg', '', '', '', '', 0, '', ''),
(946, '12', 'Оценка навыков физического развития детей с РАС на групповых занятиях', 'Для тестирования базовых физических навыков наших учеников, выявления дефицитов физического развития и оценки взаимодействия с окружающими мы разработали критерии, каждый из которых позволяет отслеживать динамику развития навыков ребенка.\r\n', '<p>Для тестирования базовых физических навыков наших учеников, выявления дефицитов физического развития и оценки взаимодействия с окружающими мы разработали критерии, каждый из которых позволяет отслеживать динамику развития навыков ребенка.</p>\r\n<p>&#128309; ФИЗИЧЕСКОЕ РАЗВИТИЕ</p>\r\n<p>&#128313; Телесное восприятие<br>\r\n&#128313; Положение тела в пространстве<br>\r\n&#128313; Моторная координация<br>\r\n&#128313; Функциональное состояние мышечное системе</p>\r\n<p>&#128993; РАБОТА В ГРУППЕ ИЗ 2-3 СВЕРСТНИКОВ</p>\r\n<p>оценка от 0 до 5 баллов, где:</p>\r\n<p>0️⃣ - не подходит или не заходит в зал, истерики, не желательное поведение<br>\r\n1️⃣ - истерики нет, но он все ровно сторониться и не подходит к преподаватели или к группе детей<br>\r\n2️⃣ - ребенок находиться в одном зале с преподавателем и группой детей<br>\r\n3️⃣ - находиться с ребенком и преподавателем в одной комнате (зале) и выполняет одну или несколько инструкций<br>\r\n4️⃣ - выполняет инструкции и взаимодействует с детьми<br>\r\n5️⃣ - взаимодействует с инструктором и детьми, полностью вовлечен в занятие.\r\n</p>\r\n<p>Занятия по адаптивной физкультуре необходимо начинать с того, что ребенку уже знакомо, и что он умеет выполнять: ходить по степам, пролазить под дугами и т.д.\r\nНовый материал необходимо вводить постепенно, чтобы не вызвать негативные реакции на занятиях или полный отказ от них.\r\nВажно учитывать особенности ребенка, его интересы и уровень развития.</p>\r\n<p>В занятия необходимо включить:</p>\r\n<p>&#9989; Упражнения на коррекцию и развитие общей и мелкой моторики, активизацию межполушарного взаимодействия (пальчиковые игры, игры с различными фактурными поверхностями).</p>\r\n<p>&#9989; Упражнения, включающие лазания, залезания, перелезания, подлезания, ползания различными способами (можно использовать различные спортивные модули, комплексы, либо выстроить полосу препятствий из подручных материалов с учетом техники безопасности).</p>\r\n<p>&#9989; Упражнения на освоение телесного и окружающего пространства (задания можно выполнять в различных исходных положениях).</p>\r\n<p>&#9989; При необходимости включить в занятие упражнения на снятие и устранение навязчивых движений.</p>\r\n<p>&#9989; Также мы используем массаж при необходимости, например, при гипертонусе.</p>\r\n<p>Структурируемые занятия спортом, как и другие занятия в специально созданных условиях, помогают сформировать \"умение учиться\".</p>\r\n<p>&#127919; Слабая мотивация к обучению и сотрудничеству – это «больное» место ребенка с аутизмом.\r\nНа занятиях мы воссоздаем мотивационные условия и поощряем усилия ребенка заниматься спортом и следовать правилам игры.</p>\r\n<p>Наш проект «Расскажи о спорте» стал победителем конкурса Благотворительного фонда Владимира Потанина в рамках программы «Сила спорта».\r\n</p>\r\n<p><a href=\"/news/detail.php?id=895\" target=\"_blank\">Подробнее о проекте</a></p>\r\n', '/images/AFK/2024/1/1.jpg', '2024-05-22', '/images/AFK/2024/1/2.jpg;\r\n/images/AFK/2024/1/3.jpg;\r\n/images/AFK/2024/1/4.jpg;\r\n/images/AFK/2024/1/5.jpg;\r\n/images/AFK/2024/1/6.jpg', '', '', 'Новости', '/news.php', 0, '', '16'),
(947, '4', 'Как оценить успехи ребенка с РАС от занятий в группе сверстников?', 'Когда мы имеем дело с ребятами с расстройствами коммуникации, их успехи в социальных навыках могут быть неочевидными для большинства людей, но очень важными для конкретного ребенка.', '<p>Когда мы имеем дело с ребятами с расстройствами коммуникации, их успехи в социальных навыках могут быть неочевидными для большинства людей, но очень важными для конкретного ребенка.</p>\r\n<p>Так, рассчитывая на то, что ребенок на коммуникативных занятиях сразу начнет обращаться к сверстникам с просьбами и соблюдать правила поведения в игре, мы можем упустить из виду социально-важные поднавыки, из-за которых не сможем сдвинуться с места.</p>\r\n<p> Один из диагностических инструментов, которые мы используем в практике обучения ребят с РАС, в том числе, социальному поведению, игре и навыкам поведения в группе - VB-MAPP.</p>\r\n<p>Из таких неочевидных поднавыков мы можем выделить, например, умение играть в обществе других детей, при этом игра может быть параллельной.</p>\r\n<p>Если ребенок в большинстве случаев пытается уединиться или вовсе уходит с проявлением агрессивного поведения - стоит поработать именно над мотивационными условиями нахождения в группе, не предъявляя ребенку сложных для него требований.\r\nПосмотреть в компании любимый мультик, полакомиться вкусняшками за одним столом с другими ребятами или поиграть вместе в сенсорные игры. Вероятнее, с таких приятных для ученика ситуаций нужное поведение закрепится.\r\n</p>\r\n<p>Еще один из важных навыков, на которых строится обучение в группе - спонтанная имитация за сверстниками.\r\n</p>\r\n<p>Повторяет ли ребенок без подсказок взрослого функциональное поведение других детей. Может ли ребенок пойти за всеми в класс или начать рисовать, видя, что дети взялись за бумагу и карандаши?</p>\r\n<p>Если спонтанного повторения за сверстниками нет, нужно проверить, умеет ли ребенок вообще повторять моторное поведение за взрослыми, по просьбе.\r\nНу и работать с имитацией, сначала воссоздавая мотивационные условия, плавно переходя к полезной деятельности.</p>\r\n<p>Эффективный пример проработки такого навыка, когда вы просите одного ученика сесть за парту и начинаете делать что-нибудь интересное или вкусненькое. Другой ребенок видит, что происходит что-то классное, присоединяется к вам без каких-либо инструкций. Повторяя процедуру несколько раз, можно обратить внимание, что ваши ученики становятся более внимательными и наблюдательны, проявляя интерес к деятельности друг друга.\r\n</p>\r\n<p>Тестирование имеющихся навыков дает более четкую картину умений учеников и помогает более структурированно ставить учебные цели для детей.</p>\r\n<p>Проект \"Коммуникативные группы для детей с РАС\" реализуется при поддержке Благотворительного фонда «Вклад в будущее».\r\n</p>\r\n\r\n', '/images/ABA/2024/1/1.jpg', '2024-05-22', '/images/grants/VK_VB.jpg;\r\n/images/ABA/2024/1/2.jpg;\r\n/images/ABA/2024/1/3.jpg;\r\n/images/ABA/2024/1/4.jpg;\r\n/images/ABA/2024/1/5.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', '21'),
(948, '13', 'Интервью для журнала \"Аутизм - это\"', 'В научно-популярном журнале \"Аутизм -  это\" вышла статья - интервью Екатерины Родыгиной, руководителя нашего центра, мамы двух особенных мальчишек с РАС. Беседа о том, как огромное желание помочь своему ребёнку может открыть суперспособности родителя и изменить ситуацию в целом регионе.', '', '/images/smi/42.jpg', '2024-05-24', '', '', 'https://autismjournal.help/articles/ekaterina-rodygina-ya-vyskochila-iz-palaty-spolzla-po-stene-zaplakala-i-poklyalas-chto-nikogda-ne-otkazhus-ot-svoego-rebenka?clckid=470126f7', '', '', 1, '', ''),
(949, '12', 'Хоккей для ребят с РАС', 'Педагоги «АПЦ РАСсвет» ездили в Липецк, в ЛРОО «ВыРАСтите мир. Аутизм в Липецке», чтобы перенять уникальный опыт специального хоккея для ребят с РАС. О поездке рассказывает руководитель нашего направления АФК, Дарья Бабкина.', '<p>Педагоги «АПЦ РАСсвет» ездили в Липецк, в ЛРОО «ВыРАСтите мир. Аутизм в Липецке», чтобы перенять уникальный опыт специального хоккея для ребят с РАС.</p>\r\n<p>О поездке рассказывает руководитель нашего направления АФК, Дарья Бабкина:</p>\r\n<p>«Нам удалось посетить настоящий хоккейный матч!\r\n10 игроков - 10 ребят с РАС на настоящей ледовой площадке, на коньках и в профессиональном хоккейном обмундировании, каждый в сопровождении родителей! Игра занимает последние 20 минут от часового занятия.\r\n</p>\r\n<p>Ребята делятся на мини-команды и оттачивают приобретенные навыки: устанавливают равновесие, ведут шайбу, целятся в ворота, тренируют удары клюшкой друг другу!</p>\r\n<p>Всему этому ребятишки учились с нуля: привыкали ко льду, учились кататься на коньках, знакомились со спортивным инвентарем и техникой работы с ним. Даже к защитной форме дети привыкали. Сначала экипировку собирали родители своими силами, но сейчас у детей настоящая профессиональная защита, благодаря реализации проекта их НКО. Так же встать на коньки пришлось учиться и родителям.\r\n</p>\r\n<p>Помимо тренировок на льду, ученики, как настоящие хоккеисты, тренируются в спортивном зале, наращивают силу и мощь!</p>\r\n<p>Удивительно, как находясь на ледовой арене, дети внимательно слушают инструкции тренера, несмотря на большую площадь помещения, шум коньков и клюшек.</p>\r\n<p>Кстати, тренер не имеет образования в прикладном анализе поведения, но уже интуитивно работает с поведением ребят. Родители так же подключены, сложные инструкции адаптируют под своих детей сами!</p>\r\n<p>Командный дух ощущается во всем - до выхода на лед дети в экипировки сидят на лавочке и ожидают тренера, стуча клюшками по полу! Без него выходить на лед запрещено.</p>\r\n<p>Что касается нежелательного поведения, дети могут начать ползать по льду или его лизнуть, но родители с этим справляются».</p>\r\n<p>Удивительно, насколько разнообразны сферы занятий людей с аутизмом. Знакомясь с некоммерческими организациями, разбросанными по всей России, появляется надежда, что все возможно!</p>\r\n<p>Возможно учится в школе, работать в кофейне или в ремесленной мастерской, общаться со сверстниками, найти друзей и даже играть в хоккейной команде!</p>\r\n<p>Опыт «ВыРАСтите мир» показывает, что хоккей не только учит детей новым физическим навыкам, но и помогает им развивать социальные навыки, сотрудничество и командный дух.</p>\r\n<p>Мы надеемся, что и в нашем регионе такие инициативы также будут успешно реализованы, открывая новые возможности для детей с РАС.</p>\r\n<p>Наш проект «Расскажи о спорте» стал победителем конкурса Благотворительного фонда Владимира Потанина в рамках программы «Сила спорта».\r\n</p>\r\n<p><a href=\"/news/detail.php?id=895\" target=\"_blank\">Подробнее о проекте</a></p>\r\n\r\n', '/images/AFK/2024/2/1.jpg', '2024-05-27', '/images/AFK/2024/2/2.jpg;\r\n/images/AFK/2024/2/3.jpg;\r\n/images/AFK/2024/2/4.jpg;\r\n/images/AFK/2024/2/5.jpg', '', '', 'Новости', '/news.php', 0, '', '16'),
(950, '3', 'Обучающий курс для стоматологов Иркутска', 'Обучение стоматологов Городской детской стоматологической поликлиники в рамках социального проекта адаптированных стоматологических приемов для детей с РАС \"Без паники! Идем к стоматологу\" происходит в несколько этапов.', '<p>Обучение стоматологов Городской детской стоматологической поликлиники в рамках социального проекта адаптированных стоматологических приемов для детей с РАС \"Без паники!Идем к стоматологу\" происходит в несколько этапов: вводный вебинар, онлайн-курс по основам установления сотрудничества и работе с фобиями пациентов с РАС, практические стажировки и стоматологические приемы под руководством поведенческих аналитиков в клинике. </p>\r\n<p>20 марта был проведен первый вводный вебинар по внедрению модели \"Дружелюбная стоматология\" в государственные стоматологические клиники. Своим опытом организации адаптированных стоматологических приемов с иркутскими коллегами делились Мария Лямцева, кандидат медицинских наук, медицинский координатор проекта «Дружелюбная стоматология для детей с РАС» и Юлия Кузнецова, директор московского Фонда “Импульс развития”. </p>\r\n<p>с 15 по 20 мая состоялся выездной семинар партнеров проекта - Марии Лямцевой и Юлии Кузнецовой, а также серия мастер-классов в реальных условиях, где медицинские специалисты Иркутской городской детской стоматологической поликлиники увидели вживую, как методики прикладного анализа поведения работают на практике при оказании стоматологической помощи детям с РАС. Участники познакомились с использованием при проведении стоматологических манипуляций детям с РАС таких подходов, как систематическая десенсибилизация (уменьшение или устранение негативных реакций на определенные стимулы), визуальная поддержка, жетонная система. </p>\r\n<p>Впереди предстоят теоретические и практические мероприятия для стоматологов, а пока \"АПЦ РАСсвет\" готовит наших маленьких пациентов с помощью поведенческих методик спокойно переносить медицинские манипуляции в условиях, близких к стоматологическому кабинету. </p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>', '/images/seminar/2024/6/1.jpg', '2024-05-30', '/images/grants/pgrants_logo.jpg;\r\n/images/seminar/2024/6/1.jpg;\r\n/images/seminar/2024/5/7.jpg;\r\n/images/seminar/2024/6/4.jpg;\r\n/images/seminar/2024/6/5.jpg;\r\n/images/seminar/2024/6/6.jpg;\r\n/images/seminar/2024/6/7.jpg;\r\n/images/seminar/2024/6/3.jpg', '', '', 'Новости', '/news.php', 0, '', '11'),
(951, '18', 'История Саввы', '\"С 2020 года Савва активно участвует во всех мастер-классах, которые проводят педагоги мастерской \"Дом синего слона\". И я не перестаю восхищаться его прогрессом и достижениями.\" - говорит Елизавета, мама Саввы.', '<p>В нашей инклюзивной мастерской мы верим, что каждый ребенок имеет право на развитие своих талантов и навыков. И сегодня хотим поделиться с вами историей Саввы, который посещает нашу мастерскую с самого начала ее работы.\r\n</p>\r\n<p>\"С 2020 года Савва активно участвует во всех мастер-классах, которые проводят педагоги мастерской \"Дом синего слона\". И я не перестаю восхищаться его прогрессом и достижениями.\" - отметила Елизавета, мама Саввы.</p>\r\n\r\n<p>Так же, Елизавета говорит, что за время, которое ее сын ходит в «Дом синего слона» он освоил множество навыков, которые не только помогут ему в изготовлении различных изделий, но и в его повседневной жизни. Он научился делать свечи, мыло, шить на машинке. А самое важное для нее было, что педагог мастерской, Александра Алексеевна, научила Савву правильно работать ножницами. А также дала советы, как маме дома поддержать и закрепить этот навык.</p>\r\n<p>- Особенно я горжусь тем, что Савва научился шить иголкой с ниткой. Сама бы я, наверное, года через 2 только стала пробовать учить сына шить иголкой. А в мастерской его уже в 6 лет этому научили. Также мой сын научился быть терпеливым, внимательным, умеет сосредотачиваться на мелочах. Эти навыки помогают ему не только в рукоделии, но и в учебе, взаимодействии с другими людьми - говорит мамы Саввы.</p>\r\n<p>А когда мы спросили самого мальчика, что ему больше нравится делать в мастерской, то Савва сказал:\r\n\"Ммм, хороший вопрос. В «Доме синего слона» мне нравится заниматься больше, чем в садике. Потому что в садике мне не помогают, а в мастерской помогают. Там я уже делал тарелочки из глины, подушки, шил полотенчики, плед, варил свечки».</p>\r\n<p>«Я благодарна педагогам мастерской \"Дом синего слона\" за их терпение, заботу и вдохновение. Они не просто учат детей рукоделию, они помогают им стать самостоятельными, уверенными в себе и готовыми к жизни.» - мама Саввы.</p>\r\n<p>Давайте вместе создавать мир, в котором каждый ребенок имеет возможность раскрыть свой потенциал и быть счастливым. Ведь вместе мы сильнее!</p>\r\n<p>АНО \"АПЦ РАСсвет\" - участник проекта Ассоциации «Аутизм-Регионы» \"Полезная занятость с применением практик и методов с научно доказанной эффективностью для молодых людей с РАС\" при поддержке Фонда президентских грантов.</p>', '/images/DSS/2024/10/1.jpg', '2024-05-27', '/images/DSS/2024/10/2.jpg;\r\n/images/DSS/2024/10/3.jpg;\r\n/images/DSS/2024/10/4.jpg', '', '', '', '', 0, '', ''),
(952, '19', 'Ресурсный класс в жизни Влада', 'Мама Влада рассказывает о роли ресурсного класса в жизни своего сына: \"Нам очень повезло оказаться в ресурсном классе... За прошедший учебный год я получила очень много ценных консультаций и научилась корректировать поведение сына дома\".', '<p>Мама Влада рассказывает о роли ресурсного класса в жизни своего сына:</p>\r\n<p>«Когда моему сыну исполнилось 7 лет, у него была возможность учиться в обычном классе общеобразовательной школы. Мы решили попробовать и пошли в подготовительный класс.\r\nТам обнаружили, что ребёнку сложно общаться со сверстниками, но очевидно, что он мог учиться (на тот момент он умел читать, знал фигуры, числовой ряд, английский алфавит).</p>\r\n<p>Учительница разводила руками и не понимала, как использовать эти умения, если ребёнок не хочет сотрудничать. Тогда нам предложили выбрать домашнюю форму обучения, но этот \"выход\" очень сомнительный, т.к. детям нужна социализация, общение и игры с ровесниками.\r\n</p>\r\n<p>Нам очень повезло оказаться в первом ресурсном классе в Иркутском районе.\r\nМы прошли дистанционный курс подготовки к школе от АНО \"АПЦ Рассвет\", что позволило Владу уверенно пойти в школу и быстро адаптироваться.</p>\r\n<p>Сегодня Владу 12 лет, он ученик 3-го класса и он любит ходить в школу, ему нравятся уроки физкультуры, ритмики, уроки творчества, бегать и играть на перемене с одноклассниками. У него появилось больше диалогов, вопросов, осмысленной речи.</p>\r\n<p>За прошедший учебный год я получила очень много ценных консультаций и научилась корректировать поведение сына дома - ребенок стал нормально воспринимать ограничение времени проведения с гаджетами, делать уроки, собираться в школу.</p>\r\n<p>У Влада были трудности в заданиях по математике, также проблемное поведение во время уроков. Он боялся звонков и ходить на третий этаж школы.\r\n</p>\r\n<p>Благодаря слаженной работе команды класса и поддержке супервизора Соломатиной Т.В., применялось много разных методов ПАП для работы над академическими навыками, над проблемным поведением.\r\nНа сегодняшний день ребёнок достаточно успешен: применяет подсказки для решения примеров, уменьшилась латентность реакции, Влад стал более включённым в процесс на уроках, без проблем ходит в любые кабинеты школы и даже на третий этаж, на звонки тоже перестал остро реагировать.</p>\r\n<p>Я очень рада быть участником проекта \"Ресурсный класс. Доступная среда\", благодаря которому специалисты ПАП могут полноценно помогать детям с РАС комфортно обучаться в школе, развиваться и быть успешными».</p>\r\n<p>АНО \"АПЦ Рассвет\" - участник проекта «Ресурсный класс. Доступная среда». Благодарим организатора проекта Ассоциацию \"Аутизм-Регионы\".\r\nПроект реализуется при поддержке Фонда президентских грантов и благотворительного фонда \"Абсолют - Помощь\".</p>\r\n\r\n\r\n', '/images/RK/2024/9/1.jpg', '2024-05-29', '/images/RK/2024/9/2.jpg;\r\n/images/RK/2024/9/3.jpg;\r\n/images/RK/2024/9/4.jpg;\r\n/images/RK/2024/9/6.jpg;\r\n/images/RK/2024/9/5.jpg', '', '', 'Новости', '/news.php', 0, '', ''),
(953, '4', '34 навыка социального взаимодействия', 'В последние годы поведенческие специалисты уделяют значительное внимание изучению отдельных видов поведения, участвующих в развитии навыков социального взаимодействия.\r\nНекоторые из этих навыков формируются через наблюдение за поведением других людей, другие требуют хорошо сформированных речевых навыков.', '<p>В последние годы поведенческие специалисты уделяют значительное внимание изучению отдельных видов поведения, участвующих в развитии навыков социального взаимодействия.\r\nНекоторые из этих навыков формируются через наблюдение за поведением других людей, другие требуют хорошо сформированных речевых навыков.</p>\r\n<p>В диагностическом инструменте для оценка базовых речевых и учебных навыков \"ABLLS-R\" выделяется 34 навыка социального взаимодействия.</p>\r\n<p>Раздел \"Социальное взаимодействие\":</p>\r\n<p>1. Может ли ребенок вести себя приемлемо, находясь со сверстниками? 30 мин, 15 мин, 5 мин или нужны постоянные подсказки?</p>\r\n<p>2. Может ли ребенок взять привлекательный предмет как у взрослого, так и у ребенка?\r\n</p>\r\n<p>3. Может ли ребенок адекватно (без плача и попыток отойти) реагировать на нейтральное физическое воздействие (дать пять, держаться за руки)?\r\n</p>\r\n<p>4. Проявляет ли ребенок интерес к поведению сверстников (игры, комментарии)?</p>\r\n<p>5. Может ли ребенок смотреть на другого человека, чтобы вступить с ним в социальное взаимодействие?</p>\r\n<p>6. Может ли ребенок вовлекать других людей в социальное взаимодействие, даже если у них нет привлекательных предметов?\r\n</p>\r\n<p>7. Может ли ребенок смотреть на другого человека в ожидании, приводящего к получению желаемого?</p>\r\n<p>8. Может ли ребенок выполнять простые инструкции?</p>\r\n<p>9. Имитирует поведение сверстников?</p>\r\n<p>10. Может ли ответить на приветствие другого человека?</p>\r\n<p>11. Может ли дать другому человеку физическую подсказку, чтобы помочь?</p>\r\n<p>12. Адекватно ли реагирует, когда сверстник пытается привлечь его к своей игре (играть с игрушкой, на детской площадке)?</p>\r\n<p>13. Разрешает ли ребенок другим детям брать предметы, которые он использует или которые ему принадлежат?</p>\r\n<p>14. Будет ли ребенок искать знакомого человека, если тот ушел?</p>\r\n<p>15. Может ли привлекать внимание других людей социально-приемлемым образом и убеждаться, что на него смотрят?</p>\r\n<p>16. Ребенок может спонтанно назвать стимул в окружающей среде для другого человека или показать на него пальцем?</p>\r\n<p>17. Смотрит в глаза при взаимодействии с другими людьми?</p>\r\n<p>18. Просит предметы у сверстника?\r\n</p>\r\n<p>19. Просит поделиться чем-либо у сверстника?</p>\r\n<p>20. Может ли ребенок предложить сверстнику предмет?</p>\r\n<p>21. Может ли ребенок первый поздороваться с другим человеком?</p>\r\n<p>22. Может ли подойти к сверстникам и присоединиться к их игре?</p>\r\n<p>23. Может ли обратить внимание на то, чем сейчас увлечены его сверстники?</p>\r\n<p>24. Может ли ребенок следовать инструкциям сверстников, чтобы вести себя согласно правилам?</p>\r\n<p>25. Изменяет свое поведение, наблюдая за сверстниками (останавливается, если все дети остановились, может вместе со сверстником нести коробку)?</p>\r\n<p>26. Помогает другим детям принимать участие в общей деятельности (если сверстник не видит картинку в книге, ребенок подсаживается ближе, чтоб тот увидел)?</p>\r\n<p>27. Знает ли ребенок, что любят и не любят другие дети?</p>\r\n<p>28. Обращает ли ребенок внимание других людей на то, что им интересно?</p>\r\n<p>29. Обращает ли ребенок внимание на то, заинтересован ли другой человек во взаимодействии с ним или нет?</p>\r\n<p>30. Передает сообщения от одного человека другому?</p>\r\n<p>31. Ждет паузы, чтобы вмешаться в диалог других людей?</p>\r\n<p>32. Может ли ребенок поддержать разговор с другими людьми?\r\n</p>\r\n<p>33. Может ли ребенок задавать вопросы взрослым и сверстникам, чтобы узнать какую-либо информацию?</p>\r\n<p>34. Может ли ребенок привлечь и удержать внимание другого человека перед тем, как начать говорить с ним, даже если после первой попытки человек не обращает на ребенка внимание?</p>\r\n<p>Ответив на приведенные вопросы, вы можете начать работать над дефицитами навыков, нужных конкретно вашему ребенку.</p>\r\n<p>Напоминаем, что \"социальное взаимодействие\", это значимая область навыков, которую нужно развивать параллельно с другими умениями самообслуживания, игры, академических навыков и моторного развития.\r\n</p>\r\n<p>Проект \"Коммуникативные группы для детей с РАС\" реализуется при поддержке Благотворительного фонда «Вклад в будущее».\r\n</p>\r\n\r\n', '/images/ABA/2024/17.jpg', '2024-05-30', '/images/grants/VK_VB.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', '21'),
(954, '4', 'Как научить ребенка с аутизмом самостоятельно надевать одежду?', 'Для детей с аутизмом развитие навыков самообслуживания может быть сложным процессом, но это не значит, что мы не можем помочь им стать более самостоятельными. Надевание футболки - один из важных навыков, который поможет ребенку чувствовать себя более уверенным.', '<p>Для детей с аутизмом развитие навыков самообслуживания может быть сложным процессом, но это не значит, что мы не можем помочь им стать более самостоятельными. Надевание футболки - один из важных навыков, который поможет ребенку чувствовать себя более уверенным.</p>\r\n<p>Самостоятельное переодевание - был один из запросов мамы Лизы, которая сейчас проходит интенсив в нашем центре.\r\n</p>\r\n<p>Мы провели комплексную работу над развитием этого навыка и малышка Елизавета научилась надевать на себя футболку.</p>\r\n<p>5 советов, как в домашних условиях научить ребенка с аутизмом надевать одежду:</p>\r\n<p>1. Установите регулярную практику. Создайте ежедневное расписание на тренировку надевания футболки. Начните с коротких сессий и постепенно увеличивайте время, чтобы дать ребенку возможность приспособиться.\r\n</p>\r\n<p>2. Используйте визуальные подсказки. Изготовьте картинки, которые покажут последовательность действий при надевании футболки. Это поможет ребенку лучше понять процесс и запомнить последовательность действий.\r\n</p>\r\n<p>3. Играйте вместе. Создайте игровую ситуацию, где ребенок будет надевать футболку на куклу или мягкую игрушку. Это поможет ему научиться правильной технике и развить моторику рук.</p>\r\n<p>4. Мотивируйте. Не забывайте похвалить ребенка за каждый прогресс. Это поможет ему почувствовать себя увереннее в своих силах и стимулирует дальнейшее развитие навыка.</p>\r\n<p>5. Будьте терпеливыми. Развитие навыка самообслуживания - длительный процесс, требующий терпения и постоянства. Не сдавайтесь, даже если прогресс будет медленным. Каждый шаг вперед - это достижение!\r\n</p>\r\n<p>Проект реализуется при поддержке Благотворительного фонда «Вклад в будущее».\r\n</p>\r\n<p><a href=\"/news/detail.php?id=938\" target=\"_blank\">Подробнее о проекте</a></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456241023&hash=eb219ab8e0e38622\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>\r\n', '/images/ABA/2024/18.jpg', '2024-05-31', '/images/grants/VK_VB.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', '21'),
(955, '13', 'О выставке регионального форума \"Сообщество\" в СМИ', 'Об участии \"Рассвета\" в выставке регионального форума \"Сообщество\" и презентации проекта \"Давай дружить\" при поддержке \"Губернского собрания общественности Иркутской области\" в сюжете программы \"Вести-Иркутск\". ', '', '/images/smi/41.jpg', '2024-06-01', '', '', 'https://vestiirk.ru/news/idei-na-blago-strany-i-regiona-luchshie-sotsialnye-praktiki-irkutskoi-oblasti-predstavili-na-vystavke-regionalnogo-foruma-soobshchestvo/', '', '', 1, '', '14'),
(956, '18', 'Как преодолеть неприязнь к грязным рукам', 'Представьте, что у вашего ребенка есть особая неприязнь к грязным рукам. Это может быть вызовом в повседневной жизни, особенно при выполнении различных заданий. Но мы нашли решение!', '<p>Представьте, что у вашего ребенка есть особая неприязнь к грязным рукам. Это может быть вызовом в повседневной жизни, особенно при выполнении различных заданий. Но мы нашли решение!\r\n</p>\r\n<p>Мы предлагаем нашим ребятам работать в резиновых перчатках. Это простой, но эффективный способ сохранить ручки чистыми и улучшить сотрудничество с нами.\r\n</p>\r\n<p>В нашей инклюзивной мастерской мы сегодня с Ваней сделали замечательную тарелочку. Благодаря перчаткам, Ваня был спокоен и не проявлял нежелательного поведения. Мы смогли сосредоточиться на творчестве и сделали прекрасную работу вместе!</p>\r\n<p>Это пример того, как маленькие изменения в подходе могут сделать большую разницу в жизни детей с аутизмом. Мы стремимся создать комфортное и вдохновляющее пространство, где каждый ребенок может раскрыть свой потенциал.\r\n</p>\r\n<p>Проект \"Давай дружить\" реализуется при поддержке «Губернского собрания общественности Иркутской области»</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239450&hash=aceec212b4659e54\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>', '/images/DSS/2024/15.jpg', '2024-06-03', '/images/grants/GS.png', '', '', '', '', 0, '', '14'),
(957, '3,12', 'Практические навыки для инклюзивного образования от \"РАСсвета\"', 'С 3 по 5 июня в стенах нашего центра проходила бесплатная практическая стажировка \"Методические основы работы с детьми с интеллектуальными расстройствами, в том числе с расстройством аутистического спектра в инклюзивном образовании\".', '<p>С 3 по 5 июня в стенах нашего центра проходила бесплатная практическая стажировка \"Методические основы работы с детьми с интеллектуальными расстройствами, в том числе с расстройством аутистического спектра в инклюзивном образовании\".\r\n</p>\r\n<p>В стажировке приняли участия учителя физкультуры и тьюторы ресурсных классов г. Иркутска ( МОУ ИРМО «СОШ п. Молодёжный», Школа №17 г.Иркутск, Школа №8 им. Д. Г. Сергеева г.Иркутска, МАОУ г.Иркутска ОК \"Лесной\"), учитель начальных классов и тьютор г. Чита и коллеги из г. Волжск «Ребёнок с будущим».</p>\r\n<p>Тренеры направления адаптивной физической культуры \"РАСсвета\" Дарья Бабкина и Валерий Клоков поделились своим опытом, предоставив участникам практические инструменты и техники для работы с детьми с РАС:</p>\r\n<p>&#9989; Короткие и понятные инструкции<br>\r\n&#9989; Визуальные подсказки<br>\r\n&#9989; Мотивационные условия на занятиях<br>\r\n&#9989; Методы работы с невербальными детьми<br>\r\n&#9989; Индивидуальный подход к каждому ученику.</p>\r\n<p>Участники получили практические навыки по адаптации уроков физкультуры для детей с РАС, освоили техники работы с невербальными детьми и детьми, кто испытывает сенсорный перегруз и быстро утомляется, чтобы создавать эффективную среду для обучения в общеобразовательной школе на уроках физкультуры.</p>\r\n<p>Чтобы поведенческие методики работали, важно их применять корректно, поэтому после каникул команда \"РАСсвета\" проведет практические стажировки непосредственно в стенах школ, чтобы учителя физкультуры могли адаптировать программу занятий для своих конкретных учеников с РАС.</p>\r\n<p>Проект «Расскажи о спорте» стал победителем конкурса Благотворительного фонда Владимира Потанина в рамках программы «Сила спорта».\r\n</p>\r\n<p><a href=\"/news/detail.php?id=895\" target=\"_blank\">Подробнее о проекте</a></p>\r\n', '/images/AFK/2024/3/1.jpg', '2024-06-06', '/images/AFK/2024/3/2.jpg;\r\n/images/AFK/2024/3/3.jpg;\r\n/images/AFK/2024/3/4.jpg;\r\n/images/AFK/2024/3/5.jpg;\r\n/images/AFK/2024/3/7.jpg;\r\n/images/AFK/2024/3/8.jpg;\r\n/images/AFK/2024/3/6.jpg', '', '', 'Новости', '/news.php', 0, '', '16'),
(958, '13', 'История, в которой каждый шаг вперед ― это общая победа', '\"История Екатерины и её сына Игоря ― пример того, как настойчивость и любовь к своему ребёнку могут изменить жизнь других семей, где растут дети с похожими диагнозами.\" История председателя АНО \"АПЦ Рассвет\" и её семьи на сайте Благотворительного фонда Сбербанка \"Вклад в будущее\"', '', '/images/smi/40.jpg', '2024-06-10', '', '', 'https://vbudushee.ru/about/news/istoriya-v-kotoroy-kazhdyy-shag-vpered-eto-obshchaya-pobeda/', '', '', 1, '', ''),
(959, '4', 'Подготовка к посещению стоматолога', 'Наши бесплатные стоматологические занятия в рамках социального проекта «Без паники! Идем к стоматологу» идут полным ходом и иркутские дети осваивают такие жизненно-важные навыки, как спокойный поход ко врачу!', '<p>Наши бесплатные стоматологические занятия в рамках социального проекта «Без паники! Идем к стоматологу» идут полным ходом и иркутские дети осваивают такие жизненно-важные навыки, как спокойный поход ко врачу!</p>\r\n<p>Так же на занятиях присутствуют стоматологи Городской детской стоматологической поликлиники и изучают поведенческие методы работы с фобиями и установления сотрудничества с детьми с расстройствами аутистического спектра, чтобы особые пациенты могли посещать адаптированные стоматологические приемы в государственной поликлинике!</p>\r\n<p>Егор посетил 3 часовых занятия вместе с мамой. Наш педагог на практике показал, как работать со страхами Егора и поощрять его старания за сотрудничество с врачом, каждый раз улучшая его поведение в кресле!\r\n</p>\r\n<p>С помощью систематической подготовки можно добиться отличных результатов и с невербальными ребятами.\r\n</p>\r\n<p>Например, ребенок сможет беспрепятственно позволять совершить профилактический осмотр в стоматологии, чтобы тщательно ухаживать за зубками и не переживать из-за зубных болезней!</p>\r\n<p>Часто деткам требуется много времени на адаптацию, не каждый ребенок сразу сможет сесть в кресло, поэтому важно провести поведенческое вмешательство заранее и подготовить маленького пациента к встрече со стоматологом или любым другим врачом, когда еще нет острой боли.</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456241050&hash=85c14cd41128bf84\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>\r\n', '/images/ABA/2024/19.jpg', '2024-06-12', '', '', '', '', '', 0, '', '11'),
(960, '4', 'Ребенок с РАС и уход за собой, чему учить?', 'Часто дети с нарушениями коммуникации не могут научиться какому-либо жизненно-важному навыку из естественной среды, поэтому очень важно организовать специальное обучение навыкам самообслуживания.', '<p>Сегодня поговорим про тестирование навыков самообслуживания и выбор актуальных целей для отработки навыков самостоятельности.</p>\r\n<p>Перечень навыков в тестировании ABLLS-R не привязан к возрастным нормам, поэтому если какое-то из перечисленных поведений отсутствует в репертуаре человека, ему можно научиться, поставив четкую адекватную цель.</p>\r\n<p>Часто дети с нарушениями коммуникации не могут научиться какому-либо жизненно-важному навыку из естественной среды: ребенок не умеет повторять за другим человеком, испытывает трудности в моторике или безучастно относится к рутине (самостоятельно есть или ходить писать на горшок просто нет мотивации), поэтому очень важно организовать специальное обучение навыкам самообслуживания.</p>\r\n<p>Вопросы для самопроверки:</p>\r\n<p>Критерий ответа можно выделить на а) – делает полностью самостоятельно и б) - требуется минимальная или максимальная помощь взрослого</p>\r\n<p>Переодевание:</p>\r\n<p>&#128994; Стягивает брюки до колен, затем надевает обратно?<br>\r\n&#128994; Умеет ли снимать и надевать обувь, футболку, рубашки, штаны, носки, верхнюю одежду?<br>\r\n&#128994; Расстегивает и застегивает молнию?<br>\r\n&#128994; \r\nМожет ли самостоятельно пользоваться молнией, пуговицами, кнопками?<br>\r\n&#128994; \r\nПоправляет ли одежду при необходимости?<br>\r\n&#128994; \r\nЗавязывает шнурки?</p>\r\n<p>Прием пищи:</p>\r\n<p>&#128309; Может ли ребенок есть руками?\r\n<br>&#128309; Умеет ли пить из трубочки?\r\n<br>&#128309; Из чашки?\r\n<br>&#128309; Умеет пользоваться ложкой и вилкой?\r\n<br>&#128309; Умеет отделять кусочки еды вилкой?\r\n<br>&#128309; Наливает жидкость в чашку?\r\n<br>&#128309; Умеет ли пользоваться ножом?\r\n<br>&#128309; Достает ли самостоятельно свой контейнер с едой и сервирует ли стол?\r\n<br>&#128309; Может ли ребенок убрать со стола за собой?\r\n<br>&#128309; Может ли ребенок есть аккуратно, не загрязняя стол вокруг тарелки?</p>\r\n<p>Уход за собой:</p>\r\n<p>&#128993; Умеет ли мыть руки, не вытирая их?\r\n<br>&#128993; Умеет вытирать руки?\r\n<br>&#128993; Умеет умываться?\r\n<br>&#128993; Умеет вытирать лицо?\r\n<br>&#128993; Умеет расчесываться?\r\n<br>&#128993; Чистит ли зубы с помощью щетки и пасты?\r\n<br>&#128993; Умеет сморкаться, если необходимо?</p>\r\n<p>Туалет:</p>\r\n<p>&#128992; Пользуется унитазом или горшком для мочеиспускания?\r\n<br>&#128992; Может ли ребенок оставаться сухим, если его регулярно высаживают на горшок или унитаз?\r\n<br>&#128992; Может ли ребенок самостоятельно пользоваться знакомым туалетом для мочеиспускания?\r\n<br>&#128992; Просится в туалет, если нужно в знакомой местности и в общественном месте?\r\n<br>&#128992; Вытирается ли ребенок после мочеиспускания (для девочек)?\r\n<br>&#128992; Может ли испражняется в унитаз или горшок?\r\n<br>&#128992; Остается чистым (не испражняется в штаны), если его регулярно высаживать на горшок или туалет?\r\n<br>&#128992; Может ли ребенок самостоятельно вытираться после испражнения?\r\n<br>&#128992; Самостоятельно пользуется знакомым туалетом для испражнения?\r\n<br>&#128992; Может ли ребенок самостоятельно ходить в туалет без помощи взрослого?</p>\r\n<p>Проект «Я могу: социализация и обучение жизненно-важным навыкам детей с РАС» реализуется с использованием пожертвования Благотворительного фонда «Абсолют-Помощь».</p>\r\n<p><a href=\"https://rassvet-apc.ru/news/detail.php?id=894\" target=\"_blank\">Подробнее о проекте</a></p>\r\n', '/images/ABA/2024/20.jpg', '2024-06-14', '/images/Absolut/logo.jpg', '', '', 'Новости', '/news.php', 0, '', '12'),
(961, '4', 'Развиваем коммуникацию с помощью микрофона', 'Будет очевидным говорить про то, что, например, развить речь ребенка с помощью микрофона можно тем, чтобы просто в него… говорить. Но если посмотреть на игрушки под другим углом. Микрофон (или любой другой предмет) как способ развития мотивационной сферы ребенка.', '<p>Будет очевидным говорить про то, что, например, развить речь ребенка с помощью микрофона можно тем, чтобы просто в него… говорить. Но если посмотреть на игрушки под другим углом. Микрофон (или любой другой предмет) как способ развития мотивационной сферы ребенка.\r\n</p>\r\n<p>Мы провели комплексную работу над развитием этого навыка и малышка Елизавета научилась надевать на себя футболку.</p>\r\n<p>1️⃣ Если у ребенка много самостимулятивного поведения, нужно внимательно изучить, какие органы восприятия стимулирует ребенок и какой предмет мы можем использовать, чтобы насытить его сенсорные потребности.</p>\r\n<p>2️⃣ Знакомим ребенка с этим предметом, всячески заинтересовывая его им. Если мотивационный стимул подобран в точку, ребенок выхватит его из ваших рук и будет не хотеть вам его отдавать.</p>\r\n<p>3️⃣ Все индивидуально. Ребенку, который вечно копался в своих волосах и не замечал ничего вокруг, зашел вибро-массажер. Илюша, который обожает голоса из техники, влюбился в микрофон.</p>\r\n<p>4️⃣ Отлично, игрушка подобрана и ребенок активно ей пользуется без вашего участия!</p>\r\n<p>Теперь список навыков, которые можно прокачать на мотивации ребенка от это самой игрушки:</p>\r\n<p>&#9989; Умение прервать мотивационную деятельность без демонстрации нежелательного поведения\r\n<br>&#9989; Формирование указательного жеста в качестве просьбы\r\n<br>&#9989; Обучение просьбе словом (либо же звуком, жестом или карточкой)\r\n<br>&#9989; Если ребенок не обращает внимание на предметы вокруг него, с помощью любимой игрушки можно учиться смотреть и замечать\r\n<br>&#9989; Изучение контекстных инструкций (возьми, положи, дай, включи, выключи и т.д.)\r\n<br>&#9989; Умение следовать визуальному расписанию (если ребенок научится визуально отслеживать, что он сможет петь в свой любимый микрофон только после обеда и чистки зубов, это наверняка облегчит жизнь всей семье)\r\n<br>&#9989; Ребенок будет готов провести время, занимаясь полезной деятельностью, без микрофона (если ребенку надо 24/7 играть в игрушку, мы можем использовать ее в качестве позитивных мотивационных условий к тому, чтобы, например, заниматься за партой с учителем).</p>\r\n<p>Проект реализуется при поддержке Благотворительного фонда «Вклад в будущее».\r\n</p>\r\n<p><a href=\"/news/detail.php?id=938\" target=\"_blank\">Подробнее о проекте</a></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456241070&hash=a508983698d6c167\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>\r\n', '/images/ABA/2024/21-1.jpg', '2024-06-20', '/images/grants/VK_VB.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', '21');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(962, '18', 'Творческий потенциал у детей с аутизмом', 'Многие из нас знают, что аутизм может влиять на социальные навыки и коммуникацию, но мало кто задумывается о том, что у этих детей может быть огромный творческий потенциал, который нужно развивать!', '<p>Многие из нас знают, что аутизм может влиять на социальные навыки и коммуникацию, но мало кто задумывается о том, что у этих детей может быть огромный творческий потенциал, который нужно развивать!</p>\r\n<p>Исследования показывают, что многие дети с аутизмом обладают особыми талантами в области искусства. Они могут быть творческими живописцами, музыкантами, писателями и даже актерами! Творчество помогает им выразить свои эмоции и чувства, а также развивает их воображение и креативность.</p>\r\n<p>Однако, чтобы раскрыть творческий потенциал у детей с аутизмом, им нужна наша поддержка и помощь. Важно создавать безопасную и поддерживающую среду, где они могут свободно выражать себя и экспериментировать. Родители и педагоги могут помочь развить интерес к искусству, предлагая разнообразные материалы и творческие задания.</p>\r\n<p>Кроме того, не забывайте о значимости индивидуального подхода. Каждый ребенок с аутизмом уникален, и их творческий потенциал может проявляться по-разному. Поддерживайте их интересы и помогайте им открыть и развить свои таланты в том направлении, которое им нравится.\r\n</p>\r\n<p>На этой неделе мы решили попробовать необычное и креативное занятие - рисование на футболках!</p>\r\n<p>И, честно говоря, все остались в полном восторге. Ведь это так здорово - создавать уникальный дизайн прямо на своей одежде! Какая настоящая возможность выразить себя и показать свою творческую натуру. А ещё это интереснее, чем просто рисовать на обычных листках бумаги.</p>', '/images/DSS/2024/11/1.jpg', '2024-06-20', '/images/DSS/2024/11/2.jpg;\r\n/images/DSS/2024/11/3.jpg;\r\n/images/DSS/2024/11/4.jpg;\r\n/images/DSS/2024/11/5.jpg;\r\n/images/DSS/2024/11/6.jpg\r\n', '', '', '', '', 0, '', ''),
(963, '16', 'Очные супервизии с Анной Устиновской', 'В «РАСсвете» прошли очные супервизии с Анной Устиновской, сертифицированным поведенческим аналитиком и супервизором (Certified Behavior Analyst and Supervisor - CBA-S). Анна прилетела к нам на 3 дня из Казани с тренингом и консультациями по работе с детьми.', '<p>В «РАСсвете» прошли очные супервизии с Анной Устиновской, сертифицированным поведенческим аналитиком и супервизором (Certified Behavior Analyst and Supervisor - CBA-S).</p>\r\n<p>Анна прилетела к нам на 3 дня из Казани с тренингом и консультациями по работе с детьми.</p>\r\n<p>На тренинге мы обсуждали особенности введения жестового языка невербальным ребятам, где достигаются результаты не только в навыках просьб, но и в понимании речи и комментировании окружающего. Анна рассказывала про ребенка, который начал говорить в 11 лет, активно используя жесты на протяжении 4 лет терапии.</p>\r\n<p>Так же мы обсудили трудности введения в повседневную речь местоимений и какие стратегии можно применить, чтоб добиться стойких результатов в их использовании.</p>\r\n<p>Также мы поделились с Анной успехами проекта «Без паники! Идем к стоматологу», где мы проводим адаптивные занятия по подготовке детей с проблемным поведением при посещении врача.</p>\r\n<p></p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>', '/images/news/2024/4/1.jpg', '2024-06-21', '/images/grants/pgrants_logo.jpg;\r\n/images/news/2024/4/2.jpg;\r\n/images/news/2024/4/3.jpg;\r\n/images/news/2024/4/4.jpg;\r\n/images/news/2024/4/5.jpg', '', '', '', '', 0, '', '11'),
(964, '2', 'Экскурсия на детскую железную дорогу', 'На прошлой неделе мы отправились вместе с ребятами на детскую железную дорогу. Поездка на детской железной дороге стала настоящим приключением для нас всех. Дети чувствовали себя комфортно и безопасно.\r\n', '<p>Прошлая неделя у нас была наполнена необычными приключениями! Мы отправились вместе с ребятами на детскую железную дорогу.</p>\r\n<p>Все началось с радостного ожидания и волнения. Мы собрались вместе на станции, и наши маленькие путешественники уже были полны энтузиазма. Все они были готовы к новым впечатлениям и приключениям.\r\n</p>\r\n<p>Когда поезд подошел к платформе, дети с нетерпением заняли свои места. Их глаза светились от счастья, когда поезд тронулся и начал движение. Весь путь мы наслаждались живописными пейзажами, которые открывались перед нашими глазами.</p>\r\n<p>Поездка на детской железной дороге стала настоящим приключением для нас всех. Дети чувствовали себя комфортно и безопасно.</p>\r\n<p>Наши маленькие пассажиры смеялись, восхищались и делились своими эмоциями. Мы с удовольствием наблюдали за ними, видя, как они открывают для себя новый мир железных дорог.</p>\r\n<p>Наша поездка закончилась, но воспоминания о ней останутся с нами надолго. Мы сделали небольшое видео, чтобы поделиться с вами нашими впечатлениями.</p>\r\n\r\n<p>Проект \"Давай дружить\" реализуется при поддержке «Губернского собрания общественности Иркутской области»</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239481&hash=a7ad1d2cd331285a\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/dosug/2024/1.jpg', '2024-06-24', '/images/grants/GS.png', '', '', '', '', 0, '', '14'),
(965, '4', 'Проблемное поведение в кабинете стоматолога', 'Частые поведенческие проблемы на приеме у стоматолога: страх перед врачом, боязнь инструментов, сенсорная чувствительность, трудности с коммуникацией. Анализируем поведение и ищем пути решения.', '<p>Частые поведенческие проблемы на приеме у стоматолога:</p>\r\n<p>&#128546; Страх перед стоматологом. <br> Ребенок может плакать, кричать, сопротивляться, избегать любого контакта с врачом.</p>\r\n<p>&#128551; Боязнь инструментов. <br>\r\nРебенок может испытывать дискомфорт от стоматологических инструментов: звуков, ощущений или просто их вида.</p>\r\n<p>&#128567; Сенсорная чувствительность.  <br>Ребенок может реагировать на яркий свет, громкие звуки, холодную воду, прикосновения.</p>\r\n<p>&#129296; Трудности с коммуникацией.<br>\r\nРебенок может не понимать инструкции и комментарии взрослых, не отвечать на вопросы, испытывать трудности с выражением своих чувств.</p>\r\n<p>Анализируем поведение:\r\n</p>\r\n<p>&#128993; Предыдущий опыт.<br>\r\nБыл ли у ребенка негативный опыт с стоматологом или медицинскими процедурами и нужно ли нам вновь восстанавливать доверие и сотрудничество с ребенком?</p>\r\n<p>&#128993; Сенсорная чувствительность.<br>\r\nКакие сенсорные стимулы вызывают у ребенка дискомфорт и можем ли мы постепенно сделать их нейтральными к восприятию или адаптировать процедуры под ребенка?</p>\r\n<p>&#128993; Коммуникация.<br>\r\nКак хорошо ребенок понимает и выражает свои чувства? Может ли он спокойно сообщить, что ему страшно или больно?</p>\r\n<p>&#128993; Понимание речи.<br>\r\nНасколько хорошо ребенок понимает речь? Может ли он воспринимать длинные предложения или же с ним нужно потренироваться следовать коротким инструкциям «сядь», «открой рот», «ручки на коленках» и т.д?</p>\r\n<p>&#128993; Визуальная опора.<br>\r\nВ стрессовой ситуации ребенку комфортнее воспринимать речь или визуальные подсказки? Можно ли заранее изучить социальную историю «Я иду к стоматологу» и обсудить с ребенком каждый этап, каждый инструмент?</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456241073&hash=3c6a9ad6a1e0af7d\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>\r\n', '/images/ABA/2024/22.jpg', '2024-06-25', '', '', '', '', '', 0, '', '11'),
(966, '18', 'Летняя ярмарка', 'В эту субботу мы провели невероятно крутую летнюю ярмарку! Было так здорово увидеть всех, кто пришел поддержать наши мастерские. Благодаря вашей поддержке и участию, мы смогли собрать средства, которые пойдут на закупку новых материалов для наших творческих занятий. ', '<p>В эту субботу мы провели невероятно крутую летнюю ярмарку! Было так здорово увидеть наших ребят и их родителей, а также всех, кто пришел поддержать наши мастерские.\r\n</p>\r\n<p>Благодаря вашей поддержке и участию, мы смогли собрать средства, которые пойдут на закупку новых материалов для наших творческих занятий. Это действительно важно для нас, потому что хочется максимально расширить возможности и вдохновить наших ребят на новые творческие идеи. \r\n</p>\r\n<p>Отдельное спасибо организатору Гаражной распродажи, которая предоставила нам такую огромную возможность принять участие в этом мероприятии. Это было настоящее счастье для нас и наших учеников.\r\n</p>\r\n<p>Мы надеемся, что наша ярмарка стала для вас не только возможностью приобрести красивые и оригинальные изделия, но и прекрасной атмосферой, где каждый мог насладиться творчеством и общением. Ведь именно в таких моментах рождаются новые идеи и дружеские связи.\r\n</p>\r\n<p>Спасибо всем, кто принял участие в нашей летней ярмарке! Мы ценим каждого из вас и вашу поддержку.</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239482&hash=837dab1e9cc35292\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2024/16.jpg', '2024-06-25', '', '', '', '', '', 0, '', ''),
(967, '4', 'История Алисы', 'Делимся с вами историей адаптивных занятий по стоматологии с Алисой. Начинали, по-классике, с работы над боязнью медицинского халата и стоматологического кресла.\r\nСейчас же Алиса спокойно позволяет провести во рту процедуру с бормашиной незнакомому для нее (настоящему!) врачу.', '<p>Делимся с вами историей адаптивных занятий по стоматологии с Алисой. Начинали, по-классике, с работы над боязнью медицинского халата и стоматологического кресла.\r\nСейчас же Алиса спокойно позволяет провести во рту процедуру с бормашиной незнакомому для нее (настоящему!) врачу.</p>\r\n<p>Почему удалось добиться хорошего и стойкого результата в столь короткий промежуток времени? Занятия начались 10 июня.</p>\r\n<p>Алиса достаточно давно в АВА-терапии и несмотря на сниженное понимание речи, девочка уверенно ориентируется по жетонной системе, понимает слова «сядь», «жди», «открой рот», «красивые ручки». А главное - она доверяет педагогам. Если ее просят оторваться от ее игр или ритуалов и пройти в кабинет, она последует за взрослым, не проявляя сложного поведения. Если у педагога в руках страшный инструмент, она знает, что ее не скрутят и не заставят терпеть, ей не нужно плакать и убегать.</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456241082&hash=ce9d1f92c84e2edc\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen class=\"social-video\"></iframe>\r\n\r\n</section>\r\n', '/images/ABA/2024/2/1.jpg', '2024-06-26', '/images/ABA/2024/2/2.jpg;\r\n/images/ABA/2024/2/3.jpg;\r\n/images/ABA/2024/2/4.jpg;\r\n/images/ABA/2024/2/5.jpg', '', '', '', '', 0, '', '11'),
(968, '4', 'Расширяем речь ребенка', 'Если ребенок односложно комментирует происходящее и ему сложно освоить такие конструкции, как «девочка танцует», «синий мячик», «холодное мороженое», то стоит потренироваться вот в чем...', '<p>Если ребенок односложно комментирует происходящее и ему сложно освоить такие конструкции, как «девочка танцует», «синий мячик», «холодное мороженое», то стоит потренироваться вот в чем:\r\n</p>\r\n<p>1️⃣ Научится наименовывать 2 существительных на картинках подряд: «девочка-мяч», «кубик-мороженое»</p>\r\n<p>2️⃣ Отучить глаголы и также уметь наименовывать их в паре: «ест», «спит», «рисует», «читает»</p>\r\n<p>3️⃣ Отучить прилагательные и также уметь наименовывать их в паре: цвета, формы\r\n.</p>\r\n<p>4️⃣ Соединить карточки разных частей речи и наименовывать их по очереди, составляя разные конструкции: «синий мяч», «девочка спит», «собака ест»</p>\r\n<p>На практике ребята часто путаются в наименовании по гендерным признакам. Чтобы не снизить мотивацию в обучении, постоянно поправляя ребенка, либо отдельно отучите различать мальчиков, девочек, теть, дядь, либо не используйте картинки с людьми.</p>\r\n<p>На видео показано, как осваивает этот навык наш подопечный Демьян. Как думаете мальчику пригодится двухкомпонентное наименование в жизни?</p>\r\n<p>Проект реализуется при поддержке Благотворительного фонда «Вклад в будущее».\r\n</p>\r\n<p><a href=\"/news/detail.php?id=938\" target=\"_blank\">Подробнее о проекте</a></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456241091&hash=b8c920468ff5fdf6\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>\r\n', '/images/ABA/2024/23.jpg', '2024-06-28', '/images/grants/VK_VB.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', '21'),
(969, '2', 'Прогулка по Ангаре', 'Прошлая неделя оказалась очень насыщенной и интересной! Мы совершили небольшое путешествие на теплоходе \"МОСКВА-67\". Лето - время для приключений, и мы не могли упустить возможность окунуться в атмосферу настоящего путешествия!', '<p>Прошлая неделя оказалась очень насыщенной и интересной! Мы совершили небольшое путешествие на теплоходе \"МОСКВА-67\". Лето - время для приключений, и мы не могли упустить возможность окунуться в атмосферу настоящего путешествия!</p>\r\n<p>Особенностью этого путешествия было то, что оно было инклюзивным. Вместе с ребятами с аутизмом плавали и дети с нормальным развитием. Было замечательно наблюдать за эмоциями и реакциями каждого ребенка. Все они были в полном восторге от этого приключения!\r\n</p>\r\n<p>На протяжении всего путешествия мы наслаждались прекрасными пейзажами реки Ангары. Каждый поворот приносил новые впечатления и красоту природы. Мы фотографировались, делились впечатлениями и просто наслаждались прекрасным летним днем.</p>\r\n<p>Такие путешествия дарят нам возможность не только отдохнуть и насладиться красотой мира, но и понаблюдать за разными людьми и их эмоциями. Инклюзия - это важное понятие, которое помогает нам понять, что каждый человек уникален и имеет право на равные возможности и приключения.</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239488&hash=d80742428f04035c\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/dosug/2024/2.jpg', '2024-07-01', '', '', '', '', '', 0, '', ''),
(970, '18', 'Применение методов ПАП в мастерской', 'В нашей мастерской используются различные методы коррекции поведения детей. Один из них — визуальное расписание, которое помогает формировать навыки выполнения изделий от начала до конца. Также мы применяем поведенческий импульс, когда переходим от простых к сложным навыкам.', '<p>В нашей мастерской используются различные методы коррекции поведения детей. Один из них — визуальное расписание, которое помогает формировать навыки выполнения изделий от начала до конца. Также мы применяем поведенческий импульс, когда переходим от простых к сложным навыкам.\r\n</p>\r\n<p>В зависимости от функции поведения ребёнка, мы используем дифференцированное подкрепление отсутствия (DRO), альтернативного (DRA) или несовместимого поведения (DRI).</p>\r\n\r\n<p>Кроме того, мы используем принцип бутерброда: сначала даём простое мотивационное задание, затем посложнее, а потом снова простое. Важно также применять безошибочное обучение, чтобы ребёнок чувствовал себя успешным и хотел продолжать заниматься и творить.</p>\r\n<p>А с помощью условной дискриминации мы учим детей различать используемые в мастерской предметы и материалы, чтобы ребята понимали нас и учились создавать изделия не механически, а погружаясь в процессы.\r\n</p>\r\n<p>Это лишь некоторые из методов, которые мы используем в нашей мастерской.</p>\r\n<p>АНО \"АПЦ РАСсвет\" - участник проекта Ассоциации «Аутизм-Регионы» \"Полезная занятость с применением практик и методов с научно доказанной эффективностью для молодых людей с РАС\" при поддержке Фонда президентских грантов.</p>', '/images/DSS/2024/12/1.jpg', '2024-06-28', '/images/DSS/2024/12/2.jpg;\r\n/images/DSS/2024/12/3.jpg;\r\n/images/DSS/2024/12/5.jpg;\r\n/images/DSS/2024/12/6.jpg;\r\n/images/DSS/2024/12/4.jpg', '', '', '', '', 0, '', ''),
(971, '4', 'В какие игрушки любят играть дети?', 'Мы выделили несколько типов игрушек, которые больше всего притягивают внимание ребят. Очень важно прислушиваться к предпочтениям и интересам ребенка и создавать для него индивидуальную программу развития.\r\n', '<p>Мы выделили несколько типов игрушек, которые больше всего притягивают внимание ребят.\r\n</p>\r\n<p>Первый тип - это сенсорные игрушки. Для детей с аутизмом особенно важно развивать тактильные ощущения. Мягкие игрушки с различными текстурами, пазлы с выступами и противоположностями, вязаные игрушки с разными узорами - все это помогает детям с аутизмом развивать тактильные навыки и получать удовольствие от прикосновений.</p>\r\n<p>Второй тип - это игрушки, которые развивают воображение. Дети с аутизмом часто имеют проблемы с коммуникацией и социальными навыками, поэтому игры, в которых они могут выражать свои мысли и чувства, особенно важны. Ролевые игры с куклами или животными, конструкторы, которые позволяют создавать свои миры - все это помогает детям с аутизмом развивать воображение и социальные навыки.</p>\r\n<p>Третий тип - это игрушки, которые способствуют развитию моторики и координации движений. Дети с аутизмом часто имеют проблемы с финомоторикой и грубой моторикой, поэтому игры, которые требуют точных движений и координации, очень полезны. Лабиринты, мозаики, конструкторы, пазлы - все это помогает детям с аутизмом развивать моторику и координацию движений.\r\n.</p>\r\n<p>Но самое главное, что нужно помнить - это то, что каждый ребенок с аутизмом уникален, и то, что подходит одному, может не подойти другому. Поэтому очень важно прислушиваться к предпочтениям и интересам ребенка и создавать для него индивидуальную программу развития.</p>\r\n<p>Проект реализуется при поддержке Благотворительного фонда «Вклад в будущее».\r\n</p>\r\n<p><a href=\"/news/detail.php?id=938\" target=\"_blank\">Подробнее о проекте</a></p>', '/images/ABA/2024/24.jpg', '2024-07-01', '/images/grants/VK_VB.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', '21'),
(972, '12', 'Готовь сани летом', 'Подготовка к зимним видам спорта требует особого подхода. Мы заботимся о том, чтобы наши ребята были готовы не только к спортивным соревнованиям, но и к зимнему катанию на лыжах. Для этого мы проводим специальные занятия тренировки и упражнения.', '<p>Подготовка к зимним видам спорта требует особого подхода. Мы заботимся о том, чтобы наши ребята были готовы не только к спортивным соревнованиям, но и к зимнему катанию на лыжах. Для этого мы проводим специальные занятия тренировки и упражнения, которые помогают развивать необходимые навыки и физическую форму.\r\n</p>\r\n<p>Одно из таких упражнений – это подготовка к катанию на лыжах. Оно поможет вашему ребёнку научиться правильно держать равновесие и управлять лыжами. Вы можете посмотреть видео, где показано, как выполнять это упражнение.</p>\r\n<p>Не забывайте, что правильная подготовка к зимним видам спорта – это залог безопасности вашего ребёнка. Поэтому не стоит пренебрегать этим важным этапом подготовки.</p>\r\n<p>Проект «Расскажи о спорте» стал победителем конкурса Благотворительного фонда Владимира Потанина в рамках программы «Сила спорта».\r\n</p>\r\n<p><a href=\"/news/detail.php?id=895\" target=\"_blank\">Подробнее о проекте</a></p>\r\n <section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456241096&hash=71d352112cf673f5\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/AFK/2024/3.jpg', '2024-07-03', '', '', '', 'Новости', '/news.php', 0, '', '16'),
(973, '18', 'Жаркий сезон в нашей мастерской', 'Лето - время отпусков и беззаботных пляжных дней, но наша инклюзивная мастерская не знает слова \"отпуск\"! Даже в самый жаркий сезон, наши участники с радостью приходят и создают удивительные изделия.', '<p>Лето - время отпусков и беззаботных пляжных дней, но наша инклюзивная мастерская не знает слова \"отпуск\"! Даже в самый жаркий сезон, наши участники с радостью приходят и создают удивительные изделия.\r\n</p>\r\n<p>Недавно мы решили вновь заняться созданием кашпо из бетона. Это было настоящее приключение!\r\n</p>\r\n<p>Наши маленькие мастера с большим усердием смешивали ингредиенты, укладывали смесь по формам. Каждое изделие стало уникальным и неповторимым. Но самое главное, что ребята получили огромное удовольствие от процесса творчества.</p>\r\n<p>Мы не просто создаем красивые предметы, мы помогаем детям с аутизмом развивать свои творческие способности, улучшать мелкую моторику и развивать навыки работы в коллективе. В нашей мастерской они чувствуют себя важными и нужными.</p>\r\n<p>Если вы хотите увидеть результаты наших трудов, загляните в наше новое видео. Уверена, что вас ждут не только красивые кашпо, но и улыбки наших ребят. Вместе мы создаем волшебство и делаем мир лучше!</p>\r\n<p>Проект \"Давай дружить\" реализуется при поддержке «Губернского собрания общественности Иркутской области»</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239489&hash=60b95bb2094f121c\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2024/17.jpg', '2024-07-05', '/images/grants/GS.png', '', '', '', '', 0, '', '14'),
(974, '4', 'Как научить ребенка с аутизмом самостоятельно есть ложкой?', 'В прошлом месяце на наш интенсивный курс пришел Ярослав. Ему всего 8 лет, и у него есть особенность - расстройство аутистического спектра. Он не мог самостоятельно есть ложкой, и мы решили помочь ему справиться с этой проблемой.', '<p>В прошлом месяце на наш интенсивный курс пришел Ярослав. Ему всего 8 лет, и у него есть особенность - расстройство аутистического спектра. Он не мог самостоятельно есть ложкой, и мы решили помочь ему справиться с этой проблемой.\r\n</p>\r\n<p>Первым делом мы сосредоточились на формировании пинцетного захвата. Для этого мы использовали тренировку с прищепками и коробкой. Ярослав с радостью участвовал в этой игре, и с каждым занятием его мастерство становилось лучше.</p>\r\n<p>Далее, мы перешли к более сложным заданиям. Мы учили Ярослава перекладывать фасоль из одной емкости в другую, используя ложку. Эта тренировка помогала развивать его навыки координации движений и улучшала его моторику.</p>\r\n<p>Не останавливаясь на достигнутом, мы продолжали занятия. Мы учили Ярослава размешивать чай ложкой. Вначале это было очень сложно для него, но благодаря нашей постоянной поддержке он с каждым разом становился лучше и увереннее.</p>\r\n<p>И вот наконец, наступил день, когда Ярослав показал свои достижения в конце нашего интенсива. Он самостоятельно ел ложкой! Нам было так гордо наблюдать за его успехом, за его прогрессом. Кажется, будущее Ярослава стало ярче и полнее возможностей.</p>\r\n<p>Проект реализуется при поддержке Благотворительного фонда «Вклад в будущее».\r\n</p>\r\n<p><a href=\"/news/detail.php?id=938\" target=\"_blank\">Подробнее о проекте</a></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456241120&hash=ab04267b873faa7b\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>', '/images/ABA/2024/25.jpg', '2024-07-19', '/images/grants/VK_VB.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', '21'),
(975, '13', 'Проект \"Жить\": герои среди нас', 'Екатерина Родыгина, мама двух детей с РАС и руководитель \"Рассвета\", стала героем проекта \"ЖИТЬ\", основанный Игорем Матвиенко. Она поделилась с проектом историей создания НКО, рассказала о деятельности организации и дала советы тем, кто столкнулся с трудностями.', '', '/images/smi/43.jpg', '2024-07-19', '', '', 'https://vk.com/zhit?w=wall-131498541_919069', '', '', 1, '', ''),
(976, '16', 'Наш проект в десятке лучших', 'Социальный проект \"Без паники! Идем к стоматологу\" вошел в 10-ку лучших работ в конкурсе от издательства \"Аргументы и Факты\" и Благотворительного фонда «АиФ. Доброе сердце».\r\n', '<p>Социальный проект \"Без паники! Идем к стоматологу\", в котором педагоги \"Рассвета\" готовят врачей Иркутской городской стоматологической поликлиники к адаптивным приемам детей с РАС, а так же обучают ребят со сложным поведением посещать больницу и спокойно относиться к медицинским манипуляциям, вошел в 10-ку лучших работ в конкурсе от издательства \"Аргументы и Факты\" и Благотворительного фонда «АиФ. Доброе сердце».\r\n</p>\r\n<p>Награждение конкурсантов проходило в Москве, куда наша команда была любезно приглашена!</p>\r\n<p>Благодарим организаторов за возможность поучаствовать в таком масштабном мероприятии, узнать про PR-продвижение от настоящих медиа-профессионалов и рассказать на всю Россию о проблемах, сопровождающих семьи с детьми с аутизмом, и о эффективных способах их решений!</p>\r\n<p>Подробнее про итоги конкурса можно прочитать <a href=\"https://aif.ru/society/people/spisok-luchshih-podvedeny-itogi-konkursa-pr-akselerator-formula-uspeha\" target=\"_blank\">ЗДЕСЬ</a>.</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>', '/images/news/2024/5/1.jpg', '2024-07-29', '/images/grants/pgrants_logo.jpg;\r\n/images/news/2024/5/2.jpg;\r\n/images/news/2024/5/3.jpg', '', '', '', '', 0, '', '11'),
(977, '18', 'Гаражная распродажа', 'В июле наша мастерская приняла участие в 13-ой гаражной распродаже! Это был незабываемый опыт как для нас, так и для наших учеников. Раньше они только создавали удивительные изделия, а сейчас смогли поучаствовать в продаже их собственных творений. ', '<p>В июле наша мастерская приняла участие в 13-ой гаражной распродаже! Это был незабываемый опыт как для нас, так и для наших учеников. Раньше они только создавали удивительные изделия, а сейчас смогли поучаствовать в продаже их собственных творений. Понаблюдать за процессом создания и продажи - это совсем другое чувство!\r\n</p>\r\n<p>Под наблюдением наших опытных сотрудников, ребята смогли продемонстрировать свои умения и таланты широкой публике. И мы гордимся тем, какие умелые ручки у наших учеников! Несмотря на первый опыт, они справились на отлично и получили массу положительных эмоций от процесса.</p>\r\n\r\n<p>Смотрите фотографии - видно, какие улыбки на лицах участников! Это было не только продажа изделий, но и настоящее празднование творчества и умений. Спасибо всем, кто пришел и поддержал нас! Надеемся, что такие мероприятия станут для наших учеников еще одним шагом к развитию и успеху.\r\n</p>\r\n<p>АНО \"АПЦ РАСсвет\" - участник проекта Ассоциации «Аутизм-Регионы» \"Полезная занятость с применением практик и методов с научно доказанной эффективностью для молодых людей с РАС\" при поддержке Фонда президентских грантов.</p>', '/images/DSS/2024/13/1.jpg', '2024-08-13', '/images/DSS/2024/13/2.jpg;\r\n/images/DSS/2024/13/3.jpg;\r\n/images/DSS/2024/13/4.jpg', '', '', '', '', 0, '', ''),
(978, '4', 'Коммуникативная группа и АВА-терапия\r\n', 'Коммуникативная группа - это структурированная среда, где дети с нарушениями коммуникации могут развивать свои социальные и коммуникативные навыки, взаимодействуя с другими детьми и взрослыми.', '<p>Коммуникативная группа - это структурированная среда, где дети с нарушениями коммуникации могут развивать свои социальные и коммуникативные навыки, взаимодействуя с другими детьми и взрослыми.\r\n</p>\r\n<p>АВА-терапия (Прикладной анализ поведения) использует научно доказанные принципы для обучения детей различным навыкам, включая социальные и коммуникативные.</p> \r\n<p>АВА-терапия позволяет не только научить ребенка навыкам, но и понять, почему он ведет себя определенным образом, и как модифицировать это поведение.</p>\r\n<p>Цели, которые можно проработать в коммуникативной группе с использованием принципов АВА-терапии:</p>\r\n<p>1️⃣ Социальные навыки</p>\r\n<p>&#8227; Обучение навыкам социального взаимодействия, например, приветствовать, прощаться, благодарить, извиняться, вести разговор, задавать вопросы, экологично выражать эмоции.<br>\r\n&#8227; Развитие эмоционального интеллекта. Понимать чувства и намерения других людей, предсказывать их поведение.<br>&#8227; Развитие навыков совместной игры. Делиться игрушками, играть по очереди, следовать правилам игры.<br>&#8227; Обучение навыкам сотрудничества. Работать в команде, помогать другим.\r\n<br>&#8227;Развитие навыков разрешения конфликтов. Мирно решать споры, заменять агрессию на более приемлемые способы коммуникации.</p>\r\n<p>2️⃣ Коммуникативные навыки</p>\r\n<p>&#8227; Развитие навыков невербальной коммуникации.\r\n<br>&#8227; Обучение функциональному использованию речи. Просить о помощи, выражать свои потребности.<br>&#8227; Развитие понимания речи, концентрации и внимания.<br>&#8227; Расширение словарного запаса, использование более сложных фраз, умение поддержать беседу.</p>\r\n<p>3️⃣ Сенсорные и моторные навыки</p>\r\n<p>&#8227; Сенсорная интеграция.\r\nПовышение толерантности к изменениям в окружающей среде, шуму, прикосновениям.<br>&#8227; Развитие моторных навыков.<br>&#8227; Обучение навыкам самообслуживания.\r\nОдеваться, кушать, использовать туалет.</p>\r\n<p>4️⃣ Эмоциональная регуляция: </p>\r\n<p>&#8227; Развитие навыков самоконтроля.<br>&#8227; Обучение навыкам управления стрессом.</p>\r\n<p>Важно❗ Цели обучения ребенка в коммуникативной группе должны быть индивидуализированы с учетом потребностей каждого ребенка. Необходимо также учитывать его возраст, уровень развития и предпочтения.</p>\r\n<p>Родители и тераписты должны работать в команде для обеспечения успеха ребенка в группе.</p>\r\n<p>Проект реализуется при поддержке Благотворительного фонда «Вклад в будущее».\r\n</p>\r\n<p><a href=\"/news/detail.php?id=938\" target=\"_blank\">Подробнее о проекте</a></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456241184&hash=cf2d422df8faf1f1\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>', '/images/ABA/2024/3/1.jpg', '2024-08-16', '/images/grants/VK_VB.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', '21'),
(979, '16', 'Важные знания', '\"Рассвет\" принял участие в конференции «Инклюзивное образование: наука и практика», которую проводили ассоциация «Спектр» совместно с МАОУ СОШ № 127 г. Новосибирска в рамках проекта «Важные знания – повышение компетенции педагогов, обучающих детей с РАС» при поддержке Фонда президентских грантов.', '<p>5 сентября проходила конференция «Инклюзивное образование: наука и практика», которую проводили ассоциация «Спектр» совместно с МАОУ СОШ № 127 г. Новосибирска в рамках проекта «Важные знания – повышение компетенции педагогов, обучающих детей с РАС» при поддержке Фонда президентских грантов.</p>\r\n<p>\"Рассвет\" принял участие в конференции не только в качестве слушателей, но и в роли докладчиков.\r\nЕлена Михеева на экспертном уровне поделилась с слушателями темой: «Перечень документов на обучающихся с РАС в общеобразовательном учреждении».</p>\r\n<p>Доклады конференции отвечали критериям научно доказательных подходов в обучении учеников с РАС и большому количеству кейсов конкретных ситуаций: от нармотивно-правовых документов до методик работы с проблемным поведением учеников на уроках.</p>\r\n<p>На конференции Ассоциация \"Спектр\" анонсировала курс по прикладному анализу поведения (ПАП), который смогут бесплатно пройти сотрудники общеобразовательных учреждений.\r\n</p>', '/images/news/2024/6/1.jpg', '2024-09-11', '/images/news/2024/6/2.jpg;\r\n/images/news/2024/6/3.jpg', '', '', 'Новости', '/news.php', 0, '', '0'),
(980, '4', 'История Ромы', 'В практике «Рассвета» довольно часто встречаются истории детей с РАС, которые хорошо взаимодействуют с мамой, но в школе или в детском саду на ребенка жалуются: не слушается, не реагирует на просьбы, не отвечает на вопросы.', '<p>В практике «Рассвета» довольно часто встречаются истории детей с РАС, которые хорошо взаимодействуют с мамой, но в школе или в детском саду на ребенка жалуются: не слушается, не реагирует на просьбы, не отвечает на вопросы.\r\nЧто может чувствовать родитель? Разочарование в педагогах, непонимание. Почему учителя не могут найти подход к ребенку? Как итог – ребенок остается за бортом общеобразовательных организаций и общества сверстников.</p>\r\n<p>8-летнего Рому поддержал «Рассвет», пригласив принять участие в проекте «Я могу», сопровождаемого БФ «Абсолют-помощь».\r\n</p>\r\n<p>Мальчик был одним из тех учеников, кто коммуницировал только с мамой. В большинстве случаев обращенная речь от чужих людей ребенком оставалась проигнорированной, а услышать голос Ромы было настоящей удачей и волей случая.</p>\r\n<p>На занятиях стало понятно, что Роман не переносит свои навыки в естественную среду по отношению к другим людям. Мама учит ребенка говорить, понимать, отвечать и Рома отлично осваивает новое, но эти навыки не тренируются в общении с другими людьми, откуда складывается ложное суждение о возможностях ребенка со стороны общества.</p>\r\n<p>Наши педагоги работали над генерализацией навыков, чтобы Рома мог успешно применять свои умения вне зависимости, находится мама рядом или нет, и отвечать за себя самостоятельно.\r\nПеред школой крайне важно научиться взаимодействовать с другими людьми на доступном ребенку уровне.</p>\r\n<p>Мы активно зарабатывали доверие мальчика и поддерживали все его интересы. Благодаря такому подходу, Рома начал раскрываться и коммуницировать с другими педагогами и детьми.\r\nМама отметила, что помимо того, что Рома стал более самостоятельным, он обзавелся другом.\r\n</p>\r\n<p>С Платоном, будущим одноклассником, Рома предпочитает проводить как можно больше перемен: активные игры и обратная связь от Платона закрепили поведение обращаться к сверстникам!</p>\r\n<p>Также родители Ромы отметили, что сын стал более уверенно говорить. В копилке умений Ромы появились новые обращенные просьбы к другим членам семьи. Расширилась фразовая речь, это не может не радовать!</p>\r\n<p>Проект «Я могу: социализация и обучение жизненно-важным навыкам детей с РАС» реализуется с использованием пожертвования Благотворительного фонда «Абсолют-Помощь».</p>\r\n<p><a href=\"https://rassvet-apc.ru/news/detail.php?id=894\" target=\"_blank\">Подробнее о проекте</a></p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456241155&hash=e6f3e98c842f0c7b\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>                        </section>\r\n', '/images/ABA/2024/26.jpg', '2024-08-08', '/images/Absolut/logo.jpg', '', '', 'Новости', '/news.php', 0, '', '12'),
(981, '12', 'Инклюзивные соревнования', 'Наши инклюзивные соревнования - это не просто спортивное мероприятие, это праздник равных возможностей и дружбы! Ребята с РАС и ребята с нормотипичным развитием объединились в одну команду, чтобы показать, что во главе единство и взаимопонимание.', '<p>Наши инклюзивные соревнования - это не просто спортивное мероприятие, это праздник равных возможностей и дружбы! \r\n</p>\r\n<p>Мы готовились к этому дню полгода, дети посещали индивидуальные и групповые занятия по физкультуре.\r\n</p>\r\n<p>Ребята с РАС и ребята с нормотипичным развитием объединились в одну команду, чтобы показать, что во главе единство и взаимопонимание.</p>\r\n<p>В этот день каждый ребенок смог почувствовать себя частью большой и дружной команды.\r\nВместе ребятишки преодолевали трудности, поддерживали друг друга и радовались каждой победе. Улыбки не сходили с лиц детей, родителей и педагогов!</p>\r\n<p>Спасибо всем, кто был с нами в этот замечательный день! Мы доказали, что вместе мы - сила, и что ничто не помешает нам быть единой и неразрывной командой!</p>\r\n<p></p>\r\n<p></p>\r\n<p>Напомним, что наш проект «Расскажи о спорте» стал победителем конкурса Фонда Потанина «Спорт для всех»</p>\r\n<p><a href=\"/news/detail.php?id=895\" target=\"_blank\">Подробнее о проекте</a></p>\r\n <section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456241196&hash=066375fa2dac29d9\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/AFK/2024/4.jpg', '2024-08-20', '', '', '', 'Новости', '/news.php', 0, '', '16'),
(982, '2', 'Приключенческий день', 'Чтобы закрепить навыки социального взаимодействия и игровые навыки, которым ребята учились на занятиях, ученики отправились на мастер-класс по изготовлению пиццы в кафе, а потом посетили городской парк, где все вместе прошли квест-игру \"Чебурашка\".', '<p>Чтобы закрепить навыки социального взаимодействия и игровые навыки, которым ребята учились на занятиях, вместе со своими семьями ученики отправились на мастер-класс по изготовлению пиццы в кафе, а потом посетили городской парк, где все вместе прошли квест-игру \"Чебурашка\".\r\n</p>\r\n<p>Гордимся поведением всех ребят! Они показали, как могут соблюдать правила поведения в общественных местах, блеснули своей культурностью и сплоченностью.</p>\r\n<p>Каждый ребенок собрал свою пиццу, опираясь на инструкции и подсказки взрослых, а потом за общим столом развернулся настоящий пир!</p>\r\n<p>Поиск клада проходил по настоящей карте сокровищ, где конечной точкой было - огромное дерево, под которым таился... ящик с мороженым и мандаринами!</p>\r\n<p>Дети проявили себя с лучшей стороны!\r\nНавыки, приобретенные в стенах центра, применяются в повседневной жизни и делают ее комфортнее и насыщеннее. Приятная компания подарила уверенность в реализации всех задумок и зарядила позитивом всех наших подопечных!</p>\r\n<p>Проект реализуется при поддержке Благотворительного фонда «Вклад в будущее».\r\n</p>\r\n<p><a href=\"/news/detail.php?id=938\" target=\"_blank\">Подробнее о проекте</a></p>', '/images/dosug/2024/2/1.jpg', '2024-08-23', '/images/grants/VK_VB.jpg;\r\n/images/dosug/2024/2/2.jpg;\r\n/images/dosug/2024/2/3.jpg;\r\n/images/dosug/2024/2/4.jpg;\r\n/images/dosug/2024/2/5.jpg;\r\n/images/dosug/2024/2/6.jpg;\r\n/images/dosug/2024/2/7.jpg;\r\n/images/dosug/2024/2/8.jpg', '', '', 'Новости', '/news.php', 0, '', '21'),
(983, '3', 'Как провести адаптированный прием пациента с РАС?', 'В рамках проекта \"Без паники! Идем к стоматологу\" поведенческие аналитики \"Рассвета\" готовят обучающий курс для детских стоматологов Иркутской городской детской стоматологической поликлиники и Городской Ивано-Матренинской детской клинической больницы.', '<p>В рамках проекта \"Без паники! Идем к стоматологу\" поведенческие аналитики \"Рассвета\" готовят обучающий курс для детских стоматологов Иркутской городской детской стоматологической поликлиники и Городской Ивано-Матренинской детской клинической больницы.</p>\r\n<p>Помимо предварительной подготовки ребенка с РАС к походу к стоматологу, важным аспектом является готовность системы здравоохранения к приему пациента с особенностями поведения.</p>\r\n<p>Адаптированные приемы, применение врачами научно доказательных методик по работе со страхами и эффективное взаимодействие с детьми со сниженным пониманием речи способствуют повышению качества оказания стоматологических услуг пациентам, обратившимся за помощью.</p>\r\n<p>Курс \"Без паники!Идем к стоматологу.Курсы подготовки\" для врачей-стоматологов направлен на:</p>\r\n<p>&#9989; повышение осведомленности о расстройствах аутистического спектра и их особенностях проявления</p>\r\n<p>&#9989; обучение практическим навыкам адаптации стоматологического приема для пациентов с РАС</p>\r\n<p>&#9989; предоставление инструментов и методик для эффективного взаимодействия с детьми с РАС</p>\r\n<p>&#9989; адаптацию условий среды для повышения безопасности и комфорта пациентов с РАС</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>', '/images/seminar/2024/7/1.jpg', '2024-08-29', '/images/grants/pgrants_logo.jpg;\r\n/images/seminar/2024/7/2.jpg;\r\n/images/seminar/2024/7/3.jpg', '', '', 'Новости', '/news.php', 0, '', '11'),
(984, '19', 'С новым учебным годом!', 'Поздравляем всех ребят и их родителей с началом учебного года! Пусть этот год будет по-настоящему инклюзивным, ярким и запоминающимся!\r\nЧтобы нежелательное поведение обходило вас стороной, а в жизни было побольше подкреплений и поменьше наказаний!', '<p>Поздравляем всех ребят и их родителей с началом учебного года!</p>\r\n<p>Пусть этот год будет по-настоящему инклюзивным, ярким и запоминающимся!\r\nЧтобы нежелательное поведение обходило вас стороной, а в жизни было побольше подкреплений и поменьше наказаний!</p>\r\n<p>В новом учебном году продолжают свою работу наши ресурсные классы в Иркутске и Иркутском районе! Ребята уже встретились со своими старыми друзьями на классных часах и линейках!</p>\r\n<p>А также распахнул свои двери новый ресурсный класс в Иркутской школе №8. Первоклассники уже знакомы друг с другом, ведь они вместе прошли курс подготовки к школе на коммуникативных занятиях в \"Рассвете\".</p>\r\n<p>Как проходили коммуникативные занятия и чему учились и практиковались ребята рассказали в видео.</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456241219&hash=c7eac68b82990791\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe></section>', '/images/RK/2024/10/1.jpg', '2024-09-02', '/images/RK/2024/10/2.jpg;\r\n/images/RK/2024/10/3.jpg;\r\n/images/RK/2024/10/4.jpg;\r\n/images/RK/2024/10/5.jpg', '', '', 'Новости', '/news.php', 0, '', '21'),
(985, '13,19', 'Об открытии ресурсного класса на Радио \"Маяк\"', 'Об открытии в Иркутске ещё одного (пятого) ресурсного класса рассказали в эфире Радио \"Маяк. Иркутск\" начальник экспертного отдела \"Рассвета\" Елена Михеева и начальник инклюзивного отдела Вероника Артамонова.', '', '/images/smi/44.jpg', '2024-09-03', '', '', 'https://vk.com/wall-211913602_871', '', '', 1, '', ''),
(986, '18', 'Участие в стратегической сессии', 'В конце августа мы участвовали в двухдневной стратегической сессии в рамках проекта «Полезная занятость с применением практик и методов с научно доказанной эффективностью для молодых людей с РАС»!', '<p>В конце августа мы участвовали в двухдневной стратегической сессии в рамках проекта «Полезная занятость с применением практик и методов с научно доказанной эффективностью для молодых людей с РАС»!\r\n</p>\r\n<p>20 представителей НКО из 10 регионов РФ собрались вместе, чтобы поделиться опытом и лучшими практиками по организации мастерских полезной дневной занятости.</p>\r\n\r\n<p>Эти два дня были наполнены открытыми обсуждениями, групповыми упражнениями и идеями, которые преображают будущее! Мы обсудили лучшие методы поддержки молодежи с РАС и нашли новые решения, чтобы помочь им стать более успешными и уверенными в себе.</p>\r\n<p>Это был невероятно важный момент для всех нас, потому что мы смогли вместе создать что-то по-настоящему ценное и значимое. Мы верим, что каждый человек заслуживает шанса на развитие и реализацию своих способностей, и наша работа направлена на то, чтобы сделать этот шанс доступным каждому.\r\n</p>\r\n<p>Спасибо всем участникам за вдохновение, за их страстное стремление к изменениям и за их готовность действовать! Давайте продолжать двигаться вперед вместе и делать мир лучше для всех!</p>\r\n<p>АНО \"АПЦ РАСсвет\" - участник проекта Ассоциации «Аутизм-Регионы» \"Полезная занятость с применением практик и методов с научно доказанной эффективностью для молодых людей с РАС\" при поддержке Фонда президентских грантов.</p>', '/images/DSS/2024/14/1.jpg', '2024-09-04', '/images/DSS/2024/14/2.jpg;\r\n/images/DSS/2024/14/3.jpg', '', '', '', '', 0, '', ''),
(987, '2', 'В мире профессий', 'Вчера мы с ребятами с аутизмом побывали в Парке развлечений \"Любо-Город\". Это удивительном месте, где каждый может попробовать себя в различных профессиях. Это был невероятный день, полный впечатлений и радости!\r\n', '<p>Вчера мы с ребятами с аутизмом побывали в Парке развлечений \"Любо-Город\". Это удивительном месте, где каждый может попробовать себя в различных профессиях. Это был невероятный день, полный впечатлений и радости!</p>\r\n<p>Наши дети с огромным интересом приняли участие в мероприятиях, где им представилась возможность почувствовать себя в роли врачей, пожарных, полицейских и многих других профессий. С каждой новой задачей они раскрывали свой потенциал, проявляли таланты и умения.\r\n</p>\r\n<p>Встреча с различными профессиями в Любо-городе стала для наших детей настоящим открытием. Они увидели, как широк спектр возможностей перед ними, и почувствовали, что мир полон интересных профессий, в которых они тоже могут проявить себя.</p>\r\n<p>Мы благодарим организаторов Любо-города за теплый прием и заботу о наших детях. Это был не просто день проведенный весело, это был день, который вдохновил наших детей на новые свершения и показал, что у каждого из них есть потенциал для самореализации.</p>\r\n<p>Мы уходили из Любо-города с улыбками на лицах и счастливыми сердцами. Этот день запомнится нам на долгие годы! Спасибо всем, кто сделал его возможным и помог нашим детям почувствовать себя уверенно и востребованно.</p>\r\n\r\n<p>Проект \"Давай дружить\" реализуется при поддержке «Губернского собрания общественности Иркутской области»</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239560&hash=af5134e454eec422\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/dosug/2024/3.jpg', '2024-09-11', '/images/grants/GS.png', '', '', '', '', 0, '', '14'),
(988, '18', 'Как поддержать \"Дом синего слона\"', 'Чтобы наша мастерская продолжала свою важную миссию, нам необходимо поддерживать ее финансово. И мы нашли для себя несколько способов, как сделать это с пользой и радостью для всех!', '<p>Мы - инклюзивная мастерская для талантливых ребят с аутизмом, где каждый день волшебство превращается в прекрасные работы и идеи! И самое потрясающее - наши занятия абсолютно бесплатные, потому что мы верим, что каждый заслуживает шанс на самореализацию и творчество.\r\n</p>\r\n<p>Но чтобы наша мастерская продолжала свою важную миссию, нам необходимо поддерживать ее финансово. И мы нашли для себя несколько способов, как сделать это с пользой и радостью для всех!\r\n\r\n</p>\r\n<p>Участие в гаражных распродажах - отличный способ показать нашим талантливым ученикам, что их труд ценится и востребован. Кроме того, это отличная возможность познакомить город с нашими яркими работами и привлечь новых поклонников и поддерживающих.</p>\r\n<p>Выполнение индивидуальных заказов - каждое изделие, созданное нашими учениками, уникально и особенно. Мы готовы воплотить в жизнь любую идею и порадовать вас оригинальным подарком или украшением для дома. Доверьтесь нашему творчеству и удивитесь результату!</p>\r\n<p>Продажа через социальные сети - в нашем мире цифровых возможностей это простой и удобный способ приобрести уникальные изделия прямо из дома. Переходите на нашу страничку, выбирайте то, что вам по душе, и поддерживайте наших талантливых мастеров!</p>\r\n<p>Вместе мы сможем сделать больше и дать возможность нашим ученикам раскрыть свой потенциал на полную катушку!</p>\r\n<p>АНО \"АПЦ РАСсвет\" - участник проекта Ассоциации «Аутизм-Регионы» «Полезная занятость с применением практик и методов с научно доказанной эффективностью для молодых людей с РАС» при поддержке Фонда президентских грантов.</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239561&hash=77c27c5318900c79\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2024/18.jpg', '2024-09-13', '', '', '', '', '', 0, '', '');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(989, '13,19', 'Новый класс для детей с РАС', 'Ресурсный класс на базе ООШ №8 уже открыт, а дети уже вовсю постигают школьную жизнь!\r\nПодробнее про открытие ресурсных классов для ребят с РАС на базах общеобразовательных школ в Иркутске на онлайн-платформе \"Открытые НКО\".', '', '/images/smi/45.jpg', '2024-09-09', '', '', 'https://dobro.live/publikacii/nko/klass-dlya-obucheniya-detey-s-rasstroystvami-autisticheskogo-spektra-otkryvaetsya-v-irkutske', '', '', 1, '', ''),
(990, '22', 'Благодарим ООО \"НИТРО\"', 'Выражаем благодарность ООО \"НИТРО\" за подарки нашим ученикам, наборы \"жвачек для рук\"! Разнообразие игрушек позволяет проводить занятия увлекательно и эффективно!', '<p>Выражаем благодарность ООО \"НИТРО\" за подарки нашим ученикам, наборы \"жвачек для рук\"! Разнообразие игрушек позволяет проводить занятия увлекательно и эффективно, ведь сочетание развивающих упражнений и мотивационной деятельности помогает ребяткам быть более успешными в освоении навыков!\r\n</p>\r\n<p>Основное различие между слаймом и лизуном - это текстура. Слайм более упругий и не липкий, что делает его более удобным для игры. Лизун, напротив, имеет более вязкую и липкую текстуру, которая может быть интересна для детей, но может также вызывать беспокойство у родителей из-за возможных пятен на одежде и волосах.</p>\r\n<p>Что выбрать для своего ребенка?\r\nИсходите из интересов и потребностей своего ребенка.</p>\r\n<p>Более безопасный вариант \"жвачки для рук\" - слайм - подходит для малышей, для чувствительных к текстурам ребятишек, для тех, кто выбирает более размеренный и спокойный формат игры, любит поиграть с формами и \"позалипать\" в приятной деятельности.\r\n</p>\r\n<p>Лизун отлично подходит для развития мелкой моторики и согласованного движения рук, потому что активный темп игры помогает лизуну не прилипать к рукам.\r\n</p>\r\n<p>Во что поиграть, когда просто слайм и лизун уже надоели?</p>\r\n<p>1. Добавлять маленькие фигурки в массу и доставать их оттуда пальчиками или пинцетом.</p>\r\n<p>2. Скатывать массу по окну. Она будет медленно стекать, создавая цветное искажение, через которое можно смотреть на улицу или в зеркало.</p>\r\n<p>3. Гонки лизунов вниз по зеркалу. Чей лизун скатится быстрее?</p>\r\n<p>4. Массу можно использовать как подставку для счетных палочек на математике.\r\n</p>\r\n<p>5. Делаем перчатки из слайма.</p>\r\n<p>6. Лизун можно надувать с помощью соломинки. Получаются липкие пузыри.</p>\r\n<p>7. Слайм можно резать ножницами или ножом (под присмотром взрослых).</p>\r\n', '/images/gratitude/foto/2024/1.jpg', '2024-09-06', '', '', '', 'Новости', '/news.php', 0, '', ''),
(991, '22', 'Благодарим фонд «Помощь рядом»', 'Благодарим социальный проект Яндекса - фонд «Помощь рядом» за возможность участвовать в программе «Товары для НКО», благодаря которым наш центр обладает ресурсами оперативно создавать адаптированные печатные материалы и визуальную поддержку для наших учеников!', '<p>Благодарим социальный проект Яндекса - фонд «Помощь рядом» за возможность участвовать в программе «Товары для НКО», благодаря которым наш центр обладает ресурсами оперативно создавать адаптированные печатные материалы и визуальную поддержку для наших учеников!</p>\r\n<p>Адаптация учебных материалов - один из способов объяснить ребенку с особыми образовательными потребностями какую-либо тему.</p>\r\n<p>«На занятиях Ариша изучает «времена года». Перед занятиями мы с коллегами готовим пособия самостоятельно.</p>\r\n<p>Во-первых, это позволяет сделать процесс обучения более понятным. Сначала Арина учится различать признаки времен года визуально, а затем учится называть их, различать на слух и запоминает написание названий.</p>\r\n<p>Во-вторых, благодаря тому, что мы сами создаем печатные материалы, нам удается проводить занятия разнообразно и интересно, поддерживая мотивацию ребенка, ведь это очень важно в АВА-терапии!», - Дарья Огоренко, АВА-инструктор «Рассвета».</p>', '/images/gratitude/foto/2024/2.jpg', '2024-09-12', '/images/gratitude/foto/2024/3.jpg;\r\n/images/gratitude/foto/2024/4.jpg', '', '', 'Новости', '/news.php', 0, '', ''),
(992, '16', 'Поведенческие аналитики Рассвета приняли участие в IX международной конференции', 'Три поведенческих аналитика «Рассвета» приняли участие в IX международной научно-практической конференции «Наука и практика прикладного анализа поведения\", самом масштабном мероприятии русскоязычного профессионального сообщества АВА-специалистов.', '<p>Три поведенческих аналитика «Рассвета» приняли участие в IX международной научно-практической конференции «Наука и практика прикладного анализа поведения\", самом масштабном мероприятии русскоязычного профессионального сообщества АВА-специалистов. \r\nКонференция была организована проектом Юлии Эрц \"Аутизм: Коррекционная работа АВА\" и  проходила 14 и 15 сентября в онлайн-формате.</p>\r\n<p>Тема конференции этого года: \"Развитие речевых и социальных навыков у детей с РАС в парадигме АВА: новейшие стратегии и социально-значимые результаты\". </p>\r\n<p>Председатель \"Рассвета\" Екатерина Родыгина (сертифицированный поведенческий аналитик MBP) совместно с супервизором проекта Анной Устиновской (сертифицированный поведенческий аналитик CBA-S) выступили с докладом \"Без паники! Идем к стоматологу: методы уменьшения деструктивного поведения детей с РАС при стоматологических и инвазивных вмешательствах\".</p>\r\n<p>Елена Михеева (сертифицированный поведенческий аналитик MBP) выступила с темой \"Обучение в малых группах детей с РАС в рамках подготовки к школе\"</p>\r\n<p>Вероника Артамонова (сертифицированный поведенческий аналитик MBP) представила доклад на тему \"Формирование такт реакций у подростка с множественными нарушениями с использованием языка жестов\".</p>\r\n<p>Наши докладчицы поделились своими впечатлениями от участия в конференции.</p>\r\n<p>Екатерина Родыгина: \"Спасибо за участие, поддержку, огонёчки всем участникам конференции. Спасибо Анна Устиновская за чуткость и поддержку на каждом шаге проекта моей мечты, от реализации, до выступления. Ещё в июне, когда Анна предложила выступить на конференции, это казалось невозможным, но всё получилось!\r\nСпасибо Юлии Эрц за такой опыт, за веру в нас, за поддержку на каждом этапе подготовки доклада.\r\nА главное, спасибо нашим ребятам за успехи! Я сто раз просматривала видео своего доклада, но всё-равно еле сдерживала слёзы, зная, какой путь был проделан ребятами, чтобы достичь результатов.\"</p>\r\n<p>Елена Михеева: \"Сегодняшний день для меня был очень волнительный!\r\nВпервые я выступала с докладом на IX Международной онлайн-конференции. Иметь возможность поделиться своими исследованиями и опытом на одной площадке с ведущими экспертами в области АВА, многими из которых я восхищаюсь, - это невероятная честь и большая ответственность. Выступать рядом с супервизорами, чьи имена известны во всем мире, - это не просто профессиональный успех, а вдохновляющий пример для меня.\r\nЯ с волнением готовилась к этому дню и у меня все получалось.\"</p>\r\n<p>Трансляция конференции была бесплатной и очень насыщенной. Записи всех докладов доступны на сайте дистанционного обучения Школы Юлии Эрц. Чтобы получить бесплатный доступ к записям, нужно зарегистрироваться <a href=\"https://aba-kurs.com/services/course/60014/\" target=\"_blank\">ЗДЕСЬ.</a></p>\r\n<p></p>\r\n<p></p>', '/images/news/2024/7/0.jpg', '2024-09-17', '/images/news/2024/7/2.jpg;\r\n/images/news/2024/7/3.jpg;\r\n/images/news/2024/7/4.jpg', '', '', 'Новости', '/news.php', 0, '', '0'),
(993, '18', 'Результаты наших малышей', 'Сегодня хотим поделиться результатами наших малышей: Ильи, Ромы и Захара в рамках проекта, который реализовался при поддержке «Губернского собрания Иркутской области».', '<p>Сегодня хотим поделиться результатами наших малышей: Ильи, Ромы и Захара в рамках проекта, который реализовался при поддержке «Губернского собрания Иркутской области».</p>\r\n<p>Ребята продемонстрировали невероятные успехи. Теперь они уверенно сворачивают свечи из вощины, следуя всей поведенческой цепочке. Научились шить подушки на швейной машинке по прямой, пока нуждаясь в частичных подсказках.</p>\r\n<p>Ребята отлично проявили себя в изготовлении мыла. Они сами нарезают мыльную основу и и уверенно справляются с микроволной печью.\r\nВсе это благодаря их усердию и трудолюбию.</p>\r\n<p>Хочется так же отметить успехи Ромы.\r\nМальчик в начале проекта, заходя в мастерскую, часто соскальзывал под стол, тем самым избегая то или иное задание.\r\nЧтобы установить сотрудничество с Ромой, использовали такой подход - мама делает, а он смотрит и далее частичное включается в деятельность.\r\nСейчас Рома выполняет от начала до конца уже знакомые ему алгоритмы заданий с частичными подсказками.</p>\r\n<p>Захар стал меньше проявлять нежелательного поведения, заходя в мастерскую, потому что стал понимать, сколько видов деятельности его ждет на занятии.\r\nСтрах к горячему, к сожалению, пока сохраняется, но мы стараемся максимально адаптировать для него горячие этапы работы. Например, мы научили Захара прижимать горячий клей деревянной палочкой.</p>\r\n<p>Илья научился ловко вставлять фурнитуру в кожаные изделия и показал прогресс в работе с текстурной пастой. Теперь Илья может делать качественные рельефы и текстуры на картинах.\r\n</p>\r\n<p>Проект \"Давай дружить\" реализуется при поддержке «Губернского собрания общественности Иркутской области»</p>', '/images/DSS/2024/15/3.jpg', '2024-09-16', '/images/grants/GS.png;\r\n/images/DSS/2024/15/1.jpg;\r\n/images/DSS/2024/15/2.jpg;\r\n/images/DSS/2024/15/5.jpg;\r\n/images/DSS/2024/15/4.jpg', '', '', '', '', 0, '', '14'),
(994, '12', 'Индивидуальные занятия адаптивной физкультурой', '\"Рассвет\" реализует проект \"Расскажи о спорте\", в рамках которого на базе нашего центра для ребят с РАС проводятся бесплатные индивидуальные и групповые занятия адаптивной физкультуры.\r\n', '<p>\"Рассвет\" реализует проект \"Расскажи о спорте\", в рамках которого на базе нашего центра для ребят с РАС проводятся бесплатные индивидуальные и групповые занятия адаптивной физкультуры.</p>\r\n<p>На индивидуальных занятиях дети прокачивают навыки:</p>\r\n<p>&#9989;  Социального взаимодействия, отчего удается минимизировать проблемное поведение.</p>\r\n<p>&#9989; Ребята изучают техники выполнения упражнений, учатся повторять в имитацию движения за тренером.</p>\r\n<p>&#9989; Дети постепенно привыкают к фронтальным инструкциям (когда тренер проводит занятие для всего класса, а не один на один с учеником)</p>\r\n<p>&#9989; Ученики тренируют моторные навыки, координацию, выносливость и улучшают индивидуальные показатели здоровья.</p>\r\n<p>Благодаря индивидуальному подходу наших педагогов, дети получают возможность осваивать новые двигательные навыки в комфортной для них обстановке и подготавливаются к групповому формату занятий вместе со сверстниками.</p>\r\n<p>Напомним, что наш проект «Расскажи о спорте» стал победителем конкурса Фонда Потанина «Спорт для всех»</p>\r\n<p><a href=\"/news/detail.php?id=895\" target=\"_blank\">Подробнее о проекте</a></p>', '/images/AFK/2024/4/6.jpg', '2024-09-18', '/images/AFK/2024/4/1.jpg;\r\n/images/AFK/2024/4/2.jpg;\r\n/images/AFK/2024/4/3.jpg;\r\n/images/AFK/2024/4/4.jpg;\r\n/images/AFK/2024/4/5.jpg;\r\n/images/AFK/2024/4/7.jpg', '', '', 'Новости', '/news.php', 0, '', '16'),
(995, '2', 'Пятница с \"Рассветом\"', 'Наша прошлая неделя завершилась так ярко и насыщенно, что до сих пор не можем отойти от впечатлений. Мы отправились в путешествие в удивительный парк деревянных скульптур «Лукоморье» в с. Савватеевка.\r\n', '<p>Наша прошлая неделя завершилась так ярко и насыщенно, что до сих пор не можем отойти от впечатлений. Мы отправились в путешествие в удивительный парк деревянных скульптур «Лукоморье» в с. Савватеевка.</p>\r\n<p>С самого приезда нас ждал вкусный обед, который подарил нам энергию и хорошее настроение на весь день. Затем мы приняли участие в мастер-классе по росписи пряников вместе с ребятами. Это было не только увлекательно, но и очень важно - ведь такие мероприятия помогают создать дружелюбную и поддерживающую атмосферу.\r\n</p>\r\n<p>После творческой части мы отправились на экскурсию по парку, где каждая скульптура ожила перед нами, рассказывая свою удивительную историю. Мы узнали много интересного о ремеслах и традициях, которые воплотились в этих произведениях искусства.</p>\r\n<p>Не могли пройти мимо речки и не побросать камешки с берега - это такой маленький, но такой приятный ритуал, который напоминает о простых радостях жизни.\r\n</p>\r\n<p>Пятница с рассветом оказалась для всех нас днем, наполненным теплом, добротой и вдохновением. Все мы вернулись домой счастливые и полными впечатлений, готовые к новым приключениям и открытиям.</p>\r\n\r\n<p>Проект \"Давай дружить\" реализуется при поддержке «Губернского собрания общественности Иркутской области»</p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239567&hash=cd10b0815ed44cba\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/dosug/2024/4.jpg', '2024-09-24', '/images/grants/GS.png', '', '', '', '', 0, '', '14'),
(996, '9', 'Причины родительского выгорания', 'Родительство - одно из самых нелёгких и ответственных занятий в мире. А когда ваш ребенок с особенностями в развитии, это становится еще более сложным и требует особого подхода. Сегодня хотим поговорить о причинах родительского выгорания.', '<p>Родительство - одно из самых нелёгких и ответственных занятий в мире. А когда ваш ребенок с особенностями в развитии, это становится еще более сложным и требует особого подхода. Сегодня хотим поговорить о причинах родительского выгорания.</p>\r\n<p>1️⃣ Постоянный стресс. Непредсказуемое поведение, сложности в общении, постоянные тревожные мысли — все это вызывает постоянный стресс у родителей. Они всегда находятся на грани эмоционального и физического истощения.</p>\r\n<p>2️⃣ Недостаток поддержки. Часто родители детей с особенностями в развитии остаются один на один с проблемами и вызовами. Недостаток поддержки со стороны общества, медицинских учреждений и даже близких людей может привести к чувству изоляции и беспомощности.</p>\r\n<p>3️⃣ Несправедливость. Родители часто сталкиваются с несправедливостью в обществе — от недоступности образовательных учреждений для детей с особенностями развития до недопонимания окружающих. Это только усиливает чувство бессилия и разочарования.</p>\r\n<p>4️⃣ Постоянная борьба за права ребенка. Родители часто вынуждены бороться за права своих детей и доступ к качественной помощи. Эта непрерывная борьба требует физических и эмоциональных ресурсов, что приводит к выгоранию.</p>\r\n<p>Дорогие родители, помните, что ваша забота и любовь невероятно важны для вашего ребенка. Не стесняйтесь обращаться за помощью, общаться с другими родителями в подобной ситуации и заботиться о своем физическом и эмоциональном благополучии. Вы не одиноки, и вместе мы сможем преодолеть все трудности!</p>\r\n', '/images/respite/10.jpg', '2024-09-23', '', '', '', 'Новости', '/news.php', 0, '', ''),
(997, '12', 'Эстафеты для учеников ресурсных классов', 'Вчера у нас прошли эстафеты среди наших ребят из ресурсных классов! Мы были рады приветствовать и присоединившихся к нам ребят из Школы №8 им. Д. Г. Сергеева г.Иркутска - вместе мы создали невероятную атмосферу поддержки и соревнований.\r\n', '<p>Вчера у нас прошли эстафеты среди наших ребят из ресурсных классов! Мы были рады приветствовать и присоединившихся к нам ребят из Школы №8 им. Д. Г. Сергеева г.Иркутска - вместе мы создали невероятную атмосферу поддержки и соревнований.</p>\r\n<p>Мы гордимся нашими учениками, которые пол года усердно тренировались на занятиях по адаптивной физкультуре. Вчера они показали все свои возможности и умения, поражая нас своими достижениями. Было замечательно видеть, как они растут и развиваются, преодолевая собственные пределы.</p>\r\n<p>Спорт объединяет нас, учит дисциплине и целеустремленности, помогает преодолевать трудности и достигать новых вершин. Мы благодарим всех участников за вдохновение, за отличное настроение и за теплую дружескую атмосферу.</p>\r\n<p>Пусть спорт будет всегда с нами, пусть он вдохновляет нас на новые свершения и помогает нам становиться лучше каждый день. Спасибо всем, кто был с нами вчера, вы делаете наш мир ярче и интереснее!\r\n</p>\r\n<p>Напомним, что наш проект «Расскажи о спорте» стал победителем конкурса Фонда Потанина «Спорт для всех»</p>\r\n<p><a href=\"/news/detail.php?id=895\" target=\"_blank\">Подробнее о проекте</a></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456241264&hash=245b610b24ba279a\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/AFK/2024/5.jpg', '2024-09-25', '/images/AFK/2024/4/1.jpg;\r\n/images/AFK/2024/4/2.jpg;\r\n/images/AFK/2024/4/3.jpg;\r\n/images/AFK/2024/4/4.jpg;\r\n/images/AFK/2024/4/5.jpg;\r\n/images/AFK/2024/4/7.jpg', '', '', 'Новости', '/news.php', 0, '', '16'),
(998, '4', 'Развитие навыка \"трогать нельзя\"', 'Развитие навыка \"трогать нельзя\" у детей с РАС может показаться непонятным или даже контринтуитивным на первый взгляд. Однако, этот навык имеет огромное значение и может принести значительные пользы как самим детям, так и их окружающим.', '<p>Развитие навыка \"трогать нельзя\" у детей с РАС может показаться непонятным или даже контринтуитивным на первый взгляд. Однако, этот навык имеет огромное значение и может принести значительные пользы как самим детям, так и их окружающим.\r\n</p>\r\n<p>Во-первых, умение сдерживать потребность трогать других людей без их разрешения является важным социальным навыком. Дети с аутизмом могут испытывать трудности в понимании границ личного пространства других людей, и развитие навыка \"трогать нельзя\" поможет им лучше взаимодействовать с окружающими и избегать неприятных ситуаций.</p>\r\n<p>Кроме того, развитие этого навыка способствует формированию эмпатии и уважения к чужим границам. Дети с РАС часто имеют трудности в понимании эмоций других людей, и умение контролировать свои действия по отношению к окружающим поможет им лучше взаимодействовать и строить отношения с другими людьми.</p>\r\n<p>Наконец, развитие навыка \"трогать нельзя\" способствует безопасности детей. Они могут не понимать потенциальные опасности, связанные с нежелательными физическими контактами, и умение сдерживать свои импульсы поможет им избежать травматических ситуаций.</p>\r\n<p>Поэтому, хотя на первый взгляд развитие навыка \"трогать нельзя\" у детей с РАС может показаться странным или ненужным, на самом деле это важное и полезное умение, которое способствует их адаптации в обществе и повышает качество их взаимодействия с окружающими.</p>\r\n<p>И в программе интенсива Ильи как раз было развитие данного навыка. И как вы наблюдаете на видео освоил он этот навык просто превосходно.</p>\r\n<p>Проект реализуется при поддержке Благотворительного фонда «Вклад в будущее».\r\n</p>\r\n<p><a href=\"/news/detail.php?id=938\" target=\"_blank\">Подробнее о проекте</a></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456241272&hash=3d8fba7180de0fc9\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/ABA/2024/27.jpg', '2024-09-30', '/images/grants/VK_VB.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', '21'),
(999, '18', '\"Дом синего слона\" на Яндекс Маркете', 'Друзья, хотим поделиться с вами потрясающей новостью! Мы радостно объявляем, что некоторые наши изделия теперь представлены на Яндекс Маркете! Сегодня мы успешно отгрузили первый заказ, и это просто невероятно волнующий момент для нас!', '<p>Друзья, хотим поделиться с вами потрясающей новостью! Мы радостно объявляем, что некоторые наши изделия теперь представлены на <a href=\"https://market.yandex.ru/business--dom-sinego-slona/124312190\" target=\"_blank\">Яндекс Маркете</a>! Сегодня мы успешно отгрузили первый заказ, и это просто невероятно волнующий момент для нас!\r\n</p>\r\n<p>Теперь, если вы не находитесь в Иркутске, у вас есть удивительная возможность заказать наши уникальные изделия через Яндекс Маркет и забрать их в удобном для вас пункте выдачи, который расположен близко к вашему дому.</p>\r\n\r\n<p>Не упустите шанс приобрести качественные изделия от нашей мастерской прямо сейчас на <a href=\"https://market.yandex.ru/business--dom-sinego-slona/124312190\" target=\"_blank\">Яндекс Маркете</a>.</p>', '/images/DSS/2024/19.jpg', '2024-10-01', '', '', '', '', '', 0, '', ''),
(1000, '16', 'Учение - свет, а неучение - тьма!', 'В нашем центре мы убеждены, что постоянное обучение - это ключ к успеху. Мы инвестируем в знания наших сотрудников, работающих не только в центре, но и в ресурсных классах. Недавно наши коллеги прошли обучение в Институте прикладного анализа поведения.', '<p>В нашем центре мы убеждены, что постоянное обучение - это ключ к успеху. Мы инвестируем в знания наших сотрудников, работающих не только в центре, но и в ресурсных классах. Недавно наши коллеги прошли обучение в Институте прикладного анализа поведения, и мы решили узнать, что им понравилось и было полезным на этом курсе.\r\n</p>\r\n<p>Иван - тьютор ресурсного класса в школе номер 8, поделился своим впечатлением: «Информацию, которую я сейчас получаю во время обучения мне уже помогает в моей работе. У нас есть в классе мальчик Саша и оказывается его одно и тоже нежелательное поведение происходит по разным причинам, а раньше я думал, что только из-за избегания требования. Так же успешно применяю методы коррекции этого нежелательного поведения.»</p>\r\n<p>Дарья - инструктор, отметила: «Из данного курса удалось многое для себя взять на вооружение: некоторые приёмы работы в естественной среде, интересные протоколы формирования навыков, методы коррекции агрессивного поведения. Буду стараться с успешностью применять полученные знания».</p>\r\n<p>Влад, еще один наш инструктор, добавил: «Одним из самых интересных моментов оказалось изучение пути развития самого метода ABA. Будучи человеком с психологическим образованием, я всегда считал важным понимать корни подхода, чтобы лучше осознать его суть. Ощущение, что ты не просто учишься технике, а понимаешь, откуда она появилась и почему работает — это, на мой взгляд, большой плюс курса».</p>\r\n<p>Мы рады, что наши сотрудники находят ценность в обучении и активно развивают свои профессиональные навыки. Вместе мы движемся к новым вершинам, освещая путь светом знания!</p>\r\n<p>Обучение организовано Ассоциацией «СПЕКТР» в рамках проекта \"Важные знания - повышение компетенций педагогов, обучающих детей с РАС\" при поддержке Фонда президентских грантов.</p>\r\n\r\n', '/images/news/2024/8/1.jpg', '2024-10-02', '/images/news/2024/8/2.jpg;\r\n/images/news/2024/8/3.jpg', '', '', 'Новости', '/news.php', 0, '', '0'),
(1002, '16', 'Гигиеническое обучение в Рассвете', 'Олег Кривобоков - исполнительный директор АНО \"АПЦ Рассвет\" и врач по общей гигиене пристально следит за тем, чтобы у каждого работника нашей организации были как профессиональные компетенции в области прикладного анализа поведения, так и прохождение гигиенической подготовки.', '<p>Олег Кривобоков - исполнительный директор АНО \"АПЦ Рассвет\" и врач по общей гигиене пристально следит за тем, чтобы у каждого работника нашей организации были как профессиональные компетенции в области прикладного анализа поведения, так и прохождение гигиенической подготовки.</p>\r\n<p>Почему это важно?</p>\r\n<p>&#9989; БЕЗ ПАНИКИ! ИДЕМ К СТОМАТОЛОГУ</p>\r\n<p>Наши специалисты знакомы с санитарно-эпидемиологическими нормами обработки и утилизации одноразового стоматологического инструмента, и как правильно его использовать при обучении детей с РАС.\r\nЕжедневная обработка стоматологического инструмента проводится с использованием дезинфицирующих средств в соответствующем установленным нормам объеме.</p>\r\n<p>&#9989; ОХРАНА ЗДОРОВЬЯ</p>\r\n<p>Дети более подвержены инфекциям из-за незрелости иммунной системы, поэтому работникам образования критически важно знать санитарные правила, чтобы предотвращать распространение инфекций, таких как грипп, ОРВИ, кишечные инфекции.</p>\r\n<p>&#9989; ЧИСТОТА</p>\r\n<p>Регулярная уборка, дезинфекция поверхностей и игрушек создает здоровую среду для детей.</p>\r\n<p>&#9989; ПРОФИЛАКТИКА ЗАБОЛЕВАНИЙ</p>\r\n<p>Обучение детей правилам личной гигиены, например, мытье рук, снижает риск заболеваний.\r\nМногие наши социальные проекты направлены на обучение детей с РАС уходу за собой.</p>\r\n<p>&#9989; КОМФОРТ</p>\r\n<p>Чистое помещение, свежий воздух и комфортная температура создают благоприятные условия для обучения и способствуют концентрации внимания детей.</p>\r\n<p>Педагоги несут ответственность за здоровье и безопасность детей. Соблюдение санитарных норм и правил является неотъемлемой частью этой ответственности.</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>', '/images/news/2024/9/1.jpg', '2024-10-10', '/images/grants/pgrants_logo.jpg;\r\n/images/news/2024/9/2.jpg;\r\n/images/news/2024/9/3.jpg', '', '', '', '', 0, '', '11'),
(1003, '1', 'Школьный мир для особенной Агаты и её друзей', 'Открыт благотворительный сбор средств на платформе \"VK Добро\" в пользу одного из ресурсных классов г. Иркутска. «Рассвет» собирает средства, чтобы ресурсный класс, в котором учится Агата, продолжал работать. Деньги пойдут на зарплату куратора и супервизора в течение учебного года.', '', '/images/help/school_world/Agatha.jpg', '2024-10-12', '', '', '/fundraising/school_world_for_Agatha.php', 'Новости', '/news.php', 1, '', '0'),
(1004, '19', 'Ресурсный класс: поддержание эффективности обучения детей с РАС', 'Благодаря благотворительным пожертвованиям Байкальского банка ПАО \"Сбербанк\" и ООО \"НИТРО\" на прошлой неделе прошли очные супервизии с Тамарой Соломатиной, поведенческим аналитиком уровня CBA-S из города Санкт-Петербург, супервизором наших ресурсных классов.', '<p>Систематические супервизии с опытным поведенческим аналитиком являются неотъемлемой частью эффективной работы ресурсных классов для детей с РАС.</p>\r\n<p>Отсутствие супервизоров, профилирующихся на работе ресурсных классов, в Иркутске и высокая стоимость привлечения специалистов из других городов сразу для 4-х ресурсных классов нашего города - актуальные проблемы нашей некоммерческой организации на сегодняшний день.</p>\r\n<p>Благодаря благотворительным пожертвованиям Байкальского банка ПАО \"Сбербанк\" и ООО \"НИТРО\" на прошлой неделе прошли очные супервизии с Тамарой Соломатиной, поведенческим аналитиком уровня CBA-S из города Санкт-Петербург, супервизором наших ресурсных классов в школах пос. Молодежный, Марковская СОШ №2, МБОУ СОШ №17. </p>\r\n<p>\"Как всегда, опытный взгляд супервизора со стороны помог нам расставить акценты в программах детей. Сосредоточиться на значимых, функциональных целях. И в то же время увидеть новые важные цели, являющиеся универсальными и охватывающие разные области знаний.</p>\r\n<p>Для кого-то из детей актуальной целью будет умение искать информацию, извлекать ее из разных источников (тексты, схемы, поиск в интернете), представлять её в другом формате.\r\nА для подростка с низким уровнем навыков и ограниченным кругом интересов, что нередко присуще людям с аутизмом, важная цель - разнообразить самозанятость в соответствии с возрастом: научиться пользоваться степлером, сортировать документы, пользоваться канцелярскими зажимами, сортировать шурупы, гайки и другие мелкие инструменты\", - делится Елена Шапилова, куратор ресурсного класса 5-ого года обучения.</p>\r\n<p>\"У меня появилась уверенность, как дальше действовать. Какие методы, приёмы и первоочередные, а что можно ненадолго отложить. Больше ясности как должны выглядеть фронтальные уроки\", - Галина Лучшева, куратор ресурсного класса учеников 1-ого года обучения.</p>\r\n<p>Необходимо продолжать поддерживать деятельность ресурсных классов для детей с РАС на высоком уровне, обеспечивая доступ команды классов к квалифицированным специалистам.\r\nКачественное обучение – это путь к самостоятельности, успешной интеграции в общество и комфортной жизни наших детей.</p>\r\n<p>Благодарим Байкальский банк ПАО Сбербанк и ООО «НИТРО» за оказание финансовой помощи в таких важных мероприятиях.</p>\r\n\r\n', '/images/RK/2024/11/1.jpg', '2024-10-14', '/images/RK/2024/11/2.jpg;\r\n/images/RK/2024/11/3.jpg;\r\n/images/RK/2024/11/4.jpg;\r\n/images/RK/2024/11/6.jpg;\r\n/images/RK/2024/11/7.jpg;\r\n/images/RK/2024/11/8.jpg;\r\n/images/RK/2024/11/5.jpg', '', '', 'Новости', '/news.php', 0, '', ''),
(1005, '4', 'Работа с проблемным поведением', 'На днях наш центр посетила поведенческий аналитик, супервизор Татьяна Овсянникова (г. Москва). Татьяна поделилась опытом с педагогами АНО \"АПЦ РАСсвет\" и тьюторами ресурсного класса МБОУ ООШ №8, проведя очные консультации и тренинг по работе с нежелательным поведением.', '<p>На днях наш центр посетила поведенческий аналитик, супервизор Татьяна Овсянникова (г. Москва).\r\n\r\nТатьяна поделилась опытом с педагогами АНО \"АПЦ РАСсвет\" и тьюторами ресурсного класса МБОУ ООШ №8, проведя очные консультации по разбору кейсов конкретных учеников и тренинг по работе с нежелательным поведением.</p>\r\n<p>На тренинге мы глубоко погрузились в проактивные методы, позволяющие предотвращать проблемное поведение с учетом его различных функций.\r\n\r\nНапример, что можно предпринять в работе с нежелательным поведением с функциями избегания, доступа к желаемому, привлечения внимания и сенсорной самостимуляции.\r\n</p>\r\n<p>Татьяна рассказала про то, как мы можем использовать визуальное расписание, чтобы предотвратить деструктивное поведение.\r\nРазобрали, что такое функциональная коммуникация и что она в себя включает.\r\nПоделились примерами социальных историй, которые помогают ученикам спокойно переживать те или иные события.\r\nПознакомились с протоколом \"круто\\не круто\".\r\nТак же Татьяна привела примеры использования видеомоделинга.</p>\r\n<p>Все проактивные методы были представлены в рамках работы с небезопасным и проблемным поведением и особенностями написания поведенческих программ для наших учеников.</p>\r\n<p>Проект «Я могу: социализация и обучение жизненно-важным навыкам детей с РАС» реализуется с использованием пожертвования Благотворительного фонда «Абсолют-Помощь».</p>\r\n<p><a href=\"https://rassvet-apc.ru/news/detail.php?id=894\" target=\"_blank\">Подробнее о проекте</a></p>\r\n', '/images/ABA/2024/4/1.jpg', '2024-10-16', '/images/Absolut/logo.jpg;\r\n/images/ABA/2024/4/2.jpg;\r\n/images/ABA/2024/4/3.jpg;\r\n/images/ABA/2024/4/4.jpg;\r\n/images/ABA/2024/4/5.jpg;\r\n/images/ABA/2024/4/6.jpg;\r\n/images/ABA/2024/4/7.jpg', '', '', 'Новости', '/news.php', 0, '', '12'),
(1006, '18', 'Волнующие моменты в нашей мастерской', 'Волнующие моменты происходят в нашей мастерской «Дом синего слона»! \r\nДля наших ребят, каждое занятие в мастерской – это возможность не только развивать ремесленные навыки, но и находить свое место в обществе.', '<p>Волнующие моменты происходят в нашей мастерской «Дом синего слона»! \r\nДля наших ребят, каждое занятие в мастерской – это возможность не только развивать ремесленные навыки, но и находить свое место в обществе.\r\n</p>\r\n<p>Социализация играет ключевую роль в жизни каждого ребенка. В нашей мастерской мы создаем безопасное пространство, где дети могут научиться взаимодействовать друг с другом и с педагогами. Мы учим их уважению, терпимости и пониманию различий. Это не просто занятия, например, с глиной, это уроки жизни.\r\n</p>\r\n<p>Сотрудничество – еще один важный аспект наших занятий. Мы поощряем детей работать в команде, делиться идеями, помогать друг другу. Вместе они создают изделия для продажи и осознают, что каждый внес свой вклад. Это учит их ценить коллективное творчество и учиться принимать помощь от других.</p>\r\n<p>В результате наших занятий ребята не только улучшают свои навыки в ремесле, но и становятся увереннее в себе, развивают социальные навыки и находят поддержку в окружающих. Мы верим, что каждый ребенок способен на удивительные свершения, и наша мастерская – это место, где они могут раскрыть свой потенциал.</p>\r\n<p>Скоро Новый Год, а значит пора придумывать оригинальные украшения для праздничной елки!</p>\r\n<p>Наши удивительные ребята уже начали создавать украшения из эпоксидной смолы, и результаты просто потрясающие! Это для них первый опыт работы с таким материалом, и они с таким энтузиазмом вплетают в каждое украшение кусочки своей души.</p>\r\n<p>Каждое украшение уникально и не похоже ни на одно другое. Они используют разные цвета, блестки, декоративные элементы, чтобы создать настоящие шедевры для вашей елки.</p>\r\n<p>Но это не просто украшения, это еще и возможность поддержать наших талантливых ребят! Ведь весь доход от продажи украшений пойдет на материал для занятий в нашей мастерской. Помогите нам сохранить наши занятия для ребят все так же бесплатными и сделайте вашу елку особенной этим Новым Годом!</p>\r\n<p>АНО \"АПЦ РАСсвет\" - участник проекта Ассоциации «Аутизм-Регионы» «Полезная занятость с применением практик и методов с научно доказанной эффективностью для молодых людей с РАС» при поддержке Фонда президентских грантов.</p>', '/images/DSS/2024/16/1.jpg', '2024-10-19', '/images/DSS/2024/16/2.jpg;\r\n/images/DSS/2024/16/3.jpg;\r\n/images/DSS/2024/16/4.jpg;\r\n/images/DSS/2024/16/5.jpg;\r\n/images/DSS/2024/16/6.jpg;\r\n/images/DSS/2024/16/7.jpg\r\n', '', '', '', '', 0, '', ''),
(1007, '16', 'РЕАЛЬНЫЙ АУТИЗМ: ЗА ПРЕДЕЛАМИ СТЕРЕОТИПОВ', 'Аутизм - это серьезное расстройство, оказывающее заметное влияние на жизнь человека и окружающих его людей.\r\nАНО \"АПЦ Рассвет\" - организация из Иркутска, которая защищает права детей и подростков с расстройствами аутистического спектра, включая их в общество, наравне со всеми людьми.', '<p>Аутизм все еще остается непонятным словом для многих людей. Кино и сериалы создали определенный стереотип о людях с аутизмом. Часто их изображают как гениев с необычными способностями, которые не понимают шуток и не любят большие компании.</p>\r\n<p>Аутизм бывает разный! На самом деле, аутизм - это спектр, а не одно состояние.\r\nЭто означает, что у каждого человека свой уникальный набор симптомов и уровень тяжести нарушений.</p>\r\n<p>Кто-то имеет инвалидность и нуждается в постоянной опеке, а кто-то живет с аутизмом всю жизнь и не подозревает об этом, списывая свои особенности на характер.</p>\r\n<p>Бывают взрослые люди с аутизмом, которые не понимают обращенную речь, не разговаривают и могут воспринимать только односложные бытовые слова, а бывают дети, у которых грамотная речь и они любят быть в центре внимания.\r\n</p>\r\n<p>ИНКЛЮЗИИ БЫТЬ!</p>\r\n<p>АНО \"АПЦ Рассвет\" - организация из Иркутска, которая защищает права детей и подростков с расстройствами аутистического спектра, включая их в общество, наравне со всеми людьми.</p>\r\n<p>Мы создаем ресурсные классы в обычных школах, мы адаптируем детей к важным медицинским процедурам, мы обучаем подростков ремесленным навыкам, чтобы в будущем они могли трудоустроится.</p>\r\n<p>Мы обучаем ребят быть самостоятельными с самого детства, обучая их навыкам самообслуживания, чтобы они могли жить максимально независимо.</p>\r\n<p>Сейчас мы идем к тому, чтобы создать в Иркутске условия для сопровождаемого проживания в домашних условиях инвалидов с расстройствами аутистического спектра старше 18 лет, неспособных вести самостоятельный образ жизни без помощи других лиц.</p>\r\n<p>Также для повышения способности людей с расстройствами аутистического спектра к самообслуживанию и удовлетворению основных жизненных потребностей - труд, досуг и общение, во избежании заключения ребят в психоневрологический диспансер до конца жизни после потери родителей или законных опекунов.</p>\r\n<p>СКОРО РАССВЕТ...</p>\r\n<p>10 лет назад Екатерина Родыгина, мама Игоря, столкнулась с проблемами аутизма один на один.\r\nВрачи не диагностировали сыну аутизм, хотя все симптомы были заметны невооруженным взглядом: нарушения коммуникации, социального взаимодействия и стереотипного, повторяющего поведения.</p>\r\n<p>- \"Почему не ставили диагноз, если все очевидно?\"<br>\r\n- \"Проблема в диагностике. Когда весь мир опирается на МКБ-11, наша медицина использует МКБ-10, где представлена устаревшая информация о диагнозе. И на тот момент, когда я столкнулась с данной проблемой, не было понимания, как работать с такой категорией пациентов, поэтому ребята с подобными симптомами получали другие диагнозы, чтобы им могли оказать помощь, оказываемую при других ментальных нарушениях, несмотря на неэффективность применяемых методов\".</p>\r\n<p>Состояние Игоря не позволяло ему нормально развиваться: он не реагировал на речь, не оборачивался на свое имя, не разговаривал.\r\nВ какой-то момент стал боятся лестниц и громких звуков. Спускаясь по ступенькам, он ложился на них, преодолевая их ползком, когда его сверстники уже вовсю прыгали по ним в окружении друзей.\r\nИгорь не мог сказать о своих страхах, он показывал их своим поведением: устраивал жуткие истерики, агрессия была направлена на себя: он мог давиться собственной рвотой, биться головой о стены и пол до кровоподтеков.</p>\r\n<p>Люди вокруг осыпали семью осуждениями за скверный характер сына и за родительскую халатность, обесценивая горе в семье.\r\nДефектологи и логопеды отказывались проводить с ним занятия, так как он истошно плакал и пытался причинить себе вред.</p>\r\n<p>Игорь - иностранец в своей собственной семье, в своем городе и в этом мире.\r\nЕго родители готовы свернуть горы, лишь бы узнать, что происходит с их любимым мальчиком, почему его детство лишено радости, смеха и общения.</p>\r\n<p>Аутизм - это серьезное расстройство, оказывающее заметное влияние на жизнь человека и окружающих его людей.\r\n</p>\r\n<p>Несмотря на гнет несправедливости и безразличия от специалистов, профилирующих на помощи детям с особенностями развития, мама Игоря нашла в себе силы найти других родителей, столкнувшимися с подобными проблемами, и создать родительское сообщество \"Рассвет\", чтобы отныне никто не остался один на один с этим непонятным аутизмом.</p>\r\n<p>ЛЮДЯМ С АУТИЗМОМ МОЖНО ПОМОЧЬ!</p>\r\n<p></p>\r\n<p>Научно обоснованная терапия для людей с расстройствами аутистического спектра на основе науки Прикладной анализ поведения отлично зарекомендовала себя во всем мире.</p>\r\n<p>Основная задача Поведенческой терапии для людей с аутизмом – помочь развить навыки, которые позволят им более успешно функционировать в обществе и достичь большей независимости.</p>\r\n<p>Наша организация подготавливает поведенческих аналитиков, чтобы поведенческая терапия в нашем городе стала доступной для всех.</p>\r\n<p>ХОРОШАЯ ИСТОРИЯ</p>\r\n<p>Самый первый ресурсный класс для ребят с аутизмом появился в Иркутске 5 лет назад на базе общеобразовательной школы МОУ ИРМО СОШ п. Молодежный, благодаря настойчивости родителей, отстаивавших права своих детей на возможность качественного образования.</p>\r\n<p>Сейчас ребята готовятся к переходу в среднее звено.\r\nОни отлично прижились среди нормотипичных сверстников, посещают совместные уроки и перемены, ходят в столовую и на общешкольные праздники.</p>\r\n<p>Одно из мероприятий, которое проводится в апреле, месяце информирования о проблемах аутизма – это «Уроки добра», на которых психологи рассказывают школьникам про расстройства аутистического спектра и знакомят ребят с особенностями поведения нейроотличных людей.</p>\r\n<p>Важно понимать, что инклюзия – это не просто слово. Это действия. Чтобы создать толерантное общество, нужно создавать инклюзивную среду и объединять людей с самого детства.</p>', '/images/news/2024/10/0.jpg', '2024-10-22', '/images/news/2024/10/1.jpg;\r\n/images/news/2024/10/2.jpg;\r\n/images/news/2024/10/3.jpg;\r\n/images/news/2024/10/4.jpg;\r\n/images/news/2024/10/5.jpg;\r\n/images/news/2024/10/6.jpg;\r\n/images/news/2024/10/7.jpg', '', '', 'Новости', '/news.php', 0, '', '0'),
(1008, '3', 'Старт обучающего курса для стоматологов', 'С июня активно идёт адаптация ребят к лечению у стоматолога. Но не только детям нужна наша помощь, врачам также важно знать, как найти подход к ребенку с РАС на приёме. И уже совсем скоро мы запускаем наш адаптированный обучающий курс для стоматологов  ОГАУЗ \"Иркутской городской детской стоматологической поликлиники\". ', '<p>Без паники! Мы совсем скоро пойдём к стоматологу! С июня активно идёт адаптация ребят, чтобы они успешно прошли осмотр, проф. чистку и даже лечение на приеме у стоматологов. И уже есть крутые результаты.</p>\r\n<p>Но не только детям нужна наша помощь, стоматологам также важно знать, как найти подход к ребенку с расстройством аутистического спектра на приёме. И уже совсем скоро мы запускаем наш адаптированный обучающий курс для стоматологов, который поможет врачам освоить все нюансы и тонкости этого важного процесса.</p>\r\n<p>Обучение пройдут наши партнёры — врачи-стоматологи ОГАУЗ \"Иркутской городской детской стоматологической поликлиники\". </p>\r\n<p>Итак, 1 ноября начинаем</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>', '/images/seminar/2024/10.jpg', '2024-10-27', '/images/grants/pgrants_logo.jpg', '', '', 'Новости', '/news.php', 0, '', '11'),
(1009, '18', '\"Неформальные каникулы\" в мастерской', 'На этой неделе мы провели инклюзивный мастер-класс в рамках неформальных каникул!  Невероятно приятно было видеть участие наших учеников и их восторг от процесса работы с воском. Мы вместе плавили воск, заливали его в формы и с нетерпением ждали результатов.', '<p>На этой неделе мы провели инклюзивный мастер-класс в рамках неформальных каникул!  Невероятно приятно было видеть участие наших учеников и их восторг от процесса работы с воском. Мы вместе плавили воск, заливали его в формы и с нетерпением ждали результатов.\r\n</p>\r\n<p>Мы расформовали некоторые из наших работ и готовы показать вам, насколько красивым получился конечный результат.  Посмотрите видео, там в конце будет результат и оцените наши творческие успехи!</p>\r\n<p>Этот мастер-класс был для нас не только возможностью создания красивых изделий, но и важным шагом в направлении инклюзивности и равенства. Мы убеждены, что каждый человек способен раскрыть свой талант и творческие способности, и гордимся тем, что можем поддержать это развитие.</p>\r\n<p>Благодарим всех, кто присоединился к нашему мастер-классу, за вдохновение и энергию!</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239601&hash=5f57c0ba0e1c7345\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2024/20.jpg', '2024-10-31', '', '', '', '', '', 0, '', ''),
(1011, '3', 'Важные знания для педагогов', 'В рамках проекта \"Важные знания - повышение компетенций педагогов, обучающих детей с РАС\" продолжаются стажировки педагогов, прошедших базовый курс Прикладного анализа поведения, на площадках в Иркутске и Новосибирске.', '<p>В рамках проекта \"Важные знания - повышение компетенций педагогов, обучающих детей с РАС\" продолжаются стажировки педагогов, прошедших базовый курс Прикладного анализа поведения, на площадках в Иркутске и Новосибирске.</p>\r\n<p>Стажировки проводят опытные поведенческие аналитики.\r\nПедагоги на практике разбирают методики, которые используются в работе с детьми с РАС: разнообразные инструменты тестирования навыков ребят и основные принципы построения индивидуальных программ развития для учеников, альтернативные системы коммуникаций, способы обучения функциональным навыкам и методики работы с проблемным поведением.</p>\r\n<p>В ходе стажировки педагоги решают практические задачи, направленные на формирование навыков самостоятельности, социального взаимодействия, коммуникации, адаптации к школьной среде у детей с РАС.\r\nНапример, участники стажировки разрабатывали индивидуальные программы обучения, практиковались жетонной системе подкрепления и применяли различные методы визуальной поддержки.</p>\r\n<p>Отзывы участников стажировки АНО \"АПЦ Рассвет\":</p>\r\n<p>\"Спасибо за тренинг, было очень познавательно, интересно и весело! Надеюсь , что удастся ещё присутствовать на других тренингах! Как радостно, что удалось посетить данное мероприятие!\" - Дарья.</p>\r\n<p>\"Спасибо за тренинг, было очень интересно!\" - Екатерина.</p>\r\n<p>\"Очень интересный тренинг! Спасибо, что помогли разобраться в трудностях, которые так часто встречаются в нашей практике\" - Анжелика.</p>\r\n<p>Обучение организовано Ассоциацией «Спектр» в рамках проекта \"Важные знания - повышение компетенций педагогов, обучающих детей с РАС\" при поддержке Фонда президентских грантов.</p>', '/images/seminar/2024/8/1.jpg', '2024-11-04', '/images/grants/pgrants_logo.jpg;\r\n/images/seminar/2024/8/2.jpg;\r\n/images/seminar/2024/8/3.jpg;\r\n/images/seminar/2024/8/4.jpg;\r\n/images/seminar/2024/8/5.jpg;\r\n/images/seminar/2024/8/6.jpg;\r\n/images/seminar/2024/8/7.jpg', '', '', 'Новости', '/news.php', 0, '', ''),
(1012, '4', 'Бесплатные занятия АВА-терапией', 'Семья Андрея была приглашена на занятия в Иркутск после проведения специалистами \"Рассвета\" выездного семинара в Чите. Для Андрея была составлена актуальная программа развития, которую мама и педагоги Андрея смогут реализовать дома в Чите.', '<p>В начале мая команда \"Рассвета\" отправилась в Читу, чтобы выступить на семинаре о Прикладном анализе поведения, поделиться опытом \"Рассвета\" с педагогами и родителями в АВА-терапии учеников с особенностями развития и провести консультации для семей, воспитывающих детей с РАС.</p>\r\n<p>Семья Андрея была приглашена и на занятия в Иркутск, на которых поведенческие аналитики \"Рассвета\" установили с ребенком сотрудничество и протестировали его навыки, чтобы составить актуальную и эффективную программу занятий, которую мама и педагоги Андрюши смогут реализовывать дома в Чите.\r\n</p>\r\n<p>Мама Андрея сама является тьютором для своего ребенка. Её интересовало не только развитие навыков Андрея, но и работа с проблемным поведением. Специалисты \"Рассвета\" рассказали о том, как построить занятие с ребенком, чтобы он шел на них с удовольствием, о способах коррекции поведения и показали, как применять их на практике. </p>\r\n\r\n<p>Проект «Я могу: социализация и обучение жизненно-важным навыкам детей с РАС» реализуется с использованием пожертвования Благотворительного фонда «Абсолют-Помощь».</p>\r\n<p><a href=\"https://rassvet-apc.ru/news/detail.php?id=894\" target=\"_blank\">Подробнее о проекте</a></p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456241324&hash=8aa65994cea25cda\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>                        </section>\r\n', '/images/ABA/2024/28.jpg', '2024-11-06', '/images/Absolut/logo.jpg', '', '', 'Новости', '/news.php', 0, '', '12');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(1013, '4', 'Как помочь ребенку с аутизмом стать более самостоятельным', 'В погоне за речью важно уделять внимание и социально-бытовым навыкам ребенка. \r\nМногие ошибочно полагают, что ребенок с РАС может \"догнать\" сверстников по самостоятельности спустя время. \r\nК сожалению, это не всегда так.  ', '<p>В погоне за речью важно уделять внимание и социально-бытовым навыкам ребенка. \r\nМногие ошибочно полагают, что ребенок с РАС может \"догнать\" сверстников по самостоятельности спустя время. \r\nК сожалению, это не всегда так.  </p>\r\n<p>Как помочь своему ребенку с аутизмом быть более независимым и самостоятельным в будущем, если ему сейчас 3 года? \r\n</p>\r\n<p>Важно понимать, что в мире существуют ребята, которые так и не научились ходить в туалет, на унитаз. Родители, отдав ребенка в детский сад, надеятся, что малыш начнет подражать сверстникам и навыки самообслуживания появятся сами собой. \r\nВремя идет, а подросток так и не начинает переодеваться самостоятельно, помогать накрывать на стол и следить за своим внешним видом. </p>\r\n<p>Кто-то не может отдать ребенка в школу лишь потому, что несмотря на хороший уровень академических навыков, ребенок просто не может самостоятельно перемещаться по школе. \r\n</p>\r\n<p> Итак, ребенку 3 года. Сфокусируйтесь на простых и повседневных вещах, навыках самообслуживания: переодевание, навыки приема пищи, умывание, чистка зубов, мытье рук. Минимальная уборка своего игрового места, простые поручения о помощи: что-то принести/убрать. </p>\r\n<p>Вы можете сталкиваться с сопротивлением со стороны ребенка. Истерики, крики, затяжной плачь и даже агрессия. Тут поможет исключительно рутина и понятные для ребенка подсказки, ведь мы помним, что ребенок может не понимать обращенную речь, не понимать указательный жест, а ваше приближение вообще расценивать как опасность.  </p>\r\n<p>Отсюда вытекает еще один очень важный пункт - понять потребности своего ребенка. Адекватно оценить его сильные и слабые стороны, уровень его навыков.  \r\nИ исходя из потребностей ребенка ставить цели. </p>\r\n<p>\r\n<blockquote align=\"left\">\"Сейчас ему 3. Его руки постоянно заняты какими-то детальками, игрушками. Он совершенно не умеет и не хочет учиться переодеваться. Как только я пробую забрать детальки, чтобы он натянул на себя штаны, ор стоит на всю квартиру.\"</blockquote></p>\r\n<p>а) Учимся расставаться с игрушками: нормально реагировать, когда мама трогает вещь и забирает ее на 1 секунду, 5 секунд, 10 секунд.  </p>\r\n<p>б) Добавляем требование в момент расставания с игрушкой: например, натянуть штанишки после туалета, когда они находятся чуть ниже колен. Когда у ребенка начнет получаться, штанишки опущены все ниже и ниже. </p>\r\n<p>в) Анализируем цепочку переодевания и обучаем маленькими шагами, поощряя ребенка за старания. </p>\r\n<p>Для каждого человека понятие «самостоятельность» раскрывается по-своему. Для детей разработаны примерные возрастные критерии оценивания навыков самообслуживания, по которым и вы можете в домашних условиях оценить умения своего ребенка, чтобы подобрать для него функциональные цели для обучения.</p>\r\n<p>Обзор инструментов для оценки социально-бытовых навыков ребенка можно найти <a href=\"https://rassvet-apc.ru/events/detail.php?id=849&back=news\" target=\"_blank\">ЗДЕСЬ</a></p>\r\n<p>Проект «Я могу: социализация и обучение жизненно-важным навыкам детей с РАС» реализуется с использованием пожертвования Благотворительного фонда «Абсолют-Помощь».</p>\r\n<p><a href=\"https://rassvet-apc.ru/news/detail.php?id=894\" target=\"_blank\">Подробнее о проекте</a></p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456241334&hash=d5302d7e32ad021a\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>                        </section>\r\n', '/images/ABA/2024/29.jpg', '2024-11-11', '/images/Absolut/logo.jpg', '', '', 'Новости', '/news.php', 0, '', '12'),
(1014, '12,19', 'Уроки физкультуры для учеников с РАС', 'Как проходит урок физкультуры у 1 класса, где все ученики с расстройствами аутистического спектра? Урок немного отличается от физкультуры у обычных классов, он адаптирован под особые образовательные потребности учеников.\r\n', '<p>Как проходит урок физкультуры у 1 класса, где все ученики с расстройствами аутистического спектра?</p>\r\n<p>Урок немного отличается от физкультуры у обычных классов, он адаптирован под особые образовательные потребности учеников.\r\nКак можете заметить на видео, упражнения, которые выполняют ребятишки, функциональные и комплексные, всем знакомые.\r\nНо обратите внимание, что в зале много взрослых - это тьюторы. Индивидуальные помощники детей, которые рекомендуются деткам психолого-педагогической комиссией, когда они поступают в учебное заведение.</p>\r\n<p>Так же важно обратить внимание на подход учителя к ребятам. Это одна из самых важных составляющих.\r\nУчитель физкультуры МБОУ г.Иркутска СОШ №17 - Юрий Иванович, прошел стажировку в нашем центре, вместе с другими педагогами иркутских школ, в которых действуют ресурсные классы.</p>\r\n<p>Все задания к ребятам обращены короткими и понятными инструкциями, речь учителя не перегружена - для ребят с аутизмом это важно.\r\nЮрий Иванович в работе используют жетоны, по которым ребята ориентируются, сколько еще упражнений и подходов им нужно выполнить до тайм-аута.\r\nВизуальные подсказки используются, чтобы снизить тревожность ребят.</p>\r\n<p>По ходу урока деткам предоставляются короткие переменки, чтобы они могли восстановиться.\r\nТакой подход снижает проблемное поведение учеников. Дети могут вокализировать, кричать из-за перегрузки нервной системы. Перегружаются ученики не столько от физической нагрузки, сколько от шума, других сенсорных раздражителей или быстрого темпа смены упражнений, в котором они не всегда успевают переключаться.</p>\r\n<p>Ну и что скрывать, Юрий Иванович - любимец ребят!\r\nДети любят и уважают его за доброту, открытость и понимание.\r\nКогда преподаватель использует игровой подход, не осуждает за поведение или нарушение дисциплины - это настоящий ключик к дружбе между учителем и учеником.</p>\r\n<p>Напомним, что наш проект «Расскажи о спорте» стал победителем конкурса Фонда Потанина «Спорт для всех»</p>\r\n<p><a href=\"/news/detail.php?id=895\" target=\"_blank\">Подробнее о проекте</a></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456241338&hash=88d7a076b6b21f9b\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen class=\"social-video\"></iframe>\r\n</section>', '/images/AFK/2024/6.jpg', '2024-11-13', '', '', '', 'Новости', '/news.php', 0, '', '16'),
(1015, '18', 'Символ 2025 года в нашей мастерской', 'Наша инклюзивная мастерская для детей с аутизмом с удовольствием представляет вам новинку — свечки \"Змейки\"! Эти волшебные свечки станут отличным дополнением к вашему новогоднему подарку и непременно создадут уютную атмосферу в доме.', '<p>Новогодние подарки — это всегда волшебство и радость! А что может быть лучше, чем подарок, который не только порадует вашего близкого, но и поможет другим детям?</p>\r\n<p>Наша инклюзивная мастерская для детей с аутизмом с удовольствием представляет вам новинку — свечки \"Змейки\"! Эти волшебные свечки станут отличным дополнением к вашему новогоднему подарку и непременно создадут уютную атмосферу в доме.</p>\r\n<p>Заказав у нас эти свечки, вы не только порадуете себя или близкого, но и поможете нашей мастерской в закупке материалов для занятий. Это значит, что вы поддержите нашу инициативу и поможете сохранить возможность проведения бесплатных занятий для детей с особенными потребностями.</p>\r\n<p>Давайте вместе сделаем мир ярче и добрее! Закажите свечку \"Змейка\" у нас и внесите свой вклад в поддержку детей с аутизмом. Спасибо, что вы с нами! </p>\r\n<p>АНО \"АПЦ РАСсвет\" - участник проекта Ассоциации «Аутизм-Регионы» «Полезная занятость с применением практик и методов с научно доказанной эффективностью для молодых людей с РАС» при поддержке Фонда президентских грантов.</p>', '/images/DSS/2024/22.jpg', '2024-11-16', '', '', '', '', '', 0, '', ''),
(1016, '19', 'Важные шаги в развитии ресурсных классов', 'В этом месяце мы совершили важные шаги в развитии наших ресурсных классов! Встречи с учителем-дефектологом Михеевой Е.С. были насыщены знаниями и новым опытом в области обучения детей с РАС. ', '<p>В этом месяце мы совершили важные шаги в развитии наших ресурсных классов!</p>\r\n<p>Встречи с учителем-дефектологом на базе МОУ ИРМО \"СОШ п.Молодежный\", МОУ \"СОШ Nº17\" г.Иркутска, МОУ \"СОШ Nº8\", МОУ ИРМО \"Марковская СОШ Nº2\", были насыщены знаниями и новым опытом в области обучения детей с РАС. Педагоги узнали об особенностях подхода и методах работы с детьми с аутизмом, что поможет им в дальнейшей работе.</p>\r\n<p>Каждому участнику мы вручили раздаточный материал в виде листовок о красных флагах аутизма и эффективных методах обучения детей с РАС. Эти материалы не только визуализировали информацию, но и помогли структурировать знания, полученные на выступлениях.\r\n</p>\r\n<p>Мы убеждены, что знание и понимание особенностей детей с РАС делает нашу образовательную среду более инклюзивной и дружелюбной для каждого ребёнка. Продолжаем двигаться вперёд вместе!</p>\r\n<p>Проект реализуется при поддержке «Губернского собрания общественности Иркутской области»</p>\r\n', '/images/RK/2024/12/1.jpg', '2024-11-18', '/images/grants/GS.png;\r\n/images/RK/2024/12/2.jpg;\r\n/images/RK/2024/12/3.jpg', '', '', '', '', 0, '', '22'),
(1017, '20', 'Тревога у людей с аутизмом', 'У людей с аутизмом в два раза чаще, чем у нейротипичных представителей популяции, диагностируется тревожное расстройство.\r\nНо далеко не все люди с аутизмом, имеющие тревожные расстройства, диагностированы.', '<p>У людей с аутизмом в два раза чаще, чем у нейротипичных представителей популяции, диагностируется тревожное расстройство. Далеко не все люди с аутизмом, имеющие тревожные расстройства, диагностированы. Причина этому - коммуникативные нарушения, обусловленные диагнозом, и отсутствие возможности выражать абстрактные понятия (такие, как чувства, эмоции, мысли).</p>\r\n<p>Из диалога с учеником:</p>\r\n<p>Ярик: А что такое тревога?</p>\r\n<p>Даша: Это такое состояние, когда ты чувствуешь себя неспокойно, взбудораженно. У тебя бывает такое?</p>\r\n<p>Ярик: Да, бывает.</p>\r\n<p>Даша: Как ты с этим справляешься?</p>\r\n<p>Ярик: С тревогой я справляюсь: гуляю и чем-нибудь занимаюсь. Так и успокаиваюсь.</p>\r\n<p>Может ли тревожность приводить к поведенческим проблемам?</p>\r\n<p>Тревожность скорее повышает вероятность, что ситуация приведет к поведенческим проблемам, но она не является непосредственной причиной поведения. Очень часто это объясняет, почему некоторые дети прекрасно справляются с ситуацией в один день, но не в другой: их тревожность была повышенной.</p>\r\n<p>На занятиях со школьниками мы уделяем большое внимание работе с навязчивым и обсессивно-компульсивным поведением, которое становится преградой в обучении ребят.</p>\r\n<p>Отследить динамику помогает тестирование VB-MAPP, раздел преград обучения.</p>\r\n<p>3 практических совета:</p>\r\n<p>1. Для многих ребят предсказуемость помогает справится с тревогой. Всевозможные визуальные подсказки: планеры, расписания, картинки, списки.</p>\r\n<p>2. Суть в том, что сама жизнь непредсказуема. Для ребенка, чье благополучие зависит от ритуалов и маршрутов, сложно спокойно принять отступление от задуманного плана, порой известного только самому ребенку.</p>\r\n<p>Помогайте ребенку принимать новые правила, создавая для него различные «проблемки». Они должны быть маленькими, еле заметными, но они будут десенсибилизировать ребенка к ситуациям, которые могут вызвать панику. Будьте рядом, проявляйте сочувствие, учитесь находить компромиссы.</p>\r\n<p>3. Не обесценивайте тревожные состояния ребенка. Если произошла ситуация, к которой ребенок еще не был готов, проявите эмпатию, скажите ребенку, что вы его понимаете, что это действительно неприятная ситуация. Выслушайте, не делая оценок (повторяйте и перефразируйте слова ребёнка, тем самым показывая, что вы его понимаете). </p>\r\n<p>Проект реализуется при поддержке «Губернского собрания общественности Иркутской области»</p>\r\n', '/images/ABA/2024/5/1.jpg', '2024-11-21', '/images/grants/GS.png;\r\n/images/ABA/2024/5/2.jpg;\r\n/images/ABA/2024/5/3.jpg', '', '', '', '', 0, '', '22'),
(1018, '18', 'Подарки для мам', 'Дорогие друзья! Скоро наступит самый нежный и теплый праздник - День матери! Мы с ребятами сделали что-то по-настоящему особенное - сшили прихватки для своих мамочек своими собственными руками! Не правда ли, это замечательно?', '<p>Дорогие друзья! Скоро наступит самый нежный и теплый праздник - День матери! И в этот прекрасный день мы хотим порадовать наших самых родных и любимых женщин - наших мамочек.\r\n</p>\r\n<p>Мы с ребятами сделали что-то по-настоящему особенное - сшили прихватки для своих мамочек своими собственными руками! Не правда ли, это замечательно? Наши ребята - настоящие таланты, их усердие и творческий подход достойны восхищения!</p>\r\n<p>По этому поводу мы хотим поздравить всех мамочек с наступающим Днем матери!  Мы благодарим вас за вашу нежность, заботу и любовь, которую вы дарите своим детям каждый день. Вы - наши настоящие герои, наши опоры и наши ангелы.</p>\r\n<p>Хотим поделиться с вами видео, чтобы вы сами могли увидеть, с каким умением и любовью наши ребята творили эту маленькую, но такую значимую для них вещицу.</p>\r\n<p>Спасибо вам, что остаетесь с нами, что поддерживаете наших детей и нашу инклюзивную мастерскую. Мы рады, что можем вместе делать добрые дела и делиться теплом и радостью.</p>\r\n<p>С наступающим Днем матери! Пусть ваш день будет наполнен любовью, счастьем и улыбками!</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239614&hash=d09cfe8eb9e167f9\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2024/23.jpg', '2024-11-23', '', '', '', '', '', 0, '', ''),
(1019, '19', 'Перемена для учеников с РАС', 'Для большинства наших учеников провести переменку в компании сверстников крайне трудная задача. Иногда дети сами не знают, как бы они хотели провести свое свободное время. Мы пробуем многое и оставляем то, что вызывает у ребят больше всего эмоций. ', '<p>Для большинства наших учеников провести переменку в компании сверстников крайне трудная задача. Иногда дети сами не знают, как бы они хотели провести свое свободное время.</p>\r\n<p>а) Провести время в одиночестве</p>\r\n<p>Иногда ребенку действительно нужно время побыть наедине с собой и перезагрузится, но не стоит расстраиваться из-за того, что это единственный вид времяпровождения. Часто дети проводят свое свободное время именно таким образом, потому что у них не сформированы навыки самозанятости и игровые навыки.</p>\r\n<p>б) Структурированные перемены</p>\r\n<p>Когда детки понимают, как можно играть, часто они подключаются к какой-либо групповой деятельности. Они могут привносить свои правила, играть не в команде, а параллельно, но тем не менее - включается функциональная деятельность, которая направлена на развитие навыков коммуникации.</p>\r\n<p>Перемена - это отличный способ \"сбросить\" физическое и эмоциональное напряжение, скопившееся за урок, но не стоит превращать это время в дополнительную физкультуру. Если тьюторы детей будут обучены стратегиям, которые можно применить во время перемены, детям будет легче социально взаимодействовать между собой.</p>\r\n<p>Как организовать структурированную перемену?</p>\r\n<p>1. Обучение простым совместным играм<br>\r\n2. Составьте расписание перемены, чтобы работа взрослых была более эффективна, а детки сами могли выбирать более предпочтительную для них деятельность, помогая составлять вам расписание<br>\r\n3. Предоставляйте поддержку коммуникации ребятишкам, помогая им взаимодействовать друг с другом в игре и проявлять инициативу.</p>\r\n<p>Во что играть?</p>\r\n<p>Нам в работе помогает то, что мы пробуем многое и оставляем то, что вызывает у ребят больше всего эмоций. Это как классические подвижные игры, настолки, так и игры, которые подходят конкретно под интересы ребят.</p>\r\n<p>Проект реализуется при поддержке Благотворительного фонда\r\n\"Вклад в будущее\".</p>\r\n\r\n', '/images/RK/2024/13/1.jpg', '2024-11-25', '/images/grants/VK_VB.jpg;\r\n/images/RK/2024/13/2.jpg;\r\n/images/RK/2024/13/3.jpg;\r\n/images/RK/2024/13/4.jpg;\r\n/images/RK/2024/13/6.jpg;\r\n/images/RK/2024/13/7.jpg;\r\n/images/RK/2024/13/5.jpg', '', '', 'Новости', '/news.php', 0, '', '21'),
(1020, '19', 'Лекция для студентов Иркутского регионального колледжа педагогического образования', 'Студенты, посещая практику в общеобразовательных школах, все чаще стали замечать особенных ребят в обычных классах. Это и стало основным запросом преподавателей Иркутского регионального колледжа педагогического образования.', '<p>Студенты, посещая практику в общеобразовательных школах, все чаще стали замечать особенных ребят в обычных классах.</p>\r\n<p>Это и стало основным запросом преподавателей Иркутского регионального колледжа педагогического образования – получить качественную информацию об учениках с аутизмом и узнать, какие инструменты может применять учитель начальных классов, чтобы помочь ребенку с особыми образовательными потребностями качественно и комфортно получать образование.</p>\r\n<p>Да, действительно, интеграция детей с аутизмом в обычные классы становится всё более распространённой практикой в образовательной системе.\r\nЭто связано с рядом факторов, включая повышение осведомлённости о расстройствах аутистического спектра, развитием инклюзивного образования и стремлением обеспечить равные возможности для всех детей.</p>\r\n<p>При этом важно, чтобы студенты и учителя были подготовлены к работе с такими детьми.\r\nЭто включает в себя знание специфических методов обучения, понимание потребностей детей с аутизмом и умение адаптировать учебный процесс. Работа с детьми с аутизмом в классе требует особого подхода и использования различных инструментов.</p>\r\n<p>Рекомендации от учителя ресурсного класса пос. Молодежный, Марии Максимовой, которые могут помочь учителям начальных классов:</p>\r\n<p>&#9989; Индивидуальный подход</p>\r\n<p>Каждый ребенок с аутизмом уникален, поэтому важно адаптировать материалы, методы обучения и подходы к каждому ребенку в зависимости от его потребностей.</p>\r\n<p>&#9989; Визуальные поддерживающие материалы</p>\r\n<p>Использование картинок, схем, графиков и других визуальных элементов может помочь детям лучше понять информацию и организовать их мысли.\r\nНапример, использование расписаний и карточек с изображениями для объяснения распорядка дня.</p>\r\n<p>&#9989; Структурирование учебного процесса</p>\r\n<p>Создание четкой и предсказуемой структуры уроков помогает детям чувствовать себя более уверенно и снизить уровень тревожности.</p>\r\n<p>&#9989; Альтернативная коммуникация</p>\r\n<p>Применение системы альтернативной коммуникации предоставляет возможность выражать свои потребности, что особенно важно для учеников, которые испытывают трудности с вербальной речью. Альтернативная коммуникация помогает снизить уровень тревожности и позволяет ученику участвовать в занятиях, задавать вопросы и получать информацию, что способствует общему образовательному прогрессу.</p>\r\n<p>&#9989; Использование сенсорных инструментов</p>\r\n<p>Обеспечьте тихие зоны, где ученик может отдохнуть и восстановить силы, если он чувствует себя перегруженным. Предоставьте возможность использовать различные сенсорные игрушки или устройства (например, мяч-антистресс, шумопоглощающие наушники, балансир и пр. ), которые помогут ученику справляться с сенсорной перегрузкой.</p>\r\n<p>&#9989; Позитивное подкрепление</p>\r\n<p>Использование системы поощрений за достижения и соблюдение правил может мотивировать детей и способствовать положительному поведению.</p>\r\n<p>&#9989; Обучение социальным навыкам</p>\r\n<p>Включение занятий, направленных на развитие социальных навыков, таких как работа в команде, обмен мнениями и совместное решение проблем.</p>\r\n<p>Важно помнить, что терпение и понимание со стороны учителя играют ключевую роль в создании поддерживающей и инклюзивной образовательной среды для всех учеников.</p>\r\n<p></p>\r\n<p>Отзывы студентов читайте на наших карточках &#129179;</p>\r\n<br>\r\n<p></p>\r\n<div class=\"help-achievements-list\">\r\n<div class=\"help-achievement\">\r\n<img class=\"help-achievement-image\" src=\"/images/RK/2024/14/6.jpg\" alt=\"\" />\r\n</div>\r\n<div class=\"help-achievement\">\r\n<img class=\"help-achievement-image\" src=\"/images/RK/2024/14/7.jpg\" alt=\"\" />\r\n</div>\r\n<div class=\"help-achievement\">\r\n<img class=\"help-achievement-image\" src=\"/images/RK/2024/14/8.jpg\" alt=\"\" />\r\n</div>\r\n<div class=\"help-achievement\">\r\n<img class=\"help-achievement-image\" src=\"/images/RK/2024/14/9.jpg\" alt=\"\" />\r\n</div>\r\n				\r\n</div>', '/images/RK/2024/14/1.jpg', '2024-11-27', '/images/RK/2024/14/2.jpg;\r\n/images/RK/2024/14/3.jpg;\r\n/images/RK/2024/14/4.jpg;\r\n/images/RK/2024/14/5.jpg', '', '', 'Новости', '/news.php', 0, '', ''),
(1021, '10', 'Проект «Коммуникативная продленка»', 'Статус проекта: действующий<br>\r\nСроки реализации: 01.09.2024 - 30.06.2025<br>\r\nИсточник софинансирования: Губернское собрание общественности Иркутской области\r\n', '<p><b>Статус проекта:</b> действующий<br>\r\n<b>Сроки реализации:</b> 01.09.2024 - 30.06.2025<br>\r\n<b>Источник софинансирования:</b> Губернское собрание общественности Иркутской области</p>\r\n<p></p>\r\n<p><b>Цель проекта:</b></p>\r\n<p>Помочь 25 детям с РАС, учащимся в начальных классах (1–4 класс) общеобразовательных школ Иркутска и Иркутского района, использовать альтернативные социально приемлемые способы коммуникации и общения при взаимодействии со сверстниками.</p>\r\n<p><b>Задачи:</b></p>\r\n<p>1. Снизить уровень тревожности у учеников с РАС при общении с участниками образовательного процесса и посещении общешкольных мероприятий.</p>\r\n<p>2. Познакомить учеников и педагогов общеобразовательных классов с особенностями детей с РАС и способами общения и коммуникации.</p>\r\n<p>3. Обучить учеников с РАС альтернативным социально приемлемым способам коммуникации со сверстниками и педагогами общеобразовательных классов.</p>\r\n<p>4. Привлечь учеников общеобразовательных классов и детей с РАС к совместной игровой деятельности.</p>\r\n<p><b>Краткое описание:</b></p>\r\n<p>Проект нацелен на обучение 25 детей с РАС, учащихся в начальных классах (1–4 класс) общеобразовательных школ Иркутска и Иркутского района, умению общаться и играть со сверстниками, а также использовать альтернативные социально приемлемые способы коммуникации при взаимодействии с учениками общеобразовательных классов. </p>\r\n<p>Для определения начального уровня тревожности и интенсивности нежелательного поведения у учеников с РАС мы предварительно проведем \"оценку преград\" по VB-MAPP, для определения начальных игровых и групповых навыков проведем \"оценку переходов\" по VB-MAPP. По результатам тестирования составим индивидуальные программы коррекции нежелательного поведения и формирования навыков коммуникации, изготовим средства визуальной поддержки и альтернативной коммуникации и организуем индивидуальные и групповые занятия. </p>\r\n<p>На индивидуальных занятиях с детьми с РАС пройдет обучение навыкам, работа с которыми затруднена в рамках школы, в результате чего у ребят снизится уровень тревожности при общении, уменьшится количество эпизодов нежелательного поведения, улучшились навыки коммуникации. На занятиях в коммуникативных группах у детей с РАС улучшатся просьбы, ответы на вопросы, толерантность к отказу, дети научатся следовать простым правилам настольных и активных игр, тем самым подготовятся к дальнейшему взаимодействию со сверстниками на переменах. Успешность формирования навыков отразится на контрольном тестировании.</p>\r\n<p>200 педагогов и 360 нейротипичных учащиеся 4х общеобразовательных классов г.Иркутска и Иркутского района узнают об особенностях детей с РАС и способах коммуникации с ними на научно-практических конференциях и уроках добра,викторине. После чего ученики с РАС и их нейротипичные сверстники попрактикуют взаимодействие друг с другом на творческих мастер-классах. На занятиях в клубе общения ученики начальных классов с РАС, научатся демонстрировать просьбу сверстникам, задавать и отвечать на вопросы, играть совместно в простую настольную игру. После организованных активных игр на переменах ученики общеобразовательных классов станут обращать внимание на детей с РАС и привлекать их к совместным активным играм на переменах. Таким образом, ученики ресурсных классов и общеобразовательных классов приобретут положительный опыт взаимодействия друг с другом, что благоприятно отразится на социализации детей с РАС и поможет ребятам общеобразовательных классов относиться с пониманием к особенностям людей с ограниченными возможностями здоровья и научиться взаимодействовать и помогать ребенку с РАС.</p>\r\n<p><a href=\"/news.php?project=22\" class=\"button\">Публикации о реализации проекта</a></p>', '/images/grants/GS.png', '2024-09-19', '', '', '', 'Гранты', '/pages/grants.php', 0, '', ''),
(1022, '4', 'Учим распознавать социальные ситуации', 'Ярослав отлично понимает обращенную речь и умеет вести диалог. Одной из целей занятий было научить Ярика различить, как вести себя в тех или иных социальных ситуациях в общении с другими людьми.\r\n', '<p>Буллинг в школе - тема, которая требует особого внимания и заботы со стороны родителей и педагогов. Недавно мы с коллегами провели интенсивные занятия с Ярославом, чтобы обсудить эту проблему и научить его навыкам эффективного общения.\r\n</p>\r\n<p>Ярослав отлично понимает обращенную речь и умеет вести диалог. Одной из целей занятий было научить Ярика различить, как вести себя в тех или иных социальных ситуациях в общении с другими людьми.\r\nВместе мы сняли несколько ситуаций, связанных с насмешками со стороны одноклассников и спросили у Ярослава, круто ли это или нет?</p>\r\n<p>В некоторых случаях Ярослав отвечал неверно, не понимая, что показываемые ситуации неправильные. Но мы не оставляли его в неведении и рассказывали и подробно объясняли, почему так не стоит делать.\r\n</p>\r\n<p>Ярослав теперь имеет представление, как поступать в тех или иных случаях, связанных с общением со сверстниками. Он научился распознавать ситуации, в которых он может стать жертвой буллинга, и реагировать адекватно, не поддаваясь на провокации.</p>\r\n<p>По окончании интенсивных занятий мы дали маме рекомендации по поводу того, что не только Ярик должен различать, какие ситуации «хорошие», а какие «нехорошие», но и учитель должен выступить в роли наставника, который поможет распознать мотив других ребят. В школе учителя должны стать главными помощниками в этом непростом и сложном деле, как буллинг.</p>\r\n<p>Проект реализуется при поддержке Благотворительного фонда «Вклад в будущее».\r\n</p>\r\n<p><a href=\"/news/detail.php?id=938\" target=\"_blank\">Подробнее о проекте</a></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456241357&hash=88b345a56f4dbabb\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/ABA/2024/30.jpg', '2024-11-29', '/images/grants/VK_VB.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', '21'),
(1023, '16', 'Рассвет на \"НЕконференции\" в Москве', 'В Москве прошла необычная \"НЕконференция\", организованная Ассоциацией «Аутизм-Регионы»! 26-28 ноября встретились родители детей с аутизмом, а также эксперты в области РАС, чтобы поделиться опытом, обсудить актуальные проблемы и найти решения вместе. ', '<p>В Москве прошла необычная \"НЕконференция\", организованная Ассоциацией «Аутизм-Регионы»! \r\n</p>\r\n<p>26-28 ноября встретились родители детей с аутизмом, а также эксперты в области РАС, чтобы поделиться опытом, обсудить актуальные проблемы и найти решения вместе. Вместо скучных лекций — только живое общение и искренняя поддержка!</p>\r\n<p>На #НЕконференции было проведено несколько интересных сессий. В проблемной сессии обсуждалась тема \"Ресурсный класс\" и внедрение этой модели в государственную систему. В секции \"Поведение — не приговор\" обсуждались важные вопросы об организации социальных сервисов и системы поддержки детей с тяжелыми нарушениями поведения.</p>\r\n<p>Еще нам удалось показать результаты работы нашей инклюзивной мастерской \"Дом синего слона\". И не поверите изделия, созданные нашими ребятами, поразили всех участников своей креативностью и уникальностью. Некоторые гости даже стали счастливыми обладателями наших изделий.</p>\r\n<p>Такие мероприятия помогают объединить родителей, педагогов и специалистов в области аутизма, чтобы вместе работать над созданием более поддерживающей среды для детей с особенностями развития.</p>\r\n<p>Организатор НЕконференции - Ассоциация «Аутизм-Регионы», соорганизатор - Аутизм-Регионы. Инклюзия</p>\r\n<p>Генеральный партнер НЕконференции - Благотворительный фонд «Вклад в будущее».</p>\r\n\r\n', '/images/news/2024/11/1.jpg', '2024-12-02', '/images/news/2024/11/2.jpg;\r\n/images/news/2024/11/3.jpg;\r\n/images/news/2024/11/4.jpg;\r\n/images/news/2024/11/5.jpg;\r\n/images/news/2024/11/6.jpg', '', '', 'Новости', '/news.php', 0, '', '0'),
(1026, '4', 'Подготовка ребенка с РАС к процедуре ЭЭГ', 'Игорю предстояло пойти в больницу, чтобы по показанию врача пройти процедуру электроэнцефалографии (ЭЭГ). У Игоря РАС, он очень тревожится, когда ему предстоит сделать что-то подобное, а объяснить ребенку со сниженным пониманием речи необходимость этого обследования практически невозможно.', '<p>Игорю предстояло пойти в больницу, чтобы по показанию врача пройти процедуру электроэнцефалографии (ЭЭГ). Это совершенно безболезненная процедура, но обычно детей пугает оборудование, которое выглядит так, как-будто сейчас собираются пытать... \r\n</p>\r\n<p>У Игоря расстройство аутистического спектра, он очень тревожится, когда ему предстоит сделать что-то подобное, может возникнуть агрессивное поведение. Игорь почти не разговаривает, а понимание речи снижено, отсюда возникают проблемы - ему достаточно сложно объяснить необходимость этого обследования.</p>\r\n<p>На занятиях мы готовили Игоря ко встрече с этим оборудованием, чтобы в больнице он позволил провести ЭЭГ.\r\n</p>\r\n<p>&#127919; Цель - ребенок позволит надеть на себя медицинское оборудование и провести процедуру электроэнцефалографии (по показанию врача) без проблемного поведения.</p>\r\n<p>Мы заранее знакомим ребят с специфическими особенностями проведения процедуры, оборудованием и новыми ощущениями, используя индивидуальный подход к детям, учитывая их особенности. Например, с невербальными учениками мы используем различные визуальные подсказки.</p>\r\n<p>Родители детей с тяжелыми нарушениями поведения поймут как это не просто, но это не невозможно. Методы прикладного анализа поведения помогают проработать такие сложные моменты, как фобии и повышенную тревожность к некоторым стимулам.</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456241358&hash=f699c973e74f427a\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/ABA/2024/31.jpg', '2024-12-06', '', '', '', 'Новости', '/news.php', 0, '', ''),
(1027, '2', 'Волшебство для каждого!', 'Новогодние праздники – это особенное время, когда каждый ребенок мечтает о чуде. В этом году мы посетили новогоднюю ёлку в игровой комнате, где их ждал настоящий Дед Мороз. Дед Мороз приехал лично, чтобы подарить детям волшебный момент. ', '<p>Новогодние праздники – это особенное время, когда каждый ребенок мечтает о чуде. А для наших ребят такие мероприятия могут стать настоящим событием, наполненным радостью и удивлением!\r\n</p>\r\n<p>В этом году мы посетили новогоднюю ёлку в игровой комнате, где их ждал настоящий Дед Мороз. Дед Мороз приехал лично, чтобы подарить детям волшебный момент. Его добрая улыбка и искренние поздравления растопили сердца всех присутствующих. Каждый из ребят получил свой подарок – символ тепла и заботы от самого зимнего волшебника.</p>\r\n<p>Атмосфера радости и удивления царила повсюду. Дети смеялись, играли и наслаждались каждым мгновением этого чудесного дня. Для многих из них такие мероприятия – редкая возможность выйти за рамки привычного мира и окунуться в атмосферу праздника.</p>\r\n<p>Проект реализуется при поддержке Благотворительного фонда «Вклад в будущее».\r\n</p>\r\n<p><a href=\"/news/detail.php?id=938\" target=\"_blank\">Подробнее о проекте</a></p>', '/images/dosug/2024/3/1.jpg', '2024-12-21', '/images/grants/VK_VB.jpg;\r\n/images/dosug/2024/3/2.jpg;\r\n/images/dosug/2024/3/3.jpg;\r\n/images/dosug/2024/3/4.jpg;\r\n/images/dosug/2024/3/5.jpg;\r\n/images/dosug/2024/3/6.jpg;\r\n/images/dosug/2024/3/7.jpg;\r\n/images/dosug/2024/3/9.jpg;\r\n/images/dosug/2024/3/8.jpg', '', '', 'Новости', '/news.php', 0, '', '21'),
(1029, '19', 'Зачем нужны уроки добра?', 'Уроки добра в школах – это важная часть образовательного процесса, которая помогает детям лучше понимать окружающий мир и учиться жить в гармонии с другими. Сегодня мы хотим поговорить о том, как наши школы становятся местом, где дети учатся принимать и уважать друг друга.', '<p>Уроки добра в школах – это важная часть образовательного процесса, которая помогает детям лучше понимать окружающий мир и учиться жить в гармонии с другими. Сегодня мы хотим поговорить о том, как наши школы становятся местом, где дети учатся принимать и уважать друг друга, независимо от особенностей развития.</p>\r\n<p>Дети с аутизмом могут испытывать трудности в понимании эмоций других людей, установлении контактов и взаимодействии с окружающим миром. Однако каждый ребенок уникален, и у каждого есть свои сильные стороны и таланты.</p>\r\n<p>Зачем нужны уроки добра?<br>\r\nНа уроках добра мы рассказываем ученикам из обычных классов о том, что каждый человек особенный и имеет право на уважение и понимание. Мы объясняем, что дети с аутизмом могут вести себя иначе, но это не значит, что они хуже или лучше остальных. Уроки добра помогают создать атмосферу принятия и поддержки, где каждый ученик может чувствовать себя комфортно и безопасно.\r\n</p>\r\n<p>Как проходят уроки добра?<br>\r\nУчителя наших ресурсных классов проводят интерактивные занятия, где ученики могут задать вопросы и узнать больше о том, как живут дети с аутизмом. Мы организуем встречи и с самими ребятами из ресурсных классов, чтобы все могли познакомиться и найти общие интересы.</p>\r\n<p>Почему это важно?<br>\r\nУроки добра способствуют развитию эмпатии и толерантности среди учеников. Они учат детей быть открытыми и дружелюбными, помогать другим и находить общий язык. Такие занятия формируют основу для создания инклюзивного общества, где каждый человек ценится и уважается.</p>\r\n<p>Проект реализуется при поддержке «Губернского собрания общественности Иркутской области»</p>\r\n', '/images/RK/2024/15/1.jpg', '2024-12-17', '/images/grants/GS.png;\r\n/images/RK/2024/15/2.jpg;\r\n/images/RK/2024/15/4.jpg;\r\n/images/RK/2024/15/5.jpg;\r\n/images/RK/2024/15/3.jpg', '', '', '', '', 0, '', '22'),
(1030, '4', 'Помогатор для ребенка с РАС', 'Визуальное расписание - система, которая наглядно показывает ребенку, что и когда будет происходить. Оно помогает развить у ребёнка самостоятельность, снизить тревогу. Но чтобы визуальное расписание работало, нужно помочь ребенку научиться им пользоваться.', '<p>Зрительная поддержка помогает детям с РАС концентрироваться и удерживать внимание, облегчает понимание времени, последовательности, причинно-следственных связей. </p><p>Визуальное расписание - система, которая наглядно показывает ребенку, что и когда будет происходить. Оно помогает развить у ребёнка самостоятельность, предупредить его об изменениях, снизить тревогу, упростить переход от одной деятельности к другой и помочь обобщить навыки.</p>\r\n<p>Но чтобы визуальное расписание работало, нужно помочь ребенку научиться им пользоваться.</p>\r\n<p>1️⃣ Выбираем доступный для понимания вид визуального расписания: пиктограммы, фотографии, списки, предметы и т.д.</p>\r\n<p>2️⃣ Разбиваем задачи на легкие мелкие части и работаем над ними не долго, ПОСТЕПЕННО (опираясь на результаты конкретного ребенка) усложняем и увеличиваем их.</p>\r\n<p>3️⃣ Превращаем привычные задачи в часть ежедневного расписания.</p>\r\n<p>4️⃣ Награждаем ребенка предпочитаемыми занятиями, которые тоже становятся частью расписания.</p>\r\n<p>5️⃣ Мотивируем выполнение задач, связывая их с любимыми делами ребенка.\r\n</p>\r\n<p>6️⃣ Концентрируемся на успехах, хвалим за выполненные этапы и не ругаемся за незаконченные.</p>\r\n<p>Например, можно в несколько подходов помыть посуду, начиная с одной тарелочки.\r\nПодметать можно не сразу всю квартиру, а начинать с одной комнаты, либо с одной ее части, визуально выделив ее, например, молярным скотчем.</p>\r\n<p>Расписание может быть как на весь день, так и в формате \"сначала-потом\", где нужно выполнить всего лишь одну задачу.\r\nВыбор расписания зависит от того, какую цель в обучении ребенка вы преследуете и каких результатов хотите достичь.</p>\r\n<p>Варианты нормы - это когда с помощью визуального расписания мы хотим убрать из рутины ребенка помощь взрослого и развить самоорганизацию, а может быть и такое, что ребенку легче ориентироваться в рутине с помощью визуальных подсказок, тогда расписание становится помощников в коммуникации. Опять же, все зависит от целей обучения.</p>\r\n<p>Проект «Я могу: социализация и обучение жизненно-важным навыкам детей с РАС» реализуется с использованием пожертвования Благотворительного фонда «Абсолют-Помощь».</p>\r\n<p><a href=\"/news/detail.php?id=894\" target=\"_blank\">Подробнее о проекте</a></p>\r\n', '/images/ABA/2024/6/1.jpg', '2024-12-09', '/images/ABA/2024/6/3.jpg;\r\n/images/ABA/2024/6/2.jpg', '', '', '', '', 0, '', '12'),
(1031, '4', 'Подводим итоги уходящего года', 'Подводим итоги уходящего года и вспоминаем теплые моменты проекта «Коммуникативные группы для детей с расстройствами аутистического спектра», в котором нас поддержали наши друзья - Благотворительный фонд «Вклад в будущее».', '<p>Подводим итоги уходящего года и вспоминаем теплые моменты проекта «Коммуникативные группы для детей с расстройствами аутистического спектра», в котором нас поддержали наши друзья - Благотворительный фонд «Вклад в будущее».\r\n</p>\r\n<p>&#128153; Мы познакомились с замечательными детьми с РАС и их семьями. Нам удалось наладить сотрудничество с ребятами на индивидуальных занятиях и простроить им индивидуальные маршруты освоения социально-коммуникативных навыков исходя из данных тестирований.</p>\r\n<p>&#129505; Семьи ребят получили и продолжают получать поддержку опытных поведенческих аналитиков в вопросах развития социально-коммуникативных навыков и дальнейшего обучения детей в условиях инклюзии.</p>\r\n<p>&#128153; Коммуникативные группы дали ребятишкам возможность познакомиться между собой в естественной обстановке. Сначала они привыкали находиться в малом кругу сверстников и заниматься параллельной деятельностью без лишнего стресса, затем плавно ребята вливались в общие активности и подключались к распорядку группы. Они учились соблюдать правила и проявлять терпимость. Как результат, многие ребята стали проявлять искренний интерес друг к другу и на проекте завязались настоящие истории дружбы!</p>\r\n<p>&#129505; Праздники, которые посещали наши маленькие ученики и их семьи, подарили много приятных воспоминаний: все-таки когда вы собираетесь большой бандой и выходите в общественные пространства - чувствуется тыл и появляется возможность позволить ребенку быть тем, кем он является - индивидуальностью. За наших учеников мы безмерно гордимся, каждый ребенок проделал большую работу над собой, чтобы находиться в шумных компаниях и соблюдать правила поведения, несмотря на особенности работы нервной системы каждого.</p>\r\n<p>&#128153; Информационная кампания по развитию коммуникативных навыков в наших социальных сетях тоже дала свои плоды - многие родители и педагоги заинтересовались форматом \"Коммуникативная группа\" и проявляли большой интерес к данной теме, просили поделиться нашим опытом подобных занятий! А мы, в свою очередь, с удовольствием им делились!</p>\r\n<p>&#129505; Друзья, впереди еще множество полезных мероприятий, в которых так нуждаются наши дети! Мы постараемся окружить вас поддержкой и опорой в этом нелегком деле - в адаптации и социализации детей с РАС! Следите за новостями о наборах на бесплатные мероприятия в наших социальных сетях, чтобы стать участниками наших проектов в Новом году!</p>\r\n<p>Проект реализуется при поддержке Благотворительного фонда «Вклад в будущее».\r\n</p>\r\n<p><a href=\"/news/detail.php?id=938\" target=\"_blank\">Подробнее о проекте</a></p>', '/images/ABA/2024/7/1.jpg', '2024-12-24', '/images/grants/VK_VB.jpg;\r\n/images/ABA/2024/7/2.jpg;\r\n/images/ABA/2024/7/3.jpg;\r\n/images/ABA/2024/7/4.jpg;\r\n/images/ABA/2024/7/5.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', '21'),
(1032, '4', 'Что такое эхолалия?', 'Эхолалия – это естественная стадия развития речи, когда ребенок начинает повторять услышанные звуки, слова или даже целые предложения. У нейротипичных детей этот этап обычно проходит быстро, трансформируясь в диалог, но у детей с задержками речи всё может быть иначе.', '<p>\"В середине занятия Полина начала петь песню, мы с родителями заплакали!\" - поделилась Дарья, инструктор, которая занималась АВА-терапией с малышкой Полиной целый месяц.\r\n</p>\r\n<p>Эхолалия – это естественная стадия развития речи, когда ребенок начинает повторять услышанные звуки, слова или даже целые предложения. У нейротипичных детей этот этап обычно проходит быстро, трансформируясь в диалог, но у детей с задержками речи он может либо не появиться естественным образом, либо затянуться или проявляться иначе, чем у сверстников.</p>\r\n<p>На этом интенсиве команда «Рассвета» начала развитие речи у Полины с развития навыков имитации (=повторения).\r\nЧто мы делали:</p>\r\n<p>- Полина очень НЕ любила заниматься за столом, плакала и убегала. Сперва мы проработали эту проблему.</p>\r\n<p>- Учились повторять движения моторики и артикуляции.</p>\r\n<p>- После того, как Поля начала внимательно повторять движения, мы начали пробовать повторять звуки.</p>\r\n<p>- Запустилось повторение речи!</p>\r\n<p>- Использовали визуальные подсказки. Карточки с картинками или детальки от разборных игр могут помочь ребенку лучше понять, что от него требуется.</p>\r\n<p>- Использовали поощрения и хвалили Полину за каждую попытку повторить слово, даже если она несовершенна. Это помогло Полине чувствовать себя увереннее и стремиться к дальнейшему развитию.</p>\r\n<p>И теперь в конце интенсива у Полины образовался свой словарик, с помощью которого она научилась просить: КУ-крутить,\r\nПУ-пузыри,\r\nПЕ-пенка,\r\nПЕ-песню переключить или включить,\r\nО-открой,\r\nДААА-дай,\r\nПА-помоги.</p>\r\n<p>Работа над развитием речи через имитацию является важным этапом в обучении детей с аутизмом и его пропустить не получится.</p>\r\n<p>Проект реализуется при поддержке Благотворительного фонда «Вклад в будущее».\r\n</p>\r\n<p><a href=\"/news/detail.php?id=938\" target=\"_blank\">Подробнее о проекте</a></p>', '/images/ABA/2024/32.jpg', '2024-12-27', '/images/grants/VK_VB.jpg\r\n', '', '', 'Новости', '/news.php', 0, '', '21'),
(1033, '18', 'Барьер на пути к творчеству', 'Когда речь идет о творчестве, важно помнить, что оно тесно связано с эмоциями, самовыражением и умением концентрироваться. Однако иногда наши дети сталкиваются с трудностями, которые мешают им полностью погрузиться в творческий процесс.\r\n', '<p>Когда речь идет о творчестве, важно помнить, что оно тесно связано с эмоциями, самовыражением и умением концентрироваться. Однако иногда наши дети сталкиваются с трудностями, которые мешают им полностью погрузиться в творческий процесс.\r\n</p>\r\n<p>Вот примеры некоторых таких трудностей и проблем, которые могут препятствовать творчеству:</p>\r\n<p>1. Гиперактивность. Если ребенок слишком подвижен, ему сложно сосредоточиться на одном занятии. В результате творчество превращается в хаос, и вместо удовольствия ребенок испытывает разочарование.</p>\r\n<p>2. Импульсивность. Некоторые дети склонны действовать бездумно, не задумываясь о последствиях своих действий. Например, они могут сломать игрушку или порвать бумагу, не успев закончить рисунок.</p>\r\n<p>3. Страх перед новыми ощущениями. Наши ребята часто испытывают трудности при взаимодействии с незнакомыми материалами или инструментами. Страх перед неизвестным может заставить их отказаться от новых видов деятельности.</p>\r\n<p>4. Проблемы с коммуникацией. Если ребенок не умеет выражать свои мысли и чувства, ему трудно объяснить, что именно вызывает у него дискомфорт или мешает процессу творчества.</p>\r\n<p>5. Отказ от сотрудничества. Иногда дети отказываются участвовать в совместных занятиях, предпочитая оставаться в одиночестве. Это затрудняет работу над групповыми проектами и ограничивает возможности для социального взаимодействия.</p>\r\n<p>Что же делать?</p>\r\n<p>1. Создайте комфортную обстановку. Убедитесь, что рабочее место вашего ребенка организовано таким образом, чтобы минимизировать отвлекающие факторы и создать спокойную атмосферу.</p>\r\n<p>2. Используйте визуальные подсказки. Визуальные расписания и карточки с изображениями могут помочь ребенку понять, что от него ожидается, и снизить уровень тревожности.</p>\r\n<p>3. Поощряйте успехи. Хвалите ребенка за любые достижения, даже самые маленькие. Это поможет укрепить уверенность в себе и желание продолжать заниматься творчеством.</p>\r\n<p>4. Будьте терпеливы. Помните, что каждый ребенок уникален, и процесс освоения новых навыков может занять больше времени, чем вы ожидали. Будьте готовы к неудачам и учитесь на них.</p>\r\n<p>5. Обратитесь за помощью. Если вам кажется, что проблемы с поведением становятся непреодолимыми, обратитесь к специалистам \"АПЦ РАСсвет\".</p>\r\n<p>Творчество – это мощный инструмент для самовыражения и развития личности. Давайте поможем нашим детям преодолеть все препятствия на пути к этому удивительному миру!</p>', '/images/DSS/2024/18/6.jpg', '2024-12-19', '', '', '', '', '', 0, '', ''),
(1034, '18', 'Новогодние игрушки из ваты', 'Накануне новогодних праздников наши юные мастера впервые попробовали сделать игрушки из ваты. Смотрим фото и ностальгируем по советским ватным дедам морозам, снегуркам и лялечкам в одеялах.', '<p>Накануне новогодних праздников наши юные мастера впервые попробовали сделать игрушки из ваты. \r\n</p>\r\n<p>Это оказался совершенно новый вид творчества для наших подопечных, и мы не можем не поделиться с вами результатами нашей работы!</p>\r\n<p>Конечно, как это часто бывает, у каждого ребенка был свой подход к созданию игрушки. Кто-то выбрал сделать снегурочку, кто-то предпочел малыша или куклу. Все эти милые создания придадут елке особую атмосферу радости и волшебства!</p>\r\n<p>К сожалению, не у всех детей все прошло гладко. Есть и те, которым было немного неприятно трогать вату в клею. Однако, мы не можем не отметить, насколько они старались и проявили свою творческую натуру! Для них это был настоящий вызов, и мы гордимся теми маленькими победами, которые они одержали! </p>\r\n<p>Смотрим фото и видео и ностальгируем по советским ватным дедам морозам, снегуркам и лялечкам в одеялах.</p>\r\n<p>АНО \"АПЦ РАСсвет\" - участник проекта Ассоциации \"Аутизм-Регионы\" «Полезная занятость с применением практик и методов с научно доказанной эффективностью для молодых людей с РАС» при поддержке Фонда президентских грантов.</p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239636&hash=ef09e87b553a6d1a\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2024/18/1.jpg', '2024-12-24', '/images/DSS/2024/18/2.jpg;\r\n/images/DSS/2024/18/3.jpg;\r\n/images/DSS/2024/18/4.jpg;\r\n/images/DSS/2024/18/5.jpg', '', '', '', '', 0, '', ''),
(1035, '13', 'Я могу: как иркутских дошкольников с РАС обучали новым навыкам', 'Благотворительный фонд \"Абсолют-Помощь\" о проекте Рассвета \"Я могу: обучение жизненно-важным навыкам детей с РАС\", который реализуется при поддержке фонда.', '', 'https://sun9-65.userapi.com/impg/MWX8y9SRRhKxL0-GCVIPJBLk3-xd1AKa7pV1wg/11NbSFfULoA.jpg?size=1280x960&quality=95&sign=aaf8463a5f47383e8e6a55c7f0540fb6&type=album', '2025-01-10', '', '', 'https://vk.com/wall-128651156_4549', '', '', 1, '', '12');
INSERT INTO `news` (`id`, `type`, `title`, `short_text`, `detail_text`, `img_url`, `date`, `gallery`, `video`, `ext_url`, `section_name`, `section_url`, `longread`, `code`, `project_id`) VALUES
(1036, '4', 'Подготовка Димы', 'Дима посещает бесплатные адаптационные занятия в рамках социального проекта «Без паники!Идем к стоматологу» в «Рассвете», чтобы подготовиться к добровольному и безопасному осмотру у настоящего врача стоматолога.', '<p>Дима посещает бесплатные адаптационные занятия в рамках социального проекта «Без паники!Идем к стоматологу» в «Рассвете», чтобы подготовиться к добровольному и безопасному осмотру у настоящего врача стоматолога.</p>\r\n<p>Навыки, которые Дима уже освоил:</p>\r\n<p>&#9989; Дима может 15 минут находиться в стоматологическом кресле и следовать инструкциям врача, не проявляя проблемного поведения</p>\r\n<p>&#9989; Спокойный осмотр полости рта стоматологическими инструментами</p>\r\n<p>&#9989; Дима может позволить врачу провести достаточно инвазивные процедуры: анестезия, лечение бормашиной.</p>\r\n<p>&#9989; Дима позволяет проводить гигиеническую чистку зубов</p>\r\n<p>Обобщение.</p>\r\n<p>Важно поддерживать полученные навыки не только в рамках занятий, но и в реальных условиях медицинского учреждения во время осмотров и лечения.\r\nОбобщение - наш следующий шаг!</p>\r\n<p>Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>\r\n', '/images/ABA/2025/2025-1.jpg', '2025-01-11', '', '', '', '', '', 0, '', '11'),
(1037, '16', 'Как мечты стали реальностью', 'В копилку достижений и блестящих результатов работы с детьми в рамках проекта \"Без паники! Идём к стоматологу\", добавилась и новость о том, что Екатерина Родыгина - руководитель \"Рассвета\", получила квалификацию Сертифицированный поведенческий аналитик и супервизор.', '<p>\"Без паники! Идем к стоматологу\" - это проект для ребятишек с РАС, которые испытывают сложности с посещением врача-стоматолога, связанные с особенностями поведения.</p>\r\n<p>Команда \"Рассвета\" ежедневно работает над развитием направления адаптивной стоматологии в Иркутске и инклюзии в целом.\r\nОбучающие площадки для детских врачей-стоматологов по особенностям взаимодействия с нейроотличными пациентами, повышение квалификации поведенческих специалистов, осуществляющих подготовку ребят к посещению врачей и, конечно же, сами адаптивные занятия с нашими учениками.</p>\r\n<p>На этом нелегком пути развития помощи детям с РАС значительную поддержку нам оказывает наш супервизор - Анна Устиновская из г.Казань.\r\nПомимо очных встреч, когда Анна прилетает к нам в центр проконсультировать в тех или иных вопросах реализации проекта, мы активно используем онлайн-формат супервизий, на которых обсуждаем различные поведенческие кейсы и получаем ценные советы от опытного специалиста.</p>\r\n<p>В копилку достижений и блестящих результатов работы с детьми на адаптивных занятиях в рамках проекта \"Без, паники! Идём к стоматологу\", которыми нам удалось поделиться на IХ Международной конференции \"Наука и практика прикладного анализа поведения\", добавилась и новость о том, что что Екатерина Родыгина - руководитель \"Рассвета\", получила квалификацию Сертифицированный поведенческий аналитик и супервизор (Certified Behavior Analyst and Supervisor - CBA-S).\r\n</p>\r\n<p>\"У меня есть традиция в новогоднюю ночь под звон курантов загадывать желание. Однажды я загадала стать мамой, открыть первый в регионе ресурсный класс… И каждый год мои желания сбывались, какими бы невероятными они ни казались. В 2024 году я загадала получить квалификацию супервизора. Честно говоря, тогда мне казалось, что это недостижимо. Но с моим супервизором все сомнения улетучились. Спасибо, Анна Устиновская, за то, что верила в меня, вдохновляла на успех, настояла на том, что пора идти на 5 модуль, спасибо за поддержку проекта моей мечты и совместное выступление на конференции, а также за множество идей по занятиям с детьми!\", - поделилась Екатерина.</p>\r\n<p>Посмотреть выступление на конференции с докладом о результатах наших учеников можно на сайте дистанционного обучения Школы Юлии Эрц. Чтобы получить бесплатный доступ к записям докладов, нужно зарегистрироваться <a href=\"https://aba-kurs.com/services/course/60014/\" target=\"_blank\">ЗДЕСЬ.</a></p>\r\n<p>Проект \"Без паники! Идем к стоматологу\" реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\r\n<p><a href=\"/news/detail.php?id=865\" target=\"_blank\">Подробнее о проекте</a></p>', '/images/news/2025/1-1.jpg', '2025-01-17', '/images/grants/pgrants_logo.jpg;\r\n/images/ABA/2025/1/1.jpg;\r\n/images/ABA/2025/1/2.jpg;\r\n/images/ABA/2025/1/3.jpg;\r\n/images/ABA/2025/1/4.jpg;\r\n/images/ABA/2025/1/5.jpg;\r\n/images/ABA/2025/1/6.jpg', '', '', 'Новости', '/news.php', 0, '', '11'),
(1038, '18', 'Какое влияние оказывает наша мастерская на ребят', '«Дом синего слона» играет важную роль в жизни ребят с аутизмом, так как мы предоставляем уникальные возможности для развития, социализации и самореализации. Вот некоторые ключевые аспекты этой роли.', '<p>«Дом синего слона» играет важную роль в жизни ребят с аутизмом, так как мы предоставляем уникальные возможности для развития, социализации и самореализации. Вот некоторые ключевые аспекты этой роли:\r\n</p>\r\n<p>1. Развитие навыков. В мастерской ребята учатся новым навыкам, таким как работа с различными материалами, создание изделий своими руками, управление инструментами. Эти навыки помогают им стать более самостоятельными и уверенными в себе.</p>\r\n<p>2. Социальная адаптация. Работа в группе способствует развитию коммуникативных навыков, умению взаимодействовать с другими людьми, понимать их эмоции и реагировать на них. Это особенно важно для детей с аутизмом, которым часто сложно устанавливать социальные контакты.</p>\r\n<p>3. Эмоциональная поддержка. Мы создаем безопасную и дружелюбную атмосферу, где каждый участник чувствует себя принятым и понятым. Это помогает снизить уровень стресса и тревоги, что положительно сказывается на общем состоянии здоровья.</p>\r\n<p>4. Творческое выражение. Занятия творчеством позволяют ребятам выразить свои мысли и чувства, которые иногда трудно передать словами. Это может быть важным способом самовыражения и самопознания.</p>\r\n<p>5. Повышение самооценки. Успехи в работе над проектами, похвала от педагогов и сверстников способствуют повышению самооценки и уверенности в своих силах. Ребятам становится легче верить в себя и свои способности.</p>\r\n<p>6. Подготовка к взрослой жизни. Навыки, приобретенные в мастерской, могут пригодиться в будущем, когда ребята начнут искать работу или заниматься каким-либо делом самостоятельно. Это важный шаг к интеграции в общество и независимой жизни.</p>\r\n<p>Таким образом, наша мастерская оказывает значительное влияние на развитие и благополучие ребят с аутизмом, помогая им раскрыть свой потенциал и жить полноценной жизнью.</p>\r\n<p></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239649&hash=060ae658572c73d5\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2025/1.jpg', '2025-01-15', '', '', '', '', '', 0, '', ''),
(1039, '18', 'Гости из программы \"Вести - Иркутск\"', 'Сегодня нашу инклюзивную мастерскую «Дом синего слона» посетили журналисты из программы \"Вести-Иркутск\", в частности, Алёна Бадуева! Они побывали на нашем групповом занятии, взяв интервью у наших ребят, их родителей и педагогов.', '<p>Сегодня нашу инклюзивную мастерскую «Дом синего слона» посетили замечательные журналисты из программы \"Вести-Иркутск\", в частности, Алёна Бадуева! Они побывали на нашем групповом занятии, взяв интервью у наших ребят, их родителей и педагогов. Это был замечательный опыт для всех участников!\r\n</p>\r\n<p>Мы рады поделиться с вами новостями о нашей работе, успехах и достижениях. Смотрите сюжет уже в воскресенье 26 января в 14:30 на телеканале «Россия 1», в 18:00 на телеканале «Россия 24». Не пропустите возможность узнать больше о тех чудесных вещах, которые мы создаем вместе с ребятами!</p>', '/images/DSS/2025/1/1.jpg', '2025-01-24', '/images/DSS/2025/1/2.jpg;\r\n/images/DSS/2025/1/3.jpg', '', '', '', '', 0, '', ''),
(1040, '13,18', 'О \"Доме синего слона\" в программе \"Вести-Иркутск\"', 'Очень тёплый сюжет о нашей инклюзивной мастерской \"Дом синего слона\" вышел 26 января в программе \"Вести-Иркутск\" на телеканалах \"Россия-1\" и \"Россия-24\". Посмотреть сюжет можно на сайте программы \"Вести-Иркутск\".', '', '/images/smi/47.jpg', '2025-01-27', '', '', 'https://vestiirk.ru/news/delaiut-mylo-sozdaiut-igrushki-v-irkutske-rabotaet-masterskaia-dlia-detei-s-rasstroistvom-autisticheskogo-spektra/', '', '', 1, '', ''),
(1041, '13,18', 'Екатерина Родыгина о мастерских для журнала \"Аутизм - это...\"', 'В рамках Дня открытых дверей проекта \"Полезная занятость с применением практик и методов с научно доказанной эффективностью для молодых людей с РАС\" ассоциации \"Аутизм-Регионы\" Екатерина Родыгина, руководитель АНО \"АПЦ РАСсвет\" рассказала, почему необходимо начинать разработку проектов для взрослых людей с РАС, когда дети еще учатся в школе. ', '', '/images/smi/48.jpg', '2025-01-31', '', '', 'https://autismjournal.help/articles/ekaterina-rodygina-shkola-zakonchitsya-vse-razrushitsya-voobsche-nichego-ne-budet-roditeli-tak-oschuschayut', '', '', 1, '', ''),
(1042, '18', 'Истории участников проекта', 'Завершается проект Ассоциации «Аутизм-Регионы» «Полезная занятость с применением практик и методов с научно доказанной эффективностью для молодых людей с РАС». Мы гордимся всеми нашими подопечными, ведь каждый сделал огромный шаг вперёд! О двоих из них расскажем в этой публикации.', '<p>Январь – это начало нового года, но одновременно и завершение одного значимого периода для нашей команды. Завершается проект Ассоциации «Аутизм-Регионы» «Полезная занятость с применением практик и методов с научно доказанной эффективностью для молодых людей с РАС».\r\n</p>\r\n<p>Эти месяцы были наполнены значимыми событиями и достижениями! Наши ребята успели попробовать себя в самых разных направлениях: от создания мыла до изготовления свечей, а также принимали активное участие в ярмарках. Каждый день был полон открытий и новых возможностей!</p>\r\n<p>Мы гордимся всеми нашими подопечными, ведь каждый сделал огромный шаг вперёд! О двоих из них расскажем в этой публикации.</p>\r\n<p>История Влада</p>\r\n<p>Влад достиг значительного прогресса в умении работать без постоянного сопровождения тьютора. Если ранее ему требовалась помощь на протяжении всего занятия, то теперь он способен справляться со многими задачами практически самостоятельно.</p>\r\n<p>Влад научился терпеливо ожидать своей очереди, например, при изготовлении свечей. Теперь он может спокойно дожидаться своей плитки или использования микроволновки. Это большое достижение!</p>\r\n<p>На занятии по изготовлению игрушек из ваты Влад почти полностью справился сам, следуя заранее установленному алгоритму действий. После того, как ему объяснили последовательность этапов работы, он успешно применил их на практике. Мы видим, что Влад значительно продвинулся в своем развитии и начинает проявлять независимость в творчестве.</p>\r\n<p>Влад отлично справляется с заданиями, опираясь на визуальную опору. Когда мы варим мыло или изготавливаем свечи, он выполняет весь процесс самостоятельно, руководствуясь наглядными инструкциями. Это свидетельствует о том, что с помощью визуальных подсказок, Влад усваивает информацию и использует её для выполнения задач независимо от взрослых.</p>\r\n<p>Также стоит отметить, что Влад стал менее тревожным и негативным на занятиях. Он приходит с пониманием того, что от него требуется, и сразу приступает к выполнению заданий. Для поддержания мотивации Владу помогает система поощрений, которая стимулирует его эффективно продолжать работу.</p>\r\n<p>История Димы</p>\r\n<p>«Перед тем, как зайти в кабинет, Дима всегда многократно открывал и закрывал дверь, далее ему важно было несколько раз перешагнуть порог. Все это занимало большое количество времени от занятия. Перед тем, как сесть за стол, Дима обходил стул только с правой стороны и, только потоптавшись на месте, садился на рабочее место. Такое поведение могло нарушать технику безопасности на занятиях. Так же проблемой было то, что Дима мог себя легонько ударять по губам. Все эти виды поведения заметно сократились или ушли из его репертуара», - отмечает сотрудник инклюзивной мастерской, Анастасия.</p>\r\n<p>Теперь Дима умеет самостоятельно занимать себя в моменты ожидания, что позволяет нам эффективно использовать время занятий. Его способность к самоорганизации увеличилась, и он готов выполнять поставленные задачи без частых отвлечений. Время продуктивной работы Димы существенно возросло, и мы гордимся тем, насколько далеко он продвинулся.</p>\r\n<p>АНО \"АПЦ РАСсвет\" - участник проекта Ассоциации \"Аутизм-Регионы\" «Полезная занятость с применением практик и методов с научно доказанной эффективностью для молодых людей с РАС» при поддержке Фонда президентских грантов.</p>', '/images/DSS/2025/2.jpg', '2025-01-29', '', '', '', '', '', 0, '', ''),
(1043, '4', 'Ребенок не понимает речь', 'Несформированные навыки понимания речи - распространённая проблема среди деток с РАС. Есть ли универсальные протоколы по работе с данной проблемой? Эффективного универсального решения нет, важно подходить к процессу обучения индивидуально.', '<p>\"Ребёнок не слушает словесные инструкции, а ориентируется только на показ, демонстрацию. Не понимает речь и бытовые инструкции ( \"дай вилку\", \"надень штаны\", \"сядь\", \"встань\") и простейшие учебные (\"покажи красный\", \"рисуй\", \"веди линию до картинки\") не понимает. Есть ли универсальные протоколы по работе с данной проблемой?\"</p>\r\n<p>Несформированные навыки понимания речи - распространённая проблема среди деток с РАС. Эффективного универсального решения данной проблемы нет, важно подходить к процессу обучения индивидуально.</p>\r\n<p>Важно оценить навыки ребенка по следующим критериям:</p>\r\n<p>&#9989; Сотрудничество и учебное поведение.</p>\r\n<p>Прежде всего, важно определить, сотрудничает ли ребёнок вообще? Готов ли он вас слушаться и помогать вам, доверяет ли он вам или бьется в истерике, когда вы просите отдать телефон с мультиками?</p>\r\n<p>Если ребенок отказывается выполнять любые задания, даже когда вы иллюстрируете обращенную к нему речь визуальными подсказками, проблема выходит за рамки понимания инструкций и требует анализа мотивации и простейших учебных навыков. Наличие учебного поведения (стремление к обучению, готовность к выполнению заданий, реагирование на предоставляемые подсказки) — ключевой фактор успеха.</p>\r\n<p>&#9989; Проверьте базовые навыки, характерные для поведения слушателя. Это поможет понять, где именно проблема.</p>\r\n<p>Например:<br>\r\nа) Реагирует ли ребенок на звуки? Поворачивает ли голову на различный шум с целю найти его источник?<br>\r\nб) Устанавливает ли зрительный контакт, если вы к нему обращаетесь?<br>\r\nв) Реагирует ли на свое имя?<br>\r\nг) Реагирует ли на обращенную речь в контексте? (Например, реагирует на \"садись\", если рядом стоит стульчик).<br>\r\nд) Реагирует ли на обращенные к нему инструкции при выборе из нескольких предметов, людей или действий? (Например, \"Где мама?\", \"Где кошка?\", \"Похлопай\", \"Где носик?\").</p>\r\n<p>Возвращаясь к вопросу нашей подписчицы, инструкции \"дай вилку\" и \"веди линию до картинки\" имеют разный уровень сложности.\r\nВ первом случае ребенок должен уметь давать и находить вилку в окружающем пространстве, во втором случае - ребенку требуется уметь вести линию карандашом, находить этот карандаш в пространстве, реагировать на инструкцию \"веди линию\" и визуально сопоставлять картинки.</p>\r\n<p>Если ребенок испытывает сложности с простейшими навыками поведения слушателя, есть смысл сосредоточится на более простых целях обучения.</p>\r\n<p>&#9989; Продумайте шаги обучения ребенка.</p>\r\n<p>Ваши задания и игры должны быть подобраны исходя из конкретных дефицитов ребенка и его сильных сторон.\r\nОни должны быть легкими, понятными, с своевременным предоставлением подсказок.\r\nНапример, сначала установление сотрудничества, уже потом обучение навыкам понимания речи.</p>', '/images/ABA/2025/2025-2.jpg', '2025-01-30', '', '', '', '', '', 0, '', ''),
(1044, '18', 'Инклюзивные занятия в мастерской', 'Сегодня в нашей мастерской мы провели настоящее инклюзивное занятие. К нам в гости пришли замечательные ребята – Леша и Лева. Их тепло встретили наши постоянные участники Витя и Дима. Атмосфера была наполнена дружелюбием!', '<p>Сегодня в нашей мастерской мы провели настоящее инклюзивное занятие. К нам в гости пришли замечательные ребята – Леша и Лева. Их тепло встретили наши постоянные участники Витя и Дима. Атмосфера была наполнена дружелюбием!\r\n</p>\r\n<p>На занятии мальчики активно взаимодействовали друг с другом: они делились красками, помогали друг другу, а также рассказывали интересные истории. Каждый старался внести свою лепту в общее дело, создавая удивительных сов. Это был настоящий пример того, как важно поддерживать друг друга и учиться вместе, независимо от различий.</p>\r\n<p>Мы рады, что такие занятия помогают ребятам развивать социальные навыки, укреплять дружеские связи и просто получать удовольствие от творчества! Спасибо всем участникам за этот замечательный день! </p>\r\n\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-211689065&id=456239674&hash=c0d0acea98ee29fd\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/DSS/2025/3.jpg', '2025-02-05', '', '', '', '', '', 0, '', ''),
(1045, '20', 'Ключ к успеху в работе с проблемным поведением', 'Ключ к успеху преодоления проблемного поведения –  определение функций (причин) поведения и разработка стратегий, которые помогают ребенку заменить неприемлемые действия на более адаптивные его виды.', '<p>Люди склонны объяснять проблемное поведение ребенка субъективными причинами: у него психическое расстройство, у него плохое настроение, так реагирует на погоду, он просто не умеет говорить, он проголодался, заболел и т.д.\r\n</p>\r\n<p>Прикладной анализ поведения смотрит на причины поведения более глубоко и беспристрастно, поэтому результаты поведенческих вмешательств показывают эффективные результаты.</p>\r\n<p>Прикладной анализ поведения фокусируется на взаимосвязи между поведением, его предшествующими событиями и последующими последствиями.\r\n</p>\r\n<p>Вместо того, чтобы гадать о внутренних причинах, прикладной анализ изучает наблюдаемые переменные.\r\nНапример, если ребенок кричит (поведение), поведенческий аналитик будет искать, что происходило перед криком (например, просьба убрать игрушки, отказ от любимой игры) и что происходило после крика (например, получение желаемой игрушки, избегание уборки).\r\nИменно эта функциональная связь позволяет разработать эффективные стратегии вмешательства.</p>\r\n<p>Вместо предположений о «плохом настроении» или «неумении говорить», прикладной анализ поведени помогает определить, какие конкретные стимулы вызывают нежелательное поведение и какие последствия его поддерживают.\r\n\r\nНапример, крик может быть эффективным способом получить желаемое, и поэтому ребенок продолжает кричать.</p>\r\n<p>Поведенческий анализ предложит альтернативные способы получить желаемое (например, использование слов, картинок, жестов).\r\nЭто позволяет изменить функциональную связь и, следовательно, изменить поведение.</p>\r\n<p><b>Способы помочь ребенку с тяжелым проблемным поведением с помощью поведенческого анализа.</b></p>\r\n<p>Ключ к успеху преодоления проблемного поведения –  определение функций (причин) поведения и разработка стратегий, которые помогают ребенку заменить неприемлемые действия на более адаптивные его виды. </p>\r\n<p>Просить перерыв, уметь ждать, привлекать внимание социально-приемлемым образом, уметь занять себя в свободное время, сообщить близким о проблеме и т.д.</p>\r\n<p>Один из самых важных этапов поведенческого вмешательства - определении причин проблемного поведения.\r\n\r\nПредставьте себе, что это - детективное расследование, цель которого –  раскрыть “мотивы преступления” – понять, зачем ваш ребенок демонстрирует это поведение.</p>\r\n<p><b>Функциональный анализ поведения</b></p>\r\n<p>Специалист наблюдает за ребенком в кабинете занятий, создавая определенные условия: играет в самые любимые игры с ребенком, предоставляя ученику внимание и чуткость к его просьбам.\r\nПостепенно специалист вводит различные требования и ограничения, чтобы проанализировать, в каких именно условиях проявляются проблемы.\r\nТакой тщательный анализ позволяет точно определить функцию поведения.</p>\r\n<p>Всего существует 4 функции поведения:</p>\r\n<p>1️⃣ Привлечение внимания. Ребенок может кричать или бить других, чтобы привлечь к себе внимание, даже если это негативное внимание.</p>\r\n<p>2️⃣ Избегание требований или уход от неприятной ситуации.\r\nРебенок может убегать или проявлять агрессию, чтобы избежать неприятные для него события (не всегда очевидны предпочтения ребенка даже близким людям, такое бывает).</p>\r\n<p>3️⃣ Получение желаемого предмета или действия.\r\nРебенок может устраивать истерики, чтобы получить игрушку или вкусняшки.</p>\r\n<p>4️⃣ Самостимулятивное поведение.\r\nРебенок может биться головой в том числе и для сенсорной стимуляции.</p>\r\n<p>Результатами проведения такого «эксперимента» будет определение функции проблемного поведения ребенка, зачем он совершает те или иные действия.</p>\r\n<p>На основании заключения функционального анализа строится план поведенческого вмешательства: какими самыми быстрыми и эффективными способами мы можем заменить или убрать из репертуара то или иное проблемное поведение. Какие навыки нужно приобрести для достижения этой цели.</p>\r\n<p></p>\r\n<p></p>\r\n<section class=\"video-detail\">\r\n<iframe src=\"https://vk.com/video_ext.php?oid=-165016766&id=456241504&hash=7cf8661a676bed28\" width=\"333\" height=\"660\" frameborder=\"0\" allowfullscreen=\"1\" allow=\"autoplay; encrypted-media; fullscreen; picture-in-picture\"></iframe>\r\n</section>', '/images/ABA/2025/2025-3.jpg', '2025-02-22', '', '', '', 'Новости', '/news.php', 0, '', ''),
(1046, '3', 'Благотворительный вебинар «Как открыть ресурсный класс?»', 'Устали от трудностей в поиске подходящей школы для ребенка с РАС? Хотите взять все в свои руки и создать ресурсный класс? Вы нуждаетесь в консультации опытного поведенческого аналитика и особенного родителя, столкнувшегося с такой же проблемой? Тогда наш вебинар – для вас!', '<p>Устали от трудностей в поиске подходящей школы для ребенка с РАС? Хотите взять все в свои руки и создать ресурсный класс? Вы нуждаетесь в консультации опытного поведенческого аналитика и особенного родителя, столкнувшегося с такой же проблемой? Тогда наш вебинар – для вас!</p>\r\n<p>&#128197; 10 марта 2025 года</p>\r\n					<p>&#128344; 19:00 по Иркутску (14:00 МСК)</p>\r\n<p>&#128091; Любая сумма</p>\r\n<p>Михеева Елена, сертифицированный поведенческий аналитик и супервизор (CBA-S), учредитель АНО \"АПЦ РАСсвет\" поделится своим опытом и ответит на все ваши вопросы.</p>\r\n<p>Вебинар проводится в поддержку действующих ресурсных классов Иркутска, ваше участие подтверждается после вашего пожертвования в пользу <a href=\"https://dobro.mail.ru/projects/shkolnyij-mir-dlya-osobennoj-agatyi-i-ee-druzej/\" target=\"_blank\">сбора</a>.</p>	\r\n<p>Чтобы попасть на вебинар:</p>\r\n<p>&#9989; Совершите пожертвование</p>	\r\n<p>&#9989; Сделайте скриншот</p>	\r\n<p>&#9989; Вставьте этот скриншот в форму регистрации ниже &#11015;</p>				\r\n<p>Телефон для справок: 89526236675</p>\r\n<p><span class=\"help-donate-team-heading\"><a href=\"https://dobro.mail.ru/projects/shkolnyij-mir-dlya-osobennoj-agatyi-i-ee-druzej/\" class=\"button red-button\" target=\"_blank\">Поддержать сбор</a></span> </p>\r\n<p>Заполняя форму, вы соглашаетесь с <a href=\"https://rassvet-apc.ru/privacy_policy\">Политикой обработки персональных данных</a></p>\r\n<p></p>\r\n<p></p>\r\n<script src=\"https://yastatic.net/s3/frontend/forms/_/embed.js\"></script><iframe src=\"https://forms.yandex.ru/u/67bc521d505690d314fe3241/?iframe=1\" frameborder=\"0\" name=\"ya-form-67bc521d505690d314fe3241\" width=\"100%\" style=\"max-width: 650px\"></iframe>\r\n</p>', '/images/seminar/2025/1.jpg', '2025-02-27', '', '', '', 'Новости', '/news.php', 0, '', ''),
(1047, '3', 'Прямой эфир «Дополнительные оплачиваемые выходные дни лицам, осуществляющим уход за детьми-инвалидами»', 'Как правильно оформить дополнительные выходные дни на работе? Какие документы необходимы для оформления? Формы заявлений и особенности их заполнения. Нюансы оформления дополнительных выходных для совместителей. Расскажем в прямом эфире!', '<p>Как правильно оформить дополнительные выходные дни на работе? Какие документы необходимы для оформления? Формы заявлений и особенности их заполнения. Нюансы оформления дополнительных выходных для совместителей. Расскажем в прямом эфире!</p>\r\n<p>&#128197; 5 марта 2025 года</p>\r\n					<p>&#128344; 16:00 по Иркутску (11:00 МСК)</p>\r\n<p>Наши спикеры:</p>\r\n<p>- Родыгина Екатерина, председатель АНО \"АПЦ РАСсвет\"</p>\r\n<p>- Ломакина Инна, юрисконсульт АНО \"АПЦ РАСсвет\"</p>\r\n<p>Не упустите возможность получить ценные знания и задать свои вопросы!</p>	\r\n<p>&#9989; Прямой эфир проводится бесплатно в честь дня рождения нашей организации!</p>	\r\n<p>Регистрация обязательна!</p>				\r\n<p>До встречи в прямом эфире!</p>\r\n<p>Заполняя форму, вы соглашаетесь с <a href=\"https://rassvet-apc.ru/privacy_policy\">Политикой обработки персональных данных</a></p>\r\n<p></p>\r\n<p></p>\r\n<script src=\"https://yastatic.net/s3/frontend/forms/_/embed.js\"></script><iframe src=\"https://forms.yandex.ru/u/67c01c0902848fc40e24f9db/?iframe=1\" frameborder=\"0\" name=\"ya-form-67bc521d505690d314fe3241\" width=\"100%\" style=\"max-width: 650px\"></iframe>\r\n</p>', '/images/seminar/2025/2.jpg', '2025-03-01', '', '', '', 'Новости', '/news.php', 0, '', '');
